import{a as c,b as h}from"./QToolbar.364eba7d.js";import{Q as p,a as g,b as _}from"./QList.bec363e7.js";import{R as S}from"./use-router-link.3adc6712.js";import{x as f,S as z,z as o,A as n,B as e,C as r,D as s,M as b,N as E,O as q,F as C,w as J,P as D,Q as P}from"./index.9f4a94b3.js";import{u as T}from"./harpa.store.8340a5c9.js";import"./create.913f1bae.js";var A=[{idverse:24760,verse:`1. 
Deus prometeu com certeza
Chuvas de gra\xE7a mandar;
Ele nos d\xE1 fortaleza,
E ricas b\xEAn\xE7\xE3os sem par
`,verseorder:1,ismainverse:0,id_hino:1},{idverse:24761,verse:`Chuvas de gra\xE7a,
Chuvas pedimos, Senhor;
Manda-nos chuva constantes,
Chuvas do Consolador.
`,verseorder:2,ismainverse:1,id_hino:1},{idverse:24762,verse:`2. 
Cristo nos tem concedido
O santo Consolador,
De plena paz nos enchido,
Para o reinado de amor.
`,verseorder:3,ismainverse:0,id_hino:1},{idverse:24763,verse:`3. 
D\xE1-nos, Senhor, amplamente,
Teu grande gozo e poder;
Fonte de amor permanente,
P\xF5e dentro de nosso ser.
`,verseorder:4,ismainverse:0,id_hino:1},{idverse:24764,verse:`4. 
Faze os teus servos piedosos,
D\xE1-lhes virtude e valor,
Dando os teus dons preciosos,
Do santo Preceptor.`,verseorder:5,ismainverse:0,id_hino:1},{idverse:24765,verse:`1. 
Oh! que saudosa lembran\xE7a
Tenho de ti, o Si\xE3o,
Terra que eu tanto amo,
Pois \xE9s do meu cora\xE7\xE3o.
Eu para ti voarei,
Quando Senhor meu voltar;
Pois Ele foi para o c\xE9u,
E brave vem me buscar.
`,verseorder:1,ismainverse:0,id_hino:2},{idverse:24766,verse:`Sim, eu porfiarei por essa terra de al\xE9m;
E l\xE1 terminarei as muitas lutas de aqu\xE9m;
L\xE1 est\xE1 meu bom Senhor, ao qual eu desejo ver;
Ele \xE9 tudo p\`ra mim, e sem Ele n\xE3o posso viver.
`,verseorder:2,ismainverse:1,id_hino:2},{idverse:24767,verse:`2. 
Bela, mui bela \xE9 a esperan\xE7a,
Dos que vigiam por ti,
Pois eles recebem for\xE7a,
Que s\xF3 se encontra ali;
Os que procuram chegar
Ao teu rega\xE7o, \xD3 Si\xE3o,
Livres ser\xE3o de pecar
E de toda a tenta\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:2},{idverse:24768,verse:`3. 
Diz a Sagrada Escritura,
Que s\xE3o formosos os p\xE9s
Daqueles que boas novas
Levam para os infi\xE9is;
E, se t\xE3o belo \xE9 falar
Dessas grandezas, aqui,
Que n\xE3o ser\xE1 o gozar
A gra\xE7a que existe ali!`,verseorder:4,ismainverse:0,id_hino:2},{idverse:24769,verse:`1. 
Plena paz o santo gozo,
Tenho em ti, \xF3 meu Jesus!
Pois eu cri em Tua morte sobre a cruz;
No Senhor s\xF3 confiando
Neste mundo viverei,
Entoando aleluias ao meu Rei!
`,verseorder:1,ismainverse:0,id_hino:3},{idverse:24770,verse:`Gl\xF3ria ao meu Jesus!
Pois \xE9 digno de louvor;
\xC9 meu Rei, meu bom pastor,
E meu Senhor.
Como os anjos, que O louvam,
Eu tamb\xE9m O louvarei,
Entoando aleluias ao meu Rei!
`,verseorder:2,ismainverse:1,id_hino:3},{idverse:24771,verse:`2. 
O amor de Jesus Cristo
\xC9 muito grande para mim,
Pois Sua gra\xE7a me encheu de amor sem fim.
Meu Jesus foi para a gl\xF3ria,
Mas um dia eu O verei,
Entoando aleluias ao meu Rei!
`,verseorder:3,ismainverse:0,id_hino:3},{idverse:24772,verse:`3. 
Este mundo n\xE3o deseja
T\xE3o bondoso Salvador,
N\xE3o sabendo agradecer Seu grande amor.
Eu, por\xE9m, estou gozando
Do favor da Sua lei,
Entoando aleluias ao meu Rei!
`,verseorder:4,ismainverse:0,id_hino:3},{idverse:24773,verse:`4. 
Quando o povo israelita
Com Jesus se consertar,
Dando gl\xF3ria ao Seu nome, sem cessar.
Nesse tempo, c\xE9u e terra
H\xE3o de ser a mesma grei,
Entoando aleluias ao meu Rei!`,verseorder:5,ismainverse:0,id_hino:3},{idverse:24774,verse:`1. 
N\xE3o desanimes, Deus prover\xE1;
Deus velar\xE1 por ti;
Sob Suas asas te acolher\xE1;
Deus velar\xE1 por ti.
`,verseorder:1,ismainverse:0,id_hino:4},{idverse:24775,verse:`Deus cuidar\xE1 de ti
No teu viver, no teu sofrer;
Seu olhar te acompanhar\xE1;
Deus velar\xE1 por ti.
`,verseorder:2,ismainverse:1,id_hino:4},{idverse:24776,verse:`2. 
Se o cora\xE7\xE3o palpitar de dor,
Deus velar\xE1 por ti;
Tu j\xE1 provaste Seu terno amor.
Deus velar\xE1 por ti.
`,verseorder:3,ismainverse:0,id_hino:4},{idverse:24777,verse:`3. 
Nos desalentos, nas prova\xE7\xF5es,
Deus velar\xE1 por ti;
Lembra-te dEle nas tenta\xE7\xF5es;
Deus velar\xE1 por ti.
`,verseorder:4,ismainverse:0,id_hino:4},{idverse:24778,verse:`4. 
Tudo o que pedes, Ele far\xE1;
Deus velar\xE1 por ti;
E o que precisas, n\xE3o negar\xE1.
Deus velar\xE1 por ti.
`,verseorder:5,ismainverse:0,id_hino:4},{idverse:24779,verse:`5. 
Como estiveres, n\xE3o temas, vem!
Deus velar\xE1 por ti;
Ele te entende e te ama bem!
Deus velar\xE1 por ti.`,verseorder:6,ismainverse:0,id_hino:4},{idverse:24780,verse:`1. 
Esp\xEDrito, alma e corpo,
Oferto a Ti, Senhor,
Como H\xF3stia verdadeira,
Em obla\xE7\xE3o de amor.
`,verseorder:1,ismainverse:0,id_hino:5},{idverse:24781,verse:`Eu tudo a Deus consagro
Em Cristo, o vivo altar;
\xD3 desce, fogo santo,
Do c\xE9u vem tu selar!
`,verseorder:2,ismainverse:1,id_hino:5},{idverse:24782,verse:`2. 
Sou teu, \xF3 Jesus Cristo!
Teu sangue me comprou;
Eu quero a tua gra\xE7a,
Pois de Ti sempre sou.
`,verseorder:3,ismainverse:0,id_hino:5},{idverse:24783,verse:`3. 
Esp\xEDrito divino,
Do Pai a promiss\xE3o;
Sedenta a alma pede,
A ti, a santa un\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:5},{idverse:24784,verse:`1. 
Tu, na maldi\xE7\xE3o da cruz,
Elevate o Teu amor,
Em rogar, Senhor Jesus:
"Pai, perdoa ao transgressor".
`,verseorder:1,ismainverse:0,id_hino:6},{idverse:24785,verse:`Queres, queres, com vigor,
Muitas almas despertar;
Queres, queres \xF3 Senhor!
Transformar, remir, curar;
Oh! sim! oh! sim!
Queres todas renovar.
`,verseorder:2,ismainverse:1,id_hino:6},{idverse:24786,verse:`2. 
O Maligno tentador
E o autor da perdi\xE7\xE3o
Do pecado, mal e dor,
Da doen\xE7a e da afli\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:6},{idverse:24787,verse:`3. 
Tu levaste, \xF3 bom Jesus,
O pecado, o mal e a dor,
E a doen\xE7a sobre a cruz;
\xC9s o grande Salvador!
`,verseorder:4,ismainverse:0,id_hino:6},{idverse:24788,verse:`4. 
Deus \xE9 bom, quer sempre o bem
Do oprimido pecador;
Mal nenhum, em Si, Deus tem;
"Deus \xE9 luz, Deus \xE9 amor!"`,verseorder:5,ismainverse:0,id_hino:6},{idverse:24789,verse:`1. 
Contra os males deste mundo,
Deus nos vale s\xF3;
N\xE3o h\xE1 mal que Deus n\xE3o cure,
Pois de n\xF3s tem d\xF3.
`,verseorder:1,ismainverse:0,id_hino:7},{idverse:24790,verse:`Cristo cura, sim,
Cristo cura, sim.
Seu amor por n\xF3s \xE9 imenso;
Ele cura, sim!
`,verseorder:2,ismainverse:1,id_hino:7},{idverse:24791,verse:`2. 
Derramou Seu sangue puro
P\`ra remir a mim;
Quando ungido sou de azeite,
Sou curado, enfim!
`,verseorder:3,ismainverse:0,id_hino:7},{idverse:24792,verse:`3. 
S\xF3 noss\`alma \xE9 bem segura,
Oculta em Jesus;
Ele o b\xE1lsamo da vida,
Derramou na cruz.
`,verseorder:4,ismainverse:0,id_hino:7},{idverse:24793,verse:`4. 
Gl\xF3ria a Deus! Eterna gl\xF3ria,
Demos-lhe louvor;
Gl\xF3ria, c\xE2nticos e hosanas
Dai ao Redentor!`,verseorder:5,ismainverse:0,id_hino:7},{idverse:24794,verse:`1. 
Cristo Jesus \xE9 fiel amigo,
Ele s\xF3, Ele s\xF3.
E nas fraquezas est\xE1 comigo,
Ele s\xF3, Ele s\xF3.
`,verseorder:1,ismainverse:0,id_hino:8},{idverse:24795,verse:`E nas lutas de cada dia
Cristo nunca me deixa s\xF3
Pois Ele \xE9 meu seguro guia,
Ele s\xF3, Ele s\xF3.
`,verseorder:2,ismainverse:1,id_hino:8},{idverse:24796,verse:`2. 
N\xE3o h\xE1 amigo mais nobre e digno,
N\xE3o, n\xE3o h\xE1; n\xE3o, n\xE3o h\xE1.
Nem mais humilde e mais benigno,
N\xE3o, n\xE3o h\xE1; n\xE3o, n\xE3o h\xE1.
`,verseorder:3,ismainverse:0,id_hino:8},{idverse:24797,verse:`3. 
Ao pecador perdoar anela,
Ele s\xF3, Ele s\xF3;
E pelos Seus santos sempre vela,
Ele s\xF3, Ele s\xF3.
`,verseorder:4,ismainverse:0,id_hino:8},{idverse:24798,verse:`4. 
Deus, em Seu Filho, se h\xE1 comprazido,
NEle s\xF3, nEle s\xF3;
Mas Sua gl\xF3ria me h\xE1 repartido,
DEle s\xF3, dEle s\xF3.
`,verseorder:5,ismainverse:0,id_hino:8},{idverse:24799,verse:`5. 
NEle n\xF3s temos um firme guia,
NEle s\xF3, nEle s\xF3;
A noite enche de alegria,
Ele s\xF3, Ele s\xF3.`,verseorder:6,ismainverse:0,id_hino:8},{idverse:24800,verse:`1. 
Marchai, soldados de Cristo Jesus,
Marchai, marchai e enfrentai
O inimigo do bem e da luz;
Soldados, avan\xE7ai!
`,verseorder:1,ismainverse:0,id_hino:9},{idverse:24801,verse:`Sim, avan\xE7ar, n\xE3o recuar;
Sem temor, com valor,
Marchai, lutai, pois Cristo vai
Na vanguarda - Avan\xE7ai!
`,verseorder:2,ismainverse:1,id_hino:9},{idverse:24802,verse:`2. 
Marchai, soldados, bem alto erguei
O pavilh\xE3o da salva\xE7\xE3o;
A Belial, firmes, ousados, vencei;
Vencei a corrup\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:9},{idverse:24803,verse:`3. 
Vossa armadura de guerra tomai,
O elmo, sim, da salva\xE7\xE3o:
E a espada: a B\xEDblia levai,
E vencereis, ent\xE3o.
`,verseorder:4,ismainverse:0,id_hino:9},{idverse:24804,verse:`4. 
Avante, avante, confiando em Jesus,
Fortes sereis por Seu amor,
Marchai invictos, soldados da luz,
Jesus \xE9 o vencedor.`,verseorder:5,ismainverse:0,id_hino:9},{idverse:24805,verse:`1. 
Gl\xF3ria a Deus, o Pai eterno,
Cujo trono \xE9 os c\xE9us;
O Universo lhe pertence.
Gl\xF3ria, sim, ao nosso Deus.
`,verseorder:1,ismainverse:0,id_hino:10},{idverse:24806,verse:`Eu te louvo! Eu te louvo!
Oh! Jesus, meu Salvador!
Dai-lhe gl\xF3rias, \xF3 remidos!
Pois seu sangue limpa o pecador.
`,verseorder:2,ismainverse:1,id_hino:10},{idverse:24807,verse:`2. 
Gl\xF3ria ao Filho, mui amado,
Que merece o louvor,
Demos gl\xF3ria ao Seu nome,
Ao cordeiro, ao Redentor.
`,verseorder:3,ismainverse:0,id_hino:10},{idverse:24808,verse:`3. 
Demos n\xF3s, ao Santo Esp\xEDrito,
Mil louvores por seu dom;
Por sua gra\xE7a t\xE3o imensa,
Gl\xF3ria a Ele, Ele \xE9 bom!
`,verseorder:4,ismainverse:0,id_hino:10},{idverse:24809,verse:`4. 
Gl\xF3ria, gl\xF3ria ao Pai bendito,
Gl\xF3ria, gl\xF3ria a Jesus;
Gl\xF3ria dai ao Santo Esp\xEDrito,
Ao Deus trino, nossa luz.`,verseorder:5,ismainverse:0,id_hino:10},{idverse:24810,verse:`1. 
\xD3 crist\xE3os, eia avante, eia avante!
Por Jesus e Sua igreja a lutar;
Co\`a palavra, essa espada flamante,
Vamos, vamos, irm\xE3os, pelejar.
`,verseorder:1,ismainverse:0,id_hino:11},{idverse:24811,verse:`\xD3 Crist\xE3o, sempre avante na lida!
O Evangelho com f\xE9 a pregar;
Ao trabalho o Senhor nos convida,
Vamos, pois, com Jesus pelejar.
`,verseorder:2,ismainverse:1,id_hino:11},{idverse:24812,verse:`2. 
\xD3 crist\xE3os, eia avante, eia avante!
Boa nova aos perdidos levar;
Por cidades e vilas adiante,
Salva\xE7\xE3o vamos n\xF3s proclamar.
`,verseorder:3,ismainverse:0,id_hino:11},{idverse:24813,verse:`3. 
\xD3 crist\xE3os, eia avante, eia avante!
Pois aurora j\xE1 vemos raiar,
Desse dia em que Cristo, triufante,
Neste mundo h\xE1 de sempre reinar.
`,verseorder:4,ismainverse:0,id_hino:11},{idverse:24814,verse:`4. 
\xD3 crist\xE3os, eia avante, eia avante!
Vexil\xE1rios da f\xE9 vamos ser;
E a vanguarda do ex\xE9rcito brilhante,
Que gloriosa vit\xF3ria h\xE1 de ter!
`,verseorder:5,ismainverse:0,id_hino:11},{idverse:24815,verse:`5. 
\xD3 crist\xE3os, eia avante, eia avante!
Com coragem, sem nada temer,
Com Jesus, divinal comandante,
N\xF3s havemos, irm\xE3os, de vencer.`,verseorder:6,ismainverse:0,id_hino:11},{idverse:24816,verse:`1. 
Pecador, v\xEA a luz, brilha para ti,
L\xE1 da cruz do Salvador,
Onde a vida deu; e do lado
Seu corre sangue redentor.
`,verseorder:1,ismainverse:0,id_hino:12},{idverse:24817,verse:`\xD3 vem j\xE1, como est\xE1s!
Vem agora, ao Salvador;
Sua vida deu, tudo padeceu;
N\xE3o demores! Vem j\xE1, pecador!
`,verseorder:2,ismainverse:1,id_hino:12},{idverse:24818,verse:`2. 
Na escurid\xE3o do Jardim orou
T\xE3o aflito meu Senhor;
Quando pranteou, sangue Seu suou,
No Gets\xEAmane em dor!
`,verseorder:3,ismainverse:0,id_hino:12},{idverse:24819,verse:`3. 
V\xEA o Salvador co\`as feridas m\xE3os
Te chamando, \xF3 pecador!
Vida eterna t\xEAm os que nele cr\xEAem
E aceitam o Seu amor.
`,verseorder:4,ismainverse:0,id_hino:12},{idverse:24820,verse:`4. 
Vem a Cristo, vem confessando, j\xE1,
Teus pecados, e te ouvir\xE1,
E no cora\xE7\xE3o sentir\xE1s o perd\xE3o,
Pois Jesus te perdoar\xE1.`,verseorder:5,ismainverse:0,id_hino:12},{idverse:24821,verse:`1. 
Jesus comprou-me do mundo aqui,
Palavras doces s\xE3o para mim;
Jesus comprou-me e vai me dar
Eterno gozo, celeste lar.
Jesus comprou-me e vai me dar
Eterno gozo, celeste lar.
`,verseorder:1,ismainverse:0,id_hino:13},{idverse:24822,verse:`2. 
Jesus comprou-me da escravid\xE3o,
A paz eu gozo por seu perd\xE3o;
Sou desprezado um pouco aqui,
Mas eu espero gozar ali.
Sou desprezado um pouco aqui,
Mas eu espero gozar ali.
`,verseorder:2,ismainverse:0,id_hino:13},{idverse:24823,verse:`3. 
Jesus comprou-me, e eu fiquei
P\`ra sempre livre da dura lei!
Agora espero at\xE9 Ele vir,
P\`ra que me leve ao Seu porvir.
Agora espero at\xE9 Ele vir,
P\`ra que me leve ao Seu porvir.
`,verseorder:3,ismainverse:0,id_hino:13},{idverse:24824,verse:`4. 
\xD3 vinde, amigos, e junto a n\xF3s,
Ouvi depressa do c\xE9u a voz;
- O que demora n\xE3o chegar\xE1,
Porque se perde e morrer\xE1.
- O que demora n\xE3o chegar\xE1,
Porque se perde e morrer\xE1.
`,verseorder:4,ismainverse:0,id_hino:13},{idverse:24825,verse:`5. 
Jesus comprou-me e me dar\xE1
Lugar \xE0s mesas, que esperam l\xE1.
Ouve o convite, \xF3 pecador,
Pois Jesus Cristo \xE9 o Salvador.
Ouve o convite, \xF3 pecador,
Pois Jesus Cristo \xE9 o Salvador.`,verseorder:5,ismainverse:0,id_hino:13},{idverse:24826,verse:`1. 
Oh! que gozo sinto em Jesus,
Que por mim expirou na cruz
A minh\`alma deu divina luz,
Gl\xF3ria a meu Jeus!
`,verseorder:1,ismainverse:0,id_hino:14},{idverse:24827,verse:`Quanta gl\xF3ria vejo em Jesus,
Gozo santo e bendita luz,
Cujos raios brilham l\xE1 na cruz,
Na qual morreu Jesus.
Sempre avante quero eu andar,
T\xE9 no c\xE9u chegar.
`,verseorder:2,ismainverse:1,id_hino:14},{idverse:24828,verse:`2. 
Livre do pecado ja estou,
Do abismo me retirou,
Sobre a rocha eterna me firmou;
Gl\xF3ria a meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:14},{idverse:24829,verse:`3. 
Divinal louvor Jesus me deu;
Harmonia que vem do c\xE9u;
Cantarei, alegre, Cristo \xE9 meu,
Gl\xF3ria a meu Jesus!
`,verseorder:4,ismainverse:0,id_hino:14},{idverse:24830,verse:`4. 
Esta nova hei de ploclamar;
Muitos h\xE3o de assim chegar,
E a gl\xF3ria receber, sem par,
Gl\xF3ria a meu Jesus!`,verseorder:5,ismainverse:0,id_hino:14},{idverse:24831,verse:`1. 
Oh! Qu\xE3o cego andei, e perdido vaguei
Longe, longe do Meu Salvador
Mas do C\xE9u Ele desceu,
e Seu Sangue Verteu
Pra Salvar um t\xE3o pobre pecador.
`,verseorder:1,ismainverse:0,id_hino:15},{idverse:24832,verse:`Foi na Cruz, foi na Cruz,
Onde um dia eu vi
Meu pecado castigado em Jesus;
Foi ali pela f\xE9, que os olhos abri,
E agora me alegro em sua Luz.
`,verseorder:2,ismainverse:1,id_hino:15},{idverse:24833,verse:`2. 
Eu ouvia falar, Nessa Gra\xE7a sem Par
Que do C\xE9u trouxe, Nosso Jesus
Mas eu surdo me fiz, Converter-me n\xE3o quis
Ao Senhor, que por mim morreu na Cruz.
`,verseorder:3,ismainverse:0,id_hino:15},{idverse:24834,verse:`3. 
Mas um dia senti meu pecado e vi
Sobre mim a Espada da Lei
Apressado fugi, em Jesus me escondi
E abrigo seguro nele Achei
`,verseorder:4,ismainverse:0,id_hino:15},{idverse:24835,verse:`4. 
Qu\xE3o ditoso, ent\xE3o, este meu Cora\xE7\xE3o
Conhecendo o excelso Amor
Que levou meu Jesus, a sofrer l\xE1 na Cruz
Pra Salvar um t\xE3o pobre pecador.`,verseorder:5,ismainverse:0,id_hino:15},{idverse:24836,verse:`1. 
Posso tendo as m\xE3os vazias,
Com Jesus eu me encontrar?
Nada fiz, e v\xE3o-se os dias,
Que lhe posso apresentar?
`,verseorder:1,ismainverse:0,id_hino:16},{idverse:24837,verse:`Posso tendo as m\xE3os vazias,
Com Jesus, eu me encontrar?
Quantas almas poderia
Ao Senhor apresentar?
`,verseorder:2,ismainverse:1,id_hino:16},{idverse:24838,verse:`2. 
N\xE3o mais temerei a morte;
Vencerei por salvo estar;
Qual ser\xE1 a minha sorte,
Se no c\xE9u vazio entrar?
`,verseorder:3,ismainverse:0,id_hino:16},{idverse:24839,verse:`3. 
No celeste lar entrando,
Como irei ao Salvador?
Quantas almas irei levando.
Para meu fiel Senhor?
`,verseorder:4,ismainverse:0,id_hino:16},{idverse:24840,verse:`4. 
Do pecado, preso em elos,
Passei anos em v\xE3o labor;
Quem me dera reav\xEA-los,
P\`ra servir ao meu Senhor.
`,verseorder:5,ismainverse:0,id_hino:16},{idverse:24841,verse:`5. 
Despertamos, j\xE1 \xE9 dia;
Trabalhemos, com fervor;
E levemos, com alegria,
Muitas almas ao Senhor.`,verseorder:6,ismainverse:0,id_hino:16},{idverse:24842,verse:`1. 
Nas horas que passo pensando em Jesus
As trevas desfa\xE7o, busacando a luz;
Que horas de vida, t\xE3o doces pra mim,
Jesus me convida, que eu suba p\`ra si!
`,verseorder:1,ismainverse:0,id_hino:17},{idverse:24843,verse:`2. 
Da vida voando, sem nenhum temor;
Acima buscando do v\xE9u o amor;
Que doce ventura, que aspecto feliz,
Que nova natura minh"alma bendiz.
`,verseorder:2,ismainverse:0,id_hino:17},{idverse:24844,verse:`3. 
Do mar o bramido, da brisa o langor;
Da ave o carpido de doce amor;
Me falam sentidos acordes dos c\xE9us,
Me trazem aos ouvidos os hinos de Deus!
`,verseorder:3,ismainverse:0,id_hino:17},{idverse:24845,verse:`4. 
Minh\`alma ansiosa j\xE1 que percorrer
A senda gloriosa que eu hei de ver;
Que coisa t\xE3o bela; oh! que luz sem v\xE9u!
Jesus me revela mist\xE9rios do c\xE9u.`,verseorder:4,ismainverse:0,id_hino:17},{idverse:24846,verse:`1. 
Grata nova Deus proclama
Hoje, ao mundo pecador!
Doce nova revelada,
L\xE1 na cruz do Salvador;
Cego e desviado, o homem,
Dos caminhos do Senhor,
Desconhece e desconfia
Deste Deus, o Deus de amor.
`,verseorder:1,ismainverse:0,id_hino:18},{idverse:24847,verse:`Grata nova, doce nova,
Vem dos l\xE1bios do Senhor;
Escutai com alegria:
"Deus \xE9 luz, Deus \xE9 amor".
`,verseorder:2,ismainverse:1,id_hino:18},{idverse:24848,verse:`2. 
Com ofertas e obras mortas,
Sacrif\xEDcios sem valor,
Enganado, pensa o homem,
Propiciar Seu Criador,
Clama, roga em seu favor,
A supostos madiadores,
Desprezando o Deus de amor.
`,verseorder:3,ismainverse:0,id_hino:18},{idverse:24849,verse:`3. 
Luz divina, respladece!
Mostra ao triste pecador,
Que na cruz est\xE3o unidos
A justi\xE7a e o amor.
Fala aos cora\xE7\xF5es feridos,
Mostra-te, Deus Salvador;
E sem fim, proclamaremos:
"Deus \xE9 luz, Deus \xE9 amor!"`,verseorder:4,ismainverse:0,id_hino:18},{idverse:24850,verse:`1. 
Eis, a voz de Cristo clama,
Sobre o revoltoso mar
Desta vida perigosa:
"Vinde em Mim vos abrigar".
`,verseorder:1,ismainverse:0,id_hino:19},{idverse:24851,verse:`2. 
Quando outrora em Galil\xE9ia,
Essa doce voz soou,
Logo, o cora\xE7\xE3o ouvinte,
Tudo, pronto, abandonou.
`,verseorder:2,ismainverse:0,id_hino:19},{idverse:24852,verse:`3. 
Sobre s vozes sedutoras,
De mudanas atra\xE7\xF5es,
Clama a meiga voz de Cristo:
"Dai-me vossos cora\xE7\xF5es!"
`,verseorder:3,ismainverse:0,id_hino:19},{idverse:24853,verse:`4. 
Quer no meio dos prazeres,
Quer no dia da afli\xE7\xE3o,
Inda clama, paciente:
"Amas-Me tu, \xF3 crist\xE3o?"
`,verseorder:4,ismainverse:0,id_hino:19},{idverse:24854,verse:`5. 
Na vereda estreita e santa,
J\xE1 pisada por Jesus,
A segui-lo, Ele te chama:
"Vem, crist\xE3o, andar na luz!"
`,verseorder:5,ismainverse:0,id_hino:19},{idverse:24855,verse:`6. 
Dessa cruz angustiosa,
Onde brilha Seu amor,
Ouve a voz que te convida,
Ouve e segue, pecador!`,verseorder:6,ismainverse:0,id_hino:19},{idverse:24856,verse:`1. 
Livres de pecado v\xF3s quereis ficar?
Olhai p\`ra o Cordeiro de Deus!
Ele morto foi na cruz, p\`ra vos salvar;
Olhai p\`ra o Cordeiro de Deus!
`,verseorder:1,ismainverse:0,id_hino:20},{idverse:24857,verse:`Olhai p\`ra o Cordeiro de Deus,
Olhai p\`ra o Cordeiro de Deus,
Porque s\xF3 Ele vos pode salvar.
Olhai p\`ra o Cordeiro de Deus!
`,verseorder:2,ismainverse:1,id_hino:20},{idverse:24858,verse:`2. 
Se estais em tentados, em hesita\xE7\xE3o,
Olhai p\`ra o Cordeiro de Deus!
Ele encher\xE1 o vosso cora\xE7\xE3o.
Olhai p\`ra o Cordeiro de Deus!
`,verseorder:3,ismainverse:0,id_hino:20},{idverse:24859,verse:`3. 
Se estais cansados e sem mais vigor,
Olhai p\`ra o Cordeiro de Deus!
Ele vos quer dar Seu divinal amor,
Olhai p\`ra o Cordeiro de Deus!
`,verseorder:4,ismainverse:0,id_hino:20},{idverse:24860,verse:`4. 
Se na vossa senda sombras v\xEAm cair,
Olhai p\`ra o Cordeiro de Deus!
Ele, com sua gra\xE7a, tudo quer suprir.
Olhai p\`ra o Cordeiro de Deus!`,verseorder:5,ismainverse:0,id_hino:20},{idverse:24861,verse:`1. 
A gloriosa aurora vem,
Do dia em que l\xE1 em Bel\xE9m
Nasceu de Deus, o Filho;
Que belo dia e de luz,
No qual p\`ra n\xF3s desceu Jesus,
Do c\xE9u mostrando o trilho.
Quantos santos hoje lembram,
E relembram a hist\xF3ria
De Jesus, o Rei da Gl\xF3ria!
`,verseorder:1,ismainverse:0,id_hino:21},{idverse:24862,verse:`2. 
Nasceu Jesus l\xE1 em Bel\xE9m,
Trazendo-nos o sumo bem
Do c\xE9u -a  liberdade;
Nos concedeu perfeita paz,
Consola\xE7\xE3o que satisfaz
Por toda a eternidade!
Como fomos desgarraidos,
Desviados e perdidos!
Hoje somos redimidos!
`,verseorder:2,ismainverse:0,id_hino:21},{idverse:24863,verse:`3. 
Conosco Cristo habitar\xE1
Com Seu poder nos levar\xE1,
Nos dando livramento;
E sempre h\xE1 de nos guiar
Pois Sua gra\xE7a vai nos dar
Em todo o sofrimento,
Scuta: Luta sempre forte,
T\xE9 a morte, pois ajuda
Tens em Cristo, que n\xE3o muda!
`,verseorder:3,ismainverse:0,id_hino:21},{idverse:24864,verse:`4. 
Jesus desceu p\`ra nos salvar,
O sol da gra\xE7a vem brilhar,
Alegres jubilemos;
Jesus, \xF3 nosso bom Pastor,
Vem nos guiar com teu amor;
A Ti n\xF3s pertecemos;
Almas salvas, perdoadas,
Libertadas hoje oram,
Reverentes te adoram!`,verseorder:4,ismainverse:0,id_hino:21},{idverse:24865,verse:`1. 
Em nossa alma n\xF3s tomamos
O Teu corpo, \xF3 Jesus!
Como oferta volunt\xE1ria,
Que nos deste l\xE1 na cruz.
`,verseorder:1,ismainverse:0,id_hino:22},{idverse:24866,verse:`Como p\xE3o do c\xE9u descente,
Vamos a Ti receber,
Pois unidos ao Teu corpo,
N\xF3s queremos, sim, viver.
`,verseorder:2,ismainverse:1,id_hino:22},{idverse:24867,verse:`2. 
Em figura, o Teu sangue
N\xF3s bebemos, \xF3 Senhor,
Pois s\xF3 nEle h\xE1 virtude
P\`ra salvar o pecador.
`,verseorder:3,ismainverse:0,id_hino:22},{idverse:24868,verse:`3. 
Vem, concede \xE0 Tua Igreja
O perd\xE3o do Teu amor,
Para que nos dois emblemas,
Contemplemos-te, Senhor.
`,verseorder:4,ismainverse:0,id_hino:22},{idverse:24869,verse:`4. 
\xD3 Senhor, por Teu Esp\xEDrito
Em n\xF3s vem j\xE1 residir,
P\`ra Teu corpo e Teu sangue
N\xF3s podermos discernir.`,verseorder:5,ismainverse:0,id_hino:22},{idverse:24870,verse:`1. 
Cantam os santos com fervor,
A quem por mim foi imolado,
Na cruz, sofrendo o meu pecado.
`,verseorder:1,ismainverse:0,id_hino:23},{idverse:24871,verse:`2. 
Gl\xF3ria a Jesus!
Gl\xF3ria a Jesus!
Gl\xF3ria a Jesus, o Salvador!
`,verseorder:2,ismainverse:0,id_hino:23},{idverse:24872,verse:`3. 
Deus \xE9 o Justificador
De quem crer no Seu Enviado
Sim, no Seu Filho mui amado.
`,verseorder:3,ismainverse:0,id_hino:23},{idverse:24873,verse:`4. 
Do Seu Pai recebeu louvor,
E com poder doi exaltado,
Por isso \xE9 glorificado.
`,verseorder:4,ismainverse:0,id_hino:23},{idverse:24874,verse:`5. 
Do "sangue pufificador"
Eu quero sempre ter lembran\xE7a,
E nEle ter mais confian\xE7a.
`,verseorder:5,ismainverse:0,id_hino:23},{idverse:24875,verse:`6. 
Por n\xF3s, na cruz, sofreu a dor,
Com sangue fomos n\xF3s comprados,
E do pecado resgatados.`,verseorder:6,ismainverse:0,id_hino:23},{idverse:24876,verse:`1. 
No Pentecostes sucedeu
O que Jesus falou,
Pois de repente l\xE1 do c\xE9u
Um vento assoprou,
Que veio a casa toda encher
E os cora\xE7\xF5es com muito poder.
`,verseorder:1,ismainverse:0,id_hino:24},{idverse:24877,verse:`Poder, poder, poder pentecostal.
\xD3 vem nos inflamar,
Tamb\xE9m nos renovar;
\xD3 vem, sim, vem, \xF3 chama divinal,
Teus servos batizar.
`,verseorder:2,ismainverse:1,id_hino:24},{idverse:24878,verse:`2. 
Em cada um veio repousar
A preciosa luz,
O preceptor que veio fiacr
Co\`os servos de Jesus;
Foi o fogo santo do Senhor,
Que os encheu com seu vigor.
`,verseorder:3,ismainverse:0,id_hino:24},{idverse:24879,verse:`3. 
E come\xE7am a falar,  Repletos de poder,
Em outras l\xEDnguas exaltar
Ao ricos dons do seu amor
E o poder consolador.
`,verseorder:4,ismainverse:0,id_hino:24},{idverse:24880,verse:`4. 
E quem deseja receber
Esta \xE1gua salutar,
Que \xE9 prometida ao que crer
E humilde esperar,
Perseverando em ora\xE7\xE3o,
Ter\xE1 poder seu cora\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:24},{idverse:24881,verse:`1. 
Meu Jesus, Tu \xE9s bom;
Tu \xE9s tudo p\`ra mim!
Foste morto, mas vivex em mim;
Tu mereces louvor,
\xD3 cordeiro de Deus!
Tu \xE9s tudo, sim, tudo p\`ra mim!
`,verseorder:1,ismainverse:0,id_hino:25},{idverse:24882,verse:`2. 
Quero a Ti dar louvor,
Pois \xE9s Rei sobre reis;
Tu \xE9s tu do, mas nada eu sou;
Enche meu cora\xE7\xE3o
De ternura e paz;
Honra eterna e gl\xF3ria Te dou.
`,verseorder:2,ismainverse:0,id_hino:25},{idverse:24883,verse:`3. 
O Teu nome \xE9 amor,
Pois tu amas a mim,
E eu quero andar neste amor;
Triufante estou,
Confiando em Ti,
Meu Jesus, grande consolador.
`,verseorder:3,ismainverse:0,id_hino:25},{idverse:24884,verse:`4. 
\xD3 ungido de Deus,
Trazes paz e perd\xE3o;
Salva\xE7\xE3o, vida, tens para os teus;
Que mensagem do c\xE9u
E de transforma\xE7\xE3o,
Enviada do trono de Deus!`,verseorder:4,ismainverse:0,id_hino:25},{idverse:24885,verse:`1. 
Qu\xE3o glorioso crist\xE3o, \xE9 pensares
Na cidade que n\xE3o tem igual,
Onde os muros s\xE3o de puro jaspe,
E as ruas de ouro e cristal;
Pensa como ser\xE1 glorioso
Ver-se a triunfal multid\xE3o,
Que cantando, aguarda a chegada
Doss que vencem a tribula\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:26},{idverse:24886,verse:`2. 
Pensa como ser\xE1 glorioso
Ver o rio da vida e luz,
Cujas as margens juncadas de l\xEDrios,
S\xE3o a gl\xF3ria de nosso Jesus;
Haver\xE1 l\xE1 perp\xE9tua aurora,
Pois Deus mesmo a alumiar\xE1;
E o cordeiro, com Sua esposa,
Noite e dia resplandecer\xE1.
`,verseorder:2,ismainverse:0,id_hino:26},{idverse:24887,verse:`3. 
Pensa na celestial melodia
Que a terra encher\xE1, de Beul\xE1;
E das harpas a doce harmonia
Ao passar o Jord\xE3o se ouvir\xE1.
Mesmo em dores que levam \xE0 morte,
S\xEA constante, n\xE3o voltes atr\xE1s,
Tua heran\xE7a, tua eterna sorte,
\xC9 Jesus, o Fiel, o Veraz.
`,verseorder:3,ismainverse:0,id_hino:26},{idverse:24888,verse:`4. 
Se \xE9 glorioso pensar nas grandezas,
Nos prazeres que acodem aqui,
Qual ser\xE1 desfrutar as riquezas
Que esperam os salvos, ali?
Os encantos do mundo n\xE3o podem
Ofuscar essa gl\xF3ria dal\xE9m;
N\xE3o almejas viver, \xF3 amigo,
Nessa formosa Jerusal\xE9m?`,verseorder:4,ismainverse:0,id_hino:26},{idverse:24889,verse:`1. 
Amor, que por amor desceste!
Amor, que por amor morreste!
Ah! quanta dor n\xE3o padeceste,
Meu cora\xE7\xE3o p\`ra conquistar,
E meu amor ganhar!
`,verseorder:1,ismainverse:0,id_hino:27},{idverse:24890,verse:`2. 
Amor, que com amor seguias!
A mim, que sem amor tu vias!
Oh! quanto amor por mim sentias,
Meu Salvador, meu bom Jesus,
Sofrendo sobre a cruz!
`,verseorder:2,ismainverse:0,id_hino:27},{idverse:24891,verse:`3. 
Amor, que tudo me perdoas!
Amor, que at\xE9 mesmo aben\xE7oas!
Um r\xE9u de quem Te afei\xE7oas!
Por ti vencido, \xF3 Salvador,
Eis-me aos teus p\xE9s, Senhor!
`,verseorder:3,ismainverse:0,id_hino:27},{idverse:24892,verse:`4. 
Amor, que nunca, nunca mudas,
Que nos Teus bra\xE7os me seguras,
Cercando-me de mil venturas!
Aceita agora, Salvador,
O meu humilde amor!`,verseorder:4,ismainverse:0,id_hino:27},{idverse:24893,verse:`1. 
Andas carregado de tristeza e dor,
Sem nemhum aux\xEDlio, nem um Salvador?
Ouve a mensagem de teu bom Jesus,
E tem mais coragem, leva a tua cruz.
`,verseorder:1,ismainverse:0,id_hino:28},{idverse:24894,verse:`Deus vai te guiar com Sua
forte m\xE3o;
Podes descansar na tribula\xE7\xE3o;
Seja tua vida livre de pesar;
Em tristeza e lida Deus vai te guiar.
`,verseorder:2,ismainverse:1,id_hino:28},{idverse:24895,verse:`2. 
Toda carga por ti, Ele quer levar;
Alma dolorida, ouve o Seu falar;
Senda espinhosa Ele j\xE1 andou,
Morte afrontosa por ti suportou.
`,verseorder:3,ismainverse:0,id_hino:28},{idverse:24896,verse:`3. 
Se est\xE1s tentado, Deus te ajudar\xE1,
Sempre confiando, te libertar\xE1;
As pisadas segue do teu Salvador,
Crendo em Deus, prossegue, seja como for.`,verseorder:4,ismainverse:0,id_hino:28},{idverse:24897,verse:`1. 
Oh! que precioso sangue,
Meu Senhor verteu,
Quando, para resgatar-nos,
Padeceu!
`,verseorder:1,ismainverse:0,id_hino:29},{idverse:24898,verse:`2. 
Oh! que precioso sangue,
Sangue de Jesus,
Que por n\xF3s, foi derramado
Sobre a cruz!
`,verseorder:2,ismainverse:0,id_hino:29},{idverse:24899,verse:`3. 
Oh! que precioso sangue,
Sangue remidor!
Sim, com este nos remiste,
Redentor!
`,verseorder:3,ismainverse:0,id_hino:29},{idverse:24900,verse:`4. 
Oh! que precioso sangue,
Sangue expiador!
Eis que da pena livra
O malfeitor!
`,verseorder:4,ismainverse:0,id_hino:29},{idverse:24901,verse:`5. 
Oh! que precioso sangue,
Purificador!
Que de toda a mancha lava
O pecador!
`,verseorder:5,ismainverse:0,id_hino:29},{idverse:24902,verse:`6. 
Oh! que precioso sangue,
Fala-nos de paz;
Tudo quanto a lei exige,
Satisfaz!
`,verseorder:6,ismainverse:0,id_hino:29},{idverse:24903,verse:`7. 
Oh! que precioso sangue!
Por Ele entrarei
Sem receio, na presen\xE7a
Do meu Rei!
`,verseorder:7,ismainverse:0,id_hino:29},{idverse:24904,verse:`8. 
Oh! que precioso sangue,
Do bom Salvador!
Hoje, a todos manifesta
Seu amor!`,verseorder:8,ismainverse:0,id_hino:29},{idverse:24905,verse:`1. 
N\xE3o sou meu! Por Cristo salvo,
Que por mim morreu na cruz,
Eu confesso alegremente,
Que perten\xE7o ao bom Jesus.
`,verseorder:1,ismainverse:0,id_hino:30},{idverse:24906,verse:`N\xE3o sou meu! oh! N\xE3o sou meu!
Bom Jesus, sou todo Teu!
Hoje mesmo e para sempre,
Bom Jesus, sou  todo Teu!
`,verseorder:2,ismainverse:1,id_hino:30},{idverse:24907,verse:`2. 
N\xE3o sou meu! Por Ele remido,
Quando o sangue derramou;
Na sua gra\xE7a confiando,
Que minh\`alma resgatou.
`,verseorder:3,ismainverse:0,id_hino:30},{idverse:24908,verse:`3. 
Jamais meu! A ti confio
Tudo quanto chamo meu;
Tudo nas Tuas m\xE3os entrego;
Meu Senhor, sou todo teu!
`,verseorder:4,ismainverse:0,id_hino:30},{idverse:24909,verse:`4. 
Jamais meu! \xD3 santifica
Tudo quanto sou, Senhor!
Da vaidade e da soberba,
Vem livrar-me, Salvador.`,verseorder:5,ismainverse:0,id_hino:30},{idverse:24910,verse:`1. 
Por mim sofreu o Salvador,
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
Louvai, comigo, ao Redentor!
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
`,verseorder:1,ismainverse:0,id_hino:31},{idverse:24911,verse:`Jesus! Jesus! \xD3 Salvador!
\xC9 doce o nome do Senhor;
Abrasa-me com santo amor;
Gl\xF3ria! Gl\xF3ria ao meu Senhor.
`,verseorder:2,ismainverse:1,id_hino:31},{idverse:24912,verse:`2. 
C\`os meus pecados carregou,
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
E sobre a cruz me resgatou.
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:31},{idverse:24913,verse:`3. 
Eu sei que perdoado estou,
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
E, com certeza, ao c\xE9u eu vou!
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
`,verseorder:4,ismainverse:0,id_hino:31},{idverse:24914,verse:`4. 
E, quando a guerra aqui findar,
Gl\xF3ria! Gl\xF3ria ao meu Jesus!
No c\xE9u, melhor, eu vou cantar,
Gl\xF3ria! Gl\xF3ria ao meu Jesus!`,verseorder:5,ismainverse:0,id_hino:31},{idverse:24915,verse:`1. 
Quem do c\xE9u por mim desceu,
Tudo em meu lugar sofreu,
E por mim na cruz morreu?
Foi Cristo! Foi  Cristo!
`,verseorder:1,ismainverse:0,id_hino:32},{idverse:24916,verse:`Os meus pecados expiou:
De toda a pena me livrou!
Da maldi\xE7\xE3o me resgatou,
Meu cristo! Meu Cristo!
`,verseorder:2,ismainverse:1,id_hino:32},{idverse:24917,verse:`2. 
Quem me trouxe, qual Pastor,
Seu cordieo com amor?
Quem quis ser meu Salvador?
Foi Cristo! Foi  Cristo!
`,verseorder:3,ismainverse:0,id_hino:32},{idverse:24918,verse:`3. 
Quem com branda compaix\xE3o,
Derreteu meu cora\xE7\xE3o?
Deu-me plena salva\xE7\xE3o?
Foi Cristo! Foi  Cristo!
`,verseorder:4,ismainverse:0,id_hino:32},{idverse:24919,verse:`4. 
Quem diz: "N\xE3o te deixarei,
Nem te desampararei,
Sempre te socorrerei?"
\xC9 Cristo! \xC9 Cristo!
`,verseorder:5,ismainverse:0,id_hino:32},{idverse:24920,verse:`5. 
Quem \xE9 digno de louvor?
Quem merece o meu amor?
\xC9 Jesus, meu Salvador,
Meu Cristo! Meu Cristo!
`,verseorder:6,ismainverse:0,id_hino:32},{idverse:24921,verse:`6. 
As minhas trevas dissipou!
Minha alma enferma Ele sarou!
Meu cora\xE7\xE3o Ele alegrou!
Meu Cristo! Meu Cristo!`,verseorder:7,ismainverse:0,id_hino:32},{idverse:24922,verse:`1. 
Com Tua m\xE3o, segura bem a minha,
Pois eu t\xE3o fraco sou, \xF3 Salvador!
Que n\xE3o me atrevo a dar nem um s\xF3 passo
Sem  Teu amparo, meu Jesus Senhor!
`,verseorder:1,ismainverse:0,id_hino:33},{idverse:24923,verse:`2. 
Com Tua m\xE3o, segura bem a minha,
E mais e mais unido a Ti, Jesus.
\xD3 Traze-me, que nunca me desvie
De ti, Senhor, a minha vida e luz!
`,verseorder:2,ismainverse:0,id_hino:33},{idverse:24924,verse:`3. 
Com Tua m\xE3o, segura bem a minha,
E, pelo mundo, alegre seguirei;
Mesmo onde as sombras caem mais escuras
Teu rosto vendo, nada temerei.
`,verseorder:3,ismainverse:0,id_hino:33},{idverse:24925,verse:`4. 
E, se chegar \xE0 beira desse rio,
Que tu por mim quiseste atravessar,
Com Tua m\xE3o segura bem a minha,
E sobre a morte eu hei de triunfar.
`,verseorder:4,ismainverse:0,id_hino:33},{idverse:24926,verse:`5. 
Quando voltares esses c\xE9us rompendo,
Segura bem a minha m\xE3o, Senhor,
E, meu Jesus, \xF3 leva-me contigo,
Para onde eu goze Teu eterno amor.`,verseorder:5,ismainverse:0,id_hino:33},{idverse:24927,verse:`1. 
Milhares de milhares em refulgente luz!
Eis os guerreiros santos, mil\xEDcia de Jesus!
Completa, sim, completa sua longa luta aqui,
Com Cristo, seu Senhor e Rei, v\xE3o descansar ali;
`,verseorder:1,ismainverse:0,id_hino:34},{idverse:24928,verse:`2. 
Que doces sinfonias enchem a terra e o c\xE9u!
Que coros d\`aleluias rompem al\xE9m do v\xE9u!
\xC9 que chegou o dia, o dia triunfal,                                
E Cristo reinar\xE1, enfim, em gl\xF3ria divinal.                              
`,verseorder:2,ismainverse:0,id_hino:34},{idverse:24929,verse:`3. 
Ent\xE3o, n\xE3o h\xE1 mais choro, nem h\xE1 mais tenta\xE7\xE3o;
As dores, as tristezas p\`ra sempre fugir\xE3o,   
E os remidos todos ver\xE3o seu Salvador;
E Transformados,  todos, ser\xE3o como o Senhor.`,verseorder:3,ismainverse:0,id_hino:34},{idverse:24930,verse:`1. 
O grande amor de meu Jesus
Por mim morrendo sobre a cruz,
Da perdi\xE7\xE3o, p\`ra me salvar;
Quem poder\xE1 contar?
`,verseorder:1,ismainverse:0,id_hino:35},{idverse:24931,verse:`Quem pode o seu amor contar?
Quem pode o seu amor contar?
O grande amor do Salvador,
Quem poder\xE1 contar?
`,verseorder:2,ismainverse:1,id_hino:35},{idverse:24932,verse:`2. 
O c\xE1lice que Jesus bebeu,
A maldi\xE7\xE3o que padeceu,
Tudo por mim, p\`ra me salvar,
Quem poder\xE1 contar?
`,verseorder:3,ismainverse:0,id_hino:35},{idverse:24933,verse:`3. 
A zombaria t\xE3o cruel,
A cruz sangrenta, o amargo fel,
Que Ele sofreu p\`ra me salvar;
Quem poder\xE1 contar?
`,verseorder:4,ismainverse:0,id_hino:35},{idverse:24934,verse:`4. 
Incompar\xE1vel Salvador!
Qu\xE3o inef\xE1vel Teu amor!
Qu\xE3o imposs\xEDvel de sondar!
Imenso e sem par!`,verseorder:5,ismainverse:0,id_hino:35},{idverse:24935,verse:`1. 
Da linda p\xE1tria estou bem longe;
Cansado estou;
Eu tenho de Jesus saudade,
Oh, quando \xE9 que eu vou?
Passarinhos, belas flores,
Querem m\`encantar;
S\xE3o v\xE3os terrestres esplendores,
Mas contemplo o meu lar.
`,verseorder:1,ismainverse:0,id_hino:36},{idverse:24936,verse:`2. 
Jesus me deu a Sua promessa;
Me vem buscar;
Meu cora\xE7\xE3o est\xE1 com pressa,
Eu quero j\xE1 voar.
Meus pecados foram muitos,
Mui culpado sou;
Por\xE9m, Seu sangue p\xF5e-me limpo;
Eu para p\xE1tria vou.
`,verseorder:2,ismainverse:0,id_hino:36},{idverse:24937,verse:`3. 
Qual filho de seu lar saudoso,
Eu quero ir;
Qual passarinho para o ninho,
P\`ra os bra\xE7os Seu fugir;
\xC9 fiel - Sua vinda \xE9 certa,
Quando... Eu n\xE3o sei.
Mas Ele manda estar alerta;
Do ex\xEDlio voltarei.
`,verseorder:3,ismainverse:0,id_hino:36},{idverse:24938,verse:`4. 
Sua vinda aguardo eu cantado;
Meu lar no c\xE9u;
Seus passos hei de ouvir soando
Al\xE9m do escuro v\xE9u.
Passarinhos, belas flores,
Querem m\`encantar;
S\xE3o v\xE3os terrestres esplendores,
Mas contemplo o meu lar.`,verseorder:4,ismainverse:0,id_hino:36},{idverse:24939,verse:"1. \nOh! que descanso em Jesus encontrei!\nCristo p`ra mim! Cristo p`ra mim!\nOh! que tesouros infindos achei!\nCristo p`ra mim! Cristo p`ra mim!\n`Scolham os outros o mundo p`ra si:\nBusquem riquezas, del\xEDcias aqui;\nEu `scolherei, \xF3 Jesus, sempre a Ti!\nCristo p`ra mim! Cristo p`ra mim!\n",verseorder:1,ismainverse:0,id_hino:37},{idverse:24940,verse:`2. 
Quer na afli\xE7\xE3o, na doen\xE7a ou na dor:
Cristo p\`ra mim! Cristo p\`ra mim!
Quer na sa\xFAde, na for\xE7a ou vigor;
Cristo p\`ra mim! Cristo p\`ra mim!
Sempre ao meu lado p\`ra me socorrer,
Com seu amor, sim, e com Seu poder;
Em cada transe pronto a me valer;
Cristo p\`ra mim! Cristo p\`ra mim!
`,verseorder:2,ismainverse:0,id_hino:37},{idverse:24941,verse:`3. 
No dia amargo da persegui\xE7\xE3o:
Cristo p\`ra mim! Cristo p\`ra mim!
Nas duras provas e na tenta\xE7\xE3o:
Cristo p\`ra mim! Cristo p\`ra mim!
Ele, o pecado e o mundo venceu
Quando, por mim, no calv\xE1rio morreu;
E da vit\xF3ria a certeza me deu;
Cristo p\`ra mim! Cristo p\`ra mim!
`,verseorder:3,ismainverse:0,id_hino:37},{idverse:24942,verse:`4. 
Quando no vale da morte eu entrar,
Cristo p\`ra mim! Cristo p\`ra mim!
Quando perante meu Deus m\`encontrar:
Cristo p\`ra mim! Cristo p\`ra mim!
S\xF3 no Teu sangue confio, Senhor,
S\xF3 no Teu sangue imut\xE1vel amor!
Inda outra vez cantarei, Salvador:
Cristo p\`ra mim! Cristo p\`ra mim!`,verseorder:4,ismainverse:0,id_hino:37},{idverse:24943,verse:`1. 
Proclamai a todo o mundo que o Senhor \xE9 Rei!
Proclamaii! Proclamai!
Proclamai que suave e doce \xE9 Sua santa lei!
Proclamai! Proclamai!
Proclamai como Ele se chama Salvador Jesus
Como Ele por n\xF3s morreu na ensang\xFCentada cruz,
Como Ele - o Cordeiro - sobre o trono est\xE1
O Deus de toda gra\xE7a, que de gra\xE7a tudo d\xE1.
`,verseorder:1,ismainverse:0,id_hino:38},{idverse:24944,verse:`Proclamai a todo o
mundo
Que o Senhor \xE9 Rei!
Proclamai! Proclamai!
Proclamai que suave e doce
\xC9 sua santa lei!
Proclamai! Proclamai!
`,verseorder:2,ismainverse:1,id_hino:38},{idverse:24945,verse:`2. 
Proclamai que reina em gra\xE7a nosso Salvador!
Proclamai! Proclamai!
Que por cetro de Seu reino ele tem o amor!
Proclamai! Proclamai!
Anunciai aos tristes que Ele vive l\xE1;
E a todos os cansados que descanso Ele d\xE1;
Contai aos pecadores que Ele veio salvar,
E a todos os cativos que Ele os pode libertar.
`,verseorder:3,ismainverse:0,id_hino:38},{idverse:24946,verse:`3. 
Proclamai que Ele, do c\xE9u em breve descer\xE1!
Proclamai! Proclamai!
E com todos os Seus santos aparecer\xE1!
Proclamai! Proclamai!
Que, sem demora, venham todos se render
E, com amor, em tudo a Cristo obedecer
Que estejam todos prontos quando Ele voltar,
E alegres, naquele dia, "Rei dos reis", o aclamar`,verseorder:4,ismainverse:0,id_hino:38},{idverse:24947,verse:`1. 
Bendito seja o Cordeiro
Que na cruz por n\xF3s padeceu!
Bendito seja o seu sangue,
Que por n\xF3s, ali Ele verteu!
Eis nesse sangue lavados,
Com roupas que t\xE3o alvas s\xE3o,
Os pecadores redimidos,
Que perante seu Deus ja est\xE3o!
`,verseorder:1,ismainverse:0,id_hino:39},{idverse:24948,verse:`Alvo mais que a neve!
Alvo mais que a neve!
Sim, nesse sangue lavado,
Mais alvo que a neve serei.
`,verseorder:2,ismainverse:1,id_hino:39},{idverse:24949,verse:`2. 
Qu\xE3o espinhosa a coroa
Que Jesus por n\xF3s suportou!
Oh! qu\xE3o profundas as chagas
Que nos provam o quanto Ele amou!
Eis nessas chagas pureza
Para o mais  pecador!
Pois que mais alvos que a neve
O Teu sangue nos torna, Senhor!
`,verseorder:3,ismainverse:0,id_hino:39},{idverse:24950,verse:`3. 
Se n\xF3s a Ti confessarmos,
E seguirmos na Tua luz,
Tu n\xE3o somente perdoas,
Purificas tamb\xE9m, \xF3 Jesus;
Sim, e de todo o pecado!
Que maravilha de amor!
Pois que mais alvos que a neve
O Teu sangue nos torna, Senhor.`,verseorder:4,ismainverse:0,id_hino:39},{idverse:24951,verse:`1. 
Que se diz os teus encantos,
\xD3 cidade do bom Deus!
Que soberba te levantas
Entre as gl\xF3rias desses C\xE9us,
Desde o mundo preparada
Para Cristo e para os seus.
`,verseorder:1,ismainverse:0,id_hino:40},{idverse:24952,verse:`2. 
S\xE3o de jaspe adamantino
Os teus muros, \xF3 Si\xE3o!
S\xE3o douradas essas ruas,
Que os remidos pisar\xE3o!
De celste luz banhadas,
Refulgentes sempre est\xE3o!
`,verseorder:2,ismainverse:0,id_hino:40},{idverse:24953,verse:`3. 
Pelo pr\xF3prio Deus fundada
Sobre a rocha secular
Qu\xE3o seguras tuas portas!
Quem as pode amea\xE7ar?
Igualmente, qu\xE3o seguro
Quem por ela l\xE1 entrar!
`,verseorder:3,ismainverse:0,id_hino:40},{idverse:24954,verse:`4. 
Qu\xE3o felizes s\xE3o teus filhos,
Vendo a face do Senhor!
Nunca mais a fome sentem,
Nunca sentem mais a dor,
Com os anjos desfrutando
Pleno gozo, paz e amor!
`,verseorder:4,ismainverse:0,id_hino:40},{idverse:24955,verse:`5. 
O trono do Cordeiro,
Noite e dia, sem cessar
Corre o rio da \xE1gua viva,
Para todos saciar!
L\xE1 com Cristo, v\xE3o teus filhos,
Satisfeitos descansar!
`,verseorder:5,ismainverse:0,id_hino:40},{idverse:24956,verse:`6. 
Se, por Teu amor infindo,
De Si\xE3o eu filho for,
Zombe o mundo e escarne\xE7a,
Nada importa, Salvador,
Pois que do Teu gozo  eterno
Mesmo aqui tenho o penhor!`,verseorder:6,ismainverse:0,id_hino:40},{idverse:24957,verse:`1. 
A Cristo coroai! Que por n\xF3s encarnou,
E Deus, o Santo Deus e Pai, aos homens revelou.
Eis a sua compaix\xE3o! Eis Sua mansid\xE3o!
Quem v\xEA a Cristo, v\xEA ao Pai; sim, v\xEA Seu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:41},{idverse:24958,verse:`2. 
A Cristo coroai! De tudo o Criador,
O filho do eterno Deus, do mundo, o Salvador, Jesus Emanuel,
o grande Redentor!
Em busca dos perdidos, vem o nosso bom Pastor.
`,verseorder:2,ismainverse:0,id_hino:41},{idverse:24959,verse:`3. 
A Cristo coroai! Que sobre a cruz, ganhou
Por n\xF3s eterna reden\xE7\xE3o e para o c\xE9u voltou!
Ele \xE9 o Rei dos reis! O Pr\xEDncipe da paz!
Jesus, da morte, o vencedor, que a salva\xE7\xE3o nos traz.
`,verseorder:3,ismainverse:0,id_hino:41},{idverse:24960,verse:`4. 
A Cristo coroai! De todos o Senhor,
A quem a multid\xE3o dos c\xE9us aclama com fervor!
Eis o Cordeiro ali, que sobre o trono est\xE1!
Que vive e reina l\xE1 por n\xF3s, e cedo voltar\xE1.`,verseorder:4,ismainverse:0,id_hino:41},{idverse:24961,verse:`1. 
Saudai o nome de Jesus!
Arcanjos, vos prostai;
Arcanjos, vos prostai;
Ao Filho do eterno Deus,
Com gl\xF3ria, gl\xF3ria,
Gl\xF3ria, gl\xF3ria,
Com gl\xF3ria, coroai!
`,verseorder:1,ismainverse:0,id_hino:42},{idverse:24962,verse:`2. 
\xD3 escolhida gera\xE7\xE3o
Do bom eterno Pai,
Do bom eterno Pai;
Ao grande autor da salva\xE7\xE3o,
Com gl\xF3ria, gl\xF3ria,
Gl\xF3ria, gl\xF3ria,
Com gl\xF3ria, coroai!
`,verseorder:2,ismainverse:0,id_hino:42},{idverse:24963,verse:`3. 
\xD3 perdoados, cujo amor
Bem triunfante vai,
Bem triunfante vai,
Ao Deus Var\xE3o, Conquistador,
Com gl\xF3ria, gl\xF3ria,
Gl\xF3ria, gl\xF3ria,
Com gl\xF3ria, coroai!
`,verseorder:3,ismainverse:0,id_hino:42},{idverse:24964,verse:`4. 
\xD3 ra\xE7as, tribos e na\xE7\xF5es,
Ao Rei dos reis honrai!
Ao Rei dos reis honrai!
A quem quebrou vossos grilh\xF5es,
Com gl\xF3ria, gl\xF3ria.
Gl\xF3ria, gl\xF3ria,
Com gl\xF3ria, coroai!`,verseorder:4,ismainverse:0,id_hino:42},{idverse:24965,verse:`1. 
Na p\xE1tria celeste, de Deus o doce lar,
Prepara Jesus, para os Seus, um lugar,
Pois longe do mal, do pecado e da dor,
Consigo p\`ra sempre os quer ter seu Senhor.
`,verseorder:1,ismainverse:0,id_hino:43},{idverse:24966,verse:`Oh! Doce, doce lar!
Ali, com Jesus, vou para sempre descansar
`,verseorder:2,ismainverse:1,id_hino:43},{idverse:24967,verse:`2. 
Oh! lar sacrossanto de paz e de amor!
Ali sobre o trono, verei meu Senhor,
O meigo Cordeiro, reinando em luz,
Por todos louvado, bendito Jesus!
`,verseorder:3,ismainverse:0,id_hino:43},{idverse:24968,verse:`3. 
Que puras del\xEDcias se encontram em ti!
Que gozos supremos esperam ali,
Aqueles a quem junto a Si Deus que ter,
E perpetuamente os satisfazer!
`,verseorder:4,ismainverse:0,id_hino:43},{idverse:24969,verse:`4. 
N\xE3o s\xE3o seus prazeres que anseio gozar,
Mas, sim, com Jesus para sempre morar!
Jamais desonr\xE1-lo, jamais ofender
A quem, p\`ra ganhar-me, por mim quis morrer.`,verseorder:5,ismainverse:0,id_hino:43},{idverse:24970,verse:`1. 
Meu Senhor, que me salvaste,
Teu, e Teu somente, eu sou;
Com Teu sangue me saraste;
Gl\xF3ria, gl\xF3ria a Ti Te dou.
`,verseorder:1,ismainverse:0,id_hino:44},{idverse:24971,verse:`Oh! Que gl\xF3ria! Oh! Que gl\xF3ria!
Gozo em meu cora\xE7\xE3o!
Eu confio em Jesus,
E, crendo, tenho salva\xE7\xE3o.
Oh! que gl\xF3ria! Oh! Que gl\xF3ria!
Gozo em meu cora\xE7\xE3o!
Eu confio em Jesus,
E em Seu sangue achei perd\xE3o.
`,verseorder:2,ismainverse:1,id_hino:44},{idverse:24972,verse:`2. 
Para obter t\xE3o grande gozo,
Muito e muito trabalhei;
Mas debalde todo esfor\xE7o,
Crendo s\xF3 \xE9 que o achei.
`,verseorder:3,ismainverse:0,id_hino:44},{idverse:24973,verse:`3. 
Confiando, confiando,
Sempre e s\xF3, Jesus, em Ti,
Teu poder e Tua gra\xE7a
Podem bem guardar-me a mim.
`,verseorder:4,ismainverse:0,id_hino:44},{idverse:24974,verse:`4. 
Consagrado ao Teu servi\xE7o,
Quero eu p\`ra ti viver;
Dando sempre o testemunho
De tua gra\xE7a e teu poder.`,verseorder:5,ismainverse:0,id_hino:44},{idverse:24975,verse:`1. 
Redentor onipotente,
Poderoso Salvador,
Advogado onisciente
\xC9 Jesus, meu bom Senhor.
`,verseorder:1,ismainverse:0,id_hino:45},{idverse:24976,verse:`\xD3 Amante da minh\`alma,
Tu \xE9s tudo para mim!
Tudo quanto eu care\xE7o
Acho, Jesus, s\xF3 em Ti.
`,verseorder:2,ismainverse:1,id_hino:45},{idverse:24977,verse:`2. 
Um abrigo sempre perto,
Para todo o pecador;
Um ref\xFAgio sempre aberto,
\xC9 Jesus, meu Salvador.
`,verseorder:3,ismainverse:0,id_hino:45},{idverse:24978,verse:`3. 
\xC1gua viva! p\xE3o da vida!
Doce sombra no calor,
Que ao descanso nos convida,
\xC9 Jesus, meu Salvador!
`,verseorder:4,ismainverse:0,id_hino:45},{idverse:24979,verse:`4. 
\xD3 Cordeiro imaculado,
Que Seu sangue derramou,
Meus pecados expiando,
A minh\`alma resgatou.
`,verseorder:5,ismainverse:0,id_hino:45},{idverse:24980,verse:`5. 
Fundamentos inabal\xE1vel!
Rocha firme e secular!
Infal\xEDvel! Imut\xE1vel!
Quem mo poder\xE1 tirar?
`,verseorder:6,ismainverse:0,id_hino:45},{idverse:24981,verse:`6. 
O caminho que seguro,
Sempre para o c\xE9u conduz,
Quem a Cristo pronto segue,
Quem tomar a sua cruz.`,verseorder:7,ismainverse:0,id_hino:45},{idverse:24982,verse:`1. 
Um pend\xE3o real vos entregou o Rei
A v\xF3s, soldados Seus;
Corajosos, pois, em tudo o defendei,
Marchando para os c\xE9us.
`,verseorder:1,ismainverse:0,id_hino:46},{idverse:24983,verse:`Com valor! Sem temor!
Por Cristo prontos a sofrer!
Bem alto erguei o Seu pend\xE3o,
Firmes sempre, at\xE9 morrer!
`,verseorder:2,ismainverse:1,id_hino:46},{idverse:24984,verse:`2. 
Eis formados j\xE1 os negros batalh\xF5es
Do grande usurpador!
Declarai-vos, hoje, bravos campe\xF5es;
Avante sem temor.
`,verseorder:3,ismainverse:0,id_hino:46},{idverse:24985,verse:`3. 
Quem receio sente no seu cora\xE7\xE3o,
E fraco se mostrar,
N\xE3o receber\xE1 o eterno galard\xE3o,
Que Cristo tem p\`ra dar.
`,verseorder:4,ismainverse:0,id_hino:46},{idverse:24986,verse:`4. 
Pois sejamos, todos, a Jesus leais,
E a Seu real pend\xE3o;
Os que na batalha sempre s\xE3o fi\xE9is,
Com Ele reinar\xE3o.`,verseorder:5,ismainverse:0,id_hino:46},{idverse:24987,verse:`1. 
Rocha eterna, meu Jesus,
Que, por mim, na amarga cruz,
Foste morto eu meu lugar,
Morto para me salvar;
Em Ti quero me esconder,
S\xF3 Tu podes me valer.
`,verseorder:1,ismainverse:0,id_hino:47},{idverse:24988,verse:`2. 
Minhas obras, eu bem sei,
Nada valem ante a lei;
Se eu chorasse sem cessar.
Trabalhasse sem cansar,
Tudo in\xFAtil, tudo em v\xE3o!
S\xF3 em Ti h\xE1 salva\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:47},{idverse:24989,verse:`3. 
Nada trago a Ti, Senhor!
\`Spero s\xF3 em Teu amor!
Todo indigno e imundo sou,
Eis, sem Ti, perdido estou!
No Teu sangue, \xF3 Salvador,
Lava um pobre pecador.
`,verseorder:3,ismainverse:0,id_hino:47},{idverse:24990,verse:`4. 
Quando a morte me chamar,
E ante Ti me apresentar,
Rocha eterna, meu Jesus,
Que por mim, na amarga cruz,
Foste morto em meu lugar,
Quero em Ti s\xF3 me abrigar.`,verseorder:4,ismainverse:0,id_hino:47},{idverse:24991,verse:`1. 
Quando l\xE1 do c\xE9u descendo,
para os Seus, Jesus voltar,
E o clarim de Deus a todos proclamar,
Que chegou o grande dia do triunfar do meu Rei,
Eu, por Sua imensa gra\xE7a, l\xE1 estarei.
`,verseorder:1,ismainverse:0,id_hino:48},{idverse:24992,verse:`Quando enfim, chegar o dia
Do triunfar do meu Rei,
Quando enfim, chegar o dia,
Pela gra\xE7a de Jesus eu l\xE1 estarei!
`,verseorder:2,ismainverse:1,id_hino:48},{idverse:24993,verse:`2. 
Nesse dia, quando os mortos
h\xE3o de a voz de Cristo ouvir,
E dos seus sepulcros h\xE3o de ressurgir,
Os remidos reunidos, logo aclamar\xE3o seu Rei,
E, por Sua imensa gra\xE7a, l\xE1 estarei.
`,verseorder:3,ismainverse:0,id_hino:48},{idverse:24994,verse:`3. 
Pelo mundo, rejeitado foi,
Jesus, meu Salvador.
Desprezaram, insultaram meu Senhor,
Mas, fautoso, vem o dia
do triunfar do meu Rei,
E, por Sua imensa gra\xE7a, l\xE1 estarei.
`,verseorder:4,ismainverse:0,id_hino:48},{idverse:24995,verse:`4. 
Em mim mesmo, nada tenho em que possa confiar,
Mas Jesus morreu na cruz p\`ra me salvar;
T\xE3o somente nEle espero, sim, e sempre esperarei,
Pois, por Sua imensa gra\xE7a, l\xE1 estarei.`,verseorder:5,ismainverse:0,id_hino:48},{idverse:24996,verse:`1. 
Deus tem prometido a salva\xE7\xE3o dar,
A quem em Seu Filho Jesus confiar.
`,verseorder:1,ismainverse:0,id_hino:49},{idverse:24997,verse:`Aleluia! J\xE1 creio em Cristo
Jesus.
E salvo j\xE1 estou, pelo sangue da cruz!
`,verseorder:2,ismainverse:1,id_hino:49},{idverse:24998,verse:`2. 
Foi tal Seu amor, que me substituiu,
E sobre o Calv\xE1rio, minh\`alma remiu!
`,verseorder:3,ismainverse:0,id_hino:49},{idverse:24999,verse:`3. 
N\xE3o posso jamais desse amor duvidar;
N\xE3o posso sen\xE3o aos Seus p\xE9s me prostar.
`,verseorder:4,ismainverse:0,id_hino:49},{idverse:25e3,verse:`4. 
E, quando, na gl\xF3ria, eu vir meu bom Rei,
Com todos os santos ali cantarei.`,verseorder:5,ismainverse:0,id_hino:49},{idverse:25001,verse:`1. 
Sempre fi\xE9is, sim, a Ti, n\xF3s seremos,
Por Tua gra\xE7a, \xF3 Cristo, Senhor!
Sempre fi\xE9is, sim, por Ti lutaremos,
Sob Teu pend\xE3o, \xF3 Jesus, Salvador!
`,verseorder:1,ismainverse:0,id_hino:50},{idverse:25002,verse:`Sempre fi\xE9is, irm\xE3os! Irm\xE3os
sejamos.
Sempre fi\xE9is a Cristo Jesus.
Que at\xE9 \xE0 morte, por n\xF3s prosseguiu,
E libertou-nos, morrendo na cruz!
`,verseorder:2,ismainverse:1,id_hino:50},{idverse:25003,verse:`2. 
Por Ti viver, \xF3 bendito Cordeiro,
Quem n\xE3o deseja - se te conhecer?
Quem, que se diga crist\xE3o verdadeiro,
Pronto n\xE3o \`steja, por Ti a sofrer?
`,verseorder:3,ismainverse:0,id_hino:50},{idverse:25004,verse:`3. 
Mas, Salvador, qu\xE3o fraquinhos n\xF3s somos,
Como podemos deixar de cair,
Se por Ti mesmo guardados n\xE3o fomos?
Quem, t\xE9 \xE0 morte, Te podes seguir?
`,verseorder:4,ismainverse:0,id_hino:50},{idverse:25005,verse:`4. 
Sempre fi\xE9is, sim, mesmo at\xE9 \xE0 morte!
Sempre fi\xE9is - Tomemos a cruz!
Eis a divisa que a n\xF3s nos pertence,
Aos libertados por Cristo Jesus!`,verseorder:5,ismainverse:0,id_hino:50},{idverse:25006,verse:`1. 
\xD3 minh\`alma, sem demora,
Ergue-te para entoar,
Os louvores do teu Cristo,
E Seu nome celebrar!
P\`ra remir-te, p\`ra remir-te,
Sua vida Ele quis dar!
`,verseorder:1,ismainverse:0,id_hino:51},{idverse:25007,verse:`2. 
Minha condi\xE7\xE3o t\xE3o triste
Conheceu meu Salvador;
Que dos c\xE9us desceu \xE0 terra,
Para ser meu Redentor!
Oh! Qu\xE3o grande! Oh! Qu\xE3o grande!
\xC9 o amor de meu Senhor!
`,verseorder:2,ismainverse:0,id_hino:51},{idverse:25008,verse:`3. 
Condenado, justamente,
Que podia eu fazer,
Dessa pena p\`ra livrar-me,
O perd\xE3o p\`ra merecer?
O Seu sangue, o Seu sangue,
Quis Jesus por mim verter!
`,verseorder:3,ismainverse:0,id_hino:51},{idverse:25009,verse:`4. 
Com meus crimes carregado,
L\xE1 na cruz, em meu lugar,
Foi Jesus crucificado,
A minh\`alma p\`ra salvar;
Vinde todos! Vinde todos,
J\xE1 comigo, O adorar!`,verseorder:4,ismainverse:0,id_hino:51},{idverse:25010,verse:`1. 
Pelo amor de Deus bendito,
Vai tudo bem!
Seu amor \xE9 infinito:
Tudo est\xE1 bem!
Esse amor nos tem provado,
Em Seu Filho muito amado,
Que, por n\xF3s, foi imolado.
Sim, sim, \`st\xE1 bem!
`,verseorder:1,ismainverse:0,id_hino:52},{idverse:25011,verse:`2. 
Cristo, agora, o cetro empunha:
Vai tudo bem!
Quem morreu \xE9 quem governa:
Tudo est\xE1 bem!
Seu amor \xE9 imut\xE1vel,
Seu poder Inabal\xE1vel,
Seu cuidado \xE9 incans\xE1vel.
Sim, sim, \`sta bem!
`,verseorder:2,ismainverse:0,id_hino:52},{idverse:25012,verse:`3. 
A f\xE9 canta na tristeza:
Vai tudo bem!
Canta, sim, e com firmeza:
Tudo est\xE1 bem!
Pois se Deus \xE9 quem nos guia,
Ternamente nos vigia,
Com bondade, noite e dia.
Sim, sim, \`st\xE1 bem!
`,verseorder:3,ismainverse:0,id_hino:52},{idverse:25013,verse:`4. 
Por caminhos escabrosos,
Vai tudo bem!
Ou por mares tormentosos,
Tudo est\xE1 bem!
A Jesus tudo obedece;
Sempre o mesmo permanece!
Nem um s\xF3 dos seus esquece!
Sim, sim, \`sta bem!
`,verseorder:4,ismainverse:0,id_hino:52},{idverse:25014,verse:`5. 
Quer na vida, quer na morte,
Vai tudo bem!
Qual feliz \xE9 nossa sorte!
Tudo est\xE1 bem!
Pelo sangue resgatados,
E do mundo separados.
Sempre por Jesus guardados,
Sim, sim \`st\xE1 bem!`,verseorder:5,ismainverse:0,id_hino:52},{idverse:25015,verse:`1. 
At\xE9 que volte o Salvador,
Cercando a mesa do Senhor,
A Ceia vimos celebrar,
De Cristo, a morte anunciar,
E com humilde devo\xE7\xE3o,
Render a Deus adora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:53},{idverse:25016,verse:`2. 
At\xE9 que volte o Salvador,
Aqui mostremos Seu amor;
Com viva f\xE9 e gratid\xE3o,
Participemos deste p\xE3o,
Obedientes a Jesus,
Lembrando assim a Sua cruz!
`,verseorder:2,ismainverse:0,id_hino:53},{idverse:25017,verse:`3. 
At\xE9 que volte o Salvador,
Bebendo o c\xE1lice do Senhor,
Seu nome queremos bendizer,
E mais e mais engrandecer,
O sangue que Ele derramou,
O sangue que nos resgatou!
`,verseorder:3,ismainverse:0,id_hino:53},{idverse:25018,verse:`4. 
Se duras s\xE3o as prova\xE7\xF5es,
Se fortes as persegui\xE7\xF5es,
Se as lutas fazem-se sentir,
E custa-nos as resistir,
N\xE3o nos deixemos pertubar:
S\xE3o s\xF3 at\xE9 Jesus voltar!
`,verseorder:4,ismainverse:0,id_hino:53},{idverse:25019,verse:`5. 
Bem prontos para O receber,
Devemos sempre aqui viver;
O tempo foge, o dia vem,
A gl\xF3ria esperamos al\xE9m,
Pois trabalhemos, com fervor,
At\xE9 que volte o Salvador.`,verseorder:5,ismainverse:0,id_hino:53},{idverse:25020,verse:`1. 
Minha\`alma, louva ao Redemtor
Jesus, teu Rei, teu Salvador,
Que sobre, em teu lugar,
Seu sangue deu p\`ra te salvar!
`,verseorder:1,ismainverse:0,id_hino:54},{idverse:25021,verse:`Sim, sobre a cruz, em meu lugar,
Jesus morreu p\`ra me salvar!
Salvo e seguro nEle estou,
Pois com Seu sangue me livrou.
`,verseorder:2,ismainverse:1,id_hino:54},{idverse:25022,verse:`2. 
Oh! qu\xE3o perdido e longe andei,
Longe de Deus e Sua lei!
Com terno e paciente amor
Seguiu-me sempre, o Salvador!
`,verseorder:3,ismainverse:0,id_hino:54},{idverse:25023,verse:`3. 
Bendito dia, quando, enfim,
Vi Cristo sobre a cruz, por mim!
Vencido pelo seu amor,
Rendi-me logo ao Salvador.
`,verseorder:4,ismainverse:0,id_hino:54},{idverse:25024,verse:`4. 
Vem, alma aflita, descansar;
Eis Cristo pronto a perdoar!
Confia nEle, de cora\xE7\xE3o,
E teu ser\xE1 o gran perd\xE3o.`,verseorder:5,ismainverse:0,id_hino:54},{idverse:25025,verse:`1. 
Meu Senhor, sou Teu - Tua voz ouvi
A chamar-me, com amor;
Mais, de Ti, mais perto eu quero estar,
\xD3 bendito Salvador!
`,verseorder:1,ismainverse:0,id_hino:55},{idverse:25026,verse:`Mais perto, perto da tua cruz
Quero estar, \xF3 salvador!
Mais perto, perto, perto da Tua cruz
`,verseorder:2,ismainverse:1,id_hino:55},{idverse:25027,verse:`2. 
A seguir-te s\xF3, me consagro eu.
Constrangido pelo amor,
E, alegre, j\xE1 me eclaro
P\`ra servir-te a Ti, Senhor.
`,verseorder:3,ismainverse:0,id_hino:55},{idverse:25028,verse:`3. 
Oh! Que pura e santa del\xEDcia \xE9
Aos Teus santos p\xE9s me achar,
E com viva e reverente f\xE9,
Com meu Salvador falar.`,verseorder:4,ismainverse:0,id_hino:55},{idverse:25029,verse:`1. 
Dantes, trabalhava sempre com temor,
Mas descanso, agora em meu Salvado;
Dantes "esperava"; hoje, "bem o sei"
Que estou salvo em Cristo, meu bendito Rei.
`,verseorder:1,ismainverse:0,id_hino:56},{idverse:25030,verse:`Tudo! Tudo em Cristo!
Que por mim morreu.
Tudo! Tudo em Cristo!
Cristo \xE9 todo meu.
`,verseorder:2,ismainverse:1,id_hino:56},{idverse:25031,verse:`2. 
Dantes, desejava b\xEAn\xE7\xE3o do Senhor;
Hoje, mais de Cristo, mais de Seu amor!
N\xE3o somente a b\xEAn\xE7\xE3o, que t\xE3o pronto d\xE1,
Mas Ele mesmo a fonte em quem tudo est\xE1.
`,verseorder:3,ismainverse:0,id_hino:56},{idverse:25032,verse:`3. 
Dantes, duvidava, era sempre "o eu";
Hoje, bem conhe\xE7o Cristo, al\xE9m do v\xE9u,
Sacerdote eterno, l\xE1 por mim entrou;
NEle estou completo, nEle aceito sou.
`,verseorder:4,ismainverse:0,id_hino:56},{idverse:25033,verse:`4. 
Dantes queria o mundo; hoje, \xE9 s\xF3 Jesus!
Dantes, eram trevas; hoje, \xE9 plena luz;
Dantes, o receio, hoje, a doce paz,
Tudo a Cristo deixo, Ele me satisfaz.`,verseorder:5,ismainverse:0,id_hino:56},{idverse:25034,verse:`1. 
Deus presente est\xE1 conosco.
Pronto todos a salvar;
Sobre as almas sequiosas,
Quer Sua b\xEAn\xE7\xE3o derramar.
`,verseorder:1,ismainverse:0,id_hino:57},{idverse:25035,verse:`Manda, \xF3 manda as ricas chuvas
Da Tua b\xEAn\xE7\xE3o, Salvador!
Imploramos! Esperamos!
Vivifica-nos, Senhor!
`,verseorder:2,ismainverse:1,id_hino:57},{idverse:25036,verse:`2. 
Eis a Ti, Senhor, erguemos
Nossos pobres cora\xE7\xF5es;
Na Tua grande e rica gra\xE7a,
Ouve as nossas peti\xE7\xF5es!
`,verseorder:3,ismainverse:0,id_hino:57},{idverse:25037,verse:`3. 
Torna a nossa f\xE9 mais viva,
Mais ardente o nosso amor;
Enche-nos de santo zelo,
De coragem e fervor.`,verseorder:4,ismainverse:0,id_hino:57},{idverse:25038,verse:`1. 
"N\xE3o temas! Contigo, Eu sempre estarei!"
Oh! que rica promessa do bondoso Rei;
Qual estrela que brilha, l\xE1 na escurid\xE3o,
Esta linda promessa brilha nomeu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:58},{idverse:25039,verse:`Comigo estar! Comigo
estar!
Sim, Jesus me promete,
Comigo estar! Comigo estar!
Sim, Jesus me promete,
Sempre comigo estar.
`,verseorder:2,ismainverse:1,id_hino:58},{idverse:25040,verse:`2. 
Os l\xEDrios mais alvos, ei-los murchos est\xE3o!
Os dias mais belos, qu\xE3o depressa v\xE3o!
Cristo, o l\xEDrio dos vales, nunca mudar\xE1:
Cristo, a luz celeste, sempre comigo estar\xE1!
`,verseorder:3,ismainverse:0,id_hino:58},{idverse:25041,verse:`3. 
E, se pelas \xE1guas tiver de passar,
Seus bra\xE7os eternos h\xE3o de me guardar;
Sim, mesmo no fogo, que vem me provar,
Meu Senhor me promete comigo estar!`,verseorder:4,ismainverse:0,id_hino:58},{idverse:25042,verse:`1. 
T\xE9 conhecer o amor sem fim,
Um pecador fui eu;
Em o meu pensar n\xE3o houve lugar
Para Cristo e o c\xE9u.
`,verseorder:1,ismainverse:0,id_hino:59},{idverse:25043,verse:`Eu creio, sim, j\xE1 creio, sim,
Jesus morreu por mim;
Pelo sangue Seu, que Ele verteu,
Libertado fui por fim.
`,verseorder:2,ismainverse:1,id_hino:59},{idverse:25044,verse:`2. 
Pois, quando vi Jesus por mim,
Sofrendo sobre a cruz,
O Meu cora\xE7\xE3o, sem hesita\xE7\xE3o,
Recebeu o amor e a luz.
`,verseorder:3,ismainverse:0,id_hino:59},{idverse:25045,verse:`3. 
Com l\xE1grimas, pedi perd\xE3o,
E dor, tamb\xE9m senti;
Veio, ent\xE3o, Jesus, e a mim falou:
"Foi por ti que Eu morri"
`,verseorder:4,ismainverse:0,id_hino:59},{idverse:25046,verse:`4. 
Eu sei que Deus, no Filho seu,
Me v\xEA perfeito e s\xE3o;
N\xE3o h\xE1 mais temor, s\xF3 bendito amor,
Gozo no meu cora\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:59},{idverse:25047,verse:`1. 
Vinde, cantai e entoai
Louvores a Jesus,
Que, para nossa salva\xE7\xE3o,
Morreu na amarga cruz,
Seu sangue derramou, de tudo me lavou,
Mais alvo do que a neve me tornou.
`,verseorder:1,ismainverse:0,id_hino:60},{idverse:25048,verse:`O sangue de Jesus me lavou, me lavou!
O sangue de Jesus me lavou, me lavou!
Alegre cantarei louvores ao meu Rei,
A meu Senhor Jesus, que me salvou.
`,verseorder:2,ismainverse:1,id_hino:60},{idverse:25049,verse:`2. 
Vinde conosco vos unir
Na guerra contra o mal;
E com nosso Salvador,
Em marcha triunfal,
A todos proclamar, Sua gra\xE7a e Seu poder.
Seu sangue derramou p\`ra nos salvar!
`,verseorder:3,ismainverse:0,id_hino:60},{idverse:25050,verse:`3. 
O Capit\xE3o da salva\xE7\xE3o
\xC9 Cristo, o Salvador;
O Rei dos reis, o Redentor.
Jesus, nosso Senhor;
Ele tudo vencer\xE1, vit\xF3ria nos dar\xE1;
A gl\xF3ria salvos, nos conduzir\xE1.`,verseorder:4,ismainverse:0,id_hino:60},{idverse:25051,verse:`1. 
Em todo tempo irm\xE3o, o Senhor
Tomar\xE1 conta de ti.
Cristo, que fala de vida e amor,
Tomar\xE1 conta de ti.
`,verseorder:1,ismainverse:0,id_hino:61},{idverse:25052,verse:`Deus tomar\xE1 conta de ti,
Deus tomar\xE1 conta de ti,
NEle descansa, sempre aqui,
Pois tem cuidado Deus, de ti.
`,verseorder:2,ismainverse:1,id_hino:61},{idverse:25053,verse:`2. 
Em toda prova, irm\xE3o, o Senhor
Tomar\xE1 conta de ti.
Cristo, que \xE9 teu amado Pastor,
Tomar\xE1 conta de ti.
`,verseorder:3,ismainverse:0,id_hino:61},{idverse:25054,verse:`3. 
Em toda parte, irm\xE3o, o Senhor
Tomar\xE1 conta de ti.
Cristo, que nos d\xE1 poder e valor,
Tomar\xE1 conta de ti.
`,verseorder:4,ismainverse:0,id_hino:61},{idverse:25055,verse:`4. 
Chegando a morte, irm\xE3o, o Senhor
Tomar\xE1 conta de ti.
Cristo ser\xE1 teu fiel condutor:
Tomar\xE1 conta de ti.`,verseorder:5,ismainverse:0,id_hino:61},{idverse:25056,verse:`1. 
Achei Jesus, achei Jesus,
Oh! gozo incompar\xE1vel!
Pois salvo estou, j\xE1 tenho luz
E paz inexplic\xE1vel!
Os meus pecados expiou
O sangue do Cordeiro;
As minhas trevas dissipou;
De Deus eu sou herdeiro!
`,verseorder:1,ismainverse:0,id_hino:62},{idverse:25057,verse:`2. 
Jesus mui longe me buscou,
Perdido me encontrava!
E do pecado me salvou,
O qual me atormentava,
Perten\xE7o ao meu bom Redentor,
Ao meu melhor amigo,
Que me livrou de toda dor,
E sempre est\xE1 comigo.
`,verseorder:2,ismainverse:0,id_hino:62},{idverse:25058,verse:`3. 
Oh! que prazer, oh! que prazer!
Jesus \xE9 meu amado!
Do Salvador eu quero ser
Um servo consagrado;
Pois j\xE1 me deu a salva\xE7\xE3o,
Que nos foi prometida,
Ao meu cansado cora\xE7\xE3o
Encheu de nova vida.`,verseorder:3,ismainverse:0,id_hino:62},{idverse:25059,verse:`1. 
Eis marchamos para aquele bom pa\xEDs,
Onde o crente (sim, \xE9 Cristo quem o diz),
Com seu Salvador p\`ra sempre ali feliz,
Vai com Ele descansar.
Trabalhemos, pois, com zelo e com vigor,
Constrangidos pelo Seu imenso amor;
Trabalhemos pelo nosso Salvador:
Eis que a vida vai findar!
`,verseorder:1,ismainverse:0,id_hino:63},{idverse:25060,verse:`Acordai! Acordai! Despertai!
Despertai!
E cantai! Sim, cantai! O Senhor n\xE3o tardar\xE1.
Eis marchamos para aquele bom p\xE1is,
Onde o crente (sim, \xE9 Cristo quem o diz),
Com seu Salvador,  p\`ra sempre ali feliz,
Vai com Ele descansar.
`,verseorder:2,ismainverse:1,id_hino:63},{idverse:25061,verse:`2. 
Eis conosco nosso insigne Capit\xE3o,
Que nos assegura eterna salva\xE7\xE3o!
Eis da santa f\xE9, o invicto pavilh\xE3o!
Vamos, vamos trabalhar!
Eis avante! nada temos que temer;
Por Jesus, havemos sempre de vencer,
Trabalhemos, pois at\xE9 o amanhecer,
E o trabalho aqui findar!
`,verseorder:3,ismainverse:0,id_hino:63},{idverse:25062,verse:`3. 
Revestivos da coura\xE7a de Jesus,
Como servos Seus e filhos, sim, da luz,
Gloriando-nos em Cristo e Sua cruz!
Vamos, vamos, trabalhar!
Os perdidos, vamos com amor buscar,
Aos desesperados, Vamos declarar
Que Jesus \`st\xE1 pronto todos a salvar!
\xD3 sim, vamos trabalhar!`,verseorder:4,ismainverse:0,id_hino:63},{idverse:25063,verse:`1. 
De todo o mundo, eis-nos aqui
Em Ti s\xF3 confiando,
Permanecendo em Teu amor,
Teu reino s\xF3 buscando,
As nossas vidas, nosso amor,
A Ti trazemos, Salvador,
Cantando: "Aleluia"!
`,verseorder:1,ismainverse:0,id_hino:64},{idverse:25064,verse:`2. 
Viver por Ti, em Ti morrer,
Os nossos pais miravam;
A Tua vontade s\xF3 fazer,
Eis o que desejavam.
E n\xF3s, que somos Teus, tamb\xE9m
Dizemos, firmes: "Sim Am\xE9m"
E: " Gl\xF3ria! "Aleluia!"
`,verseorder:2,ismainverse:0,id_hino:64},{idverse:25065,verse:`3. 
Ao Evangelho salvador,
T\xE3o puro e glorioso,
As leis sant\xEDssimas que Tu
Nos d\xE1s, Senhor bondoso,
" Am\xE9m! Dizemos com fervor,
E, pelo Teu imenso amor,
Clamamos: " Aleluia!"
`,verseorder:3,ismainverse:0,id_hino:64},{idverse:25066,verse:`4. 
Sim, constrangidos pelo amor,
Perdidos procuramos
Salvar, - ganhar p\`ra Ti, Senhor:
Eis o que ambicionamos,
Seguindo sempre a Tua lei,
A ti, leais, bendito Rei,
Cantamos: "Aleluia!"
`,verseorder:4,ismainverse:0,id_hino:64},{idverse:25067,verse:`5. 
Nem o labor ser\xE1 em v\xE3o,
Pois eis, que glorioso,
O dia vem, em que Jesus
Vir\xE1 vitorioso;
E todo mundo cantar\xE1,
E jubiloso O exaltar\xE1,
Com: "Am\xE9m", "Aleluia!"`,verseorder:5,ismainverse:0,id_hino:64},{idverse:25068,verse:`1. 
Eis os milh\xF5es, que em treva t\xE3o medonhas
Jazem perdidos, sem o Salvador!
Quem, quem ir\xE1 as novas proclamando,
Que Deus, em Cristo, salva o pecador?
`,verseorder:1,ismainverse:0,id_hino:65},{idverse:25069,verse:`" Todo o poder o Pai me
deu,
Na terra, como l\xE1 no c\xE9u!
Ide, pois, anunciar o Evangelho,
E eis-me convosco sempre!"
`,verseorder:2,ismainverse:1,id_hino:65},{idverse:25070,verse:`2. 
Portas abertas, eis por todo o mundo!
Crist\xE3os, erguei-vos! j\xE1 avante andai!
Crentes em Cristo! uni as vossas for\xE7as.
Da escravid\xE3o os povos libertai.
`,verseorder:3,ismainverse:0,id_hino:65},{idverse:25071,verse:`3. 
" \xD3 vinde a mim! A voz divina clama,
"Vinde!" Clamai em nome de Jesus:
P\`ra nos salvar da maldi\xE7\xE3o eterna,
Seu sangue derramou por n\xF3s na cruz.
`,verseorder:4,ismainverse:0,id_hino:65},{idverse:25072,verse:`4. 
\xD3 Deus apressa o dia glorioso,
Em que os remidos todos se unir\xE3o,
E, em coro excelso, santo, jubilo,
P\`ra todo o sempre, gl\xF3ria a Ti dar\xE3o.`,verseorder:5,ismainverse:0,id_hino:65},{idverse:25073,verse:`1. 
Todo o que cr\xEA no Filho de Deus,
Que, p\`ra nos remir, desceu dos c\xE9us;
Todo o que, contrito, a Deus se chegar,
Jesus est\xE1 pronto, agora, a salvar.
`,verseorder:1,ismainverse:0,id_hino:66},{idverse:25074,verse:`Sim, Jesus te chama, \xF3 meu irm\xE3o!
A ti Ele oferece pleno perd\xE3o;
Seu sangue verteu para te resgatar,
E Ele \xE9 quem quer e pode salvar.
`,verseorder:2,ismainverse:1,id_hino:66},{idverse:25075,verse:`2. 
Quem, arrependido, busca ped\xE3o,
Abrindo a Jesus o seu cora\xE7\xE3o,
A paz hoje mesmo pode gozar,
Pois Ele est\xE1 pronto, agora, a salvar.`,verseorder:3,ismainverse:0,id_hino:66},{idverse:25076,verse:`1. 
Quem quer hoje mesmo a Jesus seguir?
Seu pend\xE3o al\xE7ando, ao combate ir?
Quem quer, humilhado, sempre O Servir,
E obedec\xEA-Lo, Seu querer cumprir?
`,verseorder:1,ismainverse:0,id_hino:67},{idverse:25077,verse:`Quem que ir com cristo, quem responder\xE1,
Presto, sem demora, " Eu, Senhor, irei"?
Onde for o Mestre, tu tamb\xE9m ir\xE1s?
Quem dir\xE1 agora: "eu te seguirei"?
`,verseorder:2,ismainverse:1,id_hino:67},{idverse:25078,verse:`2. 
Quem seguir a Cristo quer, com mui amor,
Dando-lhe tributo, dando-lhe louvor;
De sua nobre causa, sendo defensor,
E na santa vinha, ser trabalhador?
`,verseorder:3,ismainverse:0,id_hino:67},{idverse:25079,verse:`3. 
Queres tu segui-lo, sem vacila\xE7\xE3o,
E fugir dos bra\xE7os da vil tenta\xE7\xE3o?
Sempre confiando na Sua prote\xE7\xE3o,
Por f\xE9 desfrutando sua salva\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:67},{idverse:25080,verse:`1. 
Podes receber a doce paz de Deus,
Crendo em Jesus, que j\xE1 desceu dos c\xE9us;
Toma, hoje, tua cruz,
Vem das trevas para a luz,
E, assim, mui gozo te dar\xE1 Jesus!
`,verseorder:1,ismainverse:0,id_hino:68},{idverse:25081,verse:`Gozo de ter salva\xE7\xE3o,
Gozo em teu cora\xE7\xE3o.
Alegria fru\xEDras,
Quando desfrutares paz,
Que te trouxe Cristo, na cruenta cruz!
`,verseorder:2,ismainverse:1,id_hino:68},{idverse:25082,verse:`2. 
O amor de Cristo podes conhecer,
Seu sustento santo podes receber;
N\xE3o mais s\xF3 combater\xE1s,
Cristo te defender\xE1,
E, assim, mui gozo te dar\xE1 Jesus.
`,verseorder:3,ismainverse:0,id_hino:68},{idverse:25083,verse:`3. 
Queres tu, de Cristo, fiel soldado ser?
Pelejar por Ele sempre sem temer?
Vem, ent\xE3o, ao Salvador;
Far-te-\xE1 mui vencedor,
E, assim, mui gozo te dar\xE1 Jesus.
`,verseorder:4,ismainverse:0,id_hino:68},{idverse:25084,verse:`4. 
Poder\xE1s por Cristo ser aqui uma luz,
Se obedeceres ao Senhor Jesus!
\xD3 vem, hoje, sem temor,
Aceitar o Salvador,
E, assim, mui gozo te dar\xE1 Jesus.`,verseorder:5,ismainverse:0,id_hino:68},{idverse:25085,verse:`1. 
Jesus quebrou os meus grilh\xF5es,
Com sangue me comprou;
A minha alma, de pris\xF5es,
Eterna resgatou.
`,verseorder:1,ismainverse:0,id_hino:69},{idverse:25086,verse:`Aleluia! Cristo salva o perdido pecador!
Aleluia! Cristo salva o perdido com amor!
`,verseorder:2,ismainverse:1,id_hino:69},{idverse:25087,verse:`2. 
\xC9 minha paz, e prote\xE7\xE3o,
Meu bom Consolador;
Aux\xEDlio certo na afli\xE7\xE3o,
Rem\xE9dio para a dor,
`,verseorder:3,ismainverse:0,id_hino:69},{idverse:25088,verse:`3. 
Nenhum perigo temerei,
Estando em Jesus;
As dores n\xE3o mais sofrerei;
Jesus \xE9 minha luz.
`,verseorder:4,ismainverse:0,id_hino:69},{idverse:25089,verse:`4. 
Jesus, Teu nome louvarei,
Com todo o cora\xE7\xE3o;
Alegre ao mundo anunciarei
T\xE3o grande salva\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:69},{idverse:25090,verse:`1. 
N\xF3s aleluia daremos a Cristo,
Quando O vimos nas nuvens voltar,
De esplendor e de gl\xF3ria vestido,
Seus escolhidos vai arrebatar.
`,verseorder:1,ismainverse:0,id_hino:70},{idverse:25091,verse:`Cristo Jesus logo vai voltar,
Vai voltar, vai voltar,
Seu povo vem, sim, arrebatar;
Jesus breve regressar\xE1!
`,verseorder:2,ismainverse:1,id_hino:70},{idverse:25092,verse:`2. 
Eis que estou no Senhor confiando,
Todo o meu ser a Jesus entreguei.
Todos prazeres eu tenho deixado,
Em Jesus Cristo meu gozo achei.
`,verseorder:3,ismainverse:0,id_hino:70},{idverse:25093,verse:`3. 
Como h\xE1 de ser para ti Sua vinda?
Ela trar\xE1 gozo ao teu cora\xE7\xE3o?
Ou te far\xE1 padecer mais ainda?
Temes que s\xF3 te trar\xE1 maldi\xE7\xE3o?
`,verseorder:4,ismainverse:0,id_hino:70},{idverse:25094,verse:`4. 
Cristo a mim a promessa tem dado
Que junto dEle, tamb\xE9m reinarei;
Breve ser\xE1 meu Jesus proclamado
Sobre a terra, por todos: "O Rei".`,verseorder:5,ismainverse:0,id_hino:70},{idverse:25095,verse:`1. 
Santo! Santo! Grande eterno Deus,
Com alegria n\xF3s te louvamos,
Rei dos reis e Criador dos c\xE9us.
Humilhados Te adoramos;
Honra e gl\xF3ria em Ti miramos,
Pois foi rasgado, na cruz, o v\xE9u.
`,verseorder:1,ismainverse:0,id_hino:71},{idverse:25096,verse:`Santo para sempre \xE9s Tu,
Senhor.
Nas batalhas \xE9s vencedor!
`,verseorder:2,ismainverse:1,id_hino:71},{idverse:25097,verse:`2. 
Adorai-O terra, c\xE9u e mar;
Todas a Igreja, Seus mensageiros,
Reunidos, vinde suplicar
Dons celestes, pois sois herdeiros
De Deus, e com Jesus coherdeiros
Da gl\xF3ria que jamais vai findar.
`,verseorder:3,ismainverse:0,id_hino:71},{idverse:25098,verse:`3. 
Rei dos s\xE9c\`los, grande eterno Deus!
Tu \xE9s veraz e incompreens\xEDvel,
Criador da terra e dos c\xE9us;
\xC9s o Anci\xE3o inv\xEDsivel,
Habitas na luz inacess\xEDvel,
L\xE1 morar\xE3o os remidos Teus.`,verseorder:4,ismainverse:0,id_hino:71},{idverse:25099,verse:`1. 
Rasgou-se o v\xE9u, franca ficou a entrada;
Confian\xE7a temos, para ali entrar,
Onde a divina gl\xF3ria revelada,
Outora, n\xE3o pod\xEDamos mirar;
Por Cristo, entrando, nada ali tememos;
A gl\xF3ria brilha, sem nos ofuscar;
Na luz estamos e permanecemos
Firmes, tranq\xFCilos, sem mais vacilar.
`,verseorder:1,ismainverse:0,id_hino:72},{idverse:25100,verse:`2. 
Contigo ali, Senhor, nos encontramos,
Pois nos levaste j\xE1 ao santo Pai,
Em cuja gra\xE7a aceitos, sempre estamos,
Em cujo amor t\xE3o grande nos atrai
A Ti, \xF3 filho - em Ti, \xF3 bem amado!
E s\xF3 em Ti temos aceita\xE7\xE3o;
E quem, no sangue estando j\xE1 lavado,
Isento est\xE1 da eterna perdi\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:72},{idverse:25101,verse:`3. 
\xD3 Deus, a Ti de cora\xE7\xE3o louvamos,
Pois de Ti nos quiseste aproximar,
Quando separados de Ti ficamos,
Por tua lei divina, violar,
Teus santos seguidores, consagrados,
No sangue do bendito Salvador,
O culto e adora\xE7\xE3o Te apresentamos,
Porque nos chamaste, \xF3 bom Senhor.`,verseorder:3,ismainverse:0,id_hino:72},{idverse:25102,verse:`1. 
Pecador escuta a voz do Senhor:
" Se est\xE1s cansado, \xF3 vem descansar;
Vem, n\xE3o te detenhas; sem nenhum temor,
Deixa o teu fardo e vem repousar".
`,verseorder:1,ismainverse:0,id_hino:73},{idverse:25103,verse:`Vem, vem a Mim e descansar\xE1s;
Toma o Meu jugo e Te guiarei;
Sou humilde e manso, dou-te minha paz;
\xD3 vem, hoje mesmo, e Te salvarei.
`,verseorder:2,ismainverse:1,id_hino:73},{idverse:25104,verse:`2. 
Vem a Deus, faminto, pobre mesmo assim;
H\xE1 man\xE1 celeste, preparado j\xE1;
Gr\xE1tis \xE9 a festa, p\`ra ti e p\`ra mim,
Cristo te convida, vem e cear\xE1s.
`,verseorder:3,ismainverse:0,id_hino:73},{idverse:25105,verse:`3. 
Andas desviado, longe do redil?
Deixa de vagar e vem ao bom Pastor;
Pelos montes, vales, h\xE1 perigos mil?
Volta ao rebanho do teu salvador.
`,verseorder:4,ismainverse:0,id_hino:73},{idverse:25106,verse:`4. 
Crendo tu, em Cristo, salva\xE7\xE3o ter\xE1s;
Gozo, paz perfeita, tudo que \xE9 mister;
Cristo n\xE3o despreza, nem rejeitar\xE1
Todo o que contrito a Ele vier.`,verseorder:5,ismainverse:0,id_hino:73},{idverse:25107,verse:`1. 
Talvez Cristo venha ao romper da aurora,
Com santos arcanjos, e com voz sonora;
Os mortos por\xE1 dos sepulcros p\`ra fora;
Jesus, breve, vem nos buscar.
`,verseorder:1,ismainverse:0,id_hino:74},{idverse:25108,verse:`Cristo, que h\xE1 de vir, vir\xE1!
Ele n\xE3o tardar\xE1, sim, Jesus vem;
Aleluia! Aleluia! Am\xE9m! Aleluia! Am\xE9m!
`,verseorder:2,ismainverse:1,id_hino:74},{idverse:25109,verse:`2. 
Talvez voltar\xE1 quando o dia fene\xE7a,
Ou em uma noite a luz resplande\xE7a;
Irm\xE3os, esperai que Jesus apare\xE7a!
Jesus, breve, vem nos buscar.
`,verseorder:3,ismainverse:0,id_hino:74},{idverse:25110,verse:`3. 
O Seu esplendor e a gl\xF3ria veremos,
Do mundo, ent\xE3o, n\xF3s por fim, sairemos,
Assim, grande gozo no c\xE9u fruiremos;
Jesus, breve, vem nos buscar.`,verseorder:4,ismainverse:0,id_hino:74},{idverse:25111,verse:`1. 
Tentado, n\xE3o cedas, ceder \xE9 pecar,
Melhor e mais nobre, ser\xE1 triunfar,
Coragem, \xF3 crente, domina o teu mal.
Deus pode livrar-te de queda fatal.
`,verseorder:1,ismainverse:0,id_hino:75},{idverse:25112,verse:`Em Jesus tens a palma
Da vit\xF3ria, minh\`alma;
E tamb\xE9m doce calma
Pelo sangue da cruz!
`,verseorder:2,ismainverse:1,id_hino:75},{idverse:25113,verse:`2. 
Evita o pecado, procura agradar
A Deus, a quem deves no corpo exaltar;
N\xE3o manches teus l\xE1bios com impura voz;
Defende tua alma do v\xEDcio atroz.
`,verseorder:3,ismainverse:0,id_hino:75},{idverse:25114,verse:`3. 
S\xEA manso e benigno, qual morto at\xE9.
Na rocha eterna, firma tua f\xE9;
Veraz \xE9 teu dito: de Deus \xE9 teu ser?
T\`espera a coroa, tu podes vencer.`,verseorder:4,ismainverse:0,id_hino:75},{idverse:25115,verse:`1. 
Vem, \xF3 pr\xF3digo, vem sem tardar, te chama Deus,
Ouve-O chamando, sim, chamando a ti;
Tu, que vagas errante escuta a voz dos c\xE9us,
\xD3 escuta a voz de amor.
`,verseorder:1,ismainverse:0,id_hino:76},{idverse:25116,verse:`Deus espera por ti, perdido, pr\xF3digo, vem;
Deus espera por ti, perdido, pr\xF3digo, vem
`,verseorder:2,ismainverse:1,id_hino:76},{idverse:25117,verse:`2. 
Com paci\xEAncia e ternura te chama o Senhor,
Ouve-O chamado, sim , chamado a ti;
Vem enquanto te chama o meigo Salvador,
\xD3 escuta a voz de amor!
`,verseorder:3,ismainverse:0,id_hino:76},{idverse:25118,verse:`3. 
L\xE1 na casa do Pai, abund\xE2ncia h\xE1 de p\xE3o;
Ouve-O chamado, sim, chamado a ti;
J\xE1 est\xE1 pronta a mesa com toda a provis\xE3o,
\xD3 escuta a voz de amor!`,verseorder:4,ismainverse:0,id_hino:76},{idverse:25119,verse:`1. 
Queres, neste mundo, ser um vencedor?
Queres tu cantas nas lutas e na dor?
Queres ser alegre, qual bom lutador?
Guarda o contacto com teu Salvador!
`,verseorder:1,ismainverse:0,id_hino:77},{idverse:25120,verse:`Guarda o contacto com teu Salvador,
E a nuvem do mal n\xE3o te cobrir\xE1;
Pela senda alegre, tu caminhar\xE1s
Indo em contacto com teu Salvador.
`,verseorder:2,ismainverse:1,id_hino:77},{idverse:25121,verse:`2. 
Neste mundo, vivem muitos a penar,
Cujos cora\xE7\xF5es transbordam de pesar;
D\xE1-lhes a mensagem de amor sem par;
Com Deus o contacto deves tu guardar!
`,verseorder:3,ismainverse:0,id_hino:77},{idverse:25122,verse:`3. 
Queres tu, com Deus, a comunh\xE3o obter?
Sua gl\xF3ria em ti sempre permanecer?
Que o mundo possa Cristo em ti ver!
Guarda o contacto co\`o supremo ser.
`,verseorder:4,ismainverse:0,id_hino:77},{idverse:25123,verse:`4. 
Deixa que o esp\xEDrito implante em teu ser,
O amor de Cristo, divinal praze;
Queres, neste mundo, todo mal vencer?
Guarda o contacto e ter\xE1s poder!`,verseorder:5,ismainverse:0,id_hino:77},{idverse:25124,verse:`1. 
Vagando eu em grande escurid\xE3o,
O Salvador me encontrou,
Perdido, mesmo, sem ter salva\xE7\xE3o;
A minh\`alma Seu sangue lavou.
`,verseorder:1,ismainverse:0,id_hino:78},{idverse:25125,verse:`Jesus \xE9 o meu forte Redentor;
Ele \xE9 meu grande Salvador,
Que me libertou na cruz, com mui amor,
Cristo \xE9 meu Rei e meu Senhor.
`,verseorder:2,ismainverse:1,id_hino:78},{idverse:25126,verse:`2. 
Nos meus pecados e vil turba\xE7\xE3o,
A luz mostrou-me, o Senhor;
E fez-me ver que ali na pedi\xE7\xE3o
Me alcan\xE7ava Seu grande amor.
`,verseorder:3,ismainverse:0,id_hino:78},{idverse:25127,verse:`3. 
Eu gozo desta grande salva\xE7\xE3o,
Meu nome j\xE1 no c\xE9u est\xE1;
E recebi a divinal un\xE7\xE3o,
Que aos fi\xE9is ainda Deus d\xE1.
`,verseorder:4,ismainverse:0,id_hino:78},{idverse:25128,verse:`4. 
Num dia alegre, Cristo voltar\xE1,
E para o c\xE9u eu subirei;
Meu corpo transformado enet\xE3o ser\xE1,
Qual o de Cristo Jesus, o meu Rei.`,verseorder:5,ismainverse:0,id_hino:78},{idverse:25129,verse:`1. 
Quando me sinto fraco, n\xE3o tenho mais vigor,
N\xE3o vendo mais o brilho do sol, da clara luz;
Jesus est\xE1 comigo, \xE9 meu Consolador;
Ele jamais me deixa, pois sempre me conduz.
`,verseorder:1,ismainverse:0,id_hino:79},{idverse:25130,verse:`Sua gra\xE7a me basta a mim, a mim,
Sua gra\xE7a me basta a mim;
Na vida d\`aqu\xE9m, no c\xE9u tamb\xE9m,
Sua gra\xE7a me basta a mim.
`,verseorder:2,ismainverse:1,id_hino:79},{idverse:25131,verse:`2. 
Quando a tempestade vem contra mim feroz,
Nas trevas navegando com minha embarca\xE7\xE3o,
Cristo Jesus me anima, ou\xE7o a Sua voz;
"Eis que estou contigo, por que temer, ent\xE3o?"
`,verseorder:3,ismainverse:0,id_hino:79},{idverse:25132,verse:`3. 
Quando m\`atingem os dardos do inimigo audaz,
Que busca com ast\xFAcia a vida me tirar,
Eu clamo, ent\xE3o, a Cristo, meu Salvador veraz,
O que me d\xE1 vit\xF3ria, se nEle eu confiar.`,verseorder:4,ismainverse:0,id_hino:79},{idverse:25133,verse:`1. 
Cristo te chama, a ti pecador,
Te chama a ti, com voz de amor;
Deixa os pecados, e vem ao Senhor.
Cristo te chama a ti!
`,verseorder:1,ismainverse:0,id_hino:80},{idverse:25134,verse:`Cristo te chama, te chama a ti!
Cristo te chama, \xF3 vem pecador!
Cristo te chama com voz de amor.
Cristo te chama a ti!
`,verseorder:2,ismainverse:1,id_hino:80},{idverse:25135,verse:`2. 
Deixa as sendas do mundo d\`horror,
Vem a Jesus, vem com amor;
Cristo te salva, \xF3 vem, pecador;
Cristo te chama a ti!
`,verseorder:3,ismainverse:0,id_hino:80},{idverse:25136,verse:`3. 
Cristo glorioso vir\xE1 com poder;
Pronto est\xE1s para ascender?
S\xF3 por Seu sangue tu podes vencer.
Cristo te chama a ti!
`,verseorder:4,ismainverse:0,id_hino:80},{idverse:25137,verse:`4. 
A compassiva voz do Salvador,
Chama-te j\xE1 com santo amor;
Vem para a fonte, \xF3 vem pecador,
Cristo te chama a ti!`,verseorder:5,ismainverse:0,id_hino:80},{idverse:25138,verse:`1. 
Somente em Cristo podemos
A salva\xE7\xE3o receber;
Novo concertos n\xF3s temos
Em quem na cruz quis morres.
`,verseorder:1,ismainverse:0,id_hino:81},{idverse:25139,verse:`Pode salvar, pode salvar,
Tira os pecados, e quer vos salvar,
Pode salvar, pode salvar,
Tira os pecados, e quer vos salvar.
`,verseorder:2,ismainverse:1,id_hino:81},{idverse:25140,verse:`2. 
Quando perdoa os pecados
Vida nos quer conceder.
Fomos por sangue comprados
Do mundo v\xE3o de viver.
`,verseorder:3,ismainverse:0,id_hino:81},{idverse:25141,verse:`3. 
Teu cora\xE7\xE3o j\xE1 entrega
A quem te pode salvar;
E quem as dores carrega:
Cristo te pode livrar!
`,verseorder:4,ismainverse:0,id_hino:81},{idverse:25142,verse:`4. 
Cristo te quer dar agora
Paz e descanso real;
Quer revelar-te esta hora:
Gra\xE7a imensa e real.`,verseorder:5,ismainverse:0,id_hino:81},{idverse:25143,verse:`1. 
Um Salvador meigo eu tenho,
A quem antes n\xE3o conheci;
Agora, alegre, eu venho
Contando a todos que O vi.
`,verseorder:1,ismainverse:0,id_hino:82},{idverse:25144,verse:`Todos, todos ver\xE3o,
Todos, todos ver\xE3o;
Jesus glorioso e bondoso,
Sim, todos, todos ver\xE3o.
`,verseorder:2,ismainverse:1,id_hino:82},{idverse:25145,verse:`2. 
De todos, \xE9 o mais formoso,
Imagem da gl\xF3ria de Deus;
Tornou-me alegre e ditoso,
E herdeiro da gl\xF3ria dos c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:82},{idverse:25146,verse:`3. 
Se os \xEDmpios me armarem cilada,
Na senda que hei de trilhar,
Jesus me dar\xE1 boa espada,
E os la\xE7os eu hei de cortar.
`,verseorder:4,ismainverse:0,id_hino:82},{idverse:25147,verse:`4. 
A vida e o amor Tu me deste,
Porque Tu amaste a mim,
Tornou, sim, a gl\xF3ria celeste,
Minha sorte bendita, enfim.`,verseorder:5,ismainverse:0,id_hino:82},{idverse:25148,verse:`1. 
Mais perto de Jesus, procuro sempre eu chegar,
Mais belo que o ouro do sol nado \xE9 a Ti mirar.
Em pensamento, sonhos, tanta gl\xF3ria nunca vi;
Pois Ele \xE9 mais belo do que eu jamais previ!
`,verseorder:1,ismainverse:0,id_hino:83},{idverse:25149,verse:`N\xE3o posso explicar
Qu\xE3o meigo \xE9 Jesus;
Mas, face a face, no teu lar,
Eu Te verei, Jesus!
`,verseorder:2,ismainverse:1,id_hino:83},{idverse:25150,verse:`2. 
A estrela resplendente da manh\xE3 \xE9 minha luz;
O l\xEDrio dos vales \xE9 o bom Senhor Jesus;
Suave e doce \xE9 o cheiro que s\xF3 vem de ti;
Pois Ele \xE9 mais belo do que eu jamais previ!
`,verseorder:3,ismainverse:0,id_hino:83},{idverse:25151,verse:`3. 
Se m\xE1goas v\xEAm me pertubar, o b\xE1lsamo Ele tem;
Me toma nos Seus bra\xE7os e, assim, descanso bem;
Na cruz levou Jesus o meu pecado sobre si;
Pois Ele \xE9 mais belo do que eu jamais previ!`,verseorder:4,ismainverse:0,id_hino:83},{idverse:25152,verse:`1. 
N\xE3o pertubeis o cora\xE7\xE3o,
Porque eu sempre sou fiel;
Eu fecho a boca do "le\xE3o"
Na cova estou com Daniel.
`,verseorder:1,ismainverse:0,id_hino:84},{idverse:25153,verse:`Sou Eu aquele, o grande "Eu Sou"
E, onde estais, tamb\xE9m estou;
N\xE3o disse, Eu, h\xE1 muito j\xE1:
"Pedi, pedi... dar-se-vos-\xE1"?
Pedi com f\xE9 e com fervor
E vos darei o Consolador.
`,verseorder:2,ismainverse:1,id_hino:84},{idverse:25154,verse:`2. 
Quem tem a f\xE9 de Abra\xE3o,
O mundo sempre h\xE1 de vencer;
Quem quer ter firme o cora\xE7\xE3o,
Precisa igualmente crer.
`,verseorder:3,ismainverse:0,id_hino:84},{idverse:25155,verse:`3. 
Um terremoto e vento, ap\xF3s,
Do c\xE9u, um fogo e mui furor,
Ouviu Elias a minha voz,
Voz do Eterno, voz de amor.
`,verseorder:4,ismainverse:0,id_hino:84},{idverse:25156,verse:`4. 
Um certo dia, Estev\xE3o viu
O c\xE9u aberto e viu-me a mim;
Apredejado, sucumbiu,
Mas foi fiel, at\xE9 o fim.
`,verseorder:5,ismainverse:0,id_hino:84},{idverse:25157,verse:`5. 
Firmando em mim, Rocha Eternal,
Assim jamais o crente cai;
Buscai o dom celestial,
Que vem da casa de Meu Pai.`,verseorder:6,ismainverse:0,id_hino:84},{idverse:25158,verse:`1. 
Do Santo Esp\xEDrito, est\xE1 escrito,
Que sois o templo, sim, templo de Deus;
J\xE1 recebestes, depois que crestes,
O dom glorioso descido do c\xE9u?
`,verseorder:1,ismainverse:0,id_hino:85},{idverse:25159,verse:`Deixai entrar, para morar,
O Esp\xEDrito de Deus;
Dom prometido, j\xE1 concedido,
Bendito dom dos altos c\xE9us.
`,verseorder:2,ismainverse:1,id_hino:85},{idverse:25160,verse:`2. 
O que vos salva, tamb\xE9m vos lava,
P\`ra vossa alma de manchas limpar;
Purificados de vis pecados,
O Santo Esp\xEDrito j\xE1 pode entrar.
`,verseorder:3,ismainverse:0,id_hino:85},{idverse:25161,verse:`3. 
\xD3 peregrinos, vede o destino,
Entrai na terra de leite e mel,
Santificados, por Deus chamados,
Sereis guiados, por guia fiel.`,verseorder:4,ismainverse:0,id_hino:85},{idverse:25162,verse:`1. 
Oh! \`stou satisfeito com Cristo,
Porque Ele minh\`alma salvou;
E sobre o madeiro, sofrendo,
O Seu grande amor revelou.
`,verseorder:1,ismainverse:0,id_hino:86},{idverse:25163,verse:`Oh! \`stou satisfeiro com Cristo,
Confesso-O por meu Salvador:
Embora de nada ser digno,
Desfruto do Seu grande amor.
`,verseorder:2,ismainverse:1,id_hino:86},{idverse:25164,verse:`2. 
Oh! s\`tou satisfeito com Cristo,
De triste, tornei-me feliz,
Ouvindo o Seu santo Evangelho,
E crendo no que Ele me diz.
`,verseorder:3,ismainverse:0,id_hino:86},{idverse:25165,verse:`3. 
Oh! \`stou satisfeito com Cristo,
E sei que vai logo voltar;
Vir\xE1 com poder glorioso,
A fim de Seu povo levar.`,verseorder:4,ismainverse:0,id_hino:86},{idverse:25166,verse:`1. 
Justificado estou;
Cristo Jesus me livrou;
Ele \xE9 meu Mediador,
Tamb\xE9m, meu bom Salvador.
`,verseorder:1,ismainverse:0,id_hino:87},{idverse:25167,verse:`Jesus, sou Teu, e Tu \xE9s meu;
Me guiar\xE1s para o c\xE9u;
Com gra\xE7a e paz me satisfaz
Cristo, meu Mestre veraz.
`,verseorder:2,ismainverse:1,id_hino:87},{idverse:25168,verse:`2. 
Santificado fiquei,
Quando a Ele roguei;
Ele me disse: tomai;
De minha gra\xE7a usai.
`,verseorder:3,ismainverse:0,id_hino:87},{idverse:25169,verse:`3. 
Fui redimido, tamb\xE9m,
Obra do meu Sumo Bem;
Tudo de gra\xE7a ganhei;
Nada por obras da lei.
`,verseorder:4,ismainverse:0,id_hino:87},{idverse:25170,verse:`4. 
Ele a promessa me deu,
Do guia santo do c\xE9u;
Tamb\xE9m me disse: ficai,
E o batismo esperai.
`,verseorder:5,ismainverse:0,id_hino:87},{idverse:25171,verse:`5. 
Quando a Ele busquei,
E batizado fiquei,
L\xEDnguas estranhas falei,
E  meu Senhor exaltei.
`,verseorder:6,ismainverse:0,id_hino:87},{idverse:25172,verse:`6. 
Agora, vou trabalhar,
E muitas almas ganhar;
Essas que Ele salvou,
Quando na cruz expiou.`,verseorder:7,ismainverse:0,id_hino:87},{idverse:25173,verse:`1. 
Jesus, meu Rei, Mestre e Senhor,
O Teu amor revela a mim,
Enquanto eu aqui viver,
At\xE9 eu ver da vida o fim.
`,verseorder:1,ismainverse:0,id_hino:88},{idverse:25174,verse:`Revela a n\xF3s, Senhor Jesus, meu Salvador,
As maravilhas mil do Teu divino amor;
E com veraz louvor, fervente gratid\xE3o;
Eleva a ti, Jesus Senhor, o nosso cora\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:88},{idverse:25175,verse:`2. 
As ben\xE7\xE3os mil do teu amor,
Qual esplendor me cercar\xE3o;
O Teu olhar ser\xE1, Jesus,
A grata luz do cora\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:88},{idverse:25176,verse:`3. 
Sorrisos Teus verei brilhar,
Se n\xE3o andar no mundo vil:
Desfrutarei prazer veraz,
Tempo de paz primaveril.
`,verseorder:4,ismainverse:0,id_hino:88},{idverse:25177,verse:`4. 
E, quando for no c\xE9u morar,
E descansar dos dias meus,
Feliz viver receberei
De Ti, meu Rei, meu santo Deus!`,verseorder:5,ismainverse:0,id_hino:88},{idverse:25178,verse:`1. 
Conta-me, sim, de Jesus,
Para na mente, eu gravar
Como foi que padeceu
Na cruz, p\`ra me libertar:
A Sua paix\xE3o e morte,
A come\xE7ar no jardim,
N\xE3o houve mais quem tivesse
Um sofrimento assim!
`,verseorder:1,ismainverse:0,id_hino:89},{idverse:25179,verse:`S\xF3 amor! S\xF3 amor!
S\xF3 amor! Sublime e grande amor!
`,verseorder:2,ismainverse:1,id_hino:89},{idverse:25180,verse:`2. 
Conta-me como sentiu
A rude morte na cruz,
Suportando a acusa\xE7\xE3o
Dos inimigos da luz;
Conta-me como tem dado
Ao malfeitor Sua paz,
Dizendo: "Hoje, comigo,
No para\xEDso estar\xE1s".
`,verseorder:3,ismainverse:0,id_hino:89},{idverse:25181,verse:`3. 
Conta-me como chorou,
Falando a Jerusal\xE9m;
Profetas fazes morrer,
Quando a ti eles v\xEAm;
Conta-me como inda chora,
Por causa do pecador,
Que n\xE3o quer Seu Evangelho,
Proclamando com amor.
`,verseorder:4,ismainverse:0,id_hino:89},{idverse:25182,verse:`4. 
Conta-me como o Senhor
Um bom testemunho deu,
Satisfazendo a lei,
Quando o c\xE1lice bebeu;
Tamb\xE9m disse estas palavras,
Com Seu t\xE3o profundo amor;
Pai, lhes perdoa a cegueira,
Que \xE9 pr\xF3pria do pecador.
`,verseorder:5,ismainverse:0,id_hino:89},{idverse:25183,verse:`5. 
Conta-me como, tamb\xE9m,
Veio ao povo judeu,
Povo que tanto amou,
Querendo dar-lhe os c\xE9us;
Como inda hoje Ele ama
A todo o pecador,
Dando-lhe paz e descanso,
Pelo Evangelho de amor.`,verseorder:6,ismainverse:0,id_hino:89},{idverse:25184,verse:`1. 
H\xE1 paz e alegria no reino da luz;
\xC1 p\xE1tria dos remidos, Jesus nos conduz,
No reino dos c\xE9us h\xE1 gozo e paz;
Os filhos de Deus t\xEAm poder veraz.
`,verseorder:1,ismainverse:0,id_hino:90},{idverse:25185,verse:`2. 
H\xE1 paz e alegria no reino dos c\xE9us;
Os salvos l\xE1 entoam louvores a Deus;
Dai gl\xF3ria a Jesus! Porque nos amou;
Por n\xF3s o Seu sangue Ele derramou!
`,verseorder:2,ismainverse:0,id_hino:90},{idverse:25186,verse:`3. 
Jesus tem prometido o Consolador!
O Esp\xEDrito Santo, o Instruidor;
Tamb\xE9m prometeu, no reino dos c\xE9us,
Um santo lugar, junto ao nosso Deus.
`,verseorder:3,ismainverse:0,id_hino:90},{idverse:25187,verse:`4. 
Firmado na promessa t\xE3o grande e eficaz,
Agora neste mundo desfruto a paz;
Espero Jesus, nas nuvens descer,
E na sua gl\xF3ria me receber.`,verseorder:4,ismainverse:0,id_hino:90},{idverse:25188,verse:`1. 
Jesus, eu almejo a pureza do c\xE9u,
Tua linda presen\xE7a no cora\xE7\xE3o meu.
Os \xEDdolos quebro, me toma Jesus;
\xD3 d\xE1-me a pureza da n\xEDvea luz.
`,verseorder:1,ismainverse:0,id_hino:91},{idverse:25189,verse:`N\xEDvea luz, a n\xEDvea luz,
\xD3 d\xE1-me a pureza da n\xEDvea luz.
`,verseorder:2,ismainverse:1,id_hino:91},{idverse:25190,verse:`2. 
Jesus, me acode enquanto lidar,
A colocar tudo no santo altar;
A ti me entrego; nas chamas me pus;
\xD3 d\xE1-me a pureza da n\xEDvea luz!
`,verseorder:3,ismainverse:0,id_hino:91},{idverse:25191,verse:`3. 
Jesus, com inst\xE2ncia, humilde, roguei
Que o sangue perfeito pureza me d\xEA
Me lava na fonte aberta na cruz;
\xD3 d\xE1-me a pureza da n\xEDvea luz!
`,verseorder:4,ismainverse:0,id_hino:91},{idverse:25192,verse:`4. 
Jesus, neste instante, espero a m\xE3o
Que h\xE1 de criar-me o bom cora\xE7\xE3o;
Aqueles que o Esp\xEDrito pedem de Deus,
D\xE1s sempre a pureza da n\xEDvia luz!`,verseorder:5,ismainverse:0,id_hino:91},{idverse:25193,verse:`1. 
Meu bondoso Salvador
Meus pecados carregou;
Foi Jesus, o Redentor,
Que da morte me livrou.
`,verseorder:1,ismainverse:0,id_hino:92},{idverse:25194,verse:`Oh! Que Salvador eu tenho!
O Seu sangue me remiu,
Quando, sobre o rude lenho,
Meu castigo Ele cumpriu.
`,verseorder:2,ismainverse:1,id_hino:92},{idverse:25195,verse:`2. 
Oh! Que amor do meu Jesus,
Que em homem se encarnou,
P\`ra sofrer por mim, na cruz;
E meus crimes perdoou.
`,verseorder:3,ismainverse:0,id_hino:92},{idverse:25196,verse:`3. 
N\xE3o h\xE1 outro nome igual
Ao bom nome de Jesus,
Porque livra-me do mal
E \xE0 gl\xF3ria me conduz.
`,verseorder:4,ismainverse:0,id_hino:92},{idverse:25197,verse:`4. 
Com Jesus, alegre, vou
Sua gra\xE7a publicar,
Porque Ele me chamou
Na Sua vinha a trabalhar.`,verseorder:5,ismainverse:0,id_hino:92},{idverse:25198,verse:`1. 
H\xE1 trabalho pronto para ti crist\xE3o;
Que demana toda a tua devo\xE7\xE3o;
Vem, alegremente, a Cristo obedecer,
Pois s\xF3 tu, \xF3 crente, o poder\xE1s fazer!
`,verseorder:1,ismainverse:0,id_hino:93},{idverse:25199,verse:`Por Jesus a trabalhar,
Prontamente, fielmente, trabalhar!
Em servi-lo, que prazer!
E s\xF3 tu, \xF3 crente, o poder\xE1s fazer.
`,verseorder:2,ismainverse:1,id_hino:93},{idverse:25200,verse:`2. 
Para cada crente o mestre preparou
Um trabalho certo, quando o resgatou,
O Trabalho a que Jesus te chama aqui,
Como ser\xE1 feito, se n\xE3o for por ti?
`,verseorder:3,ismainverse:0,id_hino:93},{idverse:25201,verse:`3. 
Pode ser humilde, mas se for p\`ra Deus,
Ele \xE9 contemplado l\xE1 dos altos c\xE9us.
E o esfor\xE7o feito n\xE3o ser\xE1 em v\xE3o,
Se tiver de Cristo, plena aprova\xE7\xE3o!
`,verseorder:4,ismainverse:0,id_hino:93},{idverse:25202,verse:`4. 
Quantos h\xE1 perdidos, sem a salva\xE7\xE3o!
Quantos que precisam de consola\xE7\xE3o!
Como Cristo os ama, f\xE1-los entender,
Pois s\xF3 tu, \xF3 crente o poder\xE1s fazer!`,verseorder:5,ismainverse:0,id_hino:93},{idverse:25203,verse:`1. 
Quando a luta desta vida
Trabalhosa se findar,
O adeus a este mundo vamos dar;
Para o c\xE9u ent\xE3o iremos,
Com Jesus nos encontrar
Na Jerusal\xE9m de Deus.
`,verseorder:1,ismainverse:0,id_hino:94},{idverse:25204,verse:`Oh! que gozo e alegria,
Quando o povo ali chegar,
Em Jerusal\xE9m! Em Jerusal\xE9m!
Sempre ali cantando hosanas,
Pois o Rei no trono est\xE1,
Na Jerusal\xE9m de Deus.
`,verseorder:2,ismainverse:1,id_hino:94},{idverse:25205,verse:`2. 
O caminho e extenso,
Mas Jesus vai me guiar,
Quando pelas prova\xE7\xF5es aqui passar;
Pois olhando nEle, eu sigo.
Para brevemente estar,
Na Jerusal\xE9m de Deus.
`,verseorder:3,ismainverse:0,id_hino:94},{idverse:25206,verse:`3. 
Ao sairmos deste mundo,
Para a divinal mans\xE3o,
Gozaremos a perfeito salva\xE7\xE3o;
E Jesus contemplaremos
A estende-nos Sua m\xE3o,
Na Jerusal\xE9m de Deus.
`,verseorder:4,ismainverse:0,id_hino:94},{idverse:25207,verse:`4. 
Quando, unidos com os salvos,
L\xE1 na p\xE1tria do Senhor,
Contemplarmos a Jesus, o Salvador,
Sempre nos alegraremos
Onde h\xE1 perfeito amor;
Na Jerusal\xE9m de Deus.`,verseorder:5,ismainverse:0,id_hino:94},{idverse:25208,verse:`1. 
Ouve, ouve, \xF3 pecador!
Ouve bem, presta aten\xE7\xE3o
A Jesus, o salvador,
Que do c\xE9u touxe o perd\xE3o;
Ouve a Sua meiga voz;
Digna \xE9 de aceita\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:95},{idverse:25209,verse:`2. 
Ouve o que diz o Senhor,
Por Sua mensagem de amor;
"Ser\xE1 livre de temor
Todo que vier a Mim";
Vem a Cristo, Salvador,
E aceita o Seu amor.
`,verseorder:2,ismainverse:0,id_hino:95},{idverse:25210,verse:`3. 
Ouve, ouve, \xF3 pecador!
E medita com aten\xE7\xE3o;
Deixa o mundo enganador;
De Jesus, ter\xE1s perd\xE3o;
Ele diz: "Quem crer em Mim,
H\xE1 de ter a Salva\xE7\xE3o".
`,verseorder:3,ismainverse:0,id_hino:95},{idverse:25211,verse:`4. 
Ouvi j\xE1, amigos meus,
N\xE3o tenhais hesita\xE7\xE3o;
Vinde ao Filho, sim, de Deus;
Dai-lhe o vosso cora\xE7\xE3o;
Ele diz: "Quem crer em Mim,
N\xE3o ter\xE1 condena\xE7\xE3o".`,verseorder:4,ismainverse:0,id_hino:95},{idverse:25212,verse:`1. 
Se ao inimigo temes combater,
Se est\xE1s em trevas e n\xE3o tens poder,
Que a formosa luz de Deus fulgure em ti;
E ser\xE1s feliz assim.
`,verseorder:1,ismainverse:0,id_hino:96},{idverse:25213,verse:`Deixa penetrar a luz!
Deixa penetrar a luz!
Que a formosa luz de Deus fulgure em ti;
E seras feliz assim.
`,verseorder:2,ismainverse:1,id_hino:96},{idverse:25214,verse:`2. 
Se a tua f\xE9 \xE9 fraca no Senhor,
E n\xE3o mostras fruto ou nenhum fervor,
Que a formosa luz de Deus fulgure em ti
E ser\xE1s feliz assim.
`,verseorder:3,ismainverse:0,id_hino:96},{idverse:25215,verse:`3. 
Se na luz estamos, que a divina luz!
Se nos limpa sempre o sangue de Jesus,
Temos claridade Ele, no teu cora\xE7\xE3o,
Viver\xE1s ent\xE3o de amor.
`,verseorder:4,ismainverse:0,id_hino:96},{idverse:25216,verse:`4. 
Se alegre fores \xE0 mans\xE3o sem par,
\xD3 enfrenta as trevas, que v\xE3o se afastar
Que a formosa luz de Deus fulgure em ti
E ser\xE1s feliz assim.`,verseorder:5,ismainverse:0,id_hino:96},{idverse:25217,verse:`1. 
H\xE1 um caminho santo, ao c\xE9u de plena luz,
\xC1s vezes espinhosa, \xE0 gl\xF3ria nos conduz;
\xC9 o caminho certo p\`ra todo pecador;
Jesus \xE9 o Salvador, Jesus \xE9 o Salvador.
`,verseorder:1,ismainverse:0,id_hino:97},{idverse:25218,verse:`2. 
H\xE1 paz inabal\xE1vel p\`ra quem est\xE1 na luz;
Por pre\xE7o incompar\xE1vel
Comprou-me a mim Jesus
O dom do Pai amado minh\`alma satisfaz,
Jesus \xE9 minha paz, Jesus \xE9 minha paz.
`,verseorder:2,ismainverse:0,id_hino:97},{idverse:25219,verse:`3. 
H\xE1 um poder que sara o triste cora\xE7\xE3o,
Que d\xE1 al\xEDvio \xE0 alma, paz e consola\xE7\xE3o,
E guarda minha veste, tamb\xE9m, em n\xEDvea luz;
Poder de meu Jesus, poder de meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:97},{idverse:25220,verse:`4. 
H\xE1 um fim glorioso, al\xE9m do escuro v\xE9u;
No fim do espinhoso caminho est\xE1 o c\xE9u;
E quando for tirado o v\xE9u da escurid\xE3o,
Verei Jesus, ent\xE3o, verei Jesus, ent\xE3o.`,verseorder:4,ismainverse:0,id_hino:97},{idverse:25221,verse:`1. 
Estar\xE1s tu vigiando, quando Jesus vier?
Ansioso, esperando, quando Jesus vier?
`,verseorder:1,ismainverse:0,id_hino:98},{idverse:25222,verse:`Oh! Que noite! solene
noite!
Brilhar\xE1 a tua luz, quando Jesus vier?
\xD3, s\xEA pronto, \xF3, s\xEA pronto!
\xD3, s\xEA pronto, quando Jesus vier.
\xD3, s\xEA pronto, \xF3 s\xEA pronto!
\xD3, s\xEA pronto, quando Jesus vier!
`,verseorder:2,ismainverse:1,id_hino:98},{idverse:25223,verse:`2. 
Ouviras tu o bem-vindo, quando Jesus vier?
Dar\xE1s o brado sorrindo, quando Jesus vier?
`,verseorder:3,ismainverse:0,id_hino:98},{idverse:25224,verse:`3. 
S\xF3 esperas preparar-te, quando Jesus vier?
Qual ser\xE1 a tua parte, quando Jesue vier?
`,verseorder:4,ismainverse:0,id_hino:98},{idverse:25225,verse:`4. 
Estar\xE1s tu jubiloso, quando Jesus vier?
Triste ou esperan\xE7oso, quando Jesus vier?
`,verseorder:5,ismainverse:0,id_hino:98},{idverse:25226,verse:`5. 
Oh! Que culto glorioso, quando Jesus vier!
Cheio de sublime gozo, quando Jesus vier!`,verseorder:6,ismainverse:0,id_hino:98},{idverse:25227,verse:`1. 
Eis o dia a declinar,
Nos convida a meditar
No Teu grande insigne amor,
\xD3 bendito Salvador,
E te louvar!
`,verseorder:1,ismainverse:0,id_hino:99},{idverse:25228,verse:`Santo! Santo! Santo Senhor dos c\xE9us
Toda a terra louve a Ti;
No c\xE9u, tudo adore a Ti,
Bondoso Deus!
`,verseorder:2,ismainverse:1,id_hino:99},{idverse:25229,verse:`2. 
Glorioso no poder!
Infinito no saber!
Na justi\xE7a e no perd\xE3o
Nos fazes ver!
`,verseorder:3,ismainverse:0,id_hino:99},{idverse:25230,verse:`3. 
Na profunda escurid\xE3o,
Ouve a nossa peti\xE7\xE3o,
Vem teus filhos proteger,
E seguros sempre os ter
Na tua m\xE3o.
`,verseorder:4,ismainverse:0,id_hino:99},{idverse:25231,verse:`4. 
Quando tudo, enfim, passar,
C\xE9us, estrelas, terra e mar,
Pela cruz do Salvador,
Faz-nos no Teu grande amor,
Contigo estar!`,verseorder:5,ismainverse:0,id_hino:99},{idverse:25232,verse:`1. 
Quem quer ir, por Jesus, a nova proclamar
Nos antros de afli\xE7\xE3o, mis\xE9rias, mal e dor?
Crist\xE3os, anunciai que o Pai quer derramar
O bom Consolador.
`,verseorder:1,ismainverse:0,id_hino:100},{idverse:25233,verse:`O bom Consolador, o bom Consolador
Que Deus nos prometeu, ao mundo j\xE1 desceu;
\xD3 ide proclamar, que Deus quer derramar
O bom Consolador.
`,verseorder:2,ismainverse:1,id_hino:100},{idverse:25234,verse:`2. 
No mundo de horror, a luz, enfim, brilhou
Que veio dissipar as sombras de terror;
Tamb\xE9m o nosso Pai, aos seus fi\xE9is mandou
O bom Consolador.
`,verseorder:3,ismainverse:0,id_hino:100},{idverse:25235,verse:`3. 
\xC9 o Consolador que traz a salva\xE7\xE3o
Aos que aprisionou o grande tentador:
Dizei que veio j\xE1, com todo o cora\xE7\xE3o,
O bom Consolador.
`,verseorder:4,ismainverse:0,id_hino:100},{idverse:25236,verse:`4. 
Oh! grande eterno amor! oh! Gozo divinal
Que tenho em proclamar o dom de meu Senhor
Pois mora j\xE1 em mim, poder celestial,
O bom Consolador.`,verseorder:5,ismainverse:0,id_hino:100},{idverse:25237,verse:`1. 
Qual \xF3rf\xE3os n\xE3o nos deixou o Senhor,
Mas como nos prometeu,
Mandou-nos o bom Consolador,
Que nos encheu do poder do c\xE9u,
Do Seu divinal amor!
`,verseorder:1,ismainverse:0,id_hino:101},{idverse:25238,verse:`Recebestes j\xE1 a un\xE7\xE3o real
Do Esp\xEDrito Consolador?
Deus quer te usar para outros salvar,
Se recebes o Consolador.
`,verseorder:2,ismainverse:1,id_hino:101},{idverse:25239,verse:`2. 
\xD3 crentes, Deus vos convida tamb\xE9m
A vir buscar Seu poder;
\xC9 para todo, que humilde vem
O Santo H\xF3spede receber:
Orando se obt\xE9m.
`,verseorder:3,ismainverse:0,id_hino:101},{idverse:25240,verse:`3. 
Um Pentecoste, Jesus prometeu,
A cada um cora\xE7\xE3o;
Prostados, roguemos, pois a Deus,
Com f\xE9, em potente ora\xE7\xE3o,
P\`ra vir o poder do c\xE9u.`,verseorder:4,ismainverse:0,id_hino:101},{idverse:25241,verse:`1. 
Hoje, Jesus pode te salvar;
Somente cr\xEA na promessa;
Dos teus pecados purificar;
Somente cr\xEA na promessa.
`,verseorder:1,ismainverse:0,id_hino:102},{idverse:25242,verse:`Cr\xEA na promessa de Jesus,
Somente cr\xEA na promessa;
B\xEAn\xE7\xE3o dos c\xE9us, vida \xE0 fluir!
Somente cr\xEA na promessa.
`,verseorder:2,ismainverse:1,id_hino:102},{idverse:25243,verse:`2. 
Do alto, Jesus dar\xE1 poder;
Somente cr\xEA na promessa;
Na Sua alegria ir\xE1s vencer;
Somente cr\xEA na promessa.
`,verseorder:3,ismainverse:0,id_hino:102},{idverse:25244,verse:`3. 
A Sua gra\xE7a vem receber;
Somente cr\xEA na promessa;
Tudo Jesus quer te conceder.
Somente cr\xEA na promessa.
`,verseorder:4,ismainverse:0,id_hino:102},{idverse:25245,verse:`4. 
Vem receber a paz do Senhor,
Somente cr\xEA na promessa;
Cheio s\xE9ras do Consolador.
Somente cr\xEA na promessa.`,verseorder:5,ismainverse:0,id_hino:102},{idverse:25246,verse:`1. 
H\xE1 uma luz, bendita luz,
Que vem a n\xF3s dos c\xE9us;
A face do Senhor Jesus
Reflete o amor de Deus.
`,verseorder:1,ismainverse:0,id_hino:103},{idverse:25247,verse:`Caminhemos na luz
Caminhemos na luz de Deus.
Caminhemos na luz
Caminhemos na luz de Deus.
`,verseorder:2,ismainverse:1,id_hino:103},{idverse:25248,verse:`2. 
H\xE1 uma gloriosa luz
Guiando o nosso andar,
Nos santos passos de Jesus,
\`T\xE9 chegarmos ao lar.
`,verseorder:3,ismainverse:0,id_hino:103},{idverse:25249,verse:`3. 
H\xE1 uma luz, a santa luz
Na qual podemos ver
No lindo c\xE9u, a gl\xF3ria \xE0 flux,
Que vamos receber.
`,verseorder:4,ismainverse:0,id_hino:103},{idverse:25250,verse:`4. 
Em santa e bendita luz.
Cercado de esplendor,
Vir\xE1 O que morreu na cruz,
Nosso amado Senhor.`,verseorder:5,ismainverse:0,id_hino:103},{idverse:25251,verse:`1. 
Eis Jesus a procura a ovelha, que a vagar
Desgarrou-se do aprisco do Senhor;
Pelos montes a sofrer, fa\xE7a sol ou a chover,
Cristo busca Sua ovelha, com amor.
`,verseorder:1,ismainverse:0,id_hino:104},{idverse:25252,verse:`Com amor... o Pastor...
Eis que chama Sua ovelha sem cessar;
Possa hoje, o pecador
escutar a voz do amor,
De Jesus, que procura p\`ra salvar.
`,verseorder:2,ismainverse:1,id_hino:104},{idverse:25253,verse:`2. 
Com Seus p\xE9s o bom Pastor, j\xE1 feridos, que horror,
De descer nos tenebrosos pedregais,
E cansado de andar, mais ainda a clamar:
"\xD3 ovelhas minhas, onde v\xF3s estais?"
`,verseorder:3,ismainverse:0,id_hino:104},{idverse:25254,verse:`3. 
Se escuta o gemer da ovelha, que prazer
Enche logo o cora\xE7\xE3o do bom Pastor!
E correndo sem parar, \`t\xE9 a ovelha abra\xE7ar,
E traz\xEA-la ao Seu ombro com amor.
`,verseorder:4,ismainverse:0,id_hino:104},{idverse:25255,verse:`4. 
J\xE1 em casa, o Pastor, oh! Jubila com dul\xE7or,
Pela ovelha desgarrada que achou;
E assim, tamb\xE9m Jesus, com os anjos l\xE1 na luz,
Se alegra pelas almas que salvou!`,verseorder:5,ismainverse:0,id_hino:104},{idverse:25256,verse:`1. 
Possuis em ti, \xF3 pecador,
A gloriosa esperan\xE7a?
\xC9 Cristo tua confian\xE7a,
Que te sustenta com vigor?
A minha \xE9 Jesus, \xE9 Jesus, \xE9 Jesus,
A minha e Jesus,
Que me concede seguran\xE7a.
`,verseorder:1,ismainverse:0,id_hino:105},{idverse:25257,verse:`2. 
Se nada mais te satisfaz,
Aqui no mundo, \xF3 perdido,
E se te sentes abatido,
Qual h\xE1 de ser a tua paz?
A minha \xE9 Jesus, \xE9 Jesus, \xE9 Jesus,
A minha e Jesus,
Que n\xE3o me deixa desvalido.
`,verseorder:2,ismainverse:0,id_hino:105},{idverse:25258,verse:`3. 
E quando o teu fim chegar,
E te cobrir o v\xE9u da morte,
Qual h\xE1 de ser a tua sorte?
Ir\xE1s sofre? Ir\xE1s gozar?
A minha \xE9 Jesus, \xE9 Jesus, \xE9 Jesus,
A minha \xE9 Jesus,
Que me tornou, de fraco, forte.
`,verseorder:3,ismainverse:0,id_hino:105},{idverse:25259,verse:`4. 
Vem a Jesus, vem pecador,
Vem dar o teu primeiro passo;
Qual \xE9 a for\xE7a do teu bra\xE7o,
Que te far\xE1 um vencedor?
A minha \xE9 Jesus, \xE9 Jesus, \xE9 Jesus,
A minha \xE9 Jesus,
Por ela quebro todos os la\xE7os.`,verseorder:4,ismainverse:0,id_hino:105},{idverse:25260,verse:`1. 
Viva Cristo, eternal riqueza,
Meu escudo, vida e firmeza,
Sua gra\xE7a - Oh! me fa\xE7a
Reluzir no Seu amor.
`,verseorder:1,ismainverse:0,id_hino:106},{idverse:25261,verse:`2. 
Viva Cristo, rocha j\xE1 eleita,
A verdade pura e perfeita;
Meu sustento - gran contento,
Que me faz andar na luz.
`,verseorder:2,ismainverse:0,id_hino:106},{idverse:25262,verse:`3. 
Viva Cristo, minha eterna sorte;
Meu tesouro \xE9 depois da morte;
Me conforta, me exorta
A viver em perfei\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:106},{idverse:25263,verse:`4. 
Viva Cristo, o Senhor da gl\xF3ria,
Que aos crentes sempre d\xE1 vit\xF3ria,
E a vida prometida,
Que, p\`ra n\xF3s ganhou na cruz.
`,verseorder:4,ismainverse:0,id_hino:106},{idverse:25264,verse:`5. 
Viva Cristo, Condutor dos santos!
Viva Cristo, fortemente canto!
Pois me ama e derrama
No meu ser, celeste paz.`,verseorder:5,ismainverse:0,id_hino:106},{idverse:25265,verse:`1. 
Firme nas promessas do meu Salvador,
Cantarei louvores ao meu Criador.
Fico na dispensa\xE7\xE3o do Seu amor,
Firme nas promessas de Jesus.
`,verseorder:1,ismainverse:0,id_hino:107},{idverse:25266,verse:`Firme, firme,
firme nas promessas de Jesus, o Cristo;
Firme, firme,
Sim, firme nas promessas de Jesus.
`,verseorder:2,ismainverse:1,id_hino:107},{idverse:25267,verse:`2. 
Firme nas promessas,  hei de n\xE3o falhar,
Quando as tempestades, v\xEAm me assolar;
Pelo verbo vivo, hei de batalhar.
Firme nas promessas de Jesus.
`,verseorder:3,ismainverse:0,id_hino:107},{idverse:25268,verse:`3. 
Firme nas promessas, sempre vejo assim,
Purifica\xE7\xE3o no sangue, para mim;
Plena liberdade gozarei, sem fim.
Firme nas promessas de Jesus.
`,verseorder:4,ismainverse:0,id_hino:107},{idverse:25269,verse:`4. 
Firme nas promessas do Senhor Jesus,
Em amor ligado com a Sua cruz.
Cada dia mais alegro-me na luz.
Firme nas promessas de Jesus.`,verseorder:5,ismainverse:0,id_hino:107},{idverse:25270,verse:`1. 
Por Jesus vamos pelejar,
Prosseguindo o nosso andar;
E com Ele, ent\xE3o, no c\xE9u,
N\xF3s iremos a paz gozar.
`,verseorder:1,ismainverse:0,id_hino:108},{idverse:25271,verse:`Lutemos todos contra o mal,
E vamos a Jesus seguir;
Ele \xE9 o nosso General
E a gl\xF3ria do porvir!
`,verseorder:2,ismainverse:1,id_hino:108},{idverse:25272,verse:`2. 
Em Jesus temos n\xF3s poder;
Avancemos, j\xE1 sem temer;
Confiando no Seu amor,
Vamos lutar, at\xE9 vencer.
`,verseorder:3,ismainverse:0,id_hino:108},{idverse:25273,verse:`3. 
Crentes, para Jesus olhai,
Pela f\xE9, sempre, sim, lutai;
Ao inimigo, \xF3 combatei;
O Evangelho anunciai.
`,verseorder:4,ismainverse:0,id_hino:108},{idverse:25274,verse:`4. 
A Escritura nos diz assim:
Que Jesus \xE9 p\`ra ti e mim,
O caminho, a luz veraz,
Que nos leva ao c\xE9u, enfim.`,verseorder:5,ismainverse:0,id_hino:108},{idverse:25275,verse:`1. 
Se algu\xE9m deseja, venha a Jesus,
\xC1gua e p\xE3o da vida, Ele te conduz.
A m\xE3o lhe estende, pede-lhe a luz,
Se algu\xE9m deseja, venha.
`,verseorder:1,ismainverse:0,id_hino:109},{idverse:25276,verse:`Se algu\xE9m deseja, se algu\xE9m deseja,
Venha hoje mesmo receber perd\xE3o,
Pois Jesus te chama, \xF3, n\xE3o chama em v\xE3o,
Se algu\xE9m deseja, venha!
`,verseorder:2,ismainverse:1,id_hino:109},{idverse:25277,verse:`2. 
Se algu\xE9m deseja, paz fruir\xE1;
Cristo que te chama, te libertar\xE1;
Com Seus bra\xE7os santos te abra\xE7ar\xE1.
Se algu\xE9m deseja, venha!
`,verseorder:3,ismainverse:0,id_hino:109},{idverse:25278,verse:`3. 
Se algu\xE9m deseja, n\xE3o morrer\xE1,
Pois a vida eterna sempre gozar\xE1;
Esta \xE9 a promessa que Jesus te d\xE1.
Se algu\xE9m deseja, venha!`,verseorder:4,ismainverse:0,id_hino:109},{idverse:25279,verse:`1. 
\xD3 clama na tribula\xE7\xE3o: Jesus, Jesus,
De todo o teu cora\xE7\xE3o: Jesus, Jesus,
Por certo, te socorrer\xE1: Jesus, Jesus,
E Gozo e paz te dar\xE1: Jesus, Jesus.
`,verseorder:1,ismainverse:0,id_hino:110},{idverse:25280,verse:`2. 
Tu queres um bom Salvador? Jesus, Jesus,
Que ama o vil pecador? Jesus, Jesus,
E que tem poder p\`ra salvar? Jesus, Jesus,
E para o c\xE9u te levar? Jesus, Jesus.
`,verseorder:2,ismainverse:0,id_hino:110},{idverse:25281,verse:`3. 
H\xE1 um que te pode curar: Jesus, Jesus.
E da corrup\xE7\xE3o te guardar: Jesus, Jesus.
S\xF3 um nome d\xE1 salva\xE7\xE3o: Jesus, Jesus.
O gozo, a paz, o perd\xE3o: Jesus, Jesus.
`,verseorder:3,ismainverse:0,id_hino:110},{idverse:25282,verse:`4. 
Aceita, no teu cora\xE7\xE3o: Jesus, Jesus;
Assim, tu ter\xE1s salva\xE7\xE3o, Jesus, Jesus;
Abra\xE7a, com terno amor: Jesus, Jesus;
Que chora por ti, pecador, Jesus, Jesus.
`,verseorder:4,ismainverse:0,id_hino:110},{idverse:25283,verse:"5. ",verseorder:5,ismainverse:0,id_hino:110},{idverse:25284,verse:`1. 
Que mudan\xE7a em mim fez o meu bom Jesus,
Vindo Ele ao meu cora\xE7\xE3o.
Deu-me paz paz divinal, deu-me gozo e luz,
Entrando em meu cora\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:111},{idverse:25285,verse:`Entrando no meu cora\xE7\xE3o!
Entrando no meu cora\xE7\xE3o!
Que mudan\xE7a em mim
Fez o meu bom Jesus,
Vindo Ele ao meu cora\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:111},{idverse:25286,verse:`2. 
Eu andava pedido, Jesus me salvou,
Enrando no meu cora\xE7\xE3o.
Meus pecados sem conta, Seu sangue lavou,
Vindo Ele ao meu cora\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:111},{idverse:25287,verse:`3. 
E agora, do c\xE9u, a certeza me deu,
Entrando no meu cora\xE7\xE3o.
Seu Esp\xEDrito,  selo e penhor concedeu,
Vindo Ele ao meu cora\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:111},{idverse:25288,verse:`4. 
Pelo vale da morte n\xE3o temo passar!
Com Ele no meu cora\xE7\xE3o;
A cidade celeste me vai transportar,
O guarda do meu cora\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:111},{idverse:25289,verse:`1. 
N\xE3o h\xE1 na terra e c\xE9u tamb\xE9m,
Um nome com do\xE7ura,
Como este a quem louvor conv\xE9m,
De toda a criatura.
`,verseorder:1,ismainverse:0,id_hino:112},{idverse:25290,verse:`Jesus, a luz \xE9s Tu do c\xE9u!
Jesus, o nosso amado!
Jesus, o verdadeiro Deus,
Por todos exaltado!
`,verseorder:2,ismainverse:1,id_hino:112},{idverse:25291,verse:`2. 
Descendo ao mundo, a luz do c\xE9u
Vestiu-se em corpo humano;
Ao santo Filho, ent\xE3o, se deu
O nome soberano.
`,verseorder:3,ismainverse:0,id_hino:112},{idverse:25292,verse:`3. 
Entregue \xE0 morte sobre a cruz,
Por cima foi escrito
O nome am\xE1vel de Jesus,
Por n\xF3s, ali maldito.
`,verseorder:4,ismainverse:0,id_hino:112},{idverse:25293,verse:`4. 
Da morte o grande vencedor
\xC0 m\xE3o do Pai sentado,
Por n\xF3s Ele \xE9 intercessor,
E \xFAnico Advogado.
`,verseorder:5,ismainverse:0,id_hino:112},{idverse:25294,verse:`5. 
Jesus, meu terno Salvador,
\xC9s tu minha esperan\xE7a,
Confio s\xF3 no Teu amor;
E p\xF5es-me em seguran\xE7a.`,verseorder:6,ismainverse:0,id_hino:112},{idverse:25295,verse:`1. 
Dia a dia vem guiar-me
Meu Jesus e meu Senhor!
N\xE3o sou meu, pois me compraste
Com teu sangue redentor;
Quero a Ti viver unido,
Pela vida at\xE9 ao fim;
Que se torne assim verdade.
"N\xE3o mais eu, mas Cristo em mim".
Que se torne assim verdade.
"N\xE3o mais eu, mas Cristo em mim".
`,verseorder:1,ismainverse:0,id_hino:113},{idverse:25296,verse:`2. 
Cada instante vem guiar-me,
O celeste Diretor!
J\xE1 fizeste em mim morada,
Tu \xE9s meu Dominador;
\xF3 transforma minha vida;
Seja meu o Teu querer;
Quero andar, passo por passo,
Confiando em Teu poder.
Quero andar, passo por passo,
Confiando em Teu poder.
`,verseorder:2,ismainverse:0,id_hino:113},{idverse:25297,verse:`3. 
Se maior intimidade
Para mim poss\xEDvel for;
Quero mais ser atra\xEDdo
A Ti, meigo Salvador!
Ah! Que fique em Ti morando
Meu secreto pavilh\xE3o,
Escondido, inteiramente,
Em Ti, minha salva\xE7\xE3o.
Escondido, inteiramente,
Em Ti, minha salva\xE7\xE3o.`,verseorder:3,ismainverse:0,id_hino:113},{idverse:25298,verse:`1. 
Se tu cansado j\xE1 est\xE1s de pecar,
Aceita o perd\xE3o de Jesus.
Se vida nova quiseres achar,
Aceita o perd\xE3o de Jesus.
`,verseorder:1,ismainverse:0,id_hino:114},{idverse:25299,verse:`Vem a Jesus, pecador.
Por ti morreu l\xE1 na cruz.
\xC9 tempo, vem sem temor;
Aceita o perd\xE3o de Jesus.
`,verseorder:2,ismainverse:1,id_hino:114},{idverse:25300,verse:`2. 
Se vida pura desejas obter,
Aceita o perd\xE3o de Jesus.
Ele \xE9 a fonte que limpa teu ser;
Aceita o perd\xE3o de Jesus.
`,verseorder:3,ismainverse:0,id_hino:114},{idverse:25301,verse:`3. 
Se tu n\xE3o podes tuas lutas calmar,
Aceita o perd\xE3o de Jesus.
Se teus desejos n\xE3o podem domar,
Aceita o perd\xE3o de Jesus.
`,verseorder:4,ismainverse:0,id_hino:114},{idverse:25302,verse:`4. 
Se com os santos te queres unir,
Aceita o perd\xE3o de Jesus.
Se para os c\xE9us tu desejas partir,
Aceita o perd\xE3o de Jesus.`,verseorder:5,ismainverse:0,id_hino:114},{idverse:25303,verse:`1. 
Eu quero trabalhar p\`ra meu Senhor,
Levando a Palavra com amor;
Quero eu cantar e orar,
E ocupado quero estar,
Sim, na vinha do Senhor.
`,verseorder:1,ismainverse:0,id_hino:115},{idverse:25304,verse:`Trabalhai e orai,
Na seara e na vinha do Senhor;
Meu desejo e orar,
Ocupado quero estar
Sim, na vinha do Senhor.
`,verseorder:2,ismainverse:1,id_hino:115},{idverse:25305,verse:`2. 
Eu quero, cada dia trabalhar;
Escravos do pecado libertar;
Conduzi-los a Jesus,
Nosso guia, nossa luz,
Sim, na vinha do Senhor.
`,verseorder:3,ismainverse:0,id_hino:115},{idverse:25306,verse:`3. 
Eu quero ser obreiro de valor,
Confiando no poder do Salvador;
Se quiseres trabalhar,
Achar\xE1s tamb\xE9m lugar,
Sim, na vinha do Senhor.`,verseorder:4,ismainverse:0,id_hino:115},{idverse:25307,verse:`1. 
Eu vagava pela senda de horror,
Oprimido pelo pecado e temor.
Quando o Salvador eu vi,
Sua terna voz ouvi,
Meu Jesus me libertou por Seu amor.
`,verseorder:1,ismainverse:0,id_hino:116},{idverse:25308,verse:`Livre estou! Livre estou!
Pela gra\xE7a de Jesus livre estou;
Livre estou! Livre estou!
Aleluia! Pela f\xE9, livre estou!
`,verseorder:2,ismainverse:1,id_hino:116},{idverse:25309,verse:`2. 
Eu vagava pela senda de horror,
N\xE3o pensava no amor do Salvador;
Eu vagava sem ter luz,
Longe do Senhor Jesus,
Mais liberto, hoje canto a Deus louvor.
`,verseorder:3,ismainverse:0,id_hino:116},{idverse:25310,verse:`3. 
Eu vagava pela senda de horror,
Mas, agora, quero andar com meu Senhor,
Quero ouvir Sua terna voz,
E segui-Lo sempre ap\xF3s,
Gl\xF3ria seja dada ao nosso bom Pastor.`,verseorder:4,ismainverse:0,id_hino:116},{idverse:25311,verse:`1. 
Aleluia! O Senhor salva a todo pecador!
Salva\xE7\xE3o! Salva\xE7\xE3o!
Jesus Cristo tem poder para todo o mal vencer,
Salva\xE7\xE3o! Salva\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:117},{idverse:25312,verse:`Salva\xE7\xE3o e reden\xE7\xE3o!
Aleluia! Cristo j\xE1 me amou e me salvou.
Gl\xF3ria dou a aleleuia, a Jesus que me salvou.
`,verseorder:2,ismainverse:1,id_hino:117},{idverse:25313,verse:`2. 
Eu confio em Jeov\xE1, Ele santidade d\xE1;
Salva\xE7\xE3o! Salva\xE7\xE3o!
tenho paz e vivo j\xE1, Sua gra\xE7a em mim est\xE1.
Salva\xE7\xE3o! Salva\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:117},{idverse:25314,verse:`3. 
Falarei sempre o que sei, exaltando o meu Rei,
Salva\xE7\xE3o! Salva\xE7\xE3o!
Pois Seu sangue j\xE1 verteu, p\`ra tornar-me filho Seu,
Salva\xE7\xE3o! Salva\xE7\xE3o!
`,verseorder:4,ismainverse:0,id_hino:117},{idverse:25315,verse:`4. 
Cantaremos, sempre ali, ao sairmos n\xF3s daqui;
Salva\xE7\xE3o! Salva\xE7\xE3o!
Exaltando o Salvador, louvaremos com fervor.
Salva\xE7\xE3o! Salva\xE7\xE3o!`,verseorder:5,ismainverse:0,id_hino:117},{idverse:25316,verse:`1. 
Na presen\xE7a estar de Cristo,
Em Sua gl\xF3ria, que ser\xE1;
L\xE1 no c\xE9u, em pleno gozo,
Minha alma O ver\xE1.
`,verseorder:1,ismainverse:0,id_hino:118},{idverse:25317,verse:`Face a face, espero v\xEA-lo;
No al\xE9m do c\xE9u de luz;
Face a face em plena gl\xF3ria,
Hei de ver o meu Jesus.
`,verseorder:2,ismainverse:1,id_hino:118},{idverse:25318,verse:`2. 
Oh! que gl\xF3ria ser\xE1 v\xEA-lo;
Que O possa eu mirar!
Eis, em breve, vem o dia;
Que sua gl\xF3ria h\xE1 de mostrar.
`,verseorder:3,ismainverse:0,id_hino:118},{idverse:25319,verse:`3. 
Quanto gozo h\xE1 em Cristo,
Quando n\xE3o houver mais dor,
Quando cessar  o perigo,
E gozarmos pleno amor.
`,verseorder:4,ismainverse:0,id_hino:118},{idverse:25320,verse:`4. 
Face a face, qu\xE3o glorioso
H\xE1 de ser o existir,
Vendo o rosto de quem veio,
Nossas almas redimir.`,verseorder:5,ismainverse:0,id_hino:118},{idverse:25321,verse:`1. 
Seja louvado o bom Salvador
Livre \xE9 Seu amor, livre \xE9 Seu amor.
H\xE1 preparado para o pecador,
Fonte preciosa de amor;
E, se em Cristo queres confiar,
Livre \xE9 Seu amor, livre \xE9 seu amor;
A vida eterna tu ir\xE1s gozar,
Inesgot\xE1vel \xE9 Seu amor.
`,verseorder:1,ismainverse:0,id_hino:119},{idverse:25322,verse:`Terno, te chama Jesus: querer ir?
\xD3 pecador, \xF3 pecador!
Ele deixou para n\xF3s, ao subir,
Fonte preciosa de amor.
`,verseorder:2,ismainverse:1,id_hino:119},{idverse:25323,verse:`2. 
Por que vivendo em pecado h\xE1 de estar?
Livre \xE9 Seu amor, livre \xE9 Seu amor.
Cristo te chama, \xF3 vem sem tardar,
Inesgot\xE1vel \xE9 Seu amor;
Deixa as trevas por luz divinal,
Livre \xE9 Seu amor, livre \xE9 Seu amor;
Hoje Jesus quer  livrar-te do mal,
Inesgot\xE1vel \xE9 Seu amor.
`,verseorder:3,ismainverse:0,id_hino:119},{idverse:25324,verse:`3. 
\xD3! se tu creres, perd\xE3o obter\xE1s,
Livre \xE9 Seu amor, livre \xE9 Seu amor;
Vem o momento que tu bendir\xE1s;
Inesgot\xE1vel \xE9 Seu amor;
Cristo te espera, \xF3 ouve o chamar;
Livre \xE9 Seu amor, livre \xE9 Seu amor;
F\xE9 em Seu nome te h\xE1 de salvar.
Inesgot\xE1vel \xE9 Seu amor.`,verseorder:4,ismainverse:0,id_hino:119},{idverse:25325,verse:`1. 
Noite de paz, Noite de Amor;
Tudo dorme, em derredor!
Entre os Astros que espargem a Luz
Bela, indicando o menino Jesus,
Brilha a estrela da Paz,
Brilha a estrela da Paz.
`,verseorder:1,ismainverse:0,id_hino:120},{idverse:25326,verse:`2. 
Noite de paz, Noite de Amor,
Ouve o fiel pastor,
Coros celestes que cantam a paz,
Que nesta noite sublime nos traz,
O nosso bom Redentor,
O nosso bom Redentor.
`,verseorder:2,ismainverse:0,id_hino:120},{idverse:25327,verse:`3. 
Noite de paz, Noite de Amor,
Oh! Que belo resplendor,
Paira no rosto do meigo Jesus!
E no pres\xE9pio do mundo, a luz!
Astro de eterno fulgor,
Astro de eterno fulgor.`,verseorder:3,ismainverse:0,id_hino:120},{idverse:25328,verse:`1. 
Maravilhoso \xE9 Jesus,
Da minha vida a luz,
Pois me salvando, me perdoando,
Para Si\xE3o me conduz.
`,verseorder:1,ismainverse:0,id_hino:121},{idverse:25329,verse:`Maravilhoso, sim, maravilhoso!
\xC9 cristo, pois salvou-me e mim!
Maravilhoso e mui glorioso,
\xC9 Cristo, que vive em mim.
`,verseorder:2,ismainverse:1,id_hino:121},{idverse:25330,verse:`2. 
Nunca podia saber,
Qual \xE9 o grande prazer,
Dum que perdido, arrependido,
A gra\xE7a vem receber.
`,verseorder:3,ismainverse:0,id_hino:121},{idverse:25331,verse:`3. 
Ele nas trevas me achou,
E ternamente me amou;
Vida abundante, gozo bastante,
Tenho por quem me salvou.
`,verseorder:4,ismainverse:0,id_hino:121},{idverse:25332,verse:`4. 
Cristo \xE9 tudo p\`ra mim,
Tamb\xE9m ser\xE1 para ti;
Vou caminhando, sempre O louvando,
Pois Ele salvou-me a mim!`,verseorder:5,ismainverse:0,id_hino:121},{idverse:25333,verse:`1. 
Fogo divino,  clamamos por ti;
Vem l\xE1 do alto, vem, desce aqui;
\xD3 vem despertar-nos com teu fulgor;
Vem inflamar-nos com teu calor.
`,verseorder:1,ismainverse:0,id_hino:122},{idverse:25334,verse:`Desce do alto, bendito fogo,
Desce poder celestial!
Desce do alto, bendito fogo,
Vem, chama pentecostal!
`,verseorder:2,ismainverse:1,id_hino:122},{idverse:25335,verse:`2. 
Desce, Esp\xEDrito Consolador,
Desce e enche-nos de santo amor,
Desce ao mundo, nos mostra Jesus;
D\xE1-nos poder, vida, gra\xE7a e luz.
`,verseorder:3,ismainverse:0,id_hino:122},{idverse:25336,verse:`3. 
Arde em minh\`alma, \xF3 chama de amor
Arde em meu peito e d\xE1-me valor;
Consome todos os restos do mal;
Desce j\xE1, \xF3 fogo pentecostal!`,verseorder:4,ismainverse:0,id_hino:122},{idverse:25337,verse:`1. 
Um dia Cristo voltar\xE1;
Ao ascender, o prometeu;
Do modo que subiu vir\xE1;
H\xE1 de ver o Rei Jesus, o povo seu.
`,verseorder:1,ismainverse:0,id_hino:123},{idverse:25338,verse:`Mui breve sim, Jesus vir\xE1,
Alegre O ver\xE1 Seu povo;
Velando, todos devem sempre estar,
A fim de v\xEA-lo voltar.
`,verseorder:2,ismainverse:1,id_hino:123},{idverse:25339,verse:`2. 
Os menageiros do Senhor,
Afirmam que Jesus vir\xE1;
E o poder Consolador
As fi\xE9is promessas nos revelar\xE1.
`,verseorder:3,ismainverse:0,id_hino:123},{idverse:25340,verse:`3. 
Oh! gozo sem compara\xE7\xE3o
No dia do meu Salvador,
Com a mui grande multid\xE3o,
Subiremos ao encontro do Senhor.
`,verseorder:4,ismainverse:0,id_hino:123},{idverse:25341,verse:`4. 
Bem-vindo sejas, meu Senhor,
Em Tua gloriosa luz;
E nossa f\xE9 ter\xE1 valor;
N\xF3s dizemos: "Ora, vem Senhor Jesus".`,verseorder:5,ismainverse:0,id_hino:123},{idverse:25342,verse:`1. 
Adorai o Rei do Universo!
Terra e c\xE9us, cantai o Seu louvor!
Todo o ser no grande mar submerso,
Louve ao Dominador!
`,verseorder:1,ismainverse:0,id_hino:124},{idverse:25343,verse:`Todos juntos O louvemos!
Grande Salvador e Redentor!
Todos O louvemos!
R\xE9gio Dominador!
`,verseorder:2,ismainverse:1,id_hino:124},{idverse:25344,verse:`2. 
Adorai-O, anjos poderosos,
V\xF3s que Sua gl\xF3ria contemplais!
V\xF3s, remidos, j\xE1 vitoriosos;
Gra\xE7as, rendei-lhe mais!
`,verseorder:3,ismainverse:0,id_hino:124},{idverse:25345,verse:`3. 
Sol e lua, coros estelares,
Sua majestade anunciai,
Hostes grandes, centos de milhares,
O Seu poder mostrai!
`,verseorder:4,ismainverse:0,id_hino:124},{idverse:25346,verse:`4. 
Ventos! Chuvas! Raios! Trovoadas!
Revelai o forte Criador!
V\xF3s dizeis, \xF3 serras elevadas,
Qu\xE3o grande \xE9 meu Senhor!
`,verseorder:5,ismainverse:0,id_hino:124},{idverse:25347,verse:`5. 
Altos cedros! Grama verdejante!
Esta sinfonia aumentai;
Aves, vermes, todo o ser gigante;
Gratos a Deus louvai!
`,verseorder:6,ismainverse:0,id_hino:124},{idverse:25348,verse:`6. 
Homens! Jovens! Velhos e meninos!
Adorai ao vosso Redentor!
Reis e s\xE1bios, grandes, pequeninos,
Dai-lhe veraz louvor!`,verseorder:7,ismainverse:0,id_hino:124},{idverse:25349,verse:`1. 
Vem, outra vez, nosso Salvador;
Quem dera hoje vir!
Para reinar com poder e amor!
Quem dera hoje vir!
J\xE1 por Sua Esposa, vem esta vez;
Que O espera em ora\xE7\xE3o;
Fora do mundo de entremez;
Quem dera hoje vir!
`,verseorder:1,ismainverse:0,id_hino:125},{idverse:25350,verse:`Gl\xF3ria, gl\xF3ria, gozo sem fim, trar\xE1;
Gl\xF3ria, gl\xF3ria, ao coroar o Rei;
Gl\xF3ria, gl\xF3ria, nos arrebatar\xE1;
Gl\xF3ria, gl\xF3ria! Cristo vem outra vez!
`,verseorder:2,ismainverse:1,id_hino:125},{idverse:25351,verse:`2. 
Terminar\xE1 o poder do mal;
Quem dera hoje ser!
Come\xE7ar\xE1 o prazer sem igual;
Quem dera hoje ser!
Todos os mortos em Cristo ser\xE3o
Arrebatados por seu Senhor;
Quando estas gl\xF3rias aqui vir\xE3o?
Quem dera hoje ser!
`,verseorder:3,ismainverse:0,id_hino:125},{idverse:25352,verse:`3. 
Em santidade nos deve achar,
Eis quEle pode vir.
Todos, velando, com gozo e paz,
Eis quEle pode vir.
Multiplicados os sinais est\xE3o;
No oriente se v\xEA o alvor;
Breve, os crentes subir\xE3o;
Quem dera hoje vir!`,verseorder:4,ismainverse:0,id_hino:125},{idverse:25353,verse:`1. 
Bem-aventurado o que confia
No Senhor, como fez Abra\xE3o;
Ele creu, ainda que n\xE3o via,
E, assim, a f\xE9 n\xE3o foi em v\xE3o.
\xC9 feliz quem segue, fielmente,
Nos caminhos santos do Senhor,
Na tribula\xE7\xE3o \xE9 paciente,
Esperando no seu Salvador.
`,verseorder:1,ismainverse:0,id_hino:126},{idverse:25354,verse:`2. 
Os her\xF3is da B\xEDblia Sagrada,
N\xE3o flu\xEDram logo seus trof\xE9us;
Mas levaram sempre a cruz pesada,
Para obter poder dos c\xE9us,
E depois, sa\xEDram pelo mundo,
Como mensageiros do Senhor,
Com coragem e amor profundo,
Proclamando Cristo, o Salvador.
`,verseorder:2,ismainverse:0,id_hino:126},{idverse:25355,verse:`3. 
Quem quiser de Deus ter a coroa,
Passar\xE1 por mais tribula\xE7\xE3o;
\xC0s alturas santas ningu\xE9m voa,
Sem as asas da humilha\xE7\xE3o;
O Senhor tem dado aos Seus queridos,
Parte do Seu glorioso ser;
Quem no cora\xE7\xE3o for  mais ferido,
Mais daquela gl\xF3ria h\xE1 de ter.
`,verseorder:3,ismainverse:0,id_hino:126},{idverse:25356,verse:`4. 
Quando aqui as flores j\xE1 fenecem,
As do c\xE9u come\xE7am a brilhar;
Quando as esperan\xE7as desvanecem,
O aflito crente vai orar;
Os mais belos hinos e poesias,
Foram escritos em tribula\xE7\xE3o,
E do c\xE9u, as lindas melodias,
Se ouviram, na escurid\xE3o.
`,verseorder:4,ismainverse:0,id_hino:126},{idverse:25357,verse:`5. 
Sim, confia tu, inteiramente;
Na imensa gra\xE7a do Senhor;
Seja de ti longe o desalento
E confia no Seu santo amor.
Aleluia seja a divisa,
Do her\xF3i e todo o vencedor;
E do c\xE9u mais forte vem a brisa,
Que te leva ao seio do Senhor.`,verseorder:5,ismainverse:0,id_hino:126},{idverse:25358,verse:`1. 
O Senhor da ceifa est\xE1 chamando:
"Quem que ir por Mim a procurar
Almas que no mundo, v\xE3o chorando;
Sem da salva\xE7\xE3o participar?"
`,verseorder:1,ismainverse:0,id_hino:127},{idverse:25359,verse:`Fala Deus! Fala Deus!
Toca-me com brasa do altar;
Dala Deus! Fala Deus!
Sim, alegre, atendo ao Teu mandar.
`,verseorder:2,ismainverse:1,id_hino:127},{idverse:25360,verse:`2. 
O profeta a Deus se aproximando,
Considera-se um pecador,
Mas, o fogo santo o queimando,
Torna-o \xFAtil para seu Senhor.
`,verseorder:3,ismainverse:0,id_hino:127},{idverse:25361,verse:`3. 
Muitos s\xE3o os que v\xE3o expirando
Sem ter esperan\xE7a de ver Deus:
Vai depressa lhes anunciando,
Que Jesus os leva para os c\xE9us.
`,verseorder:4,ismainverse:0,id_hino:127},{idverse:25362,verse:`4. 
Breve, os trabalhos ser\xE3o findos,
B\xEAn\xE7\xE3os v\xE3o os servos desfrutar;
E Jesus os saudar\xE1: "Bem-vindos",
Esta gl\xF3ria espero alcan\xE7ar.`,verseorder:5,ismainverse:0,id_hino:127},{idverse:25363,verse:`1. 
\xD3 alma triste, perdida,
Que vagas sem ter dire\xE7\xE3o!
Aceita de Deus a vida,
Entrega teu cora\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:128},{idverse:25364,verse:`D\xE1, hoje, a Cristo,
Teu cora\xE7\xE3o, teu cora\xE7\xE3o.
Pois nEle achar\xE1s a paz, o perd\xE3o,
Entrega teu cora\xE7\xE3o.
D\xE1, hoje, a Cristo,
Teu cora\xE7\xE3o, teu cora\xE7\xE3o.
Pois nEle achar\xE1s a paz, o perd\xE3o,
Entrega teu cora\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:128},{idverse:25365,verse:`2. 
Jesus n\xE3o quer teu dinheiro;
De gra\xE7a te d\xE1 salva\xE7\xE3o!
E gozo mui verdadeiro:
Entrega teu cora\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:128},{idverse:25366,verse:`3. 
De Cristo ressuscitado
Aceita o vero perd\xE3o,
Se queres ser libertado,
Entrega teu cora\xE7\xE3o!
`,verseorder:4,ismainverse:0,id_hino:128},{idverse:25367,verse:`4. 
Agora \xE9 o tempo aceit\xE1vel
O dia em que h\xE1 salva\xE7\xE3o.
A Cristo, meigo, ador\xE1vel
Entrega teu cora\xE7\xE3o.
`,verseorder:5,ismainverse:0,id_hino:128},{idverse:25368,verse:"5. ",verseorder:6,ismainverse:0,id_hino:128},{idverse:25369,verse:`1. 
\xD3 pecador, vem \xE0 fonte,
Com o teu fardo de dor,
Vem sepult\xE1-lo nas \xE1guas;
Paz achar\xE1s no Senhor.
`,verseorder:1,ismainverse:0,id_hino:129},{idverse:25370,verse:`Depressa vem, depressa vem!
Um s\xF3 instante n\xE3o queiras perder;
Jesus espera salvar-te;
Merc\xEA p\`ra ti, Ele tem.
`,verseorder:2,ismainverse:1,id_hino:129},{idverse:25371,verse:`2. 
Como est\xE1s, vem \xE0 fonte,
Jesus esperando est\xE1;
E tua alma manchada
Alva e pura ser\xE1.
`,verseorder:3,ismainverse:0,id_hino:129},{idverse:25372,verse:`3. 
O Salvador n\xE3o despreza
Quem, arrependido, crer.
A vida eterna esperando
De Suas m\xE3os receber.
`,verseorder:4,ismainverse:0,id_hino:129},{idverse:25373,verse:`4. 
Vem ser curado na fonte;
Jesus a paz te quer dar;
Vem, humilhado e contrito,
Tua alma purificar.`,verseorder:5,ismainverse:0,id_hino:129},{idverse:25374,verse:`1. 
Manso Pastor j\xE1 no mundo viveu e Ele \xE9 meu,
Nos dando a vida, por n\xF3s padeceu e Ele \xE9 meu;
Gra\xE7a t\xE3o vasta n\xE3o posso sondar, mas Ele \xE9 meu.
Sou perdoado j\xE1 posso cantar, que Ele \xE9 meu.
`,verseorder:1,ismainverse:0,id_hino:130},{idverse:25375,verse:`Cristo \xE9 meu!
Cristo \xE9 meu!
Maravilhoso, mas certo \xE9 dizer:
Jesus \xE9 meu!
`,verseorder:2,ismainverse:1,id_hino:130},{idverse:25376,verse:`2. 
H\xE1 um amigo de amor sem igual e Ele \xE9 meu;
Sempre o mais forte, o mais firme e leal e Ele \xE9 meu.
Sou t\xE3o indigno de ser um dos Seus, mas Ele \xE9 meu.
Vou Seu amor desfrutar, l\xE1 nos c\xE9us, pois Ele \xE9 meu.
`,verseorder:3,ismainverse:0,id_hino:130},{idverse:25377,verse:`3. 
Ele mandou-nos o Consolador, tamb\xE9m \xE9 meu.
Cheio de gra\xE7a, de paz e de amor, e tudo \xE9 meu.
Nada me nega dos Seus ricos dons, pois tudo \xE9 meu.
Grandes tesouros, t\xE3o puros e bons, s\xE3o todos meus.`,verseorder:4,ismainverse:0,id_hino:130},{idverse:25378,verse:`1. 
S\xF3 em Ti, meu Senhor eu desejo estar,
E tamb\xE9m receber Tua luz;
De valor em valor s\xF3 por Ti avan\xE7ar,
\xD3 me traz junto a Ti, meu Jesus.
`,verseorder:1,ismainverse:0,id_hino:131},{idverse:25379,verse:`Eu desejo, \xF3 Deus, em Jesus
habitar;
Pois minh\`alma suspira por Ti;
De valor em valor, e na f\xE9 aumentar,
Para que vejam Cristo em mim.
`,verseorder:2,ismainverse:1,id_hino:131},{idverse:25380,verse:`2. 
O caminho, Senhor, faz-me mais conhecer,
Que me leva \xE0 santa Si\xE3o;
Por Teu grande favor, e com muito prazer
Entrarei na eternal mans\xE3o.
`,verseorder:3,ismainverse:0,id_hino:131},{idverse:25381,verse:`3. 
Tua m\xE3o, \xF3 Senhor! Queira me proteger,
E dos males daqui me livrar;
Na manh\xE3 d\`esplendor, eu tamb\xE9m quero ser
Dos fi\xE9is, que v\xE3o ressuscitar.`,verseorder:4,ismainverse:0,id_hino:131},{idverse:25382,verse:`1. 
Somos obreiros do Senhor,
\xC0 messe vamos, com amor,
A proclamar a salva\xE7\xE3o
De Cristo e o Seu perd\xE3o;
Pois trabalhemos por amor
De Cristo, nosso Salvador,
Que por n\xF3s o sangue derramou,
E no Calv\xE1rio expirou.
`,verseorder:1,ismainverse:0,id_hino:132},{idverse:25383,verse:`\xC0 colheita vinde j\xE1, pois Jesus vos chama;
"Todos devem trabalhar", o Senhor proclama,
Breve, tudo findar\xE1, n\xE3o se poder\xE1 segar;
Vinde crentes, vinde trabalhar.
`,verseorder:2,ismainverse:1,id_hino:132},{idverse:25384,verse:`2. 
O Evangelho, \xF3 pregai,
A todo mundo, sim chamai,
E, sem temor, as novas dai
E a bandeira levantai
Em nossas m\xE3os Deus confiou,
A gra\xE7a que nos enviou.
As novas dai, n\xE3o vaciles,
Que a vit\xF3ria obtereis.
`,verseorder:3,ismainverse:0,id_hino:132},{idverse:25385,verse:`3. 
As horas, prestes, passar\xE3o,
E nunca  mais a v\xF3s vir\xE3o,
Se n\xE3o falardes de Jesus,
Alguns jamais ver\xE3o a luz.
Ainda hoje h\xE1 perd\xE3o,
O mundo jaz na perdi\xE7\xE3o,
Oh! n\xE3o desprezeis o favor
Do amoroso Salvador.`,verseorder:4,ismainverse:0,id_hino:132},{idverse:25386,verse:`1. 
Teu nome, por inteiro, escrito est\xE1 nos c\xE9us
No livro do Cordeiro, no livro do bom Deus,
Teremos a vit\xF3ria, olhando no Senhor;
Por isso, damos gl\xF3rias e gran louvor.
`,verseorder:1,ismainverse:0,id_hino:133},{idverse:25387,verse:`Teu nome escrito, irm\xE3o,
est\xE1 por Deus,
No livro da vida, no rol dos filhos Seus,
Teu nome escrito, irm\xE3o, est\xE1 nos c\xE9us;
Oh! gl\xF3ria, aleluia! Am\xE9m.
`,verseorder:2,ismainverse:1,id_hino:133},{idverse:25388,verse:`2. 
Alegres n\xF3s andamos na gra\xE7a do Senhor;
E nela n\xF3s achamos poder consolador!
De Cristo recebemos, ajuda e vigor;
Avante, pois marchemos, sem mais temor.
`,verseorder:3,ismainverse:0,id_hino:133},{idverse:25389,verse:`3. 
N\xF3s criaturas, somos remidas por Jesus,
No qual eleitos fomos, p\`ra desfrutar a luz,
N\xF3s temos, por heran\xE7a, Jesus  nos altos c\xE9us,
Tamb\xE9m a esperan\xE7a de ver a Deus.
`,verseorder:4,ismainverse:0,id_hino:133},{idverse:25390,verse:`4. 
Contentes, levantemos os olhos para os c\xE9us,
Escritos n\xF3s j\xE1 temos, os nomes, l\xE1 por Deus;
Sentindo grande gozo, servindo ao Senhor,
Ao todo-poderoso, ao Redentor.`,verseorder:5,ismainverse:0,id_hino:133},{idverse:25391,verse:`1. 
Quem est\xE1 batendo assim,
Que enche-me de como\xE7\xE3o?
Quem procurar\xE1 por mim?
Sinto bater no cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:134},{idverse:25392,verse:`Quem bate, diz com mansid\xE3o:
Eu sou Jesus, te vim salvar;
Abre, duro cora\xE7\xE3o,
Dentro de ti quero cear.
`,verseorder:2,ismainverse:1,id_hino:134},{idverse:25393,verse:`2. 
Quem ser\xE1? Quem pode ser?
Sinto bater no cora\xE7\xE3o!
Que farei? Que vou fazer?
No vale estou da decis\xE3o.
`,verseorder:3,ismainverse:0,id_hino:134},{idverse:25394,verse:`3. 
Quem escuto eu bater?
- Eu sou Jesus, o bom Jesus:
Abre logo, sem temer
Quero te dar a minha luz.
`,verseorder:4,ismainverse:0,id_hino:134},{idverse:25395,verse:`4. 
Entra, entra, Salvador,
Meu cora\xE7\xE3o te quero dar,
S\xF3 em Ti achei amor,
Mesmo em mim queiras morar
`,verseorder:5,ismainverse:0,id_hino:134},{idverse:25396,verse:`5. 
No cora\xE7\xE3o, \xF3 bom Jesus,
Entra, trazendo Teu perd\xE3o!
Concedendo Tua luz,
Tenho, assim, a salva\xE7\xE3o.`,verseorder:6,ismainverse:0,id_hino:134},{idverse:25397,verse:`1. 
Teu nome \xE9 precioso, amado Salvador,
E grande, glorioso, nos enche de amor,
N\xF3s todos Te adoramos com verdadeira f\xE9,
Tamb\xE9m nos humilhamos, aos Teus santos p\xE9s.
`,verseorder:1,ismainverse:0,id_hino:135},{idverse:25398,verse:`Quantas b\xEAn\xE7\xE3os
prometidas
No Teu nome, \xF3 Salvador!
Hoje tenho vida. Gra\xE7a \xE9 concedida,
No Teu nome, \xF3 Salvador!
`,verseorder:2,ismainverse:1,id_hino:135},{idverse:25399,verse:`2. 
Teu nome \xE9 sagrado, nos d\xE1 consola\xE7\xE3o;
Por Ele foi-nos dado, do c\xE9u, real perd\xE3o;
Aviva a esperan\xE7a, aumenta nosso amor,
D\xE1 firme confian\xE7a e mais so Teu vigor.
`,verseorder:3,ismainverse:0,id_hino:135},{idverse:25400,verse:`3. 
Teu nome \xE9 nossa vida e paz, \xF3 Salvador!
A arca oferecida ao pobre pecador;
Por Ele aben\xE7oada \xE9 toda a na\xE7\xE3o,
Que foi predestinada \xE1 grande voca\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:135},{idverse:25401,verse:`1. 
Em nosso socorro vem, \xF3 vem, Jesus!
Pois grande tristeza nos ofusca a luz;
A Ti recorremos, vem nos dar vigor,
Reveste-nos todos do Teu grande amor!
`,verseorder:1,ismainverse:0,id_hino:136},{idverse:25402,verse:`2. 
Em nosso socorro vem, \xF3 vem Jesus!
Em Ti confiamos, \xE9s a nossa luz;
Protege-nos sempre do vil tentador,
Reveste-nos todos do Teu grande amor!
`,verseorder:2,ismainverse:0,id_hino:136},{idverse:25403,verse:`3. 
Em nosso socorro vem, \xF3 vem, Jesus!
Combate o inimingo com a Tua luz;
A Ti n\xF3s clamamos, responde, Senhor,
Reveste-nos todos do Teu grande amor!
`,verseorder:3,ismainverse:0,id_hino:136},{idverse:25404,verse:`4. 
Em nosso socorro vem, \xF3 vem Jesus!
\xC0 plena vit\xF3ria, hoje nos conduz;
D\xE1-nos Tua gra\xE7a, poder e valor.
Reveste-nos todos do Teu grande amor!`,verseorder:4,ismainverse:0,id_hino:136},{idverse:25405,verse:`1. 
Em Babil\xF4nia, em pris\xE3o,
Estava Israel,
O povo santo de Si\xE3o,
Sofrendo dor cruel.
`,verseorder:1,ismainverse:0,id_hino:137},{idverse:25406,verse:`Mas, um dia, se ouviu,
Que do rei a voz saiu
P\`ra voltarem da escravid\xE3o.
Todo povo a jubilar,
Com as harpas a cantar,
Foi em busca de Si\xE3o.
`,verseorder:2,ismainverse:1,id_hino:137},{idverse:25407,verse:`2. 
"Cantai" disseram os caldeus,
Os hinos de Si\xE3o,
Mas isto mais entristeceu,
Os filhos de Abra\xE3o.
`,verseorder:3,ismainverse:0,id_hino:137},{idverse:25408,verse:`3. 
Libertos foram l\xE1 por Deus
Da vil escravid\xE3o.
E toda alma se encheu,
De riso e gratid\xE3o.
`,verseorder:4,ismainverse:0,id_hino:137},{idverse:25409,verse:`4. 
Qu\xE3o grandes coisas nos fez Deus!
Cantavam, j\xE1, ent\xE3o;
E qual um sonho pareceu
A volta p\`ra Si\xE3o.
`,verseorder:5,ismainverse:0,id_hino:137},{idverse:25410,verse:`5. 
E neste mundo, qual Babel,
H\xE1 muitos em pris\xE3o,
Sofrendo uma dor cruel,
Sem ter consola\xE7\xE3o.
`,verseorder:6,ismainverse:0,id_hino:137},{idverse:25411,verse:`6. 
Mas, de Cristo se ouviu
Que Deus, a voz saiu,
P\`ra voltarem da escravid\xE3o.
Queiras isto aceitar,
Com a harpa vem cantar,
E, assim, volta p\`ra Si\xE3o.`,verseorder:7,ismainverse:0,id_hino:137},{idverse:25412,verse:`1. 
Se da morte tens horror,
E salva\xE7\xE3o desejas,
Volta aos bra\xE7os do Senhor,
Para que salvo tu sejas;
Abre bem teu cora\xE7\xE3o,
E recebe salva\xE7\xE3o,
Sem nenhuma turba\xE7\xE3o;
Quem bate \xE9 Jesus Cristo.
`,verseorder:1,ismainverse:0,id_hino:138},{idverse:25413,verse:`Deves-lhe dar abrigo.
N\xE3o h\xE1 nenhum perigo;
Cristo quer s\xF3 te salvar;
Na mesa, cear contigo.
`,verseorder:2,ismainverse:1,id_hino:138},{idverse:25414,verse:`2. 
J\xE1 sofreu por ti, na cruz,
Para te dar a vida;
O divino e bom Jesus
As marcas tem das feridas;
N\xE3o O v\xEAs a solu\xE7ar,
E Seu sangue a gotejar?
N\xE3o O vais deixar entrar?
Quem bate \xE9 Jesus Cristo.
`,verseorder:3,ismainverse:0,id_hino:138},{idverse:25415,verse:`3. 
Se tu andas em temor;
Longe do Pai amado,
Quem ser\xE1 teu protetor,
Quando o mal for chegado?
Segue a quem morreu na cruz,
Pois \xE0 vida te conduz;
Dar-te-\xE1 nas trevas luz,
Quem bate \xE9 Jesus Cristo.`,verseorder:4,ismainverse:0,id_hino:138},{idverse:25416,verse:`1. 
J\xE1 o Filho de Deus \xE9 descido do c\xE9u;
A obra perfeita na cruz consumou;
E ali Sua carne, rasgada qual v\xE9u,
Vivo caminho para o c\xE9u nos consagrou.
`,verseorder:1,ismainverse:0,id_hino:139},{idverse:25417,verse:`Jesus \xE9 meu eterno
Redentor!
Por Seu sangue j\xE1 remido estou;
Deu-me paz, poder consolador;
Vivo contente, pois Ele me amou.
`,verseorder:2,ismainverse:1,id_hino:139},{idverse:25418,verse:`2. 
Por Ad\xE3o, o pecado no mundo entrou;
Ningu\xE9m dessa lei se podia libertar;
Mas o filho do homem por n\xF3s triunfou,
NEle podemos do mal ressuscitar.
`,verseorder:3,ismainverse:0,id_hino:139},{idverse:25419,verse:`3. 
Do inferno, que paga aos maus, h\xE1 de dar,
Do medo da morte, esse dardo cruel,
Do abismo eterno te pode salvar,
S\xF3 Jesus Crsito, o bom Emanuel.
`,verseorder:4,ismainverse:0,id_hino:139},{idverse:25420,verse:`4. 
Bem alegres buscamos a p\xE1tria de amor,
A qual Deus no c\xE9u para n\xF3s preparou,
Onde sempre veremos o nosso Senhor,
Cristo Jesus, que do mal nos libertou.`,verseorder:5,ismainverse:0,id_hino:139},{idverse:25421,verse:`1. 
De mal nenhum eu temerei,
Em mim est\xE1 Jesus:
Jamais pertuba\xE7\xE3o terei,
Pois vivo j\xE1 na luz.
`,verseorder:1,ismainverse:0,id_hino:140},{idverse:25422,verse:`Sinto paz mui profunda enfim,
into paz proveniente da cruz,
Pela f\xE9 sinto viver em mim,
Meu Jesus, meu Jesus.
`,verseorder:2,ismainverse:1,id_hino:140},{idverse:25423,verse:`2. 
Jesus me trouxe salva\xE7\xE3o;
Tamb\xE9m, me disse assim:
Me busca na tribula\xE7\xE3o,
P\`ra teres paz em Mim.
`,verseorder:3,ismainverse:0,id_hino:140},{idverse:25424,verse:`3. 
Na grande festa do perd\xE3o,
No Seu real poder,
Me concedeu admiss\xE3o,
Quem quis por mim morrer.
`,verseorder:4,ismainverse:0,id_hino:140},{idverse:25425,verse:`4. 
J\xE1 tenho uma viva f\xE9,
Que n\xE3o confundir\xE1;
O amor de Deus, que puro \xE9,
De paz me encher\xE1.`,verseorder:5,ismainverse:0,id_hino:140},{idverse:25426,verse:`1. 
Aonde guiar-me meu Senhor,
Eu seguirei, por Seu amor;
\xC9 Sua m\xE3o que me conduz,
Por mim ferida sobre a cruz.
`,verseorder:1,ismainverse:0,id_hino:141},{idverse:25427,verse:`Guia-me sempre, meu Senhor,
Guia meus passos, Salvador;
Tu me compraste sobre a cruz;
Rege-me em tudo, meu Jesus.
`,verseorder:2,ismainverse:1,id_hino:141},{idverse:25428,verse:`2. 
Acho prazer em Te seguir;
Descanso e paz me faz sentir;
Doce \xE9 a mim o Teu querer,
Gozo me traz Te obedecer.
`,verseorder:3,ismainverse:0,id_hino:141},{idverse:25429,verse:`3. 
Sigo sem medo o meu Senhor
Que me encheu do Seu amor;
Sentindo perto a Sua m\xE3o,
Posso cantar na escurid\xE3o.
`,verseorder:4,ismainverse:0,id_hino:141},{idverse:25430,verse:`4. 
Para Seu reino me conduz,
Pelo jardim e pela cruz;
L\xE1 ficou morto o velho "eu",
L\xE1 meu esp\xEDrito reviveu.`,verseorder:5,ismainverse:0,id_hino:141},{idverse:25431,verse:`1. 
\xD3 Si\xE3o celeste, repouso dos santos,
O teu arquiteto se chama o SENHOR;
Em ti entraremos, com gozo e canto,
Com os que adoram o bom Salvador,
Em bela plan\xEDcie est\xE1s situada,
E que majestosa rainha \xE9s tu!
De pedras preciosas est\xE1s adornada;
Demonstras a gl\xF3ria de Cristo Jesus.
De Cristo Jesus, de Cristo Jesus.
Demonstras a gl\xF3ria de Cristo Jesus.
`,verseorder:1,ismainverse:0,id_hino:142},{idverse:25432,verse:`2. 
Teus belos caminhos tratados com zelo,
E as tuas torres, que vistas far\xE3o!
De todos os pal\xE1cios, grandioso \xE9 o modelo;
Em ti n\xF3s teremos a consola\xE7\xE3o;
As portas do muro s\xE3o todas formosas;
A pra\xE7a \xE9 cal\xE7ada de ouro que luz;
Em ti essas coisas s\xE3o mui gloriosas,
E \xE9s toda cheia da paz de Jesus!
Da paz de Jesus, da paz de Jesus.
E \xE9s toda cheia da paz de Jesus.
`,verseorder:2,ismainverse:0,id_hino:142},{idverse:25433,verse:`3. 
Jamais haver\xE1 em ti noite alguma,
Teu grande luzeiro perp\xE9tuo ser\xE1;
Sem a luz do sol, nem d\`estrelas ou lua,
A gl\xF3ria de Cristo te aluminar\xE1.
E neste esplendor, de um sol verdadeiro,
Os santos e anjos do c\xE9u entrar\xE3o,
E vir\xE1 na frente Jesus, o Cordeiro;
Com Ele p\`ra sempre ali reinar\xE3o.
Ali reinar\xE3o, ali reinar\xE3o,
Com Ele p\`ra sempre ali reinar\xE3o.
`,verseorder:3,ismainverse:0,id_hino:142},{idverse:25434,verse:`4. 
Oh! que grande festa nos \xE9 concedida,
Com a mesa posta, espera o Senhor,
A todos inscritos no livro da vida,
E que j\xE1 da morte n\xE3o t\xEAm mais temor;
De todos os que foram por Cristo comprados,
O lindo cortejo composto ser\xE1;
E Deus, que h\xE1 dado o Seu filho amado,
Com Cristo na gl\xF3ria, os consolar\xE1,
Os consolar\xE1, os consolar\xE1,
Com Cristo na gl\xF3ria, os consolar\xE1.`,verseorder:4,ismainverse:0,id_hino:142},{idverse:25435,verse:`1. 
Tu \xE9s p\`ra mim o verdadeiro amigo,
S\xF3 em Ti, meu Redentor, vou confiar,
Defende-me do mundo, do perigo;
Ansioso, eu Te quero encontrar.
`,verseorder:1,ismainverse:0,id_hino:143},{idverse:25436,verse:`Vem levar me para perto
Do Teu grande e bondoso cora\xE7\xE3o;
Sempre avante e bem certo,
Com Jesus, eu vou andando p\`ra Si\xE3o.
`,verseorder:2,ismainverse:1,id_hino:143},{idverse:25437,verse:`2. 
Vem j\xE1, Senhor, com Tua gra\xE7a imensa
Ajudar-me a levar a minha cruz,
Fazendo-me sentir real presen\xE7a,
Do Consolador divino, \xF3 Jesus!
`,verseorder:3,ismainverse:0,id_hino:143},{idverse:25438,verse:`3. 
Jesus, Tu \xE9s a minha esperan\xE7a,
E contente eu desejo Te seguir;
A Ti orando, com perseveran\xE7a,
Para que me guardes sempre de cair.`,verseorder:4,ismainverse:0,id_hino:143},{idverse:25439,verse:`1. 
\xC0 Assembl\xE9ia de Deus, vem comigo,
Ouvir a Palavra de Deus;
E ter\xE1s a certeza, contigo,
Que Jesus \xE9 o caminho dos c\xE9us.
`,verseorder:1,ismainverse:0,id_hino:144},{idverse:25440,verse:`\xD3 vem, vem, vem, vem!
Vem \xE0 Assembl\xE9ia e louvemos
Ao nosso bom Deus Redentor,
Pois maior alegria n\xE3o temos,
Que fruir Seu imenso amor.
`,verseorder:2,ismainverse:1,id_hino:144},{idverse:25441,verse:`2. 
Vem, irm\xE3o, \xE0 Assembl\xE9ia dos santos,
Sentir o poder do Senhor
E ali entoar lindos cantos
Exultando no Consolador.
`,verseorder:3,ismainverse:0,id_hino:144},{idverse:25442,verse:`3. 
Na Assembl\xE9ia de Deus tu estejas
Humilde aos p\xE9s do Senhor;
Santidade conv\xE9m \xE0 Igreja,
P\`ra gozarmos celeste amor.
`,verseorder:4,ismainverse:0,id_hino:144},{idverse:25443,verse:`4. 
N\xF3s sentimos a santa presen\xE7a
Do nosso querido Jesus;
Anulada foi tua senten\xE7a;
Deixa as trevas e vem para a luz.`,verseorder:5,ismainverse:0,id_hino:144},{idverse:25444,verse:`1. 
A minha alma te ama, \xF3 Senhor!
E desfalece sedenta de amor;
\xC9s meu consolo na afli\xE7\xE3o,
E luz perfeita na escurid\xE3o;
Meu verdadeiro p\xE3o.
`,verseorder:1,ismainverse:0,id_hino:145},{idverse:25445,verse:`A minha alma ama-te, Senhor,
De ti s\xF3 recebe vida e amor!
`,verseorder:2,ismainverse:1,id_hino:145},{idverse:25446,verse:`2. 
A minha alma Te ama, \xF3 Senhor!
Almeja sempre andar no Teu temor;
Tu vives j\xE1 no meu cora\xE7\xE3o
E guias-me pela Tua m\xE3o;
\xC9s minha salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:145},{idverse:25447,verse:`3. 
A minha alma Te ama, \xF3 Senhor!
A tua face procura com fervor;
Desejo ter a celeste paz,
O gozo santo, que satisfaz,
E salva\xE7\xE3o veraz.
`,verseorder:4,ismainverse:0,id_hino:145},{idverse:25448,verse:`4. 
A minha alma Te ama, \xF3 Senhor!
Por mim sofreste incompar\xE1vel dor;
Teu nome quero, aqui louvar;
Os teus preceitos e leis honrar,
E sempre em Ti pensar.`,verseorder:5,ismainverse:0,id_hino:145},{idverse:25449,verse:`1. 
Por um caminho brilhante vou,
De majstade luz;
Pelo caminho que j\xE1 trilhou,
Nosso Senhor Jesus;
Sem me lembrar do que se passou,
Bem feliz canto, sim,
Pois o filho do Rei dos reis eu sou;
Cristo \xE9 meu, por fim;
`,verseorder:1,ismainverse:0,id_hino:146},{idverse:25450,verse:`Meu, por fim; meu, por fim!
Cristo da morte livrou-me, sim!
Me ouviu, me remiu,
Cristo \xE9 meu, por fim!
`,verseorder:2,ismainverse:1,id_hino:146},{idverse:25451,verse:`2. 
No mundo n\xE3o tenho mais prazer,
Purificado estou,
Nunca jamais hei de perecer,
Pois Jesus me livrou;
Ele me ama e me quer bem,
Tudo \xE9 para mim;
NEle encontro louvor, tamb\xE9m.
Cristo \xE9 meu, por fim.
`,verseorder:3,ismainverse:0,id_hino:146},{idverse:25452,verse:`3. 
Quem de Jesus me separar\xE1?
Tenho-O no meu cora\xE7\xE3o;
Ele nas trevas me guiar\xE1
Para a feliz Si\xE3o.
Sempre contente, sem vacilar,
Ven\xE7o o mal, assim;
Tenho prazer se na luz andar.
Cristo \xE9 meu, por fim.`,verseorder:4,ismainverse:0,id_hino:146},{idverse:25453,verse:`1. 
Quero eu servir-te, \xF3 meu Rei Jesus,
E contigo sempre caminhar na luz,
Tendo com o povo de Deus comunh\xE3o
E dos meus pecados purifica\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:147},{idverse:25454,verse:`Sim, \xF3 meu Senhor,
Quero seguir-te, \xF3 Deus de amor.
Sempre Te servindo,
E tamb\xE9m dando a Ti louvor.
`,verseorder:2,ismainverse:1,id_hino:147},{idverse:25455,verse:`2. 
No Teu bom servi\xE7o tenho eu prazer,
Nele muita gra\xE7a eu vou receber;
Sempre falarei assim do Teu amor,
E Te glorificarei, meu Salvador.
`,verseorder:3,ismainverse:0,id_hino:147},{idverse:25456,verse:`3. 
Des\xE7a Tua gra\xE7a sobre mim, Senhor,
Para trabalhar com mais e mais fervor,
D\xE1-me entendimento e veraz saber,
Para alegre, eu fazer o Teu querer.
`,verseorder:4,ismainverse:0,id_hino:147},{idverse:25457,verse:`4. 
S\xF3 em Ti confio, meu Senhor e Rei;
S\xF3 em Ti vit\xF3ria eu alcan\xE7arei.
E contigo quero sempre aqui viver,
\`T\xE9 que p\`ra Si\xE3o, me venhas receber.`,verseorder:5,ismainverse:0,id_hino:147},{idverse:25458,verse:`1. 
Louvarei meu Guia s\xE1bio,
Louvarei, louvarei;
Bendirei, com alma e l\xE1bios,
Bendirei Jesus.
`,verseorder:1,ismainverse:0,id_hino:148},{idverse:25459,verse:`Aleluia! Louvo a Jesus!
Deu-me salva\xE7\xE3o na cruz!
Aleluia a quem me conduz!
Gl\xF3ria a meu Jesus!
`,verseorder:2,ismainverse:1,id_hino:148},{idverse:25460,verse:`2. 
Louvarei a quem me ampara,
Louvarei, louvarei;
Nada dEle me separa;
Bendirei Jesus.
`,verseorder:3,ismainverse:0,id_hino:148},{idverse:25461,verse:`3. 
A quem leva os meus cuidados,
Louvarei, louvarei;
Me sustenta os p\xE9s cansados,
Bendirei Jesus.
`,verseorder:4,ismainverse:0,id_hino:148},{idverse:25462,verse:`4. 
A quem d\xE1-me aqui vit\xF3ria,
Louvarei, louvarei;
Dar-me-\xE0 coroa e gl\xF3ria;
Bendirei Jesus.
`,verseorder:5,ismainverse:0,id_hino:148},{idverse:25463,verse:`5. 
Meu sublime Rei glorioso,
Louvarei, louvarei;
Em celeste coro e gozo,
Bendirei Jesus.`,verseorder:6,ismainverse:0,id_hino:148},{idverse:25464,verse:`1. 
No meu barco a remar,
Sobre as ondas, pelo mar,
Mesmo na bonan\xE7a ou no furac\xE3o
N\xE3o desejo mais parar;
Com a rede vou pescar,
Muitos peixes para o reino de Si\xE3o,
`,verseorder:1,ismainverse:0,id_hino:149},{idverse:25465,verse:`Vou pescar os pecadores para
Cristo,
Neste mundo cheio de horror;
N\xE3o mais desanimarei;
Minha rede lan\xE7arei;
Muitos peixes apanhando p\`ra o Senhor.
`,verseorder:2,ismainverse:1,id_hino:149},{idverse:25466,verse:`2. 
O meu barco n\xE3o \xE9 bom,
De pescar n\xE3o tenho dom.
E me dizem que n\xE3o devo continuar;
Mas Jesus me quis mandar,
E por isso vou pescar,
T\xE9 que Ele se apraze em me chamar.
`,verseorder:3,ismainverse:0,id_hino:149},{idverse:25467,verse:`3. 
Tem um modo o Senhor.
Que \xE9 pr\xF3prio do amor,
Ele usa dos remidos o menor,
Todo o mundo me deixou,
E de mim se envergonhou,
Mas alegre, vou pescar, pois \xE9 melhor.
`,verseorder:4,ismainverse:0,id_hino:149},{idverse:25468,verse:`4. 
Se h\xE1 coisa de valor,
\xC9 a rede de amor,
Cujo fio \xE9 a obra de Jesus,
Que puxada sempre traz
Os perdidos e sem paz,
Para receberem do Senhor, a luz.
`,verseorder:5,ismainverse:0,id_hino:149},{idverse:25469,verse:`5. 
Quando h\xE1 um temporal,
E a pesca corre mal,
Novamente no meu barco vou pescar!
Pode ser que desta vez,
Eu n\xE3o tenha mais rev\xE9s,
Pois Jesus eu levo para m\`ensinar.
`,verseorder:6,ismainverse:0,id_hino:149},{idverse:25470,verse:`6. 
Acabando de pescar,
E deitado a pensar,
Tenho gozo pelo tempo que gastei;
Pois terei um galard\xE3o
Pela pesca, em Si\xE3o,
Pelas almas que no mundo eu ganhei.`,verseorder:7,ismainverse:0,id_hino:149},{idverse:25471,verse:`1. 
Cheios de contentamento
Estamos, Senhor amado,
Porque \xE9 o casamento
Um Teu bom mandado.
`,verseorder:1,ismainverse:0,id_hino:150},{idverse:25472,verse:`Aos noivos d\xE1 prote\xE7\xE3o,
Concedendo o Teu amor,
Para eles em uni\xE3o,
Viverem p\`ra Teu louvor.
`,verseorder:2,ismainverse:1,id_hino:150},{idverse:25473,verse:`2. 
Faze viverem guardados
Na Tua santa doutrina
E s\xF3 andarem guiados
Por Tua m\xE3o divina.
`,verseorder:3,ismainverse:0,id_hino:150},{idverse:25474,verse:`3. 
Confirma esta alian\xE7a,
N\xF3s te pedimos ainda,
Enchendo-os da esperan\xE7a
Da Tua santa vinda.`,verseorder:4,ismainverse:0,id_hino:150},{idverse:25475,verse:`1. 
Fala, Jesus querido; fala-me, hoje sim!
Fala com Tua bondade; fica ao p\xE9 de mim;

Meu cora\xE7\xE3o aberto \`st\xE1 p\`ra Tua voz ouvir;
Enche-me de louvores e gozo p\`ra Te servir.
`,verseorder:1,ismainverse:0,id_hino:151},{idverse:25476,verse:`Fala-me suavemente!
Fala, com muito amor!

Vencedor para sempre, livre te hei de por,
Fala-me cada dia, sempre em terno tom;
Ouvir Tua voz eu quero e neste mesmo som.
`,verseorder:2,ismainverse:1,id_hino:151},{idverse:25477,verse:`2. 
Para teus filhos fala, e, no caminho bom,
Pela bondade os guia a pedir o santo dom;

Querer\xE3o consagrar-se para suas vidas dar.
Obedecendo a Cristo e com fervor O amar.
`,verseorder:3,ismainverse:0,id_hino:151},{idverse:25478,verse:`3. 
Como no tempo antigo, Tu revelaste a lei,
Mostra-me Tua vontade, e \xE0 Tua santa grei;

Deixa-me gloriar-te, quero a Ti louvar,
Cantar alegremente e sempre Te honrar.`,verseorder:4,ismainverse:0,id_hino:151},{idverse:25479,verse:`1. 
Ao Calv\xE1rio, s\xF3 Jesus se transportou
Levando pesada cruz,
E morrendo, l\xE1 p\`ra o mortal deixou,
Um fanal de gloriosa luz.
`,verseorder:1,ismainverse:0,id_hino:152},{idverse:25480,verse:`Pela cruz ao c\xE9u irei;
Pela cruz eu seguirei
Para o lar de paz e eterno amor
Pela cruz ao c\xE9u irei.
`,verseorder:2,ismainverse:1,id_hino:152},{idverse:25481,verse:`2. 
S\xF3 na cruz, a alma sedenta achar\xE1
A fonte de inspira\xE7\xE3o;
E ali o fardo de dor cair\xE1;
No Calv\xE1rio, h\xE1 salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:152},{idverse:25482,verse:`3. 
Pela cruz irei \xE0 mans\xE3o celestial.
O rumo marcado est\xE1,
Em minha vida obscura ser\xE1 o fanal;
Em sua luz minha alma ir\xE1.`,verseorder:4,ismainverse:0,id_hino:152},{idverse:25483,verse:`1. 
Soldados de Cristo que entrastes na lida.
Lutai sem desmaio, lutai com valor;
E o inimigo levai de vencida,
Dizendo que Cristo \xE9 o nosso Senhor.
`,verseorder:1,ismainverse:0,id_hino:153},{idverse:25484,verse:`Soldados de Cristo, marchai
sempre avante,
Levando \xE0 destra as armas da luz;
As almas perdidas buscai triunfantes
E prestes, levai-as a Cristo Jesus.
`,verseorder:2,ismainverse:1,id_hino:153},{idverse:25485,verse:`2. 
Peleja, \xF3 crente, a santa peleja,
Prossegue avante por Cristo Jesus;
E s\xEA mui valente; na frente estejas,
Dizendo que Cristo morreu numa cruz.
`,verseorder:3,ismainverse:0,id_hino:153},{idverse:25486,verse:`3. 
A aurora se acerca do dia fautoso,
Em que pr\xEAmio ter\xE3o  a f\xE9 e o valor;
E nele, Jesus, galard\xE3o grandioso,
Dar\xE1 a qualquer que sair vencedor.`,verseorder:4,ismainverse:0,id_hino:153},{idverse:25487,verse:`1. 
Oh! doce nome de Jesus!
Que belo \xE9 a Ti cantar,
Co\`a alma cheia proclamar
O nome bom de Jesus!
`,verseorder:1,ismainverse:0,id_hino:154},{idverse:25488,verse:`Jesus, \xF3 meu doce Rei!
Jesus, verdadeiro Deus!
Jesus, sempre louvarei
De cora\xE7\xE3o, Teu nome.
`,verseorder:2,ismainverse:1,id_hino:154},{idverse:25489,verse:`2. 
Adoro o nome de Jesus;
Jamais me falha Seu amor,
E p\xF5e, \xE0 parte, minha dor,
O nome bom de Jesus.
`,verseorder:3,ismainverse:0,id_hino:154},{idverse:25490,verse:`3. 
T\xE3o puro o nome de Jesus!
Que meu pesar p\xF4de tirar.
A grata paz tamb\xE9m me d\xE1
O nome bom de Jesus.
`,verseorder:4,ismainverse:0,id_hino:154},{idverse:25491,verse:`4. 
No doce nome de Jesus,
A minha alma salva est\xE1,
E nEle tu te salvar\xE1s;
No nome bom de Jesus.`,verseorder:5,ismainverse:0,id_hino:154},{idverse:25492,verse:`1. 
Imploramos, nosso Salvador,
Teu poder, Teu poder, Teu poder,
Divinal, poder renovador,
Teu, poder, Teu poder, Teu poder,
Bendita promessa paternal!
Vem encher-nos de real valor,
Do pleno poder celestial,
Teu, poder, Teu poder, Teu poder,.
`,verseorder:1,ismainverse:0,id_hino:155},{idverse:25493,verse:`2. 
Com o \xF3leo, sim, vem nos ungir,
Teu, poder, Teu poder, Teu poder,
Pai celeste, faze-nos fruir
Teu, poder, Teu poder, Teu poder,
Tu j\xE1 prometeste derramar
Tuas b\xEAn\xE7\xE3os e nos revestir,
P\`ra Tua Palavra proclamar,
Com poder, com poder, com poder.
`,verseorder:2,ismainverse:0,id_hino:155},{idverse:25494,verse:`3. 
Com o fogo vem nos inflamar,
Teu, poder, Teu poder, Teu poder,
E de toda mancha nos limpar,
Teu, poder, Teu poder, Teu poder,
Aquece os frios, \xF3 Senhor;
Faze os que dormem despertar.
N\xF3s te suplicamos com fervor,
Teu, poder, Teu poder, Teu poder,
`,verseorder:3,ismainverse:0,id_hino:155},{idverse:25495,verse:`4. 
Como a brisa, queiras assoprar
Teu, poder, Teu poder, Teu poder,
Deus bendito, vem nos outorgar
Teu, poder, Teu poder, Teu poder,
Perene e doce comunh\xE3o,
Quero aqui contigo, \xF3 Pai, gozar;
Depressa nos enche o cora\xE7\xE3o
Com poder, com poder, com poder.`,verseorder:4,ismainverse:0,id_hino:155},{idverse:25496,verse:`1. 
O bom Pastor buscou-me
J\xE1 longe do redil,
E com ternura achou-me,
Ca\xEDdo, triste, vil!
As chagas com amor pensou,
E ao lar nos bra\xE7os me levou.
`,verseorder:1,ismainverse:0,id_hino:156},{idverse:25497,verse:`Sim, Jesus amou-me,
Com amor buscou-me,
Ele mesmo restaurou-me a Deus;
Por Seu sangue restaurou-me a Deus.
`,verseorder:2,ismainverse:1,id_hino:156},{idverse:25498,verse:`2. 
Seguiu-me, bem distante
Do seu lugar, no c\xE9u,
E disse, em voz amante:
"Achei-te, tu \xE9s Meu".
Jamais senti t\xE3o grande amor
Como este do meu bom Pastor.
`,verseorder:3,ismainverse:0,id_hino:156},{idverse:25499,verse:`3. 
Por Ele sou querido,
Que gra\xE7a singular!
Pois Ele foi ferido,
A fim de me salvar;
Assim, ovelha dEle sou,
E com o seu rebanho vou.
`,verseorder:4,ismainverse:0,id_hino:156},{idverse:25500,verse:`4. 
Prossigo alegre agora;
Deus d\xE1 poder cabal
E graca salvadora;
Protege-me do mal,
O bom Pastor comigo est\xE1,
Meus passos Ele guiar\xE1.`,verseorder:5,ismainverse:0,id_hino:156},{idverse:25501,verse:`1. 
O dia vem, a clarear,
J\xE1 fugiu a noite, brilha a luz dal\xE9m;
Um grito soa: aprontar!
Cristo, em breve, vem!
`,verseorder:1,ismainverse:0,id_hino:157},{idverse:25502,verse:`Qual forte vendaval, rugindo sobrre o mar,
Escuta-se a mensagem, que do c\xE9u prov\xE9m;
Ouvi a grande nova, que alegria traz;
"Cristo, em breve, vem!"
`,verseorder:2,ismainverse:1,id_hino:157},{idverse:25503,verse:`2. 
\xD3 povos, tribos e na\xE7\xF5es
Que escravizados no pecado estais,
\xD3 preparai os cora\xE7\xF5es!
Oh! Por que demorais?
`,verseorder:3,ismainverse:0,id_hino:157},{idverse:25504,verse:`3. 
Da  morte queiram acordar;
Trevas e pecados, \xE0 luz, h\xE3o de fugir;
Em breve iremos encontrar
Cristo, que h\xE1 de vir.`,verseorder:4,ismainverse:0,id_hino:157},{idverse:25505,verse:`1. 
Diante de Pilatos \`sta Jesus,
Por todos deixado em afli\xE7\xE3o,
O qual pergunta \xE0 multid\xE3o.
- "Que farei de Jesus Cristo?"
`,verseorder:1,ismainverse:0,id_hino:158},{idverse:25506,verse:`Que far\xE1s de Jesus Cristo?
Responde, hoje, sim;
Dir\xE1s um dia ao v\xEA-lo:
"Que far\xE1 Ele de mim?
`,verseorder:2,ismainverse:1,id_hino:158},{idverse:25507,verse:`2. 
Em julgamento ainda est\xE1
O Nazareno em afli\xE7\xE3o,
E se repete a pergunta, ent\xE3o:
- "Que farei de Jesus Cristo?"
`,verseorder:3,ismainverse:0,id_hino:158},{idverse:25508,verse:`3. 
Ser\xE1 julgado Jesus por ti,
Ou recebido com gratid\xE3o?
E, mui alegre, dir\xE1s, ent\xE3o:
-"Aceito a Jesus Cristo!"`,verseorder:4,ismainverse:0,id_hino:158},{idverse:25509,verse:`1. 
Cantai, \xF3 peregrinos, na lida n\xE3o canseis
A vossa f\xE9 crescendo, as harpas j\xE1 tocai
Eu sobre o monte santo e glorioso estou,
E para a minha p\xE1tria celestial j\xE1 vou.
`,verseorder:1,ismainverse:0,id_hino:159},{idverse:25510,verse:`Cantai, continuamente,
Louvai p\`ra todo o sempre;
Meu cora\xE7\xE3o transborda com divinal louvor.
Cantai, continuamente,
Louvai p\`ra todo o sempre;
Meu cora\xE7\xE3o transborda com divinal louvor.
`,verseorder:2,ismainverse:1,id_hino:159},{idverse:25511,verse:`2. 
Cantai, \xF3 peregrinos, enquanto aqui estais;
Cantai, a Jesus Cristo, enquanto assim passais;
Cantai a velha hist\xF3ria de Deus e Seu amor;
O coro sempiterno responde, com fervor.
`,verseorder:3,ismainverse:0,id_hino:159},{idverse:25512,verse:`3. 
Cantai, \xF3 peregrinos, em breve estareis,
Cantando melhor hino, junto ao Rei dos reis,
Com os que foram antes, e nos esperam l\xE1,
Encontraremos todos, e nada turbar\xE1.
`,verseorder:4,ismainverse:0,id_hino:159},{idverse:25513,verse:"4. ",verseorder:5,ismainverse:0,id_hino:159},{idverse:25514,verse:`1. 
Irm\xE3os, unidos, vamos publicar
Que Deus nos quis salvar,
Que Deus nos quis salvar,
Com o precioso sangue de Jesus,
Vertido l\xE1 na cruz.
`,verseorder:1,ismainverse:0,id_hino:160},{idverse:25515,verse:`Que Deus nos quis salvar,
Que Deus nos quis salvar,
Co\`o sangue do Senhor Jesus;
\xD3 irm\xE3os, devemos avan\xE7ar,
Olhando para a cruz
`,verseorder:2,ismainverse:1,id_hino:160},{idverse:25516,verse:`2. 
Irm\xE3os, ao mundo vamos proclamar
Que Deus nos quis salvar,
Que Desu nos quis salvar.
E que, mui breve, h\xE1 de vir Jesus,
Em gloriosa luz.
`,verseorder:3,ismainverse:0,id_hino:160},{idverse:25517,verse:`3. 
Na s\xE3 Palavra fomos encontrar
Que Deus nos quis salvar,
Que Deus nos quis salvar,
E que no c\xE9u veremos o Senhor,
Repletos de amor!
`,verseorder:4,ismainverse:0,id_hino:160},{idverse:25518,verse:`4. 
Alegres, sempre temos que cantar:
Que Deus nos quis salvar,
Que Deus nos quis salvar.
Por Sua gra\xE7a, pelo Seu amor,
Louvemos ao Senhor!
`,verseorder:5,ismainverse:0,id_hino:160},{idverse:25519,verse:"5. ",verseorder:6,ismainverse:0,id_hino:160},{idverse:25520,verse:`1. 
P\`ra terra celeste meu barco andar\xE1,
Ali, onde os santos j\xE1 est\xE3o;
Findando a noite, manh\xE3 romper\xE1;
Ent\xE3o, os remidos entrar\xE3o.
`,verseorder:1,ismainverse:0,id_hino:161},{idverse:25521,verse:`Sim, vou pelas terras, pelos mares
tamb\xE9m,
Alerta, Jesus me guiar\xE1; Ele prometeu a mim,
Nunca deixar-me no mundo, por\xE9m,
Sim, levar-me ao porto, co\`alegria sem fim.
`,verseorder:2,ismainverse:1,id_hino:161},{idverse:25522,verse:`2. 
Ser\xE1 jubiloso esse dia p\`ra mim,
Em que eu chegar ao c\xE9u de luz;
Que paz e descanso, com Deus, l\xE1 sem fim!
Sa\xFAdo-te j\xE1, \xF3 meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:161},{idverse:25523,verse:`3. 
\xD3 mundo que jazes no vil tentador
N\xE3o quero jamais em ti morar,
P\`ra festa celeste eu vou com fervor,
Louvores a Cristo irei cantar!
`,verseorder:4,ismainverse:0,id_hino:161},{idverse:25524,verse:`4. 
Amigos, lembai-vos que o naufragar
\xC9 f\xE1cil p\`ra qualquer um de v\xF3s;
Convite, p\`ras bodas, Jesus vos quer dar;
Lugar h\xE1 bastante para n\xF3s.
`,verseorder:5,ismainverse:0,id_hino:161},{idverse:25525,verse:"5. ",verseorder:6,ismainverse:0,id_hino:161},{idverse:25526,verse:`1. 
Da verdade, levantemos o estandarte,
Arvoremos o esndarte de Jesus,
Proclamemos, com valor, por toda parte,
A mensagem solen\xEDssima da cruz.
`,verseorder:1,ismainverse:0,id_hino:162},{idverse:25527,verse:`O mundo est\xE1 sem luz, sem paz;
Levemos paz, consola\xE7\xE3o,
A quem, na dor, no luto jaz,
Sem luz, sem paz, sem salva\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:162},{idverse:25528,verse:`2. 
Da verdade, levantemos o estandarte;
Proclamaremos o Senhor, que \xE9 luz e paz;
Pecador, ouve! Jesus tem para dar-te
Salva\xE7\xE3o que nEle s\xF3, encontrar\xE1s.
`,verseorder:3,ismainverse:0,id_hino:162},{idverse:25529,verse:`3. 
Do Evangelho, levantemos o estandarte,
Vem, desperta do teu sono, pecador;
Que o teu Deus, que o teu Senhor tem para dar-te
Copiosas b\xEAn\xE7\xE3os do Seu grande amor.`,verseorder:4,ismainverse:0,id_hino:162},{idverse:25530,verse:`1. 
Do Calv\xE1rio, pela senda,
O Senhor Jesus subiu,
E na cruz, em dor horrenda,
Obra de amor cumpriu!
`,verseorder:1,ismainverse:0,id_hino:163},{idverse:25531,verse:`Sobre a cruz, sobre a cruz,
Cristo Jesus morreu por mim;
Sobre a cruz, sobre a cruz,
Cristo Jesus morreu por ti.
`,verseorder:2,ismainverse:1,id_hino:163},{idverse:25532,verse:`2. 
Oh! Que obra gloriosa
Foi p\`ra Deus a de Jesus!
Sua morte t\xE3o penosa
Fez raiar, do c\xE9u, a luz!
`,verseorder:3,ismainverse:0,id_hino:163},{idverse:25533,verse:`3. 
Nesta obra hoje crendo,
Tenho vida e prazer;
Pela f\xE9, estou revendo
O Senhor, por mim sofrer.
`,verseorder:4,ismainverse:0,id_hino:163},{idverse:25534,verse:`4. 
Pecador na cruz remira,
De Jesus a morte atroz,
Pois o teu pecado tira,
Se ouvires Sua voz.`,verseorder:5,ismainverse:0,id_hino:163},{idverse:25535,verse:`1. 
Recebi de gra\xE7a, abundante paz
Por Desu concedida, que me satisfaz;
Queres receb\xEA-la no teu cora\xE7\xE3o?
Busca o Senhor e Sua salva\xE7\xE3o,
Mas de Sua paz, mais de Sua paz,
Quero receber de Deus, que \xE9 veraz.
Mais de Sua paz, mais de Sua paz,
Quero receber de Deus, que \xE9 veraz.
`,verseorder:1,ismainverse:0,id_hino:164},{idverse:25536,verse:`2. 
Recebi de gra\xE7a, abundante luz,
Luz maravilhosa, que me deu Jesus;
Queres receb\xEA-la, pobre pecador?
Deixa, hoje mesmo, o mundo de horror.
Mais de Sua luz, mais de Sua luz,
Quero receber do Salvador Jesus.
Mais de Sua luz, mais de Sua luz.
Quero receber do Salvador Jesus.
`,verseorder:2,ismainverse:0,id_hino:164},{idverse:25537,verse:`3. 
Recebi de gra\xE7a abundante amor,
O amor divino do Consolador;
Queres receb\xEA-lo? Abre o cora\xE7\xE3o,
E ter\xE1s mais gozo e veraz perd\xE3o.
Mais de Seu amor, mais de Seu amor,
Quero receber do bom Consolador,
Mais de Seu amor, mais de Seu amor,
Quero receber do bom Consolador.
`,verseorder:3,ismainverse:0,id_hino:164},{idverse:25538,verse:"4. ",verseorder:4,ismainverse:0,id_hino:164},{idverse:25539,verse:`1. 
Ao bom combate vamos, repletos de amor,
Vestidos de justi\xE7a, ferventes no Senhor;
Co\`os lombos bem cingidos de verdadeira luz,
Cal\xE7ados do Evangelho do Salvador Jesus.
`,verseorder:1,ismainverse:0,id_hino:165},{idverse:25540,verse:`De Deus, a armadura -
de Cristo, o valor,
Irm\xE3o meu, segura - p\`ra seres vencedor.
`,verseorder:2,ismainverse:1,id_hino:165},{idverse:25541,verse:`2. 
da f\xE9, o escudo, que pode vos salvar
Dos dardos inflamados, que vem vos atirar
O grande inimigo, o astuto tentador
Que sempre vos procura tragar, com seu furor.
`,verseorder:3,ismainverse:0,id_hino:165},{idverse:25542,verse:`3. 
Entrai no bom combate, em nome do Senhor
Com Zelo, com coragem, unidos em amor;
O inimigo, breve, assim, se vencer\xE1,
E sob os p\xE9s nossos Jesus n\xF4-lo por\xE1.`,verseorder:4,ismainverse:0,id_hino:165},{idverse:25543,verse:`1. 
Ouves tu? Jesus te chama,
Sim, te chama, \xF3 pecador!
A Jesus, que salva e ama,
Vem agora, sem temor.
`,verseorder:1,ismainverse:0,id_hino:166},{idverse:25544,verse:`Deixa entrar o Rei da gl\xF3ria
Em ti mesmo, \xF3 pecador;
Quem \xE9 este Rei da gl\xF3ria?
\xC9 Jesus, o teu Senhor.
`,verseorder:2,ismainverse:1,id_hino:166},{idverse:25545,verse:`2. 
Para o Mundo e o pecado
Tens no cora\xE7\xE3o lugar,
Mas Jesus ressuscitado,
Tu n\xE3o podes abrigar?
`,verseorder:3,ismainverse:0,id_hino:166},{idverse:25546,verse:`3. 
Do prazer, a vista linda,
Deste mundo sedutor,
Sim, um dia aqui se finda,
Com a morte, \xF3 pecador!
`,verseorder:4,ismainverse:0,id_hino:166},{idverse:25547,verse:`4. 
Hoje \xE9 dia aceit\xE1vel
Para vire t\`entregar
A Jesus, que mui am\xE1vel,
Quer, e pode te salvar.`,verseorder:5,ismainverse:0,id_hino:166},{idverse:25548,verse:`1. 
\xD3 companheiros desta causa santa
Anunciai que Cristo \xE9 Salvador,
\xC9 boa nova que o perdido salva
E do escravo faz um vencedor;
Do Amazonas, at\xE9 ao Rio Grande,
O Evangelho, ousados, proclamai,
E a mensagem, que da paz nos fala;
Eterna gl\xF3ria vos dar\xE1 do Pai.
`,verseorder:1,ismainverse:0,id_hino:167},{idverse:25549,verse:`2. 
A salva\xE7\xE3o perfeita e gloriosa,
Que, em Jesus, o Pai quis revelar,
N\xF3s publicamos, sim, por toda a parte,
Aos Que Jesus j\xE1 veio p\`ra salvar.
E muitos j\xE1 a Cristo se entregaram,
E a estes outros v\xE3o se reunir,
Sim, a Palavra, em v\xE3o, n\xE3o se semeia,
Pois quem a deu, tamb\xE9m a faz fruir.
`,verseorder:2,ismainverse:0,id_hino:167},{idverse:25550,verse:`3. 
Poder do alto p\`ra ser testemunha,
Foi a promessa qu\`Ele nos deixou,
Bem poucos crentes, \`st\xE3o \xE0 Sua espera,
Desconhecendo o que Deus nos legou,
Mas, gl\xF3ria a Deus! H\xE1 outros que acordaram
E cheios de poder pentecostal,
V\xE3o despertar os crentes ociosos,
P\`ra acharem este dom celestial.
`,verseorder:3,ismainverse:0,id_hino:167},{idverse:25551,verse:`4. 
No nome de Jesus n\xF3s proclamamos,
A salva\xE7\xE3o p\`ra todo o pecador,
A este nome cura os enfermos,
Que buscam nEle aliviar a dor;
E os dem\xF4nios ser\xE3o expulsos,
Porque Jesus poder nos prometeu,
E j\xE1 um povo est\xE1 se preparando
P\`ra encontrar Jesus, o Rei do c\xE9u.`,verseorder:4,ismainverse:0,id_hino:167},{idverse:25552,verse:`1. 
Meus irm\xE3os, nos jubilemos
Em Jesus, o Rei da gl\xF3ria,
Nesta f\xE9 que recebemos,
Nesta paz, que  d\xE1 vit\xF3ria,
No consolador divino,
Que aviva a mem\xF3ria,
E em n\xF3s entoa hinos a Jesus.
`,verseorder:1,ismainverse:0,id_hino:168},{idverse:25553,verse:`Meus irm\xE3os, nos jubilemos
Em Jesus, por Sua b\xEAn\xE7\xE3o;
Pois, assim, n\xF3s sempre temos
Salva\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:168},{idverse:25554,verse:`2. 
Meus irm\xE3os, n\xF3s congregados,
Entoemos nosso canto
Ao Senhor, o nosso amado,
Ao bom Deus, perfeito e santo;
No seu gozo jubilemos,
E secar-se-\xE1 o pranto;
Em esp\xEDrito adoremos a Jesus.
`,verseorder:3,ismainverse:0,id_hino:168},{idverse:25555,verse:`3. 
Meus irm\xE3os, em Cristo somos
Pelo esp\xEDrito gerados,
E p\`ra sermos santos, fomos
Por Jesus j\xE1 resgatados;
Pela f\xE9, n\xF3s desfrutamos
Liberdade do pecado,
E tamb\xE9m, nos alegramos em Jesus.`,verseorder:4,ismainverse:0,id_hino:168},{idverse:25556,verse:`1. 
Longe do Senhor andava,
No caminho de horror,
Por Jesus n\xE3o perguntava,
Nem queria o Seu amor.
`,verseorder:1,ismainverse:0,id_hino:169},{idverse:25557,verse:`Oh! por que Jesus me ama?
Eu n\xE3o posso t\`explicar!
Mas, a ti tamb\xE9m te chama,
Pois deseja te salvar!
`,verseorder:2,ismainverse:1,id_hino:169},{idverse:25558,verse:`2. 
No ju\xEDzo n\xE3o pensava,
Nem na minha perdi\xE7\xE3o,
Nem minha alma desejava
A eterna salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:169},{idverse:25559,verse:`3. 
J\xE1 cansado do pecado,
Fui aos p\xE9s do Salvador,
E ali, caiu o fardo
De tristeza e de dor.
`,verseorder:4,ismainverse:0,id_hino:169},{idverse:25560,verse:`4. 
Como \xE9 maravilhoso,
Pertencer ao meu Jesus!
Ter a gra\xE7a, o repouso,
E ficar ao p\xE9 da cruz!`,verseorder:5,ismainverse:0,id_hino:169},{idverse:25561,verse:`1. 
Como havia trevas no meu cora\xE7\xE3o,
Quando Jesus Cristo me salvou;
Pela luz divina, com a Sua m\xE3o,
Todas as minhas trevas dissipou!
`,verseorder:1,ismainverse:0,id_hino:170},{idverse:25562,verse:`Ao Calv\xE1rio de horror, subiu
Jesus,
Levando a cruz, levando a cruz;
Ao Calv\xE1rio de horror, subiu Jesus,
Levando a minha cruz!
`,verseorder:2,ismainverse:1,id_hino:170},{idverse:25563,verse:`2. 
Vindo um perdido ao Senhor Jesus,
Achar\xE1 o gozo divinal,
E na sua alma raiar\xE1 a luz,
Luz que vem do c\xE9u, luz eternal!
`,verseorder:3,ismainverse:0,id_hino:170},{idverse:25564,verse:`3. 
\xC9 maravilhoso o amor de Deus,
Que mandou Seu filho p\`ra salvar
Todo o perdido pecador ou r\xE9u,
Que a Sua gra\xE7a aceitar!`,verseorder:4,ismainverse:0,id_hino:170},{idverse:25565,verse:`1. 
Era um pecador, andava sem Jesus,
N\xE3o tinha esperan\xE7a, nem divina luz;
Hoje sou remido, Cristo me salvou,
Co\`o Seu sangue me lavou!
`,verseorder:1,ismainverse:0,id_hino:171},{idverse:25566,verse:`Que amor me concedeu Jesus,
Gozo santo e celeste luz;
Cristo, breve, do c\xE9u descer\xE1,
E consigo, ent\xE3o, me levar\xE1.
`,verseorder:2,ismainverse:1,id_hino:171},{idverse:25567,verse:`2. 
O Consolador j\xE1 veio em mim morar,
A Palavra santa veio iluminar;
Quero ser guiado por t\xE3o clara luz,
Que a Cristo me conduz.
`,verseorder:3,ismainverse:0,id_hino:171},{idverse:25568,verse:`3. 
Vem, Jesus amado, vem sem demorar,
Eu estou ancioso p\`ra no c\xE9u entrar;
Vem sem mais tardan\xE7a faz raiar a luz,
"Ora vem, Senhor Jesus"
`,verseorder:4,ismainverse:0,id_hino:171},{idverse:25569,verse:`4. 
Queres, pecador, gozar a salva\xE7\xE3o?
Vem a Cristo, hoje, receber perd\xE3o,
Cristo te aceita, pobre pecador,
Nos Seus bra\xE7os de amor!`,verseorder:5,ismainverse:0,id_hino:171},{idverse:25570,verse:`1. 
\xD3 pecador, cansado e abatido,
que neste mundo vagas, sem consola\xE7\xE3o!
Se queres ser, de gra\xE7a, revestido,
Aceita Cristo, e Sua salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:172},{idverse:25571,verse:`\xD3 vem te entregar
Ao grande Salvador,
Que veio te salvar,
E te espera, cheio de amor.
`,verseorder:2,ismainverse:1,id_hino:172},{idverse:25572,verse:`2. 
\xD3 pecador, se est\xE1s desanimado,
Porque o mal domina todo o teu ser;
\xD3 cr\xEA em Cristo, o crucificado,
E contra o v\xEDcio tu t\xE9ras poder.
`,verseorder:3,ismainverse:0,id_hino:172},{idverse:25573,verse:`3. 
\xD3 pecador, por que n\xE3o te entregas
A Jesus Cristo, o bondoso Salvador?
Que, vindo a morte, grato te carrega
Ao Para\xEDso, ao pa\xEDs de amor!
`,verseorder:4,ismainverse:0,id_hino:172},{idverse:25574,verse:`4. 
Se como\xE7\xE3o tu sentes em tu\`alma,
E queres hoje receber a salva\xE7\xE3o,
Aceita Cristo, com f\xE9 e com calma,
Vem te entregar, de todo o cora\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:172},{idverse:25575,verse:`1. 
Pronto \`stamos p\`ra louvar Jesus, o Salvador,
Gl\xF3ria a Jesus e aleluia!
E havemos de cantar ao nosso Redentor,
Gl\xF3ria a Jesus e aleluia!
`,verseorder:1,ismainverse:0,id_hino:173},{idverse:25576,verse:`T\xEAm os santos do Senhor
O direito de cantar
Que Jesus, com grande amor,
Veio \xE0 terra p\`ra salvar;
Nossa alma resgatou,
Com Seu sangue nos lavou;
Gl\xF3ria a Jesus e aleluia!
`,verseorder:2,ismainverse:1,id_hino:173},{idverse:25577,verse:`2. 
A certeza temos j\xE1 da nossa reden\xE7\xE3o,
Gl\xF3ria a Jesus e aleluia!
E, cantando, vamos indo p\`ra feliz Si\xE3o;
Gl\xF3ria a Jesus e aleluia!
`,verseorder:3,ismainverse:0,id_hino:173},{idverse:25578,verse:`3. 
Temos firmes as promessas que Deus nos quis dar,
Gl\xF3ria a Jesu e aleluia!
Por Jesus, aqui, podemos sempre triunfar,
Gl\xF3ria a Jesus e aleluia!
`,verseorder:4,ismainverse:0,id_hino:173},{idverse:25579,verse:`4. 
T\xEAm os santos do Senhor
O poder de exaltar
Cristo, nosso Salvador,
Que nos veio resgatar;
J\xE1 vivemos pela f\xE9,
No pal\xE1cio do que "\xC9";
Gl\xF3ria a Jesus e aleluia!`,verseorder:5,ismainverse:0,id_hino:173},{idverse:25580,verse:`1. 
Tenho gozo em dar louvor,
Gl\xF3ria, aleluia, gl\xF3ria!
A Jesus, meu Salvador,
Gl\xF3ria, aleluia, gl\xF3ria
`,verseorder:1,ismainverse:0,id_hino:174},{idverse:25581,verse:`Gl\xF3ria a Deus, Jesus me salvou!
Gl\xF3ria a Deus, Jesus me livrou!
Gl\xF3ria a Deus, que eu salvo estou!
Gl\xF3ria, aleluia, gl\xF3ria!
`,verseorder:2,ismainverse:1,id_hino:174},{idverse:25582,verse:`2. 
S\xF3 Jesus pode salvar,
Gl\xF3ria, aleluia, gl\xF3ria!
E de todo o mal livrar,
Gl\xF3ria, aleluia, gl\xF3ria!
`,verseorder:3,ismainverse:0,id_hino:174},{idverse:25583,verse:`3. 
Enquanto no mundo andar,
Gl\xF3ria, aleluia, gl\xF3ria!
A Jesus  hei de louvar,
Gl\xF3ria, aleluia, gl\xF3ria!
`,verseorder:4,ismainverse:0,id_hino:174},{idverse:25584,verse:`4. 
Quando eu no c\xE9u chegar,
Gl\xF3ria, aleluia, gl\xF3ria!
Para sempre irei cantar,
Gl\xF3ria, aleluia, gl\xF3ria!
`,verseorder:5,ismainverse:0,id_hino:174},{idverse:25585,verse:"5. ",verseorder:6,ismainverse:0,id_hino:174},{idverse:25586,verse:`1. 
Irm\xE3os amados - E resgataddos,
Segui avante - E triufantes,
Combateremos - E venceremos,
No nome santo de Jesus!
`,verseorder:1,ismainverse:0,id_hino:175},{idverse:25587,verse:`No mundo santo - alegre canto:
Eu fui lavado - Santificado;
Vivi perdido - Mas sou remido.
No nome santo de Jesus!
`,verseorder:2,ismainverse:1,id_hino:175},{idverse:25588,verse:`2. 
Irm\xE3os amados - Santificados,
Vivei unidos - Pois sois remidos,
N\xE3o mais temendo - O bem fazendo,
No nome santo de Jesus!
`,verseorder:3,ismainverse:0,id_hino:175},{idverse:25589,verse:`3. 
Irm\xE3os amados - Purificados,
Sede valentes - E mui prudentes,
Estais lavados - E libertados,
No nome santo de Jesus!
`,verseorder:4,ismainverse:0,id_hino:175},{idverse:25590,verse:"4. ",verseorder:5,ismainverse:0,id_hino:175},{idverse:25591,verse:`1. 
Ao Senhor que nos tem feito
Sacerdotes verdadeiros,
Demos-lhe louvor e gl\xF3ria, sem cessar;
Pois Seu sangue \xE9 perfeito,
Regenera por inteiro,
Toda a alma, que quer se salvar.
`,verseorder:1,ismainverse:0,id_hino:176},{idverse:25592,verse:`Todos, tendo a mesma mente,
Jubilosos e contentes,
Demos gl\xF3ria, suma honra ao Redentor:
Sobre a cruz foi traspassado;
Com Seu sangue derramado,
Resgatou-nos o bom Salvador.
`,verseorder:2,ismainverse:1,id_hino:176},{idverse:25593,verse:`2. 
Ele \xE9 o alfa e o \xD4mega,
O primeiro e o \xDAltimo,
\xC9 o Rei dos reis, de todos o Senhor;
Ele \xE9 quem nos achega
Sua gra\xE7a, Seu arrimo,
Pelo divinal Consolador.
`,verseorder:3,ismainverse:0,id_hino:176},{idverse:25594,verse:`3. 
Com Jesus, o nosso Esposo,
L\xE1 no c\xE9u, n\xF3s entraremos,
Todos juntos, sim, no Reino Seu, de amor;
V\xEA-lo-emos glorioso,
E com Ele cantaremos
Novos hinos, cheios de fervor.`,verseorder:4,ismainverse:0,id_hino:176},{idverse:25595,verse:`1. 
Salvo estou, Jesus comprou-me
Do pecado e perdi\xE7\xE3o;
Com Seu \`sp\xEDrito j\xE1 selou-me
Deu-me paz no cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:177},{idverse:25596,verse:`Gl\xF3ria, gl\xF3ria, aleluia,
J\xE1 achei a salva\xE7\xE3o;
Gl\xF3ria, gl\xF3ria, aleluia,
Cristo deu-me reden\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:177},{idverse:25597,verse:`2. 
Cantarei com alegria,
Jubiloso som do c\xE9u,
Louvarei com harmonia
A Jesus, o amado  meu.
`,verseorder:3,ismainverse:0,id_hino:177},{idverse:25598,verse:`3. 
A mensagem gloriosa
Duma eterna salva\xE7\xE3o,
Forte, sim, e poderosa,
Digna \xE9 de aceita\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:177},{idverse:25599,verse:`4. 
Ao chegar o fim da lida,
Me recebes, Salvador;
Me concede eterna vida,
No Teu reino de amor.`,verseorder:5,ismainverse:0,id_hino:177},{idverse:25600,verse:`1. 
Crendo em Cristo Jesus vim obter
Gloriosa paz, perfeita paz:
Inda que dores a\xE7oitem meu ser,
Tenho doce paz!
`,verseorder:1,ismainverse:0,id_hino:178},{idverse:25601,verse:`Paz, paz, gloriosa paz;
Paz, paz, perfeita paz;
Desde que Cristo minh\`alma salvou,
Tenho doce paz!
`,verseorder:2,ismainverse:1,id_hino:178},{idverse:25602,verse:`2. 
Paz insond\xE1vel, qual um grande mar,
Gloriosa paz, perfeita paz;
Posso nos bra\xE7os de Deus repousar,
Tenho doce paz!
`,verseorder:3,ismainverse:0,id_hino:178},{idverse:25603,verse:`3. 
Paz inef\xE1vel de Deus, que faz bem,
Gloriosa paz, perfeita paz;
Que d\xE1 alento \xE0 alma tamb\xE9m,
Tenho doce paz!
`,verseorder:4,ismainverse:0,id_hino:178},{idverse:25604,verse:`4. 
E, nos conflitos com o tentador,
Gloriosa paz, perfeita paz,
Cristo Jesus me far\xE1 vencedor,
Tenho doce paz!`,verseorder:5,ismainverse:0,id_hino:178},{idverse:25605,verse:`1. 
Redentor formoso e belo,
Que habitas em alvor;
Na minh\`alma eu anelo
De ser como \xE9s Tu, Senhor
`,verseorder:1,ismainverse:0,id_hino:179},{idverse:25606,verse:`T\xE9 que um dia v\xE1, remido,
No c\xE9u eu ver o Redentor!
Depois que eu tiver cumprido
O Seu querer, por Seu amor.
`,verseorder:2,ismainverse:1,id_hino:179},{idverse:25607,verse:`2. 
Os meus olhos, \xF3 retira,
Deste mundo de horror,
Pois minh\`alma em Ti remira
Tua gra\xE7a e Teu favor.
`,verseorder:3,ismainverse:0,id_hino:179},{idverse:25608,verse:`3. 
J\xE1 a noite se avan\xE7a;
Bela estrela vai nascer,
Temos n\xF3s a esperan\xE7a
Dum eterno e gran prazer.
`,verseorder:4,ismainverse:0,id_hino:179},{idverse:25609,verse:`4. 
Tua m\xE3o, \xF3 meu sustente,
Humilhado aos Teus p\xE9s,
Com a alma e com a mente,
Pela verdadeira f\xE9.`,verseorder:5,ismainverse:0,id_hino:179},{idverse:25610,verse:`1. 
Aonde fruirmos a paz, o amor?
Em Cristo, somente em Cristo;
Aonde n\xF3s temos poder e vigor?
Em Cristo, somente em Cristo.
`,verseorder:1,ismainverse:0,id_hino:180},{idverse:25611,verse:`Em Cristo, em Cristo,
Eu sinto em mim um gozo, sem fim;
\xC9 inexplic\xE1vel mas \xE9 agrad\xE1vel
Estar no amor de Cristo.
`,verseorder:2,ismainverse:1,id_hino:180},{idverse:25612,verse:`2. 
Aonde \xE1guas vivas n\xF3s vamos buscar?
Em Cristo, somente em Cristo:
E p\xE3o divinal para nos saciar?
Em Cristo, somente em Cristo.
`,verseorder:3,ismainverse:0,id_hino:180},{idverse:25613,verse:`3. 
Aonde havemos n\xF3s de repousar?
Em Cristo, somente em Cristo;
As for\xE7as Podemos tamb\xE9m renovar?
Em Cristo, somente em Cristo.
`,verseorder:4,ismainverse:0,id_hino:180},{idverse:25614,verse:`4. 
Aonde achamos imenso poder?
Em Cristo, somente em Cristo;
E um povo cheio de santo prazer?
Em Cristo, somente em Cristo.`,verseorder:5,ismainverse:0,id_hino:180},{idverse:25615,verse:`1. 
Vem, Senhor, do bem  a fonte,
Vem, celeste Redentor,
Ajudar-me a entoar-te
Dignos hinos de louvor;
Tu, Jesus, por mim  morreste,
Quero s\xF3 p\`ra Ti viver;
Quero em todos os momentos
Tua b\xEAn\xE7\xE3o receber.
`,verseorder:1,ismainverse:0,id_hino:181},{idverse:25616,verse:`2. 
Era pobre desgarrado
Quando Cristo me buscou;
Para me salvar do inferno
O Seu sangue derramou;
Em Sua morte t\xE3o penosa,
Paz, perd\xE3o e vida achei,
E com Ele eternamente
Sua gl\xF3ria fruirei.
`,verseorder:2,ismainverse:0,id_hino:181},{idverse:25617,verse:`3. 
De tua gra\xE7a, \xF3 meu amado,
Sou cont\xEDnuo devedor;
Mais e mais a Ti me atrai
Pelo Teu divino amor;
Sou ingrato, e bem conhe\xE7o,
Pe\xE7o, meu Senhor, perd\xE3o;
Tira-me do vil pecado,
Rege Tu meu cora\xE7\xE3o.`,verseorder:3,ismainverse:0,id_hino:181},{idverse:25618,verse:`1. 
Jesus no Gets\xEAmane foi ligado,
E pelos \xEDmpios foi arrastado
\xC0 corte, onde foi muito insultado,
E atingido, por meu pecado;
E a senten\xE7a da turba foi o brado:
"Que seja Cristo crucificado",
Vituperado e flagelado
Jesus sofreu o meu pecado.
Vituperado e flagelado
Jesus sofreu o meu pecado.
`,verseorder:1,ismainverse:0,id_hino:182},{idverse:25619,verse:`2. 
Ent\xE3o, na cruz, foi o Cristo pendurado
E duma lan\xE7a foi traspassado;
Ali estava Jesus ensang\xFCentado,
Por meus pecados atormentado!
"Deus meu, Deus meu, por que tens m\`abandonado?\`
Clamava Cristo crucificado;
"Perdoa o povo t\xE3o enganado,
Que cometeu um tal pecado".
"Perdoa o povo t\xE3o enganado,
Que cometeu um tal pecado".
`,verseorder:2,ismainverse:0,id_hino:182},{idverse:25620,verse:`3. 
Depois Jesus Cristo foi da cruz tirado,
E ao sepulcro foi carregado;
Por santos, Seu corpo foi embalsamado,
E entre ricos foi sepultado;
Estando Cristo Jesus j\xE1 enterrado;
O Seu sepulcro foi bem guardado;
Ap\xF3s tr\xEAs dias, Jesus amado,
Da morte foi ressuscitado.
Ap\xF3s tr\xEAs dias Jesus amado,
Da morte foi ressuscitado.`,verseorder:3,ismainverse:0,id_hino:182},{idverse:25621,verse:`1. 
Da sepultura, para o c\xE9u, Jesus voltou,
Depois que o pecado aniquilou;
Com gran poder foi que ressuscitou,
E liberdade aos presos proclamou;
Tremendo, a terra O saudou,
Pois que da morte se levantou.
`,verseorder:1,ismainverse:0,id_hino:183},{idverse:25622,verse:`Ressuscitou! Ressuscitou!
E para o c\xE9u Jesus tornou;
Mas voltar\xE1, tamb\xE9m de l\xE1,
E neste mundo, ent\xE3o reinar\xE1.
`,verseorder:2,ismainverse:1,id_hino:183},{idverse:25623,verse:`2. 
Em riso, o pranto dos disc\xEDpulos se tornou,
Pois vivo, Cristo se apresentou;
-"Parti p\`ra Galil\xE9ia", ordenou,
E a promessa santa revelou:
"Poder do alto, eu vos dou
Pois ao  meu Pai, pedi-lo vou".
`,verseorder:3,ismainverse:0,id_hino:183},{idverse:25624,verse:`3. 
Jesus a Sua m\xE3o divina levantou,
Aben\xE7oando seres que salvou,
E, triunfante, para o c\xE9u tornou,
E uma nuvem logo O ocultou;
Mas a promessa lhes deixou:
"Eis que convosco p\`ra sempre estou"
`,verseorder:4,ismainverse:0,id_hino:183},{idverse:25625,verse:`4. 
Sentado \xE0 destra de Deus Pai, Jesus est\xE1,
Por Sua Esposa suplicando j\xE1;
O Mundo disto n\xE3o cogita c\xE1,
Porque n\xE3o v\xEA a luz que brilha l\xE1;
Mui beve, Cristo voltar\xE1,
Mas, s\xF3 os Seus, ao c\xE9u levar\xE1.`,verseorder:5,ismainverse:0,id_hino:183},{idverse:25626,verse:`1. 
De meu terno Salvador,
Cantarei o imenso amor,
Dando gl\xF3ria e louvor a Jesus,
Pois das trevas me chamou,
De cadeias me livrou,
E da morte me salvou meu Jesus!
`,verseorder:1,ismainverse:0,id_hino:184},{idverse:25627,verse:`Meu Jesus! Meu Jesus!
Que precioso \xE9 o nome de Jesus!
Com Seu sangue me limpou;
De Seu gozo me fartou;
Oh! que gra\xE7a me mandou, meu Jesus.
`,verseorder:2,ismainverse:1,id_hino:184},{idverse:25628,verse:`2. 
Oh! que triste condi\xE7\xE3o
A do \xEDmpio cora\xE7\xE3o;
Me salvou da perdi\xE7\xE3o, meu Jesus;
Do pecado, o perd\xE3o;
Da ru\xEDna, salva\xE7\xE3o;
Por triste, galard\xE3o, meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:184},{idverse:25629,verse:`3. 
Pelo mundo a vagar,
Solit\xE1rio sem parar,
Sem a doce paz gozar de Jesus;
Todo pranto a sofrer,
H\xE3o passados, e prazer
J\xE1 come\xE7o a receber de Jesus.
`,verseorder:4,ismainverse:0,id_hino:184},{idverse:25630,verse:`4. 
Oh! que sangue remidor,
Encontrei no Salvador,
Sangue purificador, de Jesus!
Dai louvores em a\xE7\xE3o
Da bendita salva\xE7\xE3o!
Hinos dai por gratid\xE3o a Jesus!`,verseorder:5,ismainverse:0,id_hino:184},{idverse:25631,verse:`1. 
Vem Tu, \xF3 Rei dos reis,
Guiar os teus fi\xE9is p\`ra Te louvar.
Grande e glorioso Ser, Pai de todo o poder,
Vem sobre n\xF3s reger, oh! Deus sem par!
`,verseorder:1,ismainverse:0,id_hino:185},{idverse:25632,verse:`2. 
Vem Tu, verbo de Deus,
Fazer chegar aos c\xE9us nossa ora\xE7\xE3o,
Vem, sim, aben\xE7oar teu povo e prosperar
Mensagem que falar de salva\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:185},{idverse:25633,verse:`3. 
Vem, Tu, Consolador,
Inspira e d\xE1 fervor \xE0s ora\xE7\xF5es;
Esp\xEDrito de paz, afasta Satan\xE1s,
E plena gra\xE7a traz aos cora\xE7\xF5es.
`,verseorder:3,ismainverse:0,id_hino:185},{idverse:25634,verse:`4. 
Ao grande trino Deus
Louvem os anjos Seus e n\xF3s tamb\xE9m,
A Deus nosso Senhor: Pai, Filho e Condutor
Louvemos com fervor, p\`ra sempre. Am\xE9m.`,verseorder:4,ismainverse:0,id_hino:185},{idverse:25635,verse:`1. 
Pela f\xE9 que uma vez me foi dada,
Pra seguir o Cordeiro de Deus,
Pela gra\xE7a de Deus enviada,
Andarei, com valor, para os c\xE9us.
`,verseorder:1,ismainverse:0,id_hino:186},{idverse:25636,verse:`Quero andar de valor em valor
E seguir a Jesus meu Senhor;
T\xE9 que um dia receba no c\xE9u
A coroa, que me dar\xE1 Deus.
`,verseorder:2,ismainverse:1,id_hino:186},{idverse:25637,verse:`2. 
De Deus, quero vestir a armadura,
P\`ra lutar com coragem e valor,
Pois aqui a peleja \xE9 dura,
Contra as hostes do vil tentador.
`,verseorder:3,ismainverse:0,id_hino:186},{idverse:25638,verse:`3. 
Em Jesus eu farei mil proezas
No combate da f\xE9 e do amor;
NEle tenho vigor e destreza,
P\`ra lutar e p\`ra ser vencedor!
`,verseorder:4,ismainverse:0,id_hino:186},{idverse:25639,verse:`4. 
Eu direi, ao findar esta li\xE7a:
Combati o combate de amor
E coroa terei de justi\xE7a,
Que no c\xE9u me dar\xE1 o Senhor.`,verseorder:5,ismainverse:0,id_hino:186},{idverse:25640,verse:`1. 
Mais perto quero estar
Meu Deus, de Ti!
Inda que seja a dor
Que me una a Ti,
Sempre hei de suplicar
Mais perto quero estar
Mais perto quero estar
Meu Deus de Ti!
`,verseorder:1,ismainverse:0,id_hino:187},{idverse:25641,verse:`2. 
Andando trite, aqui
Na solid\xE3o,
Paz e descanso e mim
Teus bra\xE7os d\xE3o;
Nas trevas vou sonhar,
Mais perto quero estar,
Mais perto quero estar,
Meu Deus de Ti!
`,verseorder:2,ismainverse:0,id_hino:187},{idverse:25642,verse:`3. 
Minh\`alma cantar\xE1
A Ti, Senhor!
E em betel al\xE7ara
Padr\xE3o de amor,
Eu sempre hei de rogar
Mais perto quero estar,
Mais perto quero estar,
Meu Deus, de Ti!
`,verseorder:3,ismainverse:0,id_hino:187},{idverse:25643,verse:`4. 
E, quando Cristo, enfim,
Me vier chamar,
Nos c\xE9us com serafins,
Irei morar.
Ent\xE3o me alegrarei
Perto de Ti, meu Rei.
Perto de Ti, meu Rei,
Meu Deus, de Ti!`,verseorder:4,ismainverse:0,id_hino:187},{idverse:25644,verse:`1. 
Bem sei, muitos n\xE3o podem compreender
Porque feliz \xE9 meu viver;
Entrar na Cana\xE3 de santo prazer,
\xC9 gozo do c\xE9u para mim.
\xC9 gozo do c\xE9u para mim;
Sim, gozo do c\xE9u para mim;
Entrar na Cana\xE3 de santo prazer,
\xC9 gozo do c\xE9u para mim.
`,verseorder:1,ismainverse:0,id_hino:188},{idverse:25645,verse:`2. 
Os lindos acordes celestes ouvir,
No Evangelho que recebi;
Confiar em Jesus e segui-Lo aqui,
\xC9 gozo do c\xE9u para mim.
\xC9 gozo do c\xE9u para mim;
Sim, gozo do c\xE9u para mim;
Confiar em Jesus e segui-Lo aqui,
\xC9 gozo do c\xE9u para mim.
`,verseorder:2,ismainverse:0,id_hino:188},{idverse:25646,verse:`3. 
Possuir o meu ser transformado em luz,
Ter o \`sp\xEDrito de Jesus,
Negar a mim mesmo e tomar minha cruz,
\xC9 gozo do c\xE9u para mim.
\xC9 gozo do c\xE9u para mim;
Sim, gozo do c\xE9u para mim;
Negar a mim mesmo e tomar minha cruz,
\xC9 gozo do c\xE9u para mim.
`,verseorder:3,ismainverse:0,id_hino:188},{idverse:25647,verse:`4. 
A vida eterna em Cristo, gozar,
O que \xE9 "l\xE1 de cima" buscar,
Deixar meu Jesus em minha alma reinar,
\xC9 gozo do c\xE9u para mim.
\xC9 gozo do c\xE9u para mim;
Sim, gozo do c\xE9u para mim;
Deixar meu Jesus em minh\`alma reinar,
\xC9 gozo do c\xE9u para mim.`,verseorder:4,ismainverse:0,id_hino:188},{idverse:25648,verse:`1. 
Chegado \xE0 cruz do meu bom Senhor,
Prostrado aos p\xE9s do Redentor,
Ele ouviu todo meu clamor
Gl\xF3ria ao Salvador.
`,verseorder:1,ismainverse:0,id_hino:189},{idverse:25649,verse:`Gl\xF3ria ao Salvador!
Gl\xF3ria ao Salvador
Agora sei que Ele me salvou
Gl\xF3ria ao Salvador!
`,verseorder:2,ismainverse:1,id_hino:189},{idverse:25650,verse:`2. 
Que maravilha! Jesus me amou,
Tudo de gra\xE7a me perdoou;
Quebrou meus la\xE7os e me livrou;
Gl\xF3ria ao Salvador!
`,verseorder:3,ismainverse:0,id_hino:189},{idverse:25651,verse:`3. 
Junto \xE0 cruz, inda h\xE1 lugar,
Vem, \xF3 aflito, sem demorar,
Cristo est\xE1 pronto p\`ra te salvar,
Vinde ao Salvador!`,verseorder:4,ismainverse:0,id_hino:189},{idverse:25652,verse:`1. 
Oh! quanto fez Jesus por mim!
Salvou-me do pecado!
At\xE9 a morte ( Triste fim),
Amou-me o bem amado.
Com Deus, o Pai, agora est\xE1
Jesus, meu Advogado;
Morada me conceder\xE1
Na gl\xF3ria, com o amado.
`,verseorder:1,ismainverse:0,id_hino:190},{idverse:25653,verse:`Cristo! meu Cristo!
Teu nome \xE9 doce, amado!
Desejo ver-te, face a face
Meu Cristo bem amado.
`,verseorder:2,ismainverse:1,id_hino:190},{idverse:25654,verse:`2. 
Defende como protetor,
Segura o p\xE9 cansado;
E sobre mim, com terno amor,
Vigia o bem amado.
A minha humilde peti\xE7\xE3o
Escuta com agrado;
Tranq\xFCilo e d\xE9bil cora\xE7\xE3o,
Repousa em Ti, Amado.`,verseorder:3,ismainverse:0,id_hino:190},{idverse:25655,verse:`1. 
O meu Jesus, por mim, morreu
Na cruz, com dor horrenda;
Ali em meu lugar sofreu,
Ang\xFAstia mui tremenda!
Quem poderei no mundo achar,
Que a meu Jesus v\xE1 comparar?
Jesus, Jesus, somente vou amar!
`,verseorder:1,ismainverse:0,id_hino:191},{idverse:25656,verse:`2. 
O meu Jesus est\xE1 no c\xE9u,
Intercessor potente,
Por mim, pedindo sempre a Deus,
A gra\xE7a, amor fervente.
Ning\xFAem jamais ao Pai conduz
O pecador, sen\xE3o Jesus;
Jesus, Jesus, a Deus, \xF3 me introduz!
`,verseorder:2,ismainverse:0,id_hino:191},{idverse:25657,verse:`3. 
O meu Jesus ressuscitou,
Em corpo glorioso,
Tamb\xE9m, por mim, a Deus pagou
Um pre\xE7o fabuloso!
Qual dos mortais vou encontrar?
Que a meu Jesus vou igualar?
Jesus, Jesus, n\xE3o posso comparar!
`,verseorder:3,ismainverse:0,id_hino:191},{idverse:25658,verse:`4. 
Jesus do c\xE9u h\xE1 de voltar,
Em majestade e gl\xF3ria,
Ent\xE3o Seus anjos ir\xE3o cantar
O hino da vit\xF3ria,
Jesus, enfim, vai suplantar
O Anticristo, e reinar.
Jesus, Jesus, \xF3 vem me arrebatar!`,verseorder:4,ismainverse:0,id_hino:191},{idverse:25659,verse:`1. 
Pelo mundo brilha a luz,
Desde que morreu Jesus,
Pendurado l\xE1 na cruz do Calv\xE1rio!
Os pecados carregou
E de culpa nos livrou,
Com o sangue que manou, no Calv\xE1rio!
`,verseorder:1,ismainverse:0,id_hino:192},{idverse:25660,verse:`Pelo Sangue, pelo sangue,
Somos redimidos, sim
Pelo sangue carmezim;
Pelo sangue, pelo sangue,
Pelo sangue de Jesus, no Calv\xE1rio!
`,verseorder:2,ismainverse:1,id_hino:192},{idverse:25661,verse:`2. 
Antes tinha mui temor,
Mas, agora, tenho amor,
Pois compreendo o valor do Calv\xE1rio;
Eu vivi na perdi\xE7\xE3o
Mas achei a Salva\xE7\xE3o
Pela grande reden\xE7\xE3o: o Calv\xE1rio!
`,verseorder:3,ismainverse:0,id_hino:192},{idverse:25662,verse:`3. 
\xC9s um grande pecador?
Eis aqui Teu Salvador!
Tema do bom pregador: o Calv\xE1rio,
O Cordeiro divinal
Padeceu na cruz teu mal,
E ofere\xE7e gra\xE7a tal, no Calv\xE1rio.`,verseorder:4,ismainverse:0,id_hino:192},{idverse:25663,verse:`1. 
Se tu, minh\`alma,a Deus suplica,       
E n\xE3o recebes, confiando fica           
Em Suas promessas, que s\xE3o mui ricas ,
E infal\xEDveis pra te valer.
`,verseorder:1,ismainverse:0,id_hino:193},{idverse:25664,verse:`Porque te abates, \xF3 minha alma?    
E te comoves, perdendo a calma?
N\xE3o tenhas medo, em Deus espera,
Porque bem cedo, Jesus vir\xE1 .
`,verseorder:2,ismainverse:1,id_hino:193},{idverse:25665,verse:`2. 
Ele intercede por ti, minh\`alma;
Espera nEle com f\xE9 e calma;
Jesus de todos seus males salva,
E te aben\xE7oa dos altos c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:193},{idverse:25666,verse:`3. 
Ter\xE1s em breve as dores findas,
No dia alegre da Sua vinda;
Se Cristo tarda, espera ainda
Mais um pouquinho, e O ver\xE1s.`,verseorder:4,ismainverse:0,id_hino:193},{idverse:25667,verse:`1. 
H\xE1 quem vele as pisadas
Que eu dou na sombra ou luz,
Pelas sendas escarpadas,
Velar\xE1 por mim Jesus;
Pelos vales, pelos montes,
Me conduz a Sua m\xE3o;
Vejo j\xE1 os horizontes
Duma perene mans\xE3o.
`,verseorder:1,ismainverse:0,id_hino:194},{idverse:25668,verse:`H\xE1 quem vele as pisadas,
Minha senda tra\xE7ar\xE1;
Para as c\xE9licas moradas,
Sim, Jesus me guiar\xE1
`,verseorder:2,ismainverse:1,id_hino:194},{idverse:25669,verse:`2. 
H\xE1 contacto que m\`explica
A presen\xE7a do Senhor,
Que as marchas purifica
E me livra de temor;
Cristo com a m\xE3o ferida,
Prote\xE7\xE3o me pode dar,
Quando vejo desta vida
O inquieto e forte mar.
`,verseorder:3,ismainverse:0,id_hino:194},{idverse:25670,verse:`3. 
H\xE1 um cora\xE7\xE3o amante,
De infinita compaix\xE3o,
Que concede paz constante
Ao contrito cora\xE7\xE3o;
H\xE1 favor e liberdade
Em Jesus, o Salvador,
Muita gra\xE7a e verdade
E poder consolador.`,verseorder:4,ismainverse:0,id_hino:194},{idverse:25671,verse:`1. 
Benigno Salvador! Com Tua aprova\xE7\xE3o,
Consagra em doce amor esta feliz uni\xE3o,
E sobre os noivos faz descer
A gra\xE7a que lhes \xE9 mister.
`,verseorder:1,ismainverse:0,id_hino:195},{idverse:25672,verse:`2. 
F\xE1-los em paz andar unidos no Senhor,
E a vida aqui passar em terno e santo amor
Ligados no amor de Deus;
Caminhem juntos para os c\xE9us.
`,verseorder:2,ismainverse:0,id_hino:195},{idverse:25673,verse:`3. 
\xD3 digna- Te reger sua casa como Rei;
Seus cora\xE7\xF5es manter d\xF3ceis, \xE0 Tua lei;
Livra-os de toda a tenta\xE7\xE3o,
Consola-os na tribula\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:195},{idverse:25674,verse:`4. 
Se o Salvador cumprir a nossa peti\xE7\xE3o,
Podemos descobrir nesta bendita uni\xE3o
A sombra do celeste amor
Dos salvos e seu Salvador.`,verseorder:4,ismainverse:0,id_hino:195},{idverse:25675,verse:`1. 
J\xE1 achei uma flor gloriosa,
E quem deseja a mesma ter\xE1;
A rosa de sarom preciosa
Entre mil mais beleza ter\xE1;
No vale de sombra e morte,
Nas alturas de gl\xF3ria e luz,
Esta rosa ser\xE1 a minha sorte,
Precioso pra mim \xE9 Jesus!
`,verseorder:1,ismainverse:0,id_hino:196},{idverse:25676,verse:`Precioso p\`ra mim \xE9 Jesus!
Precioso p\`ra mim \xE9 Jesus!
Eu confesso na vida e na morte
Que tudo pra mim \xE9 Jesus!
`,verseorder:2,ismainverse:1,id_hino:196},{idverse:25677,verse:`2. 
J\xE1 de muitos foi achada a rosa
E provado o excelente odor
E o poder desta flor gloriosa
Que d\xE1 vida ao vil pecador.
Mui zeloso pela lei foi Saulo,
Perseguia o povo de Deus,
Mas transformado foi em um Paulo,
Pois achou ele a rosa do c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:196},{idverse:25678,verse:`3. 
Vai buscar a Jesus precioso,
Vai depressa, a noite j\xE1 vem,
E, se perdes o amor glorioso,
Ser\xE1 triste p\`ra ti o al\xE9m;
Esta flor hoje \xE9 ofertada
A quem humildemente a buscar;
Ser\xE1 logo da terra tirada,
Para brilhar em outro lugar.`,verseorder:4,ismainverse:0,id_hino:196},{idverse:25679,verse:`1. 
No c\xE9u foi Jesus preparar-nos lugar,
Na gl\xF3ria.
E breve, mui breve Ele h\xE1 de voltar,
Em gl\xF3ria.
`,verseorder:1,ismainverse:0,id_hino:197},{idverse:25680,verse:`Eu sei, eu sei, eu tenho um lar na gl\xF3ria!
Oh! eu sei, eu sei, um lar no c\xE9u!
`,verseorder:2,ismainverse:1,id_hino:197},{idverse:25681,verse:`2. 
Levanta os olhos e conta o que h\xE1,
Na gl\xF3ria.
De pedras preciosas o lar brilha l\xE1,
Em gl\xF3ria.
`,verseorder:3,ismainverse:0,id_hino:197},{idverse:25682,verse:`3. 
Ainda n\xE3o vi, mas os anjos est\xE3o,
Na gl\xF3ria.
E ruas cobertas de ouro ser\xE3o,
Na gl\xF3ria.
`,verseorder:4,ismainverse:0,id_hino:197},{idverse:25683,verse:`4. 
Um dia, feliz para o c\xE9u seguirei,
P\`ra gl\xF3ria,
Com Cristo a reinar, eu depois voltarei,
Em gl\xF3ria.
`,verseorder:5,ismainverse:0,id_hino:197},{idverse:25684,verse:`5. 
Amigos espero no c\xE9u encontrar,
Na gl\xF3ria.
E juntos havemos de sempre ficar,
Na gl\xF3ria.
`,verseorder:6,ismainverse:0,id_hino:197},{idverse:25685,verse:"6. ",verseorder:7,ismainverse:0,id_hino:197},{idverse:25686,verse:`1. 
Achei um bom amigo,
Jesus, o Salvador,
O escolhido dos milhares para mim;
Dos vales \xE9 o l\xEDrio; \xE9 o forte Mediador,
Que me purifica e guarda para Si,
Consolador amado, meu protetor do mal,
Solicitude minha toma a Si.
`,verseorder:1,ismainverse:0,id_hino:198},{idverse:25687,verse:`Dos vales \xE9 o l\xEDrio, a estrela da
manh\xE3,
O escolhido dos milhares para mim.
Consolador amado, meu protetor do mal,
Solicitude minha toma a Si,
Dos vales \xE9 o l\xEDrio, a estrela da manh\xE3,
O escolhido dos milhares para mim.
`,verseorder:2,ismainverse:1,id_hino:198},{idverse:25688,verse:`2. 
Levou-me as dores todas,
As m\xE1goas lhe enteguei;
Minha fortaleza \xE9, na tenta\xE7\xE3o.
Deixei, por Ele tudo; os \xEDdolos queimei;
Ele me conserva santo o cora\xE7\xE3o,
Que o mundo me abandone; persiga o tentador;
Jesus me guarda at\xE9 da vida o fim.
`,verseorder:3,ismainverse:0,id_hino:198},{idverse:25689,verse:`3. 
N\xE3o desampara nunca,
Nem me abandonar\xE1,
Se fiel e obediente eu viver;
Um muro \xE9 de fogo, que me proteger\xE1,
\`T\xE9 que venha a mim o tempo de morrer,
Ao c\xE9u ent\xE3o voando, Sua gl\xF3ria eu verei
Onde a dor e a morte nunca v\xEAm.`,verseorder:4,ismainverse:0,id_hino:198},{idverse:25690,verse:`1. 
Senhor, reunidos aqui
A fim da Tua morte lembrar;
Partindo este p\xE3o nos lembramos de Ti,
At\xE9 que nos venhas buscar.
`,verseorder:1,ismainverse:0,id_hino:199},{idverse:25691,verse:`2. 
O c\xE1lice que vamos beber,
\xC9 s\xEDmbolo do sangue Teu,
Do qual nunca devemos nos esquecer;
Por Ele n\xF3s temos o c\xE9u.
`,verseorder:2,ismainverse:0,id_hino:199},{idverse:25692,verse:`3. 
Faz-nos sempre dignos, Senhor,
Da Tua divinal comunh\xE3o;
Do Teu corpo e sangue purificador
Que nos d\xE1 veraz salva\xE7\xE3o.`,verseorder:3,ismainverse:0,id_hino:199},{idverse:25693,verse:`1. 
Qu\xE3o bondoso amigo \xE9 Cristo!
Carregou co\`a nossa dor,
E nos manda que levemos
Os cuidados ao Senhor.
Falta ao cora\xE7\xE3o dorido
Gozo, paz, consola\xE7\xE3o?
Isso \xE9 porque n\xE3o levamos
Tudo a Deus em ora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:200},{idverse:25694,verse:`2. 
Tu est\xE1s fraco e carregado
De cuidados e temor?
A Jesus, ref\xFAgio eterno,
Vai com f\xE9 teu mal expor!
Teus amigos te desprezam?
Conta-lhe isso em ora\xE7\xE3o,
E com Seu amor t\xE3o terno,
Paz ter\xE1s no cora\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:200},{idverse:25695,verse:`3. 
Cristo \xE9 verdaadeiro amigo,
Disto provas nos monstrou,
Quando p\`ra levar consigo
O culpado encarnou.
Derramou Seu sangue puro
Nossa mancha p\`ra lavar;
Gozo em vida e no futuro
NEI\` podemos alcan\xE7ar.`,verseorder:3,ismainverse:0,id_hino:200},{idverse:25696,verse:`1. 
Oh! Dia alegre! Eu abracei
Jesus, e nEle salva\xE7\xE3o!
O gozo deste cora\xE7\xE3o
Eu mais e mais publicarei.
`,verseorder:1,ismainverse:0,id_hino:201},{idverse:25697,verse:`Dia feliz! Dia feliz!
Quando em Jesus me satisfiz!
Jesus me ensina a vigiar.
E confiante nEle, orar,
Dia feliz! Dia feliz!
Quando em Jesus me satisfiz!
`,verseorder:2,ismainverse:1,id_hino:201},{idverse:25698,verse:`2. 
Completa a grande transa\xE7\xE3o,
Jesus \xE9 meu, eu do Senor!
Chamou-me a voz do Seu amor;
Cedi \xE0 imensa atra\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:201},{idverse:25699,verse:`3. 
Descansa, \xF3 alma! O Salvador
\xC9 teu sustento, o p\xE3o dos c\xE9us;
E quem possui o eterno Deus,
Resiste a todo o tentador.
`,verseorder:4,ismainverse:0,id_hino:201},{idverse:25700,verse:`4. 
Meu sacro voto, excelso Deus,
De dia em dia afirmarei!
E al\xE9m da morte exultarei,
Teu filho e s\xFAdito dos c\xE9us.`,verseorder:5,ismainverse:0,id_hino:201},{idverse:25701,verse:`1. 
Junto ao trono de Deus preparado
H\xE1, crist\xE3o, um lugar para ti;
H\xE1 perfumes, h\xE1 gozo exaltado,
H\xE1 del\xEDcias profusas ali;
Sim, ali; sim, ali,
De Seus anjos fi\xE9is rodeado,
Numa esfera de gl\xF3ria e de luz,
Junto a Deus nos espera Jesus.
`,verseorder:1,ismainverse:0,id_hino:202},{idverse:25702,verse:`2. 
Os encantos da terra n\xE3o podem
Dar id\xE9ia do gozo dali;
Se na terra os prazeres acodem,
S\xE3o prazeres que acabam-se aqui;
Mas ali, mas ali
As venturas eterna concorrem
Co\`a exist\xEAncia perp\xE9tua da luz,
A tornar-nos felizes com Jesus.
`,verseorder:2,ismainverse:0,id_hino:202},{idverse:25703,verse:`3. 
Conservemos em nossa lembran\xE7a,
As riquezas do lindo pa\xEDs,
E guardemos conosco a esperan\xE7a,
De uma vida melhor, mais feliz;
Pois dali, pois dali
Uma voz verdadeira n\xE3o cansa
De oferecer-nos do reino da luz,
O amor protetor de Jesus.
`,verseorder:3,ismainverse:0,id_hino:202},{idverse:25704,verse:`4. 
Se quisermos gozar da ventura
Que no belo pa\xEDs haver\xE1,
\xC9 somente pedir de alma pura,
Que de gra\xE7a Jesus nos dar\xE1.
Pois dali, pois dali
Todo cheio de amor, de ternura,
Desse amor que mostrou-nos na cruz,
Nos escuta, nos ouve Jesus.`,verseorder:4,ismainverse:0,id_hino:202},{idverse:25705,verse:`1. 
O mundo de ilus\xE3o deixei,
A senda de pecado e dor,
P\`ra ir ao meu glorioso lar;
Ali h\xE1 gozo, paz e amor.
`,verseorder:1,ismainverse:0,id_hino:203},{idverse:25706,verse:`No mundo n\xE3o est\xE1 meu lar,
Aqui n\xE3o posso descansar;
Mas quero sempre avan\xE7ar:
No c\xE9u em breve hei de entrar.
`,verseorder:2,ismainverse:1,id_hino:203},{idverse:25707,verse:`2. 
Alguns procuram m\`impedir,
Na senda gloriosa andar,
Mas quero sempre avan\xE7ar,
At\xE9 no lar poder entrar.
`,verseorder:3,ismainverse:0,id_hino:203},{idverse:25708,verse:`3. 
Amigo que no mundo est\xE1s,
N\xE3o queres me acompanhar,
\xC0 terra de eterno amor?
Jesus deseja te livrar.`,verseorder:4,ismainverse:0,id_hino:203},{idverse:25709,verse:`1. 
Sou peregrino na terra
E longe estou do meu lar,
Minh\`alma anelante espera
Que Cristo a venha buscar;
Aqui s\xF3 h\xE1 descren\xE7a,
As lutas n\xE3o t\xEAm fim,
Mas de Jesus, a presen\xE7a,
Gl\xF3ria ser\xE1 para mim!
`,verseorder:1,ismainverse:0,id_hino:204},{idverse:25710,verse:`No c\xE9u de luz vou descansar,
Com meu Jesus hei de morar.
`,verseorder:2,ismainverse:1,id_hino:204},{idverse:25711,verse:`2. 
Em Cristo tendo j\xE1 crido,
S\xF3 pela f\xE9 viverei,
Pois Deus me tem prometido,
Que no c\xE9u descansarei!
Eu tenho permanente
O bom Consolador,
Guiando-me brandamente
\xC0 fonte viva de amor.
`,verseorder:3,ismainverse:0,id_hino:204},{idverse:25712,verse:`3. 
Embora \xE0s vezes o crente
As dores sofra da cruz,
Gozo ter\xE1 permanente,
Quando no c\xE9u vir Jesus
De gl\xF3ria coroado
No trono divinal,
Por anjos sempre louvado,
Num coro celestial.`,verseorder:4,ismainverse:0,id_hino:204},{idverse:25713,verse:`1. 
A gra\xE7a de Deus revelada
Em Cristo Jesus, meu Senhor,
Ao mundo perdido \xE9 dada
Por Deus d\`infinito favor.
`,verseorder:1,ismainverse:0,id_hino:205},{idverse:25714,verse:`Gra\xE7a, gra\xE7a,
A mim basta a gra\xE7a de Deus: Jesus;
Gra\xE7a, gra\xE7a,
A gra\xE7a eu achei em Jesus.
`,verseorder:2,ismainverse:1,id_hino:205},{idverse:25715,verse:`2. 
A gra\xE7a de Deus \xE9 mais doce,
Do que bens terrestres daqui;
Em gozo meu choro tornou-se
Correndo Sua gra\xE7a p\`ra mim.
`,verseorder:3,ismainverse:0,id_hino:205},{idverse:25716,verse:`3. 
Mais alta que nuvens celestes,
Mais funda que profundo mar,
A fonte da vida fizeste,
Na qual  nos podemos fartar.
`,verseorder:4,ismainverse:0,id_hino:205},{idverse:25717,verse:`4. 
A gra\xE7a de Deus hoje prova
Tu que vives na perdi\xE7\xE3o,
Pois Ele te salva, renova,
Tamb\xE9m limpa teu cora\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:205},{idverse:25718,verse:`1. 
O clarim j\xE1 nos alerta,
Nosso cora\xE7\xE3o desperta,
Pois a vinda \xE9 bem certa de Jesus;
De mil anjos rodeado,
Para o crente preparado,
Cristo volta coroado, Aleluia!
`,verseorder:1,ismainverse:0,id_hino:206},{idverse:25719,verse:`\xD3 irm\xE3o por Deus liberto,
Pelo sangue est\xE1s coberto;
Tens o teu perd\xE3o bem certo, salvo est\xE1s;
Voz de j\xFAbilo ouviremos
E no c\xE9u n\xF3s cantaremos,
Cristo breve n\xF3s veremos. Aleluia!
`,verseorder:2,ismainverse:1,id_hino:206},{idverse:25720,verse:`2. 
L\xE1 nas bodas do Cordeiro,
Sentaremos prazenteiros;
Oh! Que gozo verdadeiro com Jesus!
Pois no c\xE9u n\xE3o h\xE1 mais pranto,
Eis que tudo ser\xE1 canto;
Cristo vem buscar os santos. Aleluia!
`,verseorder:3,ismainverse:0,id_hino:206},{idverse:25721,verse:`3. 
Sim \xE0 mesa sentaremos,
E com Cristo cearemos;
Qu\xE3o felizes n\xF3s seremos com Jesus!
Para sempre gozaremos,
E com Cristo reinaremos,
Sua gl\xF3ria fruiremos. Aleluia!`,verseorder:4,ismainverse:0,id_hino:206},{idverse:25722,verse:`1. 
Jerusal\xE9m divina
Vis\xE3o de paz ditosa,
De Cristo, santa esposa,
Radiante de esplendor;
Tens fontes cristalinas,
S\xE3o vivos teus altares
E os anjos, aos milhares,
Te cingem ao redor.
`,verseorder:1,ismainverse:0,id_hino:207},{idverse:25723,verse:`2. 
Mans\xE3o do Rei bendito,
S\xE3o doze tuas portas
Que sempre s\xE3o transpostas
Por serem imortais;
Teus livros t\xEAm escritos
Os nomes dos remidos;
Em ti, ser\xE3o ouvidos
Os hinos celestiais.
`,verseorder:2,ismainverse:0,id_hino:207},{idverse:25724,verse:`3. 
Teus santos moradores,
Um glorioso canto
Proferem ao Deus santo
Que j\xE1 lhes perdoou;
Entoam mil louvores
A Cristo, qual cordeiro,
Que manso, num madeiro
Por ele se imolou!
`,verseorder:3,ismainverse:0,id_hino:207},{idverse:25725,verse:`4. 
Ao mesmo Cristo amemos,
Ao mesmo Deus temamos,
N\xF3s que por f\xE9 andamos,
Levando a Sua cruz;
E prontos fruiremos,
Os teus umbrais passados,
Mil gozos preparados,
Na gl\xF3ria, com Jesus!`,verseorder:4,ismainverse:0,id_hino:207},{idverse:25726,verse:`1. 
Hoje queres te entregar p\`ra Jesus Cristo,
\xD3 cansado e triste pecador?
Para aquele, que, no G\xF3lgota, foi visto
Padecendo nossa culpa, nossa dor?
`,verseorder:1,ismainverse:0,id_hino:208},{idverse:25727,verse:`Vem a Cristo, vem a Cristo,
Que te chama com amor celestial;
Vem a Cristo, vem a Cristo,
Pois Jesus \xE9 o rochedo divinal.
`,verseorder:2,ismainverse:1,id_hino:208},{idverse:25728,verse:`2. 
Hoje queres obter a liberdade,
Que se encontra no Senhor Jesus
Receber no cora\xE7\xE3o a santidade,
Pelo sangue derramado l\xE1 na cruz?
`,verseorder:3,ismainverse:0,id_hino:208},{idverse:25729,verse:`3. 
Hoje queres desfrutar a paz divina,
Que no mundon\xE3o se pode achar?
Vem a Cristo, que te salva, que te ensina,
Onde podes esta paz no c\xE9u gozar!
`,verseorder:4,ismainverse:0,id_hino:208},{idverse:25730,verse:`4. 
Hoje queres renunciar o bem terreno
Crendo em Cristo, o grande Salvador?
Queres vir ap\xF3s o meigo Nazareno,
V\xEA-Lo um dia l\xE1 no c\xE9u de resplendor?`,verseorder:5,ismainverse:0,id_hino:208},{idverse:25731,verse:`1. 
Escuta a voz do bom Pastor
Em esplendorosa solid\xE3o,
Chama ao cordeiro que em temor
Vaga na densa escurid\xE3o.
`,verseorder:1,ismainverse:0,id_hino:209},{idverse:25732,verse:`Hoje vem a Jesus,
Ao bondoso Salvador,
Receber Sua luz.
Vem a Cristo, o bom pastor.
`,verseorder:2,ismainverse:1,id_hino:209},{idverse:25733,verse:`2. 
Quem ajudar que a Jesus,
Os pecados procurar,
V\xE1 espalhar a Sua luz,
O Evangelho proclamar.
`,verseorder:3,ismainverse:0,id_hino:209},{idverse:25734,verse:`3. 
Triste deserto o mundo \xE9,
Cercado de perigos mil;
Vem, chama Cristo, vem com f\xE9,
Vem para dentro do redil.
`,verseorder:4,ismainverse:0,id_hino:209},{idverse:25735,verse:`4. 
Escuta hoje, o bom Pastor,
Vem no aprisco te abrigar,
Foge do lobo enganador,
Pois ele quer te devorar.
`,verseorder:5,ismainverse:0,id_hino:209},{idverse:25736,verse:"5. ",verseorder:6,ismainverse:0,id_hino:209},{idverse:25737,verse:`1. 
Fala, fala, Senhor, nesta hora,
Que ansioso te quero ouvir;
Teu falar d\xE1 valor e restaura,
E mais s\xE1bio me faz no porvir.
`,verseorder:1,ismainverse:0,id_hino:210},{idverse:25738,verse:`Oh! Bendito o Teu nome em eterno,
Que Tu falas a quem escutar,
De sa\xFAde e repouso superno,
De alegria e paz eternal.
`,verseorder:2,ismainverse:1,id_hino:210},{idverse:25739,verse:`2. 
Fala, fala, Senhor, que conservo
Tuas palavras de vida e vigor;
Estou pronto a seguir-te com zelo,
Pelas Tuas veredas de amor.
`,verseorder:3,ismainverse:0,id_hino:210},{idverse:25740,verse:`3. 
Fala, fala, Senhor, que eu ou\xE7o
Tuas palavras com todo fervor,
Pois conduzem ao eterno repouso,
S\xE3o conselhos mui ricos de amor.`,verseorder:4,ismainverse:0,id_hino:210},{idverse:25741,verse:`1. 
Vem, enquanto Deus te chama
E tu sentes Seu amor,
Pois do c\xE9u poder derrama
P\`ra salvar o pecador!
`,verseorder:1,ismainverse:0,id_hino:211},{idverse:25742,verse:`Vem a Deus, a Jesus, entregar teu cora\xE7\xE3o;
E ter\xE1s Sua luz... A perfeita salva\xE7\xE3o!
`,verseorder:2,ismainverse:1,id_hino:211},{idverse:25743,verse:`2. 
Se da vida tens o fardo
E tu\`alma triste est\xE1,
Cr\xEA em Deus, n\xE3o sejas tardo,
Que Jesus te salvar\xE1!
`,verseorder:3,ismainverse:0,id_hino:211},{idverse:25744,verse:`3. 
Neste mundo vais andando,
Sem tranq\xFCilidade e paz;
- Volta a Deus, mas confiando,
E feliz ent\xE3o ser\xE1s.
`,verseorder:4,ismainverse:0,id_hino:211},{idverse:25745,verse:`4. 
Vem a Cristo, que te espera,
N\xE3o demores, pecador!
Nos Seus bra\xE7os Deus quisera
Receber-te com amor!`,verseorder:5,ismainverse:0,id_hino:211},{idverse:25746,verse:`1. 
Os guerreiros se preparam para a grande luta
\xC9 Jesus, o Capit\xE3o, que avante os levar\xE1.
A m\xEDlicia dos remidos marcha impoluta;
Certa que a vit\xF3ria alcan\xE7ar\xE1!
`,verseorder:1,ismainverse:0,id_hino:212},{idverse:25747,verse:`Eu quero estar com Cristo,
Onde a luta se travar,
No lance imprevisto
Na frente m\`encontrar.
At\xE9 que O possa ver na gl\xF3ria,
Se alegrando da vit\xF3ria,
Onde Deus vai me coroar!
`,verseorder:2,ismainverse:1,id_hino:212},{idverse:25748,verse:`2. 
Eis os batalh\xF5es de Cristo prosseguindo avante,
N\xE3o os v\xEAs com que valor combatem contra o mal?
Podes tu ficar dormindo, mesmo vacilante,
Quando atacam outros a Belial?
`,verseorder:3,ismainverse:0,id_hino:212},{idverse:25749,verse:`3. 
D\xE1-te pressa, n\xE3o vaciles, hoje Deus te chama
Para vires pelejar ao lado do Senhor;
Entra na batalha onde mais o fogo inflama,
E peleja contra o vil tentador!
`,verseorder:4,ismainverse:0,id_hino:212},{idverse:25750,verse:`4. 
A peleja \xE9 tremenda, torna-se renhida,
Mas s\xE3o poucos os soldados para batalhar;
\xD3 vem libertar as pobres almas oprimidas
De quem furioso, as quer tragar!`,verseorder:5,ismainverse:0,id_hino:212},{idverse:25751,verse:`1. 
Sobre a terra vou andando,
Bem alegre para o c\xE9u,
Satisfeito e salmodiando
Ao bondoso Deus;
Com ferozes inimigos
Pelejando sempre estou,
Mas conservo paz comigo,
Pois amado sou.
`,verseorder:1,ismainverse:0,id_hino:213},{idverse:25752,verse:`2. 
Meu Pastor \xE9 Pai celeste,
Que me guia p\`ra Si\xE3o
Pelos vales e mui prestes,
Com segura m\xE3o;
Nos perigos da viagem,
Nas ang\xFAstias e na dor,
Sua voz me diz: "Coragem,
Sou teu Salvador".
`,verseorder:2,ismainverse:0,id_hino:213},{idverse:25753,verse:`3. 
Com Jesus eu trabalhando,
Persevero em ora\xE7\xE3o;
Seu saber com f\xE9 buscando
P\`ra tornar-me s\xE3o.
Que prazer incompar\xE1vel
Ser guiado por Jesus.
Sim, por Cristo t\xE3o am\xE1vel,
Na perene luz.
`,verseorder:3,ismainverse:0,id_hino:213},{idverse:25754,verse:`4. 
Ao chegar-se nessa p\xE1tria
Todos prantos cessar\xE3o.
J\xE1 o sol divino raia,
Na feliz mans\xE3o.
Males, lutas e agonias,
Acabaram-se aqui;
Cristo vem, oh! Que alegria
Vamos ter ali!`,verseorder:4,ismainverse:0,id_hino:213},{idverse:25755,verse:`1. 
Nosso Redentor foi preparar
Um lugar de repouso e esplendor;
Brevemente chamar\xE1 para a casa a descansar,
N\xF3s, os salvos, do mundo enganador.
`,verseorder:1,ismainverse:0,id_hino:214},{idverse:25756,verse:`Desejamos ir l\xE1,
Desejamos ir l\xE1;
Que alegria ser\xE1,
Quando n\xF3s nos encontrarmos l\xE1.
`,verseorder:2,ismainverse:1,id_hino:214},{idverse:25757,verse:`2. 
Nesta terra tesouros n\xE3o h\xE1,
Que nos possam aqui segurar;
Desejamos ir ao c\xE9u onde Cristo j\xE1 est\xE1,
Ao lugar onde iremos descansar.
`,verseorder:3,ismainverse:0,id_hino:214},{idverse:25758,verse:`3. 
Neste lar com Jesus, o Senhor,
N\xF3s havemos de sempre reinar.
Vamos n\xF3s ali cantar, novos hinos de louvor
Ao Cordeiro que veio nos salvar.`,verseorder:4,ismainverse:0,id_hino:214},{idverse:25759,verse:`1. 
Neste mundo a vera Igreja,
Peregrina vai andar,
Anelando que esteja
No seu verdadeiro lar.
`,verseorder:1,ismainverse:0,id_hino:215},{idverse:25760,verse:`Ver-nos-emos, ver-nos-emos,
Ver-nos-emos na terra divinal;
Ver-nos-emos, ver-nos-emos,
Ver-nos-emos junto ao rio sem igual.
`,verseorder:2,ismainverse:1,id_hino:215},{idverse:25761,verse:`2. 
Nada aqui \xE9 permanente,
Tudo tem que terminar,
Mas olhamos para a frente,
Para o nosso eterno lar.
`,verseorder:3,ismainverse:0,id_hino:215},{idverse:25762,verse:`3. 
Os remidos c\xE1 enterram
Seus amados ao morrer,
Mas um dia, sim, esperam
Que no c\xE9u os h\xE3o de ver.
`,verseorder:4,ismainverse:0,id_hino:215},{idverse:25763,verse:`4. 
Com Jesus, o Rei, nos ares,
Beve havemos n\xF3s de estar,
E com anjos, aos milhares,
Sua gl\xF3ria contemplar.`,verseorder:5,ismainverse:0,id_hino:215},{idverse:25764,verse:`1. 
Louvai a Deus, com j\xFAbilo, cantando,
E Seu amor ao mundo anunciai!
De Cristo, as gl\xF3rias, juntos celebrando;
Do Salvador, a fama publicai.
`,verseorder:1,ismainverse:0,id_hino:216},{idverse:25765,verse:`Os prod\xEDgios cantaremos
De Jesus, o Redentor;
For\xE7a, vida, bens daremos
P\`ra falar do Seu amor!
`,verseorder:2,ismainverse:1,id_hino:216},{idverse:25766,verse:`2. 
Louvai a Deus, Senhor onipotente,
A quem devemos nossa salva\xE7\xE3o,
Que habita os c\xE9us, por\xE9m, aqui presente.
Seguros, guia os Seus por Sua m\xE3o!
`,verseorder:3,ismainverse:0,id_hino:216},{idverse:25767,verse:`3. 
Louvai a Deus e alegres adorai-O;
Mil gra\xE7as demos por Seu grande amor;
Com a celeste multid\xE3o louvai-O,
Deus, sempiterno e nosso Redentor.`,verseorder:4,ismainverse:0,id_hino:216},{idverse:25768,verse:`1. 
Cristo com voz amorosa
Chama-te p\`ra Sua luz,
Torna tua alma ditosa
Se carregares a cruz.
`,verseorder:1,ismainverse:0,id_hino:217},{idverse:25769,verse:`Ouve Sua voz a chamar!
Cristo te quer libertar;
Fala com voz amorosa:
"Paz hoje quero te dar".
`,verseorder:2,ismainverse:1,id_hino:217},{idverse:25770,verse:`2. 
Por que voltar n\xE3o procuras,
Do mau caminho em que est\xE1s?
No fim do qual amarguras,
E eterna dor sofrer\xE1s.
`,verseorder:3,ismainverse:0,id_hino:217},{idverse:25771,verse:`3. 
Mui breve ir\xE1s encontrar-te
Perante a morte, que atroz
Vai, sem piedades, levar-te
Onde n\xE3o ou\xE7as Sua voz!
`,verseorder:4,ismainverse:0,id_hino:217},{idverse:25772,verse:`4. 
Ouve Sua voz amorosa
Como persiste a chamar;
Com Sua m\xE3o poderosa
Cristo te quer libertar.`,verseorder:5,ismainverse:0,id_hino:217},{idverse:25773,verse:`1. 
S\xF3 em Jesus, queres tu descansar?
D\xE1 teu fardo a Jesus.
Paz e socorro n\xE3o podes achar?
D\xE1 teu fardo a Jesus.
`,verseorder:1,ismainverse:0,id_hino:218},{idverse:25774,verse:`D\xE1 teu fardo a Jesus,
As trevas far\xE1 em luz.
Em Cristo um abrigo, ter\xE1s no perigo,
\xD3, d\xE1 teu fardo a Jesus.
`,verseorder:2,ismainverse:1,id_hino:218},{idverse:25775,verse:`2. 
Paz redentora tu\`alma ter\xE1,
D\xE1 teu fardo a Jesus.
E de inquietudes te libertar\xE1.
D\xE1 teu fardo a Jesus.
`,verseorder:3,ismainverse:0,id_hino:218},{idverse:25776,verse:`3. 
Das grandes lutas n\xE3o queiras fugir,
D\xE1 teu fardo a Jesus.
Se santo gozo n\xE3o podes fruir,
D\xE1 teu fardo a Jesus.
`,verseorder:4,ismainverse:0,id_hino:218},{idverse:25777,verse:`4. 
Males te assaltam aos mil, pecador?
D\xE1 teu fardo a Jesus.
Vais j\xE1 cansado de penas e de dor?
D\xE1 teu fardo a Jesus.
`,verseorder:5,ismainverse:0,id_hino:218},{idverse:25778,verse:`5. 

Cristo vir\xE1 dissipar teu temor,
D\xE1 teu fardo a Jesus.
Pois, aos Seus santos, d\xE1 sempre vigor,
D\xE1 teu fardo a Jesus.
`,verseorder:6,ismainverse:0,id_hino:218},{idverse:25779,verse:"6. ",verseorder:7,ismainverse:0,id_hino:218},{idverse:25780,verse:`1. 
Os raios do amor de Deus,
Brilham na minh\`alma,
E o fim eu vi dos dias meus,
Tamb\xE9m Um que me salva;
Eu tenho j\xE1 do c\xE9u a luz.
Das trevas fui liberto,
Agora Cristo me conduz
Nas plagas do deserto!
`,verseorder:1,ismainverse:0,id_hino:219},{idverse:25781,verse:`O Teu amor, \xF3 Criador,
Se revelou ali na cruz.
Quando expirou em meu lugar
Quem me salvou: - o meu Jesus.
`,verseorder:2,ismainverse:1,id_hino:219},{idverse:25782,verse:`2. 
Ao meu Jesus darei louvor,
Pois hoje compreeendo,
Que o amor do grande Redentor,
Vai tudo me provendo;
J\xE1 dantes desta cria\xE7\xE3o
Jesus me preparava
A mais perfeita salva\xE7\xE3o;
Oh! Quanto a mim amava!
`,verseorder:3,ismainverse:0,id_hino:219},{idverse:25783,verse:`3. 
O dom precioso de Jesus,
Oh! Deus, me revelaste!
E Teu amor, celeste luz,
Em mim Tu derramaste!
Quem medir\xE1 o amor de Deus,
T\xE3o santo, insond\xE1vel,
O qual enche os mais altos c\xE9us;
Amor inesgot\xE1vel!`,verseorder:4,ismainverse:0,id_hino:219},{idverse:25784,verse:`1. 
Para os campos a segar,
Eis o trigo a lourejar,
Ide ceifar, ides segar;
Todo dia trabalhai
E s\xF3 de Jesus falai;
Ide ceifar, ide pregar!
`,verseorder:1,ismainverse:0,id_hino:220},{idverse:25785,verse:`Ide segar, ide ceifar!
Sim, trabalhai, e proclamai!
Eis que o amor do Salvador
Vos impele ao Seu labor
Sem demorar, ide segar.
`,verseorder:2,ismainverse:1,id_hino:220},{idverse:25786,verse:`2. 
Para o vale, monte ou mar,
O Senhor vos que mandar
A proclamar, anunciar;
Os perdidos procurar,
Pois Jesus os quer salvar;
Ide falar, e proclamar!
`,verseorder:3,ismainverse:0,id_hino:220},{idverse:25787,verse:`3. 
Falaremos com fervor,
Do poder do Salvador,
Ao pecador, ao sofredor;
Pronto chegar\xE1 o fim,
Soar\xE1 do c\xE9u o clarim,
Tereis, ent\xE3o, o galard\xE3o.`,verseorder:4,ismainverse:0,id_hino:220},{idverse:25788,verse:`1. 
Eis-me a Teus p\xE9s, Senhor, opera em mim!
Espero mais vigor, opera em mim!
Eu gozo vou fruir, se Tua lei cumprir;
\xD3 d\xE1-me Teu amor: opera em mim!
`,verseorder:1,ismainverse:0,id_hino:221},{idverse:25789,verse:`2. 
Sedento, com ardor, opera em mim!
Te pe\xE7o mais amor, opera em mim!
Tu s\xF3 me podes dar a perfei\xE7\xE3o sem par,
\xD3 bom Consolador: opera em mim!
`,verseorder:2,ismainverse:0,id_hino:221},{idverse:25790,verse:`3. 
\xD3 meu Senhor Jesus, opera em mim!
Por Tua santa luz, opera em mim!
Revela-me o valor da morte do Senhor,
Por mim, na dura cruz; opera em mim!
`,verseorder:3,ismainverse:0,id_hino:221},{idverse:25791,verse:`4. 
N\xE3o tardes mais, Senhor, opera em mim!
Escuta o meu clamor, opera em mim!
No sangue de Jesus, vertido l\xE1 na cruz,
Me guarda por favor, opera em mim!
`,verseorder:4,ismainverse:0,id_hino:221},{idverse:25792,verse:`5. 

`,verseorder:5,ismainverse:0,id_hino:221},{idverse:25793,verse:"6. ",verseorder:6,ismainverse:0,id_hino:221},{idverse:25794,verse:`1. 
Vem a Jesus; \xF3 perdido,
Vem a Jesus como est\xE1s;
O sangue na cruz vertido,
Dar-te-\xE1 gra\xE7a e paz.
`,verseorder:1,ismainverse:0,id_hino:222},{idverse:25795,verse:`Vem a Jesus, sem demora;
\xF3 cr\xEA no Filho de Deus!
Ele te salva agora,
Quer te guiar para o c\xE9u.
`,verseorder:2,ismainverse:1,id_hino:222},{idverse:25796,verse:`2. 
Por que demoras ainda,
Dar Tua vida a Jesus?
Eis quando tudo aqui finda,
Qual h\xE1 de ser tua luz?
`,verseorder:3,ismainverse:0,id_hino:222},{idverse:25797,verse:`3. 
O mundo quer enganar-te,
Para a tua alma perder,
Triste ser\xE1 a tua parte;
Al\xE9m t\`espera o sofrer.
`,verseorder:4,ismainverse:0,id_hino:222},{idverse:25798,verse:`4. 
As horas passam depressa,
Nunca jamais as ter\xE1s
Quando a tua vida aqui cessa,
Onde tua alma estar\xE1?
`,verseorder:5,ismainverse:0,id_hino:222},{idverse:25799,verse:`5. 
Portas abertas aos libertos
No c\xE9u de luz sempre est\xE3o;
Eis que os anjos alertos
A ver tua entrada estar\xE3o!`,verseorder:6,ismainverse:0,id_hino:222},{idverse:25800,verse:`1. 
Em escurid\xE3o, sem Jesus, me vi,
Com mil penas e grande dor;
Mas Jesus chamou: "Vem, sim, vem a Mim,
Quero ser teu Salvador".
`,verseorder:1,ismainverse:0,id_hino:223},{idverse:25801,verse:`Na minh\`alma reina paz;
Desde que vim a Jesus;
Tenho sempre paz
Pois avistei de Deus, a luz!
E gozo mui veraz.
`,verseorder:2,ismainverse:1,id_hino:223},{idverse:25802,verse:`2. 
Pelas grandes ondas da perdi\xE7\xE3o,
Fui cercado no alto mar,
Sem ter um piloto no furac\xE3o,
Que pudesse me guiar.
`,verseorder:3,ismainverse:0,id_hino:223},{idverse:25803,verse:`3. 
E se te encontrares a pelejar,
Sem aux\xEDlio ter eficaz,
Clama ao Salvador, clama sem tardar,
Sua gra\xE7a \xE9 mui veraz.
`,verseorder:4,ismainverse:0,id_hino:223},{idverse:25804,verse:"4. ",verseorder:5,ismainverse:0,id_hino:223},{idverse:25805,verse:`1. 
\xC9 o tempo de segar e tu sem vacilar,
Declaraste sem temor: "N\xE3o posso trabalhar?"
Vem, enquanto Cristo, o Mestre, est\xE1 a te chamar:
"Jovem, jovem, \xF3 vem trabalhar!"
`,verseorder:1,ismainverse:0,id_hino:224},{idverse:25806,verse:`Vem e v\xEA os campos brancos ja
est\xE3o
Aguardando bra\xE7os que os segar\xE3o;
Jovem, desperta, faz-te pronto e alerta!
Queiras logo responder: "Eis-me aqui, Senhor".

Olha que a seara bem madura est\xE1;
Que colheita gloriosa n\xE3o ser\xE1!
Jovem, despeta. Faz-te pronto e alerta!
Poucos dias s\xE3o que restam para o segador!
`,verseorder:2,ismainverse:1,id_hino:224},{idverse:25807,verse:`2. 
As gavelas que recolhes: j\xF3ias de esplendor
Brilhar\xE3o na tua coroa, e dar\xE3o fulgor;
Busca logo essas j\xF3ias, Deus \xE9 premiador;
Jovem, jovem entra no labor!
`,verseorder:3,ismainverse:0,id_hino:224},{idverse:25808,verse:`3. 
A manh\xE3 ja vai passando, n\xE3o mais voltar\xE1;
Da colheita o tempo brevemente findar\xE1;
E perante o teu Senhor vazio t\`achar\xE1s;
Jovem, jovem, obecede j\xE1!
`,verseorder:4,ismainverse:0,id_hino:224},{idverse:25809,verse:"4. ",verseorder:5,ismainverse:0,id_hino:224},{idverse:25810,verse:`1. 
Na batalha contra o mal, s\xEA valente!
Segue em marcha triunfal, s\xEA valente!
Olha o alvo que \xE9 Jesus,
Que \xE0 vit\xF3ria te conduz;
\xD3 n\xE3o deixes tua cruz,
S\xEA valente!
`,verseorder:1,ismainverse:0,id_hino:225},{idverse:25811,verse:`S\xEA valente! Pelejando por Jesus,
S\xEA valente! Nunca rejeitando a cruz!
Firme sempre no amor,
Com ind\xF4mito valor,
Cheio do Consolador,
S\xEA valente!
`,verseorder:2,ismainverse:1,id_hino:225},{idverse:25812,verse:`2. 
Se o maligno t\`enfrentar, s\xEA valente!
Lutar\xE1s sem recuar, s\xCA valente!
Seja aqui, ou onde for,
Escudado no Senhor,
Mostrar\xE1s o teu valor;
S\xEA valente!
`,verseorder:3,ismainverse:0,id_hino:225},{idverse:25813,verse:`3. 
Co\`altru\xEDsmo, com poder, s\xEA valente!
Franco, sem o mal temer, s\xEA valente!
Aos ca\xEDdos em redor,
Manifesta-lhes o amor;
E ser\xE1s um vencedor;
S\xEA valente!
`,verseorder:4,ismainverse:0,id_hino:225},{idverse:25814,verse:`4. 
O Evangelho a proclamar, s\xEA valente!
No Brasil, em terra ou mar, s\xEA valente!
Tua vida enobrecer!
Sempre com Jesus viver,
E a ti tamb\xE9m vencer;
S\xEA valente!!
`,verseorder:5,ismainverse:0,id_hino:225},{idverse:25815,verse:"5. ",verseorder:6,ismainverse:0,id_hino:225},{idverse:25816,verse:`1. 
Cristo, Teu santo amor deste para mim
Nada p\`ra Ti, Senhor, negarei aqui;
Me prostro em gratid\xE3o,
Cumpro com prontid\xE3o,
Me guia Tua m\xE3o, oh! me rendo a Ti.
`,verseorder:1,ismainverse:0,id_hino:226},{idverse:25817,verse:`2. 
Perante o trono estou, pede Tu, por mim,
Ao nosso Pai eu vou, s\xF3 Jesus, por Ti;
A cruz hei de levar,
Teu nome proclamar,
Um hino a Deus cantar, sim, Jesus, por Ti.
`,verseorder:2,ismainverse:0,id_hino:226},{idverse:25818,verse:`3. 
Um cora\xE7\xE3o de amor, quero, \xF3 meu Jesus,
Ao Teu igual, Senhor, espalhando a luz;
A Ti eu vou servir,
O tempo redimir,
As almas conduzir, quero eu a Ti.
`,verseorder:3,ismainverse:0,id_hino:226},{idverse:25819,verse:`4. 
Tudo que tenho ou sou, \xE9 por Teu favor;
Alegre sempre vou, junto a Ti, senhor;
Teu rosto, sim, verei,
Contigo eu estarei,
E sempre viverei, l\xE1 no c\xE9u por Ti!`,verseorder:4,ismainverse:0,id_hino:226},{idverse:25820,verse:`1. 
Deus amou de tal maneira
Este mundo sofredor,
Que \xE0 humanidade inteira,
Deus Seu Filho para Salvador.
`,verseorder:1,ismainverse:0,id_hino:227},{idverse:25821,verse:`E agora nunca mais perece
O que cr\xEA, mais vida eterna tem;
Quem em Cristo permanece,
Vai ser coroado al\xE9m.
O pecado na cruz foi vencido,
Podes, pela f\xE9, vencer tamb\xE9m;
De justi\xE7a, \xF3 S\xEA vestido,
E receber\xE1s coroa al\xE9m.
`,verseorder:2,ismainverse:1,id_hino:227},{idverse:25822,verse:`2. 
Prisioneiro do pecado,
Vagas sem esperan\xE7a ter;
Mas Jesus foi enviado,
Para liberdade te trazer.
`,verseorder:3,ismainverse:0,id_hino:227},{idverse:25823,verse:`3. 
Tu que vives desviado
Sem a paz de Deus gozar,
Ouve a voz do Pai amado,
Que te espera para te abra\xE7ar.`,verseorder:4,ismainverse:0,id_hino:227},{idverse:25824,verse:`1. 
Este mundo n\xE3o compreende,
Qu\xE3o alegre \xE9 o meu viver,
Mas a Cristo quem atende,
Do c\xE9u pode e gozo ter.
`,verseorder:1,ismainverse:0,id_hino:228},{idverse:25825,verse:`Mesmo quando em afli\xE7\xE3o,
Gozo a paz de Deus;
Cristo ao meu cora\xE7\xE3o,
Gozo traz dos c\xE9us,
Se com Cristo aqui viver,
T\xE3o glorioso \xE9,
Maior gl\xF3ria hei de ter,
Se eu guardar sempre a f\xE9.
`,verseorder:2,ismainverse:1,id_hino:228},{idverse:25826,verse:`2. 
O que me faz mui ditoso,
Oh, prov\xE9m do amor de Deus!
Mas ser\xE1 maior meu gozo,
Quando for morar nos c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:228},{idverse:25827,verse:`3. 
Sinto meu ser transformado,
Pela gra\xE7a do Senhor;
Cada dia mais chegado,
Eu \xE0 p\xE1tria, estou, de amor.`,verseorder:4,ismainverse:0,id_hino:228},{idverse:25828,verse:`1. 
Jesus, \xF3 meigo Salvador,
Do c\xE9u, \xE0 terra \xE9s vindo,
E nos Teus bra\xE7os som dul\xE7or,
Oh! vai-me conduzindo;
Assim eu vou seguindo
Na Tua luz perfeita,
Que sempre me deleita;
Tua Palavra, oh, Senhor!
`,verseorder:1,ismainverse:0,id_hino:229},{idverse:25829,verse:`2. 
Que grande gozo foi p\`ra mim,
Oh, quando me encontraste!
Do cora\xE7\xE3o, abriu enfim;
A porta e logo entraste;
Ent\xE3o Tu me levaste,
Por Tua luz perfeita,
Que sempre me deleita;
Tua Palavra, que amor, sim!
`,verseorder:2,ismainverse:0,id_hino:229},{idverse:25830,verse:`3. 
Tu me livraste, Salvador,
De miser\xE1vel sorte,
Me guardar\xE1s por Teu amor,
Fiel at\xE9 \xE0 morte;
Desejo que me exortes
Por Tua luz perfeita,
Que sempre me deleita;
Tua Palavra, meu Senhor!
`,verseorder:3,ismainverse:0,id_hino:229},{idverse:25831,verse:`4. 
\xD3 Salvador, e bom Jesus,
Meus p\xE9s na rocha fundas;
Pois Tua m\xE3, a mim conduz,
\xC0s \xE1guas mui profundas,
Do trono oriundas,
\xC0 tua luz perfeita,
Que sempre me deleita;
Tua Palavra, \xF3 Jesus!`,verseorder:4,ismainverse:0,id_hino:229},{idverse:25832,verse:`1. 
N\xF3s vogamos, o mar vencemos,
Nesta nau que nos conduz,
Um naufr\xE1gio n\xE3o tememos,
Pois ao leme vai Jesus.
`,verseorder:1,ismainverse:0,id_hino:230},{idverse:25833,verse:`Vem amigo, pois te amamos,
Sem tardar, que n\xF3s vamos;
A tormenta n\xE3o olhamos
E rumamos para o c\xE9u.
A tormenta n\xE3o olhamos
E rumamos para o c\xE9u.
`,verseorder:2,ismainverse:1,id_hino:230},{idverse:25834,verse:`2. 
Para nossa feliz morada,
Eis a hora de partir;
Hoje mesmo se faz chamada;
"Quem com Cristo que seguir?"
`,verseorder:3,ismainverse:0,id_hino:230},{idverse:25835,verse:`3. 
Ao findar da travessia,
No c\xE9u vamos ancorar;
Com presteza, com alegria,
N\xF3s havemos de saltar.
`,verseorder:4,ismainverse:0,id_hino:230},{idverse:25836,verse:`4. 
Ante n\xF3s, vemos a cidade,
Cujo porto se abrir\xE1,
E Jesus cheio de bondade
Aos chegados sorrir\xE1.
`,verseorder:5,ismainverse:0,id_hino:230},{idverse:25837,verse:"5. ",verseorder:6,ismainverse:0,id_hino:230},{idverse:25838,verse:`1. 
N\xF3s, os salvos do Senhor,
Caminhemos com valor;
Ele guarda o cora\xE7\xE3o,
Cristo \xE9 a nossa salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:231},{idverse:25839,verse:`N\xE3o foi com ouro que nos comprou
Jesus,
Mas com Seu sangue vertido l\xE1 na cruz;
N\xF3s, que somos salvos, vamos sem temer,
Sempre por Seu sangue o mal vencer.
`,verseorder:2,ismainverse:1,id_hino:231},{idverse:25840,verse:`2. 
Pela morte de Jesus,
Recebemos vida e luz;
Do abismo de horror,
N\xE3o temos mais nenhum temor.
`,verseorder:3,ismainverse:0,id_hino:231},{idverse:25841,verse:`3. 
Seguiremos o Senhor,
Pois nos guia ao Criador;
Nos concede paz real,
Nos d\xE1 repouso eternal!
`,verseorder:4,ismainverse:0,id_hino:231},{idverse:25842,verse:`4. 
Tu que n\xE3o tens salva\xE7\xE3o,
Abre j\xE1 teu cora\xE7\xE3o.
Deixa Cristo te salvar,
E para o c\xE9u te preparar.`,verseorder:5,ismainverse:0,id_hino:231},{idverse:25843,verse:`1. 
Bem-aventurado s\xE3o
Os de limpo cora\xE7\xE3o,
Que n\xE3o buscam as riquezas para si;
A tranq\xFCilidade e paz,
Que desfrutam, oh, jamais,
Poder\xE3o discriminadas ser por mim!
`,verseorder:1,ismainverse:0,id_hino:232},{idverse:25844,verse:`Oh, cantemos aleluia!
Jubilosos, com poder!
Bem alegres, com fervor,
Demos a Jesus louvor,
E mais gozo vamos dEle receber!
`,verseorder:2,ismainverse:1,id_hino:232},{idverse:25845,verse:`2. 
Grande dita e mui favor
Me concede meu Senhor,
Pelo sangue que por mim verteu na cruz;
Tornar\xE1 temb\xE9m meu ser,
Cheio de veraz prazer,
Se viver e caminhar na Sua luz!
`,verseorder:3,ismainverse:0,id_hino:232},{idverse:25846,verse:`3. 
Ao Senhor obedecer,
DEle mesmo me encher,
Para mim isto \xE9 verdadeiro c\xE9u;
E por Seu imenso amor,
Para o pobre pecador,
Quero aqui, louv\xE1-Lo, e al\xE9m do v\xE9u!
`,verseorder:4,ismainverse:0,id_hino:232},{idverse:25847,verse:`4. 
Qu\xE3o perfeita, \xE9  minha paz!
N\xE3o anelo nada mais,
Quero sempre aqui fazer o Seu querer;
Tenho o amado Salvador,
E possuo Seu amor;
Gozarei eternamente mui prazer!`,verseorder:5,ismainverse:0,id_hino:232},{idverse:25848,verse:`1. 
\xD3 Jesus cristo, aqui estamos
Unidos todos em um lugar,
P\`ra Tua morte n\xF3s relembrarmos,
Que Tu quiseste por n\xF3s provar.
`,verseorder:1,ismainverse:0,id_hino:233},{idverse:25849,verse:`2. 
Alegra-nos, \xF3 Jesus amado,
Enchendo-nos do Consolador;
\xD3 grande gra\xE7a Tu nos tens dado,
Pois somos salvos p\`lo Teu amor.
`,verseorder:2,ismainverse:0,id_hino:233},{idverse:25850,verse:`3. 
Que n\xF3s sejamos aben\xE7oados;
Tamb\xE9m o p\xE3o e o vinho aqui,
P\`lo Teu poder sejam consagrados
P\`ra nos alimentarmos de Ti.
`,verseorder:3,ismainverse:0,id_hino:233},{idverse:25851,verse:`4. 
E quando todos nos reunirmos,
Um dia, l\xE1 no Teu lar, nos c\xE9us,
N\xF3s aleluias, sim, cantaremos,
Eterno hino ao nosso Deus.
`,verseorder:4,ismainverse:0,id_hino:233},{idverse:25852,verse:"5. ",verseorder:5,ismainverse:0,id_hino:233},{idverse:25853,verse:`1. 
O gozo dos santos \xE9 o amor de Jesus,
Grande \xE9 este amor que paz nos produz!
A fonte da vida, que nos d\xE1 vigor,
\xC9 o sangue de Cristo vertido em dor!
`,verseorder:1,ismainverse:0,id_hino:234},{idverse:25854,verse:`Oh! v\xEA ali na cruz a gra\xE7a
de Jesus,
Amor potente, que gozo produz!
`,verseorder:2,ismainverse:1,id_hino:234},{idverse:25855,verse:`2. 
\xD3 v\xEA, "eis o Homem" que padece ali,
Coberto de sangue, ferido por ti.
Estampa na face t\xE3o enorme dor;
De \`spinhos coroa possui meu  senhor!
`,verseorder:3,ismainverse:0,id_hino:234},{idverse:25856,verse:`3. 
Com cravos, furados tem os p\xE9s e as m\xE3os;
Pregado no lenho, em grande afli\xE7\xE3o;
Aberto Seu lado co\`a lan\xE7a cruel
Sai \xE1gua e sangue do Emanuel!
`,verseorder:4,ismainverse:0,id_hino:234},{idverse:25857,verse:`4. 
Por Cristo cumprida foi a obra de amor,
Morreu qual cordeiro, por ti pecador;
Confiando agora na obra da cruz,
A vida n\xF3s temos por Cristo Jesus.
`,verseorder:5,ismainverse:0,id_hino:234},{idverse:25858,verse:"5. ",verseorder:6,ismainverse:0,id_hino:234},{idverse:25859,verse:`1. 
Comprado com sangue de Cristo,
Alegre ao c\xE9u, sim eu vou;
Liberto do v\xEDcio maldito,
J\xE1 sei que de Deus, filho sou.
`,verseorder:1,ismainverse:0,id_hino:235},{idverse:25860,verse:`J\xE1 sei, j\xE1 sei,
Comprado com sangue eu sou;
J\xE1 sei, j\xE1 sei,
Com Cristo ao c\xE9u, sim eu vou.
`,verseorder:2,ismainverse:1,id_hino:235},{idverse:25861,verse:`2. 
Estou livre da lei severa,
Pois Cristo me quis redimir,
Enchendo minh\`alma, deveras,
Do gozo, que vem do porvir.
`,verseorder:3,ismainverse:0,id_hino:235},{idverse:25862,verse:`3. 
Em Cristo Jesus sempre espero,
E nunca O posso deixar;
Calar Seus favores n\xE3o quero,
Vou sempre de Cristo falar!
`,verseorder:4,ismainverse:0,id_hino:235},{idverse:25863,verse:`4. 
Eu sei que, um dia, a beleza
Do grande Rei vou contemplar;
Agora me d\xE1 fortaleza,
E sempre me quer consolar!
`,verseorder:5,ismainverse:0,id_hino:235},{idverse:25864,verse:`5. 
Eu sei que me espera a coroa,
A qual, a Seus servos dar\xE1,
Jesus, a divina pessoa,
No c\xE9u onde Ele est\xE1!
`,verseorder:6,ismainverse:0,id_hino:235},{idverse:25865,verse:"6. ",verseorder:7,ismainverse:0,id_hino:235},{idverse:25866,verse:`1. 
Sobre a cruz o bom Cordeiro,
Dura morte j\xE1 provou,
Por n\xF3s todos no madeiro,
Para Deus Pai nos comprou;
Com Seu sangue tem lavado
Nossa alma imortal,
E tamb\xE9m nos resgatado
Do poder do mal.
`,verseorder:1,ismainverse:0,id_hino:236},{idverse:25867,verse:`J\xE1 nos lavou, j\xE1 nos lavou,
Com Seu precioso sangue, Jesus, Salvador.
Nos libertou, nos libertou,
Com Seu precioso sangue do vil tentador.
`,verseorder:2,ismainverse:1,id_hino:236},{idverse:25868,verse:`2. 
Pelo sangue derramado
Livre entrada se nos d\xE1
No lugar santificado
Onde o Pai celeste est\xE1;
Este sangue nos indica
O infinito, imenso amor
De Jesus, pois purifica
Todo pecador!
`,verseorder:3,ismainverse:0,id_hino:236},{idverse:25869,verse:`3. 
Fomos n\xF3s predestinaos
Para crermos em Jesus,
Com Esp\xEDrito selados,
Para desfrutar na luz;
Se v\xF3s hoje mesmo crerdes,
Por Deus, feitos v\xF3s sereis,
Para dons Lhe oferecerdes,
Sacerdotes, reis.`,verseorder:4,ismainverse:0,id_hino:236},{idverse:25870,verse:`1. 
O Noivo estamos prontos p\`ra encontrar,
Eis que do c\xE9u vir\xE1.
Com nossas luzes vamos esperar,
Jesus n\xE3o tardar\xE1!
`,verseorder:1,ismainverse:0,id_hino:237},{idverse:25871,verse:`Oh! que gozo preparado est\xE1,
Para os servos do Senhor Jesus;
Breve Cristo do c\xE9u voltar\xE1
Em gloriosa luz!
`,verseorder:2,ismainverse:1,id_hino:237},{idverse:25872,verse:`2. 
Coroa n\xF3s teremos l\xE1 no c\xE9u,
Se Cristo em n\xF3s viver;
Em breve Ele vem rompendo o v\xE9u,
O mal vem desfazer!
`,verseorder:3,ismainverse:0,id_hino:237},{idverse:25873,verse:`3. 
L\xE1 em Si\xE3o n\xF3s vamos encontrar
Com nosso Redentor;
Na gl\xF3ria eterna vamos desfrutar
Do divinal amor!`,verseorder:4,ismainverse:0,id_hino:237},{idverse:25874,verse:`1. 
\xD3 pecador desalentado
Vem a Jesus, teu Salvador;
Por que viver atribulado?
Vem a Jesus, teu Salvador;
Eis o Cristo te chamando,
Pois quer te dar Seu grande amor,
Vem nos Seus bra\xE7os te lan\xE7ando
Vem a Jesus, o teu Salvador.
`,verseorder:1,ismainverse:0,id_hino:238},{idverse:25875,verse:`2. 
\xD3 n\xE3o rejeites a liberdade,
Vem a Jesus, teu Salvador;
Recebe hoje a santidade;
Vem a Jesus, teu Salvador;
Recebe hoje a santidade;
Vem a Jesus, teu Salvador;
Tudo aqui no mundo passa;
Tu morrer\xE1s, \xF3 pecador,
Vem aceitar a vera gra\xE7a,
Vem  a Jesus, o teu Salvador.
`,verseorder:2,ismainverse:0,id_hino:238},{idverse:25876,verse:`3. 
Se queres paz, tamb\xE9m o gozo,
Vem a Jesus, teu Salvador.
Pois nEle tu ter\xE1s repouso,
Vem a Jesus, teu Salvador;
N\xE3o ter\xE1s sen\xE3o tristeza,
C\xE1 neste mundo de horror,
Mas em Jesus h\xE1 fortaleza,
Vem a Jesus, o teu Salvador.
`,verseorder:3,ismainverse:0,id_hino:238},{idverse:25877,verse:`4. 
Hoje \xE9 o dia apraz\xEDvel,
Vem a Jesus, teu Salvador.
\xD3 Seu amor \xE9 irresist\xEDvel,
Vem a Jesus, teu Salvador.
Tuas horas v\xE3o passando,
\xD3 n\xE3o desprezes, pecador;
A voz de Deus te convidando,
Vem a Jesus, o teu Salvador.`,verseorder:4,ismainverse:0,id_hino:238},{idverse:25878,verse:`1. 
Vem \xF3 Senhor, e completa
A obra Tua em mim;
Torna a minha alma dileta,
Enchendo-me de Ti.
`,verseorder:1,ismainverse:0,id_hino:239},{idverse:25879,verse:`\xD3 manda agora,
O bom Consolador;
Minh\`alma Te implora,
O meu amado Senhor!
`,verseorder:2,ismainverse:1,id_hino:239},{idverse:25880,verse:`2. 
H\xE1 tanto tempo Te espero,
\xD3 caro Salvador,
Mas hoje mesmo eu quero
O bom Consolador.
`,verseorder:3,ismainverse:0,id_hino:239},{idverse:25881,verse:`3. 
Tens me tirado p\`ra fora
Do mundo sedutor;
\xD3 vem selar-me agora
Glorioso Redentor.
`,verseorder:4,ismainverse:0,id_hino:239},{idverse:25882,verse:`4. 
A Ti pertence a gl\xF3ria,
Benigno Salvador;
Sempre me d\xE1s a vit\xF3ria,
Por Teu imenso amor.`,verseorder:5,ismainverse:0,id_hino:239},{idverse:25883,verse:`1. 
Cristo pode hoje vir,
Oh dia alegre! Irei amigos ver;
O mal ter\xE1 o seu fim,
Se Cristo do c\xE9u descer!
`,verseorder:1,ismainverse:0,id_hino:240},{idverse:25884,verse:`Oh dia alegre, da vinda de Jesus!
Jesus vem buscar-me para os c\xE9us;
Ele vem breve para os Seus;
Oh dia alegre! Da vinda de Jesus!
`,verseorder:2,ismainverse:1,id_hino:240},{idverse:25885,verse:`2. 
Se hoje for p\`ra meu lar,
Oh dia alegre! Parece-me ouvir
Os salvos cantando: "Vem!"
Quem dera p\`ra o c\xE9u partir!
`,verseorder:3,ismainverse:0,id_hino:240},{idverse:25886,verse:`3. 
Por que ansioso estar?
Oh dia alegre! H\xE1 luz na margem al\xE9m;
Temporal nunca haver\xE1;
Do c\xE9u breve Cristo vem!
`,verseorder:4,ismainverse:0,id_hino:240},{idverse:25887,verse:`4. 
Fiel a Jesus estou,
Oh dia alegre! Livre estou p\`ra dizer:
"Cristo \xE9 tudo p\`ra mim";
Do c\xE9u vir\xE1 com poder!`,verseorder:5,ismainverse:0,id_hino:240},{idverse:25888,verse:`1. 
P\`ra Si\xE3o marchamos sem algum temor,
Por f\xE9 nos chamamos filhos do Senhor;
Presto caminhamos, sem p\`ra tr\xE1s olhar,
Com Jesus n\xF3s vamos, n\xE3o podemos errar.
`,verseorder:1,ismainverse:0,id_hino:241},{idverse:25889,verse:`Marchemos sem temor,
repletos de valor,
Marchemos nas pisadas do bom Redentor;
Depressa, sem tardar, at\xE9 no c\xE9u entrar;
Por Jesus podemos sempre triunfar.
`,verseorder:2,ismainverse:1,id_hino:241},{idverse:25890,verse:`2. 
P\`ra Si\xE3o marchamos sem desanimar,
A verdade, vamos todos procalamar;
Presto caminhamos, carregando a cruz,
Pois n\xF3s desejamos ver no c\xE9u a Jesus.
`,verseorder:3,ismainverse:0,id_hino:241},{idverse:25891,verse:`3. 
P\`ra Si\xE3o marchamos em celeste luz,
Dirigidos vamos p\`lo Senhor Jesus;
Presto caminhamos, rasga-se o v\xE9u;
J\xE1 daqui estamos, pela f\xE9, vendo o c\xE9u.`,verseorder:4,ismainverse:0,id_hino:241},{idverse:25892,verse:`1. 
Eu confio firmemente,
Que no c\xE9u vou descansar
Com Jesus alegremente, aleluia!
Que prazer celeste sente
A minh\`alma em pensar,
Na gl\xF3ria que no c\xE9u eu vou gozar!
`,verseorder:1,ismainverse:0,id_hino:242},{idverse:25893,verse:`Que eterna gl\xF3ria vou no c\xE9u
gozar!
Com Jesus, que do pecado me salvou;
Santa paz e alegria l\xE1 no c\xE9u vou desfrutar;
Eterna gl\xF3ria Cristo me comprou!
`,verseorder:2,ismainverse:1,id_hino:242},{idverse:25894,verse:`2. 
No c\xE9u vou ver o Cordeiro,
Que por mim quis expirar,
Pendurado no madeiro, aleluia!
Que me fez tamb\xE9m herdeiro
Do que nunca vai murchar,
Da gl\xF3ria que no c\xE9u eu vou gozar!
`,verseorder:3,ismainverse:0,id_hino:242},{idverse:25895,verse:`3. 
De Jesus, o rosto santo,
No c\xE9u hei de contemplar,
Onde n\xE3o h\xE1 dor ou pranto, aleluia!
Mas sim um sublime canto
Que p\`ra sempre vai soar
Na gl\xF3ria, que no c\xE9u eu vou gozar!
`,verseorder:4,ismainverse:0,id_hino:242},{idverse:25896,verse:`4. 
Quando for ao c\xE9u chagado
E a gl\xF3ria alcan\xE7ar
Por te Cristo ao meu lado, aleluia!
No Seu trono assentado,
A Jesus vou adorar;
Eterna gl\xF3ria vou no c\xE9u gozar!`,verseorder:5,ismainverse:0,id_hino:242},{idverse:25897,verse:`1. 
N\xF3s abrimos este culto
Em Teu nome, \xF3 Jesus Cristo!
Ao pequeno e ao adulto,
Luz divina vem dar por isto;

Gozaremos em Tua face,
\xD3 Cordeiro ressuscitado!
Com do\xE7ura, sim, nos enlaces,
P\`ra ouvir o que nos for dado.
`,verseorder:1,ismainverse:0,id_hino:243},{idverse:25898,verse:`2. 
\xD3 nos manda Tua palavra
Pelo Teu espir\xEDto Santo,
Que no peito um fogo lavra,
Que enxuga tamb\xE9m o pranto;

Nosso pai, n\xF3s Te suplicamos
Nova vida p\`ra Tua igreja;
\xD3 n\xE3o tardes, pois desejamos
Que pureza em n\xF3s Tu vejas.
`,verseorder:2,ismainverse:0,id_hino:243},{idverse:25899,verse:`3. 
Aben\xE7oa, \xF3 Deus Santo,
Os Teus servos em todo mundo;
Aben\xE7oa o nosso canto
E d\xE1 vida  aos moribundos;

Aben\xE7oa aos cordeirinhos,
A fam\xEDlia dos Teus amados,
Como ave que no seu ninho,
Tem seus filhos bem abrigados.
`,verseorder:3,ismainverse:0,id_hino:243},{idverse:25900,verse:"4. ",verseorder:4,ismainverse:0,id_hino:243},{idverse:25901,verse:`1. 
Louvai a Jesus, amoroso;
Na cruz os pecados levou,
Que nos enche de santo gozo;
Com sangue o c\xE9u nos ganhou.
`,verseorder:1,ismainverse:0,id_hino:244},{idverse:25902,verse:`Ao meu Senhor,
Que tenho no meu cora\xE7\xE3o,
Eu dou louvor,
Pois Ele me deu salva\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:244},{idverse:25903,verse:`2. 
Louvai a Jesus poderoso,
Que veio livrar da pris\xE3o
Um povo cativo, medroso,
Que n\xE3o tinha consola\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:244},{idverse:25904,verse:`3. 
Louvai a Jesus, vosso guia
No mundo de tribula\xE7\xE3o;
E n\xE3o tem termor, quem confia
Em Deus, mas real prote\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:244},{idverse:25905,verse:`4. 
Louvai a Jesus, dai-Lhe gl\xF3ria,
Pois Rei e Senhor ele \xE9;
Na cruz nos ganhou a vit\xF3ria;
Vencei com Jesus pela f\xE9.`,verseorder:5,ismainverse:0,id_hino:244},{idverse:25906,verse:`1. 
A paz e gozo Jesus j\xE1 me deu,
Ele p\`ra isso ao  mundo baixou;
Para salvar-me, Seu sangue verteu,
Quando por mim na cruz expirou.
`,verseorder:1,ismainverse:0,id_hino:245},{idverse:25907,verse:`Paz de Deus em Jesus encontrei,
Pois tenho gozo no cora\xE7\xE3o;
Honra e gl\xF3ria tributarei
Ao que me trouxe a salva\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:245},{idverse:25908,verse:`2. 
A paz e gozo desfruto enfim,
Por Jesus Cristo, o meu Redentor;
Um amor santo, oh, tenho em mim,
Que alcancei do Consolador!
`,verseorder:3,ismainverse:0,id_hino:245},{idverse:25909,verse:`3. 
A paz e gozo tem meu cora\xE7\xE3o,
Vida celeste, repleta de maor;
Cristo me deu a real salva\xE7\xE3o,
Tamb\xE9m o dom do Consolador.`,verseorder:4,ismainverse:0,id_hino:245},{idverse:25910,verse:`1. 
Tens descanso encontrado em Jesus Cristo?
Permaneces pela gra\xE7a que Ele deu?
Tens tu paz com teu Salvador bendito?
Plenitude do Esp\xEDrito de Deus?
`,verseorder:1,ismainverse:0,id_hino:246},{idverse:25911,verse:`Tens descanso encontrado em Jesus
Cristo?
Permaneces pela gra\xE7a que Ele deu?
Plena paz, consola\xE7\xE3o, achar\xE1s na ora\xE7\xE3o:
Deus a\xED vit\xF3ria aos santos concedeu.
`,verseorder:2,ismainverse:1,id_hino:246},{idverse:25912,verse:`2. 
Estar\xE1s bem seguro e corajoso,
Quando a tenta\xE7\xE3o chegar-se para ti?
Tens a gra\xE7a p\`ra ser vitorioso
Nas terr\xEDveis prova\xE7\xF5es da vida aqui.
`,verseorder:3,ismainverse:0,id_hino:246},{idverse:25913,verse:`3. 
S\xF3 em Cristo encontrar\xE1s descanso,
Que ningu\xE9m aqui, jamais ir\xE1 tirar;
Com Jesus tu h\xE1s de aprender ser manso,
E Sua gl\xF3ria em ti o mundo h\xE1 de mirar.`,verseorder:4,ismainverse:0,id_hino:246},{idverse:25914,verse:`1. 
Deus nos guarde bem no  Seu amor;
Onde quer que nos achemos,
O Seu nome louvaremos;
Deus nos guarde bem no Seu amor!
`,verseorder:1,ismainverse:0,id_hino:247},{idverse:25915,verse:`Ao voltar Jesus vamos n\xF3s estar
Livres de qualquer separa\xE7\xE3o;
U\`a coroa, ent\xE3o vamos, sim, formar,
L\xE1 no c\xE9u, na eternal mans\xE3o.
`,verseorder:2,ismainverse:1,id_hino:247},{idverse:25916,verse:`2. 
Deus nos guarde em santifica\xE7\xE3o
Nas agruras da jornada,
Que \xE9 mui breve terminada!
Deus nos guarde em santifica\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:247},{idverse:25917,verse:`3. 
Deus nos guarde e nos conceda paz,
De que tanto carecemos;
\xD3, enquanto aqui vivemos,
Deus nos guarde e nos conceda paz.`,verseorder:4,ismainverse:0,id_hino:247},{idverse:25918,verse:`1. 
J\xE1 perto de Betfag\xE9, o Mestre ordenou:
"\xD3 ide \xE0 aldeia, que avistando estou,
Traze-Me um jumentinho, no qual ningu\xE9m montou
Dizei aos que perguntem: "Jesus requisitou".
`,verseorder:1,ismainverse:0,id_hino:248},{idverse:25919,verse:`Hosana e gl\xF3ria
davam ao Senhor Jesus,
O Rei que vem trazendo amor, poder e luz;
Tamb\xE9m vem concedendo eficaz perd\xE3o;
Por isso O louvam de todo o cora\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:248},{idverse:25920,verse:`2. 
Trouxeram o jumentinho, no qual Jesus montou,
Com ramos o caminho o povo enfeitou;
A que de Si\xE3o \xE9 fillha, \xF3 logo lhe dizei:
N\xE3o temas mas exulta, a\xED vem o Rei".
`,verseorder:3,ismainverse:0,id_hino:248},{idverse:25921,verse:`3. 
No monte das Oliveiras, ouviu-se um clamor:
"Bendito o Rei que \xE9 vindo em nome do Snehor";
Jesus diz aos que o mandam calar a multid\xE3o:
"Se estes se calarem, as pedras clamar\xE3o".`,verseorder:4,ismainverse:0,id_hino:248},{idverse:25922,verse:`1. 
\xD3 vem, Senhor, e habita no meu cora\xE7\xE3o;
D\xE1-me a gra\xE7a bendita por Tua compaix\xE3o.
`,verseorder:1,ismainverse:0,id_hino:249},{idverse:25923,verse:`\xD3 meu Deus vem dar-me
O dom do Consolador;
Vem hoje selar-me,
Enchendo-me do Teu amor!
`,verseorder:2,ismainverse:1,id_hino:249},{idverse:25924,verse:`2. 
Dia de paz e de gozo eu vou desfrutar,
Quando Jesus glorioso minh\`alma batizar.
`,verseorder:3,ismainverse:0,id_hino:249},{idverse:25925,verse:`3. 
Do Teu amor puro e santo sempre falarei;
Mesmo com dor e com pranto a Cristo louvarei.
`,verseorder:4,ismainverse:0,id_hino:249},{idverse:25926,verse:`4. 
\xD3 Deus, Te pe\xE7o agora, ouve-me Senhor:
Batiza-me nesta hora, no Teu Consolador!`,verseorder:5,ismainverse:0,id_hino:249},{idverse:25927,verse:`1. 
A Cristo unida est\xE1 a minh\`alma,
Pois nEle eu tenho meu vero sustento;
Me enche de gozo do c\xE9u, e de calma;
Tamb\xE9m me ensina os seus mandamentos.
`,verseorder:1,ismainverse:0,id_hino:250},{idverse:25928,verse:`\xD3 Noiva de Jesus,
apronta-te para ir
Para o eterno e santo reinado;
Que divianal amor ir\xE1s l\xE1, ent\xE3o, fruir
Com Jesus Cristo, o teu amado!
`,verseorder:2,ismainverse:1,id_hino:250},{idverse:25929,verse:`2. 
Na santa Igreja, com sangue remida,
J\xE1 \xE9 manifesta em mim Sua gl\xF3ria,
Que por Sua gra\xE7a me foi concedida,
Na qual abrigado, terei a vit\xF3ria.
`,verseorder:3,ismainverse:0,id_hino:250},{idverse:25930,verse:`3. 
Assim eu desejo a Cristo unido,
Seguir o caminho p\`ra bela cidade,
Pois nEle, dos males, sou bem protegido,
Tamb\xE9m rodeado de vera bondade.`,verseorder:4,ismainverse:0,id_hino:250},{idverse:25931,verse:`1. 
Ao outro culto; n\xE3o faltar,
\xD3 vinde todos p\`ra louvar
O nome santo do Senhor,
"Que nos d\xE1 vida e fervor.
`,verseorder:1,ismainverse:0,id_hino:251},{idverse:25932,verse:`2. 
Nos separarmos em amor,
Levando a paz do Salvador,
Mas breve vinde, n\xE3o falteis,
Os pecadores sim, trazei.
`,verseorder:2,ismainverse:0,id_hino:251},{idverse:25933,verse:`3. 
Se n\xE3o pudermos reunir,
Veremos santos no porvir,
No culto do Senhor Jesus,
Que vai haver no c\xE9u de luz.`,verseorder:3,ismainverse:0,id_hino:251},{idverse:25934,verse:`1. 
Santo, santo \xE9s Tu, Senhor,
Em Teu louvor eu vou cantar
Com esperan\xE7a, \xF3 Deus de amor;
Tu me queres consolar;
Eu Te pe\xE7o, \xF3 meu Jesus,
S\xF3 Tu \xE9s meu Salvador,
Que me fa\xE7as andar na luz;
Santo \xE9s, meu Redentor!
`,verseorder:1,ismainverse:0,id_hino:252},{idverse:25935,verse:`Ao meu Jesus eu cantarei
Entrando na feliz Si\xE3o;
Oh! Santo, santo, proclamarei
Na minha eternal mans\xE3o,
Com os anjos e serafins,
Santo! Sempre vou cantar
Com arcanjos e querubins;
A Jesus eu vou adorar.
`,verseorder:2,ismainverse:1,id_hino:252},{idverse:25936,verse:`2. 
Santo, santo, eu cantarei,
Ao meu Jesus, que me salvou,
E s\xF3 nEle caonfiarei,
Pois a minh\`alma resgatou;
D\xE1-me o p\xE3o do c\xE9u p\`ra comer.
O man\xE1 celestial,
P\`ra Teu povo fortalecer,
Com Teu poder vital.
`,verseorder:3,ismainverse:0,id_hino:252},{idverse:25937,verse:`3. 
Em Jesus, s\xF3 hei de buscar,
A Sua for\xE7a divinal,
Que avante me vai levar,
Para o reino eternal;
Oh! qu\xE3o doce \xE9 Teu p\xE3o,
Que nos d\xE1 real vigor
No deserto; consola\xE7\xE3o,
Que nos enche de fervor.`,verseorder:4,ismainverse:0,id_hino:252},{idverse:25938,verse:`1. 
Avante, \xF3 Crentes, o mal desfazendo,
O inimigo com a luz combateremos;
\xC0 peleja pois, todos, n\xE3o temendo,
A vit\xF3ria por Jesus receberemos.
`,verseorder:1,ismainverse:0,id_hino:253},{idverse:25939,verse:`Com a gra\xE7a de Jesus nos
firmaremos,
Com a gra\xE7a de Jesus, combateremos,
Com a gra\xE7a de Jesus n\xF3s venceremos,
Com a gra\xE7a de Jesus vit\xF3ria temos.
`,verseorder:2,ismainverse:1,id_hino:253},{idverse:25940,verse:`2. 
Avante, \xF3 crentes, marchai sem tardan\xE7a,
Jamais do bom Jesus n\xF3s nos afastaremos,
Porque nEle n\xF3s temos esperan\xE7a
E vit\xF3ria n\xF3s assim alcan\xE7aremos.
`,verseorder:3,ismainverse:0,id_hino:253},{idverse:25941,verse:`3. 
Avante, \xF3 crentes, fi\xE9is corajosos,
Vit\xF3ria por Jesus, a n\xF3s \xE9 concedida;
No combate, \xF3 sede valorosos,
Para alcan\xE7ar o galard\xE3o da vida.
`,verseorder:4,ismainverse:0,id_hino:253},{idverse:25942,verse:`4. 
Avante, \xF3 crentes, dum s\xF3 pensamento;
"O vil pecado sempre n\xF3s combateremos".
Pois Jesus vem, n\xE3o tarda um momento,
Crendo em Jesus, vit\xF3ria n\xF3s j\xE1 temos.`,verseorder:5,ismainverse:0,id_hino:253},{idverse:25943,verse:`1. 
Mais profundo no amor de Cristo
Eu desejo estar!
Mais humilde no meu ser, eu quero
Sempre aqui andar.
`,verseorder:1,ismainverse:0,id_hino:254},{idverse:25944,verse:`Mais perto de Jesus,
Eu quero aqui viver;
Mais santo, meu Jesus,
Eu desejo sempre ser.
`,verseorder:2,ismainverse:1,id_hino:254},{idverse:25945,verse:`2. 
Mais profunda a Tua vontade,
Quero conhecer;
T\xE9 que minha vida, por  inteiro,
Mostre o Teu ser.
`,verseorder:3,ismainverse:0,id_hino:254},{idverse:25946,verse:`3. 
Mais profundo, com dor, n\xE3o importa,
Pois eu quero estar,
No Teu divinal amor firmado,
Para fruto dar.
`,verseorder:4,ismainverse:0,id_hino:254},{idverse:25947,verse:`4. 
Mais profundo, dia ap\xF3s dia,
Quero eu estar,
T\xE9 que o alvo, o Mestre, aqui, me fa\xE7a,
Breve alcan\xE7ar..`,verseorder:5,ismainverse:0,id_hino:254},{idverse:25948,verse:`1. 
Na cruz morrendo meu Redentor;
Minhas maldades todas levou;
Se O recebes, tens Seu amor,
Pois teus pecados, Jesus perdoou.
`,verseorder:1,ismainverse:0,id_hino:255},{idverse:25949,verse:`Quando Deus, o sangue vir,
Que Jesus j\xE1 verteu,
Passar\xE1 sem ter ferir,
No Egito assim sucedeu.
`,verseorder:2,ismainverse:1,id_hino:255},{idverse:25950,verse:`2. 
Deus quer salvar ao vil malfeitor,
Como promete, sempre far\xE1;
NEle confia, \xF3 pecador,
E pela f\xE9 vida nova ter\xE1s.
`,verseorder:3,ismainverse:0,id_hino:255},{idverse:25951,verse:`3. 
Breve se finda a tua luz,
E no ju\xEDzo tu vais entrar;
N\xE3o te detenhas, vem a Jesus,
Que teus pecados deseja apagar.
`,verseorder:4,ismainverse:0,id_hino:255},{idverse:25952,verse:`4. 
Que maravilha, que grande amor!
Se hoje creres, salvo ser\xE1s!
Cristo te chama, vem pecador,
E gozo eterno no c\xE9u fruir\xE1s.`,verseorder:5,ismainverse:0,id_hino:255},{idverse:25953,verse:`1. 
Abandona este mar,
Onde podes naufragar;
Abandona este mundo de horror,
A mar\xE9 te levar\xE1,
O teu barco quebrar\xE1,
\xD3 aceita a Jesus, o Salvador!
`,verseorder:1,ismainverse:0,id_hino:256},{idverse:25954,verse:`Abandona j\xE1 duma vez,
Este proceloso mar.
Hoje sem tardar;
Do castigo eternal,
E de morte t\xE3o fatal,
Foge logo pecador com rapidez.
`,verseorder:2,ismainverse:1,id_hino:256},{idverse:25955,verse:`2. 
Eis far\xF3is a clarear
E perigos a marcar;
Abandona este mundo de horror,
J\xE1 na barra mais al\xE9m,
O teu barco se sust\xE9m,
\xD3 aceita a Jesus, o Salvador!
`,verseorder:3,ismainverse:0,id_hino:256},{idverse:25956,verse:`3. 
Eis que vozes sem cessar,
N\xE3o se cansam de clamar:
Abandona este mundo de horror,
\xD3 amigo, ouve j\xE1,
Essa voz te salvar\xE1.
\xD3 aceita a Jesus, o Salvdor!
`,verseorder:4,ismainverse:0,id_hino:256},{idverse:25957,verse:`4. 
N\xE3o escutas a Jesus,
Que te chama para aluz?
Abandona este mundo de horror,
Se persistes em vagar,
N\xE3o te poder\xE1s salvar.
\xD3 aceita a Jesus, o Salvador!`,verseorder:5,ismainverse:0,id_hino:256},{idverse:25958,verse:`1. 
Amoroso Salvador,
Sem igual \xE9 Teu perd\xE3o;
S\xF3 Tu \xE9s meu Redentor
E perfeita salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:257},{idverse:25959,verse:`2. 
Meu contrito cora\xE7\xE3o
Te confessa seu pecar,
Pede ao Pai real perd\xE3o,
Pelo Teu amor sem par.
`,verseorder:2,ismainverse:0,id_hino:257},{idverse:25960,verse:`3. 
Te contemplo, sem cessar,
No Teu trono desde aqui;
Oh! qu\xE3o grato \xE9 meditar,
Que intercedes Tu por mim.
`,verseorder:3,ismainverse:0,id_hino:257},{idverse:25961,verse:`4. 
A fonte \xE9s da compaix\xE3o,
Sempre a Ti darei louvor!
Faz-me bem ao cora\xE7\xE3o,
Exaltar-Te, meu Senhor!`,verseorder:4,ismainverse:0,id_hino:257},{idverse:25962,verse:`1. 
Um Salvador meigo \xE9 Cristo Jesus,
Pois Ele minh\`alma salvou;
Tamb\xE9m meus pecados tomoui sobre Si,
Na cruz quando me resgatou.
`,verseorder:1,ismainverse:0,id_hino:258},{idverse:25963,verse:`Na rocha eterna firmado estou eu,
E tenho a salva\xE7\xE3o;
Me esconde Jesus Cristo no manto Seu,
Velando-me com Sua m\xE3o.
Velando-me com Sua m\xE3o.
`,verseorder:2,ismainverse:1,id_hino:258},{idverse:25964,verse:`2. 
As ben\xE7\xE3os do c\xE9u desfrutando eu estou,
Em Cristo, o Filho de Deus;
Alegre eu vivo e ando na luz,
Prossigo na senda do c\xE9u.
`,verseorder:3,ismainverse:0,id_hino:258},{idverse:25965,verse:`3. 
As cargas que eu tive Jesus as tomou,
Agora liberto estou;
A gra\xE7a me basta que Cristo me deu,
Assim para o c\xE9u presto eu vou.
`,verseorder:4,ismainverse:0,id_hino:258},{idverse:25966,verse:`4. 
Findando a lida ent\xE3o O verei,
Com anjos e santos no c\xE9u;
E hinos de gl\xF3ria eu entoarei,
\xC0quele que por mim morreu.`,verseorder:5,ismainverse:0,id_hino:258},{idverse:25967,verse:`1. 
Creio eu na B\xEDblia, o livro do Senhor,
Pois de Jesus Cristo, mostra o doce amor;
Todos meus pecados apagados est\xE3o,
Paz e gozo tenho em meu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:259},{idverse:25968,verse:`Creio eu na B\xEDblia, livro de
meu Deus;
Para mim a B\xEDblia \xE9 o man\xE1 dos c\xE9us!
Mostra-me o caminho para o lar celestial;
Acho eu na B\xEDblia, gra\xE7a divinal!
`,verseorder:2,ismainverse:1,id_hino:259},{idverse:25969,verse:`2. 
Creio eu na B\xEDblia, ensina-me a cantar
Cantos de vit\xF3ria, e de amor sem par;
Lindas melodias, eu cantando vou,
Porque redimido pelo sangue estou!
`,verseorder:3,ismainverse:0,id_hino:259},{idverse:25970,verse:`3. 
S\xF3 a santa B\xEDblia a santidade d\xE1,
Este dom de Cristo, prometido j\xE1;
Todos os que buscam plena salva\xE7\xE3o,
Estas b\xEAn\xE7\xE3os, s\xF3 em Cristo, achar\xE3o.
`,verseorder:4,ismainverse:0,id_hino:259},{idverse:25971,verse:`4. 
Lemos hoje a B\xEDblia p\`ra todo o pecador,
Para que encontre Cristo, o Salvador;
Sendo sua vida, livre de pecar,
O divino livro, ele vai honrar!
`,verseorder:5,ismainverse:0,id_hino:259},{idverse:25972,verse:`5. 

`,verseorder:6,ismainverse:0,id_hino:259},{idverse:25973,verse:`6. 

`,verseorder:7,ismainverse:0,id_hino:259},{idverse:25974,verse:"7. ",verseorder:8,ismainverse:0,id_hino:259},{idverse:25975,verse:`1. 
Irm\xE3os avante, avante com nosso Capit\xE3o,
Seremos triunfantes, n\xE3o lutareis em v\xE3o!
Vit\xF3ria esperamos, vit\xF3ria Desu dar\xE1,
Se firmes pelejarmos, sim, contra Goli\xE1.
`,verseorder:1,ismainverse:0,id_hino:260},{idverse:25976,verse:`Avante, avante, as
trevas dissipai
Com o supremo Capit\xE3o, avante! vit\xF3ria alcan\xE7ai!
`,verseorder:2,ismainverse:1,id_hino:260},{idverse:25977,verse:`2. 
Lugares deleitosos, a sorte nos tocou;
De jugos t\xE3o penosos Jesus nos libertou;
Heran\xE7a foi-nos dada, heran\xE7a de valor;
No c\xE9u \xE9 conservada por Deus, meu Salvador.
`,verseorder:3,ismainverse:0,id_hino:260},{idverse:25978,verse:`3. 
Com nosso Comandante, avante, pelejai;
Alegres, exultantes, por Cristo avan\xE7ai;
J\xE1 temos a espada, n\xE3o temos que temer,
A B\xEDblia Sagrada p\`ra todo mal vencer.`,verseorder:4,ismainverse:0,id_hino:260},{idverse:25979,verse:`1. 
Alma triste, abatida,
Que tens medo de morrer;
Por um triz, a tua vida,
Pode hoje perecer;
V\xEA nos mares e nas dores,
Um farol jorrando luz,
Que te enche de louvores:
\xC9 Jesus, sim, \xE9 Jesus!
`,verseorder:1,ismainverse:0,id_hino:261},{idverse:25980,verse:`2. 
Tu que andas vacilante
E que o mundo suduziu,
Vem e segue, confiante,
Quem na cruz j\xE1 te remiu,
Pois a gra\xE7a n\xE3o \xE9 finda
De quem expirou na cruz;
Olha, com amor, ainda,
P\`ra Jesus, sim, p\`ra Jesus!
`,verseorder:2,ismainverse:0,id_hino:261},{idverse:25981,verse:`3. 
L\xE1 na cruz Jesus morrendo,
Teus pecados expiou;
Santo Esp\xEDrito concedendo,
A Sua Igreja adornou,
Todo o mal e sofrimento,
J\xE1 pregados est\xE3o na cruz,
Oh! sublime livramento,
De Jesus, sim, de Jesus!
`,verseorder:3,ismainverse:0,id_hino:261},{idverse:25982,verse:`4. 
A Teus p\xE9s, Jesus bondoso,
Acho paz, consola\xE7\xE3o!
Enches-me de santo gozo
E de imensa gratid\xE3o;
Quero eu constantemente
Receber a pura luz,
Que dimana fortemente
De Jesus, sim, de Jesus!`,verseorder:4,ismainverse:0,id_hino:261},{idverse:25983,verse:`1. 
Senhor, est\xE1s comigo em todo tempo,
Fazendo-me feliz no sofrimento,
\xD3 d\xE1-me cada dia o Teu sustento
E gra\xE7a p\`ra cumprir Teu mandamento.
`,verseorder:1,ismainverse:0,id_hino:262},{idverse:25984,verse:`Sim, est\xE1s comigo em todo
tempo,
Tu \xE9s o meu man\xE1, meu alimento;
Jesus, est\xE1s comigo em todo tempo,
O d\xE1-me Tua vida; Teu sustento.
`,verseorder:2,ismainverse:1,id_hino:262},{idverse:25985,verse:`2. 
Contigo, \xF3 Senhor, vou caminhando,
O Evangelho santo proclamando:
A face do Senhor Jesus busacando,
E sempre do pecado me guardando.
`,verseorder:3,ismainverse:0,id_hino:262},{idverse:25986,verse:`3. 
Por Deus estamos n\xF3s santificados
Em Ti, Jesus, que nos tens resgatado
Do mundo e de todo o vil pecado,
Tamb\xE9m o nosso ser vivificado.`,verseorder:4,ismainverse:0,id_hino:262},{idverse:25987,verse:`1. 
O culto tendo j\xE1 terminado,
Te suplicamos, \xF3 Salvador,
Se em qualquer coisa foi perturbado,
Que nos perdoes, por Teu amor.
`,verseorder:1,ismainverse:0,id_hino:263},{idverse:25988,verse:`2. 
Gravar Tu queiras a s\xE3 doutrina,
Por Teu Esp\xEDrito Consolador,
Que nos sustenta, que nos ensina,
E que nos enche do Teu amor.
`,verseorder:2,ismainverse:0,id_hino:263},{idverse:25989,verse:`3. 
Se n\xF3s aqui n\xE3o mais regressarmos,
Certeza temos de ver na luz
Os santos juntos e contemplarmos
No c\xE9u, um dia, o bom Jesus!`,verseorder:3,ismainverse:0,id_hino:263},{idverse:25990,verse:`1. 
\xD3 Pai celeste no nome santo
Do Teu amado Filho Jesus,
Grato Te seja o nosso canto,
Que reverente Tua grei produz;
\xD3 Deus, preside Teu povo unido,
Com Tua gra\xE7a e com Teu poder,
No Santo Esp\xEDrito j\xE1 recebido,
N\xF3s desejamos aqui viver.
`,verseorder:1,ismainverse:0,id_hino:264},{idverse:25991,verse:`2. 
Rocha divina, nossa firmeza,
D\xE1-nos a gra\xE7a, d\xE1-nos poder;
\xC9s dos Teus servos a fortaleza,
Onde, abrigados, n\xE3o t\xEAm pavor;
Chuvas de b\xEAn\xE7\xE3os sempre derrama
Em abund\xE2ncia dos altos c\xE9us;
A nossa alma, qual cervo brama
Em grande seca por Ti, bom Deus!
`,verseorder:2,ismainverse:0,id_hino:264},{idverse:25992,verse:`3. 
Rebanho santo, por Deus selado,
Duma s\xF3 mente, dum cora\xE7\xE3o,
E que assim seja realizado,
Ver, esperamos, com prontid\xE3o;
\xD3 nos defende como o Teu bra\xE7o,
Do inimigo, do tentador;
Sempre dirige os nossos passos,
\xC0s vivas fontes que d\xE3o vigor!
`,verseorder:3,ismainverse:0,id_hino:264},{idverse:25993,verse:`4. 
Manda, Senhor, a Tua mensagem
Pela divina e sacra un\xE7\xE3o,
Qual o man\xE1, que trouxe, em viagem,
Para Teu povo, a consola\xE7\xE3o;
Tua palavra, \xE0gua da vida,
Do nosso c\xE1lice, faz transbordar,
Cheia de gra\xE7a, bem dirigida,
Para os sedentos poder saciar.`,verseorder:4,ismainverse:0,id_hino:264},{idverse:25994,verse:`1. 
Oh! qu\xE3o doce \xE9 crer em Cristo,
E entregar-se a Jesus,
Esperar em Suas promessas
E andar na Sua luz!
`,verseorder:1,ismainverse:0,id_hino:265},{idverse:25995,verse:`Jesus Cristo, Jesus Cristo,
Teu amor me deste a mim!
Jesus Cristo, Jesus Cristo,
Sempre quero crer em Ti.
`,verseorder:2,ismainverse:1,id_hino:265},{idverse:25996,verse:`2. 
\xC9 mui doce crer em Cristo,
E cumprir o Seu querer,
N\xE3o descrendo em Sua palavra,
Que \xE9 luz p\`ra nosso ser.
`,verseorder:3,ismainverse:0,id_hino:265},{idverse:25997,verse:`3. 
\xC9 mui grato crer em Cristo;
Quando busca o cora\xE7\xE3o
Os tesouros infinitos
De amor, paz e perd\xE3o.
`,verseorder:4,ismainverse:0,id_hino:265},{idverse:25998,verse:`4. 
Sempre em Ti confiar eu quero,
Meu precioso Salvador;
Na bonan\xE7a e na luta,
Me proteja o Teu amor.`,verseorder:5,ismainverse:0,id_hino:265},{idverse:25999,verse:`1. 
N\xF3s resgatados fomos,
Por quem nos quis salvar;
Em Cristo livres somos,
P\`ra nunca mais pecar!
Em Cristo livres somos,
P\`ra nunca mais pecar!
`,verseorder:1,ismainverse:0,id_hino:266},{idverse:26e3,verse:`Resgatados fomos,
Resgatados fomos,
Resgatados fomos,
P\`ra nunca mais pecar!
`,verseorder:2,ismainverse:1,id_hino:266},{idverse:26001,verse:`2. 
N\xF3s fomos j\xE1 eleitos,
O c\xE9u, p\`ra desfrutar;
Tamb\xE9m por Deus aceitos
P\`ra nunca mais pecar!
Tamb\xE9m por Deus aceitos
P\`ra nunca mais pecar!
`,verseorder:3,ismainverse:0,id_hino:266},{idverse:26002,verse:`3. 
A imortalidade
Havemos de gozar,
Pois temos liberdade
P\`ra nunca mais pecar!
Pois temos liberdade
P\`ra nunca mais pecar!
`,verseorder:4,ismainverse:0,id_hino:266},{idverse:26003,verse:`4. 
No meio dos remidos
A Cristo vou louvar,
Por t\xEA-lo recebido
P\`ra nunca mais pecar!
Por t\xEA-lo recebido
P\`ra nunca mais pecar!`,verseorder:5,ismainverse:0,id_hino:266},{idverse:26004,verse:`1. 
Vai chegando mais perto e causando terror,
A predita enchente de pecados e dor;
Mas eu sei que potente \xE9 a m\xE3o do Senhor,
Por isso, tranquilo, n\xE3o tenho temor.
`,verseorder:1,ismainverse:0,id_hino:267},{idverse:26005,verse:`\xC9 Jesus minha for\xE7a, meu
amado Senhor,
Se Ele \xE9 o guarda, n\xE3o tenho temor;
Eu desejo encher-me do Seu grande amor,
Vivendo na gra\xE7a do meu Salvador.
`,verseorder:2,ismainverse:1,id_hino:267},{idverse:26006,verse:`2. 
Mais chegado a Cristo eu desejo ficar;
Mais alegre e contente quero sempre andar,
Nos Seus passos seguindo para nunca errar,
At\xE9 que um dia eu v\xE1 descansar.`,verseorder:3,ismainverse:0,id_hino:267},{idverse:26007,verse:`1. 
S\xEA tu, em Deus confiante;
Ora muito a Jesus,
E alegre, triufante,
Leva sempre tua cruz.
No Senhor tem esperan\xE7a;
Pois te guia Sua m\xE3o;
Vem a Ele, sem tardan\xE7a,
E ter\xE1s consola\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:268},{idverse:26008,verse:`Gl\xF3ria, gl\xF3ria. Aleluia, aleluia a Jesus!
Aleluia! Gl\xF3ria a Jesus!
Gl\xF3ria, gl\xF3ria. Aleluia, aleluia a Jesus!
Aleluia ao Redentor! Am\xE9m.
`,verseorder:2,ismainverse:1,id_hino:268},{idverse:26009,verse:`2. 
Se aqui \xE9s perseguido,
Tens a gl\xF3ria do porvir;
Se est\xE1s enfraquecido,
Novas for\xE7as h\xE3o de vir;
Mesmo que o sol se turve,
Haja forte furac\xE3o,
S\xEA fiel, n\xE3o te pertubes,
Cr\xEA em Deus, de cora\xE7\xE3o.`,verseorder:3,ismainverse:0,id_hino:268},{idverse:26010,verse:`1. 
Louvarei ao meu amado;
Sobre todos exaltado;
Por Seu sangue fui comprado;
Todo dEle sou.
`,verseorder:1,ismainverse:0,id_hino:269},{idverse:26011,verse:`2. 
No Seu nome h\xE1 melodia;
Qu\xE3o suave em harmonia!
Enche as almas de alegria
Esse som - JESUS!
`,verseorder:2,ismainverse:0,id_hino:269},{idverse:26012,verse:`3. 
Digno \xE9 de confian\xE7a,
Mui fiel e sem mudan\xE7a;
NEle est\xE1 minha esperan\xE7a;
Bem seguro estou!
`,verseorder:3,ismainverse:0,id_hino:269},{idverse:26013,verse:`4. 
Seu caminho vou seguindo,
Sua for\xE7a me cingindo,
Na carreira prosseguindo,
T\xE9 no c\xE9u entrar.
`,verseorder:4,ismainverse:0,id_hino:269},{idverse:26014,verse:`5. 
L\xE1 verei meu Rei glorioso,
Coroado, majestoso,
Mais que todos amoroso,
Meu SENHOR JESUS.`,verseorder:5,ismainverse:0,id_hino:269},{idverse:26015,verse:`1. 
Sinto prazer louvando
Ao nosso Criador,
E gozo celebrando
Os feitos do Senhor;
Saber que sou lavado
No sanggue de Jesus.
Tamb\xE9m que sou guiado,
Pela divina luz!
`,verseorder:1,ismainverse:0,id_hino:270},{idverse:26016,verse:`Alegre irei avante,
unido ao Salvador;
Cantando triunfante,
Ao nosso Criador!
`,verseorder:2,ismainverse:1,id_hino:270},{idverse:26017,verse:`2. 
Em Cristo, tenho gozo,
E paz, celeste luz;
Na obra, eu repouso,
Cumprida l\xE1 na cruz;
De gra\xE7a revestido,
Nada me faltar\xE1;
Cristo tem prometido,
Que tudo suprir\xE1!
`,verseorder:3,ismainverse:0,id_hino:270},{idverse:26018,verse:`3. 
Alegre sigo avante,
Na senda que conduz
A porta fulgurante
Da mans\xE3o de Jesus;
Com Deus aqui eu ando,
Ele me guiar\xE1,
E por fim me tomando,
Ao c\xE9u me levar\xE1!`,verseorder:4,ismainverse:0,id_hino:270},{idverse:26019,verse:`1. 
L\xE1 na mans\xE3o do Salvador,
N\xE3o h\xE1, jamais, tribula\xE7\xE3o;
Nenhum pesar, nenhuma dor,
Que me quebrante o cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:271},{idverse:26020,verse:`Ali n\xE3o h\xE1 tristeza e dor,
Nenhum pesar, nem afli\xE7\xE3o;
Quando eu estiver morando l\xE1
Direi: N\xE3o h\xE1 tribula\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:271},{idverse:26021,verse:`2. 
P\`ra mim \xE9 triste estar aqui,
Mui longe, sim, do Salvador,
Pois moram j\xE1 com Ele ali,
Os salvos pelo Seu amor.
`,verseorder:3,ismainverse:0,id_hino:271},{idverse:26022,verse:`3. 
Perfeito amor encontrarei,
L\xE1 na mans\xE3o do meu Senhor,
Completa paz, a\xED\`terei,
Pois me dar\xE1 o Salvador.
`,verseorder:4,ismainverse:0,id_hino:271},{idverse:26023,verse:`4. 
Estando l\xE1, eu gozarei
De toda a b\xEAn\xE7\xE3o divinal;
Tamb\xE9m, com Cristo reinarei,
Na Sua gl\xF3ria eternal.`,verseorder:5,ismainverse:0,id_hino:271},{idverse:26024,verse:`1. 
Quando Jesus aparecer,
Que festa para n\xF3s h\xE1 de ser,
Ao c\xE9u de luz iremos ter,
P\`ra desfrutar eterno prazer,
Quando ao Seu Pai nos apresentar,
Que alegria no c\xE9u haver\xE1;
No trono Seu teremos lugar,
Em nossa fronte coroa por\xE1.
`,verseorder:1,ismainverse:0,id_hino:272},{idverse:26025,verse:`2. 
Marchai com Deus, sem vos deter,
Em santidade e sem temer;
Se nada mais podeis fazer -
Por crer em Cristo - tendes poder,
Se hoje crerdes... Deus vos dar\xE1;
Se f\xE9 tiverdes, v\xF3s recebereis,
Vida de paz no c\xE9u gozar\xE1
Todo que crer em Jesus, nosso Rei.
`,verseorder:2,ismainverse:0,id_hino:272},{idverse:26026,verse:`3. 
Iremos n\xF3s, no c\xE9u cear
E Jesus Cristo nos servir;
A Sua grei que quis salvar,
No c\xE9u, ent\xE3o, se reunir\xE1.
Qual forte sol, dar\xE1 resplendor
Ante Jesus, o Senhor eternal!
Oh, que prazer, que sublime amor,
Existir\xE1 no lar celestial!`,verseorder:3,ismainverse:0,id_hino:272},{idverse:26027,verse:`1. 
Quer nas trevas quer na luz,
S\xF3 a Ti recorrerei,
S\xF3 a Ti meu bom Jesus,
E socorro acharei.
`,verseorder:1,ismainverse:0,id_hino:273},{idverse:26028,verse:`S\xF3 a Ti, meu Senhor,
S\xF3 a Ti recorrerei;
S\xF3 a Ti, Salvador,
Um ref\xFAgio encontrarei!
`,verseorder:2,ismainverse:1,id_hino:273},{idverse:26029,verse:`2. 
Quando lutas, penas, dor,
Neste mundo encontrar,
Eu Te buscarei, Senhor,
E vir\xE1s me consolar!
`,verseorder:3,ismainverse:0,id_hino:273},{idverse:26030,verse:`3. 
Quando o  grande tentador
A minh\`alma combater,
Pela gra\xE7a do Senhor,
Todo o mal hei de vencer.
`,verseorder:4,ismainverse:0,id_hino:273},{idverse:26031,verse:`4. 
Fortes lutas eu terei,
Neste mundo enganador,
Mas socorro eu acharei,
Em Ti, sempre, oh, Senhor!`,verseorder:5,ismainverse:0,id_hino:273},{idverse:26032,verse:`1. 
O mundo perdido em pecado se achou,
Jesus \xE9 a luz do mundo;
Mas nas densas trevas a gl\xF3ria brilhou,
Jesus \xE9 a luz do mundo!
`,verseorder:1,ismainverse:0,id_hino:274},{idverse:26033,verse:`Vem para a luz, n\xE3o queiras perder
Perfeita paz, dum novo viver:
Eu cego fui, mas j\xE1 posso ver,
Jesus \xE9 a luz do mundo!
`,verseorder:2,ismainverse:1,id_hino:274},{idverse:26034,verse:`2. 
Nas trevas do mundo j\xE1 temos Betel,
Jesus \xE9 a luz do  mundo,
Ir\xE1s pela luz se tu fores fiel,
Jesus \xE9 a luz do mundo!
`,verseorder:3,ismainverse:0,id_hino:274},{idverse:26035,verse:`3. 
\xD3 cego e presos no vale de horror,
Jesus \xE9 a luz do mundo,
E manda chamar-vos a ver Seu fulgor,
Jesus \xE9 a luz do mundo!
`,verseorder:4,ismainverse:0,id_hino:274},{idverse:26036,verse:`4. 
Nem sol, nem luzeiros no mundo haver\xE1
Jesus \xE9 a luz do mundo,
A luz do Seu rosto ali brilhar\xE1,
Jesus \xE9 a luz do mundo!`,verseorder:5,ismainverse:0,id_hino:274},{idverse:26037,verse:`1. 
\xD3 acorda, desperta dormente,
Tu que jazes no sono da morte,
E que tens uma t\xE3o triste sorte,
\xD3 desperta por Cristo, o Senhor!
Pois te chama o fiel Salvador,
O Seu sangue na cruz j\xE1 derramou,
Teus pecados ali aniquilou,
E respondes: "N\xE3o quero, Senhor?"
`,verseorder:1,ismainverse:0,id_hino:275},{idverse:26038,verse:`2. 
\xD3 n\xE3o sabes que teu pr\xF3prio alento
Vem de Deus, vem a cada momento?
Queres tu despertar num tormento
Que jamais poder\xE1 se findar?
Cristo hoje te quer libertar,
\xD3 desperta-te \xE0 voz do Salvador,
Foge logo do abismo de horror,
Que tu\`alma procura tragar.
`,verseorder:2,ismainverse:0,id_hino:275},{idverse:26039,verse:`3. 
N\xE3o te importas por teres agora,
A tua alma em negra masmorra?
Em Jesus hoje cr\xEA, busca-O e adora,
Pois assim tu ter\xE1s salva\xE7\xE3o;
Crendo em Cristo no teu cora\xE7\xE3o,
Paz e gozo por Ele obter\xE1s;
E tamb\xE9m no c\xE9u tu habitar\xE1s,
Se recebes agora o perd\xE3o.
`,verseorder:3,ismainverse:0,id_hino:275},{idverse:26040,verse:"4. ",verseorder:4,ismainverse:0,id_hino:275},{idverse:26041,verse:`1. 
Em Cana\xE3 eu entrarei, no meu pa\xEDs ditoso,
E em Si\xE3o habitarei; que paz ali terei
Com meu Jesus! Que gozo! Pois me dar\xE1 repouso,
Irei cantar, ent\xE3o, nos c\xE9us, um hino ao meu Deus!
`,verseorder:1,ismainverse:0,id_hino:276},{idverse:26042,verse:`Oh! Gl\xF3ria a
Jesus, a Jesus, a Jesus!
Oh! Gl\xF3ria a Jesus, \xF3 meu poder e luz!
Os santos reunidos, no reino dos remidos,
Entoar\xE3o, enfim, nos c\xE9us,
Um hino ao bom Deus!
`,verseorder:2,ismainverse:1,id_hino:276},{idverse:26043,verse:`2. 
J\xE1 muitas provas eu passei e lutas bem not\xF3rias;
Mas de andar n\xE3o cansarei, at\xE9 eu ver o Rei.
Jesus, que d\xE1 vit\xF3ria, me levar\xE1 em gl\xF3ria,
E entrarei no esplendor do reino do Senhor.
`,verseorder:3,ismainverse:0,id_hino:276},{idverse:26044,verse:`3. 
Mui breve Cristo voltar\xE1;
nas nuvens, v\xEA-lo-emos;
E o Seu povo levar\xE1, e festa haver\xE1;
O nosso caro e bom Jesus, verdade, vida e luz.`,verseorder:4,ismainverse:0,id_hino:276},{idverse:26045,verse:`1. 
Tens achado em Cristo plena salva\xE7\xE3o,
Pelo sangue vertido na cruz?
Toda mancha lava de Teu cora\xE7\xE3o,
Este sangue eficaz de Jesus.
`,verseorder:1,ismainverse:0,id_hino:277},{idverse:26046,verse:`Salvo est\xE1s, limpo est\xE1s,
Pelo sangue de Cristo Jesus?
Tens Teu cora\xE7\xE3o mais alvo que a luz,
Foste limpo no sangue eficaz?
`,verseorder:2,ismainverse:1,id_hino:277},{idverse:26047,verse:`2. 
Vives sempre ao lado do teu Salvador,
Pelo sangue que mana da cruz?
Do pecado fostes sempre vencedor,
Como foi teu bendito Jesus?
`,verseorder:3,ismainverse:0,id_hino:277},{idverse:26048,verse:`3. 
Ter\xE1s roupa branca quando vier Jesus,
Foste limpo na fonte de amor?
Est\xE1s pronto p\`ra seguir ao c\xE9u de luz,
Pelo sangue purificador?
`,verseorder:4,ismainverse:0,id_hino:277},{idverse:26049,verse:`4. 
Cristo hoje d\xE1 pureza e mui poder:
Fita os olhos na cruz do Senhor,
Dela, fontes sai que te enche de prazer,
Que te farta de vida e vigor.`,verseorder:5,ismainverse:0,id_hino:277},{idverse:26050,verse:`1. 
Vais levando s\xF3, e mui cansado,
Tua cruz, tua cruz?
N\xE3o sabendo que tens ao teu lado
O aux\xEDlio do Senhor Jesus?
`,verseorder:1,ismainverse:0,id_hino:278},{idverse:26051,verse:`Se tenho cargas, que s\xF3 n\xE3o posso
levar
Eu pe\xE7o aux\xEDlio ao meu bom Salvador!
Se de cansado n\xE3o posso mais caminhar,
Eu clamo a meu Jesus, que vem me dar o Seu vigor.
`,verseorder:2,ismainverse:1,id_hino:278},{idverse:26052,verse:`2. 
N\xE3o te esque\xE7as, que s\xF3, ao Calv\xE1rio,
Foi Jesus, foi Jesus,
Onde derrotou o advers\xE1rio,
Quando por ti sucumbiu na cruz.
`,verseorder:3,ismainverse:0,id_hino:278},{idverse:26053,verse:`3. 
S\xF3 em Cristo, o fiel amigo,
Achar\xE1s, achar\xE1s,
Escondido, neste bom abrigo,
A paz de Deus, ent\xE3o fruir\xE1s.`,verseorder:4,ismainverse:0,id_hino:278},{idverse:26054,verse:`1. 
Jesus me falou dum formoso lugar;
Ali quero ir, e tu?
Aonde os salvos ir\xE3o desfrutar;
Ali quero ir; e tu?
Pois Jesus as mans\xF5es nos foi preparar;
Ali quero ir; e tu?
Aonde a morte n\xE3o pode chegar;
Ali quero ir; e tu?
`,verseorder:1,ismainverse:0,id_hino:279},{idverse:26055,verse:`Ao c\xE9u quero ir
Ao c\xE9u quero ir,
Ao c\xE9u quero ir; e tu?
Ao c\xE9u quero ir;
N\xE3o queres tu ir?
No c\xE9u quero ver Jesus!
`,verseorder:2,ismainverse:1,id_hino:279},{idverse:26056,verse:`2. 
As portas s\xE3o j\xF3ias, o mar de cristal;
Ali quero ir; e tu?
\xC9 Cristo a luz do pa\xEDs celestial;
Ali quero ir; e tu?
Os que l\xE1 v\xE3o entrar, jamais morrer\xE3o;
Ali quero ir; e tu?
Aonde termina a tribula\xE7\xE3o.
Ali quero ir; e tu?
`,verseorder:3,ismainverse:0,id_hino:279},{idverse:26057,verse:`3. 
E quando navegue a nau de Si\xE3o,
Ali quero ir; e tu?
Ao dar-se \xE0 vela, p\`ra essa mans\xE3o,
Ali quero ir; e tu?
Quando v\xE3o coroar a Cristo, tamb\xE9m,
Ali quero ir; e tu?
E quando os coros disserem: "Am\xE9m".
Ali quero ir; e tu?`,verseorder:4,ismainverse:0,id_hino:279},{idverse:26058,verse:`1. 
\xD3 Jesus, no Teu caminho,
Te suplico com fervor,
Tem-me mais a Ti vizinho
P\`ra gozar do Teu amor.
`,verseorder:1,ismainverse:0,id_hino:280},{idverse:26059,verse:`2. 
\xD3 Senhor, \xF3 vem guardar-me
Sob Tua prote\xE7\xE3o;
Para o c\xE9u, \xF3 vem guiar-me
Pela Tua forte m\xE3o.
`,verseorder:2,ismainverse:0,id_hino:280},{idverse:26060,verse:`3. 
D\xE1-me Teu celeste gozo,
E vit\xF3ria sobre o mal;
D\xE1-me a gra\xE7a, Teu repouso,
Tua paz celestial!
`,verseorder:3,ismainverse:0,id_hino:280},{idverse:26061,verse:`4. 
\xD3 Senhor, vem consolar-me,
No Teu divinal amor!
Tua vida queiras dar-me,
Meu amado Redentor!`,verseorder:4,ismainverse:0,id_hino:280},{idverse:26062,verse:`1. 
Vem a Cristo, sem tardar,
N\xE3o te queiras recusar,
\xD3 amigo, vem depressa a Jesus!
Vem das trevas para a luz,
Cristo quer te consolar,
Vem a Ele, \xF3 perdido, sem tardar!
`,verseorder:1,ismainverse:0,id_hino:281},{idverse:26063,verse:`Sem tardar, vem sem tardar;
Sem tardar, vem sem tardar.
\xD3 aceita a salva\xE7\xE3o,
No teu pobre cora\xE7\xE3o,
Jesus Cristo te convida: "Sem tardar".
`,verseorder:2,ismainverse:1,id_hino:281},{idverse:26064,verse:`2. 
Vem a Cristo, sem tardar,
Um tesouro te quer dar;
Ele quer te conceder real perd\xE3o;
Cristo v\xEA teu cora\xE7\xE3o,
Tua alma a solu\xE7ar;
Vem a Ele, \xF3 perdido, sem tardar!
`,verseorder:3,ismainverse:0,id_hino:281},{idverse:26065,verse:`3. 
Vem a Cristo, sem tardar,
Com Seus dons te quer fartar,
Com riquezas que jamais se perder\xE3o.
Vem,  estende a tua m\xE3o,
Cristo quer te perdoar!
Vem a Ele, \xF3 perdido, sem tardar!
`,verseorder:4,ismainverse:0,id_hino:281},{idverse:26066,verse:`4. 
Vem a Cristo sem tardar,
Tua tenda vem firmar
Sobre a rocha que do mal te guardar\xE1;
O receio larga j\xE1,
Vem p\`ra Cristo te salvar;
Ele mesmo te avisa: "Sem tardar"!`,verseorder:5,ismainverse:0,id_hino:281},{idverse:26067,verse:`1. 
Que sangue precioso
Saiu do Salvador!
Oh sangue glorioso,
Que lava o pecador!
`,verseorder:1,ismainverse:0,id_hino:282},{idverse:26068,verse:`Sim, o sangue que Jesus
Na cruz derramou;
Sim, o sangue de Jesus,
Meu cora\xE7\xE3o lavou!
`,verseorder:2,ismainverse:1,id_hino:282},{idverse:26069,verse:`2. 
Irm\xE3os, nos recordemos
Que o sangue nos lavou
Alegres, jubilemos
Seu sangue nos comprou!
`,verseorder:3,ismainverse:0,id_hino:282},{idverse:26070,verse:`3. 
Na gl\xF3ria est\xE1 sentado,
Jesus, meu Redentor!
No trono coroado,
O meu intercessor!
`,verseorder:4,ismainverse:0,id_hino:282},{idverse:26071,verse:`4. 
Por que assim resistes
Ao meigo Salvador?
Aceita o Seu convite
P\`ras bodas de amor.`,verseorder:5,ismainverse:0,id_hino:282},{idverse:26072,verse:`1. 
Ama o pastor as ovelhas,
Com um amor paternal,
Ama o Pastor seu rebanho,
Com um amor sem igual;
Ama o Pastor inda outras,
Que desgarradas est\xE3o,
E comovido as procura,
Por onde quer qu\`elas v\xE3o.
`,verseorder:1,ismainverse:0,id_hino:283},{idverse:26073,verse:`Pelos desertos errantes,
V\xEAem-se a sofrer penas mil,
Ele ao ach\xE1-las, nos ombros
Leva-as ditoso, ao redil.
`,verseorder:2,ismainverse:1,id_hino:283},{idverse:26074,verse:`2. 
Ama o pastor seus cordeiros,
Com inef\xE1vel amor,
Aos que, \xE0s vezes, perdidos,
Gemendo se ouvem de dor.
Vede o Pastor comovido
Pelas colinas vagar,
E os cordeiros nos ombros,
Vede-O, levando ao lugar.
`,verseorder:3,ismainverse:0,id_hino:283},{idverse:26075,verse:`3. 
Ama as noventa e nove,
Que no redil abrigou;
Ama a que, desgarrada,
No campo se desviou;
" \xD3 minha ovelha perdida!"
Clama o dolente Pastor;
Quem ir\xE1 em sua ajuda,
Para salv\xE1-la, Senhor?
`,verseorder:4,ismainverse:0,id_hino:283},{idverse:26076,verse:`4. 
S\xE3o delicados teus pastos,
Mui quietas tuas \xE1guas s\xE3o;
Eis-nos aqui, \xF3 bom Mestre,
D\xE1-nos veraz dire\xE7\xE3o,
D\xE1-nos obreiros zelosos,
Enche-nos de santo amor,
Pelas ovelhas perdidas
Do Teu redil, bom Senhor.`,verseorder:5,ismainverse:0,id_hino:283},{idverse:26077,verse:`1. 
Tal como sou, vou decidir,
Que a outro eu n\xE3o posso ir;
Pois me convidas para vir,
Bendito Cristo, eis-me aqui.
`,verseorder:1,ismainverse:0,id_hino:284},{idverse:26078,verse:`2. 
Tal como sou, sem demorar,
Do mal, querendo me livrar;
S\xF3 podes Tu me perdoar:
Bendito Cristo, eis-me aqui.
`,verseorder:2,ismainverse:0,id_hino:284},{idverse:26079,verse:`3. 
Tal como sou, em afli\xE7\xE3o,
Exposto \xE0 morte, e perdi\xE7\xE3o,
Buscando vida e perd\xE3o,
Bendito Cristo, eis-me aqui.
`,verseorder:3,ismainverse:0,id_hino:284},{idverse:26080,verse:`4. 
Tal como sou, Teu grande amor
Me vence, e busco Teu favor,
Servir-Te quero com valor,
Bendito Cristo, eis-me aqui.
`,verseorder:4,ismainverse:0,id_hino:284},{idverse:26081,verse:`5. 

`,verseorder:5,ismainverse:0,id_hino:284},{idverse:26082,verse:`6. 

`,verseorder:6,ismainverse:0,id_hino:284},{idverse:26083,verse:`7. 

`,verseorder:7,ismainverse:0,id_hino:284},{idverse:26084,verse:`8. 

`,verseorder:8,ismainverse:0,id_hino:284},{idverse:26085,verse:`9. 

`,verseorder:9,ismainverse:0,id_hino:284},{idverse:26086,verse:`10. 

`,verseorder:10,ismainverse:0,id_hino:284},{idverse:26087,verse:`11. 

`,verseorder:11,ismainverse:0,id_hino:284},{idverse:26088,verse:`12. 

`,verseorder:12,ismainverse:0,id_hino:284},{idverse:26089,verse:`13. 

`,verseorder:13,ismainverse:0,id_hino:284},{idverse:26090,verse:`14. 

`,verseorder:14,ismainverse:0,id_hino:284},{idverse:26091,verse:`15. 

`,verseorder:15,ismainverse:0,id_hino:284},{idverse:26092,verse:`16. 

`,verseorder:16,ismainverse:0,id_hino:284},{idverse:26093,verse:`17. 

`,verseorder:17,ismainverse:0,id_hino:284},{idverse:26094,verse:`18. 

`,verseorder:18,ismainverse:0,id_hino:284},{idverse:26095,verse:`19. 

`,verseorder:19,ismainverse:0,id_hino:284},{idverse:26096,verse:`20. 

`,verseorder:20,ismainverse:0,id_hino:284},{idverse:26097,verse:`21. 

`,verseorder:21,ismainverse:0,id_hino:284},{idverse:26098,verse:"22. ",verseorder:22,ismainverse:0,id_hino:284},{idverse:26099,verse:`1. 
Senhor, pela f\xE9 me sustento
Na gra\xE7a, nos Teus mandamentos,
Que operam a cada momento,
No meu cora\xE7\xE3o, salva\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:285},{idverse:26100,verse:`Tu \xE9s o meu gozo,
Senhor, o meu manancial de amor;
Irei Te louvar para sempre nos c\xE9us,
Bem perto do trono de Deus!
`,verseorder:2,ismainverse:1,id_hino:285},{idverse:26101,verse:`2. 
A gra\xE7a de Deus recebida,
Encheu-me de gozo, de vida,
E sempre a mim \xE9 concedida
Por nosso Senhor, Redentor!
`,verseorder:3,ismainverse:0,id_hino:285},{idverse:26102,verse:`3. 
De b\xEAn\xE7\xE3os oh! vem me inundando,
O meu cora\xE7\xE3o inflamando,
Tamb\xE9m meus pecados queimando,
De todo meu ser, com poder.`,verseorder:4,ismainverse:0,id_hino:285},{idverse:26103,verse:`1. 
\xD3 Jesus nesta terra,
H\xE1 s\xF3 tristeza e dor;
Os \xEDmpios fazem guerra
Aos santos do Senhor.
`,verseorder:1,ismainverse:0,id_hino:286},{idverse:26104,verse:`Cristo volta
Em fulgurante luz;
O mar j\xE1 se revolta,
N\xE3o tarda vir Jesus!
`,verseorder:2,ismainverse:1,id_hino:286},{idverse:26105,verse:`2. 
Do mundo n\xF3s n\xE3o somos,
Mas do Senhor Jesus;
Remidos todos fomos
Com sangue, l\xE1 na cruz!
`,verseorder:3,ismainverse:0,id_hino:286},{idverse:26106,verse:`3. 
No mundo tenebroso,
N\xE3o vamos descansar,
Mas para o c\xE9u, de gozo,
Queremos j\xE1 voar!
`,verseorder:4,ismainverse:0,id_hino:286},{idverse:26107,verse:`4. 
Olhamos para cima
Donde vir\xE1 Jesus;
Pois isto nos anima
Para viver na luz.`,verseorder:5,ismainverse:0,id_hino:286},{idverse:26108,verse:`1. 
Oh, foi o sangue do Senhor
Que me salvou, sim, me salvou;
Sim, este sangue tem poder,
De toda a mancha me lavou.
`,verseorder:1,ismainverse:0,id_hino:287},{idverse:26109,verse:`Jesus p\`ra todos tem poder e salva\xE7\xE3o,
Meu amigo, a Ele vem, aceitando a remiss\xE3o;
Gl\xF3ria ao Salvador! Gl\xF3ria ao meu Jesus!
O Seu sangue me salvou!
`,verseorder:2,ismainverse:1,id_hino:287},{idverse:26110,verse:`2. 
Oh! maravilha de amor
A mim mostrou, sim, me mostrou;
Jesus sofreu t\xE3o grande dor,
E Tua vida resgatou.
`,verseorder:3,ismainverse:0,id_hino:287},{idverse:26111,verse:`3. 
Em mim n\xE3o h\xE1 poder algum
Mui d\xE9bil \`stou, mere\xE7o d\xF3,
Mas por Jesus eu vencerei,
Ainda que eu seja p\xF3.
`,verseorder:4,ismainverse:0,id_hino:287},{idverse:26112,verse:`4. 
Meu pai me deu o Consolador,
P\`ra me guiar, me iluminar,
Jamais temerei o tentador,
Jesus sempre h\xE1 de me guardar.`,verseorder:5,ismainverse:0,id_hino:287},{idverse:26113,verse:`1. 
Da cruz, a palavra \xE9 fiel,
Aleluia! Aleluia!
Chamando aquele que \xE9 infiel,
Aleluia! Aleluia!
Qualquer temporal pode vir,
A mensagem mui firme \`st\xE1:
Que Jesus veio nos remir,
Pela morte sobre a cruz.
`,verseorder:1,ismainverse:0,id_hino:288},{idverse:26114,verse:`Aleluia! Aleluia! A palavra da cruz \xE9
poder.
Aleluia! Aleluia! Para quem em Cristo crer.
Aleluia! Aleluia! A palavra da cruz \xE9 poder.
Aleluia! Aleluia! Para quem em Cristo crer.
`,verseorder:2,ismainverse:1,id_hino:288},{idverse:26115,verse:`2. 
Morrendo por n\xF3s l\xE1 na cruz,
Aleluia! Aleluia!
Vit\xF3ria nos ganhou Jesus,
Aleluia! Aleluia!
Ali o Cordeiro de Deus
Veio a gra\xE7a do c\xE9u raiar!
Nos salvou quem veio dos c\xE9us,
Pela morte, sobre a cruz.
`,verseorder:3,ismainverse:0,id_hino:288},{idverse:26116,verse:`3. 
N\xE3o me envergonho da cruz,
Aleluia! Aleluia!
Pois deu-me vida, deu-me luz,
Aleluia! Aleluia!
O caminho p\`ra o c\xE9u se abriu,
Para todo o vil pecador,
Quando Cristo nos redimiu,
Pela morte sobre a cruz.`,verseorder:4,ismainverse:0,id_hino:288},{idverse:26117,verse:`1. 
Asperge hoje meu cora\xE7\xE3o,
Com sangue Teu, \xF3 Redentor!
Liberta-me da vil tenta\xE7\xE3o,
Com sangue Teu, senhor!
`,verseorder:1,ismainverse:0,id_hino:289},{idverse:26118,verse:`Sob o sangue Teu, Senhor,
Guarda-me da corrup\xE7\xE3o!
Sob o sangue expiador,
Eu tenho prote\xE7\xE3o!
`,verseorder:2,ismainverse:1,id_hino:289},{idverse:26119,verse:`2. 
De todas d\xFAvidas, do temor,
No sangue Teu, vem me lavar,
Dos males do grande tentador,
\xD3 queiras me livrar!
`,verseorder:3,ismainverse:0,id_hino:289},{idverse:26120,verse:`3. 
Ref\xFAgio acha o  pecador,
No sangue Teu, \xF3 meu Jesus;
Asperge-me sempre, \xF3 Senhor,
No sangue, l\xE1 da cruz!
`,verseorder:4,ismainverse:0,id_hino:289},{idverse:26121,verse:`4. 
\xD3 vem me enchendo do Teu vigor,
Pelo manar do sangue Teu;
Fazendo-me mais que vencedor,
Pelo poder de Deus.
`,verseorder:5,ismainverse:0,id_hino:289},{idverse:26122,verse:`5. 
Concede-me a perfeita paz,
Por Tua cruz e sangue Teu;
Com ricos dons, \xF3 me satisfaz,
E faz-me ver o c\xE9u.`,verseorder:6,ismainverse:0,id_hino:289},{idverse:26123,verse:`1. 
\xD3 Senhor, n\xF3s esperamos
Que escute a ora\xE7\xE3o;
N\xF3s, Teus servos, j\xE1 clamamos,
Com humilde cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:290},{idverse:26124,verse:`Teu Esp\xEDrito, vem, derrama,
Sobre cada cora\xE7\xE3o!
E no crente, que a Ti clama,
Vem, confirma a peti\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:290},{idverse:26125,verse:`2. 
Deixa o fogo do Esp\xEDrito,
Sim, nos cora\xE7\xF5es arder,
P\`ra que tudo que \xE9 finito,
Jamais possa se reter.
`,verseorder:3,ismainverse:0,id_hino:290},{idverse:26126,verse:`3. 
\xD3 Senhor, me purifica,
Tira o mal que est\xE1 em mim,
A minh\` alma santifica,
E me guarda at\xE9 o fim.
`,verseorder:4,ismainverse:0,id_hino:290},{idverse:26127,verse:`4. 
D\xE1-nos mais da Tua gra\xE7a,
E enche-nos do Teu poder,
Do Teu templo, \xF3 Deus, se fa\xE7a
A Tua voz se perceber.
`,verseorder:5,ismainverse:0,id_hino:290},{idverse:26128,verse:`5. 
D\xE1-nos dons do Teu esp\xEDrito;
Faz milagres, \xF3 Senhor,
P\`ra que tudo, que tens dito,
Cumpra-se, meu Redentor.
`,verseorder:6,ismainverse:0,id_hino:290},{idverse:26129,verse:`6. 
Dou-te gra\xE7as, Rei da gl\xF3ria,
Pois ouviste a peti\xE7\xE3o;
Belos hinos de vit\xF3ria,
Mais Tu deste ao cora\xE7\xE3o.`,verseorder:7,ismainverse:0,id_hino:290},{idverse:26130,verse:`1. 
Rude cruz se erigiu,
Dela o dia fugiu,
Como emblema de vergonha e dor;
Mas contemplo esta cruz,
Porque nela Jesus
Deu a vida por mim, pecador.
`,verseorder:1,ismainverse:0,id_hino:291},{idverse:26131,verse:`Sim, eu amo a mensagem da cruz
T\xE9 morrer eu a vou proclamar;
Levarei eu tamb\xE9m minha cruz
T\xE9 por uma coroa trocar.
`,verseorder:2,ismainverse:1,id_hino:291},{idverse:26132,verse:`2. 
Desde a gl\xF3ria dos c\xE9us,
O Cordeiro de Deus,
Ao Calv\xE1rio humilhante baixou;
Essa cruz  tem p\`ra mim
Atrativos sem fim,
Porque nela Jesus me salvou.
`,verseorder:3,ismainverse:0,id_hino:291},{idverse:26133,verse:`3. 
Nesta cruz padeceu
E por mim j\xE1 morreu,
Meu Jesus, para dar-me o perd\xE3o
E eu me alegro na cruz,
Dela vem gra\xE7a e luz,
Para minha santifica\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:291},{idverse:26134,verse:`4. 
Eu aqui com Jesus,
A vergonha na cruz
Quero sempre levar e sofrer;
Cristo vem me buscar,
E com ele, no lar,
Uma parte da gl\xF3ria hei de ter.`,verseorder:5,ismainverse:0,id_hino:291},{idverse:26135,verse:`1. 
Qual o pre\xE7o do perd\xE3o?
S\xF3 o sangue de Jesus Cristo.
O que limpa o cora\xE7\xE3o?
S\xF3 o sangue de Jesus Cristo.
`,verseorder:1,ismainverse:0,id_hino:292},{idverse:26136,verse:`Qual o poder real
Que limpa todo o mal,
E d\xE1 paz divinal?
S\xF3 o sangue de Jesus Cristo.
`,verseorder:2,ismainverse:1,id_hino:292},{idverse:26137,verse:`2. 
Fez resgate eficaz
S\xF3 o sangue de Jesus Cristo.
Deu-nos santidade e paz.
S\xF3 o sangue de Jesus Cristo.
`,verseorder:3,ismainverse:0,id_hino:292},{idverse:26138,verse:`3. 
Sempre pode me curar,
S\xF3 o sangue de Jesus Cristo.
E do mal me libertar.
S\xF3 o sangue de Jesus Cristo.
`,verseorder:4,ismainverse:0,id_hino:292},{idverse:26139,verse:`4. 
L\xE1 no  c\xE9u, eu vou cantar:
S\xF3 o sangue de Jesus Cristo.
Deu-me gra\xE7a para entrar.
S\xF3 o sangue de Jesus Cristo.`,verseorder:5,ismainverse:0,id_hino:292},{idverse:26140,verse:`1. 
Jesus no Calv\xE1rio, por mim sofreu
A morte da maldi\xE7\xE3o;
Minh\`alma ganhou, com o sangue Seu
O pre\xE7o da reden\xE7\xE3o.
Do alto da cruz, Jesus exclamou:
"Consumado est\xE1", e o esp\xEDrito rendeu;
O sangue verteu, e me expiou,
Unindo-me ao povo Seu.
`,verseorder:1,ismainverse:0,id_hino:293},{idverse:26141,verse:`2. 
Na cruz do Calv\xE1rio, a c\xE9dula foi
Cravada que Ele riscou.
O v\xE9u se rasgou, a porta se abriu,
Aberta p\`ra Deus ficou.
Sobre a cruz eu morri, com Cristo Jesus;
O meu "ego" falaz sobre o lenho levou.
A noite fugiu, e raiou a luz!
Por Cristo eu salvo estou.
`,verseorder:2,ismainverse:0,id_hino:293},{idverse:26142,verse:`3. 
Na cruz do Calv\xE1rio, ao mundo morri;
Aqui um estranho fiquei;
N\xE3o vivo mais eu, com Cristo me uni;
Que vida excelsa achei!
O Cordeiro de Deus minh\`alma nevou;
O sangue pascoal a culpa tirou,
O destruidor minh\`alma passou,
Em Cristo liberto estou.`,verseorder:3,ismainverse:0,id_hino:293},{idverse:26143,verse:`1. 
Mui triste em pecado, vagava sem luz,
Mas j\xE1 vivo alegre, pois a Cristo achei,
Em todos os dias, eu canto a Jesus,
Eu sigo com Cristo, meu Mestre, meu Rei.
`,verseorder:1,ismainverse:0,id_hino:294},{idverse:26144,verse:`Eu vou com Jesus;
aleluia!
Eu sigo com Cristo, o Rei;
N\xE3o vago jamais, eu quero seguir
A Cristo Jesus, o meu Rei.
`,verseorder:2,ismainverse:1,id_hino:294},{idverse:26145,verse:`2. 
Por anos e anos, sem Cristo, a s\xF3s,
Eu nunca, no mundo feliz me achei;
Mas j\xE1 com as aves elevo a voz,
E canto feliz, pois eu vou com o Rei.
`,verseorder:3,ismainverse:0,id_hino:294},{idverse:26146,verse:`3. 
\xD3 alma que vives no vale sem luz,
Se olhas p\`ra cima, ver\xE1s um farol;
\xD3 sobe depressa, vem j\xE1 p\`ra Jesus;
De Cristo, a luz brilha mais que o sol.`,verseorder:4,ismainverse:0,id_hino:294},{idverse:26147,verse:`1. 
Novo canto de louvor,
Canta j\xE1 meu cora\xE7\xE3o;
Hino de paz e de amor,
Que me traz consola\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:295},{idverse:26148,verse:`Ouve qu\xE3o doce \xE9 o louvor,
Ao Cordeiro divinal!
Jesus, canto inspirador,
Harmonia angelical.
`,verseorder:2,ismainverse:1,id_hino:295},{idverse:26149,verse:`2. 
Canto hinos de louvor,
Porque Cristo me salvou
Deste mundo enganador;
Sobre a rocha me firmou.
`,verseorder:3,ismainverse:0,id_hino:295},{idverse:26150,verse:`3. 
Este canto novo \xE9
cada noite que passar;
Sombras d\xE3o lugar \xE0 f\xE9,
Se a Deus louvor cantar.
`,verseorder:4,ismainverse:0,id_hino:295},{idverse:26151,verse:`4. 
Quando o cora\xE7\xE3o vibrar,
Os acordes l\xE1 do ceu,
E da rola a voz soar,
No jardim do nosso Deus.
`,verseorder:5,ismainverse:0,id_hino:295},{idverse:26152,verse:`5. 
Quando meu Jesus voltar,
E levar-me com os Seus,
Inda eu irei cantar,
Este hino l\xE1 nos c\xE9us.
`,verseorder:6,ismainverse:0,id_hino:295},{idverse:26153,verse:`6. 
No c\xE9u, hino de louvor,
Canta a grande multid\xE3o.
Vinde \xE0s bodas de amor,
Ao banquete do perd\xE3o.
`,verseorder:7,ismainverse:0,id_hino:295},{idverse:26154,verse:"7. ",verseorder:8,ismainverse:0,id_hino:295},{idverse:26155,verse:`1. 
No Jardim Jesus Cristo clamava,
Quando os \xEDmpios O foram prender;
E falando co\`o Pai suplicava,
Pelo c\xE1lice que ia beber.
`,verseorder:1,ismainverse:0,id_hino:296},{idverse:26156,verse:`Com Jesus a minh\`alma deseja estar
Com jardim, em constante ora\xE7\xE3o;
Quando a noite chegar, e o mal me cercar,
Quero estar em constante ora\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:296},{idverse:26157,verse:`2. 
Qual o orvalho que d\xE1 vida \xE0s flores,
Assim \xE9 para o crente a ora\xE7\xE3o;
Meus cuidados, tristezas e dores,
Cristo as sabe por minha ora\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:296},{idverse:26158,verse:`3. 
Jesus teve completa vit\xF3ria,
Porque sempre viveu em ora\xE7\xE3o,
Muitos santos chegaram \xE0 gl\xF3ria,
Sob o manto da doce ora\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:296},{idverse:26159,verse:`4. 
Renovados em for\xE7as seremos,
N\xF3s teremos u\`a nova un\xE7\xE3o;
E com Deus, no jardim falaremos,
Se vivermos sempre em ora\xE7\xE3o.
`,verseorder:5,ismainverse:0,id_hino:296},{idverse:26160,verse:"5. ",verseorder:6,ismainverse:0,id_hino:296},{idverse:26161,verse:`1. 
Desde que Jesus deu-me Sua luz,
Posso jubilar, cantar;
Ao meu cora\xE7\xE3o trouxe salva\xE7\xE3o
Que eu n\xE3o posso te explicar.
`,verseorder:1,ismainverse:0,id_hino:297},{idverse:26162,verse:`Abundantemente Cristo d\xE1,
Gra\xE7a, que n\xE3o h\xE1 igual, n\xE3o h\xE1;
Aleluia, gl\xF3ria ao nome Seu!
Que alegria sinto eu!
`,verseorder:2,ismainverse:1,id_hino:297},{idverse:26163,verse:`2. 
Desde que na cruz contemplei Jesus,
Meus pecados dissipou;
Tenho grande paz e perd\xE3o veraz,
Quando a Seus p\xE9s estou.
`,verseorder:3,ismainverse:0,id_hino:297},{idverse:26164,verse:`3. 
L\xE1 da cruz prov\xE9m, um caudal tamb\xE9m,
Que ao cansado for\xE7as d\xE1;
E das chagas vem o perd\xE3o, p\`ra quem
Mui sedento e fraco est\xE1!
`,verseorder:4,ismainverse:0,id_hino:297},{idverse:26165,verse:`4. 
Grato, ent\xE3o, darei gl\xF3ria a Cristo, o Rei,
Pelo gozo que \xE9 sem par;
Em continua\xE7\xE3o, o meu cora\xE7\xE3o,
Inda mais vai se alegrar.`,verseorder:5,ismainverse:0,id_hino:297},{idverse:26166,verse:`1. 
Avante, servos de Jesus, por nosso Deus;
Nas trevas brilhe vossa luz, a luz l\xE1 dos c\xE9us.
`,verseorder:1,ismainverse:0,id_hino:298},{idverse:26167,verse:`Gl\xF3ria a Deus,
pois vencerei,
Gl\xF3ria a Deus, pois vencerei,
Triunfante sigo, levando a cruz!
Gl\xF3ria a Deus, pois vencerei,
Gl\xF3ria a Deus, pois vencerei,
Em nome de Jesus!
`,verseorder:2,ismainverse:1,id_hino:298},{idverse:26168,verse:`2. 
Levanta Josu\xE9 pend\xE3o, com mui valor;
E o inimingo foge, ent\xE3o, com grande pavor.
`,verseorder:3,ismainverse:0,id_hino:298},{idverse:26169,verse:`3. 
Sai com trezentos Gide\xE3o, a combater;
Os midianitas logo v\xE3o a guerra perder.
`,verseorder:4,ismainverse:0,id_hino:298},{idverse:26170,verse:`4. 
Golias mui soberbo est\xE1, zomba de Deus.
Mas vem Davi, e nele d\xE1, com ajuda do c\xE9u.
`,verseorder:5,ismainverse:0,id_hino:298},{idverse:26171,verse:`5. 
Cerrai fileiras, \xF3 fi\xE9is, sempre avan\xE7ai;
No c\xE9u coroas e laur\xE9is, Jesus vai nos dar.
`,verseorder:6,ismainverse:0,id_hino:298},{idverse:26172,verse:"6. ",verseorder:7,ismainverse:0,id_hino:298},{idverse:26173,verse:`1. 
H\xE1 um canto novo no meu ser,
\xC9 a voz de meu Jesus,
Que me chama: "Vem em mim obter
A paz, que eu ganhei na cruz".
`,verseorder:1,ismainverse:0,id_hino:299},{idverse:26174,verse:`Cristo, Cristo, Cristo, nome sem
igual;
Enches o contrito, de prazer celestial.
`,verseorder:2,ismainverse:1,id_hino:299},{idverse:26175,verse:`2. 
Preso no pecado eu me achei,
Sem paz no meu cora\xE7\xE3o;
Mas em Cristo eu j\xE1 encontrei
Doce paz e prote\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:299},{idverse:26176,verse:`3. 
Tenho Sua gra\xE7a divinal,
Sob as asas de amor,
E riquezas que fluem em caudal,
L\xE1 do trono do Senhor.
`,verseorder:4,ismainverse:0,id_hino:299},{idverse:26177,verse:`4. 
Pelas \xE1guas fundas me levou;
Provas muitas encontrei;
Mas Jesus bendito me guiou
Por Seu sangue vencerei.
`,verseorder:5,ismainverse:0,id_hino:299},{idverse:26178,verse:`5. 
Cristo, numa nuvem voltar\xE1,
Baixar\xE1 do c\xE9u em luz;
Pelo Seu poder me levar\xE1,
P\`ra Seu lar, o bom Jesus.`,verseorder:6,ismainverse:0,id_hino:299},{idverse:26179,verse:`1. 
Jesus, sim, vem no c\xE9u, em gl\xF3ria Ele vem!
Ecoa a nova pelo mundo al\xE9m;
Oh esperan\xE7a que a Sua Igreja tem!
Dai gl\xF3ria a Deus, Jesus em breve vem!
`,verseorder:1,ismainverse:0,id_hino:300},{idverse:26180,verse:`Nossa esperan\xE7a \xE9 Sua
vinda
O Rei dos reis vem nos buscar;
N\xF3s aguardamos, Jesus, ainda,
T\xE9 a luz da manh\xE3 raiar.
Nossa esperan\xE7a \xE9 Sua vinda
O Rei dos reis vem nos buscar;
N\xF3s aguardamos, Jesus, ainda,
T\xE9 a luz da manh\xE3 raiar.
`,verseorder:2,ismainverse:1,id_hino:300},{idverse:26181,verse:`2. 
Jesus, sim, vem, os mortos esperando est\xE3o;
O gran momento da ressurrei\xE7\xE3o
E do sepulcro em breve se levantar\xE3o!
Dai gl\xF3ria a Deus, Jesus em breve vem!
`,verseorder:3,ismainverse:0,id_hino:300},{idverse:26182,verse:`3. 
Jesus, sim, vem do c\xE9u cercado de esplendor,
Aniquilando a corrup\xE7\xE3o e a dor,
Quebrando os la\xE7os do astuto usurpador,
Dai gl\xF3ria a Deus, Jesus em breve vem!
`,verseorder:4,ismainverse:0,id_hino:300},{idverse:26183,verse:`4. 
Jesus, sim, vem, completamente restaurar
O mundo que se arru\xEDna sem parar;
Sim, todas as coisas vem depressa transformar
Dai gl\xF3ria a Deus,  Jesus em breve vem!
`,verseorder:5,ismainverse:0,id_hino:300},{idverse:26184,verse:`5. 
Jesus, sim, vem, e sempiterna adora\xE7\xE3o
Daremos n\xF3s ao Rei de cora\xE7\xE3o;
Ao grande autor da nossa eterna salva\xE7\xE3o,
Dai gl\xF3ria a Deus, Jesus em breve vem!`,verseorder:6,ismainverse:0,id_hino:300},{idverse:26185,verse:`1. 
Cristo ja nos preparou
Um manjar que nos comprou,
E, agora, nos convida a cear;
Com celestial man\xE1,
Que de gra\xE7a Deus te d\xE1
Vem, faminto, tua alma saciar.
`,verseorder:1,ismainverse:0,id_hino:301},{idverse:26186,verse:`" Vem cear", o Mestre chama -"vem cear".
Mesmo hoje tu te podes saciar;
Poucos p\xE3es multiplicou,
\xC1gua em vinho transformou ,
Vem, faminto, a Jesus, "vem cear".
`,verseorder:2,ismainverse:1,id_hino:301},{idverse:26187,verse:`2. 
Eis disc\xEDpulos a voltar,
Sem os peixes apanhar,
Mas Jesus os manda outra vez partir,
Ao tornar \xE0 praia, ent\xE3o,
V\xEAem no fogo, peixe e p\xE3o ,
E Jesus, que os convida \xE0 ceia vir .
`,verseorder:3,ismainverse:0,id_hino:301},{idverse:26188,verse:`3. 
Quem sedento se achar,
Venha a Cristo, sem tarda,r
Pois o vinho sem mistura Ele d\xE1;
E tamb\xE9m da vida, o p\xE3o,
Que nos traz consola\xE7\xE3o;
Eis que tudo preparado j\xE1 est\xE1.
`,verseorder:4,ismainverse:0,id_hino:301},{idverse:26189,verse:`4. 
Breve, Cristo vai descer,
E a noiva receber
Seu lugar ao lado do Senhor Jesus;
Quem a fome suportou,
E a sede j\xE1 passou,
L\xE1 do c\xE9u ir\xE1 cear em santa luz.`,verseorder:5,ismainverse:0,id_hino:301},{idverse:26190,verse:`1. 
No mundo murmura-se tanto,
Entre os que crist\xE3os dizem ser;
Em vez de louvores h\xE1 pranto,
Fraqueza em lugar de poder.
Murmuram-assim no deserto,
Em Mara, Israel murmurou;
Oh! N\xE3o v\xEAem que Deus est\xE1 perto;
Jamais Seu aux\xEDlio negou.
`,verseorder:1,ismainverse:0,id_hino:302},{idverse:26191,verse:`Em vez de murmurares, canta
Um hino de louvor a Deus;
Jesus quer te dar vida santa,
Qual noiva levar-te p\`ra os c\xE9us.
`,verseorder:2,ismainverse:1,id_hino:302},{idverse:26192,verse:`2. 
Tu vives, irm\xE3o, murmurando,
Tal como um escravo do mal;
Se Deus a tua f\xE9 \`st\xE1 provando,
Tu n\xE3o tens raz\xE3o para tal.
Deus castiga aquele a quem ama,
De ti, tamb\xE9m n\xE3o se esqueceu;
Qual pai amoroso te chama,
E cuida, sim, do que \xE9 Seu.
`,verseorder:3,ismainverse:0,id_hino:302},{idverse:26193,verse:`3. 
E mesmo se as ondas rugirem,
No revolto e bravio mar,
Os c\xE9us poder\xE1s ver se abrirem,
Se um hino tua alma cantar,
N\xE3o temas ciladas, nem morte,
P\`ra cima tu deves olhar;
O leme segura bem forte,
At\xE9 do c\xE9u a luz raiar.
`,verseorder:4,ismainverse:0,id_hino:302},{idverse:26194,verse:`4. 
Se um hino cantar tu puderes,
Nas horas de grande afli\xE7\xE3o,
Ent\xE3o voar\xE1s, se quiseres,
At\xE9 a celeste mans\xE3o;
Nas asas da \xE1guia levado,
Bem perto do mar de cristal
E por fim ent\xE3o libertado,
\xC0 terra, chegar, celestial.`,verseorder:5,ismainverse:0,id_hino:302},{idverse:26195,verse:`1. 
Quando o sol brilhar em qualquer lugar,
Tu precisas de Jesus;
Quando escurecer, tudo fenecer,
Tu precisas de Jesus!
`,verseorder:1,ismainverse:0,id_hino:303},{idverse:26196,verse:`Eu preciso de Jesus,
Tu precisas de Jesus;
Pecador, vem para a luz
Que resplandeceu na cruz;
Tu precisas de Jesus!
`,verseorder:2,ismainverse:1,id_hino:303},{idverse:26197,verse:`2. 
P\`ra obter perd\xE3o, plena salva\xE7\xE3o,
Tu precisas de Jesus;
Para caminhar firme, sem errar;
Tu precisas de Jesus!
`,verseorder:3,ismainverse:0,id_hino:303},{idverse:26198,verse:`3. 
Mesmo havendo paz, calma mui veraz,
Tu precisas de Jesus;
Na persegui\xE7\xE3o, na tribula\xE7\xE3o,
Tu precisas de Jesus!
`,verseorder:4,ismainverse:0,id_hino:303},{idverse:26199,verse:`4. 
Quando a morte entrar em teu proprio lar,
Tu precisas de Jesus;
Ante o tribunal, decis\xE3o final,
Tu precisas de Jesus!`,verseorder:5,ismainverse:0,id_hino:303},{idverse:26200,verse:`1. 
Desprezando toda dor eu vou a cantar,
E o Calv\xE1rio, ao pecador, sempre apontar;
Flechas transpassaram-me padeci gran dor;
Mas Jesus, minha luz, fez-me vencedor.
`,verseorder:1,ismainverse:0,id_hino:304},{idverse:26201,verse:`\xC1 face adorada de Jesus
verei,
Com a grei amada, no c\xE9u estarei,
Na mans\xE3o dourada, hinos vou cantar
A Jesus, minha luz, que me quis salvar!
`,verseorder:2,ismainverse:1,id_hino:304},{idverse:26202,verse:`2. 
Pode a noite escura ser no servir Jesus,
Mas clamando, com poder, brilhar\xE1 a luz;
Podem os la\xE7os de Sat\xE3, todos me cercar,
Mas Jesus, pela cruz, faz-me triunfar!
`,verseorder:3,ismainverse:0,id_hino:304},{idverse:26203,verse:`3. 
Quando estou a contemplar a montanha al\xE9m,
Onde a luta governar, \`st\xE1 Jesus tamb\xE9m,
Que estende a Sua m\xE3o sobre n\xF3s dali;
Sei assim, que por mim, Cristo vela aqui.
`,verseorder:4,ismainverse:0,id_hino:304},{idverse:26204,verse:`4. 
Se entre as ondas estou sem luz, quase a perecer,
Meu piloto \xE9 Jesus, pode me valer:
O meu barco guia bem pelo bravo mar,
Sim, Jesus me conduz, posso sossegar!`,verseorder:5,ismainverse:0,id_hino:304},{idverse:26205,verse:`1. 
Soldados somos de Jesus
E campe\xF5es do bem, da luz;
Nos ex\xE9rcitos de Deus,
Batalhamos pelos c\xE9us,
Cantando, vamos combater,
O vil pecado e seu poder;
A batalha ganha est\xE1;
A vit\xF3ria Deus nos d\xE1.
`,verseorder:1,ismainverse:0,id_hino:305},{idverse:26206,verse:`Breve vamos terminar a batalha aqui,
E p\`ra sempre descansar com Jesus ali;
Todos os que s\xE3o fi\xE9is ao bom Capit\xE3o,
H\xE3o de receber laur\xE9is como galard\xE3o.
`,verseorder:2,ismainverse:1,id_hino:305},{idverse:26207,verse:`2. 
Levai o escudo, sim, da f\xE9,
Pois a peleja dura \xE9,
Mas promessa temos n\xF3s
De jamais lutarmos s\xF3s.
- As flechas do mal n\xE3o temer,
Mas combater at\xE9 vencer,
Olham os campe\xF5es p\`ros c\xE9us,
A vit\xF3ria vem de Deus.
`,verseorder:3,ismainverse:0,id_hino:305},{idverse:26208,verse:`3. 
Se algu\xE9m cansado se encontrar,
Sem for\xE7as para pelejar,
O Senhor quer te ajudar
A vit\xF3ria alcan\xE7ar;
O mal vencendo avan\xE7ai,
E hinos a Jesus cantai,
E da salva\xE7\xE3o falai;
Almas ao Senhor levai.`,verseorder:4,ismainverse:0,id_hino:305},{idverse:26209,verse:`1. 
A Palavra de Deus \xE9 para mim
Um tesouro sem igual em valor!
Fala do amor de Deus, do amor que n\xE3o tem fim;
Mais precioso do que ouro \xE9 este amor!
`,verseorder:1,ismainverse:0,id_hino:306},{idverse:26210,verse:`A Palavra de Deus \xE9 doce,
mais que o mel,
O que a toma pela f\xE9 h\xE1 de ser fiel,
Porque Deus nos concedeu o Emanuel,
Rocha viva donde mana leite e mel.
`,verseorder:2,ismainverse:1,id_hino:306},{idverse:26211,verse:`2. 
Luz que guia pela senda da paz,
E alumia os que trevas est\xE3o;
L\xE2mpada que nos faz ver os ardis de Satan\xE1s,
E que brilha mesmo na escurid\xE3o.
`,verseorder:3,ismainverse:0,id_hino:306},{idverse:26212,verse:`3. 
\xC9 um farol que sempre resplandeceu,
E que mostra o porto sa salva\xE7\xE3o;
Quem na arca j\xE1 entrou
e do mundo se esqueceu,
Chegar\xE1 por certo \xE0 eternal mans\xE3o.`,verseorder:4,ismainverse:0,id_hino:306},{idverse:26213,verse:`1. 
Ao Pai, ao Filho, o Redentor,
Ao divinal Consolador;
Ao trino Deus, sim, adorai,
Louvor eterno tributai, Am\xE9m.
`,verseorder:1,ismainverse:0,id_hino:307},{idverse:26214,verse:"2. ",verseorder:2,ismainverse:0,id_hino:307},{idverse:26215,verse:`1. 
S\xF3 o sangue de Jesus tem poder p\`ra nos lavar,
E de toda mancha nos limpa;
Todo o que lavado for nesse sangue expiador,
N\xE3o mais teme a morte nem a dor!
`,verseorder:1,ismainverse:0,id_hino:308},{idverse:26216,verse:`S\xF3 o sangue pode libertar,
E tamb\xE9m nos lavar;
Pelo sangue vamos n\xF3s alcan\xE7ar,
A morada de Jesus.
`,verseorder:2,ismainverse:1,id_hino:308},{idverse:26217,verse:`2. 
S\xF3 o sangue de Jesus, guarda-nos da corrup\xE7\xE3o,
E nos livra da vil tenta\xE7\xE3o;
Esse sangue tem valor, pois \xE9 pre\xE7o do amor
Com que resgatou-nos o Senhor.
`,verseorder:3,ismainverse:0,id_hino:308},{idverse:26218,verse:`3. 
S\xF3 o sangue de Jesus tem poder de preservar
O fiel que a Deus quer agradar;
Todo o ouro \xE9 sem valor p\`ra pagar o gran favor,
Do eterno e divino amor!`,verseorder:4,ismainverse:0,id_hino:308},{idverse:26219,verse:`1. 
Finda a lida deste dia, \xF3 bendito Salvador;
Eis-nos todos reunidos a cantar o Teu louvor;
Vem encher-nos do Teu santo e doce amor!
`,verseorder:1,ismainverse:0,id_hino:309},{idverse:26220,verse:`2. 
A leitura da Palavra, vem, Senhor, aben\xE7oar,
E no cora\xE7\xE3o dos crentes Teus ensinos vem gravar.
Tuas b\xEAn\xE7\xE3os continua a derramar!
`,verseorder:2,ismainverse:0,id_hino:309},{idverse:26221,verse:`3. 
Congregados nos acharmos constrangidos peloamor
Que emana do Teu nome, \xF3 amado Salvador;
Aprendendo no Teu livro, \xF3 Senhor! Am\xE9m.`,verseorder:3,ismainverse:0,id_hino:309},{idverse:26222,verse:`1. 
Jesus \xE9 o Redentor, que veio nos salvar,
Do mundo de horror, das ondas deste mar;
A todo o que crer vit\xF3ria lhe dar\xE1,
Tamb\xE9m real poder, que gozo lhe trar\xE1.
`,verseorder:1,ismainverse:0,id_hino:310},{idverse:26223,verse:`Avante eu vou, avante eu
vou,
Para entrar no c\xE9u, na divinal mans\xE3o;
Tenho gozo mui profundo; Jesus no cora\xE7\xE3o,
N\xE3o olhando para o mundo, avante eu vou.
`,verseorder:2,ismainverse:1,id_hino:310},{idverse:26224,verse:`2. 
Jesus meu Salvador, ao c\xE9u me levar\xE1,
E do destruidor, o corpo esmagar\xE1,
Ao caro Redentor, fiel aqui serei,
A Ele, com fervor, somente seguirei.
`,verseorder:3,ismainverse:0,id_hino:310},{idverse:26225,verse:`3. 
Trombeta soar\xE1 do nosso Criador;
A grei se elevar\xE1 dos santos do Senhor;
Em gl\xF3ria e poder Jesus regressar\xE1,
Seus servos h\xE3o de O ver, pois breve voltar\xE1.
`,verseorder:4,ismainverse:0,id_hino:310},{idverse:26226,verse:`4. 
Eis que vir\xE1 Jesus, a fim de me levar,
A desfrutar em luz, o Seu amor sem par;
Abra\xE3o, Isaque e J\xF3 ali no c\xE9u verei,
Ao lado de Jac\xF3, com eles cearei.`,verseorder:5,ismainverse:0,id_hino:310},{idverse:26227,verse:`1. 
Jesus salvou-me do mundo,
Ele \xE9 t\xE3o doce p\`ra mim;
Amor, Lhe tenho, profundo,
Por Sua gra\xE7a sem fim;
Quando ia eu no deserto,
Sem gozo, paz e sem luz,
Ele buscou-me, por certo,
T\xE9 que achou-me - Jesus!
`,verseorder:1,ismainverse:0,id_hino:311},{idverse:26228,verse:`Jesus, Jesus, Tu \xE9s meu Salvador,
Jesus, Jesus, s\xF3 Teu serei, Senhor.
Na sanda mui verdadeira,
Guia-me Tua m\xE3o,
E ao findar minha carreira,
Receberei galard\xE3o.
`,verseorder:2,ismainverse:1,id_hino:311},{idverse:26229,verse:`2. 
Jesus de Saron \xE9 a rosa,
L\xEDrio suave \xE9 p\`ra mim;
Ele \xE9 a rocha preciosa,
Onde h\xE1 gozo sem fim;
\xC9 da manh\xE3 a estrela,
Na noite escura e sem luz,
Tenho do\xE7ura em v\xEA-la,
Pois ela \xE9 meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:311},{idverse:26230,verse:`3. 
Na manjedora nascendo,
No mundo, s\xF3 batalhou;
Em meu lugar padecendo,
Sangue por mim derramou,
Ressurgiu da sepultura,
Subindo \xE0 dextra de Deus;
Breve vir\xE1 das alturas,
Descendo em nuvens dos c\xE9us.`,verseorder:4,ismainverse:0,id_hino:311},{idverse:26231,verse:`1. 
Subiu triufante p\`ra o c\xE9u, o Senhor:
Assim mesmo h\xE1 de voltar;
"Eis que venho breve", diz o Salvador,
Quando o alarido ecoar.
`,verseorder:1,ismainverse:0,id_hino:312},{idverse:26232,verse:`Com muita prud\xEAncia eu quero estar
Esperando por meu Senhor,
E sempre alerta aqui vigiar,
T\xE9 que venha o meu Salvador.
`,verseorder:2,ismainverse:1,id_hino:312},{idverse:26233,verse:`2. 
Com Cristo encontrar-me, gozoso irei.
Quando Ele nas nuvens descer,
Corpo igual ao Seu, nesse dia terei;
Que dia de gl\xF3ria h\xE1 de ser.
`,verseorder:3,ismainverse:0,id_hino:312},{idverse:26234,verse:`3. 
Bendizendo a Cristo, no c\xE9u entrarei;
Irei no mil\xEAnio ingressar;
S\xF3 com meu Jesus, eu aqui voltarei,
Pois todas na\xE7\xF5es vem julgar.
`,verseorder:4,ismainverse:0,id_hino:312},{idverse:26235,verse:`4. 
Vem sobre o Teu povo reinar, \xF3 Senhor!
Teu reinado aqui gozo traz,
\xD3 vem, prote\xE7\xE3o nos trazer, Salvador!
Vem, Jesus, com justi\xE7a e paz!
`,verseorder:5,ismainverse:0,id_hino:312},{idverse:26236,verse:`5. 
Vem, Cristo, meu Rei, Tua Esposa buscar;
Eu quero contigo subir;
Um canto perene Te quero entoar,
E a gl\xF3ria eterna fruir.`,verseorder:6,ismainverse:0,id_hino:312},{idverse:26237,verse:`1. 
Ao Pai Eterno dai louvor,
Ao Filho Seu tamb\xE9m,
E ao celestial Consolador,
Louvai p\`ra sempre am\xE9m!`,verseorder:1,ismainverse:0,id_hino:313},{idverse:26238,verse:`1. 
Um Pai bondoso tenho l\xE1 no c\xE9u,
Um Pai bondoso tenho l\xE1 no c\xE9u,
A Ele breve eu irei,
E a meu Pai, no c\xE9u verei;
Eu irei, eu irei, eu irei ao c\xE9u;
Eu irei, eu irei, eu irei ao c\xE9u;
Meu Pai me chama, vou andar
P\`ra Sua face contemplar.
`,verseorder:1,ismainverse:0,id_hino:314},{idverse:26239,verse:`2. 
Um Salvador eu tenho l\xE1 no c\xE9u;
Um Salvador eu tenho l\xE1 no c\xE9u;
A Ele presto eu irei,
Seu esplendor no c\xE9u verei,
Eu verei, eu verei, eu verei no c\xE9u,
Eu verei, eu verei, eu verei no c\xE9u.
Jesus ao c\xE9u me levar\xE1
E grande gozo me dar\xE1.
`,verseorder:2,ismainverse:0,id_hino:314},{idverse:26240,verse:`3. 
Uma coroa tenho l\xE1 no c\xE9u:
Uma coroa tenho l\xE1 no c\xE9u;
De espinhos a teve o Salvador,
Mas eu terei de esplendor,
Eu terei, eu terei, eu terei no c\xE9u;
Eu terei, eu terei, eu terei no c\xE9u;
Jesus coroa me dar\xE1,
Que minha fronte cingir\xE1.
`,verseorder:3,ismainverse:0,id_hino:314},{idverse:26241,verse:`4. 
N\xF3s nos encontraremos l\xE1 no c\xE9u;
N\xF3s nos encontraremos l\xE1 no c\xE9u;
Unidos todos ao Senhor,
E cheios sempre de amor;
Andarei, andarei, andarei p\`ro c\xE9u,
Andarei, andarei, andarei p\`ro c\xE9u,
A paz eterna l\xE1 terei.
A face de Jesus verei.
`,verseorder:4,ismainverse:0,id_hino:314},{idverse:26242,verse:"5. ",verseorder:5,ismainverse:0,id_hino:314},{idverse:26243,verse:`1. 
Oh! Amor bendito, Tu possues a mim!
Enches o infinito, oh! amor sem fim!
Vivo hoje em Cristo, vivo no Fiel;
Por Deus sou benquisto no Emanuel.
`,verseorder:1,ismainverse:0,id_hino:315},{idverse:26244,verse:`Oh! Amor bendito: Tu possues
a mim!
Enches o infinito, oh! amor sem fim!
`,verseorder:2,ismainverse:1,id_hino:315},{idverse:26245,verse:`2. 
Paz e gl\xF3ria eterna, h\xE1 p\`ra min no c\xE9u,
Vida sempiterna com o nosso Deus.
Em verdade sinto vida no Senhor,
E do c\xE9u pressinto gozo e esplendor!
`,verseorder:3,ismainverse:0,id_hino:315},{idverse:26246,verse:`3. 
Cora\xE7\xE3o, desfruta deste grande amor;
Na Paz ou na luta, faz-te vencedor;
Que este amor \xE9 forte, viu-se ali na cruz;
Forte mais que a morte: O amor de Jesus!`,verseorder:4,ismainverse:0,id_hino:315},{idverse:26247,verse:`1. 
Saindo do Egito em busca de Si\xE3o,
Meu Salvador guiou-me com Sua forte m\xE3o;
Levou-me em vit\xF3rias pelo fogo e o amar;
Agora, canto a Deus, pela salva\xE7\xE3o sem par.
`,verseorder:1,ismainverse:0,id_hino:316},{idverse:26248,verse:`N\xE3o busquem mais a
Mim na escravid\xE3o,
Ergui a tenda, enfim, na terra de Si\xE3o.
N\xE3o busquem mais a Mim na escravid\xE3o,
Ergui a tenda, enfim, na terra de Si\xE3o.
`,verseorder:2,ismainverse:1,id_hino:316},{idverse:26249,verse:`2. 
Mui perto de Jesus e vendo o galard\xE3o,
P\`ra Cana\xE3 eu sigo, l\xE1 est\xE1 meu cora\xE7\xE3o;
Jesus p\`ra grande heran\xE7a, h\xE1 de me levar,
A quem deu-me esperan\xE7a, eu hei de aqui louvar.
`,verseorder:3,ismainverse:0,id_hino:316},{idverse:26250,verse:`3. 
Andei eu pelos campos, onde fruto achei;
Em Hebron, uvas vivas tamb\xE9m eu apanhei,
Aqui melhor mel, vinho e leite eu encontrei,
P\`ra escravid\xE3o do Egito jamais eu voltarei.
`,verseorder:4,ismainverse:0,id_hino:316},{idverse:26251,verse:`4. 
As lutas desta vida eu n\xE3o sinto mais,
Pois sempre vou cantando Sua gra\xE7a, amor e paz;
Eu vou marchando para o eterno lar,
E breve, l\xE1 no c\xE9u - minha p\xE1tria - hei de estar.`,verseorder:5,ismainverse:0,id_hino:316},{idverse:26252,verse:`1. 
Como brilha do oriente
O rel\xE2mpago ao ocidente,
Deste mundo voltar\xE1 do c\xE9u, Jesus;
Levar\xE1 Sua Noiva em gozo
Ao Seu lado mui ditoso,
Para o lar eterno de celeste luz.
`,verseorder:1,ismainverse:0,id_hino:317},{idverse:26253,verse:`Em breve, do c\xE9u, Jesus vir\xE1
nos buscar;
Com gran poder, vir\xE1 Jesus.
E n\xF3s devemos todos preparados estar,
Com vestes brancas, e brilhando como luz.
`,verseorder:2,ismainverse:1,id_hino:317},{idverse:26254,verse:`2. 
Eis que a porta est\xE1 aberta
Para a alma que desperta,
Aceitando a Jesus, o Salvador,
Pois Seu sangue derramado
Tem vencido o vil pecado,
Carregando, no madeiro nossa dor!
`,verseorder:3,ismainverse:0,id_hino:317},{idverse:26255,verse:`3. 
Jesus vem, vem triunfante,
Com Seus olhos chamejantes,
Pois Sua face brilhar\xE1 qual luz do sol!
Mui felizes os que clamam,
Sua vinda, pois O amam,
E no c\xE9u, os nomes escritos t\xEAm no rol.
`,verseorder:4,ismainverse:0,id_hino:317},{idverse:26256,verse:`4. 
Jesus vem, qual sol raiando,
Este mundo iluminando,
A Igreja subir\xE1 ao encontro Seu;
Na Sua m\xE3o traz luz brilhante,
Qual estrela cintilante,
E os louvores se ouvir\xE3o nos altos c\xE9us!
`,verseorder:5,ismainverse:0,id_hino:317},{idverse:26257,verse:"5. ",verseorder:6,ismainverse:0,id_hino:317},{idverse:26258,verse:`1. 
Quero vos contar a hist\xF3ria dum var\xE3o,
Que Jesus outrora relatou,
O qual tinha dois filhos em feliz mans\xE3o,
T\xE9 que o mais jovem o deixou.
`,verseorder:1,ismainverse:0,id_hino:318},{idverse:26259,verse:`Filho desleal ao lar paternal,
Queiras hoje regressar!
Pois o Pai est\xE1 por ti esperando l\xE1;
Pecador, \xF3 volta p\`ra teu lar!
`,verseorder:2,ismainverse:1,id_hino:318},{idverse:26260,verse:`2. 
O mais mo\xE7o disse ao seu bondoso pai:
"A heran\xE7a minha queiras dar";
E a sua fazenda, alegremente vai
Num pa\xEDs distante desfrutar.
`,verseorder:3,ismainverse:0,id_hino:318},{idverse:26261,verse:`3. 
O incauto jovem os seus bens esbanjou
Com seus camaradas, no prazer;
Eis que vindo a fome, do seu pai se lembrou
Que pudera sempre o suster.
`,verseorder:4,ismainverse:0,id_hino:318},{idverse:26262,verse:`4. 
O perdido filho, ao alr por fim, voltou,
Mui arrependido do que fez;
E o pai contente, amoroso o beijou,
Pois seu filho via outra vez!`,verseorder:5,ismainverse:0,id_hino:318},{idverse:26263,verse:`1. 
Inda h\xE1 lugar, p\`ra ti, \xF3 pecador!
Ouve o convite do teu Salvador.
Entra, entra, \`inda h\xE1 p\`ra ti lugar.
`,verseorder:1,ismainverse:0,id_hino:319},{idverse:26264,verse:`2. 
A tua vida breve vai findar,
As sombras crescem, onde vais morar?
Entra, entra, \`inda h\xE1 p\`ra ti lugar.
`,verseorder:2,ismainverse:0,id_hino:319},{idverse:26265,verse:`3. 
Eis l\xE1 no c\xE9u, Jesus nos preparou
Bodas festivais, e nos convidou,
Entra, entra, \`inda h\xE1 p\`ra ti lugar.
`,verseorder:3,ismainverse:0,id_hino:319},{idverse:26266,verse:`4. 
A multid\xE3o que salva l\xE1 j\xE1 est\xE1,
Vem, pois t\`espera a ela te ajuntar.
Entra, entra, \`inda h\xE1 p\`ra ti lugar.
`,verseorder:4,ismainverse:0,id_hino:319},{idverse:26267,verse:`5. 
V\xEAs tu a porta aberta por Jesus?
Ele te chama, vem p\`ra Sua luz;
Entra, entra, \`inda h\xE1 p\`ra ti lugar.
`,verseorder:5,ismainverse:0,id_hino:319},{idverse:26268,verse:`6. 
Enche-se a sala, vem sem demorar;
Quem se descuida, perde o seu lugar;
Entra, entra, \`inda h\xE1 p\`ra ti lugar.
`,verseorder:6,ismainverse:0,id_hino:319},{idverse:26269,verse:`7. 
Ali se encontra gozo, paz sem fim,
Gl\xF3ria te espera; a luta ter\xE1 fim.
Entra, entra, \`inda h\xE1 p\`ra ti lugar.`,verseorder:7,ismainverse:0,id_hino:319},{idverse:26270,verse:`1. 
Queres tu seguir a Cristo,
E andar na Sua luz?
Pelo mundo sempre visto,
Por levar a sua cruz?
`,verseorder:1,ismainverse:0,id_hino:320},{idverse:26271,verse:`2. 
Queres hoje dar o passo,
Para seres um crist\xE3o?
Vem, quebranta triste la\xE7o
Do pecado e sedu\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:320},{idverse:26272,verse:`3. 
A cidade na montanha,
N\xE3o se pode esconder,
E tamb\xE9m, a f\xE9 tamanha
Que \xE0 vida deu poder.
`,verseorder:3,ismainverse:0,id_hino:320},{idverse:26273,verse:`4. 
Amas tu a Jesus Cristo,
Que por ti Sua vida deu?
Vem, ent\xE3o, declara isto -
J\xFAbilo ter\xE1s no c\xE9u.
`,verseorder:4,ismainverse:0,id_hino:320},{idverse:26274,verse:`5. 
\xD3 v\xEA bem o teu intento,
Custa tua vida, sim!
N\xE3o resistas! Com alento,
Vem a Cristo, a luz sem fim.
`,verseorder:5,ismainverse:0,id_hino:320},{idverse:26275,verse:`6. 
Vem, confessa teus pecados
Junto \xE0 cruz do Salvador;
Pede gra\xE7a de segui-Lo,
Com coragem e amor.
`,verseorder:6,ismainverse:0,id_hino:320},{idverse:26276,verse:`7. 
Considera o grande gozo
Que no c\xE9u espera, sim;
Aqui lutas, l\xE1 repouso,
Galard\xE3o ter\xE1s enfim.`,verseorder:7,ismainverse:0,id_hino:320},{idverse:26277,verse:`1. 
S\xF3 em Jesus fuir\xE1s paz e gozo,
Tu, pecador, que tens sede de amor;
S\xF3 em Jesus poder\xE1s ser ditoso;
NEle a gra\xE7a obt\xE9ns do Senhor.
`,verseorder:1,ismainverse:0,id_hino:321},{idverse:26278,verse:`2. 
S\xF3 em Jesus achar\xE1s forte escudo
Para abrigar-te dos dardos do mal;
NEle, oculto das dores, de tudo,
Receber\xE1s uma paz divinal.
`,verseorder:2,ismainverse:0,id_hino:321},{idverse:26279,verse:`3. 
S\xF3 em Jesus, sobre a divina rocha,
Salvar-te-\xE1s deste mundo, qual mar;
S\xF3 em Jesus, sim na luz desta tocha,
Podes o santo caminho trilhar.
`,verseorder:3,ismainverse:0,id_hino:321},{idverse:26280,verse:`4. 
S\xF3 em Jesus, neste porto seguro,
Podes tua alma qual nau ancorar,
S\xF3 em Jesus, um brilhante futuro,
\xD3 pecador, poder\xE1s encontrar.
`,verseorder:4,ismainverse:0,id_hino:321},{idverse:26281,verse:`5. 
S\xF3 em Jesus, deixa tudo portanto,
Quer seja o mundo, o  mal ou pecar;
Vem p\`ra Jesus, que por Deus te garanto,
Que paz e gozo ir\xE1s desfrutar.`,verseorder:5,ismainverse:0,id_hino:321},{idverse:26282,verse:`1. 
S\xE3o as santas Escrituras
Que nos contam de Jesus,
O qual vindo das alturas
Fez brilhar nas trevas e luz!
P\`ra nos dar eterna vida,
\`T\xE9 a morte se humilhou;
Tendo j\xE1 vencido a lida,
Deus, o Pai, O exaltou.
`,verseorder:1,ismainverse:0,id_hino:322},{idverse:26283,verse:`Nunca mais vai ser ouvido
Outro conto de amor,
Que converta um perdido,
E rebelde pecador,
Como o santo Evangelho,
Que nos fala de perd\xE3o,
E transforma o homem velho
Numa nova cria\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:322},{idverse:26284,verse:`2. 
Sobre a cruz foi derramado
O Seu santo remidor;
Duma lan\xE7a traspassado
Foi o lado do Senhor;
Suas m\xE3os e p\xE9s pregaram
Com uns cravos, sobre a cruz;
Tudo isto me contaram
Da bondade de Jesus.
`,verseorder:3,ismainverse:0,id_hino:322},{idverse:26285,verse:`3. 
Quero continuar ouvindo
A hist\xF3ria do Senhor;
Salva\xE7\xE3o estou fruindo
Deste conto de amor!
Do ju\xEDzo fui liberto,
Da condena\xE7\xE3o, da dor,
Pela B\xEDblia estou bem certo,
Que Jesus \xE9 o Salvador.`,verseorder:4,ismainverse:0,id_hino:322},{idverse:26286,verse:`1. 
Levantai os vossos olhos para cima,
\xD3 remidos do Senhor Jesus!
A figueira mostra que se aproxima
O ver\xE3o: Brotos j\xE1 produz!
`,verseorder:1,ismainverse:0,id_hino:323},{idverse:26287,verse:`Levantai, levantai!
Vossos olhos para o c\xE9u donde Jesus vir\xE1;
Levantai, levantai!
A reden\xE7\xE3o breve se far\xE1.
`,verseorder:2,ismainverse:1,id_hino:323},{idverse:26288,verse:`2. 
Muitos dizem que Jesus est\xE1 tardando,
Para vir buscar o povo Seu;
Qual  nos dias de No\xE9, est\xE3o pecando,
Sem temer o bondoso Deus.
`,verseorder:3,ismainverse:0,id_hino:323},{idverse:26289,verse:`3. 
Muitos, como Fara\xF3, est\xE3o dizendo:
"Quem \xE9 o Senhor p\`ra lhe ouvir?"
E tamb\xE9m o cora\xE7\xE3o endurecendo:
Mas as pragas est\xE3o p\`ra vir.
`,verseorder:4,ismainverse:0,id_hino:323},{idverse:26290,verse:`4. 
Ser arrebatado, eu, p\`ro c\xE9u, quem der\xE1!
Pois a Igreja Cristo levar\xE1.
A figueira est\xE1 em flor, \xE9 primavera,
Levantai os vossos olhos j\xE1.`,verseorder:5,ismainverse:0,id_hino:323},{idverse:26291,verse:`1. 
Meu Jesus \xE9 o melhor amigo,
NEle ponho toda a minha f\xE9;
Minhas chagas quer sarar,
E minh\`alma consolar,
Oh, Jesus \xE9 o melhor amigo!
`,verseorder:1,ismainverse:0,id_hino:324},{idverse:26292,verse:`Jesus \xE9 o melhor amigo,
Jesue \xE9 o melhor amigo,
Sempre prestes a ajudar,
Ele a todos quer salvar,
Sim, Jesus \xE9 o melhor amigo!
`,verseorder:2,ismainverse:1,id_hino:324},{idverse:26293,verse:`2. 
Meu Jesus \xE9 o fiel amigo,
E me d\xE1 Sua for\xE7a e poder;
Torna em gozo minha dor,
Me inspira, d\xE1 valor;
Oh! Jesus \xE9 o melhor amigo!
`,verseorder:3,ismainverse:0,id_hino:324},{idverse:26294,verse:`3. 
Quando as ondas se embravecem,
E meu barco querem afundar,
Ele d\xE1-me calma e paz,
E bonan\xE7a sempre traz,
Sim Jesus \xE9 o melhor amigo!
`,verseorder:4,ismainverse:0,id_hino:324},{idverse:26295,verse:`4. 
Ele deu a Sua pr\xF3pria vida
P\`ra salvar o \xEDmpio pecador;
Oh! Jamais me quer deixar,
Nunca me desamparar!
Sim, Jesus \xE9 o melhor amigo!`,verseorder:5,ismainverse:0,id_hino:324},{idverse:26296,verse:`1. 
Noutro tempo, sei como eu andei,
Do pecado sob o v\xE9u,
Mas do c\xE9u raiou e em mim entrou
A luz do glorioso c\xE9u.
`,verseorder:1,ismainverse:0,id_hino:325},{idverse:26297,verse:`Em meu cora\xE7\xE3o a luz raiou
Desde a gl\xF3ria, pois Jesus me amou;
E agora vivo sempre alegre,
Depois que luz do c\xE9u raiou!
`,verseorder:2,ismainverse:1,id_hino:325},{idverse:26298,verse:`2. 
E poder me d\xE1, que s\xF3 vem de l\xE1,
De Jesus, que me salvou;
E eu vivo aqui, a louvar a Deus,
Que minh\`alma libertou.
`,verseorder:3,ismainverse:0,id_hino:325},{idverse:26299,verse:`3. 
O descanso entrou e o lutar findou,
Com Jesus, meu Salvador;
Seu amor sem fim revelado a mim,
Enche-me de paz e amor.
`,verseorder:4,ismainverse:0,id_hino:325},{idverse:26300,verse:`4. 
Vou morar, ent\xE3o, na feliz Si\xE3o,
Com meu Salvador Jesus,
Para sempre ali, gozarei prazer
L\xE1 na gloriosa luz.`,verseorder:5,ismainverse:0,id_hino:325},{idverse:26301,verse:`1. 
\xD3 meu Jesus, quando lutas no caminho encontrar,
A Tua m\xE3o divina vem me ajudar;
N\xE3o temerei amparado por Ti mesmo, meu Jesus,
\xD3 Salvador, a vit\xF3ria me vem por Tua luz,
Por Ti espero somente, meu Senhor,
Para andar de valor em valor;
\xD3 meu Jesus, minhas for\xE7as hei de ver mais aumentar;
Tendo a f\xE9; Tua gra\xE7a me h\xE1 de bastar.
`,verseorder:1,ismainverse:0,id_hino:326},{idverse:26302,verse:`2. 
\xD3 meu Jesus, encostado em Teu bra\xE7o vou andar,
E com amor na s\xE3 Palavra meditar;
Sempre assim na peleja,
por Jesus eu vencerei,
E pela f\xE9, abrigado do mal estarei;
J\xE1 bem armado as trevas desfarei,
Pelo poder de Jesus, o meu Rei!
\xD3 meu Jesus, que me d\xE1s,
no cora\xE7\xE3o, do Teu amor,
Hei de Te ver glorioso, no c\xE9u d\`esplendor!
`,verseorder:2,ismainverse:0,id_hino:326},{idverse:26303,verse:`3. 
\xD3 meu Jesus, o Teu nome invocarei com mui amor,
Todo o poder Te foi entregue Salvador;
De todo o ser, o joelho ante Ti se dobrar\xE1;
Que \xE9s o Senhor, toda l\xEDngua testificar\xE1,
Os nossos p\xE9s, Salvador queiras firmar
Na rocha, que n\xE3o se pode abalar;
\xD3 meu Jesus, Tua gra\xE7a quero sempre procurar,
T\xE9 que eu v\xE1 nos Teus bra\xE7os enfim repousar.`,verseorder:3,ismainverse:0,id_hino:326},{idverse:26304,verse:`1. 
Jesus Cristo, o Teu nome
\xC9 t\xE3o doce para mim -
Tua do\xE7ura incompar\xE1vel
Enche-me de amor sem fim.
Tua do\xE7ura incompar\xE1vel
Enche-me de amor sem fim.
`,verseorder:1,ismainverse:0,id_hino:327},{idverse:26305,verse:`2. 
\xC9s a estrela resplendente,
Que rebrilha com fulgor,
E dirige os meus passos,
Neste mundo de horror.
E dirige os meus passos,
Neste mundo de horror.
`,verseorder:2,ismainverse:0,id_hino:327},{idverse:26306,verse:`3. 
Teus s\xE3o os eternos bra\xE7os,
Protegendo o seguidor,
Que n\xE3o figue enla\xE7ado
P\`lo maligno tentador.
Que n\xE3o fique enla\xE7ado
P\`lo maligno tentador.
`,verseorder:3,ismainverse:0,id_hino:327},{idverse:26307,verse:`4. 
Com meu Salvador amado
Posso alegre prosseguir,
E com Ele eterno gozo
Eu terei l\xE1 no porvir.
E com Ele eterno gozo
Eu terei l\xE1 no porvir.
`,verseorder:4,ismainverse:0,id_hino:327},{idverse:26308,verse:`5. 
Oh! Teu nome precioso
Continua a soar
Pelo mundo de pecado,
Para as almas despertar!
Pelo mundo de pecado,
Para as almas despertar!`,verseorder:5,ismainverse:0,id_hino:327},{idverse:26309,verse:`1. 
O p\xE3o da vida, descido dos c\xE9us,
D\xE1 paz, sa\xFAde e vigor,
O p\xE3o celeste, mandado por Deus,
\xC9 Cristo, o Salvador;
\xD3 Redentor, vem sem tardar,
Do pecador o mal sanar!
Se algum perdido buscar Tua luz,
Depressa vem a paz lhe dar;
N\xE3o tardes mais, amoroso Jesus,
\xD3 vem me confortar!
`,verseorder:1,ismainverse:0,id_hino:328},{idverse:26310,verse:`2. 
H\xE1 gozo santo p\`ra quem tem a luz,
Em se lembrar do seu Senhor,
\xC9 s\xF3 falar do amor de Jesus,
O grande Redentor!
Teu jugo \xE9 doce, meu Senhor,
Teu fardo \xE9 leve, que amor!
Se eu n\xE3o posso levar minha cruz,
Depressa vem me ajudar;
N\xE3o tardes mais, amoroso Jesus,
\xD3 vem me confortar!
`,verseorder:2,ismainverse:0,id_hino:328},{idverse:26311,verse:`3. 
Por duras provas e persegui\xE7\xF5es,
Tu fazes o fiel passar;
E quem vencer h\xE1 de ter galard\xE3o,
Tamb\xE9m no c\xE9u lugar,
Eu l\xE1 verei Teu esplendor,
A Tua gl\xF3ria, Salvador;
Se n\xE3o puder carregar minha cruz,
Depressa vem me auxiliar;
N\xE3o tardes mais, amoroso Jesus,
\xD3 vem me confortar!
`,verseorder:3,ismainverse:0,id_hino:328},{idverse:26312,verse:`4. 
Jesus, o Teu insond\xE1vel amor,
Me faz sentir no cora\xE7\xE3o;
O amor de Deus, este santo amor,
E viverei, ent\xE3o;
A Ti, Jesus, eu dou louvor;
Tu me d\xE1s gra\xE7a e vigor;
Tu \xE9s o p\xE3o que a vida produz;
Minh\`alma vem alimentar;
N\xE3o tardes mais, amoroso Jesus
\xD3 vem me confortar!`,verseorder:4,ismainverse:0,id_hino:328},{idverse:26313,verse:`1. 
Jesus nos bra\xE7os me tomou,
Jesus me deu a paz;
Agora canto a quem me amou,
Jesus \xE9 minha paz!
`,verseorder:1,ismainverse:0,id_hino:329},{idverse:26314,verse:`2. 
Faminto e sedento andei,
Sem ter descanso, aqui;
Mas \xE1gua viva eu aceitei;
Achei descanso aqui!
`,verseorder:2,ismainverse:0,id_hino:329},{idverse:26315,verse:`3. 
Onipotente p\`ra me amar,
\xC9 Cristo, o Rei dos c\xE9us;
Alegremente vou cantar;
Achei descanso em Deus!`,verseorder:3,ismainverse:0,id_hino:329},{idverse:26316,verse:`1. 
Pela f\xE9 que uma vez foi entregue aos santos,
Que nos vem por Jesus, e nos seca o pranto,
Podem os crentes, sem ter nenhum espanto,
Prosseguir nas pisadas do Senhor.
`,verseorder:1,ismainverse:0,id_hino:330},{idverse:26317,verse:`Nas tristezas, nas lutas, na
dor,
Recorriam ao caro Jesus;
A vit\xF3ria lhes dava o Senhor,
Pelo sangue vertido na cruz.
`,verseorder:2,ismainverse:1,id_hino:330},{idverse:26318,verse:`2. 
Os crist\xE3os foram por tribunais vis julgados,
Nas pris\xF5es, inocentes, \xE0 for\xE7a lan\xE7ados,
E de seus bens terrestres despojados,
Mas alegres seguiram o Senhor.
`,verseorder:3,ismainverse:0,id_hino:330},{idverse:26319,verse:`3. 
Peregrinos os santos no mundo andavam,
Nas carvenas da terra se refugiavam,
E ali reunidos, celebravam
A Jesus, seu amado Salvador.
`,verseorder:4,ismainverse:0,id_hino:330},{idverse:26320,verse:`4. 
Pela f\xE9 \xE9 que n\xF3s, crentes, sempre devemos
Nos firmar em Jesus e assim venceremos,
E um dia, no c\xE9u encontraremos
Os fi\xE9is, que amaram o Senhor.
`,verseorder:5,ismainverse:0,id_hino:330},{idverse:26321,verse:`5. 
L\xE1 no c\xE9u n\xE3o teremos mais dor,
E o pranto ali h\xE1 de findar,
Quano perto do nosso Senhor,
Nossa alma, enfim, repousar.`,verseorder:6,ismainverse:0,id_hino:330},{idverse:26322,verse:`1. 
\xD3 caro Salvador, manancial de amor,
Te sinto em mim!
Teu gozo a mim vir\xE1, jamais me faltar\xE1,
O qual me levar\xE1 ao c\xE9u enfim.
`,verseorder:1,ismainverse:0,id_hino:331},{idverse:26323,verse:`2. 
\xD3 caro Salvador, riqueza de amor,
Vela por mim;
Tem-me na Tua m\xE3o, \xF3 grande Capit\xE3o,
E lutarei, ent\xE3o, contigo assim.
`,verseorder:2,ismainverse:0,id_hino:331},{idverse:26324,verse:`3. 
\xD3 caro Salvador, meu vero intercessor,
Ora por mim;
Eu posso ent\xE3o falar, oh! deste amor sem par,
Que veio me salvar do mundo, sim!
`,verseorder:3,ismainverse:0,id_hino:331},{idverse:26325,verse:`4. 
\xD3 caro Salvador, com Teu poder, Senhor,
Opera em mim;
Na senda de valor, repleto de vigor,
Sem ter nenhum temor, irei assim!`,verseorder:4,ismainverse:0,id_hino:331},{idverse:26326,verse:`1. 
Para o c\xE9u eu vou, embora, for nublado;
O caminho, \xE0s vezes, espinhoso est\xE1,
Por Jesus, meu Mestre, sempre bem guiado,
Em Seus passos sigo, at\xE9 que chegue l\xE1.
`,verseorder:1,ismainverse:0,id_hino:332},{idverse:26327,verse:`Aleluia P\`ro c\xE9u vou
caminhando,
Nada me desanimar\xE1!
Para o c\xE9u eu me vou aproximando,
Sempre meu Jesus me guiar\xE1!
`,verseorder:2,ismainverse:1,id_hino:332},{idverse:26328,verse:`2. 
Para o c\xE9u eu vou, ainda que lutando
Contra tenta\xE7\xF5es, que surgem ante mim;
Sob o sangue de Jesus me abrigando,
Escondido e bem seguro, estou assim.
`,verseorder:3,ismainverse:0,id_hino:332},{idverse:26329,verse:`3. 
Para o c\xE9u eu vou, embora, mui cansado,
E amigos velhos, voltem-se p\`ra tr\xE1s.
Forte sigo co\`o Senhor Jesus ao lado,
Convidando a outros aceitar a paz.
`,verseorder:4,ismainverse:0,id_hino:332},{idverse:26330,verse:`4. 
Para o c\xE9u eu vou, alegre caminhando;
Breve findar\xE1 a peregrina\xE7\xE3o,
Se cansado, neste mundo vou andando,
Eu repousarei na divinal mans\xE3o.`,verseorder:5,ismainverse:0,id_hino:332},{idverse:26331,verse:`1. 
\xC9 o meu lar, o c\xE9u, o c\xE9u de luz;
Livre da carne, o v\xE9u, verei Jesus;
Oh! Que feliz serei ao lado do Senhor,
Louvando ao meu Rei, o Salvador!
`,verseorder:1,ismainverse:0,id_hino:333},{idverse:26332,verse:`2. 
\xC9 o meu lar, o c\xE9u, o c\xE9u de luz;
Por mim sofreu, qual r\xE9u, o meu Jesus;
Oh! Que feliz serei no reino de amor,
Pois sempre gozarei, com meu Senhor!
`,verseorder:2,ismainverse:0,id_hino:333},{idverse:26333,verse:`3. 
\xC9 o meu lar, o c\xE9u, o c\xE9u de luz;
E com os meus trof\xE9us vou a Jesus;
Oh! Que feliz serei quando, com mui fervor,
Aos p\xE9s estar do Rei, dando louvor!`,verseorder:3,ismainverse:0,id_hino:333},{idverse:26334,verse:`1. 
O fim de todas coisas vem, n\xE3o tarda, cuidado!
N\xE3o queiras hoje recusasr a gra\xE7a do Salvador;
Procura bem depressa, ficar abrigado,
No sangue do Cordeiro, no sangue remidor.
`,verseorder:1,ismainverse:0,id_hino:334},{idverse:26335,verse:`Que sangue precioso,
saiu de meu Jesus!
Que sangue glorioso, vertido foi, na cruz!
`,verseorder:2,ismainverse:1,id_hino:334},{idverse:26336,verse:`2. 
O fundamento do crist\xE3o \xE9 a rocha eleita,
Por isso pode resistir os ventos da tenta\xE7\xE3o.
A todo o que sincero, a Cristo aceita,
O sangue do Cordeiro garante salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:334},{idverse:26337,verse:`3. 
Quem pois ouvir, h\xE1 de viver com Cristo na gl\xF3ria,
Quem n\xE3o ouvir h\xE1 de morrer, tormentos padecer\xE1,
Pois quem deseja sobre o  mal a vit\xF3ria,
No sangue do Cordeiro poder encontrar\xE1.`,verseorder:4,ismainverse:0,id_hino:334},{idverse:26338,verse:`1. 
Elias no Carmelo orou
Com insist\xEAncia a Deus;
Curvado ao p\xF3, com f\xE9 clamou
Por chuva l\xE1 dos c\xE9us.
`,verseorder:1,ismainverse:0,id_hino:335},{idverse:26339,verse:`Chuva, manda Senhor,
A chuva do santo Consolador;
Manda chuva forte, Salvador:
A chuva de poder.
`,verseorder:2,ismainverse:1,id_hino:335},{idverse:26340,verse:`2. 
Vai, servo meu, do monte olhar
Se nuvens negras v\xEAm;
Pois pode agora Deus mandar
A chuva a n\xF3s tamb\xE9m.
`,verseorder:3,ismainverse:0,id_hino:335},{idverse:26341,verse:`3. 
Uma nuvem surge qual u\`a m\xE3o,
No horizonte al\xE9m,
Ao longe ouve-se um trov\xE3o:
Sim, muita chuva vem.
`,verseorder:4,ismainverse:0,id_hino:335},{idverse:26342,verse:`4. 
\xD3 crente frio, vai buscar
A Deus em ora\xE7\xE3o,
E tua vida consagrar,
P\`ra receber a un\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:335},{idverse:26343,verse:`1. 
\xD3 bom Jesus, tesouro de poder,
Quero sentir-Te, sempre no meu ser!
Faz-me andar, oh! sempre nesta luz,
Pois dentro de mim vives, \xF3 meu bom Jesus!
`,verseorder:1,ismainverse:0,id_hino:336},{idverse:26344,verse:`Oh! Em mim, j\xE1 enfim,
ressuscitou Jesus;
Das trevas livre estou assim, por Sua luz!
`,verseorder:2,ismainverse:1,id_hino:336},{idverse:26345,verse:`2. 
\xD3 bom Jesus, \xF3 fonte de amor,
Faz-me ouvir a Tua voz, Senhor;
Sempre viver de Ti eu quero sim,
\xD3 manifesta mais o Teu poder em mim!
`,verseorder:3,ismainverse:0,id_hino:336},{idverse:26346,verse:`3. 
\xD3 bom Jesus, real Consolador,
Longe de Ti est\xE1 o pecador!
Vives em mim, \xE9s minha salva\xE7\xE3o;
S\xF3 \xE9 feliz, o que Te tem no cora\xE7\xE3o!`,verseorder:4,ismainverse:0,id_hino:336},{idverse:26347,verse:`1. 
Quero sempre, Jesus, receber Tua luz,
Revestir-me do Consolador;
E um dia nos c\xE9us, junto ao trono de Deus,
Fruirei abundante amor!
`,verseorder:1,ismainverse:0,id_hino:337},{idverse:26348,verse:`\xD3 Jesus, guarda o meu cora\xE7\xE3o,
Nele faz Tua eterna habita\xE7\xE3o,
Quero sempre Teu nome louvar!
Pois quiseste minh\`alma resgatar!
`,verseorder:2,ismainverse:1,id_hino:337},{idverse:26349,verse:`2. 
Sob o sangue estarei abrigado da lei,
Do pecado e da vil tenta\xE7\xE3o;
Neste sangue h\xE1 paz, gozo que satisfaz
E certeza de vero perd\xE3o!
`,verseorder:3,ismainverse:0,id_hino:337},{idverse:26350,verse:`3. 
Oh! Que consola\xE7\xE3o eu terei em Si\xE3o,
Vendo a face do meigo Jesus;
Um eterno louvor eu darei ao Senhor,
Ao entrar na cidade da luz!`,verseorder:4,ismainverse:0,id_hino:337},{idverse:26351,verse:`1. 
Oh! Jesus ressuscitado,
Grande \xE9 o Teu poder!
S\xEA comigo, \xF3 meu amado,
D\xE1-me a gra\xE7a e o saber.
`,verseorder:1,ismainverse:0,id_hino:338},{idverse:26352,verse:`P\`ra fazer a Tua vontade,
Quero de ti mais poder;
D\xE1-me a Tua santidade,
Quero s\xF3 p\`ra Ti viver.
`,verseorder:2,ismainverse:1,id_hino:338},{idverse:26353,verse:`2. 
Meu Senhor, Tu \xE9s meu guia,
D\xE1-me for\xE7a, vida e luz;
Plena paz e alegria,
Me concede, \xF3 meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:338},{idverse:26354,verse:`3. 
\xC9s a luz t\xE3o radiosa,
Que me traz consola\xE7\xE3o;
Com Tua morte dolorosa,
Tu me deste a salva\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:338},{idverse:26355,verse:`4. 
Quero, \xF3 Deus, agradecer-Te
O Teu grande, imenso amor,
Porque j\xE1 me concedeste
O real Consolador.`,verseorder:5,ismainverse:0,id_hino:338},{idverse:26356,verse:`1. 
Eis que surge um povo forte,
Revestido de poder;
E n\xE3o teme nem a morte,
Quem a ele pertencer;
E ter\xE1 sublime sorte,
Pois com Cristo ao c\xE9u vai,
Podes tu dizer tamb\xE9m.
"Sou um dos tais"?
`,verseorder:1,ismainverse:0,id_hino:339},{idverse:26357,verse:`Um dos tais. Um dos tais.
Podes tu tamb\xE9m dizer: "Sou um dos tais"?
Um dos tais, um dos tais.
Podes tu tamb\xE9m dizer: "Sou um dos tais"?
`,verseorder:2,ismainverse:1,id_hino:339},{idverse:26358,verse:`2. 
No Cen\xE1c\`lo reunidos,
O poder buscavam ent\xE3o,
Pelo amor de Deus unidos
A clamar em ora\xE7\xE3o;
Eis que um vento \xE9 descido
E o fogo do c\xE9u cai;
Podes tu dizer tamb\xE9m:
"Sou um dos tais"?
`,verseorder:3,ismainverse:0,id_hino:339},{idverse:26359,verse:`3. 
Este povo destemido,
(S\xE3o os disc\xEDpulos de Jesus)
Pelo mundo perseguido,
Por levar a sua cruz,
E agora revestido
Com poder ao mundo sai;
Podes tu dizer tamb\xE9m:
"Sou um dos tais"?
`,verseorder:4,ismainverse:0,id_hino:339},{idverse:26360,verse:`4. 
\xD3 n\xE3o sejas descuidoso
P\`ra buscar o dom de Deus,
Dom que te far\xE1 ditoso,
Dar-te-\xE1 vis\xF5es do c\xE9u.
E JEsus maravilhoso
Proclamando aos outros vais,
Poder\xE1s ent\xE3o dizer:
"Sou um dos tais".`,verseorder:5,ismainverse:0,id_hino:339},{idverse:26361,verse:`1. 
Minh\`alma aspira habitar
Nos \xE1trios do Senhor,
E para sempre ouvir soar
A voz do meu Redentor.
`,verseorder:1,ismainverse:0,id_hino:340},{idverse:26362,verse:`2. 
Descanso n\xE3o se encontrar\xE1
No lar do pecador;
S\xF3 em Jesus se achar\xE1
Justi\xE7a, paz e amor.
`,verseorder:2,ismainverse:0,id_hino:340},{idverse:26363,verse:`3. 
\xD3 vinde, crentes, vamos J\xE1
A casa do Senhor,
Pois Ele nos ensinar\xE1
Servi-Lo com mui fervor!`,verseorder:3,ismainverse:0,id_hino:340},{idverse:26364,verse:`1. 
Do  meu cora\xE7\xE3o vibram as cordas,
Afinadas p\`ra o Mestre louvar;
O pecado este som n\xE3o outorga;
S\xF3 o G\xF3lgota as pode afinar.
`,verseorder:1,ismainverse:0,id_hino:341},{idverse:26365,verse:`Aleluia! Gl\xF3ria a Deus,
Que no G\xF3lgota a minh\`alma afinou!
Aleluia! Gl\xF3ria a Deus,
Que no Calv\xE1rio a minh\`alma afinou!
`,verseorder:2,ismainverse:1,id_hino:341},{idverse:26366,verse:`2. 
Tamb\xE9m fomos no mundo afinados,
Mas n\xE3o era agrad\xE1vel o som;
Mas no G\xF3lgota j\xE1 transformados,
Temos nova harmonia e tom.
`,verseorder:3,ismainverse:0,id_hino:341},{idverse:26367,verse:`3. 
\xC9 Jesus quem as almas afina,
O Instrumento ele cuida mui bem,
Cada dia o som mais refina,
T\xE9 tocar os acordes de al\xE9m.
`,verseorder:4,ismainverse:0,id_hino:341},{idverse:26368,verse:`4. 
Se provar esta afina\xE7\xE3o queres,
Hoje chega-te \xE0 cruz com amor;
Harmonia ter\xE1s se estiveres
Em Jesus, o teu afinador!`,verseorder:5,ismainverse:0,id_hino:341},{idverse:26369,verse:`1. 
\xD3 pecador que n\xE3o tens luz.
Abre o cora\xE7\xE3o;
Aceita hoje o bom Jesus;
Abre o cora\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:342},{idverse:26370,verse:`Quem sinto bater no meu cora\xE7\xE3o?
Jesus diz: "Abre sem tardar o teu cora\xE7\xE3o".
`,verseorder:2,ismainverse:1,id_hino:342},{idverse:26371,verse:`2. 
Tu que almejas salvo ser.
Abre o cora\xE7\xE3o;
A paz de Deus vem obter,
Abre o cora\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:342},{idverse:26372,verse:`3. 
Que meiga voz escutas j\xE1!
Abre o cora\xE7\xE3o;
Jesus tua alma salvar\xE1,
Abre o cora\xE7\xE3o!
`,verseorder:4,ismainverse:0,id_hino:342},{idverse:26373,verse:`4. 
Sim, hoje vem em Cristo crer
Abre o cora\xE7\xE3o;
No amanh\xE3 que vai haver?
Abre o cora\xE7\xE3o!
`,verseorder:5,ismainverse:0,id_hino:342},{idverse:26374,verse:`5. 
Ouvindo a voz do bom Jesus,
Abre o cora\xE7\xE3o;
\xD3 vem depressa, vem p\`ra luz,
Abre o cora\xE7\xE3o!`,verseorder:6,ismainverse:0,id_hino:342},{idverse:26375,verse:`1. 
Entre os l\xEDrios, no meio dos vales
\`St\xE1 um amigo, que \xE9 caro p\`ra mim;
Quero segui-Lo, isento dos males,
V\xEA-Lo no c\xE9u, com os anjos enfim.
`,verseorder:1,ismainverse:0,id_hino:343},{idverse:26376,verse:`Meu Salvador Jesus, \xD3 Filho do
bom Deus;
Gra\xE7as por Tua luz, que veio dos c\xE9us!
Por mim provaste a cruz,
Por mim morreste, Jesus!
`,verseorder:2,ismainverse:1,id_hino:343},{idverse:26377,verse:`2. 
Hoje Te busco, \xF3 vem, revelar-me
A luz divina, onde est\xE1s, meu Jesus,
Com Teu rebanho vem j\xE1 a levar-me
Para o rio que vida produz.
`,verseorder:3,ismainverse:0,id_hino:343},{idverse:26378,verse:`3. 
Ouve-me agora, Jesus mui amado,
Deixa-me Teu grande amor desfrutar;
N\xE3o mais me \`scondas Teu rosto adorado,
Pois me chamaste p\`ra me libertar.
`,verseorder:4,ismainverse:0,id_hino:343},{idverse:26379,verse:`4. 
A voz de Cristo \xE9 p\`ra mim mais preciosa
Que a voz dos anjos; e do sol a luz,
N\xE3o tem o brilho da face radiosa
Quando sorri  meu amado Jesus.`,verseorder:5,ismainverse:0,id_hino:343},{idverse:26380,verse:`1. 
Oh! Tenho gozo no meu cora\xE7\xE3o,
Tamb\xE9m a vida, e santo fervor;
Pois Cristo me deu real salva\xE7\xE3o
E a certeza dum reino de amor!
`,verseorder:1,ismainverse:0,id_hino:344},{idverse:26381,verse:`2. 
No  mundo nunca ir\xE1s conhecer
O gozo santo do meu Salvador,
Que j\xE1 desfruta, sim, todo o que crer,
E vive sempre repleto de amor.
`,verseorder:2,ismainverse:0,id_hino:344},{idverse:26382,verse:`3. 
Louvemos juntos o Rei de Israel,
Por Sua gra\xE7a,  que nos d\xE1 vigor,
Pois Ele esconde o crente fiel,
Em Suas asas de paz e amor.
`,verseorder:3,ismainverse:0,id_hino:344},{idverse:26383,verse:`4. 
O Pai Eterno Seu povo selou,
Com a promessa do Consolador;
Oh! Foi de gra\xE7a, que Deus me salvou
Por Seu imenso e santo amor!`,verseorder:4,ismainverse:0,id_hino:344},{idverse:26384,verse:`1. 
Que alegria agora, \xE9 meu o c\xE9u,
Pois Jesus rasgou o sagrado v\xE9u;
A condena\xE7\xE3o n\xE3o mais temerei,
E meu Redentor sempre louvarei.
`,verseorder:1,ismainverse:0,id_hino:345},{idverse:26385,verse:`L\xE1 no c\xE9u eu descansarei
Com Jesus, o nosso Rei;
Vem a Deus, \xF3 pecador,
Pois no c\xE9u te espera com amor.
`,verseorder:2,ismainverse:1,id_hino:345},{idverse:26386,verse:`2. 
Pelo mundo eu solit\xE1rio andei,
Fora de Sodoma o c\xE9u avistei,
Pois olhei p\`ra cima, p\`ra meu Jesus,
Que por mim morreu na sangrenta cruz.
`,verseorder:3,ismainverse:0,id_hino:345},{idverse:26387,verse:`3. 
Eu sou um soldado p\`ra combater,
E com Deus irei todo o mal vencer;
A bandeira \xE9 o angue de Jesus,
Que por mim vcertido foi l\xE1 na cruz.
`,verseorder:4,ismainverse:0,id_hino:345},{idverse:26388,verse:`4. 
Quando um dia eu venha a perecer,
Sim, meu cora\xE7\xE3o cesse de bater,
Subirei ao c\xE9u, sem nenhum temor,
Descansado nos bra\xE7os do Senhor.
`,verseorder:5,ismainverse:0,id_hino:345},{idverse:26389,verse:`5. 
Ante o trono alegre eu estarei,
E com Deus irei todo o mal vencer;
Hei de ver as palmas da multid\xE3o
Que aleluias a Deus, sempre cantar\xE3o.`,verseorder:6,ismainverse:0,id_hino:345},{idverse:26390,verse:`1. 
Vem a Cristo, vem agora,
Vem assim, como est\xE1s;
De Deus, sem demora,
O perd\xE3o obter\xE1s.
`,verseorder:1,ismainverse:0,id_hino:346},{idverse:26391,verse:`2. 
Cr\xEA e firma tua confian\xE7a
Na Sua morte por ti;
Pois gozo alcan\xE7a,
Todo que faz assim.
`,verseorder:2,ismainverse:0,id_hino:346},{idverse:26392,verse:`3. 
Vem a Cristo, n\xE3o temendo,
Pensa no Seu amor;
Vem logo, mas crendo,
Pois te chama o Senhor.
`,verseorder:3,ismainverse:0,id_hino:346},{idverse:26393,verse:`4. 
Ele anela redimir-te,
Quer livrar-te do mal;
As portas abrir-te,
Do prazer divinal.`,verseorder:4,ismainverse:0,id_hino:346},{idverse:26394,verse:`1. 
Vagava longe de Jesus, em plena salva\xE7\xE3o,
At\xE9 que a preciosa luz encheu meu cora\xE7\xE3o,
Assim, os meus pecados vi, qual negra escurid\xE3o;
Ao p\xE9 da cruz, ent\xE3o, ca\xED, e recebi perd\xE3o.
`,verseorder:1,ismainverse:0,id_hino:347},{idverse:26395,verse:`Cristo por mim
sofrendo, l\xE1 na cruz morrendo,
Minh\`alma salvou e meus grilh\xF5es j\xE1 quebrou
Aleluia!
Cristo por mim sofrendo, l\xE1 na cruz morrendo,
Deu-me paz, perd\xE3o, no meu cora\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:347},{idverse:26396,verse:`2. 
Jesus morreu em grande dor, a fim de me salvar,
Verteu Seu sangue expiador, que p\xF4de me lavar:
Sem murmurar, sofreu Jesus por mim. Vil pecador;
Por isso vim ao p\xE9 da cruz, vencido pelo amor.
`,verseorder:3,ismainverse:0,id_hino:347},{idverse:26397,verse:`3. 
Escuta a voz do salvador que chama-te p\`ra luz;
Confessa-te um pecador, e vem ao bom Jesus,
Que te dar\xE1 real poder, o gozo e paz de Deus;
E quando tudo fenecer, tu viver\xE1s no c\xE9u.
`,verseorder:4,ismainverse:0,id_hino:347},{idverse:26398,verse:"4. ",verseorder:5,ismainverse:0,id_hino:347},{idverse:26399,verse:`1. 
Os dons do c\xE9u prometidos,
Pai esperamos aqui;
Em santo amor reunidos,
N\xF3s suplicamos a Ti.
`,verseorder:1,ismainverse:0,id_hino:348},{idverse:26400,verse:`Manda, \xF3 Senhor, chuvas dos c\xE9us;
Chuvas do Consolador,
Manda dos c\xE9us, \xF3 bom Deus!
Manda, \xF3 Senhor, chuvas dos c\xE9us,
Que nos d\xE3o sempre vigor;
Manda-nos j\xE1, \xF3 bom Deus!
`,verseorder:2,ismainverse:1,id_hino:348},{idverse:26401,verse:`2. 
Manda-nos chuvas constantes,
Chuvas do Consolador;
Chuvas, sim, mas abundantes,
Chuvas de vida e de amor!
`,verseorder:3,ismainverse:0,id_hino:348},{idverse:26402,verse:`3. 
O Teu poder esperamos,
\xD3 glorioso Senhor!
Dos altos c\xE9us aguardamos
Chuvas do Consolador!
`,verseorder:4,ismainverse:0,id_hino:348},{idverse:26403,verse:`4. 
Pai aben\xE7oa os crentes
Que sempre est\xE3o a buscar
A Tua face, tementes;
Chuvas, lhes queiras mandar.`,verseorder:5,ismainverse:0,id_hino:348},{idverse:26404,verse:`1. 
Tens tu lido a hist\xF3ria da dura cruz,
Na qual Jesus morreu,
Desfazendo as trevas raiou a luz,
Manando o sangue Seu?
`,verseorder:1,ismainverse:0,id_hino:349},{idverse:26405,verse:`Jesus padeceu na cruz por ti;
Jesus padeceu na cruz!
Oh! Grande amor do Salvador
Morrendo na cruz por mim!
`,verseorder:2,ismainverse:1,id_hino:349},{idverse:26406,verse:`2. 
Tens tu lido que Cristo n\xE3o se queixou,
No lenho, em grande dor,
Mas ao Pai o perd\xE3o \`inda suplicou
P\`ra o povo pecador?
`,verseorder:3,ismainverse:0,id_hino:349},{idverse:26407,verse:`3. 
Tens tu lido que Cristo salvou, por fim,
Na cruz, um malfeitor?
Que sofrendo na cruz Lhe rogou:
"De mim, \xF3 lembra-Te, Senhor"?
`,verseorder:4,ismainverse:0,id_hino:349},{idverse:26408,verse:`4. 
Tens lido que Cristo exclamou na cruz:
"Eis consumado est\xE1".
Foi por mim, foi por ti, que morreu Jesus,
Aceita Cristo j\xE1.`,verseorder:5,ismainverse:0,id_hino:349},{idverse:26409,verse:`1. 
S\xE3o felizes os que t\xEAm o alegre som
Da salva\xE7\xE3o real do nosso Deus,
Pois eles t\xEAm ouvido num suave tom,
A multid\xE3o que canta l\xE1 no c\xE9u.
`,verseorder:1,ismainverse:0,id_hino:350},{idverse:26410,verse:`J\xE1 os filhos de Deus bem alegres
est\xE3o,
Por\xE9m no c\xE9u prazer melhor ter\xE3o,
Os gozos do crist\xE3o, apenas gotas s\xE3o
Do mar de b\xEAn\xE7\xE3os, em Si\xE3o!
`,verseorder:2,ismainverse:1,id_hino:350},{idverse:26411,verse:`2. 
Secam-se do mundo os mananciais por fim,
Deixando aqui somente sequid\xE3o,
P\xF3rem, as fontes divinais n\xE3o s\xE3o assim,
Pois sempre \xE1guas vivas manar\xE3o.
`,verseorder:3,ismainverse:0,id_hino:350},{idverse:26412,verse:`3. 
Paulo e Silas, eis que na cadeia est\xE3o,
No tronco t\xEAm seguros m\xE3os e p\xE9s,
Mas sem se importarem, mesmo na pris\xE3o,
Entoam hinos ao Senhor, com f\xE9!
`,verseorder:4,ismainverse:0,id_hino:350},{idverse:26413,verse:`4. 
Eis Estev\xE3o numa grande exulta\xE7\xE3o,
Embora \xE0s perdradas sucumbiu,
Porque do Esp\xEDrito cheio teve a vis\xE3o:
O Salvador \xE0 dextra de Deus, viu!
`,verseorder:5,ismainverse:0,id_hino:350},{idverse:26414,verse:`5. 
Muitos s\xE9culos t\xEAm-se passado j\xE1,
Depois que Estev\xE3o, pela f\xE9 morreu,
Mas salva\xE7\xE3o igual Jesus ainda d\xE1,
A todos crentes e fi\xE9is em Deus.
`,verseorder:6,ismainverse:0,id_hino:350},{idverse:26415,verse:`6. 
Qual pombinho j\xE1 em canto divinal,
Que louva: "Aleluia", em nosso ser;
Se perguntares: "Onde apanharei igual?"
"No G\xF3lgota" n\xF3s vamos responder.`,verseorder:7,ismainverse:0,id_hino:350},{idverse:26416,verse:`1. 
\xD3 Jesus, nossa esperan\xE7a,
Vide santa - n\xF3s os ramos;
Com fervor e confian\xE7a,
Vera gra\xE7a Te rogamos,
\xD3 Salvador.
`,verseorder:1,ismainverse:0,id_hino:351},{idverse:26417,verse:`2. 
Nossos cora\xE7\xF5es contritos
A Ti n\xF3s apresentamos.
Vem lav\xE1-los, Jesus Cristo,
Com Teu sangue, suplicamos,
\xD3 Salvador.
`,verseorder:2,ismainverse:0,id_hino:351},{idverse:26418,verse:`3. 

`,verseorder:3,ismainverse:0,id_hino:351},{idverse:26419,verse:`4. 
Aos que Tu tens resgatado,
Mais e mais os purifica.
J\xE1 que os tens p\`ra luz chamado
Suas almas santifica,
\xD3 Salvador.`,verseorder:4,ismainverse:0,id_hino:351},{idverse:26420,verse:`1. 
\xD3 tu qu\`inda vagas no mundo d\`horror,
Nas trevas e na escurid\xE3o,
Por que n\xE3o descansas em Cristo, o Senhor,
Que trouxe do c\xE9u Salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:352},{idverse:26421,verse:`\xD3 vem, \xF3 pr\xF3digo, \xF3 vem a Jesus;
Por ti foi erguido no alto da cruz;
Aceita agora de Deus o favor;
\xD3 vem, sem demora a Cristo, o Senhor.
`,verseorder:2,ismainverse:1,id_hino:352},{idverse:26422,verse:`2. 
O mundo jazia na dura pris\xE3o,
Mas Cristo, a porta abriu;
\xD3 cr\xEA na mensagem que d\xE1 salva\xE7\xE3o,
Pois Cristo na cruz te remiu!
`,verseorder:3,ismainverse:0,id_hino:352},{idverse:26423,verse:`3. 
Que gozo completo ter\xE1s em Jesus,
Se aceitares o Seu perd\xE3o;
\xD3 deixa o pecado e vem para a luz;
Entrega o teu cora\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:352},{idverse:26424,verse:`1. 
Eu tenho que guardar
Minh\`alma imortal,
E prepar\xE1-la para entrar,
No reino celestial.
`,verseorder:1,ismainverse:0,id_hino:353},{idverse:26425,verse:`2. 
P\`ra isto eu fazer,
Meu Deus, vem me ajudar;
A Teu servi\xE7o, sim, meu ser,
Te quero consagrar.
`,verseorder:2,ismainverse:0,id_hino:353},{idverse:26426,verse:`3. 
Teu filho quero ser,
De todo cora\xE7\xE3o
E para sempre obter
Tua eterna prote\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:353},{idverse:26427,verse:`4. 
Ajuda-me a velar,
Na f\xE9 me firmar bem,
Pois em Ti posso confiar,
E descansar tamb\xE9m!`,verseorder:4,ismainverse:0,id_hino:353},{idverse:26428,verse:`1. 
Fala do amor de Cristo
Ao triste pecador,
Amor por todos visto
Na cruz do Redentor;
Com alegria canta
A paz que vem do c\xE9u;
A tua voz levanta
Em gl\xF3ria a Deus.
`,verseorder:1,ismainverse:0,id_hino:354},{idverse:26429,verse:`Cristo na cruz ganhou-nos
Eterna salva\xE7\xE3o.
Com sangue Seu lavou-nos
O Rei da reden\xE7\xE3o!
No templo, o v\xE9u rasgado;
Caminho abriu Jesus
Na cruz, inaugurado
P\`ro c\xE9u de luz!
`,verseorder:2,ismainverse:1,id_hino:354},{idverse:26430,verse:`2. 
Para os aflitos, canta,
As b\xEAn\xE7\xE3os que em Deus h\xE1;
Da cruz pend\xE3o levanta
Pois \xE2nimo nos d\xE1.
Canta para os perdidos
A gra\xE7a de Jesus;
Seu sangue j\xE1 vertido,
Que flui da cruz!
`,verseorder:3,ismainverse:0,id_hino:354},{idverse:26431,verse:`3. 
Canta com esperan\xE7a,
Na escurid\xE3o tamb\xE9m,
Canta com confian\xE7a,
H\xE1 vida no al\xE9m;
Canta no alvor do dia,
E quando a noite vem;
Em Cristo, harmonia
Com Deus se obt\xE9m!`,verseorder:4,ismainverse:0,id_hino:354},{idverse:26432,verse:`1. 
Perto, sim, mui perto est\xE1,
O dia n\xE3o tardar\xE1;
Iremos n\xF3s residir
Com Jesus, que h\xE1 de vir.
`,verseorder:1,ismainverse:0,id_hino:355},{idverse:26433,verse:`2. 
Oh! Quanta gl\xF3ria h\xE1 de ser,
se Cristo hoje descer,
Pois os fi\xE9is em Jesus,
Gozar\xE3o eterna luz!
`,verseorder:2,ismainverse:0,id_hino:355},{idverse:26434,verse:`3. 

Que gl\xF3ria imensa, sem par,
Havemos n\xF3s de gozar
Ao lado, sim, do Senhor,
Nosso amado Salvador!
`,verseorder:3,ismainverse:0,id_hino:355},{idverse:26435,verse:`4. 
Devemos, pois, esperar,
E ao mundo olhos fechar;
Andemos sempre na luz,
A buscar o Rei Jesus.`,verseorder:4,ismainverse:0,id_hino:355},{idverse:26436,verse:`1. 
\xD3 nosso Pai que est\xE1s no c\xE9u,
Do universo o benfeitor;
Santificado seja o nome Teu,
O glorioso Deus de amor!
`,verseorder:1,ismainverse:0,id_hino:356},{idverse:26437,verse:`2. 
O reino Teu, venha com poder,
Conforme o an\xFAncio Teu;
E seja feito sempre Teu querer,
Na terra, como l\xE1 no c\xE9u.
`,verseorder:2,ismainverse:0,id_hino:356},{idverse:26438,verse:`3. 
E cada dia o nosso p\xE3o,
D\xE1-nos Senhor, e sem faltar;
E nossas almas que sedentas est\xE3o,
Vem para sempre saciar.
`,verseorder:3,ismainverse:0,id_hino:356},{idverse:26439,verse:`4. 
Vem perdoar nossas faltas, vem,
Pai glorioso eternal;
Pois perdoado temos n\xF3s, tamb\xE9m,
Os que a n\xF3s fizeram mal.
`,verseorder:4,ismainverse:0,id_hino:356},{idverse:26440,verse:`5. 
Da tenta\xE7\xE3o queiras nos livrar,
E do mal nos afastar bem,
Pois Te pertence o c\xE9u, a terra e o mar.
O reino para sempre, am\xE9m!`,verseorder:5,ismainverse:0,id_hino:356},{idverse:26441,verse:`1. 
Quando esta vida de lutas cessar,
Vou para o c\xE9u meu descanso gozar
Com meu Jesus, l\xE1 na gl\xF3ria sem fim;
Oh! Que ventura ser\xE1 para mim!
`,verseorder:1,ismainverse:0,id_hino:357},{idverse:26442,verse:`Oh! Que prazer! Gl\xF3ria a Jesus!
Eu hei de ver no c\xE9u de luz;
Nunca morrer, a Jesus sempre ver,
Oh! Ser\xE1 gozo eterno, p\`ra mim!
`,verseorder:2,ismainverse:1,id_hino:357},{idverse:26443,verse:`2. 
Penso na paz e no grande amor,
Do lar celeste do meu salvador;
Em ver Seu rosto, em sorrir tamb\xE9m,
Na grande gl\xF3ria do dida que vem.
`,verseorder:3,ismainverse:0,id_hino:357},{idverse:26444,verse:`3. 
Pais e amigos, irm\xE3os, h\xE3o de estar
No santo rio, de gozo a cantar,
Sempre alegres, louvando ao Senhor,
Gozando b\xEAn\xE7\xE3os do seu Criador`,verseorder:4,ismainverse:0,id_hino:357},{idverse:26445,verse:`1. 
Os fi\xE9is oravam unidos
Num cen\xE1c\`lo ao Senhor,
Quando foi do c\xE9u descido
O real Consolador!
`,verseorder:1,ismainverse:0,id_hino:358},{idverse:26446,verse:`Senhor, manda j\xE1 o Teu poder.
Senhor, manda j\xE1 o Teu poder.
Senhor, manda j\xE1 o Teu poder,
E batiza cada um!
`,verseorder:2,ismainverse:1,id_hino:358},{idverse:26447,verse:`2. 
Qual um vento veemente,
O poder a casa encheu;
L\xEDnguas vieram sobre os crentes,
Mas de fogo, l\xE1 do c\xE9u!
`,verseorder:3,ismainverse:0,id_hino:358},{idverse:26448,verse:`3. 
Noutras l\xEDnguas, sim, falaram,
Como Cristo concedeu;
Do Esp\xEDrito transbordaram,
Exaltando o Rei do c\xE9u!
`,verseorder:4,ismainverse:0,id_hino:358},{idverse:26449,verse:`4. 
O poder foi prometido
Para os servos do Senhor:
\xC9 p\`ra todos concedido,
Se o pedirem com fervor!
`,verseorder:5,ismainverse:0,id_hino:358},{idverse:26450,verse:"5. ",verseorder:6,ismainverse:0,id_hino:358},{idverse:26451,verse:`1. 
Eis que clamo, Eu Jesus, o poderoso,
Vem a Mim, pecador;
Quero dar-te Minha paz, perd\xE3o e gozo,
Vem a Mim, sofredor.
`,verseorder:1,ismainverse:0,id_hino:359},{idverse:26452,verse:`Eu te amo, \xF3 pecador perdido,
Morri p\`ra ser teu Salvador,
Por teus crimes e pecados fui ferido;
Vem a Mim, sofredor.
`,verseorder:2,ismainverse:1,id_hino:359},{idverse:26453,verse:`2. 
Dos pecados hoje posso libertar-te,
Vem a Mim, pecador;
Pois na cruz, Eu padecendo, quis livrar-te,
Vem a  Mim, sofredor.
`,verseorder:3,ismainverse:0,id_hino:359},{idverse:26454,verse:`3. 
Hoje quero te fazer feliz, ditoso,
Vem a Mim, pecador;
Com meu sangue abri um trilho glorioso,
Vem a Mim, sofredor.
`,verseorder:4,ismainverse:0,id_hino:359},{idverse:26455,verse:`4. 
N\xE3o desprezes com orgulho o Meu convite
Vem a Mim, pecador;
Pois teu cora\xE7\xE3o \xE9 fraco, n\xE3o resiste,
Vem a Mim, sofredor.`,verseorder:5,ismainverse:0,id_hino:359},{idverse:26456,verse:`1. 
Vem \xE0 fonte sanadora,
Que abriu o Salvador,
Cujas \xE1guas sedutoras
T\xEAm um divinal sabor.
`,verseorder:1,ismainverse:0,id_hino:360},{idverse:26457,verse:`Oh! Preciosa fonte sanadora
Para todos corre, sim!
Oh! Preciosa fonte sanadora!
Gl\xF3ria a Deus, me sara a mim!
`,verseorder:2,ismainverse:1,id_hino:360},{idverse:26458,verse:`2. 
Nesta fonte, que nos sara,
Encontrei real perd\xE3o;
Minha veste fez-se clara,
Tenho purifica\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:360},{idverse:26459,verse:`3. 
Nesta fonte, que nos cura,
Deu-me Cristo, salva\xE7\xE3o;
S\xE3 tornou minh\`alma e pura,
E lhe deu consola\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:360},{idverse:26460,verse:`4. 
Vem \xE0  fonte que te cura;
Hoje vem  purificar
A tu\`alma, que impra,
L\xE1 no c\xE9u n\xE3o pode entrar.`,verseorder:5,ismainverse:0,id_hino:360},{idverse:26461,verse:`1. 
Peregrino segue para a gl\xF3ria,
Pois no c\xE9u em breve entrar\xE1s!
Ouves j\xE1 os cantos de vit\xF3ria?
Tua voz tamb\xE9m l\xE1 unir\xE1s.
`,verseorder:1,ismainverse:0,id_hino:361},{idverse:26462,verse:`Sim, queres entoar
O c\xE2ntico dos salvos, l\xE1 na gl\xF3ria,
Que nos c\xE9us h\xE1 de sempre soar?
Sim, queres tu cantar,
Os gloriosos hinos de vit\xF3ria?
A Jesus mil louvores entoar?
`,verseorder:2,ismainverse:1,id_hino:361},{idverse:26463,verse:`2. 
Vem do c\xE9u um som de alegria,
Na escura noite te chamar,
Mui suave e cheio de harmonia.
- \xD3 minh\`alma, volta p\`ra teu lar!
`,verseorder:3,ismainverse:0,id_hino:361},{idverse:26464,verse:`3. 
Raios \xFAltimos do sol se escondem,
Na dourada porta de Si\xE3o;
Terminada a luta estarei onde
As saudades n\xE3o mais chegar\xE3o.
`,verseorder:4,ismainverse:0,id_hino:361},{idverse:26465,verse:`4. 
De Jesus, o sangue, d\xE1 entrada;
Gl\xF3ria a Deus, a porta aberta est\xE1!
Queres tu no c\xE9u feliz morada?
Pois ser\xE1 mui triste n\xE3o ir l\xE1.`,verseorder:5,ismainverse:0,id_hino:361},{idverse:26466,verse:`1. 
O Esp\xEDrito e a Noiva dizem:
"Vem beber da \xE1gua da vida";
Que voz de amor do Salvador,
A uma alma perdida!
`,verseorder:1,ismainverse:0,id_hino:362},{idverse:26467,verse:`O Esp\xEDrito diz: Vem! A Noiva diz: Vem!
E toma de gra\xE7a da \xE1gua da vida.
O Esp\xEDrito diz: Vem! A Noiva diz: Vem!
E toma de gra\xE7a da \xE1gua da vida.
`,verseorder:2,ismainverse:1,id_hino:362},{idverse:26468,verse:`2. 
E o que escuta diga: Vem!
E fale da paz obtida,
Pois recebeu e j\xE1 bebeu
Da \xE1gua que lhe deu vida.
`,verseorder:3,ismainverse:0,id_hino:362},{idverse:26469,verse:`3. 
\xD3 pecador sedento, vem!
\xC1 fonte das \xE1guas puras!
Vem te saciar, vem sem tardar,
\xD3 infeliz criatura!
`,verseorder:4,ismainverse:0,id_hino:362},{idverse:26470,verse:`4. 
Se tu quiseres, hoje: Vem!
A Cristo, alma sequiosa;
Tu beber\xE1s, te fartar\xE1s
Da \xE1gua  maravilhosa!`,verseorder:5,ismainverse:0,id_hino:362},{idverse:26471,verse:`1. 
Amor e vida dou a Ti,
Jesus, pois l\xE1 na cruz, por mim,
Verteste sangue carmezim;
Meu Deus e Salvador!
`,verseorder:1,ismainverse:0,id_hino:363},{idverse:26472,verse:`Amor e vida dou a Ti,
Que foste \xE0 cruz sofrer por mim,
Amor e vida dou a Ti,
Jesus, meu Salvador!
`,verseorder:2,ismainverse:1,id_hino:363},{idverse:26473,verse:`2. 
Tu me salvaste, isto sei;
A minha f\xE9 em ti porei;
Feliz, agora viverei!
Contigo, meu Jesus!
`,verseorder:3,ismainverse:0,id_hino:363},{idverse:26474,verse:`3. 
Tu que morreste sobre a cruz,
Concede-me, Senhor Jesus,
Que sempre ande na Tua luz,
Em santifica\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:363},{idverse:26475,verse:`1. 
Oh! Que paz eu sinto no Senhor Jesus
Paz eterna, que me faz andar na luz,
E est\xE1 em mim na prova e na dor;
Minha paz \xE9 o Senhor!
`,verseorder:1,ismainverse:0,id_hino:364},{idverse:26476,verse:`Oh! Que paz recebo pela cruz,
Paz eterna no Senhor,
Se viver sempre em Jesus
E vencer por Seu amor!
Oh! Que paz, que luz tem o pecador
Quando vem a Ti, Jesus!
`,verseorder:2,ismainverse:1,id_hino:364},{idverse:26477,verse:`2. 
Oh! Que paz eu tenho no meu cora\xE7\xE3o,
Paz eterna, que me d\xE1 consola\xE7\xE3o!
\xC9 a paz que goza todo o fiel;
De Jesus: o Emanuel!
`,verseorder:3,ismainverse:0,id_hino:364},{idverse:26478,verse:`3. 
Oh! Que paz celeste me d\xE1 o Senhor!
Paz eterna, que me enche de amor;
Essa \xE9 Cristo, a vit\xF3ria d\`Israel,
Que possui quem for fiel!
`,verseorder:4,ismainverse:0,id_hino:364},{idverse:26479,verse:`4. 
Oh! Amados, caminhemos com Jesus!
S\xF3 pensando nEle e na Sua cruz;
Ele \xE9 a paz que alegra o cora\xE7\xE3o
Pois nos d\xE1 o Seu perd\xE3o!`,verseorder:5,ismainverse:0,id_hino:364},{idverse:26480,verse:`1. 
D\xE9bil, pobre, cego sou,
Nada pode me valer;
Aos Teus p\xE9s, ansioso vou,
Para gra\xE7a obter.
`,verseorder:1,ismainverse:0,id_hino:365},{idverse:26481,verse:`S\xF3 em Ti vou confiar
Oh! Jesus, meu Salvador,
Nos Teus bra\xE7os descansar
E fruir o Teu amor!
`,verseorder:2,ismainverse:1,id_hino:365},{idverse:26482,verse:`2. 
Muito tempo o mal em mim,
H\xE1 reinado, sem cessar;
Eu, Senhor, acudo a Ti,
Desejando descansar.
`,verseorder:3,ismainverse:0,id_hino:365},{idverse:26483,verse:`3. 
Toma meu fraquinho ser,
Nele faz habita\xE7\xE3o;
Nunca deixes me reter,
Os ardis da tenta\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:365},{idverse:26484,verse:`4. 
Cristo vem ao cora\xE7\xE3o
Para os males dissipar:
D\xE1-me hoje Teu perd\xE3o,
Vem minh\`alma consolar.`,verseorder:5,ismainverse:0,id_hino:365},{idverse:26485,verse:`1. 
Em B\xE9lem nasceu Jesus,
Nosso grande Redentor,
Sendo a bendita luz
Para um mundo pecador.
`,verseorder:1,ismainverse:0,id_hino:366},{idverse:26486,verse:`Oh! Aleluia a Cristo Jesus,
O Enviado, Filho de Deus!
Gozo e luz que vida produz,
Cristo trouxe l\xE1 dos c\xE9us!
`,verseorder:2,ismainverse:1,id_hino:366},{idverse:26487,verse:`2. 
Com pastores, a Bel\xE9m
Vamos a Jesus louvar,
Nossa gratid\xE3o, tamb\xE9m,
Dar a quem nos quis salvar.
`,verseorder:3,ismainverse:0,id_hino:366},{idverse:26488,verse:`3. 
Em humilde adora\xE7\xE3o
A Jesus, sim, ofertar
Nosso grato cora\xE7\xE3o,
Que sentiu o amor sem par.
`,verseorder:4,ismainverse:0,id_hino:366},{idverse:26489,verse:`4. 
L\xE1 do c\xE9u Jesus desceu,
Fez-se homem, sendo Deus;
Nossas dores padeceu,
P\`ra levar-nos para os c\xE9us.
`,verseorder:5,ismainverse:0,id_hino:366},{idverse:26490,verse:`5. 
Demos gl\xF3ria, gl\xF3ria a Deus,
Pelo dom do Seu amor!
Louvaremos, sim, nos c\xE9us,
A Jesus, com mais fervor.
`,verseorder:6,ismainverse:0,id_hino:366},{idverse:26491,verse:`6. 
Cantam anjos, gl\xF3ria a Deus!
Para os homens santa paz!
Cantam gl\xF3ria os filhos Seus:
Salve o Rei, que gozo traz.`,verseorder:7,ismainverse:0,id_hino:366},{idverse:26492,verse:`1. 
Esp\xEDrito Consolador, divino e eternal,
Preciosa fonte de amor e vida imortal.
`,verseorder:1,ismainverse:0,id_hino:367},{idverse:26493,verse:`2. 
Com s\xEDmbolos que o Salvador morrendo nos deixou,
Celebraremos Seu amor, que na cruz demonstrou.
`,verseorder:2,ismainverse:0,id_hino:367},{idverse:26494,verse:`3. 
Tu, p\xE3o da vida Redentor! Em nosso cora\xE7\xE3o
Infunde gra\xE7a, com fervor, e celestial un\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:367},{idverse:26495,verse:`4. 
Acende em n\xF3s fogo eficaz de f\xE9 e de valor;
Concede-me perd\xE3o e paz, humilha\xE7\xE3o e amor.
Am\xE9m.`,verseorder:4,ismainverse:0,id_hino:367},{idverse:26496,verse:`1. 
Eu estava no pecado, perdido, triste e s\xF3;
Que diferen\xE7a agora, Jesus de  mim tem d\xF3;
Eu hoje s\xF3 confio na gra\xE7a que me deu;
O Seu amor conhe\xE7o e ando para o c\xE9u.
`,verseorder:1,ismainverse:0,id_hino:368},{idverse:26497,verse:`Jesus, tudo \xE9 p\`ra mim, de
culpa me livrou;
Amor e paz eterna,
na cruz p\`ra mim ganhou;
Sim, tudo \xE9 p\`ra mim,
mostrou Seu imenso amor,
Gl\xF3ria ao santo nome do amado Salvador!
`,verseorder:2,ismainverse:1,id_hino:368},{idverse:26498,verse:`2. 
Estou p\`ra o mundo morto, mas vivo p\`ra Jesus;
So Ele \xE9 o piloto que certo, me conduz;
A B\xEDblia \xE9 que me ensina, que na cruz padeceu;
Comprou-me com Seu sangue e Sua paz me deu.
`,verseorder:3,ismainverse:0,id_hino:368},{idverse:26499,verse:`3. 
Mui perto vou chegando do glorioso lar;
Me preparou morada, onde irei habitar,
Jesus me santifica, ajuda-me a lutar
Contra a vil injusti\xE7a e, mesmo me negar.`,verseorder:4,ismainverse:0,id_hino:368},{idverse:26500,verse:`1. 
Sob as asas de Deus, descansado;
Inda que \xE9 noite confiando estou;
Sob Suas asas me acho abrigado,
Fui redimido, de Cristo eu sou.
`,verseorder:1,ismainverse:0,id_hino:369},{idverse:26501,verse:`Descansarei, descansarei,
Sob Suas asas preciosas!
Descansarei, e n\xE3o temerei.
Sob asas t\xE3o poderosas!
`,verseorder:2,ismainverse:1,id_hino:369},{idverse:26502,verse:`2. 
Sob Suas asas ref\xFAgio eu tenho,
Meu cora\xE7\xE3o pode, ent\xE3o, descansar;
E se aflito, a Cristo eu venho,
Perfeito gozo vou nEle encontrar.
`,verseorder:3,ismainverse:0,id_hino:369},{idverse:26503,verse:`3. 
Sob Suas Asas, promessa preciosa!
Cristo me livra, me pode guardar;
Bem protegido, nas asas gloriosas.
Paz e alegria eu posso achar.`,verseorder:4,ismainverse:0,id_hino:369},{idverse:26504,verse:`1. 
\xD3 meu Senhor, d\xE1-me mais gratid\xE3o,
Por tudo que Tu fizeste por mim
Por Tua gra\xE7a no  meu cora\xE7\xE3o,
Que me encheu de ventura sem fim!
`,verseorder:1,ismainverse:0,id_hino:370},{idverse:26505,verse:`Mais grato a Ti, mais grato a
Ti.
Mais consagrado, \xF3 faz-me, Senhor!
Mais humilhado e cheio de amor,
Faz-me mais grato a ti, mais grato a Ti!
`,verseorder:2,ismainverse:1,id_hino:370},{idverse:26506,verse:`2. 
De gra\xE7as deste ao meu cora\xE7\xE3o
A santidade, a paz e a f\xE9;
Gozo celeste e consola\xE7\xE3o,
E liberdade de estar aos Teus p\xE9s.
`,verseorder:3,ismainverse:0,id_hino:370},{idverse:26507,verse:`3. 
\xD3 meu Senhor, Tu fizeste por mim,
O que ningu\xE9m poderia fazer;
Na cruz pregado verteste, assim,
Sangue, no qual sempre posso vencer!`,verseorder:4,ismainverse:0,id_hino:370},{idverse:26508,verse:`1. 
Breve vem o grande dia
Em que lutas findar\xE3o;
Todos males, agonias,
Deste mundo cessar\xE3o.
`,verseorder:1,ismainverse:0,id_hino:371},{idverse:26509,verse:`Cessar\xE1 no c\xE9u o pranto,
Pois n\xE3o haver\xE1 mais dor,
E ouvir-se-\xE1 o canto,
Dos remidos do Senhor!
`,verseorder:2,ismainverse:1,id_hino:371},{idverse:26510,verse:`2. 
Oh! que gozo, estar com Cristo,
Escutando a Sua voz!
Eu almejo hoje isto,
E segui-Lo sempre ap\xF3s!
`,verseorder:3,ismainverse:0,id_hino:371},{idverse:26511,verse:`3. 
Se Jesus Cristo \xE9 meu guia,
O caiminho hei de trilhar;
Quem assim em Deus confia,
L\xE1 no c\xE9u h\xE1 de chegar.`,verseorder:4,ismainverse:0,id_hino:371},{idverse:26512,verse:`1. 
Quem possui a Cristo, nEle firme est\xE1,
Achar\xE1 poder para o mal combater;
Porque Suas promessas Ele cumprir\xE1.
Quem est\xE1 em Cristo, sempre h\xE1 de vencer.
`,verseorder:1,ismainverse:0,id_hino:372},{idverse:26513,verse:`Vencer\xE1, vencer\xE1, por
Seu sanguevencer\xE1;
Vencer\xE1, vencer\xE1, sempre vencer\xE1;
Pois Jesus que impera, novas for\xE7as d\xE1;
E quem nEle espera sempre vencer\xE1.
`,verseorder:2,ismainverse:1,id_hino:372},{idverse:26514,verse:`2. 
Em qualquer batalha, n\xE3o desmaiar\xE1;
Do poder das trevas h\xE1 de triunfar;
Deus lhe dar\xE1 for\xE7as, nunca falhar\xE1;
Porque Jeov\xE1 combate em seu lugar.
`,verseorder:3,ismainverse:0,id_hino:372},{idverse:26515,verse:`3. 
Entre os inimigos, mui audaz ser\xE1,
Quem unido a Cristo nunca vacilar.
Em Jesus firmado, luz espargir\xE1;
A luz do Evangelho para os libertar.`,verseorder:4,ismainverse:0,id_hino:372},{idverse:26516,verse:`1. 
Pecador, que segues no mundo errante
Sem achar consola\xE7\xE3o;
Hoje, vem a Cristo, vem confiante,
E ter\xE1s a salva\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:373},{idverse:26517,verse:`\xD3 vem ao teu Salvador,
Que foi morto sobre a cruz,
Pois ali, salvou o vil pecador;
\xF3 vem ao Senhor Jesus!
`,verseorder:2,ismainverse:1,id_hino:373},{idverse:26518,verse:`2. 
Pecador, se tu sentes em tu\` alma,
Do pecado a escravid\xE3o;
A Jesus, se vieres, com f\xE9 e calma,
Fruir\xE1s de Deus, o perd\xE3o.
`,verseorder:3,ismainverse:0,id_hino:373},{idverse:26519,verse:`3. 
Pecador aflito e t\xE3o doente,
Vem p\`ra Cristo te curar.
Vem a Ele hoje humildemente,
Pois tu\`alma quer salvar!`,verseorder:4,ismainverse:0,id_hino:373},{idverse:26520,verse:`1. 
N\xF3s queremos ter vida abundante,
De pureza e de santidade,
Para amarmos a Deus em verdade,
Pela gra\xE7a que Ele nos deu.
`,verseorder:1,ismainverse:0,id_hino:374},{idverse:26521,verse:`Vem nos dar Tua vida abundante,
Nosso amado e divino Senhor;
Tua vida de gozo exultante,
Abundante no Consolador.
`,verseorder:2,ismainverse:1,id_hino:374},{idverse:26522,verse:`2. 
N\xF3s queremos ter vida abundante
De amor, que o Pai nos tem dado
Em Jesus, o Seu Filho amado,
Cuja vida por n\xF3s derramou.
`,verseorder:3,ismainverse:0,id_hino:374},{idverse:26523,verse:`3. 
N\xF3s queremos ter vida abundante
De Jesus, a veraz fortaleza,
Que nos d\xE1 do perd\xE3o a certeza,
E nos enche de consola\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:374},{idverse:26524,verse:`1. 
Na rocha firme, triunfal,
Est\xE1 a Igreja universal;
Fundada foi por Ti, Jesus,
Ao expirares l\xE1 na cruz.
`,verseorder:1,ismainverse:0,id_hino:375},{idverse:26525,verse:`Com Teu poder, enche meu ser;
\xD3 Deus de amor, d\xE1-me fervor;
Remido estou, Teu filho sou,
Com Teu poder, \xF3 vem me encher.
`,verseorder:2,ismainverse:1,id_hino:375},{idverse:26526,verse:`2. 
A gra\xE7a imensa do bom Deus,
Lugar me deu, de filho Seu,
Com plena paz no cora\xE7\xE3o,
Amor veraz, e salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:375},{idverse:26527,verse:`3. 
Eis, pedra viva agora sou;
No templo Seu me colocou;
Do corpo Seu, um membro sou,
O sangue Seu me consagrou.
`,verseorder:4,ismainverse:0,id_hino:375},{idverse:26528,verse:`4. 
Na rocha, n\xE3o vai se abalar,
A Tua Igreja e vencer\xE1;
Quando este mundo se acabar,
Contigo, ela reinar\xE1.`,verseorder:5,ismainverse:0,id_hino:375},{idverse:26529,verse:`1. 
Vamos todos trablhar para o Senhor Jesus,
Nosso guia Ele \xE9, para o campo nos conduz,
Prontos n\xF3s devemos \`star, pois Jesus n\xE3o vai tardar.
Subiremos para o c\xE9u, onde vamos habitar.
`,verseorder:1,ismainverse:0,id_hino:376},{idverse:26530,verse:`2. 
Reunidos em amor, com Jesus, o Salvador,
Vamos sempre nos lembrar das palavras do Senhor:
"\xD3rf\xE3os n\xE3o vos deixarei, mas ao Pai, Eu rogarei,
Dar-vos o Consolador, que do c\xE9u enviarei"
`,verseorder:2,ismainverse:0,id_hino:376},{idverse:26531,verse:`3. 
Aos disc\xEDpulos ordenou, logo ap\xF3s ressuscitar:
"O Evangelho de amor, ide a todos, sim, pregar,
O que cr\xEA se salvar\xE1, e sinais receber\xE1,
Pelo Esp\xEDrito de Deus, novas l\xEDnguas falar\xE1".
`,verseorder:3,ismainverse:0,id_hino:376},{idverse:26532,verse:`4. 
Este selo de amor foi mandado por Jesus,
O cen\xE1c\`lo todo encheu de poder do c\xE9u, de luz,
Compungidos, os judeus, creram no Rei divinal;
Aceitaram a Jesus pelo pode do sinal.`,verseorder:4,ismainverse:0,id_hino:376},{idverse:26533,verse:`1. 
De Deus as santas promessas,
Hoje se cumprir\xE3o.
Desde que a Deus sempre pe\xE7as,
Crendo de cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:377},{idverse:26534,verse:`Terra e c\xE9us ardendo,
Os montes \`st\xE3o tremendo,
Mas, nas promessas crendo,
Deus sempre as vai cumprir!
`,verseorder:2,ismainverse:1,id_hino:377},{idverse:26535,verse:`2. 
Contando Abra\xE3o as estrelas
Na vastid\xE3o dos c\xE9us,
Vendo-as brilhantes, t\xE3o belas!
Creu inda mais em Deus!
`,verseorder:3,ismainverse:0,id_hino:377},{idverse:26536,verse:`3. 
Se tens em ti amargura,
Firma-te, sim, em Deus,
Breve, a noite escura
H\xE1 de findar p\`ros Seus.
`,verseorder:4,ismainverse:0,id_hino:377},{idverse:26537,verse:`4. 
Se fores tu perseguido,
S\xF3 Deus te livrar\xE1;
O Filho de Deus, o Ungido,
Por ti combater\xE1.
`,verseorder:5,ismainverse:0,id_hino:377},{idverse:26538,verse:`5. 
Se os teus se tornam inimigos,
Querem te abandonar,
Cristo, o melhor dos amigos
Quer ao teu lado estar.
`,verseorder:6,ismainverse:0,id_hino:377},{idverse:26539,verse:`6. 
Cr\xEA, pois a f\xE9 alivia
Teu sofrimento e dor
Quem nas promessas confia,
No c\xE9u dar\xE1 louvor!`,verseorder:7,ismainverse:0,id_hino:377},{idverse:26540,verse:`1. 
Quero seguir as pisadas do Mestre,
Quero ir ap\xF3s o meu Rei e Senhor,
Para que no Seu poder me adestre,
Eu vou orando ao meu Redentor.
`,verseorder:1,ismainverse:0,id_hino:378},{idverse:26541,verse:`Qu\xE3o doce \xE9 seguir as pisadas do
Mestre
Na bendita luz, perto de Jesus!
Qu\xE3o doce \xE9 seguir as pisadas do Mestre,
E levar a cruz!
`,verseorder:2,ismainverse:1,id_hino:378},{idverse:26542,verse:`2. 
Ando mais perto de Um que me guia,
Quando o maligno me vem enganar;
Que me sustenta na grande porfia,
Devo por isso, o Seu nome exaltar!
`,verseorder:3,ismainverse:0,id_hino:378},{idverse:26543,verse:`3. 
Sigo Seus passos de terno carinho,
Miseric\xF3rdia, amor e mansid\xE3o;
Qual ave que vai em busca do ninho,
Eu me dirijo \xE0 santa Si\xE3o.
`,verseorder:4,ismainverse:0,id_hino:378},{idverse:26544,verse:`4. 
Quero seguir as pisadas de Cristo,
Sempre acima desejo galgar.
Para Si\xE3o, que por f\xE9 eu avisto,
Onde p\`ra sempre eu vou descansar.`,verseorder:5,ismainverse:0,id_hino:378},{idverse:26545,verse:`1. 
Que alegria, Jesus me salvou,
Por Sua gra\xE7a, pois Ele levou
Os meus pecados, cravando-os na cruz,
Fazendo brilhar sobre  mim a luz.
`,verseorder:1,ismainverse:0,id_hino:379},{idverse:26546,verse:`Salvo de gra\xE7a por meu Jesus!
Salvo de gra\xE7a, j\xE1 tenho a luz!
Demos-Lhe gl\xF3ria por grande vit\xF3ria,
Que por mim ganhou o Senhor Jesus.
`,verseorder:2,ismainverse:1,id_hino:379},{idverse:26547,verse:`2. 
Sempre errante no mundo andei,
Longe de Deus, dos preceitos da lei,
Que me promete o gozo sem par,
Tamb\xE9m liberdade aqui me dar.
`,verseorder:3,ismainverse:0,id_hino:379},{idverse:26548,verse:`3. 
Choro e prantos n\xE3o podem valer,
Nem seus esfor\xE7os, nem grande saber,
Para um perdido sedento de luz,
Se ele n\xE3o crer no Senhor Jesus.
`,verseorder:4,ismainverse:0,id_hino:379},{idverse:26549,verse:`4. 
Tenho descaanso do c\xE9u, gozo enfim,
Pois Jesus Cristo salvou-me a mim;
Quando eu entrar no Seu reino de luz,
Melhor vou louvar o Senhor Jesus.`,verseorder:5,ismainverse:0,id_hino:379},{idverse:26550,verse:`1. 
Debaixo dumas mui formosas tamareiras,
Estando j\xE1 Berseba na escurid\xE3o,
As aves escutando, entre as roseiras,
Se v\xEA andar o patriarca Abra\xE3o.
Seu cora\xE7\xE3o perante Deus est\xE1 aflito,
Pois quer que O sirvamos sem murmura\xE7\xE3o;
E por amor pergunta ao senhor bendito:
"O meu amado filho queres Tu, ent\xE3o?"
`,verseorder:1,ismainverse:0,id_hino:380},{idverse:26551,verse:`2. 
A voz de Jeov\xE1 potente \xE9 ouvida:
"O teu Isaque oferece para  Mim,
Embora fiques com tu\`alma dolorida,
Pois te aben\xE7oo se fizeres tu assim".
De abatido Abra\xE3o se torna forte
E canta hinos, pois com f\xE9 medita j\xE1:
"Deus pode o meu filho libertar da morte!"
E n\xE3o temendo, segue para Mori\xE1.
`,verseorder:2,ismainverse:0,id_hino:380},{idverse:26552,verse:`3. 
Ao p\xE9 do  monte do supremo sacrif\xEDcio,
Profunda d\xFAvida entrou em Abra\xE3o:
"Irei perder da minha vida o benef\xEDcio?"
E triste come\xE7ou subir com lentid\xE3o,
Pois ia dar, do cora\xE7\xE3o a esperan\xE7a
- No seu outono, sacrif\xEDcio duma flor,
Assim levou o seu cordeiro \xE0 matan\xE7a,
Em obedi\xEAncia ao mandato do Senhor.
`,verseorder:3,ismainverse:0,id_hino:380},{idverse:26553,verse:`4. 
Isaque com a lenha, presto vai na frente,
Oh! Quanto \xE9 formoso para Abra\xE3o!
Mas eis que volta p\`ra seu pai suavemente
E lhe dirige esta interroga\xE7\xE3o:
"O fogo e  a lenha estou vendo que trouxemos,
Mas o cordeiro d\`holocausto onde est\xE1?"
E a resposta de Abra\xE3o na B\xEDblia temos:
"Meu filho, Deus p\`ra Si, cordeiro prover\xE1".
`,verseorder:4,ismainverse:0,id_hino:380},{idverse:26554,verse:`5. 
Chegando Abra\xE3o aonde Deus mandara,
Fez um altar e nele a lenha arrumou:
E a seu filho, que ja dantes amarrara,
Tomando nos seus bra\xE7os sobre o altar deitou,
Mas quando Abra\xE3o foi para imol\xE1-lo,
O anjo do Senhor bradou-lhe desde os c\xE9us:
"A tua m\xE3o, \xF3 n\xE3o estendas p\`ra mat\xE1-lo;
Porquanto eu agora sei que temes Deus".
`,verseorder:5,ismainverse:0,id_hino:380},{idverse:26555,verse:`6. 
Erguendo Abra\xE3o seus olhos de repente,
V\xEA um cordeiro, que no mato preso est\xE1,
E o tomando, oferece-o alegremente;
Assim "No monte do Senhor se prover\xE1".
A voz do Anjo \xE9 ouvida novamente;
"Diz o Senhor: Porque fizeste esta a\xE7\xE3o,
Deveras, Eu aben\xE7oarei a tua semente,
E nela, as na\xE7\xF5es benditas se far\xE3o".
`,verseorder:6,ismainverse:0,id_hino:380},{idverse:26556,verse:`7. 
O nosso Isaque oferecemos com firmeza,
No Mori\xE1 onde finda o ideal,
Pois foi ali que alcan\xE7aram fortaleza,
Os vencedores, sob canto angelical;
Ali, o nosso Deus jurou fidelidade,
Tamb\xE9m os santos se encheram de valor,
E s\xF3 teremos a perfeita santidade,
Depois que formos para o monte do Senhor.`,verseorder:7,ismainverse:0,id_hino:380},{idverse:26557,verse:`1. 
Vejo um homem na cruz pendurado
A derramar sangue, por meu pecado,
\xC9 o Cordeiro de Deus imolado,
Que por mim sofre grand\xEDssima dor;
Quanto padece na cruz, no altar,
Cristo Jesus, o meu bom Salvador,
Para fazer-me um tesouro herdar,
No santo reino do Senhor!
`,verseorder:1,ismainverse:0,id_hino:381},{idverse:26558,verse:`2. 
Em ora\xE7\xE3o, quando rompe  alva,
Sinto remorsos no fundo da alma,
Porque na cruz sofreu Cristo com calma,
Todo castigo, por mim pecador;
Quis o supremo Pai determinar
T\xE3o cruel morte ao meu Salvador;
Para no c\xE9u conceder-me lugar
Junto de Cristo Redentor.
`,verseorder:2,ismainverse:0,id_hino:381},{idverse:26559,verse:`3. 
Oh! Quanta dor n\xE3o sentiu o Eterno,
Quando nos deu Cristo meigo e terno!
Para minh\`alma salvar do inferno,
Por mim morreu o meu bom Salvador,
Os meus pecados na cruz expiou
E morte horrenda sofreu com amor,
Mas sobre Si minha culpa tomou,
O meu amado Redentor.
`,verseorder:3,ismainverse:0,id_hino:381},{idverse:26560,verse:`4. 
Faz Jesus Cristo, que eu  mais Te ame
E mais e mais Tua gra\xE7a proclame;
Livra-me de todos fortes liames,
Que sempre tece o vil tentador;
Sobre o altar, por mim Cristo subiu,
Oferecendo holocausto de amor;
Qual um cordeiro, na cruz sucumbiu
Cristo Jesus, meu salvador.`,verseorder:4,ismainverse:0,id_hino:381},{idverse:26561,verse:`1. 
Na cruz do Calv\xE1rio olhando,
Avistei o Salvador;
Meu pecado expiando,
Revelou-se o Seu amor.
`,verseorder:1,ismainverse:0,id_hino:382},{idverse:26562,verse:`L\xE1 na fonte desta luz,
que me trouxe um clar\xE3o,
Foi onde eu achei abrigo,
Vida, paz e salva\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:382},{idverse:26563,verse:`2. 
Vi Jesus crucificado
A sofrer t\xE3o grande dor,
Foi Seu sangue derramado
Pelo pobre pecador.
`,verseorder:3,ismainverse:0,id_hino:382},{idverse:26564,verse:`3. 
\xD3 Cordeiro imaculado,
Que morreste sobre a cruz!
Livra-me do vil pecado,
\xD3 meu Salvador Jesus!`,verseorder:4,ismainverse:0,id_hino:382},{idverse:26565,verse:`1. 
D\xE1 sustento \xE0 minha vida,
A Palavra do Senhor;
A minh\`alma abatida,
Vem poder Consolador:
Livramento glorioso
N\xF3s achamos em Jesus,
NEle o cora\xE7\xE3o ditoso
J\xE1 desfruta Sua luz.
`,verseorder:1,ismainverse:0,id_hino:383},{idverse:26566,verse:`Jesus Cristo deu-me salva\xE7\xE3o,
Tamb\xE9m vida eternal;
Deu-me gozo no meu cora\xE7\xE3o,
E poder celestial;
Sua gra\xE7a \xE9 bastante,
Divinal \xE9 Seu amor;
Tenho vida exultante,
Pelo bom Consolador!
`,verseorder:2,ismainverse:1,id_hino:383},{idverse:26567,verse:`2. 
Santos, todos proclamemos
Que Jesus h\xE1 de voltar,
E que Lhe servir devemos
E sinceros O amar.
Ressurgiu da sepultura,
E com vida se monstrou
As felizes criaturas,
Que no mundo tanto amou.
`,verseorder:3,ismainverse:0,id_hino:383},{idverse:26568,verse:`3. 
Que n\xE3o haja aciosos
Na Igreja do Senhor,
Mas, sim, crentes fervorosos
Trabalhando com vigor;
Quem aqui tiver vencido
Por Jesus e Seu amor,
H\xE1 de ter real vestido,
Para as bordas d\`esplendor!
`,verseorder:4,ismainverse:0,id_hino:383},{idverse:26569,verse:`4. 
Entraremos na formosa
Sala, cheia de fulgor,
Preparada para a Esposa
De Jesus, o Salvador;
Quando formos l\xE1 chegados
Grande festa haver\xE1;
E Jesus, que nos tem dado
Salva\xE7\xE3o, nos servir\xE1`,verseorder:5,ismainverse:0,id_hino:383},{idverse:26570,verse:`1. 
Sinto que vivo no Senhor,
Que veio me salvar;
E Sua lei, por Seu amor,
Desejo sempre guardar.
`,verseorder:1,ismainverse:0,id_hino:384},{idverse:26571,verse:`Habitarei sempre em Jesus,
Firm\xE1-lo-ei no cora\xE7\xE3o;
Deus, pela f\xE9, em mim produz
Perfei\xE7\xE3o!
`,verseorder:2,ismainverse:1,id_hino:384},{idverse:26572,verse:`2. 
Sinto que vivo no Senhor,
Em liberdade j\xE1,
Jesus me cobre de favor,
Repouso sempre me d\xE1.
`,verseorder:3,ismainverse:0,id_hino:384},{idverse:26573,verse:`3. 
Sinto que vivo no Senhor,
Na rocha eternal,
Que me sustenta com vigor,
Nas tempestades do mal.
`,verseorder:4,ismainverse:0,id_hino:384},{idverse:26574,verse:`4. 
Sinto que vivo no Senhor.
E sempre viverei;
Por Seu poder consolador,
Em Deus permanecerei.`,verseorder:5,ismainverse:0,id_hino:384},{idverse:26575,verse:`1. 
Irm\xE3os, amemos o Senhor,
Por Seu divinal amor;
Pois a nossa culpa expiou,
Com sangue que derramou.
`,verseorder:1,ismainverse:0,id_hino:385},{idverse:26576,verse:`Irm\xE3os, amemos o Senhor,
Pois Ele j\xE1 nos amou;
E do pecado enganador,
Com Seu sangue nos lavou.
`,verseorder:2,ismainverse:1,id_hino:385},{idverse:26577,verse:`2. 
Irm\xE3os, amemos o Senhor,
O nosso ensinador;
Ele \xE9 a fonte do saber
P\`ra todo que nEle crer.
`,verseorder:3,ismainverse:0,id_hino:385},{idverse:26578,verse:`3. 
Irm\xE3os, amemos o Senhor,
O nosso bom Salvador;
E o consumador daquela f\xE9,
Que desce do Deus, que "\xC9".
`,verseorder:4,ismainverse:0,id_hino:385},{idverse:26579,verse:`4. 
Irm\xE3os, amemos o Senhor,
Tamb\xE9m o Consolador,
E  a nosso Pai Celestial,
Que paz nos d\xE1 eternal.`,verseorder:5,ismainverse:0,id_hino:385},{idverse:26580,verse:`1. 
\xD3, n\xE3o temas tu, Esposa de Jesus;
Em justi\xE7a Deus te edificar\xE1!
Tua renda que dimana viva luz,
Ele mais aumentar\xE1.
`,verseorder:1,ismainverse:0,id_hino:386},{idverse:26581,verse:`Toda arma de rancor,
Contra ti se desfar\xE1,
Quando o inimigo
se erguer com seu furor
Ele n\xE3o te vencer\xE1!
`,verseorder:2,ismainverse:1,id_hino:386},{idverse:26582,verse:`2. 
Toda arma fabricada com rancor,
Te combate, mais jamais te vencer\xE1,
O inimigo que ataca com furor,
O Senhor abater\xE1.
`,verseorder:3,ismainverse:0,id_hino:386},{idverse:26583,verse:`3. 
L\xE1 no c\xE9u n\xE3o h\xE1s de ter recorda\xE7\xE3o
Dos combates que sofreste por amor;
Mas ali ter\xE1s sublime posi\xE7\xE3o
Com Jesus, teu salvador.
`,verseorder:4,ismainverse:0,id_hino:386},{idverse:26584,verse:`4. 
Patriarcas e profetas j\xE1 est\xE3o
L\xE1 no c\xE9u te esperando com amor;
S\xEA fiel e guarda a divinal un\xE7\xE3o
Para as bodas d\`esplendor.`,verseorder:5,ismainverse:0,id_hino:386},{idverse:26585,verse:`1. 
Derrama sobre n\xF3s o Teu Esp\xEDrito,
Como fizeste em Jerusal\xE9m;
\xC0 Tua grei, \xD3 manda o mesmo fogo,
Indispens\xE1vel para n\xF3s tamb\xE9m!
\xC0 Tua grei, \xD3 manda o mesmo fogo,
Indispens\xE1vel para n\xF3s tamb\xE9m!
`,verseorder:1,ismainverse:0,id_hino:387},{idverse:26586,verse:`2. 
Derrama sobre n\xF3s o Teu Esp\xEDrito
Como em casa do centuri\xE3o,
E d\xE1-nos o poder da Tua Palavra
Fazendo a luz brilhar na escurid\xE3o.
E d\xE1-nos o poder da Tua Palavra
Fazendo a luz brilhar na escurid\xE3o.
`,verseorder:2,ismainverse:0,id_hino:387},{idverse:26587,verse:`3. 
Derrama sobre n\xF3s o Teu Esp\xEDrito,
E d\xE1-nos, hoje, muitas convers\xF5es;
\xD3 deixa-nos sentir poder celeste,
E vivifica os nossos cora\xE7\xF5es
\xD3 deixa-nos sentir poder celeste,
E vivifica os nossos cora\xE7\xF5es.
`,verseorder:3,ismainverse:0,id_hino:387},{idverse:26588,verse:`4. 
Derrama sobre n\xF3s o Teu Esp\xEDrito,
E aos que sofrem d\xE1 Tua prote\xE7\xE3o;
A orar ficamos em amor unidos,
Para obter a prometida un\xE7\xE3o,
A orar ficamos em amor unidos,
Para obter a prometida un\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:387},{idverse:26589,verse:`5. 
Desperta, Jesus Cristo, os que dormem
O mui profundo sono do "jardim";
Como operaste nos antigos tempos,
Com Teu poder nos guia at\xE9 o fim.
Como operaste nos antigos tempos,
Com Teu poder nos guia at\xE9 o fim.`,verseorder:5,ismainverse:0,id_hino:387},{idverse:26590,verse:`1. 
Canta com vida, \xF3 crente!
Doce ser\xE1 cantar!
Anda s\xF3 para a frente,
Deixa o teu pesar;
Canta nas noites tristes,
Canta no sol, na luz;
O mal assim resistes:
Canta p\`ra Jesus!
`,verseorder:1,ismainverse:0,id_hino:388},{idverse:26591,verse:`2. 
Canta com vida, \xF3 Crente,
Alegra o cora\xE7\xE3o!
Louva ao Deus clemente,
Com feliz can\xE7\xE3o!
Cheio est\xE1 o mundo
De turba\xE7\xE3o e dor;
Canta o amor profundo
Do teu salvador.
`,verseorder:2,ismainverse:0,id_hino:388},{idverse:26592,verse:`3. 
Canta com vida, \xF3 crente!
Deus teu socorro \xE9;
Guarda-te a m\xE3o potente,
Se tiveres f\xE9.
Cristo, sim, te levanta,
Quando medroso est\xE1s,
Se confiando cantas
Seu amor veraz.`,verseorder:3,ismainverse:0,id_hino:388},{idverse:26593,verse:`1. 
Tua justi\xE7a eu quero cumprir,
Alegremente me vou batizar
Por imers\xE3o, para Cristo seguir
E meus pecados, assim sepultar.
`,verseorder:1,ismainverse:0,id_hino:389},{idverse:26594,verse:`Lava-me, lava-me, \xF3 Deus de amor,
No sangue puro de Cristo Jesus;
Torna minh\`alma mais alva que a luz,
No sangue puro de Cristo Jesus.
`,verseorder:2,ismainverse:1,id_hino:389},{idverse:26595,verse:`2. 
Quando das \xE1guas eu ressuscitar,
J\xE1 cria\xE7\xE3o nova sou em Jesus;
Posso e Ele servir e honrar,
Por crer na obra sublime da cruz.
`,verseorder:3,ismainverse:0,id_hino:389},{idverse:26596,verse:`3. 
Com toda for\xE7a, meu bom salvador,
Teu santo nome eu invocarei,
Para que eu v\xE1 de valor em valor,
Ao c\xE9u de luz, onde descansarei.`,verseorder:4,ismainverse:0,id_hino:389},{idverse:26597,verse:`1. 
Um cora\xE7\xE3o, bondoso sim, custa dor, obter;
Ser manso, amoroso e santo em todo o ser;
Manando nova vida ao cora\xE7\xE3o - d\xE1 luz.
E toda a minha lida ser\xE1 s\xF3 em Jesus.
`,verseorder:1,ismainverse:0,id_hino:390},{idverse:26598,verse:`2. 
Quem quer seguir os passos
De nosso pai Abra\xE3o,
No cora\xE7\xE3o, os la\xE7os, ent\xE3o, se quebrar\xE3o;
Isaque \xE9 oferecido em cima do altar;
Jac\xF3, com gran gemido, Raquel vai enterrar.
`,verseorder:2,ismainverse:0,id_hino:390},{idverse:26599,verse:`3. 
Se grande \xE9 o sacrif\xEDcio - Isaque no altar,
Se torna em benef\xEDcio p\`ra quem o ofertar,
Pois tem profunda calma e paz celestial,
Tamb\xE9m de Deus alcan\xE7a, aprova\xE7\xE3o real.
`,verseorder:3,ismainverse:0,id_hino:390},{idverse:26600,verse:`4. 
Na cova, Jeremias, lamenta grande dor,
Na escurid\xE3o n\xE3o via o bom Consolador;
Sim, grandes amarguras se tornam em prazer,
V\xEAm anjos das alturas "O sonhador" a ver.
`,verseorder:4,ismainverse:0,id_hino:390},{idverse:26601,verse:`5. 
Ar\xE3o foi castigado por sua rebeli\xE3o,
E, triste, humilhado, aceita exorta\xE7\xE3o;
Consagra tua vida p\`ra Deus melhor honrar;
Assim em dor e lida, preciosas te guardar.
`,verseorder:5,ismainverse:0,id_hino:390},{idverse:26602,verse:`6. 
Acima nuvens densas o sol sempre a brilhar;
Promessas mais imensas n\xE3o podem a ti falhar.
P\xF5e plena confian\xE7a em quem n\xE3o pode errar,
Jesus, j\xE1 sem tardan\xE7a vem tua dor sarar.`,verseorder:6,ismainverse:0,id_hino:390},{idverse:26603,verse:`1. 
Jesus disse aos disc\xEDpulos, no monte, ao Subir:
"Detei-vos em Jerusal\xE9m, pois h\xE1 de se cumprir
A vinda gloriosa do Consolador,
P\`ra revestir as testemunhas do Senhor".
`,verseorder:1,ismainverse:0,id_hino:391},{idverse:26604,verse:`Descendo o fogo do
altar, o vento de amor,
Depressa foram proclamar o salvador.
`,verseorder:2,ismainverse:1,id_hino:391},{idverse:26605,verse:`2. 
Encheu-se o cen\xE1c\`lo de gloriosa luz;
Descendo o fogo divinal, a n\xF3s voltou Jesus.
E todos, revestidos com o poder do c\xE9u,
Falaram noutras l\xEDnguas em louvor a Deus.
`,verseorder:3,ismainverse:0,id_hino:391},{idverse:26606,verse:`3. 
O povo, assustado com o poder do c\xE9u,
N\xE3o compreendeu que isto foi o que Deus prometeu.
Mas Pedro lhes proclama: "Assim diz o Senhor:
No fim dos dias, Vos darei Consolador".
`,verseorder:4,ismainverse:0,id_hino:391},{idverse:26607,verse:`4. 
Assim Jesus te manda orar e esperar
O Santo Esp\xEDrito de Deus, que vem p\`ra t\`ensinar;
Jesus, a gloriosa un\xE7\xE3o, te mandar\xE1,
E Seu amor sublime, proclamar ir\xE1s.`,verseorder:5,ismainverse:0,id_hino:391},{idverse:26608,verse:`1. 
Peregrinos somos aqui
T\xE9 o labor findar;
Deste  mundo queremos ir
Ao celeste lar.
`,verseorder:1,ismainverse:0,id_hino:392},{idverse:26609,verse:`Oh! P\xE1tria mui feliz
Em ti irei morar;
Eu irei morar
Eu irei morar
Jerusal\xE9m, oh meu pa\xEDs
E meu querido lar!
Jerusal\xE9m, oh meu pa\xEDs
E meu querido lar!
`,verseorder:2,ismainverse:1,id_hino:392},{idverse:26610,verse:`2. 
Minha grande consola\xE7\xE3o
\xC9 sempre em ti morar,
Oh, querida, bela Si\xE3o,
Meu eterno lar!
`,verseorder:3,ismainverse:0,id_hino:392},{idverse:26611,verse:`3. 
Como o povo de Abra\xE3o,
Sempre a viajar,
Para a terra da promiss\xE3o,
Santo e doce lar.
`,verseorder:4,ismainverse:0,id_hino:392},{idverse:26612,verse:`4. 
\xD3 Senhor, queremos vencer,
E de Ti alcan\xE7ar
Gozo, paz, eterno prazer,
No bendito lar!
`,verseorder:5,ismainverse:0,id_hino:392},{idverse:26613,verse:`5. 
\xC9 o meu desejo, Jesus,
Sempre peregrinar;
Dirigido por Tua luz
T\xE9 que chegue ao lar.`,verseorder:6,ismainverse:0,id_hino:392},{idverse:26614,verse:`1. 
H\xE1 um amigo mui chegado - Cristo Senhor,
Pois salvou-nos do pecado e do temor;
Este amigo agonizava,
Quando o mundo resgatava,
E de Deus o amor mostrava; dai-Lhe louvor!
`,verseorder:1,ismainverse:0,id_hino:393},{idverse:26615,verse:`2. 
Conhec\xEA-Lo \xE9 ter vida, Cristo Senhor.
NEle nos \xE9 permitida paz no labor;
Ele sobre n\xF3s derrama
Sua gra\xE7a, pois nos ama,
E p\`ra Si a todos chama; dai-Lhe louvor!
`,verseorder:2,ismainverse:0,id_hino:393},{idverse:26616,verse:`3. 
Sempre \xE9 o mesmo, nunca muda, Cristo Senhor
\xC9 a pedra de ajuda; vem, pecador!
Bom man\xE1 \xE9 no deserto,
Nosso guia, amigo certo,
Seu amor \xE9 c\xE9u aberto; dai-Lhe louvor!`,verseorder:3,ismainverse:0,id_hino:393},{idverse:26617,verse:`1. 
Quem sua m\xE3o ao arado j\xE1 p\xF4s,
Constante precisa ser;
O sol declina e, logo ap\xF3s,
Vai escurecer.
Avante, em Cristo pensando,
Em ora\xE7\xE3o vigiando,
Com gozo e amor trabalhando,
P\`ra teu Senhor.
`,verseorder:1,ismainverse:0,id_hino:394},{idverse:26618,verse:`2. 
N\xE3o desanimes, por ser tua cruz
Maior que a de teu irm\xE3o;
A mais pesada levou teu Jesus,
Te consola, ent\xE3o;
A tua cruz vai levando,
Como Jesus perdoando,
Alegremente andando
P\`ra o lindo c\xE9u.
`,verseorder:2,ismainverse:0,id_hino:394},{idverse:26619,verse:`3. 
S\xEA bom soldado de Cristo Jesus,
Sofrendo as afli\xE7\xF5es,
N\xE3o sufocando a mensagem da cruz,
Nas persegui\xE7\xF5es;
Vai Seu amor proclamando,
Novas de paz, sim, levando,
Aos que est\xE3o aguardando
A salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:394},{idverse:26620,verse:`4. 
Quando, enfim, tu largares a cruz,
Jesus te coroar\xE1;
Com santo gozo em gl\xF3ria e luz
Te consolar\xE1.
Esquecer\xE1s teus lidares,
Tribula\xE7\xF5es e pesares,
Quando no c\xE9u desfrutares,
Perfeita paz.`,verseorder:4,ismainverse:0,id_hino:394},{idverse:26621,verse:`1. 
Ide por todo o mundo e dizei:
"\xC9-Me dado todo o poder";
Ide, anunciai que j\xE1 ressuscitei,
P\`ra dar vida a todo o que crer.
`,verseorder:1,ismainverse:0,id_hino:395},{idverse:26622,verse:`Confiando, irei onde me mandar;
Pela terra, pelo mar tamb\xE9m.
Eis que estou convosco para vos guardar,
Por todos os s\xE9culos. Am\xE9m.
`,verseorder:2,ismainverse:1,id_hino:395},{idverse:26623,verse:`2. 
Ide e ensinai todas as na\xE7\xF5es;
Publicai a salva\xE7\xE3o e paz;
Cristo j\xE1 venceu a morte e seus grilh\xF5es,
Liberdade agora, a todos traz.
`,verseorder:3,ismainverse:0,id_hino:395},{idverse:26624,verse:`3. 
Da salva\xE7\xE3o a porta aberta est\xE1;
Quem por ela hoje quer entrar?
E Jesus quem oferece e quem d\xE1
Livre entrada no celeste lar.`,verseorder:4,ismainverse:0,id_hino:395},{idverse:26625,verse:`1. 
Muito al\xE9m do  nosso entendimento,
Alto mais que todo o pensamento,
Glorioso em seu sublime intento,
\xC9 o amor de Deus, sem par.
`,verseorder:1,ismainverse:0,id_hino:396},{idverse:26626,verse:`Grande amor! Amor de Deus!
Enche a terra e enche os c\xE9us!
Grande amor! Amor que abrange
A todo o mundo e atinge a mim!
`,verseorder:2,ismainverse:1,id_hino:396},{idverse:26627,verse:`2. 
Fez um sacrif\xEDcio infinito
Dum valor imenso, inaudito;
Dando-nos o Filho Seu bendito;
Calculai o amor de Deus!
`,verseorder:3,ismainverse:0,id_hino:396},{idverse:26628,verse:`3. 
Grande, foi mui grande o meu pecado;
Triste, perigoso o meu estado;
Mas o amor que nunca foi sondado
Me salvou - o amor de Deus!
`,verseorder:4,ismainverse:0,id_hino:396},{idverse:26629,verse:`4. 
Foi quem perdoou os pecadores,
Rogos atendeu de malfeitores,
Quem sarou os pobres sofredores,
Esse imenso amor da Deus!`,verseorder:5,ismainverse:0,id_hino:396},{idverse:26630,verse:`1. 
Em pecados e temor o Salvador me achou,
T\xE3o indigno pecador, sem ter do c\xE9u a luz,
No Calv\xE1rio ao morrer minh\`alma resgatou;
Meu perd\xE3o foi consumado l\xE1 na cruz.
`,verseorder:1,ismainverse:0,id_hino:397},{idverse:26631,verse:`2. 
Vem ao Senhor, vem pecador:
Ele \xE9 o terno e mui fiel, vem pecador!
Vem ao Senhor, Deus \xE9 amor;
Ouve Sua terna voz: "Vem, pecador"!
`,verseorder:2,ismainverse:0,id_hino:397},{idverse:26632,verse:`3. 
Do seupulcro ressurgiu meu Redentor Jesus;
Ele a morte j\xE1 venceu - tamb\xE9m o nosso algoz;
Vida eterna o pecador goza por Sua cruz
Pois os mortos h\xE3o de ouvir Sua terna voz.
`,verseorder:3,ismainverse:0,id_hino:397},{idverse:26633,verse:`4. 
Para o c\xE9u subiu Jesus, ganha a vit\xF3ria c\xE1.
E na dextra do bom Deus,
Tem-No por Mediador,
Intercede em teu favor, n\xE3o te detenhas l\xE1.
N\xE3o desprezes esta voz de teu Senhor.`,verseorder:4,ismainverse:0,id_hino:397},{idverse:26634,verse:`1. 
Desejo estar em um lugar
Mui perto do meu Redentor;
Ali eu posso descansar,
Por Seu amparo e Seu amor.
`,verseorder:1,ismainverse:0,id_hino:398},{idverse:26635,verse:`Mui perto do meu Redentor,
Seguro abrigo encontrarei;
Me guardar\xE1 do tentador,
Assim eu nada temerei.
`,verseorder:2,ismainverse:1,id_hino:398},{idverse:26636,verse:`2. 
Ningu\xE9m me poder\xE1 tirar
A paz que frui o cora\xE7\xE3o;
Jesus bondoso vai me dar
A mais segura prote\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:398},{idverse:26637,verse:`3. 
Tristezas, nem temor terei,
Estando perto de Jesus;
Envolto sempre me verei,
Com Seu fulgor, com Sua luz.`,verseorder:4,ismainverse:0,id_hino:398},{idverse:26638,verse:`1. 
Deste mundo, sim, al\xE9m, h\xE1 um pa\xEDs de luz;
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?
Onde as trevas n\xE3o se v\xEAem,
pois brilha ali Jesus;
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?
`,verseorder:1,ismainverse:0,id_hino:399},{idverse:26639,verse:`Terra de Jesus,
terra de amor!
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?
Onde brilha a luz do meu Salvador;
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?
`,verseorder:2,ismainverse:1,id_hino:399},{idverse:26640,verse:`2. 
Pelas portas de fulgor, n\xE3o pode o  mal entrar;
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?
Nem a morte, luto ou dor, no c\xE9u ter\xE3o lugar,
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?
`,verseorder:3,ismainverse:0,id_hino:399},{idverse:26641,verse:`3. 
Oh! Que divinal mans\xE3o Jesus nos preparou!
Oh! N\xE3o queres ir l\xE1! Oh! N\xE3o queres ir l\xE1?
Onde todos os irm\xE3os
v\xE3o ver quem os salvos,
Oh! N\xE3o queres ir l\xE1? Oh! N\xE3o queres ir l\xE1?`,verseorder:4,ismainverse:0,id_hino:399},{idverse:26642,verse:`1. 
Em Jesus, vivendo cada dia,
Em Jesus eu tenho alegria!
Em Jesus, oh, doce harmonia!
Em Jesus, desfruto a paz de Deus!
`,verseorder:1,ismainverse:0,id_hino:400},{idverse:26643,verse:`2. 
Em Jesus, na rocha inabal\xE1vel,
Em Jesus, no homem incompar\xE1vel!
Em Jesus, no Deus t\xE3o ador\xE1vel!
Em Jesus, o mal n\xE3o temerei!
`,verseorder:2,ismainverse:0,id_hino:400},{idverse:26644,verse:`3. 
Em Jesus a gra\xE7a \xE9 infinita,
Em Jesus, oh! B\xEAn\xE7\xE3o inaudita;
Em Jesus, minh\`alma \xE9 bendita;
Em Jesus eu tenho salva\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:400},{idverse:26645,verse:`4. 
Em Jesus n\xE3o temo o mal e a morte,
Em Jesus estou firmado e forte!
Em Jesus meu barco ruma ao norte;
Em Jesus eu sempre hei de vencer!`,verseorder:4,ismainverse:0,id_hino:400},{idverse:26646,verse:`1. 
Tudo nos mostra que Cristo j\xE1 volta;
Breve Jesus voltar\xE1!
J\xE1 deste mundo o mar se revolta;
Breve Jesus Voltar\xE1.
`,verseorder:1,ismainverse:0,id_hino:401},{idverse:26647,verse:`2. 
Breve vir\xE1, brve vir\xE1,
Breve Jesus voltar\xE1.
`,verseorder:2,ismainverse:0,id_hino:401},{idverse:26648,verse:`3. 
Crist\xE3o, acorda, Sua vinda \xE9 certa:
Breve Jesu voltar\xE1!
P\`ra receb\xEA-Lo est\xE1s bem alerta?
Breve Jesus voltar\xE1.
`,verseorder:3,ismainverse:0,id_hino:401},{idverse:26649,verse:`4. 
Crente proclama para os pecadores:
Breve Jesus voltar\xE1!
N\xE3o haver\xE1 mais tristezas nem dores:
Breve Jesus voltar\xE1.
`,verseorder:4,ismainverse:0,id_hino:401},{idverse:26650,verse:`5. 
Consola o cora\xE7\xE3o que lhe clama:
Breve Jesus voltar\xE1!
P\`ra Suas bodas o bom Rei nos chama:
Breve Jesus voltar\xE1.`,verseorder:5,ismainverse:0,id_hino:401},{idverse:26651,verse:`1. 
Ao G\xF3lgota ascende alma minha,
Contempla ali teu Senhor,
Tamb\xE9m a agonia que tinha,
Sofrendo por ti com amor;
N\xE3o v\xEAs uma rubra torrente
Cobrindo a terra de horror?
\xC9 o sangue de Cristo inocente
Vertido por mim, pecador.
`,verseorder:1,ismainverse:0,id_hino:402},{idverse:26652,verse:`2. 
Minh\`alma, te lava contente
No sangue do teu Redentor!
\xC9 Cristo a senda somente,
Que leva ao repouso de amor;
Jubila no onipotente
Por toda a Sua merc\xEA,
Tamb\xE9m pelo dom excelente
De Cristo em todo o que cr\xEA!
`,verseorder:2,ismainverse:0,id_hino:402},{idverse:26653,verse:`3. 
Minh\`alma, \xF3 sobe p\`ra o alto!
Ali ouvir\xE1s o falar
D\`Aquele a quem eu exalto;
N\xE3o sabes que v\xEA teu penar?
Na gra\xE7a divina medita,
Que tens em Jesus, teu Senhor;
Recebe a gl\xF3ria bendita
Orando a Deus, com fervor!
`,verseorder:3,ismainverse:0,id_hino:402},{idverse:26654,verse:`4. 
Hosanas ao onisciente!
Jesus veio aqui p\`ra salvar
Minh\`alma da "velha serpente"
Que anda no mundo a tentar;
Que anda no mundo a tentar;
Hosanas a Deus entoemos
Por Sua palavra de amor;
Hosanas e gl\xF3ria rendemos,
A Cristo Jesus, o Senhor.`,verseorder:4,ismainverse:0,id_hino:402},{idverse:26655,verse:`1. 
Oh! Que gozo real sinto no meu ser,
J\xE1 tenho de Deus perd\xE3o!
Pois Jesus nos diz: Que qualquer que crer
Recebe salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:403},{idverse:26656,verse:`Se tu cr\xEAs, \xF3 vem o passo dar,
Vem o passo dar, sim, vem o passo dar!
Se tu cr\xEAs, \xF3 vem o passo dar,
Pois Jesus te quer salvar.
`,verseorder:2,ismainverse:1,id_hino:403},{idverse:26657,verse:`2. 
Por amor foi Cristo, meu Redentor,
Da gl\xF3ria, aqui desceu;
E transforma a vida dum pecador,
Na gra\xE7a que nos deu.
`,verseorder:3,ismainverse:0,id_hino:403},{idverse:26658,verse:`3. 
Que merc\xEA, que amor o Senhor mostrou
Morrendo na dura cruz;
Sangue divinal por n\xF3s derramou,
O Salvador Jesus!`,verseorder:4,ismainverse:0,id_hino:403},{idverse:26659,verse:`1. 
Espera do c\xE9u, seu Senhor, o fiel
Aguarda orando, pois h\xE1 de voltar
Na nuvem, em gl\xF3ria o Emanuel,
Com grande poder, a fim de o buscar.
`,verseorder:1,ismainverse:0,id_hino:404},{idverse:26660,verse:`Em gl\xF3ria vir\xE1 o Filho de
Deus,
Sempre orando Jesus esperai;
Se virdes a "estrela da alva" no c\xE9u,
Alerta estai!
`,verseorder:2,ismainverse:1,id_hino:404},{idverse:26661,verse:`2. 
\xD3 pai amoroso, envia Jesus,
O crente fiel O espera do c\xE9u;
\xD3 manda-lhe logo a celeste luz,
Em que h\xE1 de vir o Filho de Deus.
`,verseorder:3,ismainverse:0,id_hino:404},{idverse:26662,verse:`3. 
O santo Evangelho se h\xE1 de cumprir
E breve nas nuvens veremos Jesus;
"Assim descer\xE1 como O vistes subir"
O que S\`imolou por n\xF3s sobre a cruz!`,verseorder:4,ismainverse:0,id_hino:404},{idverse:26663,verse:`1. 
Queres ser salvo, queres perd\xE3o,
Paz e repouso no cora\xE7\xE3o?
O que far\xE1s para teres a luz?
Com toda a for\xE7a - Cr\xEA em Jesus;
Se hoje queres em Cristo crer,
Inda que morto h\xE1s de viver;
Deixa este mundo enganador,
Cr\xEA s\xF3 em Cristo, teu Salvador.
`,verseorder:1,ismainverse:0,id_hino:405},{idverse:26664,verse:`2. 
O cr\xEA em Cristo - ei-Lo aqui
Em nosso meio, chora por ti;
Ele te ama; queres a luz?
Queres vit\xF3ria? - Cr\xEA em Jesus,
Sim! Hoje creio no Salvador,
\xC9 minha vida, meu Redentor;
Nas minhas lutas, o Capit\xE3o;
Creio em Cristo de cora\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:405},{idverse:26665,verse:`3. 
Quando o inimigo com seu terror
Ruge em torno com mui furor
N\xE3o desanimes por tua cruz,
Olha p\`ra cima - Cr\xEA em Jesus;
E nas terr\xEDveis provas d\`horror,
Segue avante com teu Senhor,
Ele te guarda da corrup\xE7\xE3o
E te liberta da tenta\xE7\xE3o.`,verseorder:3,ismainverse:0,id_hino:405},{idverse:26666,verse:`1. 
Precioso sempre encostar-me
Aos p\xE9s do meu bom Redentor;
Presto vir\xE1 p\`ra levar-me
Aos c\xE9us, minha p\xE1tria de amor.
`,verseorder:1,ismainverse:0,id_hino:406},{idverse:26667,verse:`Eu preciso sempre lan\xE7ar-me
Nos bra\xE7os do meu Redentor;
S\xF3 em Jesus vou tamb\xE9m abrigar-me
No Seu sublime amor.
`,verseorder:2,ismainverse:1,id_hino:406},{idverse:26668,verse:`2. 
Precioso sempre deter-me
\xC0 sombra do meu Redentor;
N\xE3o posso s\xF3 defender-me
E al\xE7ar a bandeira de amor.
`,verseorder:3,ismainverse:0,id_hino:406},{idverse:26669,verse:`3. 
Preciso sempre chegar-me
Contrito aos p\xE9s do Salvador,
Para poder consolar-me
No Seu abundante amor.`,verseorder:4,ismainverse:0,id_hino:406},{idverse:26670,verse:`1. 
\xD3 Criador bendito, \xF3 fonte de esperan\xE7a,
A Ti, \xF3 Pai contritos, buscamos com confian\xE7a;
Nos deste em heran\xE7a a gl\xF3ria sempiterna;
O p\xE3o da vida eterna, \xF3 manda-nos dos c\xE9us!
`,verseorder:1,ismainverse:0,id_hino:407},{idverse:26671,verse:`2. 
Excelso Criador, supremo benfeitor,
O Teu amor derrama em n\xF3s, qual viva chama
Excelso Criador, supremo benfeitor,
O Teu amor derrama, por Teu Consolador.
`,verseorder:2,ismainverse:0,id_hino:407},{idverse:26672,verse:`3. 
Unidos espermaos o Teu celeste ensino;
\xD3 manda, suplicamos por Teu poder divino;
Aceita este hino, que n\xF3s a Ti cantamos,
Pois gratos Te louvamos, por Tua salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:407},{idverse:26673,verse:`4. 
O cora\xE7\xE3o se alegra e a alma est\xE1 contente
No dia em que se entrega
ao Deus onipotente;
Que paz profunda sente a alma do perdido
Que hoje \xE9 um remido por Cristo, o Salvador!`,verseorder:4,ismainverse:0,id_hino:407},{idverse:26674,verse:`1. 
Abre os meus olhos em Tua luz,
Teu rosto quero ver, Jesus,
P\xF5e em meu cora\xE7\xE3o Teu amor,
E d\xE1-me a paz e Teu fervor;
Humildemente acudo a Ti,
Porque Tua terna voz ouvi
Meu guia s\xEA, Esp\xEDrito Consolador.
`,verseorder:1,ismainverse:0,id_hino:408},{idverse:26675,verse:`2. 
Abro os ouvidos para ouvir
Tua Palavra e me ungir,
Belas palavras de doce amor,
O! Meu bendito Salvador,
Consagro a Ti meu fr\xE1gil ser!
Para cumprir o Teu querer,
Enche meu ser, Esp\xEDrito Consolador.
`,verseorder:2,ismainverse:0,id_hino:408},{idverse:26676,verse:`3. 
Abre-me a mente para ver
O Teu amor em gran poder;
D\xE1-me Tua gra\xE7a, d\xE1-me vigor;
Faz-me na luta, vencedor;
S\xEA tu o meu ref\xFAgio aqui,
Aumenta minha f\xE9 em Ti,
Sustenta-me, Esp\xEDrito Consolador.
`,verseorder:3,ismainverse:0,id_hino:408},{idverse:26677,verse:`4. 
Abre-me a porta divinal
Do Teu pal\xE1cio de cristal,
Pois o Teu lindo rosto Senhor,
Quero no c\xE9u ver, d\`esplendor;
E quando l\xE1 enfim entrar,
Teu santo nome vou louvar
Pelo teu bom Esp\xEDrito Consolador.`,verseorder:4,ismainverse:0,id_hino:408},{idverse:26678,verse:`1. 
Trabalhadores do Evangelho,
Em breve ceifareis;
Quer  fracos v\xF3s sejais, ou velhos,
Do c\xE9u vigor tereis.
`,verseorder:1,ismainverse:0,id_hino:409},{idverse:26679,verse:`Os que esperam no Senhor
Novas for\xE7as obter\xE3o;
Como \xE1guias voar\xE3o,
Subir\xE3o para as alturas;
Correr\xE3o sem se cansar,
Sem fatigar h\xE3o de andar;
Correr\xE3o sem se cansar,
Sem fatigar h\xE3o de andar;
Correr\xE3o sem se cansar,
At\xE9 no c\xE9u chegar!
`,verseorder:2,ismainverse:1,id_hino:409},{idverse:26680,verse:`2. 
No bom trabalho, quantas vezes,
Estamos a falhar,
Sempre devemos nos revezes
Em Cristo confiar.
`,verseorder:3,ismainverse:0,id_hino:409},{idverse:26681,verse:`3. 
Jesus est\xE1 ao nosso lado,
Sua for\xE7a nos dar\xE1;
O nosso Salvador amdao,
Sim, tudo suprir\xE1.
`,verseorder:4,ismainverse:0,id_hino:409},{idverse:26682,verse:`4. 
Em Cristo sempre confiando
Socorro vamos ter;
O Salvador do c\xE9u baixando
Vir\xE1 nos socorrer!`,verseorder:5,ismainverse:0,id_hino:409},{idverse:26683,verse:`1. 
Jesus \xE9 meu Rei glorioso,
\xC9 grato cantar-Lhe louvor;
\xC9 Rei, mas me torna ditoso,
Enchendo-me do Seu amor;
Deixando Seu trono de gl\xF3ria
Me veio livrar desta esc\xF3ria.
J\xE1 sou mui feliz, j\xE1 sou mui feliz com Deus!
`,verseorder:1,ismainverse:0,id_hino:410},{idverse:26684,verse:`2. 
Jesus \xE9 meu Rei mui amado,
Nas alturas, na dor, sempre est\xE1
Paciente e humilde ao meu lado,
Ajuda e consolo me d\xE1;
Por isso constante O sigo,
Pois \xE9 meu Pastor e amigo.
J\xE1 sou mui feliz, j\xE1 sou mui feliz com Deus!
`,verseorder:2,ismainverse:0,id_hino:410},{idverse:26685,verse:`3. 
Jesus, que pudera eu dar-Te
Por tanto amor para mim?
Bastante \xE9 servir-Te e amar-Te?
E tudo entregar-me a Ti?
Aceita, ent\xE3o, minha vida,
Que a Ti s\xF3, se prostra rendida.
J\xE1 sou mui feliz, j\xE1 sou mui feliz com Deus!`,verseorder:3,ismainverse:0,id_hino:410},{idverse:26686,verse:`1. 
Filho do eterno Deus, n\xF3s somos Teus!
Compraste-nos, Jesus, por Tua cruz,
Rendemo-nos, Senhor, cheios de f\xE9 e amor;
N\xF3s somos Teus, Filho de Deus!
`,verseorder:1,ismainverse:0,id_hino:411},{idverse:26687,verse:`2. 
O Teu caminho, \xF3 Deus que leva ao c\xE9u,
Faze-nos percorrer at\xE9 morrer.
N\xE3o tem que recear todo o que nele andar;
Tu \xE9s, Senhor, o bom Pastor.
`,verseorder:2,ismainverse:0,id_hino:411},{idverse:26688,verse:`3. 
A quem nos quis salvar vamos louvar
Com voz e cora\xE7\xE3o, em santa uni\xE3o,
Aceita, pois, Senhor, nosso veraz louvor!
Gra\xE7as, oh! Deus, que somos Teus!`,verseorder:3,ismainverse:0,id_hino:411},{idverse:26689,verse:`1. 
Gl\xF3ria ao Salvador divino,
Nosso guia e Redentor,
Gl\xF3ria seja ao Deus trino;
Aleluia ao Deus de amor!
`,verseorder:1,ismainverse:0,id_hino:412},{idverse:26690,verse:`Jesus Cristo, bem amado,
Poderoso Salvador,
Ele era ontem, \xE9 hoje,
Ser\xE1 sempre o Deus de amor!
`,verseorder:2,ismainverse:1,id_hino:412},{idverse:26691,verse:`2. 
Alegrar-se todos devem
Pela grande salva\xE7\xE3o;
Honra e gl\xF3ria a Deus elevem,
Pois nos d\xE1 Seu galard\xE3o.
`,verseorder:3,ismainverse:0,id_hino:412},{idverse:26692,verse:`3. 
Adorai a Deus, com canto,
Ele \xE9 compassivo e bom,
\xD3 divino Esp\xEDrito Santo,
Des\xE7a sobre n\xF3s Teu dom.
`,verseorder:4,ismainverse:0,id_hino:412},{idverse:26693,verse:`4. 
Ao Teu nome seja gl\xF3ria,
\xD3 bendito e bom Senhor!
Tu nos guias p\`ra vit\xF3ria,
Aleluia ao Redentor!`,verseorder:5,ismainverse:0,id_hino:412},{idverse:26694,verse:`1. 
Meu Pastor \xE9 Jesus Cristo
NEle quero confiar;
Com Jesus, n\xE3o tenho falta,
Junto a Ele eu quero andar;
Me conduz ao bom descanso;
Para o campo verde em flor,
Refrigera, sim, minh\`alma,
Saciando-me de amor.
`,verseorder:1,ismainverse:0,id_hino:413},{idverse:26695,verse:`2. 
Ele \xE9 meu companheiro;
Com Jesus desejo andar;
Pois me d\xE1 vigor e for\xE7a,
P\`ra que eu possa caminhar;
Ele \xE9 quem d\xE1 coragem,
Para nada eu temer;
Mesmo pr\xF3ximo \xE0 morte
Certo estou que vou vencer.
`,verseorder:2,ismainverse:0,id_hino:413},{idverse:26696,verse:`3. 
Caminhando ao Seu lado
N\xE3o mais temerei o mal;
Seu bord\xE3o e Seu cajado
D\xE3o-me prote\xE7\xE3o real;
Contra o  meu inimigo
O Senhor sempre vir\xE1;
Me prepara uma mesa
E comigo nela est\xE1.
`,verseorder:3,ismainverse:0,id_hino:413},{idverse:26697,verse:`4. 
Unge a cabe\xE7a minha
Com \xF3leo do Seu amor;
O meu gozo \xE9 transbordante,
Pois meu c\xE1lice tem dul\xE7or;
Por Sua miseric\xF3rdia
E por Seu sublime bem
Habitarei em Sua casa
Eternamente, am\xE9m!`,verseorder:4,ismainverse:0,id_hino:413},{idverse:26698,verse:`1. 
\xF3 Salvador, \xF3 Pai de amor!
Tem compaix\xE3o do pecador;
Sem ser em Ti n\xE3o achar\xE1
A salva\xE7\xE3o, que Tu nos d\xE1s.
`,verseorder:1,ismainverse:0,id_hino:414},{idverse:26699,verse:`Tem compaix\xE3o, \xF3 Salvador,
Tem compaix\xE3o do pecador!
\xD3 vem lavar seu cora\xE7\xE3o,
\xD3 vem lhe dar a salva\xE7\xE3o!
`,verseorder:2,ismainverse:1,id_hino:414},{idverse:26700,verse:`2. 
Sem ser em Ti n\xE3o h\xE1 perd\xE3o
P\`ra o pecador, nem salva\xE7\xE3o;
S\xF3 mesmo em Ti o pecador
Encontrar\xE1 um Salvador.
`,verseorder:3,ismainverse:0,id_hino:414},{idverse:26701,verse:`3. 
\xD3 Rei dos reis, tem compaix\xE3o
Do pecador na perdi\xE7\xE3o
Seu cora\xE7\xE3o, \xF3 vem lavar
No sangue Teu purificar.`,verseorder:4,ismainverse:0,id_hino:414},{idverse:26702,verse:`1. 
A terna voz do Salvador
Te fala comovida;
"\xD3 vem ao m\xE9dico de amor,
Que d\xE1 aos mortos vida"!
`,verseorder:1,ismainverse:0,id_hino:415},{idverse:26703,verse:`Cristo Jesus te quer curar,
E tem poder p\`ra te curar,
Dos males todos te livrar,
Se nEle confiares!
`,verseorder:2,ismainverse:1,id_hino:415},{idverse:26704,verse:`2. 
Cr\xEA tu, a quem j\xE1 Satan\xE1s
H\xE1 anos tem ligado;
A f\xE9 te salva, vai-te em paz,
De todo o mal curado!
`,verseorder:3,ismainverse:0,id_hino:415},{idverse:26705,verse:`3. 
Os surdos ouvem, cegos v\xEAem,
Pois Cristo \xE9 poderoso!
os coxos saram e andam bem,
Por Seu poder glorioso!
`,verseorder:4,ismainverse:0,id_hino:415},{idverse:26706,verse:`4. 
Sinais p\`ra sempre seguir\xE3o
Aos verdadeiros crentes!
Dem\xF4nios, sim, expulsar\xE3o,
E curar\xE3o as gentes.`,verseorder:5,ismainverse:0,id_hino:415},{idverse:26707,verse:`1. 
Anjos aos milhares, no c\xE9u, ver\xE3o
Quando o povo salvo entrar;
Crentes coroados tamb\xE9m ser\xE3o,
Quando o povo salvo entrar.
`,verseorder:1,ismainverse:0,id_hino:416},{idverse:26708,verse:`Quando povo salvo entrar,
Quando povo salvo entrar;
Muito grande alegria no c\xE9u haver\xE1,
Quando o povo salvo entrar.
`,verseorder:2,ismainverse:1,id_hino:416},{idverse:26709,verse:`2. 
Os remidos l\xE1 h\xE3o de se encontrar,
Quando o povo salvo entrar;
Vestes brancas todos h\xE3o de trajar,
Quando o povo salvo entrar.
`,verseorder:3,ismainverse:0,id_hino:416},{idverse:26710,verse:`3. 
Todos vencedores, l\xE1 h\xE3o de estar,
Quando o povo salvo entrar;
A tribula\xE7\xE3o n\xE3o ter\xE1 lugar,
Quando o povo salvo entrar.
`,verseorder:4,ismainverse:0,id_hino:416},{idverse:26711,verse:`4. 
O "Cordeiro morto", (que vivo est\xE1)
Quando o povo salvo entrar;
Para sempre em gl\xF3ria dominar\xE1,
Quando o povo salvo entrar.`,verseorder:5,ismainverse:0,id_hino:416},{idverse:26712,verse:`1. 
No meio duma dor,
A cruz de Cristo eu vi,
Qual viva fonte de amor,
Bastante para mim!
`,verseorder:1,ismainverse:0,id_hino:417},{idverse:26713,verse:`L\xE1 da cruz corre sem cessar,
Insond\xE1vel como \xE9 o mar.
Esta gra\xE7a, que brota assim,
Basta para mim!
`,verseorder:2,ismainverse:1,id_hino:417},{idverse:26714,verse:`2. 
Meu cora\xE7\xE3o, a vacilar
E apenas, pude assim
Na gra\xE7a crer; mas fui achar
Bastante para mim!
`,verseorder:3,ismainverse:0,id_hino:417},{idverse:26715,verse:`3. 
Cravados j\xE1, ent\xE3o, na cruz,
Os meus pecados vi;
E a gra\xE7a achei de meu Jesus;
Bastante para mim!
`,verseorder:4,ismainverse:0,id_hino:417},{idverse:26716,verse:`4. 
Com meu Jesus eu cantarei
No c\xE9u, alegre, enfim;
Que Sua gra\xE7a foi, direi:
Bastante para mim!`,verseorder:5,ismainverse:0,id_hino:417},{idverse:26717,verse:`1. 
Sou um soldado de Jesus
E servo do Senhor;
N\xE3o temerei levar a cruz,
Sofrendo grande dor.
`,verseorder:1,ismainverse:0,id_hino:418},{idverse:26718,verse:`Depois da batalha me coroar\xE1,
Deus me coroar\xE1;
Deus me coroar\xE1;
Depois da batalha me coroar\xE1,
Na celeste mans\xE3o;
L\xE1 verei o meu Rei,
E terei meu galard\xE3o,
Depois da batalha me coroar\xE1,
Na cidade de Si\xE3o!
`,verseorder:2,ismainverse:1,id_hino:418},{idverse:26719,verse:`2. 
Lutaram outros sem temor,
Mui forte hei de ser;
Pelejarei por meu Senhor,
Confiando em Seu poder.
`,verseorder:3,ismainverse:0,id_hino:418},{idverse:26720,verse:`3. 
Se eu for fiel ao meu Jesus
E n\xE3o voltar p\`ra tr\xE1s,
Alcan\xE7arei no c\xE9u de luz,
Lugar de santa paz.`,verseorder:4,ismainverse:0,id_hino:418},{idverse:26721,verse:`1. 
Vinde todos pecadores
A Jesus, o Salvador;
Vinde logo, sem temores,
Encontrar o Redentor.
`,verseorder:1,ismainverse:0,id_hino:419},{idverse:26722,verse:`Sim eu sei; Oh! Eu sei,
Cristo salva o perdido pecador!
Sim eu sei; Oh! Eu sei,
Cristo salva o perdido pecador!
`,verseorder:2,ismainverse:1,id_hino:419},{idverse:26723,verse:`2. 
D\xE1 aos fracos, fortaleza;
Das montanhas planos faz,
Ao deserto d\xE1 beleza,
E aos crentes, luz e paz.
`,verseorder:3,ismainverse:0,id_hino:419},{idverse:26724,verse:`3. 
Nas fraquezas, ei-Lo perto,
Dominando a for\xE7a m\xE1;
No caminho, guia certo,
E Sua gra\xE7a sempre d\xE1.
`,verseorder:4,ismainverse:0,id_hino:419},{idverse:26725,verse:`4. 
Tenho gozo mui superno
Pelo sangue que verteu,
Nem o mundo, nem inferno,
Tiram a vida que me deu.
`,verseorder:5,ismainverse:0,id_hino:419},{idverse:26726,verse:`5. 
Quando Cristo vier na gl\xF3ria
A buscar o povo Seu,
Cantaremos a hist\xF3ria,
Do amor de Deus, no c\xE9u.`,verseorder:6,ismainverse:0,id_hino:419},{idverse:26727,verse:`1. 
O que buscas ansioso
\xD3 perdido pecador?
Tudo que tens por precioso,
Murchar\xE1 qual linda flor!
Quando teu fim chegar,
Quando teu fim chegar,
H\xE1 de cessar o teu gozo,
Mas vai o crente entrar,
Mas vai o crente entrar,
Num lar eterno de repouso!
`,verseorder:1,ismainverse:0,id_hino:420},{idverse:26728,verse:`2. 
Neste mundo h\xE1 belas flores,
Que nos querem seduzir,
Mas espinhos t\xCAm, que dores,
H\xE3o de sempre produzir;
Mas Cristo, o Salvador,
Mas Cristo, o Salvador,
De Saron \xE9 linda rosa,
Tem excelente odor,
Tem excelente odor,
E alegra a alma desditosa!
`,verseorder:2,ismainverse:0,id_hino:420},{idverse:26729,verse:`3. 
Larga o mundo e o pecado
Que te trazem perdi\xE7\xE3o;
E aceita de bom grado
A Jesus no cora\xE7\xE3o.
Vida eternal ter\xE1s;
Vida eternal ter\xE1s;
Sim, fruir\xE1s santo gozo;
Cristo ser\xE1 tua paz,
Cristo ser\xE1 tua paz,
E os altos c\xE9us o teu repouso.`,verseorder:3,ismainverse:0,id_hino:420},{idverse:26730,verse:`1. 
Quando de Ti care\xE7o
Jesus, meu Salvador!
O Teu amor, \xD3 vem m\`alegrar,
Quando de Ti care\xE7o.`,verseorder:1,ismainverse:0,id_hino:421},{idverse:26731,verse:`1. 
No c\xE9u n\xE3o entra pecado
Fadiga, tristeza, nem dor;
N\xE3o h\xE1 cora\xE7\xE3o quebrantado,
Pois todos s\xE3o cheios de amor,
As nuvens da vida terrestre
N\xE3o podem a gl\xF3ria ofuscar
Do reino de gozo celeste,
Que Deus quis p\`ra mim preparar!
`,verseorder:1,ismainverse:0,id_hino:422},{idverse:26732,verse:`Irei eu p\`ra linda cidade,
Jesus me dar\xE1 um lugar,
Co\`os crentes de todas idades,
A Deus hei de sempre louvar.
Do c\xE9u tenho muitas saudades,
Das gl\xF3rias que l\xE1 hei de ver;
Oh! Que gozo vou ter,
Quando eu vir meu Senhor,
Rodeado de grande esplendor!
`,verseorder:2,ismainverse:1,id_hino:422},{idverse:26733,verse:`2. 
Pagar n\xE3o \xE9 necess\xE1rio
A casa, que l\xE1 hei de ter;
E meu eternal vestu\xE1rio,
No c\xE9u, nunca vai se romper.
Jamais viverei em pobreza,
Aflito no meu santo lar,
Ali h\xE1 bastante riqueza,
Da qual poderei desfrutar.
`,verseorder:3,ismainverse:0,id_hino:422},{idverse:26734,verse:`3. 
No c\xE9u o luto \xE9 banido,
Enterros n\xE3o h\xE3o de passar;
Sepulcros jamais s\xE3o erguidos,
L\xE1 mortos n\xE3o vou encontrar.
Os velhos ser\xE3o transformados;
Mudados n\xF3s vamos ficar,
Quais astros por Deus espalhados
No c\xE9u, para sempre brilhar.`,verseorder:4,ismainverse:0,id_hino:422},{idverse:26735,verse:`1. 
Jesus, preciso mais de amor,
Reino de Deus, em meu cora\xE7\xE3o;
Da compaix\xE3o Tu \xE9s o Senhor;
De Ti preciso mais.
De Ti preciso mais,
Do Teu amor veraz;
Sei que est\xE1s pronto p\`ra me valer,
De Ti preciso mais!
`,verseorder:1,ismainverse:0,id_hino:423},{idverse:26736,verse:`2. 
Jesus, preciso mais de poder,
Dom do eterno Consolador;
\xC9s o dador que enche meu ser;
De Ti preciso mais!
De Ti preciso mais,
Do Teu poder veraz,
Da Tua gra\xE7a, \xF3 vem me encher,
De Ti preciso mais!
`,verseorder:2,ismainverse:0,id_hino:423},{idverse:26737,verse:`3. 
Jesus, preciso mais Te imitar,
Com humildade e submiss\xE3o;
Tua instru\xE7\xE3o eu quero guardar,
De Ti preciso mais,
De Ti preciso mais,
Da gra\xE7a eficaz,
Sei que Tu queres m\`a conceder,
De Ti preciso mais!
`,verseorder:3,ismainverse:0,id_hino:423},{idverse:26738,verse:`4. 
Jesus, preciso mais de ouvir
No meu sil\xEAncio a voz de amor,
Que d\xE1 vigor para eu Te servir,
De Ti preciso mais.
De Ti preciso mais.
A Tua voz veraz
No meu sil\xEAncio sempre atender,
De Ti preciso mais!
`,verseorder:4,ismainverse:0,id_hino:423},{idverse:26739,verse:`5. 
Jesus, preciso mais comunh\xE3o,
Contigo estar em santo Tabor;
\xC9 esplendor, transfigura\xE7\xE3o;
De Ti preciso mais.
De Ti preciso mais,
Transforma\xE7\xE3o veraz;
A Tua face eu quero ter,
De Ti preciso mais!`,verseorder:5,ismainverse:0,id_hino:423},{idverse:26740,verse:`1. 
Jesus me salvou com Seu precioso sangue;
Jesus me comprou com Seu precioso sangue,
Com Seu poder j\xE1 me curou;
O meu pecado j\xE1 cravou na cruz!
Jesus p\`ra sempre me salvou!`,verseorder:1,ismainverse:0,id_hino:424},{idverse:26741,verse:`1. 
Perdido neste mundo andei,
Imerso em sua escurid\xE3o;
Em v\xE3s promessas confiei;
N\xE3o tinha Deus, nem salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:425},{idverse:26742,verse:`Perdido andei, mas me salvei,
Sendo guiado pela luz,
Que no Calv\xE1rio avistei
E me levou ao Rei Jesus!
`,verseorder:2,ismainverse:1,id_hino:425},{idverse:26743,verse:`2. 
Sem for\xE7as \`stava p\`ra viver,
Sem nada mais eu esperar;
Quando uma luz eu pude ver,
Que veio me iluminar.
`,verseorder:3,ismainverse:0,id_hino:425},{idverse:26744,verse:`3. 
Fitei a luz e logo cri,
E muito alegre eu fiquei;
Prazer do c\xE9u tamb\xE9m senti;
Quando essa luz eu abracei.
`,verseorder:4,ismainverse:0,id_hino:425},{idverse:26745,verse:`4. 
Confesso com satisfa\xE7\xE3o,
Que nessa luz me sinto bem;
A paz entrou no cora\xE7\xE3o,
Porque vi um abrigo al\xE9m.
`,verseorder:5,ismainverse:0,id_hino:425},{idverse:26746,verse:`5. 
Nos bra\xE7os do Senhor Jesus,
Prazer eu tenho em viver,
Unido \xE1 salvadora luz,
Que Cristo veio me trazer.`,verseorder:6,ismainverse:0,id_hino:425},{idverse:26747,verse:`1. 
Quando aqui nos reunimos,
\xD3 irm\xE3os, em santo amor,
Certamente n\xF3s fru\xEDmos,
B\xEAn\xE7\xE3os do Consolador!
Sempre pode Sua presen\xE7a,
De Jesus testificar;
Com palavras e senten\xE7as
A quem quer o escutar.
`,verseorder:1,ismainverse:0,id_hino:426},{idverse:26748,verse:`2. 
Quando n\xF3s nos humilhamos
Ante o bom Consolador,
O poder que n\xF3s buscamos,
Nos concede com amor;
E se verdes glorioso,
Em vis\xE3o Jesus passar,
Eis que busca ansioso,
Uma alma batizar.
`,verseorder:2,ismainverse:0,id_hino:426},{idverse:26749,verse:`3. 
Quando todos nos achamos
Prontos para escutar,
Pela un\xE7\xE3o, ouvir n\xF3s vamos
O bom Deus, a n\xF3s falar;
E no c\xE9u, sim, ao chegarmos,
Oh! Que festa haver\xE1,
Quando j\xE1 nos encontrarmos
No lugar que Deus dar\xE1!
`,verseorder:3,ismainverse:0,id_hino:426},{idverse:26750,verse:`4. 
\xD3 remidos, sempre firmes,
Esperando com ardor,
Que em n\xF3s Deus mais confirme
Seu poder Consolador;
Eia! Avante, a caminho,
Pela gloriosa luz,
Pois de n\xF3s vai mui vizinho,
O amado e bom Jesus!`,verseorder:4,ismainverse:0,id_hino:426},{idverse:26751,verse:`1. 
De pesar est\xE1s cercado,
\xC9s provado pela dor?
De sofrer est\xE1s cansado,
E tamb\xE9m do teu labor?
`,verseorder:1,ismainverse:0,id_hino:427},{idverse:26752,verse:`Vai orando, vai orando,
Ao soprar do furac\xE3o.
O Senhor est\xE1 velando,
Dar-te-\xE1 consola\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:427},{idverse:26753,verse:`2. 
Nas doen\xE7as, incertezas,
Que tu\`alma passar\xE1,
S\xF3 Jesus dar\xE1 firmeza,
E tamb\xE9m te curar\xE1.
`,verseorder:3,ismainverse:0,id_hino:427},{idverse:26754,verse:`3. 
Ao Senhor a voz eleva,
Ora a Deus com mui fervor,
Pois as faltas de releva
E mitiga a tua dor.
`,verseorder:4,ismainverse:0,id_hino:427},{idverse:26755,verse:`4. 
Tarda Deus em consolar-te?
Vai orando com poder;
Presto, ajuda pode dar-te,
Teu pedido responder.`,verseorder:5,ismainverse:0,id_hino:427},{idverse:26756,verse:`1. 
Mostra-Te, estrela da manh\xE3,
\xD3 bondoso Salvador!
Sem Tua luz, a vida \xE9 v\xE3,
Sem Tua luz n\xE3o temos vigor!
Sem Tua luz n\xE3o temos vigor!
`,verseorder:1,ismainverse:0,id_hino:428},{idverse:26757,verse:`\xD3 estrela da alva, estrela que salva
A perdida alma que vaga sem ter dire\xE7\xE3o!
`,verseorder:2,ismainverse:1,id_hino:428},{idverse:26758,verse:`2. 
Cristo Jesus \xE9, do fiel,
A estrela de fulgor,
Tamb\xE9m Emanuel,
Que de manh\xE3 nos enche de amor,
Que de manh\xE3 nos enche de amor.
`,verseorder:3,ismainverse:0,id_hino:428},{idverse:26759,verse:`3. 
Bendita estrela dos hebreus,
Que o mundo iluminou,
E nos mandou os raios Seus,
Raios d\`amor, que j\xE1 nos salvou,
Raios d\`amor, que j\xE1 nos salvou.`,verseorder:4,ismainverse:0,id_hino:428},{idverse:26760,verse:`1. 
\xD3 pecador desejoso
De verdadeiro perd\xE3o,
A voz de Cristo, ditoso,
Escuta com aten\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:429},{idverse:26761,verse:`Escuta o Evangelho
Do Filho de Deus,
O divinal conselho,
Que vem dos mais altos c\xE9us!
`,verseorder:2,ismainverse:1,id_hino:429},{idverse:26762,verse:`2. 
Hoje, com f\xE9 e de gra\xE7a
Vem aceitar o perd\xE3o,
De Cristo, pois te abra\xE7a,
Dando-te a salva\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:429},{idverse:26763,verse:`3. 
Cristo Jesus tem vertido
Seu sangue com grande dor,
Para salvar-te, perdido,
E te encher de amor!
`,verseorder:4,ismainverse:0,id_hino:429},{idverse:26764,verse:`4. 
Se tu contrito aceitas
De Cristo, o Seu perd\xE3o,
Ter\xE1s ent\xE3o paz perfeita
E gozo da salva\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:429},{idverse:26765,verse:`1. 
Ouves, como o Evangelho
Nos d\xE1 vera salva\xE7\xE3o,
E transforma o homem velho
Numa nova cria\xE7\xE3o?
Bem algum em mim n\xE3o via,
Mas somente corrup\xE7\xE3o,
E cansado da porfia
Em Jesus achei perd\xE3o.
`,verseorder:1,ismainverse:0,id_hino:430},{idverse:26766,verse:`2. 
Como a pomba que cansada
Foi na arca repousar,
A minh\`alma fatigada
Em Jesus vai descansar;
Mas o corvo foi s\`embora,
Sobre os mortos foi pousar,
Isto fazes tu agora?
Querer\xE1s ao mal voltar?
`,verseorder:2,ismainverse:0,id_hino:430},{idverse:26767,verse:`3. 
\xC9 Jesus a minha arca
Onde posso repousar,
E dali, do mal as marcas,
Nem eu posso avistar!
Oh! Qu\xE3o doce a chamada
Que a mim me fez Jesus!
"Vem , \xF3 alma t\xE3o cansada,
Vem das trevas para luz".
`,verseorder:3,ismainverse:0,id_hino:430},{idverse:26768,verse:`4. 
Pecador que est\xE1s ouvindo
A mensagem do Senhor,
Tu na arca \xE9s bem-vindo,
No ref\xFAgio de amor,
Pois as \xE0guas do pecado
Breve te alcan\xE7ar\xE3o,
Pela morte dspertado,
Bater\xE1s na porta em v\xE3o!`,verseorder:4,ismainverse:0,id_hino:430},{idverse:26769,verse:`1. 
Cristo chama, chama, chama com muito amor,
"Hoje vem a Mim, pecador!"
A Cristo d\xE1 teu cora\xE7\xE3o,
Manchado j\xE1 por mal atroz;
Vem, hoje, vem! Bela ocasi\xE3o!
N\xE3o queres vir a esta voz?
`,verseorder:1,ismainverse:0,id_hino:431},{idverse:26770,verse:`Aceitar\xE1s ao Salvador?
\xC9 teu Senhor, e benfeitor;
Te chama j\xE1, vem sem tardar,
Aceitar\xE1s o Salvador?
Aceitar\xE1s o Salvador?
`,verseorder:2,ismainverse:1,id_hino:431},{idverse:26771,verse:`2. 
Cristo chama, chama, chama com muito amor;
"Hoje vem a Mim pecador!"
A T\`esperar est\xE1 Jesus,
Pois quer te dar amor sem par;
N\xE3o tardes mais, vem para luz,
N\xE3o ouves tu Jesus chamar?
`,verseorder:3,ismainverse:0,id_hino:431},{idverse:26772,verse:`3. 
Cristo chama, chama, chama com muito amor;
"Hoje vem a Mim pecador!"
Oh! Amanh\xE3? N\xE3o viver\xE1s...
E por lavar... teu cora\xE7\xE3o?
Tempo feliz tu perder\xE1s
E n\xE3o ter\xE1s mais salva\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:431},{idverse:26773,verse:`1. 
Minha vida seja sim,
Consagrada a Ti, Senhor;
Possas sempre Tu por mim,
Operar o Teu amor.
`,verseorder:1,ismainverse:0,id_hino:432},{idverse:26774,verse:`A minh\`alma lava Salvador
No Teu sangue puro, carmezim;
Minha vida toma para ser, Senhor,
Tua para sempre, sim.
`,verseorder:2,ismainverse:1,id_hino:432},{idverse:26775,verse:`2. 
Que meus p\xE9s somente v\xE3o
Onde os santos possam ir;
Numa eternal can\xE7\xE3o,
Minha voz se fa\xE7a ouvir.
`,verseorder:3,ismainverse:0,id_hino:432},{idverse:26776,verse:`3. 
Seja o meu tempo j\xE1,
Consagrado em Teu labor;
Que meus l\xE1bios, Jeov\xE1,
Falem s\xF3 do Teu  amor.
`,verseorder:4,ismainverse:0,id_hino:432},{idverse:26777,verse:`4. 
Toma, \xF3 Deus, o meu querer,
Faze-o Teu, \xF3 Salvador,
Hoje habita no meu ser;
Enche-me do Teu fervor.
`,verseorder:5,ismainverse:0,id_hino:432},{idverse:26778,verse:`5. 
\xD3 recebe-me, Senhor,
Quando venho me prostar
A Teus p\xE9s, \xF3 Salvador,
Hoje vem me consagrar.`,verseorder:6,ismainverse:0,id_hino:432},{idverse:26779,verse:`1. 
Nossas vozes jubilosas
Elevamos com fervor,
Pela vinda amistosa
Dos obreiros do Senhor.
`,verseorder:1,ismainverse:0,id_hino:433},{idverse:26780,verse:`Sois bem-vindos,
Sois bem-vindos,
Campe\xF5es de Jeov\xE1!
Parab\xE9ns, mas n\xE3o fingidos,
A congrega\xE7\xE3o vos d\xE1.
`,verseorder:2,ismainverse:1,id_hino:433},{idverse:26781,verse:`2. 
Recebei os lutadores
Da verdade, do amor,
Demos a Jesus louvores,
Que os trouxe com vigor.
`,verseorder:3,ismainverse:0,id_hino:433},{idverse:26782,verse:`3. 
Abra\xE7ai os bons soldados
Das fileiras de Jesus,
Aos que lutam denodados
Para difundir a luz.
`,verseorder:4,ismainverse:0,id_hino:433},{idverse:26783,verse:`4. 
Dos fi\xE9is \xE9 bem superno
Trabalhar sem dila\xE7\xE3o;
P\`ra fazer que o Rei eterno
Reina em cada cora\xE7\xE3o!`,verseorder:5,ismainverse:0,id_hino:433},{idverse:26784,verse:`1. 
A Teus p\xE9s, \xF3 Jesus Cristo,
Tua face buscarei,
Escutando qual Maria,
As palavras de amor;
A Teus p\xE9s, \xF3 Jesus Cristo,
Meu passado esquecerei,
Pois Tua m\xE3o fiel e terna,
Tem me salvo do temor.
`,verseorder:1,ismainverse:0,id_hino:434},{idverse:26785,verse:`2. 
A Teus p\xE9s, \xF3 Jesus Cristo,
Acho terna compaix\xE3o,
Para todos meus pesares
Meus conflitos, minha dor;
Livra-me, \xF3 Jesus Cristo,
De cuidados, de afli\xE7\xE3o,
E concede-me constante
Teu poder consolador!
`,verseorder:2,ismainverse:0,id_hino:434},{idverse:26786,verse:`3. 
A Teus p\xE9s, \xF3 Jesus Cristo,
Eu desfruto Teu amor,
Em Teus olhos h\xE1 do\xE7ura
Em Teu seio prote\xE7\xE3o;
D\xE1-me gra\xE7a e fervor,
E que o mundo possa sempre
Ver em mim a salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:434},{idverse:26787,verse:`4. 
A Teus p\xE9s, \xF3 Jesus Cristo,
Tenho um gozo divinal!
A Teus p\xE9s encontro abrigo,
Oh! Bondoso Salvador!
S\xF3 em Ti, \xF3 Jesus Cristo,
H\xE1 consolo sem igual,
Para minh\`alma abatida,
Neste mundo de horror.`,verseorder:4,ismainverse:0,id_hino:434},{idverse:26788,verse:`1. 
O Salvador, por Seu amor,
Ao mundo vil por n\xF3s baixou,
E dum abismo de horror,
Minh\`alma levantou!
`,verseorder:1,ismainverse:0,id_hino:435},{idverse:26789,verse:`Que  salva\xE7\xE3o me deu Jesus,
A qual na cruz p\`ra mim ganhou,
Estando em sombra, \xE0 plena luz,
Com seu poder  me levantou!
`,verseorder:2,ismainverse:1,id_hino:435},{idverse:26790,verse:`2. 
A Sua voz eu resisti!
Enquanto amante me chamou,
Mas Sua gra\xE7a recebi,
A qual me levantou!
`,verseorder:3,ismainverse:0,id_hino:435},{idverse:26791,verse:`3. 
Oh! Quanta dor sofreu por mim,
Na cruz pregado espirou.
Mas me salvou Jesus assim,
Ali me levantou!
`,verseorder:4,ismainverse:0,id_hino:435},{idverse:26792,verse:`4. 
Que dom feliz em mim se v\xEA,
Pois Sua gra\xE7a me salvou,
Mas n\xE3o compreendo eu, por qu\xEA
Jesus me levantou!`,verseorder:5,ismainverse:0,id_hino:435},{idverse:26793,verse:`1. 
Crentes, cantai e entoai louvores
Ao Senhor, ao nosso Deus,
Deus de bondade infinita, imensa,
Criador da terra e c\xE9us;
Que, com poder t\xE3o vasto e eficaz,
Deu-nos salva\xE7\xE3o e plena paz.
Gl\xF3ria e louvor ao Deus de amor;
Com fervor, sinceramente,
Gl\xF3ria ao Pai onipotente!
`,verseorder:1,ismainverse:0,id_hino:436},{idverse:26794,verse:`Ao nosso Criador,
Honra e gl\xF3ria tributemos,
Ao nosso Salvador,
Mil louvores entoemos;
E ao bom Consolador,
Honra e louvor demos tamb\xE9m;
Honra e Trindade para sempre, am\xE9m.
`,verseorder:2,ismainverse:1,id_hino:436},{idverse:26795,verse:`2. 
Crentes, cantai e entoai louvores
A Jesus, o Salvador,
Que nos amou e nos salvou p\`ra sempre,
Do poder do tentador,
Gl\xF3ria a Jesus, autor da salva\xE7\xE3o,
Toda honra, gl\xF3ria e adora\xE7\xE3o,
Gl\xF3ria e louvor ao Salvador,
Com vigor, ardentemente,
Gl\xF3ria ao Filho onisciente!
`,verseorder:3,ismainverse:0,id_hino:436},{idverse:26796,verse:`3. 
Crentes, cantai e entoai louvores
Ao divino Preceptor,
Que nos mandou Cristo, j\xE1 exaltado,
Pela dextra do Senhor;
Hoje louvai ao bom Consolador,
Que nos enche de fervente amor!
Gl\xF3ria e louvor ao instruidor,
Com ardor, e um lindo canto
Dai ao Esp\xEDrito Santo!`,verseorder:4,ismainverse:0,id_hino:436},{idverse:26797,verse:`1. 
Assim que Deus me batizou,
A minha alma viu mais luz,
Pois dom celeste o Pai mandou,
P\`ra dar louvor ao meu Rei Jesus,
Sou testemunha do meu Senhor,
E sempre dEle vou falar;
Tamb\xE9m do selo de amor,
Que o meu c\xE1lice faz transbordar.
`,verseorder:1,ismainverse:0,id_hino:437},{idverse:26798,verse:`Louvado seja Jesus, o Cristo,
Que continua a batizar,
Com l\xEDngua estranha, n\xF3s temos visto,
O dom celeste, o Pai mandar!
`,verseorder:2,ismainverse:1,id_hino:437},{idverse:26799,verse:`2. 
O bom caminho vou trilhar,
Se eu quiser obedecer
Ao Evangelho, \xE0 luz sem par,
Aonde vida vou receber,
De Jesus Cristo eu falarei,
Que \xE9 dos homens Salvador;
\xD3 vinde hoje e recebei,
Divina gra\xE7a do meu Senhor.`,verseorder:3,ismainverse:0,id_hino:437},{idverse:26800,verse:`1. 
Assim a n\xF3s falou Jesus:
"Voltai-vos para Mim,
A salva\xE7\xE3o. a paz, a luz,
Encontrareis enfim".
\xD3 esperai o bom Jesus,
A estrela da manh\xE3,
Pois Ele ao c\xE9u a v\xF3s conduz;
Deixai a vida v\xE3!
`,verseorder:1,ismainverse:0,id_hino:438},{idverse:26801,verse:`2. 
Tamb\xE9m Jesus vos disse assim:
"D\xE1 vida, \xE1gua sou,
E quem tem sede venha a Mim,
De gra\xE7a o dom lhe dou".
E do seu ventre sair\xE3o
Em gl\xF3ria ao Salvador,
Torrentes vivas, que ser\xE3o:
A paz, a f\xE9, o amor!
`,verseorder:2,ismainverse:0,id_hino:438},{idverse:26802,verse:`3. 
Ainda assim falou Jesus
A todo o pecador:
"\xD3 vinde a Mim, Eu sou aluz,
Que brilha com fulgor";
E tu, que jazes, \xF3 mortal,
Nas trevas de horror,
\xD3 vem p\`ra luz celestial,
A Cristo, o Salvador!
`,verseorder:3,ismainverse:0,id_hino:438},{idverse:26803,verse:`4. 
Os que remidos dEle s\xE3o,
Seus passos v\xE3o seguir;
A Sua justi\xE7a, e Seu perd\xE3o,
De gra\xE7a v\xE3o fruir,
Jesus \xE9 tudo para mim;
O Rei, e meu Senhor;
Olhando, eu tenho sim,
Do c\xE9u real vigor!`,verseorder:4,ismainverse:0,id_hino:438},{idverse:26804,verse:`1. 
De Cristo ressurgido,
Aos seus p\xE9s nos prostramos;
Com f\xE9 O adoramos,
Do mundo \xE9 o Salvador;
N\xF3s, crentes redimidos,
Por sangue j\xE1 lavados,
Na gra\xE7a bem guardados,
Temos de Deus favor!
`,verseorder:1,ismainverse:0,id_hino:439},{idverse:26805,verse:`2. 
Tudo que n\xF3s sofremos,
Cristo j\xE1 tem provado,
Ao pecador cansado,
Pode, quer ajudar;
Oh! Quanta dor n\xF3s vemos,
Cristo na cruz sofrendo,
Com sede ali morrendo,
Para o mortal salvar!
`,verseorder:2,ismainverse:0,id_hino:439},{idverse:26806,verse:`3. 
"Eu tenho sede", exclama
O divinal Cordeiro,
Pregado no madeiro,
Na cruz da maldi\xE7\xE3o;
Oh! Quanto Cristo ama
O  pecador perdido,
Pois sangue tem vertido,
Para lhe dar perd\xE3o!
`,verseorder:3,ismainverse:0,id_hino:439},{idverse:26807,verse:`4. 
A n\xF3s, que santos somos,
D\xE1-nos a vera gra\xE7a,
Para que assim se fa\xE7a,
Em n\xF3s o Teu querer,
Na cruz achar n\xF3s fomos
Consolo abundante,
Gra\xE7a de Deus bastante,
E divinal poder!`,verseorder:4,ismainverse:0,id_hino:439},{idverse:26808,verse:`1. 
Em viver p\`ra Cristo firme permanecer\xE1s
E a paz ap\xF3s, tu sempre seguir\xE1s?
E ao p\xE9 da cruz os teus cuidados deixar\xE1s?
Faze j\xE1 o Seu querer!
`,verseorder:1,ismainverse:0,id_hino:440},{idverse:26809,verse:`O Seu poder e Seus dons te dar\xE1;
De gozo, Cristo, sim, te inundar\xE1;
Do cora\xE7\xE3o as manchas limpar\xE1
Fazendo tu, p\`ra sempre, Seu querer!
`,verseorder:2,ismainverse:1,id_hino:440},{idverse:26810,verse:`2. 
Queres ir ap\xF3s de Cristo e carregar a cruz?
Queres conhecer a paz que d\xE1 Jesus?
Queres que te guie com a verdadeira luz?
Faze j\xE1 o Seu querer!
`,verseorder:3,ismainverse:0,id_hino:440},{idverse:26811,verse:`3. 
Queres l\xE1 no c\xE9u os teus amigos encontrar?
Deves preparar tua vida sem tardar,
Queres p\`ra Jesus a tua vida consagrar?
Faze j\xE1 o Seu querer!`,verseorder:4,ismainverse:0,id_hino:440},{idverse:26812,verse:`1. 
Un\xE2nimes ao p\xE9 da cruz,
Pedimos com fervor,
Nos manda hoje, \xF3 Jesus,
O bom Consolador!
`,verseorder:1,ismainverse:0,id_hino:441},{idverse:26813,verse:`Sim, manda outra chuva,
\xD3 bom salvador!
E com Tua chama
Destr\xF3i de n\xF3s o mal;
\xD3 d\xE1-nos a chuva,
Do Consolador;
Ao povo Teu inflama
Com poder celestial!
`,verseorder:2,ismainverse:1,id_hino:441},{idverse:26814,verse:`2. 
Qual vivo fogo ou furac\xE3o
\xD3 f\xE1-Lo a n\xF3s descer;
E possa cada cora\xE7\xE3o
Um templo dEle ser.
`,verseorder:3,ismainverse:0,id_hino:441},{idverse:26815,verse:`3. 
Mediante a f\xE9 e a ora\xE7\xE3o,
O c\xE9u Tu abrir\xE1s,
O \xF3leo santo da un\xE7\xE3o,
Em n\xF3s derramar\xE1s!
`,verseorder:4,ismainverse:0,id_hino:441},{idverse:26816,verse:`4. 
Pedimos n\xF3s, agora a Ti,
\xD3 queima todo mal
E vivifica-nos aqui,
Com fogo celestial!`,verseorder:5,ismainverse:0,id_hino:441},{idverse:26817,verse:`1. 
Breve no c\xE9u, Jesus h\xE1 de aparecer
Em gloriosa luz; todos O h\xE3o de ver
Naquele dia, ent\xE3o, eu hei de receber
De Cristo galard\xE3o; oh! que prazer!
`,verseorder:1,ismainverse:0,id_hino:442},{idverse:26818,verse:`Breve verei o bom Jesus,
E viverei em plena luz;
No lindo c\xE9u eu gozarei...
De toda a dor, por Deus, livre serei.
`,verseorder:2,ismainverse:1,id_hino:442},{idverse:26819,verse:`2. 
Na vinda do Senhor irei eu receber,
Do Seu eterno amor, repouso e prazer,
- Disso, meu bom Jesus, tem-me falado j\xE1
E da celeste luz de Jeov\xE1!
`,verseorder:3,ismainverse:0,id_hino:442},{idverse:26820,verse:`3. 
Na vinda do Senhor desfrutarei prazer,
Quando meu Salvador em gl\xF3ria aparecer;
Eis que Ele breve vem, os santos levar\xE1
Para a mans\xE3o de al\xE9m, donde vir\xE1!`,verseorder:4,ismainverse:0,id_hino:442},{idverse:26821,verse:`1. 
Pecador, um convite te queremos dar.
Um convite divino o consolador;
Hoje queres \xE0 casa paternal voltar,
Onde Cristo te espera cheio de amor.
`,verseorder:1,ismainverse:0,id_hino:443},{idverse:26822,verse:`Larga o mundo j\xE1, vem ap\xF3s
Jesus;
O qual pode agora mesmo te salvar!
Tudo pronto est\xE1, que divina luz!
Cristo hoje te convida para vir cear!
`,verseorder:2,ismainverse:1,id_hino:443},{idverse:26823,verse:`2. 
Quando Cristo sofreu pregado l\xE1 na cruz;
Preparou p\`ra teu ser um divinal manjar;
E agora te chama para Sua luz;
Pois tua alma faminta Ele quer fartar!
`,verseorder:3,ismainverse:0,id_hino:443},{idverse:26824,verse:`3. 
\xD3 aceita o convite que Jesus te faz,
Que franqueia a entrada na mans\xE3o de luz;
E o gozo divino que te satisfaz,
\xD3 aceita o convite do Senhor Jesus!
`,verseorder:4,ismainverse:0,id_hino:443},{idverse:26825,verse:`4. 
Ai do que o convite santo recusar,
E tiver por profano o sangue de Jesus;
O banquete celeste n\xE3o vai desfrutar,
Quem despreza o convite feito l\xE1 na cruz!`,verseorder:5,ismainverse:0,id_hino:443},{idverse:26826,verse:`1. 
Escuta, pobre pecador,
Em Cristo h\xE1 perd\xE3o;
Aceita agora o Salvador,
Pois nEle h\xE1 salva\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:444},{idverse:26827,verse:`Vem a Cristo sem tardan\xE7a,
Receber perd\xE3o,
E a vida d\`esperan\xE7a
Fruir\xE1s, ent\xE3o!
`,verseorder:2,ismainverse:1,id_hino:444},{idverse:26828,verse:`2. 
P\`ra redimir-te, o Redentor
Seu sangue derramou!
Na cruz; sofrendo grande dor,
Jesus te resgatou!
`,verseorder:3,ismainverse:0,id_hino:444},{idverse:26829,verse:`3. 
Caminho certo \xE9 Jesus;
Achamos nEle paz!
Caminho santo e de luz,
Que sempre satisfaz!
`,verseorder:4,ismainverse:0,id_hino:444},{idverse:26830,verse:`4. 
Vem hoje, vem te entregar
Deixando todo mal,
Deus quer agora te salvar,
\xD3 vem p\`ra luz real!`,verseorder:5,ismainverse:0,id_hino:444},{idverse:26831,verse:`1. 
Com ouro e prata n\xE3o fui resgatado,
Tesouro terrestre, jamais me livrou;
O pre\xE7o foi o sangue de Cristo, manado
Na cruz do Calv\xE1rio onde a vida raiou!
`,verseorder:1,ismainverse:0,id_hino:445},{idverse:26832,verse:`Por prata e ouro n\xE3o fui
remido,
Nem por tesouro de mui  valor,
Mas pelo sangue que foi vertido
Por Jesus, meu Salvador!
`,verseorder:2,ismainverse:1,id_hino:445},{idverse:26833,verse:`2. 
O ouro e a prata valor, oh! n\xE3o tinham
P\`ra satisfazer a justi\xE7a de Deus,
Mas Cristo pagou toda a d\xEDvida minha;
Encheu-me de amor e de paz l\xE1 no c\xE9u.
`,verseorder:3,ismainverse:0,id_hino:445},{idverse:26834,verse:`3. 
Por ouro e por prata n\xE3o pude chegar-me
Diante de Deus e o perd\xE3o obter;
P\`lo sangue de Cristo, eu pude lan\xE7ar-me
Na gra\xE7a divina e o favor receber.
`,verseorder:4,ismainverse:0,id_hino:445},{idverse:26835,verse:`4. 
Por ouro e por prata n\xE3o fui redimido;
A entrada do c\xE9u n\xE3o se pode comprar;
Mas, s\xF3 pelo sangue de Cristo vertido
Na cruz, poderemos a gl\xF3ria alcan\xE7ar.`,verseorder:5,ismainverse:0,id_hino:445},{idverse:26836,verse:`1. 
Vem, \xF3 alma cansada, que sentes dor,
A teu bom Salvador, a Jesus,
E confia no sangue do Redentor,
Derramado, que foi l\xE1 na cruz.
`,verseorder:1,ismainverse:0,id_hino:446},{idverse:26837,verse:`\xD3 vem j\xE1! \xD3 vem j\xE1! A Jesus vem
como est\xE1s;
N\xE3o demores pecador aceitar o teu Salvador.
\xD3 vem j\xE1! \xD3 vem j\xE1! A Jesus vem como est\xE1s,
N\xE3o demores pecador aceitar o teu Salvador.
`,verseorder:2,ismainverse:1,id_hino:446},{idverse:26838,verse:`2. 
Tu que vagas mui longe do Salvador,
No pecado, no mal preso est\xE1s;
S\xF3 Jesus \xE9 que vai te livrar da dor,
Salva\xE7\xE3o hoje mesmo te dar.
`,verseorder:3,ismainverse:0,id_hino:446},{idverse:26839,verse:`3. 
Cristo chama ainda: "\xD3 vinde a Mim,
Oprimidos, cansados, sem paz;
Vida eterna darei para v\xF3s, enfim,
No meu reino de gozo veraz".`,verseorder:4,ismainverse:0,id_hino:446},{idverse:26840,verse:`1. 
Nasceste de novo? Andas j\xE1 com Deus?
Pertences ao povo, que vai para os c\xE9us?
Tens a lei escrita no teu cora\xE7\xE3o?
Em ti j\xE1 habita plena salva\xE7\xE3o?
`,verseorder:1,ismainverse:0,id_hino:447},{idverse:26841,verse:`Se o caminho \xE9 estreito, a porta
\xE9 tamb\xE9m,
Tudo est\xE1 feito, n\xE3o demores, vem!
No portal da vida Cristo achar\xE1s,
Ao findar a lida l\xE1 no c\xE9u tu estar\xE1s.
`,verseorder:2,ismainverse:1,id_hino:447},{idverse:26842,verse:`2. 
J\xE1 desceste \xE0s \xE1guas, passaste o Jord\xE3o?
Tens ainda m\xE1goas no teu cora\xE7\xE3o?
Foste batizado como foi Jesus?
Segues a Seu lado para o c\xE9u de luz?
`,verseorder:3,ismainverse:0,id_hino:447},{idverse:26843,verse:`3. 
O poder vindouro recebeste j\xE1?
Tens o teu tesouro escondido l\xE1?
Vida de vit\xF3ria vives j\xE1 com Deus?
Marchas para gl\xF3ria? Andas para os c\xE9us?
`,verseorder:4,ismainverse:0,id_hino:447},{idverse:26844,verse:`4. 
Ao ouvir o brado: "Eis que Cristo vem",
Tens te preparado p\`ra dizer am\xE9m?
Alvo \xE9 teu vestido, clara \xE9 tua luz?
Canta co\`os remidos - Vem, Senhor Jesus.`,verseorder:5,ismainverse:0,id_hino:447},{idverse:26845,verse:`1. 
Crentes, avan\xE7ai,
Firmes pelejai
Contra o v\xEDcio, contra o mal!
Quem  quer triunfar,
Cristo agradar,
Vem \xE0 luta divinal,
Nosso guia \xE9 Jesus,
Ele sempre nos conduz;
A vit\xF3ria nos dar\xE1,
Cristo sempre vencer\xE1.
`,verseorder:1,ismainverse:0,id_hino:448},{idverse:26846,verse:`2. 
O celeste lar
Vamos alcan\xE7ar,
Estrelas j\xE1 resplandecem;
A festa come\xE7ara!
Descanso ali prometido,
Minh\`alma ent\xE3o gozar\xE1;
Ouve a mensagem, perdido;
Jesus em breve vir\xE1!`,verseorder:2,ismainverse:0,id_hino:448},{idverse:26847,verse:`1. 
\xC0 beira da estrada est\xE3o
Muitos que esperam a salva\xE7\xE3o;
Podem tais almas sem Deus perecer,
Ou est\xE1s pronto para as socorrer?
`,verseorder:1,ismainverse:0,id_hino:449},{idverse:26848,verse:`Quem hoje dir\xE1: "Eis-me aqui,
Senhor,
Eu quero aos perdidos falar do amor,
A quantos na senda da vida achar,
De Cristo eu irei falar
`,verseorder:2,ismainverse:1,id_hino:449},{idverse:26849,verse:`2. 
\xC0 beira da estrada est\xE3o
Muitos que buscam do c\xE9u o p\xE3o;
Para os famintos, oh! quem falar\xE1
Do Evangelho que lhes saciar\xE1?
`,verseorder:3,ismainverse:0,id_hino:449},{idverse:26850,verse:`3. 
Pelo caminho em que vais,
Existem enfermos espirituais;
Qual o bom samaritano, far\xE1s,
Ou qual escriba, al\xE9m passar\xE1s?
`,verseorder:4,ismainverse:0,id_hino:449},{idverse:26851,verse:`4. 
Quando a jornada terminar,
E o fim da estrada se aproximar,
Conhecer\xE1s muitos na multid\xE3o,
Que receberam, de Deus, salva\xE7\xE3o.`,verseorder:5,ismainverse:0,id_hino:449},{idverse:26852,verse:`1. 
O sol da justi\xE7a vamos proclamar
Sua luz resplandece em qualquer lugar,
Seu fulgor dourado de amor e paz,
Salva\xE7\xE3o completa aos ca\xEDdos traz.
`,verseorder:1,ismainverse:0,id_hino:450},{idverse:26853,verse:`Resplandece, \xF3 sol verdadeiro!
Teus celestes raios vamos espalhar;
Resplandece, \xF3 sol verdadeiro!
T\xE3o formosos raios vamos proclamar!
`,verseorder:2,ismainverse:1,id_hino:450},{idverse:26854,verse:`2. 
Resplendor celeste brilha desde os c\xE9us.
Fala-nos de Cristo, do amor de Deus;
Com Jesus n\xF3s vamos, todos, trabalhar,
Do c\xE9u aos perdidos o esplendor levar.
`,verseorder:3,ismainverse:0,id_hino:450},{idverse:26855,verse:`3. 
S\xF3 uns raios fortes deste resplendor,
Toda a terra podem inundar de amor;
Podem dum deserto fazer um jardim;
Resplendor do sol, \xF3 brilha para mim.`,verseorder:4,ismainverse:0,id_hino:450},{idverse:26856,verse:`1. 
Os testemunhos indicam
Que breve Cristo vir\xE1;
J\xE1 os sinais testificam;
Que Ele vem, n\xE3o tardar\xE1.
Oh! Dia de grande gl\xF3ria,
Para os remidos ser\xE1!
Dia ser\xE1 de vit\xF3ria;
Jesus em breve vir\xE1.
`,verseorder:1,ismainverse:0,id_hino:451},{idverse:26857,verse:`Meu Jesus vem, meu Jesus vem;
Vou encontr\xE1-Lo no c\xE9u!
Os anjos nas harpas tangem,
Dando louvores a Deus!
`,verseorder:2,ismainverse:1,id_hino:451},{idverse:26858,verse:`2. 
Tudo \xE9 trevas no mundo,
A noite chegada est\xE1,
Mas voz de amor mui profundo
Diz que Jesus voltar\xE1.
Meu corpo j\xE1 transformado,
Um c\xE9u aberto achar\xE1!
Com anjos mil a Seu lado,
Jesus em breve vir\xE1!
`,verseorder:3,ismainverse:0,id_hino:451},{idverse:26859,verse:`3. 
Tristezas desaparecem
E dores n\xE3o haver\xE1;
Estrelas j\xE1 resplandecem;
A festa come\xE7ar\xE1!
Descanso ali prometido,
Minh\`alma ent\xE3o gozar\xE1;
Ouve a mensagem, perdido;
Jesus em breve vir\xE1!`,verseorder:4,ismainverse:0,id_hino:451},{idverse:26860,verse:`1. 
Sempre! Sempre! Seguirei a Cristo!
Onde quer que Ele for, O seguirei,
Sempre! Sempre! Seguirei a Cristo!
Onde quer que Ele for, O seguirei!`,verseorder:1,ismainverse:0,id_hino:452},{idverse:26861,verse:`1. 
Oh! Como \xE9 bom dos tempos primitivos lembrar
E dos avivamentos de que ouvimos falar;
Deus quer nos dar mais fervor espiritual,
Ao Seu povo mandar o poder pentecostal!
`,verseorder:1,ismainverse:0,id_hino:453},{idverse:26862,verse:`Nosso Deus \xE9 o mesmo
hoje como ent\xE3o,
Ele cura, batiza, e nos d\xE1 salva\xE7\xE3o.
Abundante \xE9 aqui a vida espiritual
Para os que recebem poder penteconstal!
`,verseorder:2,ismainverse:1,id_hino:453},{idverse:26863,verse:`2. 
Oh! Como \xE9 bom dos crentes primitivos lembrar
As ferventes ora\xE7\xF5es, o seu alegre cantar,
Deus os ouviu e sempre os livrou do mal,
E tamb\xE9m os encheu de poder pentecostal!
`,verseorder:3,ismainverse:0,id_hino:453},{idverse:26864,verse:`3. 
Alguns dizem: "Mudado tudo hoje est\xE1".
Mas eu sei que meu Deus nunca jamais mudar\xE1;
Deus permanecer\xE1 quel farol eternal
Enviando ao Seu povo poder pentecostal!`,verseorder:4,ismainverse:0,id_hino:453},{idverse:26865,verse:`1. 
Glorioso \xE9 o Cordeiro,
Ele \xE9 digno de louvor;
\xC9 o resgate verdadeiro
Do pecado e do horror,
Alegrai-vos, \xF3 remidos;
Cantai gl\xF3ria ao Redentor!
`,verseorder:1,ismainverse:0,id_hino:454},{idverse:26866,verse:`Aleluia! Aleluia! Cantai gl\xF3ria e dai
louvor!
Aleluia! Aleluia! Cantai gl\xF3ria ao Redentor!
`,verseorder:2,ismainverse:1,id_hino:454},{idverse:26867,verse:`2. 
Honra, gl\xF3ria e dom\xEDnio
A Jesus, o Rei dos reis!
Que no santo vatic\xEDnio
\xC9 a luz dos Seus fi\xE9is.
Alegrai-vos, \xF3 remidos:
Cantai gl\xF3ria, e vencereis!`,verseorder:3,ismainverse:0,id_hino:454},{idverse:26868,verse:`1. 
O povo de Deus, aqui na terra tem um sinal:
Povo que vive em santa guerra contra o mal;
Povo que espera a Jesus Cristo, que presto vem
\xC9 pois Ele mesmo, quem nos diz isto: "Vigiai mui bem".
`,verseorder:1,ismainverse:0,id_hino:455},{idverse:26869,verse:`2. 
A minha paz, sim, n\xE3o a do mundo, Eu vos darei
O cora\xE7\xE3o de gozo profundo, Eu encherei;
Com o Seu bra\xE7o Ele nos guia, oh! grande amor
Para os que Lhe pedem, logo envia o Consolador!
`,verseorder:2,ismainverse:0,id_hino:455},{idverse:26870,verse:`3. 
\xD3 vinde a Mim, Jesus convida, quem sede tem!
Beber, \xF3 vinde, da \xE1gua da vida, celeste bem!
Cristo sacia a alma sedenta do pecador!
Salva, purifica e alimenta, enche de amor!
`,verseorder:3,ismainverse:0,id_hino:455},{idverse:26871,verse:`4. 
Em Cristo, crendo sempre O seguindo qu\xE3o doce \xE9
A paz profunda e o amor sentido: Tudo por f\xE9
Feliz o homem que guarda isto, pois vida tem
H\xE1 de reinar sempre com Jesus Cristo na mans\xE3o de al\xE9m.`,verseorder:4,ismainverse:0,id_hino:455},{idverse:26872,verse:`1. 
Oh! Que fonte transbordante!
Mais profunda que o mar.
\xC9 de Deus, o amor imenso,
Que Jesus me veio dar.
`,verseorder:1,ismainverse:0,id_hino:456},{idverse:26873,verse:`Ele me abriu a porta, e me reconciliou,
Por Seu sangue derramado; para Deus
me consagrou.
`,verseorder:2,ismainverse:1,id_hino:456},{idverse:26874,verse:`2. 
Como a pomba perseguida
E cansada estava eu
Mas Jesus jamais rejeita,
Quem buscar abrigo Seu.
`,verseorder:3,ismainverse:0,id_hino:456},{idverse:26875,verse:`3. 
Gra\xE7a abundantemente
Sobre mim j\xE1 derramou;
Onde abundou pecado,
Gra\xE7a superabundou.
`,verseorder:4,ismainverse:0,id_hino:456},{idverse:26876,verse:`4. 
Quando alvorecer meu dia.
Com Jesus, irei p\`ro c\xE9u;
Eu O exaltarei p\`ra sempre,
Pois salvou um perdido r\xE9u.`,verseorder:5,ismainverse:0,id_hino:456},{idverse:26877,verse:`1. 
Oh! Que festim de gl\xF3ria para n\xF3s h\xE1 de ser!
Quando tiver nas nuvens, Cristo de aparecer.
`,verseorder:1,ismainverse:0,id_hino:457},{idverse:26878,verse:`Oh! Nesse evento
mui feliz
e de prazer tamb\xE9m,
Eu hei de ver meu bom Jesus,
fonte de todo bem.
`,verseorder:2,ismainverse:1,id_hino:457},{idverse:26879,verse:`2. 
Nesse glorioso dia, o corpo meu mortal,
Ser\xE1 como o de anjos, no lar celestial.
`,verseorder:3,ismainverse:0,id_hino:457},{idverse:26880,verse:`3. 
Oh! Que ditosa vinda, a do meu Salvador!
Eu O estou esperando, mui firme em Seu amor!
`,verseorder:4,ismainverse:0,id_hino:457},{idverse:26881,verse:`4. 
\xD3 Cristo, apressa o dia em que h\xE1s de voltar!
Para ver Tua face eu \`stou a esperar.`,verseorder:5,ismainverse:0,id_hino:457},{idverse:26882,verse:`1. 
Guia me passos, Senhor Jesus,
Senhor de consola\xE7\xE3o,
Leva-me \xE0 estrada da Tua luz,
\xC0 fonte do Teu perd\xE3o.
`,verseorder:1,ismainverse:0,id_hino:458},{idverse:26883,verse:`L\xE1 fora, eis a chorar, sem Deus,
Sem luz, sem lar.
O pecador... Ouve, Senhor,
a queixa do pecador.
`,verseorder:2,ismainverse:1,id_hino:458},{idverse:26884,verse:`2. 
Tuas ovelhas fi\xE9is conduz
Por vales, verg\xE9is em flor,
Ao Teu aprisco, Senhor Jesus...
Jesus, nosso bom Pastor.
`,verseorder:3,ismainverse:0,id_hino:458},{idverse:26885,verse:`3. 
Na larga estrada vai triste e s\xF3
O filho de Belial...
Triste, faminto, envolto em p\xF3,
Escravo infeliz do mal.
`,verseorder:4,ismainverse:0,id_hino:458},{idverse:26886,verse:`4. 
Senhor dos simples, Senhor Jesus,
Oh! Glorioso Deus de amor!
A ovelha errante atrai, conduz
Ao Teu redil, bom Pastor.`,verseorder:5,ismainverse:0,id_hino:458},{idverse:26887,verse:`1. 
De Deus mui firmes s\xE3o as promessas
Mais firmes que as montanhas s\xE3o.
Quando socorro terrestre cessa,
Os de Deus n\xE3o falhar\xE3o!
`,verseorder:1,ismainverse:0,id_hino:459},{idverse:26888,verse:`De Deus mui firmes s\xE3o as promessas,
Falhando tudo, n\xE3o falhar\xE3o;
Se das estrelas o brilho cessa,
Mas as promessas brilhar\xE3o!
`,verseorder:2,ismainverse:1,id_hino:459},{idverse:26889,verse:`2. 
Se a f\xE9 te falta, nos teus apertos,
Nas suas promessas descansa em paz.
Quando \xE9s tentado, estou bem certo
Que Cristo aux\xEDlio te traz.
`,verseorder:3,ismainverse:0,id_hino:459},{idverse:26890,verse:`3. 
Se a febre arde, se extingue a vida
E quer a morte te arrebatar,
Nas Suas promessas ter\xE1s guarida
Bastante pra te abrigar.
`,verseorder:4,ismainverse:0,id_hino:459},{idverse:26891,verse:`4. 
Promessas temos que ao c\xE9u de gozo
Vir\xE1 levar-nos o Rei Jesus;
Ent\xE3o ao crente fiel corajoso,
Dar\xE1 coroa de luz.`,verseorder:5,ismainverse:0,id_hino:459},{idverse:26892,verse:`1. 
Se est\xE1s cansado e abatido,
Dize-o a Cristo; Dize-o a Cristo;
Se te sentes d\xE9bil, confundido,
Dize-o ao teu Salvador.
`,verseorder:1,ismainverse:0,id_hino:460},{idverse:26893,verse:`Dize-o a Cristo; dize-o a Cristo,
Que \xE9 o amigo mais fiel;
N\xE3o h\xE1 outro amigo como Cristo,
Dize-o ao Emanuel.
`,verseorder:2,ismainverse:1,id_hino:460},{idverse:26894,verse:`2. 
Quando est\xE1s de tenta\xE7\xE3o cercado,
Olha p\`ra Cristo; olha p\`ra Cristo;
Quando rugem hostes do pecado,
Olha p\`ra teu Salvador.
`,verseorder:3,ismainverse:0,id_hino:460},{idverse:26895,verse:`3. 
Se sa\xEDram outros j\xE1 das senda,
Segue a Cristo; segue a Cristo;
Se aumenta em torno a contenda,
Segue a teu salvador.
`,verseorder:4,ismainverse:0,id_hino:460},{idverse:26896,verse:`4. 
Ao chegars ao fim da jornada,
Confia em Cristo; confia em Cristo;
Dar-te-\xE1 no c\xE9u feliz entrada,
Confia em teu Salvador.`,verseorder:5,ismainverse:0,id_hino:460},{idverse:26897,verse:`1. 
Jerusal\xE9m por Cristo \xE9 contemplada.
Com os seus altos que rebrilham l\xE1,
Mas entre as belas torres levantadas;
V\xEA as do templo onde esteve j\xE1;
Jesus com Seu olhar mui penetrante,
N\xE3o v\xEA somente o lindo exterior,
Mas sim as almas tristes, vacilantes,
Que rejeitaram Seu divino amor.
`,verseorder:1,ismainverse:0,id_hino:461},{idverse:26898,verse:`2. 
Jesus chorou: Seu cora\xE7\xE3o rasgado
Lamenta e sente uma dor sem par,
Por ver Seu povo indo descuidado,
P\`ra perdi\xE7\xE3o eterna caminhar!
Um som alegre sobe da cidade,
O povo est\xE1 em festa a jubilar,
Mas quando o sol, se esconde \xE0 tarde,
Jesus ao Cedrom foi p\`ra lamentar.
`,verseorder:2,ismainverse:0,id_hino:461},{idverse:26899,verse:`3. 
Jerusal\xE9m, que segues o mau trilho,
E apedrejas os fi\xE9is de Deus;
\xD3 quantas vezes quis juntar teus filhos,
Como a galinha ajunta os pintos seus;
Mas para Mim, as portas tu fechaste,
No dia que Eu vim te dar a paz;
Por teu pecado cega te tornaste,
N\xE3o aceitando o meu amor veraz!
`,verseorder:3,ismainverse:0,id_hino:461},{idverse:26900,verse:`4. 
Ainda se repele a mesma hist\xF3ria,
Jesus do c\xE9u est\xE1 a contemplar
Cidades que t\xEAm aparente gl\xF3ria,
E continua sempre a chorar;
Pois Ele \xE9 quem pode dar a vida
\xC0s multid\xF5es que na condena\xE7\xE3o
Rejeitam, sim, a gra\xE7a oferecida,
A eternal e grande salva\xE7\xE3o!`,verseorder:4,ismainverse:0,id_hino:461},{idverse:26901,verse:`1. 
Gl\xF3ria! Gl\xF3ria! Aleluia!
Gl\xF3ria! Gl\xF3ria ao nosso Salvador!
Gl\xF3ria a Jesus! Gl\xF3ria a Jesus!
Nosso grande Redentor.`,verseorder:1,ismainverse:0,id_hino:462},{idverse:26902,verse:`1. 
Como o povo de Abra\xE3o
Para terra de Si\xE3o
Entre os povos inimigos viajou;
Hoje eu cheio de amor,
E de f\xE9 no Salvador,
Caminhando para o c\xE9u, contente vou.
`,verseorder:1,ismainverse:0,id_hino:463},{idverse:26903,verse:`L\xE1 no c\xE9u, l\xE1 no c\xE9u,
As tristezas eu jamais conhecerei;
Ruas \xE0ureas brilham l\xE1,
Na cidade de Jeov\xE1;
Sempre alegre, vou marchando para o c\xE9u!
`,verseorder:2,ismainverse:1,id_hino:463},{idverse:26904,verse:`2. 
Sustentado pelo amor
Do bendito Salvador,
Vigiando, sempre orando, certo vou;
Dirigido pela luz
Da presen\xE7a de Jesus,
Caminhando para o c\xE9u, eu sempre estou.
`,verseorder:3,ismainverse:0,id_hino:463},{idverse:26905,verse:`3. 
L\xE1 no c\xE9u eu chegarei,
Sua gl\xF3ria gozarei
Com aqueles que servirem ao Senhor;
Quero sempre trabalhar
Para O glorificar,
P\`ra que todos vejam em mim o Seu amor.`,verseorder:4,ismainverse:0,id_hino:463},{idverse:26906,verse:`1. 
Plena gra\xE7a para me salvar
Sangue puro para me lavar,
E poder p\`ra sempre me guardar,
Tem meu Senhor.`,verseorder:1,ismainverse:0,id_hino:464},{idverse:26907,verse:`1. 
Eu li que Jesus fora preso;
De dor a minh\`alma vibrou;
Eu antes assim n\xE3o sentia,
Agora isto a mim empolgou,
Eu li que Ele foi conduzido
\xC0 corte de Jerusal\xE9m;
Ali padeceu grande afronta,
Foi c\`roado de espinhos tamb\xE9m.
`,verseorder:1,ismainverse:0,id_hino:465},{idverse:26908,verse:`Eu sei que eu era culpado,
Mas Ele sofreu j\xE1 por mim;
Eu sei que Ele era inocente,
Padecendo tudo assim.
`,verseorder:2,ismainverse:1,id_hino:465},{idverse:26909,verse:`2. 
Eu li que Jesus foi julgado;
U\`a cruz mui pesada levou,
E nela, por mim expirando,
Os meus vis pecados tomou;
Enquanto na cruz, pendurado,
U\`a lan\xE7a Seu lado furou;
Na esponja Lhe deram vinagre,
E Ele, por  mim, o tragou
`,verseorder:3,ismainverse:0,id_hino:465},{idverse:26910,verse:`3. 
Depois Jos\xE9 de Arimat\xE9ia
E outros disc\xEDpulos tamb\xE9m
Puseram-No, em um sepulcro,
E os guardas vigiavam bem;
Enquanto no mundo, Ele disse,
Que havia de ressuscitar,
E Deus fez ent\xE3o um milagre,
Fazendo-O dos mortos tornar!
`,verseorder:4,ismainverse:0,id_hino:465},{idverse:26911,verse:`4. 
Os guardas ainda vigiavam,
Quando um anjo veio do c\xE9u,
E a pedra que estava na porta,
Com grande poder removeu;
Depressa os la\xE7os ca\xEDram;
O plano de Deus era assim;
E a luz e a vida resplendem,
E isto foi tudo por mim!`,verseorder:5,ismainverse:0,id_hino:465},{idverse:26912,verse:`1. 
Amigo, conheces, tamb\xE9m enalteces
Jesus, o Filho de Deus?
Tens nEle j\xE1 crido, e assim recebido
Jesus, o Filho de Deus?
`,verseorder:1,ismainverse:0,id_hino:466},{idverse:26913,verse:`Que maravilha! Que maravilha!
Jesus, o Filho de Deus!
Oh! Eu te adoro! Sim, eu te amo!
Jesus, o Filho de Deus!
`,verseorder:2,ismainverse:1,id_hino:466},{idverse:26914,verse:`2. 
Seguir, quem almeija, e n\`alma deseja
Jesus, o Filho de Deus?
Quem foi rejeitado por esse adorado
Jesus, o Filho de Deus?
`,verseorder:3,ismainverse:0,id_hino:466},{idverse:26915,verse:`3. 
Se hoje confiares, tamb\xE9m aceitares
Jesus, o Filho de Deus.
Ir\xE1s jubilando, e sempre louvando
Jesus, o Filho de Deus.
`,verseorder:4,ismainverse:0,id_hino:466},{idverse:26916,verse:`4. 
Dos c\xE9us, muito breve, descer p\`ra n\xF3s deve,
Jesus, o Filho de Deus!
\xD3 vem, eu te clamo, \xF3 vem, pois te amo,
Jesus, o filho de Deus.`,verseorder:5,ismainverse:0,id_hino:466},{idverse:26917,verse:`1. 
Oh! Por que duvidar,
Sobre as ondas do mar,
Quando Cristo caminho abriu?
Quando for\xE7ado \xE9s, contra as ondas lutar,
Seu amor a ti quer revelar
`,verseorder:1,ismainverse:0,id_hino:467},{idverse:26918,verse:`Solta o cabo da nau
Toma os remos na m\xE3o
E navega com f\xE9 em Jesus;
E ent\xE3o, tu ver\xE1s que bonan\xE7a se faz
Pois com Ele, seguro ser\xE1s .
`,verseorder:2,ismainverse:1,id_hino:467},{idverse:26919,verse:`2. 
Trevas v\xEAm te assustar ,
tempestades no mar?
Da montanha o mestre te v\xEA;
Na tribula\xE7\xE3o Ele vem socorrer,
Sua m\xE3o bem te pode suster,
`,verseorder:3,ismainverse:0,id_hino:467},{idverse:26920,verse:`3. 
Podes tu recordar,
Maravilhas sem par?
No deserto o povo fartou;
E o mesmo poder
Ele sempre ter\xE1,
Pois n\xE3o muda e n\xE3o falhar\xE1.
`,verseorder:4,ismainverse:0,id_hino:467},{idverse:26921,verse:`4. 
Quando pedes mais f\xE9,
Ele ouve o cr\xEA!
Mesmo sendo em tribula\xE7\xE3o;
Quando a m\xE3o de poder o teu "ego" tirar,
Sobre as ondas poder\xE1s andar.`,verseorder:5,ismainverse:0,id_hino:467},{idverse:26922,verse:`1. 
Queres tu seguir a Cristo,
E andar na Sua luz?
Queres ser por todos visto
Carregando a tua cruz?
`,verseorder:1,ismainverse:0,id_hino:468},{idverse:26923,verse:`Jesus Cristo est\xE1 chamando,
Quer da morte te livrar;
Com paci\xEAncia te esperando,
Vem, p\`ra Ele te salvar.
`,verseorder:2,ismainverse:1,id_hino:468},{idverse:26924,verse:`2. 
Queres hoje dar o passo
E tornares-te crist\xE3o?
Do pecado, rompe o la\xE7o
E ter\xE1s a salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:468},{idverse:26925,verse:`3. 
Por que tens tu resistido,
Esta voz de compaix\xE3o?
S\xEA do amor de Deus vencido,
Vem, recebe a salva\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:468},{idverse:26926,verse:`4. 
O que pede, aqui recebe,
O perd\xE3o no sangue Seu;
Quem da \xE1gua viva bebe,
J\xFAbilo ter\xE1 no c\xE9u!`,verseorder:5,ismainverse:0,id_hino:468},{idverse:26927,verse:`1. 
Quando a angelical trombeta neste mundo estrugir,
O meu nome ouvirei Jesus chamar;
Pois eu creio na promessa, e que Deus a vai cumprir
Quando ouvir Jesus meu nome proclamar!
`,verseorder:1,ismainverse:0,id_hino:469},{idverse:26928,verse:`Gl\xF3ria! Gl\xF3ria! Aleluia!
O meu nome ouvirei Jesus chamar;
Gl\xF3ria! Gl\xF3ria! Aleluia!
Eu espero ouvir Jesus a me chamar.
`,verseorder:2,ismainverse:1,id_hino:469},{idverse:26929,verse:`2. 
Quando o c\xE9u for enrolado e o sol n\xE3o der mais luz,
O meu nome ouvirei Jesus chamar;
Passar\xE3o a terra, o mar, mas permanecer\xE1 Jesus,
Que meu nome vai na gl\xF3ria pronunciar.
`,verseorder:3,ismainverse:0,id_hino:469},{idverse:26930,verse:`3. 
Oh! Que m\xFAsica suave h\xE1 de ser p\`ra mim ouvir
O meu nome Jesus Cristo anunciar,
Oh! Que gozo vai minha alma l\xE1 nos altos c\xE9us fruir
Quando o Cristo o meu nome proclamar!`,verseorder:4,ismainverse:0,id_hino:469},{idverse:26931,verse:`1. 
Tua gra\xE7a, \xF3 Deus amado,
Vem agora conceder,
Aos que v\xE3o ser batizados,
D\xE1-lhes o Esp\xEDrito e poder.
`,verseorder:1,ismainverse:0,id_hino:470},{idverse:26932,verse:`Com o Teu Esp\xEDrito Santo,
Estas almas vem selar;
D\xE1-lhes vida e novo canto,
P\`ra Tua gl\xF3ria conquistar.
`,verseorder:2,ismainverse:1,id_hino:470},{idverse:26933,verse:`2. 
A fim de p\`ra Ti viverem,
Mortos para o mundo est\xE3o;
Para o Esp\xEDrito receberem,
D\xE1-lhes puro cora\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:470},{idverse:26934,verse:`3. 
Com Esp\xEDrito por Deus dado,
Vem, \xF3 vem j\xE1 batizar,
Os que hoje seus pecados,
V\xEAm nas \xE1guas, sepultar.
`,verseorder:4,ismainverse:0,id_hino:470},{idverse:26935,verse:`4. 
J\xE1 contigo ressurgidos,
O Teu nome louvar\xE3o;
Em Jerusal\xE9m, vestidos;
Sim, de branco, reinar\xE3o!`,verseorder:5,ismainverse:0,id_hino:470},{idverse:26936,verse:`1. 
Avan\xE7ai, fi\xE9is soldados,
Contra as hostes infernais;
Ouve-se de guerra, brados,
Homens de Gide\xE3o sejais;
Deste mundo, a ilus\xE3o,
O terror da pr\xF3pria morte
N\xE3o nos turba o cora\xE7\xE3o;
Torna Deus, o fraco - forte.
`,verseorder:1,ismainverse:0,id_hino:471},{idverse:26937,verse:`Pelo sangue do Cordeiro
N\xF3s vamos, sim, triunfar,
O Senhor, ao vencedor vai coroar;
Hei de vencer
Com Jesus, o verdadeiro,
Hei de vencer,
Pelo Seu poder.
`,verseorder:2,ismainverse:1,id_hino:471},{idverse:26938,verse:`2. 
Avan\xE7ai, fi\xE9is soldados,
A seguir o pavilh\xE3o;
Ei-lo na frente arvorado
Por Jesus, nosso Capit\xE3o;
Escutemos o clamor
Dos que foram abatidos;
Vamos, logo sem temor,
Em socorro dos feridos.
`,verseorder:3,ismainverse:0,id_hino:471},{idverse:26939,verse:`3. 
Avan\xE7ai, fi\xE9is soldados,
Sempre fortes, a lutar,
E os povos escravizados,
Ide avante, a libertar,
Manda Cristo avan\xE7ar
A vanguarda dos remidos;
Galard\xE3o h\xE3o de alcan\xE7ar
Os que forem destemidos!`,verseorder:4,ismainverse:0,id_hino:471},{idverse:26940,verse:`1. 
Cristo Jesus l\xE1 na cruz j\xE1 sofre
Em meu lugar, em meu lugar,
Tudo por mim Ele ali padeceu,
Para a minh\`alma salvar.
`,verseorder:1,ismainverse:0,id_hino:472},{idverse:26941,verse:`2. 
Gl\xF3ria a Jesus, meu pecado cravou
Sobre a cruz, sobre a cruz,
Sangue precioso minh\`alma lavou;
Gl\xF3ria ao bondoso Jesus.`,verseorder:2,ismainverse:0,id_hino:472},{idverse:26942,verse:`1. 
Que outro bem ansioso buscarei?
Bem melhor, que Jesus n\xE3o acharei;
NEle s\xF3 para sempre, hei de estar,
E de cora\xE7\xE3o O amar.
`,verseorder:1,ismainverse:0,id_hino:473},{idverse:26943,verse:`Outro bem n\xE3o procurarei,
Outro bem eu n\xE3o acharei;
J\xE1 em mim, \`st\xE1 Jesus,
NEle s\xF3 vou confiar.
Quem aqui pode me saciar,
Neste mundo enganador?
S\xF3 Jesus, Jesus, s\xF3 Jesus, Jesus,
Rei meu e fiel Senhor!
`,verseorder:2,ismainverse:1,id_hino:473},{idverse:26944,verse:`2. 
A vaidade do mundo acabar\xE1,
Tudo aqui a seu tempo findar\xE1;
Para sempre somente, h\xE1 de durar
Quem na luz de Cristo andar.
`,verseorder:3,ismainverse:0,id_hino:473},{idverse:26945,verse:`3. 
Entre os homens n\xE3o buscarei a luz;
N\xE3o mais vivo, mais vive em mim Jesus;
Sua gra\xE7a \xE9 bastante para mim,
N\xE3o ter\xE1 no c\xE9u, mais fim!`,verseorder:4,ismainverse:0,id_hino:473},{idverse:26946,verse:`1. 
Paz, doce paz! Que brota, l\xE1 da cruz;
Nos trouxe paz o sangue de Jesus.
`,verseorder:1,ismainverse:0,id_hino:474},{idverse:26947,verse:`2. 
Paz, doce paz! Andar no Teu querer,
De Cristo, a n\xF3s a paz vai nos trazer.
`,verseorder:2,ismainverse:0,id_hino:474},{idverse:26948,verse:`3. 
Paz, doce paz! P\`ra todo o pecador!
Descanso e paz tereis no Salvador.
`,verseorder:3,ismainverse:0,id_hino:474},{idverse:26949,verse:`4. 
Paz, doce paz! Paz na separa\xE7\xE3o,
A paz de Cristo d\xE1 consola\xE7\xE3o!
`,verseorder:4,ismainverse:0,id_hino:474},{idverse:26950,verse:`5. 
Paz, doce paz! Enquanto ao meu porvir,
Jesus, paz, sempre me far\xE1 fruir.
`,verseorder:5,ismainverse:0,id_hino:474},{idverse:26951,verse:`6. 
Paz, doce paz! N\xE3o temo a morte, a dor,
Jesus venceu a morte e seu terror.
`,verseorder:6,ismainverse:0,id_hino:474},{idverse:26952,verse:`7. 
Paz, doce paz! N\xE3o tardar\xE1 Jesus,
E nos dar\xE1 celeste paz e luz. Am\xE9m.`,verseorder:7,ismainverse:0,id_hino:474},{idverse:26953,verse:`1. 
L\xE1 em Bel\xE9m nasceu Jesus,
Pr\xEDncipe excelso da paz;
Amor divino e santa luz,
Para o mundo perdido, Ele traz.
`,verseorder:1,ismainverse:0,id_hino:475},{idverse:26954,verse:`Louvemos ao Rei bendito;
Louvemos ao Rei bendito;
Louvemos ao Rei bendito;
O Seu poder \xE9 infinito.
`,verseorder:2,ismainverse:1,id_hino:475},{idverse:26955,verse:`2. 
Honra e louvor em alta voz,
Demos ao Filho de Deus,
Pois a certeza temos n\xF3s,
De herdar os refulgente c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:475},{idverse:26956,verse:`3. 
A jubilar os anjos est\xE3o,
Por te nascido o Senhor;
Os magos v\xEAm p\`ra adora\xE7\xE3o
De Jesus, o veraz Redentor.
`,verseorder:4,ismainverse:0,id_hino:475},{idverse:26957,verse:`4. 
S\xF3 mesmo o t\xE3o profundo amor
Do nosso bendito Deus,
Mandava ao mundo de horror,
O Seu Filho, p\`ra salvar os Seus.`,verseorder:5,ismainverse:0,id_hino:475},{idverse:26958,verse:`1. 
Enquanto para Jeric\xF3
O bom Jesus passava,
Um cego, filho de Jac\xF3,
Sem se cansar clamava:
`,verseorder:1,ismainverse:0,id_hino:476},{idverse:26959,verse:`"Jesus, \xF3 Filho de Davi,
Tem compaix\xE3o de mim, Senhor!"
"Jesus, \xF3 Filho de Davi,
Atende ao meu clamor!"
`,verseorder:2,ismainverse:1,id_hino:476},{idverse:26960,verse:`2. 
A multid\xE3o vendo-o clamar,
- "S\xEA quieto", lhe dizia;
Mas ele sem a escutar,
Mais forte repetia.
`,verseorder:3,ismainverse:0,id_hino:476},{idverse:26961,verse:`3. 
Jesus ent\xE3o mandou chamar
O cego que gritava,
Que veio logo sem tardar,
Mas "inda assim clamava".
`,verseorder:4,ismainverse:0,id_hino:476},{idverse:26962,verse:`4. 
Jesus pergunta com prazer:
"Que queres que te fa\xE7a?
"Jesus, eu quero hoje ver,
Por Teu poder e gra\xE7a".
`,verseorder:5,ismainverse:0,id_hino:476},{idverse:26963,verse:`5. 
Ent\xE3o Jesus lhe respondeu:
"Vai, a tua f\xE9 te salvou".
E logo viu, o Bartimeu,
E ao bom Jesus louvou!`,verseorder:6,ismainverse:0,id_hino:476},{idverse:26964,verse:`1. 
Sede fortes, irm\xE3os na lida,
\xC9 Cristo quem vos conduz;
Garantindo eterna vida,
Aos que trilharem na luz.
`,verseorder:1,ismainverse:0,id_hino:477},{idverse:26965,verse:`Sim, andai na luz de Cristo,
Libertos da eterna dor;
Buscai, v\xF3s que a tendes visto,
A gl\xF3ria do Redentor!
`,verseorder:2,ismainverse:1,id_hino:477},{idverse:26966,verse:`2. 
Sede santos, velai orando,
Amai com santo fervor;
Muitos ficar\xE3o chorando,
Na vinda do Salvador.
`,verseorder:3,ismainverse:0,id_hino:477},{idverse:26967,verse:`3. 
Os fi\xE9is adormecidos,
A voz do anjo ouvir\xE3o;
Transformados os remidos,
A Cristo encontrar\xE3o.
`,verseorder:4,ismainverse:0,id_hino:477},{idverse:26968,verse:`4. 
E vereis naquele dia
Sua gl\xF3ria em esplendor,
Fruireis paz e alegria,
Nos bra\xE7os do salvador.`,verseorder:5,ismainverse:0,id_hino:477},{idverse:26969,verse:`1. 
Eis-me, Jesus, Te louvando,
Pois Tu padeceste assim;
O sangue Teu derramado,
Na dura cruz por mim.
`,verseorder:1,ismainverse:0,id_hino:478},{idverse:26970,verse:`Gl\xF3ria, aleluia! \xD3 Cordeiro,
Tua vida deu-me paz!
Dou-Te louvor verdadeiro,
Por gra\xE7a t\xE3o veraz!
`,verseorder:2,ismainverse:1,id_hino:478},{idverse:26971,verse:`2. 
Do Teu amor me encheste,
Oh! Maravilhoso Jesus;
Quando o Esp\xEDrito me deste,
Trazendo gozo \xE0 flux!
`,verseorder:3,ismainverse:0,id_hino:478},{idverse:26972,verse:`3. 
Os Teus disc\xEDpulos, fizeste
Em l\xEDnguas estranhas falar;
O mesmo a mim concedeste,
E d\xE1s a quem buscar.
`,verseorder:4,ismainverse:0,id_hino:478},{idverse:26973,verse:`4. 
Que Teu Esp\xEDrito des\xE7a,
A fim de Teu povo selar,
Realizando a promessa
Naquele que confiar.
`,verseorder:5,ismainverse:0,id_hino:478},{idverse:26974,verse:`5. 
Os dons do Santo Esp\xEDrito
S\xE3o nove, que Cristo nos d\xE1;
E ao que pede contrito,
Jesus n\xE3o os negar\xE1.
`,verseorder:6,ismainverse:0,id_hino:478},{idverse:26975,verse:`6. 
\xC9 Teu amor, c\xE9u e gozo,
No qual sempre me alegrarei;
Ao meu Senhor t\xE3o bondoso,
Mil gl\xF3rias Lhe darei!`,verseorder:7,ismainverse:0,id_hino:478},{idverse:26976,verse:`1. 
\xC9 meia noite, o ano j\xE1 fenece,
N\xF3s elevamos os olhos aos c\xE9us,
Todos orando para que comece
O ano novo na gra\xE7a de Deus.
`,verseorder:1,ismainverse:0,id_hino:479},{idverse:26977,verse:`As nossas vozes juntas levantemos,
Com alegria e suave som;
Em gratid\xE3o a Cristo, jubilemos,
Pelo raiar de mais um ano bom.
`,verseorder:2,ismainverse:1,id_hino:479},{idverse:26978,verse:`2. 
Senhor, pedimos n\xF3s ardetemente,
Ouve, por Teu infinito amor;
Salva da nossa p\xE1tria toda gente,
P\`ra Tua honra, e p\`ra Teu louvor.
`,verseorder:3,ismainverse:0,id_hino:479},{idverse:26979,verse:`3. 
As nossas almas torna impolutas,
N\xE3o nos deixando em fraquezas cair,
Nossa ora\xE7\xE3o, Senhor, hoje escuta,
Para podemos aqui Te servir.
`,verseorder:4,ismainverse:0,id_hino:479},{idverse:26980,verse:`4. 
Gra\xE7as a Ti, por esse ano findo,
Pois nos supriste com muito poder;
Gra\xE7as a Ti, por esse ano vindo,
Pois b\xEAn\xE7\xE3os mil vamos n\xF3s receber.`,verseorder:5,ismainverse:0,id_hino:479},{idverse:26981,verse:`1. 
Deus eterno, em Tua presen\xE7a,
Nossos s\xE9culos horas s\xE3o
E um segundo a exist\xEAncia
Da humana gera\xE7\xE3o;
Mas o homem que ao Teu lado,
Pela f\xE9 quer j\xE1 voar,
Neste mundo de pecado,
Lento o tempo v\xEA passar.
`,verseorder:1,ismainverse:0,id_hino:480},{idverse:26982,verse:`2. 
Outro ano h\xE1 fenecido,
Que o viver mais encurtou,
E o descanso apetecido,
Algo mais se aproximou.
Gra\xE7as mil por Teus favores,
Tua Igreja, \xF3 Deus, te d\xE1!
Aleluias e louvores,
Teu poder nos suster\xE1!
`,verseorder:2,ismainverse:0,id_hino:480},{idverse:26983,verse:`3. 
Tu proteges as fam\xEDlias,
Visitando cada lar;
Se o Senhor nos auxilia,
Que nos pode aqui faltar?
Onde quer que Te ame o homem,
E Te sirva, andando bem,
Faze que seja o Teu nome
Exaltado sempre! Am\xE9m! Am\xE9m!`,verseorder:3,ismainverse:0,id_hino:480},{idverse:26984,verse:`1. 
Tu deixaste, Senhor,
Tua gl\xF3ria, esplendor,
Quando ao mundo quiseste descer,
N\xE3o puderam achar
Em Bel\xE9m um lugar,
Num pres\xE9pio Tu foste nascer.
`,verseorder:1,ismainverse:0,id_hino:481},{idverse:26985,verse:`Vem ao meu cora\xE7\xE3o, \xF3 Cristo,
Nele tenho p\`ra Ti um lugar!
Vem ao meu cora\xE7\xE3o, \xF3 Cristo vem!
Nele podes p\`ra sempre morar!
`,verseorder:2,ismainverse:1,id_hino:481},{idverse:26986,verse:`2. 
Hinos de adora\xE7\xE3o,
Anjos no c\xE9u Te d\xE3o,
Te rendendo excelso louvor.
Mas humilde o Senhor
Veio ao mundo de horror,
P\`ra dar vida ao mais vil pecador!
`,verseorder:3,ismainverse:0,id_hino:481},{idverse:26987,verse:`3. 
As raposas aqui
Covas t\xEAm para si,
E seus ninhos as aves do c\xE9us,
S\xF3 n\xE3o teve um lugar
P\`ra cabe\xE7a pousar
Jesus Cristo, o Filho de Deus.
`,verseorder:4,ismainverse:0,id_hino:481},{idverse:26988,verse:`4. 
Do c\xE9u vieste Jesus,
Nos trazendo Tua luz,
Que nos d\xE1 eternal salva\xE7\xE3o;
E com \xF3dio e furor
Te cravaram, Senhor,
Sobre a cruz, donde deste o perd\xE3o!
`,verseorder:5,ismainverse:0,id_hino:481},{idverse:26989,verse:`5. 
Aleluias nos c\xE9us,
Ao Cordeiro de Deus!
Quando vier o Seu povo buscar;
Sua voz se ouvir\xE1
E p\`ra mim, oh! Dir\xE1:
"Vem, Eu tenho p\`ra ti um lugar".`,verseorder:6,ismainverse:0,id_hino:481},{idverse:26990,verse:`1. 
Bendito Jesus, n\xF3s chamamos por Ti,
Cercamos Tua mesa, Senhor;
Por Teu Paracleto nos preside aqui;
Vem encher-nos de santo fervor.
`,verseorder:1,ismainverse:0,id_hino:482},{idverse:26991,verse:`2. 
A grande ternura do Teu cora\xE7\xE3o
Chamou-nos aqui, oh! Senhor!
P\`ra n\xF3s celebrarmos em santa uni\xE3o,
Tua morte, oh! bom Redentor!
`,verseorder:2,ismainverse:0,id_hino:482},{idverse:26992,verse:`3. 
Os p\xE9s dos Teus servos lavando, Senhor,
Quiseste o exemplo nos dar,
Da Tua humildade, do Teu grande amor,
Que entre n\xF3s deve sempre reinar.
`,verseorder:3,ismainverse:0,id_hino:482},{idverse:26993,verse:`4. 
Aben\xE7oaste o p\xE3o e o partiste a dizer:
"Tomai o meu corpo e comei,
E todas as vezes, pois \xE9 o dever,
Em mem\xF3ria de Mim o fazei".
`,verseorder:4,ismainverse:0,id_hino:482},{idverse:26994,verse:`5. 
O c\xE1lice da Nova Alian\xE7a de amor,
No Teu sangue, \xF3 meigo Jesus,
Nos encha de gra\xE7a, de novo vigor,
Nos anime na senda da luz!
`,verseorder:5,ismainverse:0,id_hino:482},{idverse:26995,verse:`6. 
Queremos, Jesus, neste ato de amor,
O teu memorial celebrar;
P\`ra n\xF3s ele tem um infinito valor,
Um valor que jamais vai cessar.`,verseorder:6,ismainverse:0,id_hino:482},{idverse:26996,verse:`1. 
Disse Jesus, eis que presto Eu venho,
Trago comigo, tamb\xE9m galard\xE3o;
Venho do c\xE9u com a gl\xF3ria que tenho,
\xD3 vigiai firmes na ora\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:483},{idverse:26997,verse:`Ora vem Jesus, gloriosa luz,
Ainda n\xF3s Te esperamos; ora vem Jesus!
De ver-Te, sedentos bradamos: Oh! Senhor Jesus
Por Ti velando estamos; ora vem, Jesus!
`,verseorder:2,ismainverse:1,id_hino:483},{idverse:26998,verse:`2. 
Disse Jesus, eis que presto Eu venho,
E os remidos p\`ro c\xE9u levarei,
E em lugar do opr\xF3brio do lenho,
Uma coroa p\`ra eles darei!
`,verseorder:3,ismainverse:0,id_hino:483},{idverse:26999,verse:`3. 
Disse Jesus, eis que presto Eu venho,
A minha Igreja vou arrebatar;
Hoje buscai-Me com todo o empenho,
Para que Eu possa vos prerarar!`,verseorder:4,ismainverse:0,id_hino:483},{idverse:27e3,verse:`1. 
Houve Um que morreu por meus crimes na cruz,
\`Inda indigno e vil como sou;
Sou feliz, pois Seu sangue verteu meu Jesus
E com este, meus crimes lavou.
`,verseorder:1,ismainverse:0,id_hino:484},{idverse:27001,verse:`Meus pecados levou na cruz onde
morreu
O sublime e meigo Jesus: os desprezos sofreu,
A minh\`alma salvou
E mudou minhas trevas em luz.
`,verseorder:2,ismainverse:1,id_hino:484},{idverse:27002,verse:`2. 
Ele \xE9 terno e am\xE1vel, n\xE3o h\xE1 outro igual!
Pois converte o infifel cora\xE7\xE3o;
E por essa paci\xEAncia e amor divinal
Estou livre da condena\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:484},{idverse:27003,verse:`3. 
Meu anelo constante \xE9 em Cristo viver,
Meu caminho, Seu exemplo marcou;
E p\`ra dar-me a vida, Jesus quis morrer;
Sobre a cruz meus pecados levou.`,verseorder:4,ismainverse:0,id_hino:484},{idverse:27004,verse:`1. 
\`Stou andando para o c\xE9u
Onde os santos j\xE1 est\xE3o:
\`Stou alegre e satisfeito!
Minha p\xE1tria \xE9 Si\xE3o,
Morte l\xE1 n\xE3o entrar\xE1;
Que alegria ali ser\xE1!
`,verseorder:1,ismainverse:0,id_hino:485},{idverse:27005,verse:`Oh! Gl\xF3ria! Aleluia!
Meu desejo \xE9 estar no c\xE9u;
\`Stou alegre \xE9 satisfeito;
Oh! Gl\xF3ria! Aleluia!
Meu desejo \xE9 estar no c\xE9u;
Que alegria ali ser\xE1!
`,verseorder:2,ismainverse:1,id_hino:485},{idverse:27006,verse:`2. 
\`Stou andando para o c\xE9u,
Onde as ruas de ouro s\xE3o;
\`Stou alegre e satisfeito!
Tenho ali feliz mans\xE3o,
Meu Jesus l\xE1 estar\xE1!
Que alegria ali ser\xE1!
`,verseorder:3,ismainverse:0,id_hino:485},{idverse:27007,verse:`3. 
\`Stou andando para o c\xE9u,
Onde reina gozo e paz,
\`Stou alegre e satisfeito!
Gozo santo e veraz
Todo o crente ali ter\xE1,
Que alegria ali ser\xE1!
`,verseorder:4,ismainverse:0,id_hino:485},{idverse:27008,verse:`4. 
\`Stou andando para o c\xE9u,
Para ver meu Salvador;
\`Stou alegre e satisfeito!
Cantarei o Seu louvor,
Pois minh\`alma alegre est\xE1;
Que alegria ali ser\xE1!`,verseorder:5,ismainverse:0,id_hino:485},{idverse:27009,verse:`1. 
Qual vazio vaso \`sta teu cora\xE7\xE3o,
Para receber de Deus a salva\xE7\xE3o?
S\xF3 Jesus teu vaso poder\xE1 encher,
De b\xEAn\xE7\xE3os que d\xE3o poder.
`,verseorder:1,ismainverse:0,id_hino:486},{idverse:27010,verse:`Deixa encher teu vaso at\xE9 transbordar;
Que Jesus tua vida possa governar,
P\xF5e teu sacrif\xEDcio hoje sobre o altar;
E ver\xE1s as b\xEAn\xE7\xE3os descer sem cessar.
`,verseorder:2,ismainverse:1,id_hino:486},{idverse:27011,verse:`2. 
A vida abundante tu receber\xE1s,
E constantemente em Deus te alegrar\xE1s,
Qual ribeiro d\`\xE1gua, o Senhor quer ver
O deserto florescer
`,verseorder:3,ismainverse:0,id_hino:486},{idverse:27012,verse:`3. 
O Senhor precisa vasos para encher,
Mas vazios vasos que ir\xE3o conter
Toda Sua gra\xE7a, brasas do altar,
Para o fogo espalhar.`,verseorder:4,ismainverse:0,id_hino:486},{idverse:27013,verse:`1. 
Gl\xF3ria! Gl\xF3ria seja ao grande Deus onipotente
Ao Teu nome damos gl\xF3ria porque \xE9s onisciente;
Aleluia! Aleluia a Deus Pai onipresente.
`,verseorder:1,ismainverse:0,id_hino:487},{idverse:27014,verse:`2. 
Gloria! Gl\xF3ria! Gl\xF3ria a Jesus,
o Salvador amado,
Ao Teu nome damos gl\xF3ria,
porque tens nos libertado
Aleluia! Aleluia a Jesus ressuscitado.
`,verseorder:2,ismainverse:0,id_hino:487},{idverse:27015,verse:`3. 
Gl\xF3ria! Gl\xF3ria! Gl\xF3ria seja dada ao Esp\xEDrito Santo,
Ao Teu nome damos gl\xF3ria,
Aleluia! Aleluia ao Deus trino, ao Deus Santo.`,verseorder:3,ismainverse:0,id_hino:487},{idverse:27016,verse:`1. 
Ao raiar do \xE1ureo dia, o bom Jesus vem nos buscar
P\`ra com Ele, sempre estarmos l\xE1 no c\xE9u;
Os milh\xF5es purificados pelo sangue de Jesus
Subir\xE3o p\`ra contemplar o amado Seu.
`,verseorder:1,ismainverse:0,id_hino:488},{idverse:27017,verse:`Quando os salvos
congregados,
Dentre os povos e na\xE7\xF5es,
Virem Cristo mui amado,
Oh! Que gozo encher\xE1 seus cora\xE7\xF5es.
`,verseorder:2,ismainverse:1,id_hino:488},{idverse:27018,verse:`2. 
L\xE1 da \xCDndia vem um povo que Jesus purificou
E da \xC1frica, as tribos chegar\xE3o,
Quem de Cristo, a mensagem lhes levou, os
Conquistou
Juntos todos, l\xE1 no c\xE9u se alegrar\xE3o.
`,verseorder:3,ismainverse:0,id_hino:488},{idverse:27019,verse:`3. 
Quando os milh\xF5es da China,
todos que Jesus ganhou
Juntos dos que foram salvos no Brasil,
L\xE1 no c\xE9u unidos, gl\xF3ria derem todos a Jesus,
Triunfantes estar\xE3o no Seu redil.
`,verseorder:4,ismainverse:0,id_hino:488},{idverse:27020,verse:`4. 
Espalhemos a mensagem, que Jesus nos entregou
Da gloriosa nova que que vem l\xE1 da cruz;
Que Seu sangue precioso purifica o pecador,
Derramando nele gloriosa luz.`,verseorder:5,ismainverse:0,id_hino:488},{idverse:27021,verse:`1. 
Chegai, pastorzinhos, chegai p\`ra adorar
O Rei do universo, que nasce em Bel\xE9m,
Sim, ricos presentes podemos Lhe dar,
A f\xE9 o amor, nossa vida tamb\xE9m.
`,verseorder:1,ismainverse:0,id_hino:489},{idverse:27022,verse:`2. 
Um r\xFAstico teto abrigo Lhe d\xE1;
Por casa um pres\xE9pio, por templo um portal;
Num leito de palhas deitado est\xE1,
Quem deu \xE0s estrelas a luz celestial.
`,verseorder:2,ismainverse:0,id_hino:489},{idverse:27023,verse:`3. 
Seguindo uma estrela que veio O anunciar,
Os magos do Oriente atr\xE1s dEle, v\xE3o:
E diante se prostam do Rei de Jud\xE1,
De incenso, ouro e mirra, tributo Lhe d\xE3o.
`,verseorder:3,ismainverse:0,id_hino:489},{idverse:27024,verse:`4. 
Oh! Gl\xF3ria, Aleluia, os salvos Te d\xE3o,
Hosanas os anjos, entoam tamb\xE9m,
Louvai ao Messias, que trouxe perd\xE3o;
A Cristo Jesus, que nasceu em Bel\xE9m.`,verseorder:4,ismainverse:0,id_hino:489},{idverse:27025,verse:`1. 
Vem, fatigado cora\xE7\xE3o,
Cristo est\xE1 passando;
A Ele estende tua  m\xE3o,
No Seu amor, confiando.
`,verseorder:1,ismainverse:0,id_hino:490},{idverse:27026,verse:`Passando est\xE1 - passando est\xE1...
O amoroso Salvador!
Vem tu a Ele, \xF3 pecador!
Passa aqui - Chama por ti.
`,verseorder:2,ismainverse:1,id_hino:490},{idverse:27027,verse:`2. 
Vem, oprimido pecador,
Cristo est\xE1 passando;
Vem receber do Seu amor,
Que est\xE1 ofertando!
`,verseorder:3,ismainverse:0,id_hino:490},{idverse:27028,verse:`3. 
Vem, \xF3 faminto, te fartar!
Cristo est\xE1 passando;
O p\xE3o da vida vem tomar;
Por que est\xE1s demorando?
`,verseorder:4,ismainverse:0,id_hino:490},{idverse:27029,verse:`4. 
Vem, pecador, sem demorar,
Cristo est\xE1 passando;
Nos bra\xE7os Seus vem te lan\xE7ar,
Jesus te est\xE1 chamando.`,verseorder:5,ismainverse:0,id_hino:490},{idverse:27030,verse:`1. 
O teu pecado tu queres deixar?
No sangue h\xE1 poder, sim, h\xE1 poder;
Queres do mal a vit\xF3ria ganhar?
Seu sangue tem este poder!
`,verseorder:1,ismainverse:0,id_hino:491},{idverse:27031,verse:`H\xE1 poder, sim, for\xE7a e vigor
Neste sangue de Jesus;
H\xE1 poder, sim, no bom Salvador;
Oh! Cocnfia no Cristo da cruz.
`,verseorder:2,ismainverse:1,id_hino:491},{idverse:27032,verse:`2. 
Queres os v\xEDcios abandonar?
No sangue a poder, sim, h\xE1 poder;
Confia em Cristo para te curar;
Seu sangue tem este poder!
`,verseorder:3,ismainverse:0,id_hino:491},{idverse:27033,verse:`3. 
Oh! Paral\xEDtico, queres andar?
No sangue h\xE1 poder, sim, h\xE1 poder;
Para fazer-te tamb\xE9m caminhar,
Seu sangue tem este poder!
`,verseorder:4,ismainverse:0,id_hino:491},{idverse:27034,verse:`4. 
Queres pureza p\`ra teu cora\xE7\xE3o?
No sangue h\xE1 poder, sim, h\xE1 poder;
Mais lealdade, mais consagra\xE7\xE3o,
Seu sangue tem este poder!
`,verseorder:5,ismainverse:0,id_hino:491},{idverse:27035,verse:`5. 
Queres de Cristo a mensagem levar?
No sangue h\xE1 poder, sim, h\xE1 poder;
Queres co\`os anjos, na gl\xF3ria cantar?
Seu sangue tem este poder!`,verseorder:6,ismainverse:0,id_hino:491},{idverse:27036,verse:`1. 
Jesus em breve vir\xE1 do c\xE9u,
Cumprir o que subindo prometeu,
Tudo revela que vir\xE1 presto,
Com Ele iremos p\`ra o reino Seu.
`,verseorder:1,ismainverse:0,id_hino:492},{idverse:27037,verse:`Jesus com sangue me tem
comprado,
E pelo Esp\xEDrito me tem selado;
\`Stou remido, tenho o penhor
Da  minha heran\xE7a em meu Senhor.
`,verseorder:2,ismainverse:1,id_hino:492},{idverse:27038,verse:`2. 
E, pelo mundo sem descansar,
Os mensageiros \`st\xE3o a anunciar
O Evangelho do Filho amado,
Que a este mundo quis resgatar!
`,verseorder:3,ismainverse:0,id_hino:492},{idverse:27039,verse:`3. 
Do mundo inteiro j\xE1 v\xE3o chegar!
Em breve tu n\xE3o achas mais lugar;
Se vir desejas, n\xE3o te demores,
Mas vem depressa p\`ra te salvar.
`,verseorder:4,ismainverse:0,id_hino:492},{idverse:27040,verse:`4. 
J\xE1 vem o dia, clareando est\xE1,
Pois a trombeta em breve soar\xE1,
A Sua Noiva em vestes brancas,
Jesus p\`ra gl\xF3ria conduzir\xE1.`,verseorder:5,ismainverse:0,id_hino:492},{idverse:27041,verse:`1. 
Ao Pai voltai, v\xF3s que chorais,
Voltai ao lar paterno;
Pois l\xE1 Jesus, em santa luz,
Vos d\xE1 um gozo eterno!
`,verseorder:1,ismainverse:0,id_hino:493},{idverse:27042,verse:`Ao Pai voltai, voltai ao lar eterno;
A Deus tornai, ao vosso lar paterno.
`,verseorder:2,ismainverse:1,id_hino:493},{idverse:27043,verse:`2. 
O Pai est\xE1 ansioso l\xE1,
Pois quer vos ver a salvo;
Tereis ent\xE3o a salva\xE7\xE3o
Se Cristo for o alvo!
`,verseorder:3,ismainverse:0,id_hino:493},{idverse:27044,verse:`3. 
O mal deixai e regressai
P\`ra o lar do Pai glorioso,
Ent\xE3o, assim, sereis enfim
P\`ra sempre mui ditoso.
`,verseorder:4,ismainverse:0,id_hino:493},{idverse:27045,verse:`4. 
N\xE3o recuseis, mas escolhei
A Cristo, o vero trilho;
Voltai ao lar, p\`ra l\xE1 morar,
Com Deus e com Seu Filho.`,verseorder:5,ismainverse:0,id_hino:493},{idverse:27046,verse:`1. 
Jerusal\xE9m celestial,
Mans\xE3o de paz t\xE3o divianal!
De jaspe tem muro em redor;
Onde \xE9 a luz, o meu Senhor.
`,verseorder:1,ismainverse:0,id_hino:494},{idverse:27047,verse:`Jerusal\xE9m, mans\xE3o de luz,
Jerusal\xE9m de meu Jesus!
Cidade que p\`ra n\xF3s fez Deus;
Eterna gl\xF3ria para os filhos seus.
`,verseorder:2,ismainverse:1,id_hino:494},{idverse:27048,verse:`2. 
Um rio tem, e qual cristal,
Cuja \xE1gua faz ser imortal;
As \xE1rvores l\xE1 mui belas s\xE3o,
E p\`ra curar oh! servir\xE3o.
`,verseorder:3,ismainverse:0,id_hino:494},{idverse:27049,verse:`3. 
Ali n\xE3o h\xE1 mais pranto e dor;
A maldi\xE7\xE3o n\xE3o tem vigor;
A noite foi e n\xE3o vem mais;
O sol raiou, passaram os "ais".
`,verseorder:4,ismainverse:0,id_hino:494},{idverse:27050,verse:`4. 
Ali verei o meu Senhor,
Contemplarei seu resplendor,
E cantarei o seu louvor
Em gratid\xE3o e com fervor.`,verseorder:5,ismainverse:0,id_hino:494},{idverse:27051,verse:`1. 
Jesus Cristo \xE0 porta est\xE1;
Bate sempre para entrar;
Se ouvires abre j\xE1;
Abre o cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:495},{idverse:27052,verse:`Abre o cora\xE7\xE3o!
Abre o cora\xE7\xE3o!
Jesus quer te libertar;
Abre o cora\xE7\xE3o!
`,verseorder:2,ismainverse:1,id_hino:495},{idverse:27053,verse:`2. 
Jesus bate ainda l\xE1
E, se n\xE3o abrires j\xE1,
Ele triste voltar\xE1;
Abre o cora\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:495},{idverse:27054,verse:`3. 
Jesus bate, abre j\xE1,
Abre, sim, \xF3 pecador!
O Ju\xEDzo \xE0 porta est\xE1;
Abre o cora\xE7\xE3o!`,verseorder:4,ismainverse:0,id_hino:495},{idverse:27055,verse:`1. 
Jazia num lago de lama, de horror,
N\xE3o tinha esperan\xE7a em mim, pecador,
Mas tenho clamado a Jesus Salvador,
P\`ra fora do lago me tirou o Senhor.
`,verseorder:1,ismainverse:0,id_hino:496},{idverse:27056,verse:`Dum lago horr\xEDvel Jesus me
tirou,
Sobre a rocha meus p\xE9s firmou!
Um novo canto em mim soou,
Ao Salvador, Aleluia!
`,verseorder:2,ismainverse:1,id_hino:496},{idverse:27057,verse:`2. 
Agora desejo a Cristo seguir,
Com todo o meu ser eu O quero sentir,
Pois Ele salvou-me a fim de O servir,
E para Seu gozo, eu tamb\xE9m fruir.
`,verseorder:3,ismainverse:0,id_hino:496},{idverse:27058,verse:`3. 
Somente eu quero servir ao Senhor,
Com todo prazer e com muito fervor,
Assim vou andar de valor em valor,
Na luz gloriosa do Consolador!
`,verseorder:4,ismainverse:0,id_hino:496},{idverse:27059,verse:`4. 
P\`ra sempre agora eu proclamarei,
"Foi crendo em Cristo que me libertei,
E foi no Seu sangue que me lavei,
Na rocha eterna mais me firmarei!"`,verseorder:5,ismainverse:0,id_hino:496},{idverse:27060,verse:`1. 
Jesus \xE9 precioso, meu bom Salvador,
P\`ra sempre O louvo, pois deu-me a luz;
Se d\xE9bil me encontro me d\xE1 Seu vigor,
P\`ra mim \xE9 precioso Jesus!
`,verseorder:1,ismainverse:0,id_hino:497},{idverse:27061,verse:`Precioso \xE9 Jesus, meu Jesus!
Precioso \xE9 Jesus, minha luz!
Que gl\xF3ria ser\xE1 o Seu rosto mirar!
P\`ra mim \xE9 precioso Jesus!
`,verseorder:2,ismainverse:1,id_hino:497},{idverse:27062,verse:`2. 
Andando no mundo, sem Deus, a vagar,
Na senda do mal, que a morte conduz,
Me disse Jesus: "Eu te quero salvar"
P\`ra mim \xE9 precioso Jesus!
`,verseorder:3,ismainverse:0,id_hino:497},{idverse:27063,verse:`3. 
J\xE1 por Sua gra\xE7a, eu posso gozar;
Encheu-me de gozo do reino da luz;
Contemplo por f\xE9, meu celeste lugar.
P\`ra mim \xE9 precioso Jesus!
`,verseorder:4,ismainverse:0,id_hino:497},{idverse:27064,verse:`4. 
Jesus \xE9 precioso, pois me quis salvar,
Morrendo por mim pendurado na cruz,
E mui glorioso vir\xE1 me buscar,
P\`ra mim \xE9 precioso Jesus!`,verseorder:5,ismainverse:0,id_hino:497},{idverse:27065,verse:`1. 
Guia-me, \xF3 Salvador, pela senda divinal,
Ao Teu lado, sem temor, tenho gozo perenal!
`,verseorder:1,ismainverse:0,id_hino:498},{idverse:27066,verse:`Cristo! Cristo! N\xE3o
me deixes, \xF3 Senhor;
Guia-me por Teu amor, e serei um vancedor.
`,verseorder:2,ismainverse:1,id_hino:498},{idverse:27067,verse:`2. 
N\xE3o me deixes, \xF3 Senhor, neste horrendo e
Forte mar; Leva-me por Teu amor para o meu eterno
lar.
`,verseorder:3,ismainverse:0,id_hino:498},{idverse:27068,verse:`3. 
\xC9s minha salva\xE7\xE3o, meu querido Redentor;
Da terr\xEDvel corrup\xE7\xE3o me salvaste com amor!
`,verseorder:4,ismainverse:0,id_hino:498},{idverse:27069,verse:`4. 
Quero Teu somente ser, Te amar e Te sevir,
Cristo \xE9, para mim, viver e morrer, o c\xE9u fruir.`,verseorder:5,ismainverse:0,id_hino:498},{idverse:27070,verse:`1. 
Santa B\xEDblia, para  mim,
\xC9s o meu tesouro, sim;
Tu cont\xE9ns a lei de Deus
E me mostras lindos c\xE9us;
Tu me dizes quem eu sou,
Donde vim, p\`ra onde vou!
`,verseorder:1,ismainverse:0,id_hino:499},{idverse:27071,verse:`2. 
Tu repreendes meu falar,
Me exortas sem cessar.
Alumias os meus p\xE9s,
E me guias, pela f\xE9,
Para as fontes de amor,
Do bendito Salvador!
`,verseorder:2,ismainverse:0,id_hino:499},{idverse:27072,verse:`3. 
\xC9s a voz dos altos c\xE9us,
Do Esp\xEDrito de Deus.
Que vigor p\`ra alma d\xE1,
Quando em afli\xE7\xE3o est\xE1;
Me ensinas triunfar
Dentre os mortos, do pecar!
`,verseorder:3,ismainverse:0,id_hino:499},{idverse:27073,verse:`4. 
Por Tua santa letra sei,
Que com Cristo reinarei;
Eu que t\xE3o indigno sou,
Por Tua luz, ao c\xE9u eu vou;
Santa B\xEDblia para mim,
\xC9s o meu tesouro, sim!`,verseorder:4,ismainverse:0,id_hino:499},{idverse:27074,verse:`1. 
Quero ver a Jesus Cristo, meu precioso Salvador,
Ver a face do meu Mestre, que por mim sofreu na
Cruz;
Quero ir ao c\xE9u sublime, ver o trono do Senhor,
Viverei, sim, nessa p\xE1tria de eterno gozo e luz.
`,verseorder:1,ismainverse:0,id_hino:500},{idverse:27075,verse:`Quero ver a
desejada
P\xE1tria onde reina eterno amor;
Quero ter feliz morada,
Com Jesus, meu Salvador.
`,verseorder:2,ismainverse:1,id_hino:500},{idverse:27076,verse:`2. 
Quero dar a Jesus Cristo, sem reserva, meu amor.
E tamb\xE9m viver p\`ra sempre entre o coro angelical;
Quero ser um mensageiro conduzindo o pecador
Para a Cruz de Jesus Cristo, para a p\xE1tria celestial.
`,verseorder:3,ismainverse:0,id_hino:500},{idverse:27077,verse:`3. 
Pela obra redentora de Jesus que me salvou,
Viverei eternamente p\`ra gozar o Seu amor;
Pelo sangue do Cordeiro, redimido j\xE1 estou
Com as hostes redimidas louvarei ao meu Senhor
`,verseorder:4,ismainverse:0,id_hino:500},{idverse:27078,verse:`4. 
Vou morar na minha p\xE1tria que Jesus foi preparar,
Habitar entre os remidos pelo sangue do Senhor;
Entre os anjos e os arcanjos l\xE1 no reino eu vou
morar
E dar gl\xF3rias para sempre ao bondoso Salvador.`,verseorder:5,ismainverse:0,id_hino:500},{idverse:27079,verse:`1. 
O que serve a Deus de cora\xE7\xE3o vence o mal;
Quem vencer ter\xE1 um galard\xE3o eternal;
\xD3 n\xE3o queiras mais desanimar, quando vier a prova\xE7\xE3o;
Vencer\xE1, quem puro conservar seu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:501},{idverse:27080,verse:`Com todo o fervor,
contra o tentador,
Vencer\xE1 ent\xE3o, co\`o bom Capit\xE3o!
`,verseorder:2,ismainverse:1,id_hino:501},{idverse:27081,verse:`2. 
Nada poder\xE1 a f\xE9 tirar, que d\xE1 Deus;
Pois seu povo sempre vai guiar para os c\xE9us;
Quem a Ele tudo confessar, nunca lutar\xE1 em v\xE3o,
Vencer\xE1, quem puro consevar seu cora\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:501},{idverse:27082,verse:`3. 
Eis que as armas para combater v\xEAm dos c\xE9us,
S\xE3o as armas d\`eficaz poder do bom Deus;
Com a espada pode pelejar e vencer a tenta\xE7\xE3o,
Vencer\xE1, quem puro conservar seu cora\xE7\xE3o.`,verseorder:4,ismainverse:0,id_hino:501},{idverse:27083,verse:`1. 
\xD3 Pastor bendito, escuta
De Teus filhos a ora\xE7\xE3o,
Cordeirinhos que, na luta,
D\xE3o a Ti seu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:502},{idverse:27084,verse:`2. 
Cristo Rei, meu soberano,
Faze que ao repousar
Te contemple em brando sono,
E Te busque ao despertar.
`,verseorder:2,ismainverse:0,id_hino:502},{idverse:27085,verse:`3. 
Gra\xE7as mil, Pastor amado,
Hoje Teus cordeiros d\xE3o,
Pois Tuas m\xE3os, oh! nos t\xEAm dado
Que vestir e nosso p\xE3o.
`,verseorder:3,ismainverse:0,id_hino:502},{idverse:27086,verse:`4. 
Pequeninos, Te buscamos,
Esta noite em ora\xE7\xE3o,
Reverentes esperamos,
De  manh\xE3 u\`a nova un\xE7\xE3o. \xC1m\xE9m.`,verseorder:4,ismainverse:0,id_hino:502},{idverse:27087,verse:`1. 
Cristo busca j\xE1 puros,
Que proclamem sempre o perd\xE3o da cruz,
Que aos transgressores mostre os bens futuros,
P\`ra voltarem do pecado para luz!
`,verseorder:1,ismainverse:0,id_hino:503},{idverse:27088,verse:`Ide trabalhar al\xE9m nos campos
do Senhor,
Que para a sega se apresentam brancos j\xE1;
Depressa ao labor, ordena Jeov\xE1,
Desfrutareis, ent\xE3o, Seu grande amor!
`,verseorder:2,ismainverse:1,id_hino:503},{idverse:27089,verse:`2. 
L\xE1bios puros Cristo necessita,
Que com gozo falem desta salva\xE7\xE3o;
L\xEDnguas cheias duma gra\xE7a infinita,
Que proclamem aos cativos, reden\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:503},{idverse:27090,verse:`3. 
Cristo busca m\xE3os, mas consagradas,
Para trabalhar com santa prontid\xE3o;
Pedem vosso aux\xEDlio messes j\xE1 douradas,
Os trigais maduros breve cair\xE3o!
`,verseorder:4,ismainverse:0,id_hino:503},{idverse:27091,verse:`4. 
Vidas santas Cristo necessita,
Que aos pecadores mostrem seu poder,
Em Jesus firmadas e tamb\xE9m contritas,
Para que por elas, fa\xE7a Seu querer.`,verseorder:5,ismainverse:0,id_hino:503},{idverse:27092,verse:`1. 
\xC9 solene o momento;
J\xE1 expirando o ano est\xE1;
Presto como o pensamento,
O que resta passar\xE1.
`,verseorder:1,ismainverse:0,id_hino:504},{idverse:27093,verse:`2. 
D\xE9bil sopro \xE9 a exist\xEAncia,
Breve, ef\xEAmera qual flor;
Pois somente tem a ci\xEAncia,
Quem ao tempo d\xE1 valor.
`,verseorder:2,ismainverse:0,id_hino:504},{idverse:27094,verse:`3. 
Nome, fama, imp\xE9rio, gl\xF3ria...
Nada humano fica em p\xE9;
Pois s\xF3 firme \xE9 a vit\xF3ria,
Que se alcan\xE7a pela f\xE9.
`,verseorder:3,ismainverse:0,id_hino:504},{idverse:27095,verse:`4. 
N\xE9scio o homem que atr\xE1s corre
P\`ra alcan\xE7ar bens sob os c\xE9us;
Ai do m\xEDsero que morre,
Sem achar a paz de Deus!
`,verseorder:4,ismainverse:0,id_hino:504},{idverse:27096,verse:`5. 
A insond\xE1vel eternidade
\xC9 bendita por Jesus,
Que me d\xE1 imortalidade,
Gozo eterno, plena luz!`,verseorder:5,ismainverse:0,id_hino:504},{idverse:27097,verse:`1. 
Anelo eu ouvir a voz do meu bom Salvador,
Seus ditos um banquete s\xE3o, banquete de amor
Qual o man\xE1 celestial, vigor me d\xE3o tamb\xE9m,
Me trazem sempre instru\xE7\xE3o do Mestre de Bel\xE9m.
`,verseorder:1,ismainverse:0,id_hino:505},{idverse:27098,verse:`As palavras do
Senhor,
S\xE3o palavras de vigor,
Me ensinam, me guiam,
Me trazem a luz, as palavras de Jesus.
`,verseorder:2,ismainverse:1,id_hino:505},{idverse:27099,verse:`2. 
S\xF3 as palavras de Jesus infundem-me valor,
Afastam do meu cora\xE7\xE3o os males e a dor,
A mansid\xE3o do Salvador me fazem conhecer,
O gozo divinal do c\xE9u me trazem ao meu ser.
`,verseorder:3,ismainverse:0,id_hino:505},{idverse:27100,verse:`3. 
Em todo tempo meu Jesus, palavras me dir\xE1,
Nas provas elas s\xE3o meu bem, sua voz me alentar\xE1,
Minh\`alma nelas tem a paz, quem pode me turbar,
Jesus me d\xE1 real festim me chama p\`ra crer.
`,verseorder:4,ismainverse:0,id_hino:505},{idverse:27101,verse:`4. 
Lugar n\xE3o h\xE1, nem haver\xE1 no mundo de horror,
Em que o perdido tenha paz a n\xE3o ser no Senhor
Pois, as palavras que nos diz, s\xE3o fonte de poder,
P\`ra todo pobre pecador, que humilhado crer.`,verseorder:5,ismainverse:0,id_hino:505},{idverse:27102,verse:`1. 
B\xEDblia Sagrada, de Deus mandada;
Celeste tocha, de vera luz,
Torna minh\`alma cheia de calma,
Pois nele ou\xE7o a voz de Jesus!
`,verseorder:1,ismainverse:0,id_hino:506},{idverse:27103,verse:`2. 
B\xEDblia gloriosa, j\xF3ia preciosa,
\xC9 meu tesouro do cora\xE7\xE3o,
Que me enriquece, me fortalece;
Mostra-me em Cristo a salva\xE7\xE3o!
`,verseorder:2,ismainverse:0,id_hino:506},{idverse:27104,verse:`3. 
B\xEDblia bendita, gra\xE7a infinita,
De Deus revela, o Seu poder!
Dom verdadeiro ao mundo inteiro,
Dela obtenho sumo saber!`,verseorder:3,ismainverse:0,id_hino:506},{idverse:27105,verse:`1. 
O Salvador te quer salvar,
\xD3 pecador, vem sem tardar;
Recebe no teu cora\xE7\xE3o
A gloriosa salva\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:507},{idverse:27106,verse:`Jesus salva, Jesus salva,
Jesus salva o pecador.
Ele te ama, hoje te chama,
Com Sua voz de amor;
Jesus salva, Jesus salva,
Jesus salva, o pecador.
Vem tu agora, vem sem demora,
Te entregar ao Senhor.
`,verseorder:2,ismainverse:1,id_hino:507},{idverse:27107,verse:`2. 
O Salvador te chama, vem,
Recebe logo o sumo bem;
A salva\xE7\xE3o, o dom de Deus,
A Jesus Cristo, o Rei dos c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:507},{idverse:27108,verse:`3. 
O Salvador, chorando est\xE1,
Por que n\xE3o te entregas j\xE1?
Vem hoje mesmo a Jesus.
Vem receber celeste luz!`,verseorder:4,ismainverse:0,id_hino:507},{idverse:27109,verse:`1. 
Justa, expl\xEDcita e mui pura
\xC9 a lei do meu Senhor;
Para os tristes d\xE1 ventura,
Libertando-os do temor.
`,verseorder:1,ismainverse:0,id_hino:508},{idverse:27110,verse:`2. 
Sua Palavra revelada
\xC9 caudal de gran poder.
Donde a alma atribulada,
Pode a seu sabor beber.
`,verseorder:2,ismainverse:0,id_hino:508},{idverse:27111,verse:`3. 
Olha para o c\xE9u t\xE3o belo
Onde est\xE1 teu Redentor;
Para sempre cesse o duelo;
Faze a paz com teu Senhor.
`,verseorder:3,ismainverse:0,id_hino:508},{idverse:27112,verse:`4. 
Clama ao Salvador, e a alma
Lhe entrega, \xF3 pecador!
Ele paz dar\xE1 e calma,
Dando fim \xE0 tua dor!`,verseorder:4,ismainverse:0,id_hino:508},{idverse:27113,verse:`1. 
Quando o Jord\xE3o passarmos unidos,
E entrarmos no c\xE9u, veremos l\xE1,
Como areia da praia os remidos,
Oh! Que gloriosa vista ser\xE1!
`,verseorder:1,ismainverse:0,id_hino:509},{idverse:27114,verse:`Tantos como a areia da praia!
Tantos como a areia do mar!
Que gozo sentir\xE1
Todo o salvo pois ver\xE1,
Sim, tantos como a areia da praia!
`,verseorder:2,ismainverse:1,id_hino:509},{idverse:27115,verse:`2. 
Quando virmos os salvos do mundo,
Que a morte jamais alcan\xE7ar\xE1,
Se saudarem com gozo profundo,
Oh! Que gloriosa vista ser\xE1!
`,verseorder:3,ismainverse:0,id_hino:509},{idverse:27116,verse:`3. 
L\xE1 na margem do rio da vida,
Onde paz e justi\xE7a haver\xE1,
N\xF3s veremos a terra prometida;
Oh! Que gloriosa vista ser\xE1!
`,verseorder:4,ismainverse:0,id_hino:509},{idverse:27117,verse:`4. 
Quando Cristo Jesus contemplarmos,
Coroado no c\xE9u como est\xE1,
E prostrado aos Seus p\xE9s adorarmos,
Oh! Que gloriosa vista ser\xE1!`,verseorder:5,ismainverse:0,id_hino:509},{idverse:27118,verse:`1. 
Um dom real Deus despertou
Nos seus fi\xE9is - Dom de curar!
Toda a doen\xE7a Deus sarou,
E sara ainda e vai sarar;
Por Jesus, por Jesus,
Oh! N\xE3o falhou, nem vai falhar!
`,verseorder:1,ismainverse:0,id_hino:510},{idverse:27119,verse:`Oh! Que riqueza divinal,
Eu gozo j\xE1 por f\xE9 e luz,
Por vis\xE3o triunfal,
Mais gozarei com meu Jesus!
`,verseorder:2,ismainverse:1,id_hino:510},{idverse:27120,verse:`2. 
\xC9s cego? Cr\xEA, que tu ver\xE1s,
\xC9s mudo? Cr\xEA, que vais falar;
\xC9s surdo? Cr\xEA que ouvir\xE1s,
\xC9s coxo? Cr\xEA, que vais andar;
F\xE9 em Deus, f\xE9 em Deus,
Cr\xEA que Jesus te vai curar!
`,verseorder:3,ismainverse:0,id_hino:510},{idverse:27121,verse:`3. 
De que sofreis? Dos rins, pulm\xF5es?
De febre, gripe, ou cora\xE7\xE3o?
De tosse, nervos, ou les\xF5es?
De pele, dentes, deflux\xE3o?
Sarareis, sarareis,
Pelo poder da ora\xE7\xE3o!
`,verseorder:4,ismainverse:0,id_hino:510},{idverse:27122,verse:`4. 
Deixai as capas e vereis,
As maravilhas do Senhor!
Tirai a pedra e gozareis
As grandes b\xEAn\xE7\xE3os do amor!
F\xE9 em Deus, f\xE9 em Deus,
A quem rendemos o louvor!
`,verseorder:5,ismainverse:0,id_hino:510},{idverse:27123,verse:`5. 
quando a doen\xE7a a n\xF3s vem ter,
Em Deus devemos confiar,
Pois Jesus Cristo tem poder,
P\`ra num momento nos curar;
Gl\xF3ria a Deus, gl\xF3ria a Deus.
Que \xE9 poderoso p\`ra sarar.`,verseorder:6,ismainverse:0,id_hino:510},{idverse:27124,verse:`1. 
Glorioso Deus, que est\xE1 no c\xE9u,
Conselheiro \xE9 contra o rev\xE9s,
Se aqui trevas h\xE1, m\`iluminar\xE1;
Na tribula\xE7\xE3o, nEle os crentes firmados est\xE3o.
`,verseorder:1,ismainverse:0,id_hino:511},{idverse:27125,verse:`Oh! Qu\xE3o bom \xE9
nosso Deus!
Tudo preparou p\`ra os Seus,
Que por Ele, chegar\xE3o,
Satisfeitos \xE0 Sua mans\xE3o.
`,verseorder:2,ismainverse:1,id_hino:511},{idverse:27126,verse:`2. 
Vindo a morte da golpe em meu lar,
Ent\xE3o, eu bem sei, onde acharei,
Paz que satisfaz, pois Jesus me traz
O que me \xE9 mister e o que me possa aqui conter.
`,verseorder:3,ismainverse:0,id_hino:511},{idverse:27127,verse:`3. 
Longe do meu lar, sinto eu pesar,
Mas o bom Jesus d\xE1-me Sua luz.
E minh\`alma diz: "St\xE1s em mim feliz,
\`Stou contigo aqui", estar\xE1s, sim, comigo ali.
`,verseorder:4,ismainverse:0,id_hino:511},{idverse:27128,verse:`4. 
Peregrino sou, mas em breve estarei
Onde ficarei sempre com Sua grei;
L\xE1 hei de gozar Seu amor sem par;
Grato cantarei aleluias ao grande Rei.`,verseorder:5,ismainverse:0,id_hino:511},{idverse:27129,verse:`1. 
O santo amor de Cristo, que n\xE3o ter\xE1 igual;
A Sua vera gra\xE7a, sublime e eternal,
E a miseric\xF3rdia imensa como o mar,
A qual ao c\xE9u atinge, com gozo, hei de cantar.
`,verseorder:1,ismainverse:0,id_hino:512},{idverse:27130,verse:`Como \xE9 inesgot\xE1vel
O amor de meu Jesus!
Rico e inef\xE1vel; nada \xE9 compar\xE1vel
Ao amor de meu Jesus!
`,verseorder:2,ismainverse:1,id_hino:512},{idverse:27131,verse:`2. 
Jesus andou no mundo, e o povo O procurou,
E todas as ang\xFAstias, sim, aos Seus p\xE9s deixou;
E Seu amor brotava, qual rio divinal,
Pujante, forte, imenso, sanando todo o mal.
`,verseorder:3,ismainverse:0,id_hino:512},{idverse:27132,verse:`3. 
Tamb\xE9m, nos olhos cegos p\xF4s uma nova luz,
A luz que  nos d\xE1 vida, que j\xE1 brilhou na cruz;
E deu tamb\xE9m \xE0s almas, a gl\xF3ria de Seu ser,
Ao implantar Sua gra\xE7a, e Seu real poder.
`,verseorder:4,ismainverse:0,id_hino:512},{idverse:27133,verse:`4. 
O amor de Jesus Cristo, no mundo, \xE9 um fanal,
Que marca vitorioso a senda do ideal;
Embora passem os anos, \xE9 sempre eficaz,
Precioso \xE9 dar \xE0 alma incompar\xE1vel paz.`,verseorder:5,ismainverse:0,id_hino:512},{idverse:27134,verse:`1. 
Vem, \xF3 meu bom Jesus, me consolar;
Vem, leva-me daqui, p\`ra contigo estar.
`,verseorder:1,ismainverse:0,id_hino:513},{idverse:27135,verse:`Minh\`alma Te quer, vem \xF3
meu Jesus sem par!
Vem, leva-me daqui, p\`ra contigo estar.
`,verseorder:2,ismainverse:1,id_hino:513},{idverse:27136,verse:`2. 
Que sofrimento atroz eu sinto aqui.
Longe da p\xE1tria al\xE9m, choro eu por Ti.
`,verseorder:3,ismainverse:0,id_hino:513},{idverse:27137,verse:`3. 
Quero descanso ter ao p\xE9 de Ti.
Desejo s\xF3 partir deste mundo aqui.
`,verseorder:4,ismainverse:0,id_hino:513},{idverse:27138,verse:`4. 
Nada me satisfaz longe do lar,
Que preparaste ali, para eu morar.
`,verseorder:5,ismainverse:0,id_hino:513},{idverse:27139,verse:`5. 
Que gl\xF3ria, que prazer eu sentirei,
Quando eu daqui, partir, com o eterno Rei.
`,verseorder:6,ismainverse:0,id_hino:513},{idverse:27140,verse:`6. 
Vem, ora vem, Senhor, \xF3 Rei dos c\xE9us!
Vem j\xE1 me libertar, para estar com Deus.`,verseorder:7,ismainverse:0,id_hino:513},{idverse:27141,verse:`1. 
Em gl\xF3ria esplendente, na nuvem luzente,
Ao mundo vir\xE1, outra vez, o  Senhor;
O santo Cordeiro, que l\xE1 no madeiro,
Verteu o Seu sangue purificador.
`,verseorder:1,ismainverse:0,id_hino:514},{idverse:27142,verse:`Se Cristo agora voltar, tu\`alma
ir\xE1 jubilar?
Tens l\xE2mpada pronta e as vestes tamb\xE9m,
P\`ra ires com Cristo al\xE9m?
N\xE3o mais te demores, mas vem a Jesus.
A fim de ascender tua luz.
`,verseorder:2,ismainverse:1,id_hino:514},{idverse:27143,verse:`2. 
Come\xE7ando a ira, perece a mentira,
No dia da vinda de Cristo, o Senhor;
O mundo, enganado, no v\xEDcio e pecado,
N\xE3o vai tomar parte nas bodas de amor.
`,verseorder:3,ismainverse:0,id_hino:514},{idverse:27144,verse:`3. 
A gra\xE7a perfeita, n\xE3o temendo, aceita,
Pois breve Jesus h\xE1 de vir, l\xE1 do c\xE9u;
\xD3 veste tu\`alma, com gra\xE7a e com calma.
De dia e de noite, orando ao teu Deus.`,verseorder:4,ismainverse:0,id_hino:514},{idverse:27145,verse:`1. 
Se pelos vales, eu peregrino vou andar
Ou na luz gloriosa de Cristo habitar,
Irei com meu Senhor p\`ra onde Ele for.
Confiando na gra\xE7a de meu Salvador.
`,verseorder:1,ismainverse:0,id_hino:515},{idverse:27146,verse:`Se Cristo comigo vai, eu irei
E n\xE3o temerei, com gozo irei; comigo vai;
\xC9 grato servir a Jesus, levar a cruz;
Se Cristo comigo vai, eu irei.
`,verseorder:2,ismainverse:1,id_hino:515},{idverse:27147,verse:`2. 
Se l\xE1 para o deserto Jesus me quer mandar;
Levando boas novas de salva\xE7\xE3o sem par;
Eu lidarei, ent\xE3o, com paz no cora\xE7\xE3o.
A Cristo seguindo, sem mais dila\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:515},{idverse:27148,verse:`3. 
Ser\xE1 a minha sorte a dura cruz levar,
Sua gra\xE7a e Seu poder,
quero sempre aqui contar.
Contente com Jesus, levando a minha cruz.
Eu falo de Cristo que \xE9 minha luz.
`,verseorder:4,ismainverse:0,id_hino:515},{idverse:27149,verse:`4. 
Ao Salvador Jesus eu desejo obedecer,
Pois na Sua Palavra encontro o meu saber;
Fiel a Deus serei, o mundo vencerei,
Jesus vai comigo, n\xE3o mais temerei.`,verseorder:5,ismainverse:0,id_hino:515},{idverse:27150,verse:`1. 
Antes eu n\xE3o conhecia a Jesus o salvador;
Pois vivia em pecados e temor;
Mas achei que minhas culpas, sobre Si Jesus levou
E meu gozo nunca mais aqui cessou.
`,verseorder:1,ismainverse:0,id_hino:516},{idverse:27151,verse:`\xC9 Jesus Cristo meu vero amigo,
Que minhas culpas perdoou.
\xC9 Jesus Cristo meu vero amigo,
Pois a minh\`alma libertou.
`,verseorder:2,ismainverse:1,id_hino:516},{idverse:27152,verse:`2. 
Antes, eu n\xE3o conhecia essa purifica\xE7\xE3o.
E o poder que desejava o cora\xE7\xE3o.
Mas vigor das promessas a minh\`alma transformou
E Jesus \xE1 fonte viva me levou.
`,verseorder:3,ismainverse:0,id_hino:516},{idverse:27153,verse:`3. 
Pronto estou p\`ra ir com Cristo a mensagem proclamar,
Pelas vinhas onde vou a trabalhar;
Bem eu sei que \xE9 poderoso;
Sua gra\xE7a me dar\xE1; E vit\xF3ria plena me conceder\xE1.`,verseorder:4,ismainverse:0,id_hino:516},{idverse:27154,verse:`1. 
Cristo Jesus hoje vai te curar,
Se podes crer, se podes crer.
Pois tem virtude e gra\xE7a sem par,
P\`ra curar teu ser!
`,verseorder:1,ismainverse:0,id_hino:517},{idverse:27155,verse:`Vem, Vem, para te curar,
Cr\xEA, cr\xEA, que h\xE1s de ver;
Cura divina se manifestar
No teu fraco ser!
`,verseorder:2,ismainverse:1,id_hino:517},{idverse:27156,verse:`2. 
Crendo em Cristo ir\xE1s receber.
Cura do c\xE9u, cura do c\xE9u;
Pois Ele tem \`inda o mesmo poder,
O poder de Deus!
`,verseorder:3,ismainverse:0,id_hino:517},{idverse:27157,verse:`3. 
Mui poderoso \xE9 Cristo Jesus,
P\`ra te curar, p\`ra curar.
Todas as doen\xE7as cravou sobre a cruz,
Para te sarar!
`,verseorder:4,ismainverse:0,id_hino:517},{idverse:27158,verse:`4. 
"N\xE3o te hei dito: Se creres em Mim,
Que h\xE1s de ver, que h\xE1s de ver;
Cura divina manando enfim,
Se puderes crer!"`,verseorder:5,ismainverse:0,id_hino:517},{idverse:27159,verse:`1. 
Por seus pecados, \`st\xE1 o pecador
Qual pobre n\xE1ufrago sem Salvador;
Que triste sorte, perece no mar,
Nada, se cansa, e vai afundar.
`,verseorder:1,ismainverse:0,id_hino:518},{idverse:27160,verse:`Orando, joga ao que se afoga,
O salva-vidas sem demorar;
Antes que des\xE7a - Desapare\xE7a
Nas profundezas do mar.
`,verseorder:2,ismainverse:1,id_hino:518},{idverse:27161,verse:`2. 
O salva-vidas n\xE3o queres lan\xE7ar
Ao que \`s afoga nas ondas do mar?
Que morte horr\xEDvel tem o pecador,
Que n\xE3o aceita o bom Salvador!
`,verseorder:3,ismainverse:0,id_hino:518},{idverse:27162,verse:`3. 
\xC9 Jesus Cristo, o Filho de Deus;
O salva-vidas lan\xE7ado dos c\xE9us;
Cristo te pode salvar hoje at\xE9,
O salva-vidas segura com f\xE9.`,verseorder:4,ismainverse:0,id_hino:518},{idverse:27163,verse:`1. 
Achei Jesus, o meu Salvador,
De amor fiel, veraz;
N\xE3o cessarei de Lhe dar louvor,
Pois tenho a salva\xE7\xE3o e paz.
`,verseorder:1,ismainverse:0,id_hino:519},{idverse:27164,verse:`Salvo por Cristo sou,
Salvo por quem me amou!
Perfeita paz, Ele agora me traz,
Porque salvo estou!
`,verseorder:2,ismainverse:1,id_hino:519},{idverse:27165,verse:`2. 
Vem me guardar Sua forte m\xE3o,
De todo o v\xEDcio e mal;
Vencendo vou cada tenta\xE7\xE3o;
Sust\xE9m-me o bra\xE7o divinal.
`,verseorder:3,ismainverse:0,id_hino:519},{idverse:27166,verse:`3. 
Jesus me achou, t\xE3o aflito e mal,
Sem ter consola\xE7\xE3o;
Chamou-me a Si, com amor real;
Cheguei-me e achei a salva\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:519},{idverse:27167,verse:`4. 
Da morte eterna me resgatou,
Da dura escravid\xE3o;
Na rocha eterna seguro estou;
Cantando vou, com gratid\xE3o.`,verseorder:5,ismainverse:0,id_hino:519},{idverse:27168,verse:`1. 
Eu me achei num estado de tristeza e dor,
J\xE1 prevendo da vida o fim;
Hoje, exulto ao lembrar-me da hora feliz,
Em que Cristo olhou para mim.
`,verseorder:1,ismainverse:0,id_hino:520},{idverse:27169,verse:`Era s\xF3 Sua doce e preciosa voz
Que eu sempre ouvia soar,
Ent\xE3o, recebi a esperan\xE7a,
Que Jesus me havia de curar.
`,verseorder:2,ismainverse:1,id_hino:520},{idverse:27170,verse:`2. 
Hoje, eu bem alegre testifico a v\xF3s,
Que Jesus, o meu bom Salvador,
\xC9 o meu defensor, o meu guia e luz,
Meu conforto, na luta, na dor.
`,verseorder:3,ismainverse:0,id_hino:520},{idverse:27171,verse:`3. 
Oh! Que dia ditoso e belo p\`ra mim,
Em que ouvi a voz de meu Senhor,
Exortando-me a nEle eu bem confiar,
Pois curou-me, por Seu grande amor!`,verseorder:4,ismainverse:0,id_hino:520},{idverse:27172,verse:`1. 
Com teu pecado vem j\xE1 p\`ra Jesus,
Ele te quer perdoar,
Deixa as trevas, vem j\xE1 para a luz;
Vem tua vida salvar.
`,verseorder:1,ismainverse:0,id_hino:521},{idverse:27173,verse:`Vem! Vem! Vem o passo dar!
Vem teu cora\xE7\xE3o entregar;
Vem j\xE1 dizer: "Jesus me salvou,
Do meu pecado limpo estou".
`,verseorder:2,ismainverse:1,id_hino:521},{idverse:27174,verse:`2. 
Contra o pecado outra fonte n\xE3o h\xE1,
S\xF3 Jesus pode limpar;
Dos teus temores e culpas, vem j\xE1
NEle te refugiar.
`,verseorder:3,ismainverse:0,id_hino:521},{idverse:27175,verse:`3. 
Oh! Que oferta por ti fez Jesus,
Para te purificar!
Deu o Seu sangue no alto da cruz;
Vem tua alma lavar!
`,verseorder:4,ismainverse:0,id_hino:521},{idverse:27176,verse:`4. 
Vem dar o passo, sem hesita\xE7\xE3o,
Jesus te que pedoar;
Agora mesmo toma a salva\xE7\xE3o
Vem tua vida salvar.`,verseorder:5,ismainverse:0,id_hino:521},{idverse:27177,verse:`1. 
O Rei da gl\xF3ria, quem ser\xE1,
Senhor onipotente?
S\xF3 Deus, o Filho, o poder ser,
Que reina eternamente.
As chagas que Ele recebeu,
A morte que na cruz sofreu,
O provam cabalmente!
`,verseorder:1,ismainverse:0,id_hino:522},{idverse:27178,verse:`2. 
A morte que Ele \xE0 morte deu,
Ganhando a vit\xF3ria,
A sepultura que rompeu,
Subindo \xE0 excelsa gl\xF3ria,
Declaram Seu real poder,
E ao mundo d\xE3o a conhecer,
Que Cristo \xE9 o Rei da gl\xF3ria!
`,verseorder:2,ismainverse:0,id_hino:522},{idverse:27179,verse:`3. 
Eis, elevado l\xE1 no c\xE9u,
Jesus, o rei da gl\xF3ria,
Ali reinando al\xE9m do v\xE9u,
Com Deus, na eterna gl\xF3ria,
Onde remida multid\xE3o,
Num \xEAxtase de adora\xE7\xE3o,
D\xE1 a Jesus a gl\xF3ria!
`,verseorder:3,ismainverse:0,id_hino:522},{idverse:27180,verse:`4. 
Oh! Qu\xE3o feliz quem nEle crer,
Pois v\xEA-Lo-\xE1 na gl\xF3ria!
Sim, todo aquel\`que renascer
Tem o penhor da gl\xF3ria!
Confessa ser um pecador.
Confia s\xF3 no Salvador,
Jesus, o Rei da gl\xF3ria!`,verseorder:4,ismainverse:0,id_hino:522},{idverse:27181,verse:`1. 
Cristo nos deu uma fonte escondida.
Que qualquer sede pode estancar.
E cujas \xE1guas repletas de vida,
Sempre de gra\xE7a nos h\xE3o de fartar.
`,verseorder:1,ismainverse:0,id_hino:523},{idverse:27182,verse:`\xC1gua sair\xE1, viva manar\xE1
Da fonte que abriu o Senhor, \xE1gua correr\xE1!
Se todos quiserem beber, n\xE3o se esgotar\xE1;
Milhares de milhares venham; sempre sair\xE1!
`,verseorder:2,ismainverse:1,id_hino:523},{idverse:27183,verse:`2. 
H\xE1 muitas fontes de gozo no mundo,
Nelas os homens v\xE3o sempre beber,
Mas s\xE3o cisternas e po\xE7os imundos,
Que \xE1guas vivas n\xE3o podem conter.
`,verseorder:3,ismainverse:0,id_hino:523},{idverse:27184,verse:`3. 
J\xE1 muitos homens gastaram dinheiro,
Sem a fonte de Deus encontrar;
Mas Jesus Cristo \xE9 o nosso roteiro,
Que para a fonte, nos h\xE1 de guiar.
`,verseorder:4,ismainverse:0,id_hino:523},{idverse:27185,verse:`4. 
Oh, Aleluia ao bom Pai supremo,
Pelas b\xEAn\xE7\xE3os que a todos quer dar,
E pela fonte de paz que n\xF3s temos,
Em nosso ser, para nos alegrar!`,verseorder:5,ismainverse:0,id_hino:523},{idverse:27186,verse:`1. 
Atrav\xE9s da tempestade
Em qualquer calamidade,
Me consola esta verdade:
Cristo pensa em mim.
`,verseorder:1,ismainverse:0,id_hino:524},{idverse:27187,verse:`Pensa em mim, Jesus
L\xE1 na excelsa luz;
J\xE1 provaste a cruz,
Com amor sem fim;
Satisfeito estou,
Sem medo vou;
Cristo pensa em mim.
Cristo pensa em mim.
`,verseorder:2,ismainverse:1,id_hino:524},{idverse:27188,verse:`2. 
Quando conforto eu desejo,
For\xE7a, ousadia almejo,
Nisto, pois, eu tudo vejo:
Cristo pensa em mim.
`,verseorder:3,ismainverse:0,id_hino:524},{idverse:27189,verse:`3. 
Se eu receio o inimigo,
Tendo temor do perigo,
Em Jesus encontro abrigo:
Cristo pensa em mim.
`,verseorder:4,ismainverse:0,id_hino:524},{idverse:27190,verse:`4. 
Caso eu v\xE1 ausentar-me,
Dos meus irm\xE3os separar-me,
Nisto hei de consolar-me:
Cristo pensa em mim.`,verseorder:5,ismainverse:0,id_hino:524},{idverse:27191,verse:`1. 
J\xE1 refulge a gl\xF3ria eterna
De Jesus, o Rei dos reis;
Breve os reinos deste mundo
Seguir\xE3o as suas leis!
Os sinais da sua vinda
Mais se mostram cada vez:
Vencendo vem Jesus!
`,verseorder:1,ismainverse:0,id_hino:525},{idverse:27192,verse:`Gl\xF3ria, gl\xF3ria! Aleluia!
Gl\xF3ria, gl\xF3ria! Aleluia!
Gl\xF3ria, gl\xF3ria! Aleluia!
Vencendo vem Jesus!
`,verseorder:2,ismainverse:1,id_hino:525},{idverse:27193,verse:`2. 
O clarim que chama os crentes
\xC0 batalha, j\xE1 soou;
Cristo, \xE0 frente do seu povo,
Multid\xF5es j\xE1 conquistou.
O inimigo, em retirada,
Seu furor j\xE1 demonstrou:
Vencendo vem Jesus!
`,verseorder:3,ismainverse:0,id_hino:525},{idverse:27194,verse:`3. 
Eis que em gl\xF3ria refulgente
Sobre as nuvens descer\xE1,
Eas na\xE7\xF5es e os reis da terra
Com poder governar\xE1.
Sim, em paz e santidade
Toda a terra reger\xE1.
Vencendo vem Jesus!
`,verseorder:4,ismainverse:0,id_hino:525},{idverse:27195,verse:`4. 
E por fim entronizado
As na\xE7\xF5es h\xE1 de julgar:
Todos, grandes e pequenos,
O juiz h\xE3o de encarar.
E os remidos triunfantes,
L\xE1 no c\xE9u ir\xE3o cantar
"Vencendo Vem Jesus!"
`,verseorder:5,ismainverse:0,id_hino:525},{idverse:27196,verse:`5. 
Gl\xF3ria, gl\xF3ria! Aleluia!
Gl\xF3ria, gl\xF3ria! Aleluia!
Gl\xF3ria, gl\xF3ria! Aleluia!
Venceu o Rei Jesus!`,verseorder:6,ismainverse:0,id_hino:525},{idverse:27197,verse:`1. 
Senhor, meu Deus, quando eu, maravilhado,
Contemplo a tua imensa cria\xE7\xE3o,
A terra e o  mar e o c\xE9u todo estrelado
Me v\xEAm falar da tua perfei\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:526},{idverse:27198,verse:`Ent\xE3o minh\`alma canta a ti,
Senhor:
- "Grandioso \xE9s Tu! - grandioso \xE9s Tu!"
Ent\xE3o minh\`alma canta a ti, Senhor:
- "Grandioso \xE9s Tu! - grandioso \xE9s Tu!"
`,verseorder:2,ismainverse:1,id_hino:526},{idverse:27199,verse:`2. 
Quando as estrelas, t\xE3o de mim distantes,
Vejo a brilhar com v\xEDvido esplendor,
Relembro, oh! Deus, as gl\xF3rias cintilantes
Que meu Jesus deixou, por meu amor!
`,verseorder:3,ismainverse:0,id_hino:526},{idverse:27200,verse:`3. 
Olho as florestas murmurando ao vento
E, ao ver que Tu plantaste cada p\xE9,
Recordo a cruz, o lenho t\xE3o cruento,
E no teu Filho afirmo a  minha f\xE9.
`,verseorder:4,ismainverse:0,id_hino:526},{idverse:27201,verse:`4. 
E quando penso que Tu n\xE3o poupaste
Teu Filho amado por amor de mim,
Meu Cora\xE7\xE3o, que nele Tu ganhaste,
Transborda, oh! Pai, de amor que n\xE3o tem fim!
`,verseorder:5,ismainverse:0,id_hino:526},{idverse:27202,verse:`5. 
E quando Cristo, o amado meu, voltando,
Vier dos c\xE9us o povo seu buscar,
No lar eterno quero, jubilando,
A tua santa face contemplar.`,verseorder:6,ismainverse:0,id_hino:526},{idverse:27203,verse:`1. 
Dominador \xE9 Deus sem par,
Pois, com potente m\xE3o,
A tempestade faz cessar,
E reina paz, ent\xE3o.
`,verseorder:1,ismainverse:0,id_hino:527},{idverse:27204,verse:`2. 
Os c\xE9us e terra Ele criou;
Seres e mananciais.
Sua vontade Ele ditou,
E vida a todos traz.
`,verseorder:2,ismainverse:0,id_hino:527},{idverse:27205,verse:`3. 
Oh! santos! nele esperai!
At\xE9 em afli\xE7\xE3o;
Pois as promessas do bom Pai
Jamais nos falhar\xE3o.
`,verseorder:3,ismainverse:0,id_hino:527},{idverse:27206,verse:`4. 
Oh! Jamais duvideis de Deus!
Devemos confiar.
Ele aben\xE7oa os filhos seus,
Por muito os amar.`,verseorder:4,ismainverse:0,id_hino:527},{idverse:27207,verse:`1. 
Eu te amo, \xF3 Deus, n\xE3o s\xF3 porque
Almejo o C\xE9u fruir,
Nem s\xF3 porque eu temo aqui
Na morte sucumbir.
`,verseorder:1,ismainverse:0,id_hino:528},{idverse:27208,verse:`2. 
Eu te amo, \xF3 Deus, porque na cruz
Provaste o teu amor:
Jesus desceu e em meu lugar
Morreu, sofrendo a dor.
`,verseorder:2,ismainverse:0,id_hino:528},{idverse:27209,verse:`3. 
Por isso, \xF3 Deus, o meu amor
Eterno h\xE1 de ser.
Cantar no C\xE9u o teu louvor
Ser\xE1 o meu prazer.`,verseorder:3,ismainverse:0,id_hino:528},{idverse:27210,verse:`1. 
Em sua gra\xE7a, seu rico amor,
Veio do C\xE9u a Bel\xE9m meu Senhor.
Que maravilha! Meu Salvador
Por mim na cruz morreu!`,verseorder:1,ismainverse:0,id_hino:529},{idverse:27211,verse:`1. 
Vivifica a tua igreja,
\xD3 bendito Salvador!
Sem tua gra\xE7a, ela murcha
ficar\xE1, e sem vigor.
`,verseorder:1,ismainverse:0,id_hino:530},{idverse:27212,verse:`2. 
Vivifica! - vivifica
Nossas almas, oh! Senhor!
Vivifica! - vivifica
Nossas almas, oh! Senhor!`,verseorder:2,ismainverse:0,id_hino:530},{idverse:27213,verse:`1. 
Leva tu contigo o nome
De Jesus, o Salvador!
Este nome d\xE1 conforto
Hoje, sempre e onde for.
`,verseorder:1,ismainverse:0,id_hino:531},{idverse:27214,verse:`Nome bom! - doce \xE0 f\xE9
- A esperan\xE7a do porvir!
Nome bom! - doce \xE0 f\xE9
- A esperan\xE7a do porvir!
`,verseorder:2,ismainverse:1,id_hino:531},{idverse:27215,verse:`2. 
Este nome leva sempre
Para bem te defender:
Ele \xE9 a arma ao teu alcance,
Quando o mal te aparecer.
`,verseorder:3,ismainverse:0,id_hino:531},{idverse:27216,verse:`3. 
Oh! que nome precioso!
Gozo traz ao cora\xE7\xE3o!
Sendo por Jesus aceito,
Tu ter\xE1s o seu perd\xE3o.
`,verseorder:4,ismainverse:0,id_hino:531},{idverse:27217,verse:`4. 
Santo nome! ador\xE1vel!
Tem Jesus, o amado teu:
- "Rei dos reis", "Senhor eterno"
Tu o aclamar\xE1s no C\xE9u.`,verseorder:5,ismainverse:0,id_hino:531},{idverse:27218,verse:`1. 
Este nome santo,
Nome de Jesus,
Doce melodia
Em meu cora\xE7\xE3o produz,`,verseorder:1,ismainverse:0,id_hino:532},{idverse:27219,verse:`1. 
Cristo, nosso Salvador,
Adoramos-te, Senhor!
Gratos, damos-te louvor,
Honras sejam ao Cordeiro
`,verseorder:1,ismainverse:0,id_hino:533},{idverse:27220,verse:`2. 
Ele foi que padeceu,
Que por n\xF3s na cruz morreu!
E que assim por n\xF3s se deu,
Honras sejam ao Cordeiro!
`,verseorder:2,ismainverse:0,id_hino:533},{idverse:27221,verse:`3. 
Sofredor que ali penou,
Vencedor que triunfou!
Salvador que nos amou,
Honras sejam ao Cordeiro!
`,verseorder:3,ismainverse:0,id_hino:533},{idverse:27222,verse:`4. 
Digno \xE9s Tu, Senhor, que a ti
Demos nossos culto aqui!
Como os teus, nos c\xE9us, ali
Honras sejam ao Cordeiro`,verseorder:4,ismainverse:0,id_hino:533},{idverse:27223,verse:`1. 
Cristo \xE9 real pra mim,
Cristo \xE9 real pra mim.
Ele vive em meu ser;
Sua paz posso ter,
Pois Cristo \xE9 real pra mim.`,verseorder:1,ismainverse:0,id_hino:534},{idverse:27224,verse:`1. 
Tu \xE9s fiel, Senhor, meu Pai celeste:
Pleno poder aos teus filhos dar\xE1s.
Nunca mudaste: tu nunca faltaste:
Tal como eras, tu sempre ser\xE1s.
`,verseorder:1,ismainverse:0,id_hino:535},{idverse:27225,verse:`Tu \xE9s fiel, Senhor! Tu \xE9s fiel,
Senhor!
Dia ap\xF3s dia, com b\xEAn\xE7\xE3os sem fim,
Tua merc\xEA me sustenta e me guarda.
Tu \xE9s fiel, Senhor, fiel a mim.
`,verseorder:2,ismainverse:1,id_hino:535},{idverse:27226,verse:`2. 
Flores e frutos, montanhas e mares,
Sol, Lua, estrelas no c\xE9u a brilhar:
Tudo criaste na terra e nos ares.
Todo o Universo vem pois te louvar!
`,verseorder:3,ismainverse:0,id_hino:535},{idverse:27227,verse:`3. 
Pleno perd\xE3o tu d\xE1s: paz, seguran\xE7a:
Cada momento me guias, Senhor.
E, no porvir, oh! que doce esperan\xE7a:
Desfrutarei do teu rico favor!`,verseorder:4,ismainverse:0,id_hino:535},{idverse:27228,verse:`1. 
Amigo sem igual!
Amigo sem igual!
Jesus morreu, em meu lugar,
A fim de me salvar.
Amigo sem igual!
Amigo sem igual!
Seu grande amor n\xE3o mudar\xE1,
E nunca falhar\xE1.
`,verseorder:1,ismainverse:0,id_hino:536},{idverse:27229,verse:`2. 
Amigo sem igual!
Amigo sem igual!
Bem perto est\xE1 e quer salvar
Quem nele confiar.
Amigo sem igual!
Amigo sem igual!
Convida com imenso amor
A todo o pecador.`,verseorder:2,ismainverse:0,id_hino:536},{idverse:27230,verse:`1. 
Sempre comigo est\xE1 Jesus,
Seja nas trevas, seja na luz;
Ele protege-me, cuida de mim;
Eu quero amar a quem me guarda assim.`,verseorder:1,ismainverse:0,id_hino:537},{idverse:27231,verse:`1. 
Tu, Senhor, \xE9s nosso amigo,
Que vieste nos salvar,
Tu, que nos tens sempre amado,
Vives para nos guardar.`,verseorder:1,ismainverse:0,id_hino:538},{idverse:27232,verse:`1. 
Ao pensar no sofrer e na dor crucial
Que na cruz suportou por amor nosso Rei,
Pr\xF3prio eu, vida, enfim, e prazer mundanal,
Coisas v\xE3s aqui nunca mais amarei.
`,verseorder:1,ismainverse:0,id_hino:539},{idverse:27233,verse:`2. 
Destas m\xE3os, destes p\xE9s sangue esdt\xE1 a jorrar,
Manancial de poder, e de paz, e de amor.
Sempre irei nesta cruz do Senhor me gloriar,
Neste amor sem fim pelo vil pecador.
`,verseorder:2,ismainverse:0,id_hino:539},{idverse:27234,verse:`3. 
Nem do C\xE9u a extens\xE3o \xE9 capaz de conter
A amplid\xE3o sem igual e o poder deste amor.
L\xE1 no C\xE9u, pela f\xE9 neste amor e poder,
Estarei aos p\xE9s do meu bom Salvador.`,verseorder:3,ismainverse:0,id_hino:539},{idverse:27235,verse:`1. 
Ao ver morrer na rude cruz,
Em dor e ang\xFAstia meu Jesus,
Compreendo ser um pecador,
E meu orgulho sem valor.
`,verseorder:1,ismainverse:0,id_hino:540},{idverse:27236,verse:`2. 
As m\xE3os, os p\xE9s e a fronte em dor
Derramam sangue por amor;
Oh! santa m\xE3o! Ferida m\xE3o!
Deseja dar-me salva\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:540},{idverse:27237,verse:`3. 
Dou gra\xE7as mil a meu Jesus;
O amor prrovado sobre a cruz
Ir\xE1 suster e conduzir
A minha vida no porvir.`,verseorder:3,ismainverse:0,id_hino:540},{idverse:27238,verse:`1. 
No Calv\xE1rio contemplamos
Nosso meigo Salvador!
- Oh! revela\xE7\xE3o de amor! -
O mist\xE9rio h\xE1 tanto oculto,
Deus em Cristo revelou!
- Oh! revela\xE7\xE3o de amor!
`,verseorder:1,ismainverse:0,id_hino:541},{idverse:27239,verse:`Calv\xE1rio - Calv\xE1rio!
- Oh! grande dor!
Calv\xE1rio! - Calv\xE1rio!
- Oh! revela\xE7\xE3o de amor!
`,verseorder:2,ismainverse:1,id_hino:541},{idverse:27240,verse:`2. 
Deus amou a este mundo
E seu Filho ofereceu.
- Oh! revela\xE7\xE3o de amor! -
O Senhor, na cruz morrendo,
De n\xF3s se compadeceu!
- Oh! revela\xE7\xE3o de amor!
`,verseorder:3,ismainverse:0,id_hino:541},{idverse:27241,verse:`3. 
Deus, o Pai, olhou o Calv\xE1rio
E seu Filho abandonou!
- Oh! revela\xE7\xE3o de amor! -
Mas sofrendo este abandono,
Cristo deu-nos seu perd\xE3o!
- Oh! revela\xE7\xE3o de amor!`,verseorder:4,ismainverse:0,id_hino:541},{idverse:27242,verse:`1. 
Conta-me a hist\xF3ria de Cristo,
Grava-a no meu cora\xE7\xE3o,
A linda hist\xF3ria inaudita,
De gra\xE7a, paz e perd\xE3o!
Conta como Ele encarnando,
Veio no mundo morar,
E aos pecadores, indignos,
De Deus o amor revelar!
`,verseorder:1,ismainverse:0,id_hino:542},{idverse:27243,verse:`2. 
Conta como Ele, bondoso,
Nunca a ningu\xE9m rejeitou,
Como, de m\xE3os estendidas,
Todos a si convidou!
Como n\xE3o pode o bom Mestre,
Seja a quem for, recusar,
Se, convencido e contrito,
O seu convite aceitar.
`,verseorder:2,ismainverse:0,id_hino:542},{idverse:27244,verse:`3. 
Contra-me quando no monte
Sobre a cidade chorou,
pois orgulhosa e rebelde
O seu amor rejeitou!
Conta como Ele ainda chora
Sobre os que seguem o mal;
E, endurecidos, resistem
Ao seu amor divinal.
`,verseorder:3,ismainverse:0,id_hino:542},{idverse:27245,verse:`4. 
Conta tamb\xE9m como Cristo,
Mesmo na cruz, se lembrou
Dos seus cru\xE9is inimigos,
E ao Pai por eles rogou!
Como ao ladr\xE3o moribundo
T\xE3o prontamente escutou,
E, nesse dia, consigo
Para o descanso o levou!
`,verseorder:4,ismainverse:0,id_hino:542},{idverse:27246,verse:`5. 
Conta-me as duras afontas
Que mansamente sofreu:
Como, na cruz levantado,
Ele por \xEDmpios morreu!
D\xE1-me o viver na certeza
De que foi mesmo por mim!
Que seu amor inef\xE1vel
N\xE3o tem mudan\xE7a nem fim!`,verseorder:5,ismainverse:0,id_hino:542},{idverse:27247,verse:`1. 
Cristo, meu Redentor!
Cristo, meu Redentor!
Tu, que morreste, e teu sangue verteste,
\xC9s meu Salvador!`,verseorder:1,ismainverse:0,id_hino:543},{idverse:27248,verse:`1. 
Cristo Jesus, o sangue teu,
Teu grande amor e retid\xE3o
S\xE3o para mim glorioso v\xE9u
A me cobrir com teu perd\xE3o.
`,verseorder:1,ismainverse:0,id_hino:544},{idverse:27249,verse:`2. 
Creio, Senhor, na reden\xE7\xE3o
Dada na cruz ao pecador,
Que resgatou da perdi\xE7\xE3o
Meu pobre ser, por santo amor.
`,verseorder:2,ismainverse:0,id_hino:544},{idverse:27250,verse:`3. 
Creio, Senhor, que nunca mais
Mal h\xE1 de entrar no lar do Al\xE9m,
Que, pela cruz, os imortais
Desfrutar\xE3o o eterno bem.`,verseorder:3,ismainverse:0,id_hino:544},{idverse:27251,verse:`1. 
Deus enviou seu Filho amado
Pra perdoar, pra me salvar.
Na cruz morreu por meu pecado,
Mas ressurgiu e vivo com o Pai est\xE1.
`,verseorder:1,ismainverse:0,id_hino:545},{idverse:27252,verse:`Porque Ele vive, posso crer
no amanh\xE3.
Porque Ele vive, temor n\xE3o h\xE1.
Mas eu bem sei, eu sei, que a minha vida
Est\xE1 nas m\xE3os de meu Jesus, que vivo est\xE1.
`,verseorder:2,ismainverse:1,id_hino:545},{idverse:27253,verse:`2. 
E quando, enfim, chegar a hora
Em que a morte enfrentarei,
Sem medo, ent\xE3o, terei vit\xF3ria:
Irei \xE0 Gl\xF3ria, ao meu Jesus que vivo est\xE1.`,verseorder:3,ismainverse:0,id_hino:545},{idverse:27254,verse:`1. 
Quando, enfim, voltar o Salvador,
No venturoso alvor,
Nunca mais ser\xE3o a morte atroz e a dor.
`,verseorder:1,ismainverse:0,id_hino:546},{idverse:27255,verse:`2. 
Que manh\xE3! feliz ressurrei\xE7\xE3o!
Queridos l\xE1 est\xE3o!
Todos os remidos vida em Deus ter\xE3o.
`,verseorder:2,ismainverse:0,id_hino:546},{idverse:27256,verse:`3. 
Oh! prazer supremo que seduz!
Ressuscitar em luz!
Ver a paz do C\xE9u e contemplar Jesus!`,verseorder:3,ismainverse:0,id_hino:546},{idverse:27257,verse:`1. 
Os fi\xE9is s\xE3o trasladados; seu trabalho aqui findou.
A carreira desses santos, nesta vida j\xE1 cessou.
Do Senhor os bons ceifeiros, terminaram seu labor;
A colheita completou-se: \xE9 a vinda do Senhor!
`,verseorder:1,ismainverse:0,id_hino:547},{idverse:27258,verse:`O Rei est\xE1
voltando! o Rei est\xE1 voltando!
A trombeta est\xE1 soando, para os santos
trasladar.
Sim, o Rei est\xE1 voltando! o Rei est\xE1 voltando!
Aleluia! Ele vem nos buscar!
`,verseorder:2,ismainverse:1,id_hino:547},{idverse:27259,verse:`2. 
Desta Terra est\xE3o subindo os remidos para o c\xE9u,
Ao encontro do Deus Filho, que aparece al\xE9m do v\xE9u.
E o templo est\xE1 deserto; sua prega\xE7\xE3o cessou.
\xC9 not\xEDcia em toda parte: "Jesus Cristo j\xE1 voltou!"
`,verseorder:3,ismainverse:0,id_hino:547},{idverse:27260,verse:`3. 
Os remidos v\xE3o subindo: \xE9 a festa celestial:
Todo o C\xE9u est\xE1 se abrindo, num "Bem-vindo!"
Sem igual,
Qual o som de muitas \xE1guas, n\xF3s ouvimos entoar
Aleluias ao Cordeiro! -  vamos indo para o lar!`,verseorder:4,ismainverse:0,id_hino:547},{idverse:27261,verse:`1. 
Breve Jesus h\xE1 de vir l\xE1 do C\xE9u, buscar-nos para o Lar.
Devo estar pronto, ao Jesus chegar, e assim vou para
o Lar.`,verseorder:1,ismainverse:0,id_hino:548},{idverse:27262,verse:`1. 
Senhor, n\xE3o mais retardes
A luz do reino Teu,
Pois j\xE1 a vida aqui
Toda em dor se converteu.
`,verseorder:1,ismainverse:0,id_hino:549},{idverse:27263,verse:`2. 
Por Ti, Jesus, ansiosos
Est\xE3o os cora\xE7\xF5es;
Oh! vem, sem mais tardar,
Desfazer as afli\xE7\xF5es.
`,verseorder:2,ismainverse:0,id_hino:549},{idverse:27264,verse:`3. 
Com f\xE9 a Igreja aguarda
O alvor da tua luz,
E est\xE1 a suplicar:
"Ora vem, Senhor Jesus!"`,verseorder:3,ismainverse:0,id_hino:549},{idverse:27265,verse:`1. 
Breve Ele volta;
Quando, eu n\xE3o sei;
Breve Ele volta;
Jesus vir\xE1 outra vez.`,verseorder:1,ismainverse:0,id_hino:550},{idverse:27266,verse:`1. 
O Santo Esp\xEDrito Consolador,
J\xE1 nos encheu de seu eterno amor,
De sua gra\xE7a e plena un\xE7\xE3o,
Pra anunciarmos a salva\xE7\xE3o! Am\xE9m.`,verseorder:1,ismainverse:0,id_hino:551},{idverse:27267,verse:`1. 
Canta o grande amor de Cristo,
Sua gra\xE7a e compaix\xE3o:
Num lugar tranq\xFCilo e belo
Faz-nos eternal mans\xE3o.
`,verseorder:1,ismainverse:0,id_hino:552},{idverse:27268,verse:`Quando ao C\xE9u n\xF3s chegarmos,
Oh! que dia de j\xFAbilo h\xE1 de ser!
A Jesus daremos
Hosanas, cheios de prazer.
`,verseorder:2,ismainverse:1,id_hino:552},{idverse:27269,verse:`2. 
Somos hoje peregrinos:
Nuvens toldam nosso c\xE9u,
Mas um dia - estrada finda -
N\xE3o mais sombras, n\xE3o mais v\xE9u.
`,verseorder:3,ismainverse:0,id_hino:552},{idverse:27270,verse:`3. 
Vamos ser leais, portanto,
Seja em crer, seja em servir!
Um relance l\xE1 da Gl\xF3ria
Atra\xE7\xE3o d\xE1 ao porvir.
`,verseorder:4,ismainverse:0,id_hino:552},{idverse:27271,verse:`4. 
Eia, ao galard\xE3o depressa:
Vamos logo ao C\xE9u chegar:
Abrem-se os portais gloriosos:
Eis Jesus a nos saudar.`,verseorder:5,ismainverse:0,id_hino:552},{idverse:27272,verse:`1. 
Oh! Pai, o Santo Esp\xEDrito,
Pedimos, vem nos dar,
Pra que Ele venha j\xE1
O seu poder nos outorgar!
`,verseorder:1,ismainverse:0,id_hino:553},{idverse:27273,verse:`2. 
Conven\xE7a-nos de todo o mal,
Console os cora\xE7\xF5es,
Ampare-nos a f\xE9
Em meio \xE0 dor e \xE0s afli\xE7\xF5es!
`,verseorder:2,ismainverse:0,id_hino:553},{idverse:27274,verse:`3. 
Habite nossos cora\xE7\xF5es
Pra sermos templos teus,
Opere convers\xE3o
E reden\xE7\xE3o, oh! nosso Deus!`,verseorder:3,ismainverse:0,id_hino:553},{idverse:27275,verse:`1. 
O meu Salvador, por seu grande amor,
Do C\xE9u, veio aqui morrer,
Pra, salvo levar quem nele confiar
E ir sempre com Ele viver.`,verseorder:1,ismainverse:0,id_hino:554},{idverse:27276,verse:`1. 
Sei que vive o Redentor,
Sei que h\xE1 vida em seu favor,
Ele aqui na cruz morreu:
Reina em gl\xF3ria l\xE1 no C\xE9u.
`,verseorder:1,ismainverse:0,id_hino:555},{idverse:27277,verse:`2. 
Vive para interceder
E nas lutas me valer,
Vive pra me sustentar,
E do mal me resguardar.
`,verseorder:2,ismainverse:0,id_hino:555},{idverse:27278,verse:`3. 
Deus me livra do temor,
Minorando a minha dor,
A tristeza me desfaz,
D\xE1-me gozo, vida e paz.
`,verseorder:3,ismainverse:0,id_hino:555},{idverse:27279,verse:`4. 
Vive! Hosanas eu lhe dou!
Vive! reina! e salvo eu sou!
Vivo nele, o Redentor,
Sempre firme em seu amor!`,verseorder:4,ismainverse:0,id_hino:555},{idverse:27280,verse:`1. 
Se desejas conhecer
A mensagem do Senhor,
Tua alma enriquecer
Na verdade e no Amor,
`,verseorder:1,ismainverse:0,id_hino:556},{idverse:27281,verse:`L\xEA a B\xEDblia! - Teu fervor
Nela vem alimentar!
Tua vida redimida
No Senhor vai exultar.
L\xEA a B\xEDblia - Teu fervor
Nela vem alimentar!
Tua vida redimida
No Senhor vai exultar.
`,verseorder:2,ismainverse:1,id_hino:556},{idverse:27282,verse:`2. 
Se almejas alcan\xE7ar
As promessas de Jesus
E pretendes caminhar
Sob a prote\xE7\xE3o da Cruz,
`,verseorder:3,ismainverse:0,id_hino:556},{idverse:27283,verse:`3. 
Se em verdade queres ir
Habitar com o Senhor,
Junto dele usufruir
Do mais rico e pleno amor.`,verseorder:4,ismainverse:0,id_hino:556},{idverse:27284,verse:`1. 
Cristo, oh! celeste vida,
Manifesta o teu poder!
Vivifica os sem-alento,
Faze os mortos reviver!
Vida eterna - vida eterna
Vem a todos conceder!
Vem a todos conceder!
`,verseorder:1,ismainverse:0,id_hino:557},{idverse:27285,verse:`2. 
Abre-nos teu santo livro!
Brilhe em n\xF3s a luz dos c\xE9us!
Esclarece todo o engano
E dos erros livra os teus!
Ilumina! - ilumina
Nossas almas, Rei dos c\xE9us!
Nossas almas, Rei dos c\xE9us!
`,verseorder:2,ismainverse:0,id_hino:557},{idverse:27286,verse:`3. 
Na leitura desta B\xEDblia
D\xE1-nos gozo, oh! Senhor!
Tendo, pelo teu ensino,
Comunh\xE3o em santo amor,
Exultemos - exultemos
Entoando o teu louvor!
Entoando o teu louvor!
`,verseorder:3,ismainverse:0,id_hino:557},{idverse:27287,verse:`4. 
Pelo estudo da Palavra
Aprendemos oh! Jesus!
- Deus concede os belos frutos
Que tua instru\xE7\xE3o produz.
Desfrutamos - desfrutamos
Alegria, vida e luz!
Alegria, vida e luz! Am\xE9m.`,verseorder:4,ismainverse:0,id_hino:557},{idverse:27288,verse:`1. 
Dai-me a B\xEDblia, que eu tanto anelo:
Por seu caminho quero sempre andar.
Por suas palavras s\xE3o a grata nova
Que Jesus Cristo veio anunciar.
`,verseorder:1,ismainverse:0,id_hino:558},{idverse:27289,verse:`Dai-me a B\xEDblia, livro t\xE3o
precioso!
Doutrina santa: fonte \xE9 de luz.
E seus ensinos, qual farol glorioso,
A n\xF3s conduzem ao Senhor Jesus.
`,verseorder:2,ismainverse:1,id_hino:558},{idverse:27290,verse:`2. 
Dai-me a B\xEDblia: dela eu preciso,
Pois do pecado a mim vai livrar
E suas palavras s\xE3o t\xE3o poderosas,
Que minha alma podem renovar.
`,verseorder:3,ismainverse:0,id_hino:558},{idverse:27291,verse:`3. 
Dai-me a B\xEDblia! Que meu guia seja!
E nesta vida meu caminho e luz!
E seus ensinos - o roteiro certo -,
Que me conduzam para o bom Jesus.`,verseorder:4,ismainverse:0,id_hino:558},{idverse:27292,verse:`1. 
B\xEDblia Sagrada,
\xC9s a Palavra de Deus:
\xC9s o mapa da jornada
Dos que seguem para o C\xE9u.
\xC9s a carta enviada,
Que o Atalaia tem na m\xE3o:
\xC9s as velas da jangada
Dos que levam a salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:559},{idverse:27293,verse:`2. 
Livro de amor,
Livro que nos d\xE1 prazer
No Salvador,
No Esp\xEDrito e no saber.
Livro bendito!
S\xF3 em ti pode habitar
A Palavra de amor,
Que dos l\xE1bios do Senhor
Saiu para em ti falar.
`,verseorder:2,ismainverse:0,id_hino:559},{idverse:27294,verse:`3. 
B\xEDblia Sagrada,
\xC9s o livro de fulgor,
\xC9s a j\xF3ia rejeitada
Pelo vituperador.
\xC9s o livro para a senda,
Contra o horror da corrup\xE7\xE3o.
\xC9s o livro que nos manda
Falar desta salva\xE7\xE3o.
`,verseorder:3,ismainverse:0,id_hino:559},{idverse:27295,verse:`4. 
Al\xE9m do v\xE9u,
Est\xE1s, livro do Senhor,
Livro do C\xE9u,
Que transforma o pecador.
Livro Sagrado!
Bem-aventurados s\xE3o
Os que cantam o louvor,
Que registras com amor,
Pela nossa salva\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:559},{idverse:27296,verse:`5. 
B\xEDblia Sagrada,
\xC9s a luz do meu Brasil,
\xC9s a lan\xE7a e a espada
Deste povo varonil.
\xC9s dos dedos, o brilhante,
\xC9s do jardim, a linda flor,
\xC9s a luz mais radiante
Que nos mostra o Salvador.
`,verseorder:5,ismainverse:0,id_hino:559},{idverse:27297,verse:`6. 
Tu vens de Deus,
Livro santo de Jesus.
Dos altos c\xE9us,
Revelaste a n\xF3s a Cruz.
Livro que fala
Do poder do Salvador,
Livro que nos revelou
Como Deus a n\xF3s amou,
\xC9s a voz do Redentor.`,verseorder:6,ismainverse:0,id_hino:559},{idverse:27298,verse:`1. 
Tenho paz em meu ser, uma perfeita paz,
Que o mundo n\xE3o me pode dar.
E na prova fatal ou na luta mortal
Tenho paz para me confortar.
`,verseorder:1,ismainverse:0,id_hino:560},{idverse:27299,verse:`Em meu ser habita Cristo, o Senhor.
Em meu ser habita profundo amor!
Sim, n\xE3o me desampara: Ele \xE9 fiel:
Estar\xE1 comigo, meu Salvador.
`,verseorder:2,ismainverse:1,id_hino:560},{idverse:27300,verse:`2. 
Quando eu fui a Jesus, encontrei plena luz,
De paz a minh\`alma inundou.
Toda noite passou: novo dia raiou,
Porque Cristo Jesus me salvou.
`,verseorder:3,ismainverse:0,id_hino:560},{idverse:27301,verse:`3. 
Esta paz sem igual, que me livra do mal,
Sublime, sem par, eternal,
N\xE3o a quero deixar: ela vai me ajudar,
T\xE9 chegar \xE0 mans\xE3o celestial.`,verseorder:4,ismainverse:0,id_hino:560},{idverse:27302,verse:`1. 
Que b\xEAn\xE7\xE3o: a lua brilhando no espa\xE7o!
Que b\xEAn\xE7\xE3o: as ondas que rolam no mar!
Que b\xEAn\xE7\xE3o: as flores brotando nos campos!
Mas a maior b\xEAn\xE7\xE3o eu vou te contar:
`,verseorder:1,ismainverse:0,id_hino:561},{idverse:27303,verse:`Jesus me salvou, dos
pecados meus,
Que b\xEAn\xE7\xE3o, Jesus! que b\xEAn\xE7\xE3o, meu Deus!
Jesus me salvou, dos pecados meus,
Que b\xEAn\xE7\xE3o, Jesus! que b\xEAn\xE7\xE3o, meu Deus!
`,verseorder:2,ismainverse:1,id_hino:561},{idverse:27304,verse:`2. 
Que b\xEAn\xE7\xE3o: as aves que cantam e encantam!
Que b\xEAn\xE7\xE3o: crian\xE7as em simples brincar!
Que b\xEAn\xE7\xE3o: rebanhos pastando entre os rios!
Mas a maior b\xEAn\xE7\xE3o eu vou te contar:
`,verseorder:3,ismainverse:0,id_hino:561},{idverse:27305,verse:`3. 
Que b\xEAn\xE7\xE3o: amigos que a mim procuram!
Que b\xEAn\xE7\xE3o: a P\xE1tria, a qual posso amar!
Que b\xEAn\xE7\xE3o: fam\xEDlia, sa\xFAde, fartural!
Mas a maior b\xEAn\xE7\xE3o eu vou te contar:`,verseorder:4,ismainverse:0,id_hino:561},{idverse:27306,verse:`1. 
Autor da vida, Cristo, Deus!
A vida fluis a teu querer.
Perd\xE3o por ti nos vem dos c\xE9us,
E tu nos d\xE1s real prazer,
Fazendo em nosso cora\xE7\xE3o
Raiar a luz da salva\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:562},{idverse:27307,verse:`2. 
Nos deste a vida eterna a n\xF3s -
Outrora mortos para Deus -
Fizeste ouvir a tua voz
- A voz que vivifica os teus.
Pra te render real louvor,
Estamos juntos, oh! Senhor!`,verseorder:2,ismainverse:0,id_hino:562},{idverse:27308,verse:`1. 
Feliz, eu sou feliz, feliz, oh! sempre mui feliz!
Pois Cristo veio e me salvou, meu cora\xE7\xE3o lavou.
Feliz, eu sou feliz, oh! sempre mui feliz!
Feliz, eu sou feliz, feliz, oh! sempre mui feliz!`,verseorder:1,ismainverse:0,id_hino:563},{idverse:27309,verse:`1. 
Se da vida as vagas procelosas s\xE3o,
Se, com desalento, julgas tudo v\xE3o,
Conta as muitas b\xEAn\xE7\xE3os, dize-as duma vez,
E ver\xE1z, surpreso, quanto Deus j\xE1 fez!
`,verseorder:1,ismainverse:0,id_hino:564},{idverse:27310,verse:`2. 
Tens acaso m\xE1goas? Triste \xE9 teu lidar?
\xC9 a cruz pesada, que tens de levar?
Conta as muitas b\xEAn\xE7\xE3os, n\xE3o duvidar\xE1s,
E em canto alegre os dias passar\xE1s.
`,verseorder:3,ismainverse:0,id_hino:564},{idverse:27311,verse:`3. 
Quando vires outros com seu ouro e bens,
Lembra que tesouros prometidos tens!
Nunca os bens da terra poder\xE3o comprar
A mans\xE3o celste que vais habitar.
`,verseorder:4,ismainverse:0,id_hino:564},{idverse:27312,verse:`4. 
Seja o conflito fraco ou forte aqui,
N\xE3o te desanimes: Deus ser\xE1 por ti,
Seu divino aux\xEDlio, minorando o mal,
Te dar\xE1 consolo, e galard\xE3o final.`,verseorder:5,ismainverse:0,id_hino:564},{idverse:27313,verse:`1. 
Jesus, eu sei que tu \xE9s meu,
O Dom que Deus, o Pai, me deu.
Teus feitos majestoso s\xE3o
O gozo do meu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:565},{idverse:27314,verse:`2. 
Tudo o que tens, \xE9 para mim.
O meu prazer est\xE1 em ti.
Tomaste aqui o meu lugar,
Pra eu estar no santo lar.
`,verseorder:2,ismainverse:0,id_hino:565},{idverse:27315,verse:`3. 
Por mim est\xE1s nos c\xE9us, Jesus.
Que eu mostre aqui a tua luz!
Revele sempre o teu amor!
At\xE9 que venhas, meu Senhor!`,verseorder:3,ismainverse:0,id_hino:565},{idverse:27316,verse:`1. 
Paz perfeita, sim, busquei,
E jamais a encontrei,
Pois o pre\xE7o n\xE3o queria eu pagar.
Longe de Deus a vagar,
Sua promessa a rejeitar.
Do pecado n\xE3o querendo me apartar.
`,verseorder:1,ismainverse:0,id_hino:566},{idverse:27317,verse:`Mas agora estou vivendo pra
Jesus,
Esconder-me quero junto \xE0 sua Cruz.
Inda que sofrendo dor,
Quero estar com meu Jesus:
Encontrei por fim a vida em meu Senhor.
`,verseorder:2,ismainverse:1,id_hino:566},{idverse:27318,verse:`2. 
Eu estava em ora\xE7\xE3o,
Deus tocou meu cora\xE7\xE3o
E me disse: "P\xF5e tua vida no altar
Para eu santificar!"
O pecado ent\xE3o deixei:
Minha vida a Deus eu logo dediquei.
`,verseorder:3,ismainverse:0,id_hino:566},{idverse:27319,verse:`3. 
Com seu  sangue me comprou,
O meu ser purificou.
- Uma vida de vit\xF3ria encontrei.
"Teus pecados perdoei
Pelo muito que te amei"
Ternamente me falou o Grande Rei.`,verseorder:4,ismainverse:0,id_hino:566},{idverse:27320,verse:`1. 
Sou feliz, feliz, feliz no meu viver!
Sou feliz, pois tenho um grande Salvador.
Desde quando me salvou com o seu poder,
Vivo alegre, mui feliz no seu amor.`,verseorder:1,ismainverse:0,id_hino:567},{idverse:27321,verse:`1. 
Manso e suave, \xE9 Cristo chamando:
Chama por ti e por mim.
Eis que Ele \xE0s portas espera velando:
Vela por ti e por mim.
`,verseorder:1,ismainverse:0,id_hino:568},{idverse:27322,verse:`Vem j\xE1! vem j\xE1! alma cansada, vem j\xE1!
Manso e suave, Jesus convidando,
Chama: - "Vem, pecador, vem!"
`,verseorder:2,ismainverse:1,id_hino:568},{idverse:27323,verse:`2. 
Com paci\xEAncia, Ele est\xE1 esperando,
Hoje, por ti e por mim.
Oh! n\xE3o desprezes a quem, convidando,
Chama por ti e por mim.
`,verseorder:3,ismainverse:0,id_hino:568},{idverse:27324,verse:`3. 
Correm os dias, as horas se passam,
Passam por ti e por mim.
Transes de morte, por fim, v\xE3o chegando,
Tanto por ti e por mim.
`,verseorder:4,ismainverse:0,id_hino:568},{idverse:27325,verse:`4. 
Oh! quanto amor que Jesus nos tem dado!
Tudo por ti e por mim!
Seu sangue foi sobre a Cruz derramado,
Sim, foi por ti e por mim.`,verseorder:5,ismainverse:0,id_hino:568},{idverse:27326,verse:`1. 
"Morri na cruz por ti:
Morri pra te livrar!
Meu sangue, ali, verti,
E posso te salvar."
`,verseorder:1,ismainverse:0,id_hino:569},{idverse:27327,verse:`"Morri! morri na cruz por ti:
- Que fazes tu por mim?
Morri! morri na cruz por ti:
- Que fazes tu por mim?"
`,verseorder:2,ismainverse:1,id_hino:569},{idverse:27328,verse:`2. 
"Vivi assim por ti,,
Em dor, em dissabor,
E tudo fiz aqui
Pra ser teu Salvador."
`,verseorder:3,ismainverse:0,id_hino:569},{idverse:27329,verse:`3. 
"Sofri na cruz por ti,
A fim de te salvar.
A vida conseguir,
Que tu ir\xE1s gozar."
`,verseorder:4,ismainverse:0,id_hino:569},{idverse:27330,verse:`4. 
"Eu trouxe a salva\xE7\xE3o,
Dos altos c\xE9us louvor.
\xC9 certo meu perd\xE3o,
\xC9 grande o meu amor!"`,verseorder:5,ismainverse:0,id_hino:569},{idverse:27331,verse:`1. 
- Ao findar o labor desta vida,
Quando a morte a teu lado chegar,
Que destino h\xE1 de ter a tua alma?
Qual ser\xE1 no futuro teu lar?
`,verseorder:1,ismainverse:0,id_hino:570},{idverse:27332,verse:`Meu amigo, hoje tu tens a escolha:
- Vida ou morte - qual vais aceitar?
Amanh\xE3 pode ser muito tarde:
Hoje Cristo te quer libertar.
`,verseorder:2,ismainverse:1,id_hino:570},{idverse:27333,verse:`2. 
Tu procuras a paz neste mundo,
Em prazeres que passam em v\xE3o,
Mas, na \xFAltima hora da vida,
Eles n\xE3o mais te satisfar\xE3o.
`,verseorder:3,ismainverse:0,id_hino:570},{idverse:27334,verse:`3. 
- Por acaso tu riste, \xF3 amigo,
Quando ouviste falar de Jesus?
Mas somente Jesus pode dar-te
Salva\xE7\xE3o pela morte na cruz.
`,verseorder:4,ismainverse:0,id_hino:570},{idverse:27335,verse:`4. 
Tens manchada tua alma e n\xE3o podes
Contemplar o semblante de Deus:
S\xF3 os crentes de cora\xE7\xF5es limpos
Poder\xE3o ter o gozo nos c\xE9us,
`,verseorder:5,ismainverse:0,id_hino:570},{idverse:27336,verse:`5. 
Se decides deixar teus pecados,
E entregar tua vida a Jesus,
Trilhar\xE1s, sim, na \xFAltima hora
Um caminho brilhante de luz.`,verseorder:6,ismainverse:0,id_hino:570},{idverse:27337,verse:`1. 
Rende o cora\xE7\xE3o agora a Jesus,
Pois est\xE1 \xE0 tua espera, com amor.
Quem se entrega a Cristo goza paz e luz;
Oh! Vem ao Salvador!`,verseorder:1,ismainverse:0,id_hino:571},{idverse:27338,verse:`1. 
"A porta sou eu", diz o Senhor,
" Algu\xE9m entrando por mim, por mim,
Salvo ser\xE1, salvo ser\xE1; salvar-se-\xE1".`,verseorder:1,ismainverse:0,id_hino:572},{idverse:27339,verse:`1. 
A gra\xE7a de nosso Senhor,
E o amor de Deus,
E a comunh\xE3o do Esp\xEDrito Santo,
Sejam conosco. Am\xE9m, Am\xE9m.`,verseorder:1,ismainverse:0,id_hino:573},{idverse:27340,verse:`1. 
\xD3 Pai bondoso, vem nos guiar!
No novo ano vem nos guardar!`,verseorder:1,ismainverse:0,id_hino:574},{idverse:27341,verse:`1. 
Eu s\xF3 espero em ti,
Cordeiro, que por mim,
Foste \xE0 cruz.
Ouve minha ora\xE7\xE3o,
D\xE1-me a tua m\xE3o,
Enche meu cora\xE7\xE3o
Com santa luz!
`,verseorder:1,ismainverse:0,id_hino:575},{idverse:27342,verse:`2. 
D\xE1-me consola\xE7\xE3o,
Guarda meu cora\xE7\xE3o!
Teu grande amor,
Teu sangue carmesim,
Deste na cruz por mim.
Que eu viva para ti,
Com muito ardor!
`,verseorder:2,ismainverse:0,id_hino:575},{idverse:27343,verse:`3. 
A Trilha seguirei,
E provas eu terei.
Meu guia s\xEA,
Do mal vem me livrar:
Puro me conservar!
Pra sempre vou cantar
O teu louvor.`,verseorder:3,ismainverse:0,id_hino:575},{idverse:27344,verse:`1. 
Assim como estou: sem nada dizer,
Sen\xE3o que por mim vieste a morrer,
E me convidaste a ti recorrer:
Bendito Jesus, me chego a ti!
`,verseorder:1,ismainverse:0,id_hino:576},{idverse:27345,verse:`2. 
Assim como estou: sem mais demorar,
Minha alma do mal querendo limpar,
A ti, que de tudo me podes lavar:
Bendito Jesus, me chego a ti!
`,verseorder:2,ismainverse:0,id_hino:576},{idverse:27346,verse:`3. 
Assim como estou: em grande afli\xE7\xE3o,
T\xE3o digno de morte e da perdi\xE7\xE3o,
Rogando-te vida, com paz e perd\xE3o:
Bendito Jesus, me chego a ti!
`,verseorder:3,ismainverse:0,id_hino:576},{idverse:27347,verse:`4. 
Assim como estou: o teu grande amor
Me vence: e crendo em ti, sem temor,
Eu quero servir-te, divino Senhor!
- Bendito Jesus, me chego a ti!`,verseorder:4,ismainverse:0,id_hino:576},{idverse:27348,verse:`1. 
Em fervente ora\xE7\xE3o, vem o teu cora\xE7\xE3o
Na presen\xE7a de Deus derramar!
Mas s\xF3 podes fruir o que est\xE1s a pedir,
Quando tudo deixares no altar.
`,verseorder:1,ismainverse:0,id_hino:577},{idverse:27349,verse:`Quando tudo perante o Senhor
estiver,
E todo o teu ser Ele controlar,
S\xF3, ent\xE3o, h\xE1s de ver que o Senhor tem poder,
Quando tudo deixares no Altar.
`,verseorder:2,ismainverse:1,id_hino:577},{idverse:27350,verse:`2. 
Maravilhas de amor te far\xE1 o Senhor,
Atendendo \xE0 ora\xE7\xE3o que aceitar.
Seu imenso poder, te vir\xE1 socorrer,
Quando tudo deixares no Altar.
`,verseorder:3,ismainverse:0,id_hino:577},{idverse:27351,verse:`3. 
Se orares, por\xE9m, sem o teu cora\xE7\xE3o
Ter a paz que o Senhor pode dar,
Foi por Deus n\xE3o sentir que tua alma se abriu,
Tudo, tudo, deixando no Altar.`,verseorder:4,ismainverse:0,id_hino:577},{idverse:27352,verse:`1. 
- \xD3 Mestre! O mar se revolta:
As ondas nos d\xE3o pavor:
O c\xE9u se reveste de trevas:
N\xE3o temos um Salvador!
N\xE3o se d\xE1 que morramos?
Podes assim dormir,
Se a cada momento nos vemos,
Sim, prestes a submergir?
`,verseorder:1,ismainverse:0,id_hino:578},{idverse:27353,verse:`- "As ondas atendem ao meu mandar:
Sossegai!
Seja o encapelado mar
A ira dos homens, o g\xEAnio do mal:
Tais \xE1guas n\xE3o podem a nau tragar,
Que leva o Senhor, Rei do C\xE9u e mar,
Pois todos ouvem  o meu mandar:
Sossegai! - sossegai!
Convosco estou para vos salvar:
Sim, sossegai!"
`,verseorder:2,ismainverse:1,id_hino:578},{idverse:27354,verse:`2. 
- Mestre, na minha tristeza
Estou quase a sucumbir:
A dor que pertuba minha alma,
Oh! Pe\xE7o-te, vem banir!
De ondas do mal que me encobrem,
Quem me far\xE1 sair?
Pere\xE7o, sem ti, oh! meu Mestre!
Vem logo, vem me acudir!
`,verseorder:3,ismainverse:0,id_hino:578},{idverse:27355,verse:`3. 
- Mestre, chegou a bonan\xE7a,
Em paz eis o c\xE9u e o  mar!
O meu cora\xE7\xE3o goza calma
Que n\xE3o poder\xE1 findar.
Fica comiga, oh! meu Mestre,
Dono da Terra e C\xE9u,
E assim chegarei bem seguro
Ao porto, destino meu.`,verseorder:4,ismainverse:0,id_hino:578},{idverse:27356,verse:`1. 
Oh! por que te aliges, pobre cora\xE7\xE3o?
Pois o teu Jesus quer dar consola\xE7\xE3o.
S\xE3o demais as cargas? \xC9 cruel a dor?
Olha para o c\xE9u e v\xEA teu lar de amor.
`,verseorder:1,ismainverse:0,id_hino:579},{idverse:27357,verse:`2. 
Pesa na consci\xEAncica teu pecado vil?
Olha para a cruz, Jesus \xE9 t\xE3o gentil!
Seu precioso sangue Ele j\xE1 verteu
Para dar perd\xE3o e abrir caminho ao c\xE9u.
`,verseorder:2,ismainverse:0,id_hino:579},{idverse:27358,verse:`3. 
Quando te comprimem garras de temor,
Quando em afli\xE7\xF5es, confia no Senhor.
Ele te protege, triste cora\xE7\xE3o!
Ele te conduz ao ce\xFA com sua m\xE3o!`,verseorder:3,ismainverse:0,id_hino:579},{idverse:27359,verse:`1. 
Ergo os meus olhos para os altos montes:
O meu socorro, de onde me vir\xE1?
O meu socorro vem do Deus eterno
Que fez a terra, fez o c\xE9u e o mar.
`,verseorder:1,ismainverse:0,id_hino:580},{idverse:27360,verse:`2. 
Ele te guarda se teu p\xE9 vacila:
Teu guarda nunca tosquenejar\xE1.
\xC9 tua sombra: nem de noite a lua
Nem o sol de dia te molestar\xE3o.
`,verseorder:2,ismainverse:0,id_hino:580},{idverse:27361,verse:`3. 
Todos os males Ele \xE9 quem afasta,
Pois tua alma Ele guardar\xE1.
Guarda a sa\xEDda, guarda a tua entrada,
Sim, desde agora, para sempre. am\xE9m.`,verseorder:3,ismainverse:0,id_hino:580},{idverse:27362,verse:`1. 
Castelo forte \xE9 nosso Deus,
Amparo e fortaleza:
Com seu poder defendo os seus
Na luta e na franqueza.
Nos tenta e na fraqueza.
Nos tenta Satan\xE1s,
Com f\xFAria pertinaz,
Com artimanhas tais
E ast\xFAcias t\xE3o cru\xE9is,
Que iguas n\xE3o h\xE1 na Terra.
`,verseorder:1,ismainverse:0,id_hino:581},{idverse:27363,verse:`2. 
A nossa for\xE7a nada faz:
Estamos, sim, perdidos.
Mas nosso Deus socorro traz
E somos protegidos.
Defende-nos Jesus,
O que venceu na cruz,
O Senhor dos altos c\xE9us.
E sendo tamb\xE9m Deus
Triunfa na batalha.
`,verseorder:2,ismainverse:0,id_hino:581},{idverse:27364,verse:`3. 
Se nos quisessem devorar
Dem\xF4nios n\xE3o contados,
N\xE3o nos podiam assustar,
Nem somos derrotados.
O grande acusador
Dos servos do Senhor
J\xE1 condenado est\xE1:
Vencido cair\xE1
Por uma s\xF3 palavra.
`,verseorder:3,ismainverse:0,id_hino:581},{idverse:27365,verse:`4. 
Que Deus a luta vencer\xE1,
Sabemos com certeza,
E nada nos assustar\xE1
Com Cristo por defesa.
Se temos de perder
Fam\xEDlia, bens, poder,
E, embora a vida v\xE1,
Por n\xF3s Jesus est\xE1,
E dar-nos-\xE1 seu reino.`,verseorder:4,ismainverse:0,id_hino:581},{idverse:27366,verse:`1. 
Eu tenho de andar neste mundo
Qual barca vogando no mar,
Mas sei o segredo profundo
De nele jamais naufragar:
`,verseorder:1,ismainverse:0,id_hino:582},{idverse:27367,verse:`Cristo \xE9 o Piloto para me guiar,
Da vida a barca, at\xE9 no C\xE9u chegar.
`,verseorder:2,ismainverse:1,id_hino:582},{idverse:27368,verse:`2. 
A luz que ao C\xE9u me dirige,
A Santa Palavra de Deus,
Desvios e faltas corrige,
Mostrando o caminho dos c\xE9us.
`,verseorder:3,ismainverse:0,id_hino:582},{idverse:27369,verse:`3. 
O mar tormentoso da vida
Rugindo, me busca perder,
Mas sempre por Cristo mantida,
Minh\`alma bonan\xE7a h\xE1 de ter.
`,verseorder:4,ismainverse:0,id_hino:582},{idverse:27370,verse:`4. 
Assim eu n\xE3o temo perigo:
N\xE3o vou naufragar neste mar.
Se Cristo, Jesus vai comigo,
No c\xE9u, sim, eu hei de entrar.`,verseorder:5,ismainverse:0,id_hino:582},{idverse:27371,verse:`1. 
S\xEA tu meu Guia, \xF3 Cristo: estou medroso
De andar sozinho pela solid\xE3o!
S\xEA tu meu Guia, e o ermo pavoroso
J\xE1 n\xE3o ser\xE1 lugar de escurid\xE3o!
`,verseorder:1,ismainverse:0,id_hino:583},{idverse:27372,verse:`2. 
S\xEA tu meu Guia, leva-me a teu lado,
Pois, junto a ti desejo estar, Senhor!
Pois por teu bra\xE7o, estando assim firmado,
N\xE3o hei de trope\xE7ar nem ter temor.
`,verseorder:2,ismainverse:0,id_hino:583},{idverse:27373,verse:`3. 
S\xEA tu meu Guia, em tempo radiante,
Ou na bonan\xE7a, ou mesmo em temporal!
S\xEA tu me Guia, que eu prossiga avante,
Sem me afastar do rumo divinal.
`,verseorder:3,ismainverse:0,id_hino:583},{idverse:27374,verse:`4. 
S\xEA tu meu Guia, at\xE9 que tenha entrada
na casa paternal, no C\xE9u al\xE9m;
Ali, enfim, minha alma descansada
Ter\xE1 contigo seu eterno bem.`,verseorder:4,ismainverse:0,id_hino:583},{idverse:27375,verse:`1. 
Guia, Cristo, minha nau
Sobre o revoltoso mar,
T\xE3o enfurecido e mau,
Quer faz\xEA-la naufragar.
Vem Jesus, oh! vem guiar,
Minha nau vem pilotar!
`,verseorder:1,ismainverse:0,id_hino:584},{idverse:27376,verse:`2. 
Como sabe serenar
Boa m\xE3e o filho seu,
Vem, acalma, assim, o mar
Que se eleva at\xE9 o ce\xFA.
Vem Jesus, oh! vem guiar,
Minha nau vem pilotar!
`,verseorder:2,ismainverse:0,id_hino:584},{idverse:27377,verse:`3. 
Se, no porto quando entrar,
Mais o mar se enfurecer,
Que me possa deleitar
Em ouvir Jesus dizer:
"Entra, pobre viajor,
No descanso do Senhor."`,verseorder:3,ismainverse:0,id_hino:584},{idverse:27378,verse:`1. 
Jesus me satisfaz,
E gozo sua paz;
Com Ele vou,
Contente estou:
- Jesus me satisfaz`,verseorder:1,ismainverse:0,id_hino:585},{idverse:27379,verse:`1. 
Gozo e vida tenho no meu cora\xE7\xE3o,
Desde que Jesus Cristo deu-me seu perd\xE3o.`,verseorder:1,ismainverse:0,id_hino:586},{idverse:27380,verse:`1. 
Neste lenho me glorio,
Manancial de resplendor;
Brilha intenso o supl\xEDcio
Do divino Redentor.
`,verseorder:1,ismainverse:0,id_hino:587},{idverse:27381,verse:`2. 
Quando temos caridade,
Mansid\xE3o, perfeito amor,
Uma intensa claridade
Mostra ao mundo seu fulgor.
`,verseorder:2,ismainverse:0,id_hino:587},{idverse:27382,verse:`3. 
Que suprema dor e ang\xFAstia
Suportou o Salvador!
Sim, por morte t\xE3o terr\xEDvel
Redimiu-nos com amor.`,verseorder:3,ismainverse:0,id_hino:587},{idverse:27383,verse:`1. 
Fui pecador, perverso e mau,
Longe de Ti vaguei!
Mas, mui feliz eu hoje sou,
Jesus, meu Deus e Rei.
`,verseorder:1,ismainverse:0,id_hino:588},{idverse:27384,verse:`2. 
Eu percebi a tua voz
A me chamar: "Oh, vem!
Vem, filho meu! Eu dar-te-ei
O meu perd\xE3o! Sim, vem!"
`,verseorder:2,ismainverse:0,id_hino:588},{idverse:27385,verse:`3. 
Logo a teus p\xE9s, humilde, vim,
E me prostrei, Senhor!
Oh! sou feliz, porque senti
O teu benigno amor!`,verseorder:3,ismainverse:0,id_hino:588},{idverse:27386,verse:`1. 
Am\xE9m, Am\xE9m, Am\xE9m, Am\xE9m!`,verseorder:1,ismainverse:0,id_hino:589},{idverse:27387,verse:`1. 
Eu j\xE1 contente estou: Tenho Jesus!
Com alegria vou: Tenho Jesus!
Gozo que o mundo traz, bem cedo se desfaz:
Tenho perfeita paz, paz em Jesus.
`,verseorder:1,ismainverse:0,id_hino:590},{idverse:27388,verse:`2. 
Eu posso envelhecer, nunca Jesus!
Eu posso empobrecer, nunca Jesus!
Tudo me suprir\xE1, sempre me valer\xE1,
Nada me faltar\xE1, tendo Jesus.
`,verseorder:2,ismainverse:0,id_hino:590},{idverse:27389,verse:`3. 
Vai tudo aqui findar menos Jesus!
Quando o Juiz chegar: \xE9 meu Jesus!
Bem grato me h\xE1 de ser quando meu Rei descer.
Certo Ele ir\xE1 dizer: "Sou teu Jesus!"`,verseorder:3,ismainverse:0,id_hino:590},{idverse:27390,verse:`1. 
Pura, sim, mais pura quero a mente ter!
Gozo, sim, mais gozo possa eu conhecer!
Oh! Jesus Bendito, ouve esta ora\xE7\xE3o,
Pois a ti pertence o meu cora\xE7\xE3o.
`,verseorder:1,ismainverse:0,id_hino:591},{idverse:27391,verse:`2. 
Calmo, sim, mais calmo sempre quero estar.
Firme, sim, mais firme possa eu sempre andar!
Nunca me esmore\xE7a este cora\xE7\xE3o,
Pois em Cristo tenho toda a perfei\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:591},{idverse:27392,verse:`3. 
Alto, sim, mais alto que as estrelas vou.
Perto, sim, mais perto de Jesus estou.
Cristo \xE9 meu modelo, sempre o seguirei:
Tudo quanto anelo nele encontrarei.`,verseorder:3,ismainverse:0,id_hino:591},{idverse:27393,verse:`1. 
Eu estou no lugar onde h\xE1 b\xEAn\xE7\xE3o e paz,
Onde sinto comigo Jesus.
Aos humildes seu sangue assegura perd\xE3o:
Seu Esp\xEDrito os enche de luz.
`,verseorder:1,ismainverse:0,id_hino:592},{idverse:27394,verse:`Estou no lugar onde h\xE1 b\xEAn\xE7\xE3o e
paz,
Onde Cristo nos mostra afei\xE7\xE3o.
Descansai! exultai! confessai-o!
- Publicai que nele h\xE1 salva\xE7\xE3o.
`,verseorder:2,ismainverse:1,id_hino:592},{idverse:27395,verse:`2. 
Que alegria estar onde h\xE1 b\xEAn\xE7\xE3o e paz!
Que riqueza de amor sem igual:
O cansado recebe alimento e vigor:
Para o triste h\xE1 consolo real!
`,verseorder:3,ismainverse:0,id_hino:592},{idverse:27396,verse:`3. 
Que ternura estar onde h\xE1 b\xEAn\xE7\xE3o e paz,
E perd\xE3o de inef\xE1vel valor!
Mas somente os fi\xE9is \xE9 que podem contar
Esta gra\xE7a do bom Salvador.
`,verseorder:4,ismainverse:0,id_hino:592},{idverse:27397,verse:`4. 
H\xE1 louvor sem igual onde h\xE1 b\xEAn\xE7\xE3o e paz!
E eis que os anjos desejam cantar
Com os homens, em coro, a bendita merc\xEA
De Jesus, que nos veio salvar!`,verseorder:5,ismainverse:0,id_hino:592},{idverse:27398,verse:`1. 
Oh! Jesus, meu salvador,
Estou seguro em teu amor,
E por ti sou vencedor,
Pois est\xE1s comigo!`,verseorder:1,ismainverse:0,id_hino:593},{idverse:27399,verse:`1. 
Almejo a f\xE9 que forte \xE9
Diante do terror,
Que, calma, n\xE3o recuar\xE1
Do mundo aterrador.
`,verseorder:1,ismainverse:0,id_hino:594},{idverse:27400,verse:`2. 
A f\xE9 que n\xE3o me faz queixar
Na dor da corre\xE7\xE3o,
Mas quanto o fogo mais arder,
Mais firma o cora\xE7\xE3o.
`,verseorder:2,ismainverse:0,id_hino:594},{idverse:27401,verse:`3. 
A f\xE9 que sempre brilha mais
No negro furac\xE3o,
Que no perigo sente paz,
Audaz na escurid\xE3o.
`,verseorder:3,ismainverse:0,id_hino:594},{idverse:27402,verse:`4. 
\xD3 Deus, me d\xE1 tamanha f\xE9,
Que venha o que vier!
Ent\xE3o na lide provarei
Celestial prazer.`,verseorder:4,ismainverse:0,id_hino:594},{idverse:27403,verse:`1. 
Quem tenho, \xF3 Deus, no c\xE9u a\xED
Sen\xE3o a ti - somente a ti!
E mais ningu\xE9m adoro aqui
Al\xE9m de ti, somente a ti!
`,verseorder:1,ismainverse:0,id_hino:595},{idverse:27404,verse:`2. 
A Rocha deste cora\xE7\xE3o
\xC9s tu, Senhor, e meu quinh\xE3o.
Com teu olhar guiar-me-\xE0s
E em gl\xF3ria me receber\xE1s.`,verseorder:2,ismainverse:0,id_hino:595},{idverse:27405,verse:`1. 
Eu te agrade\xE7o, Jesus, meu Senhor,
As tuas b\xEAn\xE7\xE3os a flux.
Eu me enlevo por teu grande amor
Manifestado na cruz!
Ao lado estou das riquezas dos c\xE9us,
Flu\xEDdas de tuas m\xE3os.
Quero cantar mil cantares!
- Oferta profunda do meu cora\xE7\xE3o!
`,verseorder:1,ismainverse:0,id_hino:596},{idverse:27406,verse:`Louvemos, pois, o nosso Rei
Jesus,
Que por amor morreu na dura cruz!
S\xF3 Ele nos d\xE1 paz,
Sua gra\xE7a satisfaz:
Louvemos, pois, o nosso Rei Jesus!
`,verseorder:2,ismainverse:1,id_hino:596},{idverse:27407,verse:`2. 
Tua bondade \xE9 sublime e real,
Eternamente pra mim.
Aceita agora o meu voto leal
Por tua gra\xE7a sem fim!
Tu nos prov\xEAs da fartura dos ce\xFAs,
Com tuas divinas m\xE3os.
Por isto canto mil cantos,
Que jorram de dentro do meu cora\xE7\xE3o!
`,verseorder:3,ismainverse:0,id_hino:596},{idverse:27408,verse:`3. 
Mui amoroso \xE9s, Cristo Jesus,
Desde o Calv\xE1rio de dor,
Onde abriu-se a fonte da luz
Que sara o sofredor.
Por gratid\xE3o, quero sempre cantar
Louvores de salva\xE7\xE3o,
Quero cantar mil cantares
Com gozo imenso do meu cora\xE7\xE3o!`,verseorder:4,ismainverse:0,id_hino:596},{idverse:27409,verse:`1. 
Gra\xE7as dou por esta vida: pelo bem que revelou.
Gra\xE7as dou pelo futuro, e por tudo que passou.
Pelas b\xEAn\xE7\xE3os derramadas, pela dor, pela afli\xE7\xE3o.
Pela gra\xE7a revelada! - gra\xE7as dou pelo perd\xE3o.
`,verseorder:1,ismainverse:0,id_hino:597},{idverse:27410,verse:`2. 
Gra\xE7as pelo azul celeste e por nuvens que h\xE1 tamb\xE9m,
Pelas rosas do caminho, por espinhos que elas t\xEAm,
Pela escurid\xE3o da noite, pela estrela que brilhou,
Pela prece respondida, pelo sonho que falhou.
`,verseorder:2,ismainverse:0,id_hino:597},{idverse:27411,verse:`3. 
Pela cruz e o sofrimento e por toda prova\xE7\xE3o,
Pelo amor que \xE9 sem medida, pela paz no cora\xE7\xE3o,
Pela l\xE1grima vertida, pelo al\xEDvio que \xE9 sem par.
Pelo dom da eterna vida, sempre gra\xE7as hei de dar!`,verseorder:3,ismainverse:0,id_hino:597},{idverse:27412,verse:`1. 
Oh! buscai, n\xE3o as riquezas
Deste mundo de incertezas!
As do C\xE9u n\xE3o t\xEAm tristezas:
Oh! buscai-as, sim, buscai!
`,verseorder:1,ismainverse:0,id_hino:598},{idverse:27413,verse:`2. 
Oh! buscai, n\xE3o as loucuras
Que s\xF3 trazem amarguras,
Mas as eternais venturas:
Oh! buscai-as, sim, buscai!
`,verseorder:2,ismainverse:0,id_hino:598},{idverse:27414,verse:`3. 
Oh! buscai Jesus primeiro,
Ao real, fiel Cordeiro!
Nele h\xE1 gozo verdadeiro:
Oh! buscai-o, sim, buscai!
`,verseorder:3,ismainverse:0,id_hino:598},{idverse:27415,verse:`4. 
Sim, buscai Jesus bendito,
Seu amor \xE9 infinito,
Inef\xE1vel, inaudito:
Oh! buscai-o, sim, buscai!
`,verseorder:4,ismainverse:0,id_hino:598},{idverse:27416,verse:`5. 
Oh! buscai a santidade,
A pureza, a caridade!
Com temor, com humildade,
Oh! buscai-as! sim, buscai!
`,verseorder:5,ismainverse:0,id_hino:598},{idverse:27417,verse:`6. 
De Jesus imitadores,
Sede bons trabalhadores!
E buscai os pecadores:
Oh! buscai-os, sim, buscai!
`,verseorder:6,ismainverse:0,id_hino:598},{idverse:27418,verse:`7. 
Com amor, a convid\xE1-los,
Ide agora procur\xE1-los,
Pois Jesus manda cham\xE1-los:
Oh! buscai-os, sim, buscai!`,verseorder:7,ismainverse:0,id_hino:598},{idverse:27419,verse:`1. 
\xD3 tu, herdeiro do reino celeste,
N\xE3o, n\xE3o dormites, o fim vai chegar;
Oh! cinge as armas e p\xF5e-te na luta,
Pois a vit\xF3ria desejas ganhar.
`,verseorder:1,ismainverse:0,id_hino:599},{idverse:27420,verse:`2. 
N\xE3o te detenham prazeres terrenos,
Pois bem depressa ter\xE3o de passar;
Oh! deixa a vida que ao mundo te prende,
Vem, vem a Cristo que te h\xE1 de salvar!
`,verseorder:2,ismainverse:0,id_hino:599},{idverse:27421,verse:`3. 
Eis as pot\xEAncias dispostas \xE0 luta;
S\xEA fervoroso, fiel, bom crist\xE3o;
J\xE1 no horizonte refulgem as gl\xF3rias
Oh! nobre herdeiro, prorrompe em can\xE7\xE3o!`,verseorder:3,ismainverse:0,id_hino:599},{idverse:27422,verse:`1. 
M\xE3os ao trabalho, crentes,
Vai j\xE1 passando o alvor.
Vamos, enquanto temos
Nossa vida em flor!
Vamos, enquanto \xE9 dia,
Com for\xE7a trabalhar:
Eia que em vindo a noite,
J\xE1 n\xE3o h\xE1 lidar!
`,verseorder:1,ismainverse:0,id_hino:600},{idverse:27423,verse:`2. 
M\xE3os ao trabalho, crentes,
Andai enquanto h\xE1 luz:
Eia, que, agora \xE9 tempo
De servir Jesus!
Ide o vigor da vida
Todos ao bem votar.
Eia, que em vindo a noite,
J\xE1 n\xE3o h\xE1 lidar!
`,verseorder:2,ismainverse:0,id_hino:600},{idverse:27424,verse:`3. 
M\xE3os ao trabalho, crentes,
Breve nos chega o fim.
Firmes enquanto a morte
N\xE3o tocar clarim!
Vamos, irm\xE3os, \xE0 obra,
Por Cristo trabalhar:
Eia, que em vindo a noite,
Vamos descansar.`,verseorder:3,ismainverse:0,id_hino:600},{idverse:27425,verse:`1. 
A seara est\xE1 madura;
H\xE1 trabalho pra fazer.
Ouve a voz de Deus chamando
Para \xE0 ceifa te trazer!
`,verseorder:1,ismainverse:0,id_hino:601},{idverse:27426,verse:`Do teu labor o fruto aumenta,
Quando vives sempre a orar.
Ter\xE1s justa recompensa,
Se tu fores trabalhar.
`,verseorder:2,ismainverse:1,id_hino:601},{idverse:27427,verse:`2. 
Bem-avanturado o servo
Que viveu a trabalhar.
Ao fiel o Pai declara:
"No meu gozo vais entrar."`,verseorder:3,ismainverse:0,id_hino:601},{idverse:27428,verse:`1. 
Juntos trabalhamos pelo nosso Rei;
Frutos mostraremos quando aqui voltar.
Homens maus buscamos para a sua grei,
Pois o nosso Rei tamb\xE9m os quer salvar.
`,verseorder:1,ismainverse:0,id_hino:602},{idverse:27429,verse:`2. 
Mesmo em nossa casa, nosso derredor,
Vamos trabalhando pelo salvador.
Nossa recompensa h\xE1 de ser maior,
Sendo-lhe fi\xE9is e ao seu supremo amor.
`,verseorder:2,ismainverse:0,id_hino:602},{idverse:27430,verse:`3. 
Temos a mensagem para proclamar,
Tempos perigosos s\xE3o os atuais;
Cristo em breve volta, temos que lutar;
Sempre avante, sim - Retroceder, jamais!`,verseorder:3,ismainverse:0,id_hino:602},{idverse:27431,verse:`1. 
\xC9 bom falr com algu\xE9m
De Cristo, de Cristo,
\xC9 bom falar com algu\xE9m
De Cristo.
`,verseorder:1,ismainverse:0,id_hino:603},{idverse:27432,verse:`2. 
Vai hoje e traze algu\xE9m
A Cristo, a Cristo,
Sim, vai e traze algu\xE9m
A Cristo.
`,verseorder:2,ismainverse:0,id_hino:603},{idverse:27433,verse:`3. 
Vai hoje orar por algu\xE9m
A Cristo, a Cristo,
Sim, vai orar por algu\xE9m
A Cristo.
`,verseorder:3,ismainverse:0,id_hino:603},{idverse:27434,verse:`4. 
Algu\xE9m assim salvar\xE1s
Por Cristo, por Cristo,
E grande gozo ter\xE1s
Em Cristo.`,verseorder:4,ismainverse:0,id_hino:603},{idverse:27435,verse:`1. 
Senhor, os mensageiros teus
Ir\xE3o levar por onde for
A luz da gra\xE7a ao mundo mau,
E a f\xE9 no Salvador.
`,verseorder:1,ismainverse:0,id_hino:604},{idverse:27436,verse:`2. 
Senhor, envia obreiros bons,
De f\xE9, coragem, zelo e amor;
Fi\xE9is a ti e ao seu dever,
Leais ao Redentor.
`,verseorder:2,ismainverse:0,id_hino:604},{idverse:27437,verse:`3. 
Senhor, possamos todos n\xF3s
Aqui do teu perd\xE3o falar;
A quem do mal cativo est\xE1,
At\xE9 Jesus voltar.`,verseorder:3,ismainverse:0,id_hino:604},{idverse:27438,verse:`1. 
Quero, Jesus, fazer o teu querer;
Quero servir-te, sim,
Tua palavra sempre obedecer,
Pois tens amado a mim.
`,verseorder:1,ismainverse:0,id_hino:605},{idverse:27439,verse:`Quero Te servir
Oh! meu Salvador
Quero Te servir,
Pois \xE9s o meu Pastor.
`,verseorder:2,ismainverse:1,id_hino:605},{idverse:27440,verse:`2. 
Nesta jornada sempre confiarei
No teu grandioso amor.
Tua mensagem sempre levarei
Aos que padecem dor.
`,verseorder:3,ismainverse:0,id_hino:605},{idverse:27441,verse:`3. 
Um dia, aqui, as lutas findar\xE3o;
Contigo estarei.
Alegre irei, sim, \xE0 feliz Si\xE3o,
Com tua santa grei`,verseorder:4,ismainverse:0,id_hino:605},{idverse:27442,verse:`1. 
Eu triunfarei pelo sangue de Jesus;
Sim, triunfarei pela sua cruz.`,verseorder:1,ismainverse:0,id_hino:606},{idverse:27443,verse:`1. 
Luz ap\xF3s trevas, gl\xF3ria ap\xF3s luz,
Ganho ap\xF3s perda, trono ap\xF3s cruz,
Paz ap\xF3s luta, fruto ap\xF3s flor,
Riso ap\xF3s pranto, gozo ap\xF3s dor.
`,verseorder:1,ismainverse:0,id_hino:607},{idverse:27444,verse:`2. 
Crente ap\xF3s \xEDmpio, justo ap\xF3s r\xE9u,
Gra\xE7as ap\xF3s ira, vista ap\xF3s v\xE9u,
Sol ap\xF3s chuva, mel ap\xF3s sal,
Lar ap\xF3s lida, bem ap\xF3s mal.
`,verseorder:2,ismainverse:0,id_hino:607},{idverse:27445,verse:`3. 
Perto ap\xF3s longe, Cristo ap\xF3s eu,
Vida ap\xF3s tumba, terra ap\xF3s C\xE9u,
Gl\xF3ria, paz, vida, f\xE9 trono e luz,
Tudo isso eu tenho, crendo em Jesus.`,verseorder:3,ismainverse:0,id_hino:607},{idverse:27446,verse:`1. 
Se em teu caminho aqui
A escurid\xE3o e as sombras v\xEAm,
E se nenhum fulgor sorri,
Trazendo luz do vasto al\xE9m,
Ter\xE1s a luz do C\xE9u.
`,verseorder:1,ismainverse:0,id_hino:608},{idverse:27447,verse:`2. 
Se em teu viver aqui
Persegui\xE7\xE3o trouxer a dor,
Trouxer o sofrimento a ti,
Oh, sim ent\xE3o, em teu favor
Vir\xE1 a paz do C\xE9u.
`,verseorder:2,ismainverse:0,id_hino:608},{idverse:27448,verse:`3. 
Mas oh! se o teu sofrer
E a luta atroz findar aqui,
E a paz, enfim, puderes ter,
Qu\xE3o calmo e bom, qu\xE3o doce a ti
Ser\xE1 o lar do C\xE9u.`,verseorder:3,ismainverse:0,id_hino:608},{idverse:27449,verse:`1. 
Vit\xF3ria Deus dar\xE1 a mim, eu sei,
Vit\xF3ria Deus dar\xE1 a mim, eu sei.
Se eu andar em plena luz,
Confiar s\xF3 em Jesus,
Vit\xF3ria Deus dar\xE1 a mim, eu sei.`,verseorder:1,ismainverse:0,id_hino:609},{idverse:27450,verse:`1. 
F\xE9 \xE9 a vit\xF3ria,
Sim, f\xE9 sempre tem poder.
F\xE9 \xE9 a vit\xF3ria:
Ao mundo ir\xE1 vencer.`,verseorder:1,ismainverse:0,id_hino:610},{idverse:27451,verse:`1. 
Sempre avante! manda o General;
Sempre avante! guerra contra o mal!
Firmes, fortes, nunca vacilar!
Pois os seus fi\xE9is ir\xE3o ganhar.`,verseorder:1,ismainverse:0,id_hino:611},{idverse:27452,verse:`1. 
\xD3 Pai eterno que no C\xE9u habitas,
N\xF3s, os teus filhos, vimos te pedir
Que nos amparem tuas m\xE3os benditas,
Em nossas lutas hoje e no porvir.
`,verseorder:1,ismainverse:0,id_hino:612},{idverse:27453,verse:`2. 
N\xE3o nos impute nossos vis pecados,
Teus, n\xF3s sejamos, sempre mais e mais;
Pois, confiantes, somos vigorados
Em teus paternos bra\xE7os divinais.
`,verseorder:2,ismainverse:0,id_hino:612},{idverse:27454,verse:`3. 
\xD3 Pai, transforma nossa natureza,
Vem ensinar-nos a fazer o bem.
Em nossa mente d\xE1-nos mais pureza,
Mais santidade, por Jesus, Am\xE9m.`,verseorder:3,ismainverse:0,id_hino:612},{idverse:27455,verse:`1. 
Quando chegar ao fim da jornada,
Finda a luta e ao C\xE9u eu for;
Doce ser\xE1, sim, gozar o repouso
E as boas  vindas de Cristo, Senhor.`,verseorder:1,ismainverse:0,id_hino:613},{idverse:27456,verse:`1. 
Eu aviso uma terra feliz,
Onde irei para sempre morar:
H\xE1 mans\xF5es nesse lindo pa\xEDs,
Que Jesus foi pra mim preparar.
`,verseorder:1,ismainverse:0,id_hino:614},{idverse:27457,verse:`Vou morar! - vou morar
Nessa terra celeste porvir!
Vou morar! - vou morar!
Nessa terra celeste porvir!
`,verseorder:2,ismainverse:1,id_hino:614},{idverse:27458,verse:`2. 
Cantarei nesse lindo pa\xEDs
Belos hinos ao meu Salvador,
Pois ali viverei bem feliz,
Sem ang\xFAstias, tristezas, nem dor.
`,verseorder:3,ismainverse:0,id_hino:614},{idverse:27459,verse:`3. 
Vou cantar! - vou cantar
Nessa terra celeste porvir!
Vou cantar! vou cantar
Nessa terra celeste porvir!
`,verseorder:4,ismainverse:0,id_hino:614},{idverse:27460,verse:`4. 
Deixarei este mundo, afinal,
Para ir a Jesus adorar.
Nessa linda cidade real,
Em venturas sem fim vou estar.
`,verseorder:5,ismainverse:0,id_hino:614},{idverse:27461,verse:`5. 
Vou estar! - vou estar
Nessa terra celeste porvir!
Vou estar! - vou estar
Nessa terra celeste porvir!`,verseorder:6,ismainverse:0,id_hino:614},{idverse:27462,verse:`1. 
Vou rumo ao meu lar, meu lar celestial;
Vou rumo ao meu lar e a Cristo;
L\xE1, n\xE3o mais pesar, tristezas ou dor,
Vou rumo ao meu lar e a Cristo.`,verseorder:1,ismainverse:0,id_hino:615},{idverse:27463,verse:`1. 
Quanto almejo, lar celeste,
Tua gl\xF3ria ver!
Vestes brancas, perenais,
De Deus receber!
`,verseorder:1,ismainverse:0,id_hino:616},{idverse:27464,verse:`2. 
Quanto almejo junto aos coros
Minha voz erguer!
Com meu Mestre caminhar,
Ser\xE1 meu prazer!
`,verseorder:2,ismainverse:0,id_hino:616},{idverse:27465,verse:`3. 
Quanto almejo sua volta,
V\xEA-lo vir em luz,
Para em breve desfrutar
A paz com Jesus!`,verseorder:3,ismainverse:0,id_hino:616},{idverse:27466,verse:`1. 
Quanto anelo contemplar a Cristo;
Quanto anelo seu divino rosto ver!
\`Stou cansado de andar qual peregrino triste aqui;
Quanto anelo eu a Cristo ter.`,verseorder:1,ismainverse:0,id_hino:617},{idverse:27467,verse:`1. 
\xC9 nosso lar dos c\xE9us, no qual n\xE3o entra o mal,
Morada, junto ao santo Deus, na gl\xF3ria divinal.
`,verseorder:1,ismainverse:0,id_hino:618},{idverse:27468,verse:`2. 
A m\xE3o do amante Pai conduz-nos para o lar:
Bendito lar que sempre foi de Deus o seu lugar.
`,verseorder:2,ismainverse:0,id_hino:618},{idverse:27469,verse:`3. 
O sangue de Jesus j\xE1 no-lo assegurou;
A sua morte sobre a cruz, o t\xEDtulo firmou.
`,verseorder:3,ismainverse:0,id_hino:618},{idverse:27470,verse:`4. 
E o bom Consolador, penhor da reden\xE7\xE3o,
Pra o c\xE9u nos leva com amor, paz e satisfa\xE7\xE3o.
`,verseorder:4,ismainverse:0,id_hino:618},{idverse:27471,verse:`5. 
Consolador, sem par aqui ser\xE1 pra n\xF3s,
At\xE9 ouvirmos nesse lar de Cristo a terna voz.
`,verseorder:5,ismainverse:0,id_hino:618},{idverse:27472,verse:`6. 
Eternamente, ent\xE3o, folgando no Senhor,
No lar faremos, em can\xE7\xE3o, soar o seu louvor.`,verseorder:6,ismainverse:0,id_hino:618},{idverse:27473,verse:`1. 
Feliz Jerusal\xE9m, meu lar!
Em ti almejo estar!
Sentir a calma em gl\xF3ria ali,
E teu prazer gozar.
`,verseorder:1,ismainverse:0,id_hino:619},{idverse:27474,verse:`2. 
Oh! lar feliz de amor e paz!
Jardim sem outro igual!
O teu fulgor de gl\xF3ria e luz,
Jamais o viu mortal.
`,verseorder:2,ismainverse:0,id_hino:619},{idverse:27475,verse:`3. 
Jesus em breve ir\xE1 voltar,
E a paz do lar de al\xE9m
Ir\xE1, ent\xE3o, em ti reinar,
Meu lar - Jerusal\xE9m!`,verseorder:3,ismainverse:0,id_hino:619},{idverse:27476,verse:`1. 
Na jornada para o C\xE9u,
Vivo sempre a meditar.
Muito alegre vou cantando
E seguindo pro meu lar.
Muito alegre vou cantando
E seguindo pro meu lar.
`,verseorder:1,ismainverse:0,id_hino:620},{idverse:27477,verse:`Sei que \xE9 certa a vit\xF3ria,
Que Jesus me garantiu:
Eu vou chegar \xE0 Gl\xF3ria       (bis)
Que o mortal inda n\xE3o viu.
`,verseorder:2,ismainverse:1,id_hino:620},{idverse:27478,verse:`2. 
Caminhando com Jesus,
Meu prazer sempre aumentou:
Revelou-me o amor da cruz,
Que meus crimes perdoou
Revelou-me o amor da cruz,
Que meus crimes perdoou.
`,verseorder:3,ismainverse:0,id_hino:620},{idverse:27479,verse:`3. 
Um clar\xE3o da luz do C\xE9u
Minha alma iluminou
Ent\xE3o vi o abismo eterno
Do qual Deus j\xE1 me salvou.
Ent\xE3o vi o abismo eterno
Do qual Deus j\xE1 me salvou.
`,verseorder:4,ismainverse:0,id_hino:620},{idverse:27480,verse:`4. 
Nesta senda gloriosa,
Me ajuda o meu Rei:
Tenho paz, e mui ditosa,
E mais paz no C\xE9u terei.
Tenho paz, e mui ditosa,
E mais paz no C\xE9u terei.`,verseorder:5,ismainverse:0,id_hino:620},{idverse:27481,verse:`1. 
Iremos juntos viver no C\xE9u;
Felizes vamos ser.
Felizes l\xE1 no C\xE9u,
Iremos juntos viver no C\xE9u:
Jesus e eu!`,verseorder:1,ismainverse:0,id_hino:621},{idverse:27482,verse:`1. 
T\xE3o grato me \xE9 lembrar
Que cada dia estou
Mais perto do celeste lar,
Em que morar eu vou!
`,verseorder:1,ismainverse:0,id_hino:622},{idverse:27483,verse:`Cada vez mais perto do lar,
Perto do meu eterno lar,
Em que morar eu vou.
`,verseorder:2,ismainverse:1,id_hino:622},{idverse:27484,verse:`2. 
Em breve vou partir
Pra onde n\xE3o h\xE1 dor.
Del\xEDcias no c\xE9u vou fruir
Naquele lar de amor.
`,verseorder:3,ismainverse:0,id_hino:622},{idverse:27485,verse:`3. 
\xD3 Mestre, que leal
Eu seja sempre aqui!
Pois quero junto aos salvos mil
Contigo estar a\xED.`,verseorder:4,ismainverse:0,id_hino:622},{idverse:27486,verse:`1. 
Oh, sim, bem sei que o Redentor
Nos altos c\xE9us est\xE1;
Que v\xEA-lo-ei ao regressar,
E a vida me dar\xE1.
`,verseorder:1,ismainverse:0,id_hino:623},{idverse:27487,verse:`2. 
\xC0 plena luz dos olhos meus
Verei o Salvador;
Em carne, sim, verei a Deus,
Na paz do c\xE9u de amor.
`,verseorder:2,ismainverse:0,id_hino:623},{idverse:27488,verse:`3. 
Oh, sim, bem sei, bem certo estou
Que vive, al\xE9m, meu Rei;
E pelo seu eterno amor,
Com Ele viverei.`,verseorder:3,ismainverse:0,id_hino:623},{idverse:27489,verse:`1. 
Anseio a paz, um lugar de amor!
Saudoso estou deste lar feliz;
Subir em vestes de fulgor
Ao sempiterno, bom pa\xEDs.
`,verseorder:1,ismainverse:0,id_hino:624},{idverse:27490,verse:`2. 
Jamais vir\xE3o os pecados vis
A penetrar nos portais de luz;
Ser\xE1 no centro do pa\xEDs
O trono eterno de Jesus.
`,verseorder:2,ismainverse:0,id_hino:624},{idverse:27491,verse:`3. 
Ali fruir infind\xE1veis paz,
Ali viver s\xE3o os sonhos meus;
E o doce enlevo que me traz,
Real se torne, queira Deus!`,verseorder:3,ismainverse:0,id_hino:624},{idverse:27492,verse:`1. 
Tenho lido da bela cidade
Situada no Reino de Deus,
Amurada de jaspe luzente,
E juncada com \xE1ureos trof\xE9us.
Bem no meio da pra\xE7a eis o rio
Da vida e vigor eternal;
Mas metade da gl\xF3ria celeste
Jamais se contou ao mortal.
`,verseorder:1,ismainverse:0,id_hino:625},{idverse:27493,verse:`Jamais se contou ao mortal!
Jamais se contou ao mortal!
Metade da gl\xF3ria celeste
Jamais se contou ao mortal.
`,verseorder:2,ismainverse:1,id_hino:625},{idverse:27494,verse:`2. 
Tenho lido dos belos pal\xE1cios,
Que Jesus foi no C\xE9u preparar,
Onde os santos fi\xE9is, para sempre,
Mui felizes ir\xE3o habitar;
Nem tristeza, nem dor, nem velhice
Veremos no lar paternal;
Mas metade da gl\xF3ria celeste
Jamais se contou ao mortal.
`,verseorder:3,ismainverse:0,id_hino:625},{idverse:27495,verse:`3. 
Tenho lido das vestes brilhantes,
Das coroas que os crentes ter\xE3o,
Quando o Pai os chamar proclamando:
- "Recebei eternal galard\xE3o!"
Tenho lido que os santos na gl\xF3ria
T\xEAm gozo e prazer perenal.
Mas metade da gl\xF3ria celeste
Jamais se contou ao mortal.
`,verseorder:4,ismainverse:0,id_hino:625},{idverse:27496,verse:`4. 
Tenho lido da hist\xF3ria bendita
De Jesus, o fiel Redentor,
Que por n\xF3s padeceu no Calv\xE1rio
E recebe ao mais vil pecador.
Tenho lido do sangue vertido
Que a todos redime do mal;
Mas metade da gl\xF3ria celeste
Jamais se contou ao mortal.`,verseorder:5,ismainverse:0,id_hino:625},{idverse:27497,verse:`1. 
Am\xE9m, Am\xE9m, Am\xE9m!`,verseorder:1,ismainverse:0,id_hino:626},{idverse:27498,verse:`1. 
L\xE1, eu verei meu Salvador,
Coros de anjos quero ouvir;
Junto tamb\xE9m hei de cantar
Que meu Jesus me quis remir.`,verseorder:1,ismainverse:0,id_hino:627},{idverse:27499,verse:`1. 
Al\xE9m do c\xE9u azul,
Foi Jesus preparar
Um lar pra dar a quem
A vit\xF3ria alcan\xE7ar.
Por Ele consegui
A vida do porvir,
Com f\xE9 no meu Senhor Jesus.
`,verseorder:1,ismainverse:0,id_hino:628},{idverse:27500,verse:`2. 
Bem sei que eu de mim
Nada tenho pra dar
Mas sei que meu Jesus
J\xE1 me veio salvar.
Por Ele livre estou,
Seguindo a Ele vou,
At\xE9 seu rosto ver al\xE9m.`,verseorder:2,ismainverse:0,id_hino:628},{idverse:27501,verse:`1. 
Conversaremos ali no C\xE9u,
Conversaremos: Jesus e eu.
Dir\xE1 o porqu\xEA do meu sofrer.
Naquele dia eu entenderei`,verseorder:1,ismainverse:0,id_hino:629},{idverse:27502,verse:`1. 
N\xE3o h\xE1 separa\xE7\xE3o na gl\xF3ria;
Na alvorada, nas alturas.
N\xE3o h\xE1 separa\xE7\xE3o na gl\xF3ria,
Quando estivermos l\xE1 no C\xE9u!`,verseorder:1,ismainverse:0,id_hino:630},{idverse:27503,verse:`1. 
Eis plantada neste dia,
Qual semente singular,
Esta pedra - que alegria
Faz o povo aqui cantar!
`,verseorder:1,ismainverse:0,id_hino:631},{idverse:27504,verse:`Ouvi, oh! Terra e toda gente,
O louvor, a gratid\xE3o
A Jesus e ao Pai somente
Pelas almas que vir\xE3o!
`,verseorder:2,ismainverse:1,id_hino:631},{idverse:27505,verse:`2. 
Tantas vidas t\xE3o vazias
Ou repletas de afli\xE7\xE3o,
Neste templo, novos dias
No Senhor desfrutar\xE3o!
`,verseorder:3,ismainverse:0,id_hino:631},{idverse:27506,verse:`3. 
Quantos jovens sem futuro
A esta Casa h\xE3o de chegar
- E desfeito o negro muro
Paz e amor v\xE3o respirar!
`,verseorder:4,ismainverse:0,id_hino:631},{idverse:27507,verse:`4. 
M\xE3es e esposas suspirosas
A clamar, aqui vir\xE3o;
Logo ap\xF3s, vitoriosas,
Gl\xF3ria e gl\xF3ria ao Rei dar\xE3o!
`,verseorder:5,ismainverse:0,id_hino:631},{idverse:27508,verse:`5. 
Muitos seres quebrantados,
Nesta Casa de Ora\xE7\xE3o,
Pelo Esp\xEDrito visitados,
"Aleluia!" exclamar\xE3o.`,verseorder:6,ismainverse:0,id_hino:631},{idverse:27509,verse:`1. 
Hoje, inaugura-se aqui, santo Deus,
Mais um padr\xE3o de teu amor:
Um novo templo, fanal para os c\xE9us,
Causa de mais louvor!
`,verseorder:1,ismainverse:0,id_hino:632},{idverse:27510,verse:`Gl\xF3ria a Deus! - gl\xF3ria a Deus
Cantem os filhos teus!
Gl\xF3ria a Deus! - gl\xF3ria a Deus!
Gl\xF3ria nos altos c\xE9us!
`,verseorder:2,ismainverse:1,id_hino:632},{idverse:27511,verse:`2. 
Casa de cultos e fonte de luz,
Onde, Senhor, d\xE1s salva\xE7\xE3o
Pelo Evangelho que trouxe Jesus,
Com tanta compaix\xE3o!
`,verseorder:3,ismainverse:0,id_hino:632},{idverse:27512,verse:`3. 
Marco sublime da proclama\xE7\xE3o
Do teu amor - do teu querer:
Os pecadores aqui ouvir\xE3o
Qual \xE9 o seu dever.
`,verseorder:4,ismainverse:0,id_hino:632},{idverse:27513,verse:`4. 
Seja esta casa lugar de ora\xE7\xE3o,
Habita\xE7\xE3o tua, oh! Deus!
E porta do C\xE9u, lugar de perd\xE3o,
Vida de paz nos c\xE9us!`,verseorder:5,ismainverse:0,id_hino:632},{idverse:27514,verse:`1. 
Meu Brasil, na\xE7\xE3o gigante e mui sublime,
Creio ver-te muito breve bem maior,
Combatendo a iniq\xFCidade, o v\xEDcio, o crime,
Redimido aos p\xE9s de Cristo, o Salvador.
`,verseorder:1,ismainverse:0,id_hino:633},{idverse:27515,verse:`2. 
Meu Brasil - meu Brasil!
Abre o largo peito e deixa a luz raiar!
Meu Brasil! - meu Brasil!
O Evangelho de Jesus te quer salvar!
`,verseorder:2,ismainverse:0,id_hino:633},{idverse:27516,verse:`3. 
Debelar  a escurid\xE3o minh\`alma anseia
Do teu meio, onde o Evangelho prega a paz.
Espalhando livros santos a mancheia,
A vit\xF3ria, meu Brasil, alcan\xE7ar\xE1s.
`,verseorder:3,ismainverse:0,id_hino:633},{idverse:27517,verse:`4. 
N\xE3o te pe\xE7o, meu Senhor, poder, riqueza,
Nem as gl\xF3rias que oferece o mundo vil:
D\xE1 que eu possa ver fulgindo de beleza,
Na coroa de Jesus - o meu Brasil.`,verseorder:4,ismainverse:0,id_hino:633},{idverse:27518,verse:`1. 
Minha P\xE1tria para Cristo!
- Eis a minha peti\xE7\xE3o.
- Minha P\xE1tria t\xE3o querida,
Eu te dei meu cora\xE7\xE3o.
Lar prezado, lar formoso,
\xC9 por ti o meu amor.
Que o meu Deus de excelsa gra\xE7a
Te dispense seu favor!
`,verseorder:1,ismainverse:0,id_hino:634},{idverse:27519,verse:`2. 
Salve Deus a minha P\xE1tria,
Minha P\xE1tria varonil!
Salve Deus a minha terra,
Esta terra do Brasil!
`,verseorder:2,ismainverse:0,id_hino:634},{idverse:27520,verse:`3. 
Quero, pois, com alegria,
Ver feliz a m\xE3e gentil,
Por vencer o Evangelho
Nesta terra do Brasil.
Brava gente brasileira,
Longe v\xE1 temor servil;
Ou ficar a P\xE1tria salva,
Ou morrer pelo Brasil.`,verseorder:3,ismainverse:0,id_hino:634},{idverse:27521,verse:`1. 
\xD3 nosso Rei e Redentor,
Vem atender-nos a ora\xE7\xE3o;
S\xEA o supremo Protetor,
Eis o Brasil em tua m\xE3o.
`,verseorder:1,ismainverse:0,id_hino:635},{idverse:27522,verse:`2. 
D\xE1 compreens\xE3o e b\xEAn\xE7\xE3o mil
Aos que dirigem a na\xE7\xE3o;
P\xF5e neles zelo mais febril
Por tua lei e retid\xE3o.
`,verseorder:2,ismainverse:0,id_hino:635},{idverse:27523,verse:`3. 
Pai, quando, enfim, voltar Jesus,
Vier para dar-nos galard\xE3o,
Com os remidos l\xE1 na luz,
D\xE1 ao Brasil a salva\xE7\xE3o.`,verseorder:3,ismainverse:0,id_hino:635},{idverse:27524,verse:`1. 
Por nossa P\xE1tria oramos
A ti, supremo Deus!
Por nosso lar clamamos
A ti, \xF3 Rei dos c\xE9us!
Bendize a vida pastoril,
Governa o brio senhoril,
Modera a lida mercantil!
- Deus salve a P\xE1tria!
`,verseorder:1,ismainverse:0,id_hino:636},{idverse:27525,verse:`2. 
Da P\xE1tria, que nos deste,
Desvie tua m\xE3o
Desgra\xE7as, fome e peste,
Perf\xEDdia e sedi\xE7\xE3o!
Sustenta a ordem nacional,
O bom governo imparcial,
E d\xE1-nos gra\xE7a divinal!
- Deus salve a P\xE1tria!
`,verseorder:2,ismainverse:0,id_hino:636},{idverse:27526,verse:`3. 
D\xE1-nos real civismo,
Fiel, constante, audaz!
Promove o cristianismo
Do Pr\xEDncipe da paz!
Da P\xE1tria afasta cren\xE7as v\xE3s,
Derrama b\xEAn\xE7\xE3os tempor\xE3s!
Dominem s\xF3 doutrinas s\xE3s!
- Deus salve a P\xE1tria!
`,verseorder:3,ismainverse:0,id_hino:636},{idverse:27527,verse:`4. 
A tua Igreja inflama
Com Zelo e terno amor,
E seja o seu programa
Cumprido com vigor!
Ent\xE3o, os salvos de Jesus,
Lutando firmes pela Cruz,
Difundir\xE3o de Cristo a luz
Por toda a P\xE1tria! Am\xE9m.`,verseorder:4,ismainverse:0,id_hino:636},{idverse:27528,verse:`1. 
Salve lindo pend\xE3o da esperan\xE7a!
Salve, s\xEDmbolo augusto da paz!
Tua nobre presen\xE7a \xE0 lembran\xE7a
A grandeza da P\xE1tria nos traz.
`,verseorder:1,ismainverse:0,id_hino:637},{idverse:27529,verse:`2. 
Recebe o afeto que se encerra
Em nosso peito juvenil,
Querido s\xEDmbolo da terra,
Da amada terra do Brasil!
`,verseorder:2,ismainverse:0,id_hino:637},{idverse:27530,verse:`3. 
Em teu seio formoso retratas
Este c\xE9u de pur\xEDssimo azul,
A verdura sem-par destas matas,
E o esplendor do Cruzeiro do Sul.
`,verseorder:3,ismainverse:0,id_hino:637},{idverse:27531,verse:`4. 
Contemplando teu vulto sagrado,
Compreendemos o nosso dever,
E o Brasil, por seus filhos amado,
Poderoso e feliz h\xE1 de ser.
`,verseorder:4,ismainverse:0,id_hino:637},{idverse:27532,verse:`5. 
Sobre a imensa na\xE7\xE3o brasileira,
Nos momentos de festa ou de dor,
Paira sempre, sagrada bandeira,
Pavilh\xE3o da justi\xE7a e do amor!`,verseorder:5,ismainverse:0,id_hino:637},{idverse:27533,verse:`1. 
Ouviram do Ipiranga as margens pl\xE1cidas
De um povo her\xF3ico o brado retumbante,
E o sol da liberdade, em raios f\xFAlgidos,
Brilhou no c\xE9u da P\xE1tria neste instante.
Se o penhor dessa igualdade
Conseguimos conquistar com bra\xE7o forte,
Em teu seio, \xF3 liberdade,
Desafia o nosso peito \xE0 pr\xF3pria morte!
\xD3 P\xE1tria amada, idolatrada, salve! salve!
Brasil, um sonho intenso, um raio v\xEDvido
De amor e de esperan\xE7a \xE0 terra desce,
Se em teu formoso c\xE9u, risonho e l\xEDmpido,
A imagem do Cruzeiro resplandece.
Gigante pela pr\xF3pria natureza,
\xC9s belo, \xE9s forte, imp\xE1vido, colosso,
E o teu futuro espelha essa grandeza.
`,verseorder:1,ismainverse:0,id_hino:638},{idverse:27534,verse:`2. 
Terra adorada, entre outras mil \xE9s tu, Brasil,
\xD3 P\xE1tria amada!
Dos filhos deste solo \xE9s m\xE3e gentil,
P\xE1tria amada, Brasil!
`,verseorder:2,ismainverse:0,id_hino:638},{idverse:27535,verse:`3. 
Deitado eternamente em ber\xE7o espl\xEAndido,
Ao som do mar e \xE1 luz do c\xE9u profundo,
Fulguras, \xF3 Brasil, flor\xE3o da Am\xE9rica,
Iluminado ao sol do Novo mundo!
Do que a terra mais garrida
Teus risonhos, lindos campos t\xEAm mais flores
"Nossos bosques t\xEAm mais vida,
Nossa vida", no teu seio, "mais amores".
\xD3 P\xE1tria amada, idolatrada, salve! salve!
Brasil, de amor eterno seja s\xEDmbolo
O l\xE1baro que ostentas estrelado,
E diga o verde louro desta fl\xE2mula:
"Paz no futuro e gl\xF3ria no passado".
Mas, se ergues da justi\xE7a a clava forte,
Ver\xE1s que um filho teu n\xE3o fonge \xE0 luta,
Nem teme, quem te adora, a pr\xF3pria morte.`,verseorder:3,ismainverse:0,id_hino:638},{idverse:27536,verse:`1. 
J\xE1 podeis da P\xE1tria filhos
Ver contente a m\xE3e gentil,
J\xE1 raiou a liberdade
No horizonte do Brasil!
J\xE1 raiou a liberdade
J\xE1 raiou a liberdade
No horizonte do Brasil.
`,verseorder:1,ismainverse:0,id_hino:639},{idverse:27537,verse:`2. 
Brava gente brasileira
Longe v\xE1 temor servil
Ou ficar a P\xE1tria livre
Ou morrer pelo Brasil
Ou ficar a P\xE1tria livre
Ou morrer pelo Brasil
`,verseorder:2,ismainverse:0,id_hino:639},{idverse:27538,verse:`3. 
Mal soou na serra ao longe
Nosso grito varonil;
Nos imensos ombros, logo,
A cabe\xE7a ergue o Brasil!
Nos imensos ombros, logo,
Nos imensos ombros, logo,
A cabe\xE7a ergue o Brasil.
`,verseorder:3,ismainverse:0,id_hino:639},{idverse:27539,verse:`4. 
Os grilh\xF5es que nos forjava
Da perf\xEDdia astuto ardil
Houve m\xE3o mais poderosa
Zombou deles o Brasil.
Houve m\xE3o mais poderosa
Houve m\xE3o mais poderosa
Zombou deles o Brasil!
`,verseorder:4,ismainverse:0,id_hino:639},{idverse:27540,verse:`5. 
Parab\xE9ns, \xF3 brasileiros
J\xE1 com garbo juvenil
Do Universo entre as na\xE7\xF5es
Resplandece a do Brasil.
Do Universo entre as na\xE7\xF5es
Do Universo entre as na\xE7\xF5es
Resplandece a do Brasil.`,verseorder:5,ismainverse:0,id_hino:639},{idverse:27541,verse:`1. 
Seja um p\xE1lio de luz desdobrado
Sob a larga amplid\xE3o destes c\xE9us
Este canto rebel, que o passado
Vem remir dos mais torpes lab\xE9us!
Seja um hino de gl\xF3ria que fale
De esperan\xE7as de um novo porvir!
Com vis\xF5es de triunfos embale
Quem por ele lutando surgir!
`,verseorder:1,ismainverse:0,id_hino:640},{idverse:27542,verse:`2. 
Liberdade! Liberdade!
Abre as asas sobre n\xF3s!
Das lutas na tempestade
D\xE1 que ou\xE7amos tua voz!
`,verseorder:2,ismainverse:0,id_hino:640},{idverse:27543,verse:`3. 
N\xF3s nem cremos que escravos outrora
Tenha havido em t\xE3o nobre pa\xEDs...
Hoje, o rubro lampejo da Aurora
Acha irm\xE3os, n\xE3o tiranos hostis.
Somos todos iguais! Ao futuro
Saberemos, unidos, levar
Nosso augusto estandarte que, puro,
Brilha, ovante, da P\xE1tria no altar!
`,verseorder:3,ismainverse:0,id_hino:640},{idverse:27544,verse:`4. 
Se \xE9 mister que de pertos valentes
Haja sangue no nosso pend\xE3o,
Sangue vivo do her\xF3i Tiradentes
Batizou este audaz pavilh\xE3o!
Mensageiros da paz, paz queremos,
\xC9 de amor nossa for\xE7a e poder,
Mas da guerra nos transes supremos
Heis de ver-nos lutar e vencer!
`,verseorder:4,ismainverse:0,id_hino:640},{idverse:27545,verse:`5. 
Do Ipiranga \xE9 preciso que o brado
Seja um grito soberbo de f\xE9!
O Brasil j\xE1 surgiu libertado,
Sobre as p\xFArpuras r\xE9gias de p\xE9!
Eia, pois, brasileiros, avante!
Verdes louros colhamos lou\xE7\xE3os!
Seja o nosso pa\xEDs, triunfante,
Livre terra de livres irm\xE3os!`,verseorder:5,ismainverse:0,id_hino:640},{idverse:27546,verse:`Conta as b\xEAn\xE7\xE3os! dize quantas s\xE3o,
Recebidas da divina m\xE3o!
Vem diz\xEA-las, todas duma vez,
E ver\xE1s, surpreso, quanto Deus j\xE1 fez!`,verseorder:2,ismainverse:1,id_hino:564}];const Q={class:"q-pa-md"},O={class:"row"},N={class:"col-12"},j={class:"row"},M={class:"col-12"},I=f({__name:"VersoView",setup(V){const i=T(),{numero:d}=z(i),v=C(),m=A.filter(a=>a.id_hino===d.value);function u(){v.push({path:"/hino"})}return(a,R)=>(o(),n("div",null,[e("div",Q,[e("div",O,[e("div",N,[r(h,{class:"text-primary"},{default:s(()=>[r(c,{onClick:u,flat:"",round:"",dense:"",icon:"arrow_back"})]),_:1})])]),e("div",j,[e("div",M,[r(p,{bordered:"",separator:""},{default:s(()=>[(o(!0),n(b,null,E(q(m),(t,l)=>J((o(),D(g,{key:l},{default:s(()=>[r(_,null,{default:s(()=>[e("span",null,P(t.verse),1)]),_:2},1024)]),_:2},1024)),[[S]])),128))]),_:1})])])])]))}});export{I as default};
